'use client';

import React, { useState, useEffect } from 'react';
import {
  Container,
  Grid,
  Paper,
  Text,
  Group,
  Badge,
  SimpleGrid,
  Stack,
  Button,
  Title,
  Card,
  TextInput,
  Select,
  LoadingOverlay,
  Alert
} from '@mantine/core';
import { useDisclosure } from '@mantine/hooks';
import { notifications } from '@mantine/notifications';
import { DatePickerInput } from '@mantine/dates';
import {
  IconPlus,
  IconSearch,
  IconCalendar,
  IconClock,
  IconEdit,
  IconTrash,
  IconEye,
  IconCalendarEvent,
  IconCheck,
  IconAlertCircle
} from '@tabler/icons-react';
import Layout from '../../components/shared/Layout';
import DataTable from '../../components/shared/DataTable';
import AppointmentForm from '../../components/appointments/AppointmentForm';
import AppointmentDetails from '../../components/appointments/AppointmentDetails';
import { useAppStore } from '../../stores/appStore';
import { User, UserRole, TableColumn } from '../../types/common';
import appointmentsService from '../../services/appointments.service';
import patientsService from '../../services/patients.service';
import type { CreateAppointmentDto, UpdateAppointmentDto, AppointmentFilters } from '../../services/appointments.service';

const mockUser: User = {
  id: '1',
  username: 'sjohnson',
  email: 'sarah.johnson@hospital.com',
  firstName: 'Sarah',
  lastName: 'Johnson',
  role: UserRole.DOCTOR,
  permissions: [],
  isActive: true,
  tenantInfo: {
    tenantId: 'T001',
    tenantName: 'Main Hospital',
  },
  createdAt: new Date(),
  updatedAt: new Date(),
};

function AppointmentsPage() {
  const { user, setUser } = useAppStore();
  const [appointments, setAppointments] = useState<any[]>([]);
  const [patients, setPatients] = useState<any[]>([]);
  const [doctors, setDoctors] = useState<any[]>([]);
  const [stats, setStats] = useState<any>(null);
  const [loading, setLoading] = useState(true);
  const [selectedAppointment, setSelectedAppointment] = useState<any>(null);
  const [searchQuery, setSearchQuery] = useState('');
  const [statusFilter, setStatusFilter] = useState('');
  const [dateFilter, setDateFilter] = useState<Date | null>(null);

  const [formOpened, { open: openForm, close: closeForm }] = useDisclosure(false);
  const [detailsOpened, { open: openDetails, close: closeDetails }] = useDisclosure(false);

  useEffect(() => {
    if (!user) {
      setUser(mockUser);
    }
    fetchAppointments();
    fetchStats();
    fetchPatients();
  }, [user, setUser]);

  const fetchAppointments = async () => {
    setLoading(true);
    try {
      const filters: AppointmentFilters = {};
      if (searchQuery) filters.search = searchQuery;
      if (statusFilter) filters.status = statusFilter;
      if (dateFilter) {
        filters.startDate = dateFilter.toISOString();
        filters.endDate = dateFilter.toISOString();
      }

      const response = await appointmentsService.getAppointments(filters);
      if (response.success && response.data) {
        setAppointments(response.data || []);
      }
    } catch (error: any) {
      console.error('Error fetching appointments:', error);
      notifications.show({
        title: 'Error',
        message: error?.message || 'Failed to fetch appointments',
        color: 'red',
      });
    } finally {
      setLoading(false);
    }
  };

  const fetchStats = async () => {
    try {
      const response = await appointmentsService.getAppointmentStats();
      if (response.success && response.data) {
        setStats(response.data);
      }
    } catch (error: any) {
      console.error('Error fetching stats:', error);
    }
  };

  const fetchPatients = async () => {
    try {
      const response = await patientsService.getPatients();
      if (response.success && response.data) {
        setPatients(response.data.patients || []);
      }
    } catch (error: any) {
      console.error('Error fetching patients:', error);
    }
  };

  const handleCreateAppointment = async (data: CreateAppointmentDto) => {
    try {
      const response = await appointmentsService.createAppointment(data);
      if (response.success) {
        notifications.show({
          title: 'Success',
          message: 'Appointment created successfully',
          color: 'green',
        });
        fetchAppointments();
        fetchStats();
        closeForm();
      }
    } catch (error: any) {
      console.error('Error creating appointment:', error);
      notifications.show({
        title: 'Error',
        message: error?.message || 'Failed to create appointment',
        color: 'red',
      });
      throw error;
    }
  };

  const handleUpdateAppointment = async (data: UpdateAppointmentDto) => {
    if (!selectedAppointment) return;

    try {
      const response = await appointmentsService.updateAppointment(selectedAppointment.id, data);
      if (response.success) {
        notifications.show({
          title: 'Success',
          message: 'Appointment updated successfully',
          color: 'green',
        });
        fetchAppointments();
        fetchStats();
        closeForm();
        setSelectedAppointment(null);
      }
    } catch (error: any) {
      console.error('Error updating appointment:', error);
      notifications.show({
        title: 'Error',
        message: error?.message || 'Failed to update appointment',
        color: 'red',
      });
      throw error;
    }
  };

  const handleDeleteAppointment = async (appointment: any) => {
    if (!window.confirm('Are you sure you want to delete this appointment?')) {
      return;
    }

    try {
      const response = await appointmentsService.deleteAppointment(appointment.id);
      if (response.success) {
        notifications.show({
          title: 'Success',
          message: 'Appointment deleted successfully',
          color: 'green',
        });
        fetchAppointments();
        fetchStats();
      }
    } catch (error: any) {
      console.error('Error deleting appointment:', error);
      notifications.show({
        title: 'Error',
        message: error?.message || 'Failed to delete appointment',
        color: 'red',
      });
    }
  };

  const handleStatusChange = async (appointmentId: string, status: string) => {
    try {
      const response = await appointmentsService.updateAppointmentStatus(appointmentId, status);
      if (response.success) {
        notifications.show({
          title: 'Success',
          message: `Appointment status updated to ${status}`,
          color: 'green',
        });
        fetchAppointments();
        fetchStats();
        closeDetails();
      }
    } catch (error: any) {
      console.error('Error updating status:', error);
      notifications.show({
        title: 'Error',
        message: error?.message || 'Failed to update status',
        color: 'red',
      });
    }
  };

  const handleViewAppointment = (appointment: any) => {
    setSelectedAppointment(appointment);
    openDetails();
  };

  const handleEditAppointment = (appointment: any) => {
    setSelectedAppointment(appointment);
    openForm();
  };

  const handleNewAppointment = () => {
    setSelectedAppointment(null);
    openForm();
  };

  const getStatusColor = (status: string) => {
    const colors: Record<string, string> = {
      SCHEDULED: 'blue',
      ARRIVED: 'teal',
      IN_PROGRESS: 'yellow',
      COMPLETED: 'green',
      CANCELLED: 'red',
      NO_SHOW: 'gray',
      RESCHEDULED: 'orange'
    };
    return colors[status] || 'gray';
  };

  const formatDateTime = (dateString: string) => {
    const date = new Date(dateString);
    return {
      date: date.toLocaleDateString(),
      time: date.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })
    };
  };

  const columns: TableColumn[] = [
    {
      key: 'patient',
      title: 'Patient',
      sortable: true,
      render: (appointment: any) => (
        <div>
          <Text fw={500}>
            {appointment.patient?.firstName} {appointment.patient?.lastName}
          </Text>
          <Text size="xs" c="dimmed">
            {appointment.patient?.medicalRecordNumber || appointment.patient?.id}
          </Text>
        </div>
      )
    },
    {
      key: 'doctor',
      title: 'Doctor',
      sortable: true,
      render: (appointment: any) => (
        <Text>
          Dr. {appointment.doctor?.firstName} {appointment.doctor?.lastName}
        </Text>
      )
    },
    {
      key: 'dateTime',
      title: 'Date & Time',
      sortable: true,
      render: (appointment: any) => {
        const { date, time } = formatDateTime(appointment.startTime);
        return (
          <div>
            <Text fw={500}>{date}</Text>
            <Text size="xs" c="dimmed">{time}</Text>
          </div>
        );
      }
    },
    {
      key: 'reason',
      title: 'Reason',
      render: (appointment: any) => (
        <Text lineClamp={2}>{appointment.reason}</Text>
      )
    },
    {
      key: 'status',
      title: 'Status',
      sortable: true,
      render: (appointment: any) => (
        <Badge color={getStatusColor(appointment.status)}>
          {appointment.status}
        </Badge>
      )
    },
    {
      key: 'actions',
      title: 'Actions',
      render: (appointment: any) => (
        <Group gap="xs">
          <Button
            size="xs"
            variant="subtle"
            leftSection={<IconEye size={14} />}
            onClick={() => handleViewAppointment(appointment)}
          >
            View
          </Button>
          <Button
            size="xs"
            variant="subtle"
            leftSection={<IconEdit size={14} />}
            onClick={() => handleEditAppointment(appointment)}
          >
            Edit
          </Button>
          <Button
            size="xs"
            variant="subtle"
            color="red"
            leftSection={<IconTrash size={14} />}
            onClick={() => handleDeleteAppointment(appointment)}
          >
            Delete
          </Button>
        </Group>
      )
    }
  ];

  return (
    <Layout user={user ? { 
      id: user.id, 
      name: `${user.firstName} ${user.lastName}`, 
      email: user.email, 
      role: user.role,
      avatar: undefined 
    } : mockUser ? {
      id: mockUser.id,
      name: `${mockUser.firstName} ${mockUser.lastName}`,
      email: mockUser.email,
      role: mockUser.role,
      avatar: undefined
    } : undefined} notifications={0} onLogout={() => {}}>
      <Container size="xl" py="xl">
        <Stack gap="lg">
          {/* Header */}
          <Group justify="space-between">
            <div>
              <Title order={2}>Appointment Management</Title>
              <Text c="dimmed" size="sm">
                Manage and schedule patient appointments
              </Text>
            </div>
            <Button
              leftSection={<IconPlus size={16} />}
              onClick={handleNewAppointment}
            >
              Book Appointment
            </Button>
          </Group>

          {/* Statistics Cards */}
          {stats && (
            <SimpleGrid cols={{ base: 1, sm: 2, md: 4 }}>
              <Card withBorder padding="lg">
                <Group justify="space-between">
                  <div>
                    <Text c="dimmed" size="xs" tt="uppercase" fw={700}>
                      Total
                    </Text>
                    <Text fw={700} size="xl">
                      {stats.total}
                    </Text>
                  </div>
                  <IconCalendarEvent size={32} color="#228be6" />
                </Group>
              </Card>

              <Card withBorder padding="lg">
                <Group justify="space-between">
                  <div>
                    <Text c="dimmed" size="xs" tt="uppercase" fw={700}>
                      Today
                    </Text>
                    <Text fw={700} size="xl">
                      {stats.today}
                    </Text>
                  </div>
                  <IconClock size={32} color="#12b886" />
                </Group>
              </Card>

              <Card withBorder padding="lg">
                <Group justify="space-between">
                  <div>
                    <Text c="dimmed" size="xs" tt="uppercase" fw={700}>
                      Pending
                    </Text>
                    <Text fw={700} size="xl">
                      {stats.pending}
                    </Text>
                  </div>
                  <IconAlertCircle size={32} color="#fab005" />
                </Group>
              </Card>

              <Card withBorder padding="lg">
                <Group justify="space-between">
                  <div>
                    <Text c="dimmed" size="xs" tt="uppercase" fw={700}>
                      Completed
                    </Text>
                    <Text fw={700} size="xl">
                      {stats.completed}
                    </Text>
                  </div>
                  <IconCheck size={32} color="#40c057" />
                </Group>
              </Card>
            </SimpleGrid>
          )}

          {/* Filters */}
          <Paper withBorder p="md">
            <Grid>
              <Grid.Col span={{ base: 12, sm: 6, md: 3 }}>
                <TextInput
                  placeholder="Search appointments..."
                  leftSection={<IconSearch size={16} />}
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                />
              </Grid.Col>
              <Grid.Col span={{ base: 12, sm: 6, md: 3 }}>
                <Select
                  placeholder="Filter by status"
                  data={[
                    { value: '', label: 'All Statuses' },
                    { value: 'SCHEDULED', label: 'Scheduled' },
                    { value: 'ARRIVED', label: 'Arrived' },
                    { value: 'IN_PROGRESS', label: 'In Progress' },
                    { value: 'COMPLETED', label: 'Completed' },
                    { value: 'CANCELLED', label: 'Cancelled' }
                  ]}
                  value={statusFilter}
                  onChange={(value) => setStatusFilter(value || '')}
                />
              </Grid.Col>
              <Grid.Col span={{ base: 12, sm: 6, md: 3 }}>
                <DatePickerInput
                  placeholder="Filter by date"
                  leftSection={<IconCalendar size={16} />}
                  value={dateFilter}
                  onChange={(value) => setDateFilter(value as unknown as Date | null)}
                  clearable
                />
              </Grid.Col>
              <Grid.Col span={{ base: 12, sm: 6, md: 3 }}>
                <Button fullWidth onClick={fetchAppointments}>
                  Apply Filters
                </Button>
              </Grid.Col>
            </Grid>
          </Paper>

          {/* Appointments Table */}
          <Paper withBorder>
            <LoadingOverlay visible={loading} />
            {appointments.length === 0 && !loading ? (
              <Alert icon={<IconAlertCircle size={16} />} title="No appointments found" color="blue">
                No appointments match your current filters. Try adjusting your search criteria or book a new appointment.
              </Alert>
            ) : (
              <DataTable
                columns={columns}
                data={appointments}
                loading={loading}
              />
            )}
          </Paper>
        </Stack>
      </Container>

      {/* Appointment Form Modal */}
      <AppointmentForm
        opened={formOpened}
        onClose={closeForm}
        appointment={selectedAppointment}
        onSubmit={selectedAppointment ? handleUpdateAppointment : handleCreateAppointment}
        patients={patients}
        doctors={doctors}
      />

      {/* Appointment Details Modal */}
      {selectedAppointment && (
        <AppointmentDetails
          opened={detailsOpened}
          onClose={closeDetails}
          appointment={selectedAppointment}
          onEdit={handleEditAppointment}
          onDelete={handleDeleteAppointment}
          onStatusChange={handleStatusChange}
        />
      )}
    </Layout>
  );
}

export default AppointmentsPage;

'use client';

import React, { useState } from 'react';
import {
  Button,
  Card,
  TextInput
} from '@mantine/core';
import Layout from '../../components/shared/Layout';
import { UserRole } from '../../types/common';

interface Appointment {
  id: string;
  patientId: string;
  patientName: string;
  doctorId: string;
  doctorName: string;
  department: string;
  startTime: string;
  endTime: string;
  status: 'SCHEDULED' | 'ARRIVED' | 'IN_PROGRESS' | 'COMPLETED' | 'CANCELLED' | 'NO_SHOW' | 'RESCHEDULED';
  reason: string;
  notes?: string;
  type: 'consultation' | 'follow_up' | 'surgery' | 'emergency' | 'telemedicine';
  priority: 'low' | 'medium' | 'high' | 'urgent';
}

const AppointmentsPage = () => {
  const [currentView, setCurrentView] = useState<'calendar' | 'list'>('calendar');
  const [selectedDate, setSelectedDate] = useState(new Date().toISOString().split('T')[0]);
  const [filterStatus, setFilterStatus] = useState('all');
  const [filterDepartment, setFilterDepartment] = useState('all');
  const [searchTerm, setSearchTerm] = useState('');

  const [appointments] = useState<Appointment[]>([
    {
      id: '1',
      patientId: 'p1',
      patientName: 'John Doe',
      doctorId: 'd1',
      doctorName: 'Dr. Sarah Wilson',
      department: 'Cardiology',
      startTime: '2024-12-05T09:00:00',
      endTime: '2024-12-05T09:30:00',
      status: 'SCHEDULED',
      reason: 'Regular checkup',
      type: 'consultation',
      priority: 'medium'
    },
    {
      id: '2',
      patientId: 'p2',
      patientName: 'Emily Johnson',
      doctorId: 'd2',
      doctorName: 'Dr. Michael Chen',
      department: 'Pediatrics',
      startTime: '2024-12-05T10:30:00',
      endTime: '2024-12-05T11:00:00',
      status: 'IN_PROGRESS',
      reason: 'Vaccination',
      type: 'consultation',
      priority: 'low'
    },
    {
      id: '3',
      patientId: 'p3',
      patientName: 'Robert Smith',
      doctorId: 'd3',
      doctorName: 'Dr. Lisa Rodriguez',
      department: 'Orthopedics',
      startTime: '2024-12-05T14:00:00',
      endTime: '2024-12-05T14:45:00',
      status: 'SCHEDULED',
      reason: 'Knee pain assessment',
      type: 'consultation',
      priority: 'high'
    },
    {
      id: '4',
      patientId: 'p4',
      patientName: 'Maria Garcia',
      doctorId: 'd1',
      doctorName: 'Dr. Sarah Wilson',
      department: 'Cardiology',
      startTime: '2024-12-05T16:30:00',
      endTime: '2024-12-05T17:15:00',
      status: 'COMPLETED',
      reason: 'Post-surgery follow-up',
      type: 'follow_up',
      priority: 'high'
    },
    {
      id: '5',
      patientId: 'p5',
      patientName: 'David Wilson',
      doctorId: 'd4',
      doctorName: 'Dr. James Kumar',
      department: 'Emergency',
      startTime: '2024-12-05T08:15:00',
      endTime: '2024-12-05T09:00:00',
      status: 'COMPLETED',
      reason: 'Chest pain',
      type: 'emergency',
      priority: 'urgent'
    }
  ]);

  const departments = ['Cardiology', 'Pediatrics', 'Orthopedics', 'Emergency', 'Neurology', 'Dermatology'];
  const timeSlots = Array.from({ length: 24 }, (_, i) => `${i.toString().padStart(2, '0')}:00`);

  const filteredAppointments = appointments.filter(appointment => {
    const matchesSearch = 
      appointment.patientName.toLowerCase().includes(searchTerm.toLowerCase()) ||
      appointment.doctorName.toLowerCase().includes(searchTerm.toLowerCase()) ||
      appointment.department.toLowerCase().includes(searchTerm.toLowerCase());

    const matchesStatus = filterStatus === 'all' || appointment.status === filterStatus;
    const matchesDepartment = filterDepartment === 'all' || appointment.department === filterDepartment;
    const matchesDate = appointment.startTime.startsWith(selectedDate);

    return matchesSearch && matchesStatus && matchesDepartment && matchesDate;
  });

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'SCHEDULED': return '#3b82f6';
      case 'ARRIVED': return '#10b981';
      case 'IN_PROGRESS': return '#f59e0b';
      case 'COMPLETED': return '#059669';
      case 'CANCELLED': return '#ef4444';
      case 'NO_SHOW': return '#dc2626';
      case 'RESCHEDULED': return '#8b5cf6';
      default: return '#6b7280';
    }
  };

  const getPriorityColor = (priority: string) => {
    switch (priority) {
      case 'urgent': return '#dc2626';
      case 'high': return '#ea580c';
      case 'medium': return '#d97706';
      case 'low': return '#65a30d';
      default: return '#6b7280';
    }
  };

  const getTypeIcon = (type: string) => {
    switch (type) {
      case 'consultation': return 'ü©∫';
      case 'follow_up': return 'üîÑ';
      case 'surgery': return '‚öïÔ∏è';
      case 'emergency': return 'üö®';
      case 'telemedicine': return 'üíª';
      default: return 'üìã';
    }
  };

  const AppointmentCard = ({ appointment }: { appointment: Appointment }) => (
    <Card 
      variant="elevated" 
      style={{ 
        marginBottom: '1rem',
        borderLeft: `4px solid ${getStatusColor(appointment.status)}`
      }}
    >
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start' }}>
        <div style={{ flex: 1 }}>
          <div style={{ display: 'flex', alignItems: 'center', marginBottom: '0.5rem' }}>
            <span style={{ fontSize: '1.2rem', marginRight: '0.5rem' }}>
              {getTypeIcon(appointment.type)}
            </span>
            <h3 style={{ 
              margin: '0 1rem 0 0', 
              fontSize: '1.1rem', 
              fontWeight: '600', 
              color: '#1f2937' 
            }}>
              {appointment.patientName}
            </h3>
            <span style={{
              padding: '0.25rem 0.5rem',
              borderRadius: '12px',
              fontSize: '0.75rem',
              fontWeight: '600',
              backgroundColor: `${getPriorityColor(appointment.priority)}15`,
              color: getPriorityColor(appointment.priority)
            }}>
              {appointment.priority.toUpperCase()}
            </span>
          </div>

          <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))', gap: '1rem' }}>
            <div>
              <p style={{ margin: '0 0 0.25rem 0', fontSize: '0.875rem', color: '#6b7280' }}>
                <strong>Doctor:</strong> {appointment.doctorName}
              </p>
              <p style={{ margin: '0 0 0.25rem 0', fontSize: '0.875rem', color: '#6b7280' }}>
                <strong>Department:</strong> {appointment.department}
              </p>
              <p style={{ margin: '0 0 0.25rem 0', fontSize: '0.875rem', color: '#6b7280' }}>
                <strong>Time:</strong> {new Date(appointment.startTime).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })} - {new Date(appointment.endTime).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
              </p>
            </div>

            <div>
              <p style={{ margin: '0 0 0.25rem 0', fontSize: '0.875rem', color: '#6b7280' }}>
                <strong>Reason:</strong> {appointment.reason}
              </p>
              <p style={{ margin: '0 0 0.25rem 0', fontSize: '0.875rem', color: '#6b7280' }}>
                <strong>Type:</strong> {appointment.type.replace('_', ' ')}
              </p>
              {appointment.notes && (
                <p style={{ margin: '0 0 0.25rem 0', fontSize: '0.875rem', color: '#6b7280' }}>
                  <strong>Notes:</strong> {appointment.notes}
                </p>
              )}
            </div>
          </div>
        </div>
        
        <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'end', gap: '0.5rem' }}>
          <span style={{
            padding: '0.25rem 0.75rem',
            borderRadius: '12px',
            fontSize: '0.75rem',
            fontWeight: '600',
            backgroundColor: `${getStatusColor(appointment.status)}15`,
            color: getStatusColor(appointment.status)
          }}>
            {appointment.status.replace('_', ' ')}
          </span>

          <div style={{ display: 'flex', gap: '0.25rem' }}>
            <Button size="sm" variant="outline" onClick={() => window.location.href = `/appointments/${appointment.id}`}>
              View
            </Button>
            <Button size="sm" variant="primary" onClick={() => window.location.href = `/appointments/${appointment.id}/edit`}>
              Edit
            </Button>
          </div>
        </div>
      </div>
    </Card>
  );

  const CalendarView = () => {
    const today = new Date(selectedDate);
    const hours = Array.from({ length: 14 }, (_, i) => i + 8); // 8 AM to 9 PM

    return (
      <Card>
        <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', marginBottom: '1.5rem' }}>
          <h3 style={{ fontSize: '1.25rem', fontWeight: '600', color: '#1f2937' }}>
            Schedule for {today.toLocaleDateString('en-US', { weekday: 'long', year: 'numeric', month: 'long', day: 'numeric' })}
          </h3>
          <div style={{ display: 'flex', gap: '0.5rem' }}>
            <Button size="sm" variant="outline" onClick={() => {
              const yesterday = new Date(today);
              yesterday.setDate(yesterday.getDate() - 1);
              setSelectedDate(yesterday.toISOString().split('T')[0]);
            }}>
              ‚Üê Previous
            </Button>
            <Button size="sm" variant="outline" onClick={() => {
              setSelectedDate(new Date().toISOString().split('T')[0]);
            }}>
              Today
            </Button>
            <Button size="sm" variant="outline" onClick={() => {
              const tomorrow = new Date(today);
              tomorrow.setDate(tomorrow.getDate() + 1);
              setSelectedDate(tomorrow.toISOString().split('T')[0]);
            }}>
              Next ‚Üí
            </Button>
          </div>
        </div>

        <div style={{ 
          display: 'grid', 
          gridTemplateColumns: '80px 1fr',
          gap: '0.5rem',
          maxHeight: '600px',
          overflowY: 'auto'
        }}>
          {hours.map(hour => {
            const appointmentsInHour = filteredAppointments.filter(apt => {
              const aptHour = new Date(apt.startTime).getHours();
              return aptHour === hour;
            });

            return (
              <React.Fragment key={hour}>
                <div style={{
                  padding: '0.5rem',
                  fontSize: '0.875rem',
                  color: '#6b7280',
                  fontWeight: '500',
                  textAlign: 'center',
                  borderTop: '1px solid #f1f5f9'
                }}>
                  {hour.toString().padStart(2, '0')}:00
                </div>
                <div style={{
                  minHeight: '60px',
                  borderTop: '1px solid #f1f5f9',
                  padding: '0.5rem',
                  position: 'relative'
                }}>
                  {appointmentsInHour.map((appointment, index) => (
                    <div
                      key={appointment.id}
                      style={{
                        position: 'absolute',
                        left: `${index * 200 + 8}px`,
                        width: '190px',
                        background: `${getStatusColor(appointment.status)}15`,
                        border: `2px solid ${getStatusColor(appointment.status)}`,
                        borderRadius: '6px',
                        padding: '0.5rem',
                        fontSize: '0.75rem',
                        cursor: 'pointer'
                      }}
                      onClick={() => window.location.href = `/appointments/${appointment.id}`}
                    >
                      <div style={{ fontWeight: '600', color: '#1f2937', marginBottom: '0.25rem' }}>
                        {appointment.patientName}
                      </div>
                      <div style={{ color: '#6b7280' }}>
                        {appointment.doctorName}
                      </div>
                      <div style={{ color: '#6b7280' }}>
                        {new Date(appointment.startTime).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
                      </div>
                    </div>
                  ))}
                </div>
              </React.Fragment>
            );
          })}
        </div>
      </Card>
    );
  };

  return (
    <Layout>
      <div style={{ maxWidth: '1400px', margin: '0 auto' }}>
        {/* Header */}
        <div style={{ 
          display: 'flex', 
          justifyContent: 'space-between', 
          alignItems: 'center', 
          marginBottom: '2rem' 
        }}>
          <div>
            <h1 style={{ fontSize: '2rem', fontWeight: 'bold', color: '#1f2937', marginBottom: '0.5rem' }}>
              Appointment Management
            </h1>
            <p style={{ color: '#6b7280', fontSize: '1rem' }}>
              Manage patient appointments, scheduling, and medical consultations
            </p>
          </div>
          <Button onClick={() => window.location.href = '/appointments/new'}>
            + Schedule New Appointment
          </Button>
        </div>

        {/* Controls */}
        <Card style={{ marginBottom: '2rem' }}>
          <div style={{ 
            display: 'grid', 
            gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))', 
            gap: '1rem',
            alignItems: 'end'
          }}>
            <div>
              <label style={{ 
                display: 'block', 
                marginBottom: '0.5rem', 
                fontSize: '0.875rem', 
                fontWeight: '600', 
                color: '#374151' 
              }}>
                View Mode
              </label>
              <div style={{ display: 'flex', gap: '0.5rem' }}>
                <Button 
                  variant={currentView === 'calendar' ? 'primary' : 'outline'}
                  size="sm"
                  onClick={() => setCurrentView('calendar')}
                >
                  üìÖ Calendar
                </Button>
                <Button 
                  variant={currentView === 'list' ? 'primary' : 'outline'}
                  size="sm"
                  onClick={() => setCurrentView('list')}
                >
                  üìã List
                </Button>
              </div>
            </div>

            <div>
              <label style={{ 
                display: 'block', 
                marginBottom: '0.5rem', 
                fontSize: '0.875rem', 
                fontWeight: '600', 
                color: '#374151' 
              }}>
                Date
              </label>
              <input
                type="date"
                value={selectedDate}
                onChange={(e) => setSelectedDate(e.target.value)}
                style={{
                  width: '100%',
                  padding: '0.75rem 1rem',
                  border: '1px solid #d1d5db',
                  borderRadius: '8px',
                  fontSize: '1rem',
                  backgroundColor: 'white',
                  color: '#374151'
                }}
              />
            </div>

            <TextInput
              placeholder="Search appointments..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              label="Search"
            />

            <div>
              <label style={{ 
                display: 'block', 
                marginBottom: '0.5rem', 
                fontSize: '0.875rem', 
                fontWeight: '600', 
                color: '#374151' 
              }}>
                Status Filter
              </label>
              <select
                value={filterStatus}
                onChange={(e) => setFilterStatus(e.target.value)}
                style={{
                  width: '100%',
                  padding: '0.75rem 1rem',
                  border: '1px solid #d1d5db',
                  borderRadius: '8px',
                  fontSize: '1rem',
                  backgroundColor: 'white',
                  color: '#374151'
                }}
              >
                <option value="all">All Status</option>
                <option value="SCHEDULED">Scheduled</option>
                <option value="ARRIVED">Arrived</option>
                <option value="IN_PROGRESS">In Progress</option>
                <option value="COMPLETED">Completed</option>
                <option value="CANCELLED">Cancelled</option>
                <option value="NO_SHOW">No Show</option>
              </select>
            </div>

            <div>
              <label style={{ 
                display: 'block', 
                marginBottom: '0.5rem', 
                fontSize: '0.875rem', 
                fontWeight: '600', 
                color: '#374151' 
              }}>
                Department
              </label>
              <select
                value={filterDepartment}
                onChange={(e) => setFilterDepartment(e.target.value)}
                style={{
                  width: '100%',
                  padding: '0.75rem 1rem',
                  border: '1px solid #d1d5db',
                  borderRadius: '8px',
                  fontSize: '1rem',
                  backgroundColor: 'white',
                  color: '#374151'
                }}
              >
                <option value="all">All Departments</option>
                {departments.map(dept => (
                  <option key={dept} value={dept}>{dept}</option>
                ))}
              </select>
            </div>
          </div>
        </Card>

        {/* Statistics */}
        <div style={{ 
          display: 'grid', 
          gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))', 
          gap: '1rem',
          marginBottom: '2rem'
        }}>
          <Card variant="bordered">
            <div style={{ textAlign: 'center' }}>
              <div style={{ fontSize: '2rem', fontWeight: 'bold', color: '#3b82f6' }}>
                {filteredAppointments.length}
              </div>
              <div style={{ fontSize: '0.875rem', color: '#6b7280' }}>Today&apos;s Appointments</div>
            </div>
          </Card>

          <Card variant="bordered">
            <div style={{ textAlign: 'center' }}>
              <div style={{ fontSize: '2rem', fontWeight: 'bold', color: '#10b981' }}>
                {filteredAppointments.filter(a => a.status === 'COMPLETED').length}
              </div>
              <div style={{ fontSize: '0.875rem', color: '#6b7280' }}>Completed</div>
            </div>
          </Card>

          <Card variant="bordered">
            <div style={{ textAlign: 'center' }}>
              <div style={{ fontSize: '2rem', fontWeight: 'bold', color: '#f59e0b' }}>
                {filteredAppointments.filter(a => a.status === 'IN_PROGRESS').length}
              </div>
              <div style={{ fontSize: '0.875rem', color: '#6b7280' }}>In Progress</div>
            </div>
          </Card>

          <Card variant="bordered">
            <div style={{ textAlign: 'center' }}>
              <div style={{ fontSize: '2rem', fontWeight: 'bold', color: '#ef4444' }}>
                {filteredAppointments.filter(a => a.status === 'CANCELLED').length}
              </div>
              <div style={{ fontSize: '0.875rem', color: '#6b7280' }}>Cancelled</div>
            </div>
          </Card>
        </div>

        {/* Main Content */}
        {currentView === 'calendar' ? (
          <CalendarView />
        ) : (
          <div>
            <div style={{ 
              display: 'flex', 
              justifyContent: 'space-between', 
              alignItems: 'center', 
              marginBottom: '1rem' 
            }}>
              <h2 style={{ fontSize: '1.25rem', fontWeight: '600', color: '#1f2937' }}>
                Appointments ({filteredAppointments.length})
              </h2>
              <div style={{ display: 'flex', gap: '0.5rem' }}>
                <Button variant="secondary" size="sm">
                  Export
                </Button>
                <Button variant="secondary" size="sm">
                  Print Schedule
                </Button>
              </div>
            </div>

            {filteredAppointments.length > 0 ? (
              filteredAppointments
                .sort((a, b) => new Date(a.startTime).getTime() - new Date(b.startTime).getTime())
                .map(appointment => (
                  <AppointmentCard key={appointment.id} appointment={appointment} />
                ))
            ) : (
              <Card>
                <div style={{ textAlign: 'center', padding: '2rem' }}>
                  <div style={{ fontSize: '3rem', marginBottom: '1rem' }}>üìÖ</div>
                  <h3 style={{ fontSize: '1.25rem', fontWeight: '600', color: '#1f2937', marginBottom: '0.5rem' }}>
                    No appointments found
                  </h3>
                  <p style={{ color: '#6b7280', marginBottom: '1rem' }}>
                    No appointments match your current search criteria for the selected date.
                  </p>
                  <Button onClick={() => window.location.href = '/appointments/new'}>
                    Schedule New Appointment
                  </Button>
                </div>
              </Card>
            )}
          </div>
        )}
      </div>
    </Layout>
  );
};

export default AppointmentsPage;

'use client';

import React, { useState, useEffect } from 'react';
import {
  Container,
  Grid,
  Paper,
  Text,
  Group,
  Badge,
  SimpleGrid,
  Stack,
  Button,
  Title,
  Card,
  TextInput,
  Select,
  LoadingOverlay,
  Alert,
  Tabs,
  Table,
  ActionIcon,
  Menu
} from '@mantine/core';
import { useDisclosure } from '@mantine/hooks';
import { notifications } from '@mantine/notifications';
import { DatePickerInput } from '@mantine/dates';
import {
  IconPlus,
  IconSearch,
  IconCurrencyRupee,
  IconReceipt,
  IconCash,
  IconEdit,
  IconTrash,
  IconEye,
  IconDotsVertical,
  IconCalendar,
  IconTrendingUp,
  IconAlertCircle,
  IconCheck,
  IconClock
} from '@tabler/icons-react';
import Layout from '../../components/shared/Layout';
import DataTable from '../../components/shared/DataTable';
import InvoiceForm from '../../components/billing/InvoiceForm';
import PaymentForm from '../../components/billing/PaymentForm';
import InvoiceDetails from '../../components/billing/InvoiceDetails';
import { useAppStore } from '../../stores/appStore';
import { User, UserRole, TableColumn } from '../../types/common';
import billingService from '../../services/billing.service';
import patientsService from '../../services/patients.service';
import type { CreateInvoiceDto, CreatePaymentDto, InvoiceFilters, PaymentFilters } from '../../services/billing.service';

const mockUser: User = {
  id: '1',
  username: 'admin',
  email: 'admin@hospital.com',
  firstName: 'Admin',
  lastName: 'User',
  role: UserRole.ADMIN,
  permissions: [],
  isActive: true,
  tenantInfo: {
    tenantId: 'T001',
    tenantName: 'Main Hospital',
  },
  createdAt: new Date(),
  updatedAt: new Date(),
};

function BillingPage() {
  const { user, setUser } = useAppStore();
  const [activeTab, setActiveTab] = useState('invoices');
  const [invoices, setInvoices] = useState<any[]>([]);
  const [payments, setPayments] = useState<any[]>([]);
  const [patients, setPatients] = useState<any[]>([]);
  const [stats, setStats] = useState<any>(null);
  const [loading, setLoading] = useState(true);
  const [selectedInvoice, setSelectedInvoice] = useState<any>(null);
  const [searchQuery, setSearchQuery] = useState('');
  const [statusFilter, setStatusFilter] = useState('');
  const [dateFilter, setDateFilter] = useState<Date | null>(null);

  const [invoiceFormOpened, { open: openInvoiceForm, close: closeInvoiceForm }] = useDisclosure(false);
  const [paymentFormOpened, { open: openPaymentForm, close: closePaymentForm }] = useDisclosure(false);
  const [detailsOpened, { open: openDetails, close: closeDetails }] = useDisclosure(false);

  useEffect(() => {
    if (!user) {
      setUser(mockUser);
    }
    fetchInvoices();
    fetchPayments();
    fetchStats();
    fetchPatients();
  }, [user, setUser]);

  const fetchInvoices = async () => {
    setLoading(true);
    try {
      const filters: InvoiceFilters = {};
      if (searchQuery) filters.search = searchQuery;
      if (statusFilter) filters.status = statusFilter;
      if (dateFilter) {
        filters.startDate = dateFilter.toISOString();
        filters.endDate = dateFilter.toISOString();
      }

      const response = await billingService.getInvoices(filters);
      if (response.success && response.data) {
        setInvoices(response.data || []);
      }
    } catch (error: any) {
      console.error('Error fetching invoices:', error);
      notifications.show({
        title: 'Error',
        message: error?.message || 'Failed to fetch invoices',
        color: 'red',
      });
    } finally {
      setLoading(false);
    }
  };

  const fetchPayments = async () => {
    try {
      const response = await billingService.getPayments();
      if (response.success && response.data) {
        setPayments(response.data || []);
      }
    } catch (error: any) {
      console.error('Error fetching payments:', error);
    }
  };

  const fetchStats = async () => {
    try {
      const response = await billingService.getBillingStats();
      if (response.success && response.data) {
        setStats(response.data);
      }
    } catch (error: any) {
      console.error('Error fetching stats:', error);
    }
  };

  const fetchPatients = async () => {
    try {
      const response = await patientsService.getPatients();
      if (response.success && response.data) {
        setPatients(response.data.patients || []);
      }
    } catch (error: any) {
      console.error('Error fetching patients:', error);
    }
  };

  const handleCreateInvoice = async (data: CreateInvoiceDto) => {
    try {
      const response = await billingService.createInvoice(data);
      if (response.success) {
        notifications.show({
          title: 'Success',
          message: 'Invoice created successfully',
          color: 'green',
        });
        fetchInvoices();
        fetchStats();
        closeInvoiceForm();
      }
    } catch (error: any) {
      console.error('Error creating invoice:', error);
      notifications.show({
        title: 'Error',
        message: error?.message || 'Failed to create invoice',
        color: 'red',
      });
      throw error;
    }
  };

  const handleUpdateInvoice = async (data: any) => {
    if (!selectedInvoice) return;

    try {
      const response = await billingService.updateInvoice(selectedInvoice.id, data);
      if (response.success) {
        notifications.show({
          title: 'Success',
          message: 'Invoice updated successfully',
          color: 'green',
        });
        fetchInvoices();
        fetchStats();
        closeInvoiceForm();
        setSelectedInvoice(null);
      }
    } catch (error: any) {
      console.error('Error updating invoice:', error);
      notifications.show({
        title: 'Error',
        message: error?.message || 'Failed to update invoice',
        color: 'red',
      });
      throw error;
    }
  };

  const handleCancelInvoice = async (invoice: any) => {
    if (!window.confirm('Are you sure you want to cancel this invoice?')) {
      return;
    }

    try {
      const response = await billingService.cancelInvoice(invoice.id);
      if (response.success) {
        notifications.show({
          title: 'Success',
          message: 'Invoice cancelled successfully',
          color: 'green',
        });
        fetchInvoices();
        fetchStats();
      }
    } catch (error: any) {
      console.error('Error cancelling invoice:', error);
      notifications.show({
        title: 'Error',
        message: error?.message || 'Failed to cancel invoice',
        color: 'red',
      });
    }
  };

  const handleCreatePayment = async (data: CreatePaymentDto) => {
    try {
      const response = await billingService.createPayment(data);
      if (response.success) {
        notifications.show({
          title: 'Success',
          message: 'Payment recorded successfully',
          color: 'green',
        });
        fetchInvoices();
        fetchPayments();
        fetchStats();
        closePaymentForm();
      }
    } catch (error: any) {
      console.error('Error recording payment:', error);
      notifications.show({
        title: 'Error',
        message: error?.message || 'Failed to record payment',
        color: 'red',
      });
      throw error;
    }
  };

  const handleViewInvoice = (invoice: any) => {
    setSelectedInvoice(invoice);
    openDetails();
  };

  const handleEditInvoice = (invoice: any) => {
    setSelectedInvoice(invoice);
    openInvoiceForm();
  };

  const handleRecordPayment = (invoice: any) => {
    setSelectedInvoice(invoice);
    openPaymentForm();
  };

  const handleNewInvoice = () => {
    setSelectedInvoice(null);
    openInvoiceForm();
  };

  const getStatusColor = (status: string) => {
    const colors: Record<string, string> = {
      PENDING: 'yellow',
      PARTIALLY_PAID: 'orange',
      PAID: 'green',
      OVERDUE: 'red',
      CANCELLED: 'gray'
    };
    return colors[status] || 'gray';
  };

  const formatCurrency = (amount: number) => {
    return `‚Çπ${amount.toFixed(2)}`;
  };

  const formatDate = (dateString: string) => {
    const date = new Date(dateString);
    return date.toLocaleDateString();
  };

  const invoiceColumns: TableColumn[] = [
    {
      key: 'invoiceNumber',
      title: 'Invoice #',
      sortable: true,
      render: (invoice: any) => (
        <Text fw={600}>{invoice.invoiceNumber}</Text>
      )
    },
    {
      key: 'patient',
      title: 'Patient',
      sortable: true,
      render: (invoice: any) => (
        <div>
          <Text fw={500}>
            {invoice.patient?.firstName} {invoice.patient?.lastName}
          </Text>
          <Text size="xs" c="dimmed">
            {invoice.patient?.medicalRecordNumber || invoice.patient?.id}
          </Text>
        </div>
      )
    },
    {
      key: 'date',
      title: 'Date',
      sortable: true,
      render: (invoice: any) => (
        <Text>{formatDate(invoice.date)}</Text>
      )
    },
    {
      key: 'dueDate',
      title: 'Due Date',
      sortable: true,
      render: (invoice: any) => (
        <Text>{formatDate(invoice.dueDate)}</Text>
      )
    },
    {
      key: 'totalAmount',
      title: 'Amount',
      sortable: true,
      render: (invoice: any) => (
        <Text fw={600}>{formatCurrency(invoice.totalAmount)}</Text>
      )
    },
    {
      key: 'status',
      title: 'Status',
      sortable: true,
      render: (invoice: any) => (
        <Badge color={getStatusColor(invoice.status)}>
          {invoice.status}
        </Badge>
      )
    },
    {
      key: 'actions',
      title: 'Actions',
      render: (invoice: any) => (
        <Group gap="xs">
          <ActionIcon
            variant="subtle"
            onClick={() => handleViewInvoice(invoice)}
          >
            <IconEye size={16} />
          </ActionIcon>
          {invoice.status === 'PENDING' && (
            <ActionIcon
              variant="subtle"
              onClick={() => handleEditInvoice(invoice)}
            >
              <IconEdit size={16} />
            </ActionIcon>
          )}
          {invoice.status !== 'PAID' && invoice.status !== 'CANCELLED' && (
            <ActionIcon
              variant="subtle"
              color="green"
              onClick={() => handleRecordPayment(invoice)}
            >
              <IconCash size={16} />
            </ActionIcon>
          )}
          <Menu position="bottom-end">
            <Menu.Target>
              <ActionIcon variant="subtle">
                <IconDotsVertical size={16} />
              </ActionIcon>
            </Menu.Target>
            <Menu.Dropdown>
              <Menu.Item
                leftSection={<IconEye size={14} />}
                onClick={() => handleViewInvoice(invoice)}
              >
                View Details
              </Menu.Item>
              {invoice.status === 'PENDING' && (
                <Menu.Item
                  leftSection={<IconEdit size={14} />}
                  onClick={() => handleEditInvoice(invoice)}
                >
                  Edit
                </Menu.Item>
              )}
              {invoice.status !== 'PAID' && invoice.status !== 'CANCELLED' && (
                <>
                  <Menu.Item
                    leftSection={<IconCash size={14} />}
                    onClick={() => handleRecordPayment(invoice)}
                  >
                    Record Payment
                  </Menu.Item>
                  <Menu.Divider />
                  <Menu.Item
                    leftSection={<IconTrash size={14} />}
                    color="red"
                    onClick={() => handleCancelInvoice(invoice)}
                  >
                    Cancel Invoice
                  </Menu.Item>
                </>
              )}
            </Menu.Dropdown>
          </Menu>
        </Group>
      )
    }
  ];

  const paymentColumns: TableColumn[] = [
    {
      key: 'paymentNumber',
      title: 'Payment #',
      sortable: true,
      render: (payment: any) => (
        <Text fw={600}>{payment.paymentNumber}</Text>
      )
    },
    {
      key: 'invoice',
      title: 'Invoice',
      render: (payment: any) => (
        <Text>{payment.invoice?.invoiceNumber}</Text>
      )
    },
    {
      key: 'patient',
      title: 'Patient',
      render: (payment: any) => (
        <Text>
          {payment.invoice?.patient?.firstName} {payment.invoice?.patient?.lastName}
        </Text>
      )
    },
    {
      key: 'amount',
      title: 'Amount',
      sortable: true,
      render: (payment: any) => (
        <Text fw={600}>{formatCurrency(payment.amount)}</Text>
      )
    },
    {
      key: 'paymentMethod',
      title: 'Method',
      render: (payment: any) => (
        <Badge variant="light">{payment.paymentMethod}</Badge>
      )
    },
    {
      key: 'paymentDate',
      title: 'Date',
      sortable: true,
      render: (payment: any) => (
        <Text>{formatDate(payment.paymentDate)}</Text>
      )
    },
    {
      key: 'status',
      title: 'Status',
      render: (payment: any) => (
        <Badge color={payment.status === 'COMPLETED' ? 'green' : 'gray'}>
          {payment.status}
        </Badge>
      )
    }
  ];

  const currentUser = user || mockUser;
  const userForLayout = {
    ...currentUser,
    name: `${currentUser.firstName} ${currentUser.lastName}`,
  };

  return (
    <Layout user={userForLayout} notifications={0} onLogout={() => {}}>
      <Container size="xl" py="xl">
        <Stack gap="lg">
          {/* Header */}
          <Group justify="space-between">
            <div>
              <Title order={2}>Billing & Invoicing</Title>
              <Text c="dimmed" size="sm">
                Manage invoices, payments, and billing operations
              </Text>
            </div>
            <Button
              leftSection={<IconPlus size={16} />}
              onClick={handleNewInvoice}
            >
              Create Invoice
            </Button>
          </Group>

          {/* Statistics Cards */}
          {stats && (
            <SimpleGrid cols={{ base: 1, sm: 2, md: 4 }}>
              <Card withBorder padding="lg">
                <Group justify="space-between">
                  <div>
                    <Text c="dimmed" size="xs" tt="uppercase" fw={700}>
                      Total Revenue
                    </Text>
                    <Text fw={700} size="xl">
                      ‚Çπ{stats.monthlyRevenue.toFixed(2)}
                    </Text>
                    <Text size="xs" c="dimmed">This month</Text>
                  </div>
                  <IconTrendingUp size={32} color="#40c057" />
                </Group>
              </Card>

              <Card withBorder padding="lg">
                <Group justify="space-between">
                  <div>
                    <Text c="dimmed" size="xs" tt="uppercase" fw={700}>
                      Pending
                    </Text>
                    <Text fw={700} size="xl">
                      {stats.pendingInvoices}
                    </Text>
                    <Text size="xs" c="dimmed">Invoices</Text>
                  </div>
                  <IconClock size={32} color="#fab005" />
                </Group>
              </Card>

              <Card withBorder padding="lg">
                <Group justify="space-between">
                  <div>
                    <Text c="dimmed" size="xs" tt="uppercase" fw={700}>
                      Paid
                    </Text>
                    <Text fw={700} size="xl">
                      {stats.paidInvoices}
                    </Text>
                    <Text size="xs" c="dimmed">Invoices</Text>
                  </div>
                  <IconCheck size={32} color="#40c057" />
                </Group>
              </Card>

              <Card withBorder padding="lg">
                <Group justify="space-between">
                  <div>
                    <Text c="dimmed" size="xs" tt="uppercase" fw={700}>
                      Today
                    </Text>
                    <Text fw={700} size="xl">
                      ‚Çπ{stats.todayRevenue.toFixed(2)}
                    </Text>
                    <Text size="xs" c="dimmed">Revenue</Text>
                  </div>
                  <IconCurrencyRupee size={32} color="#228be6" />
                </Group>
              </Card>
            </SimpleGrid>
          )}

          {/* Tabs */}
          <Tabs value={activeTab} onChange={(value) => setActiveTab(value || 'invoices')}>
            <Tabs.List>
              <Tabs.Tab value="invoices" leftSection={<IconReceipt size={16} />}>
                Invoices
              </Tabs.Tab>
              <Tabs.Tab value="payments" leftSection={<IconCash size={16} />}>
                Payments
              </Tabs.Tab>
            </Tabs.List>

            <Tabs.Panel value="invoices" pt="md">
              {/* Filters */}
              <Paper withBorder p="md" mb="md">
                <Grid>
                  <Grid.Col span={{ base: 12, sm: 6, md: 3 }}>
                    <TextInput
                      placeholder="Search invoices..."
                      leftSection={<IconSearch size={16} />}
                      value={searchQuery}
                      onChange={(e) => setSearchQuery(e.target.value)}
                    />
                  </Grid.Col>
                  <Grid.Col span={{ base: 12, sm: 6, md: 3 }}>
                    <Select
                      placeholder="Filter by status"
                      data={[
                        { value: '', label: 'All Statuses' },
                        { value: 'PENDING', label: 'Pending' },
                        { value: 'PARTIALLY_PAID', label: 'Partially Paid' },
                        { value: 'PAID', label: 'Paid' },
                        { value: 'CANCELLED', label: 'Cancelled' }
                      ]}
                      value={statusFilter}
                      onChange={(value) => setStatusFilter(value || '')}
                    />
                  </Grid.Col>
                  <Grid.Col span={{ base: 12, sm: 6, md: 3 }}>
                    <DatePickerInput
                      placeholder="Filter by date"
                      leftSection={<IconCalendar size={16} />}
                      value={dateFilter}
                      onChange={(value) => setDateFilter(value as unknown as Date | null)}
                      clearable
                    />
                  </Grid.Col>
                  <Grid.Col span={{ base: 12, sm: 6, md: 3 }}>
                    <Button fullWidth onClick={fetchInvoices}>
                      Apply Filters
                    </Button>
                  </Grid.Col>
                </Grid>
              </Paper>

              {/* Invoices Table */}
              <Paper withBorder>
                <LoadingOverlay visible={loading} />
                {invoices.length === 0 && !loading ? (
                  <Alert icon={<IconAlertCircle size={16} />} title="No invoices found" color="blue">
                    No invoices match your current filters. Try adjusting your search criteria or create a new invoice.
                  </Alert>
                ) : (
                  <DataTable
                    columns={invoiceColumns}
                    data={invoices}
                    loading={loading}
                  />
                )}
              </Paper>
            </Tabs.Panel>

            <Tabs.Panel value="payments" pt="md">
              {/* Payments Table */}
              <Paper withBorder>
                <LoadingOverlay visible={loading} />
                {payments.length === 0 && !loading ? (
                  <Alert icon={<IconAlertCircle size={16} />} title="No payments found" color="blue">
                    No payment records available.
                  </Alert>
                ) : (
                  <DataTable
                    columns={paymentColumns}
                    data={payments}
                    loading={loading}
                  />
                )}
              </Paper>
            </Tabs.Panel>
          </Tabs>
        </Stack>
      </Container>

      {/* Invoice Form Modal */}
      <InvoiceForm
        opened={invoiceFormOpened}
        onClose={closeInvoiceForm}
        invoice={selectedInvoice}
        onSubmit={selectedInvoice ? handleUpdateInvoice : handleCreateInvoice}
        patients={patients}
      />

      {/* Payment Form Modal */}
      {selectedInvoice && (
        <PaymentForm
          opened={paymentFormOpened}
          onClose={closePaymentForm}
          invoice={selectedInvoice}
          onSubmit={handleCreatePayment}
        />
      )}

      {/* Invoice Details Modal */}
      {selectedInvoice && (
        <InvoiceDetails
          opened={detailsOpened}
          onClose={closeDetails}
          invoice={selectedInvoice}
          onEdit={handleEditInvoice}
          onDelete={handleCancelInvoice}
          onRecordPayment={handleRecordPayment}
        />
      )}
    </Layout>
  );
}

export default BillingPage;

'use client';

import React, { useState, useEffect } from 'react';
import {
  Container,
  Stack,
  Button,
  Title,
  Card,
  Tabs,
  ActionIcon,
  Group,
  Text,
  Badge,
  SimpleGrid,
  Paper,
  LoadingOverlay,
  Alert,
  TextInput,
  Select,
  Avatar,
  Menu
} from '@mantine/core';
import {
  IconPlus,
  IconMail,
  IconBell,
  IconTrash,
  IconMailOpened,
  IconEye,
  IconCheck,
  IconDotsVertical,
  IconInbox,
  IconSend,
  IconSearch,
  IconAlertCircle
} from '@tabler/icons-react';
import Layout from '../../components/shared/Layout';
import DataTable from '../../components/shared/DataTable';
import MessageForm from '../../components/communications/MessageForm';
import MessageDetails from '../../components/communications/MessageDetails';
import { useAppStore } from '../../stores/appStore';
import { User, UserRole, TableColumn } from '../../types/common';
import communicationsService from '../../services/communications.service';
import type { CreateMessageDto, MessageFilters, NotificationFilters } from '../../services/communications.service';

const mockUser: User = {
  id: '1',
  username: 'admin',
  email: 'admin@hospital.com',
  firstName: 'Admin',
  lastName: 'User',
  role: UserRole.ADMIN,
  permissions: [],
  isActive: true,
  tenantInfo: {
    tenantId: 'T001',
    tenantName: 'Main Hospital',
  },
  createdAt: new Date(),
  updatedAt: new Date(),
};

function CommunicationsPage() {
  const { user, setUser } = useAppStore();
  const [activeTab, setActiveTab] = useState('messages');
  const [messages, setMessages] = useState<any[]>([]);
  const [notificationsList, setNotificationsList] = useState<any[]>([]);
  const [users, setUsers] = useState<any[]>([]);
  const [stats, setStats] = useState<any>(null);
  const [loading, setLoading] = useState(true);
  const [selectedMessage, setSelectedMessage] = useState<any>(null);
  const [searchQuery, setSearchQuery] = useState('');
  const [statusFilter, setStatusFilter] = useState('');

  const [messageFormOpened, { open: openMessageForm, close: closeMessageForm }] = useDisclosure(false);
  const [detailsOpened, { open: openDetails, close: closeDetails }] = useDisclosure(false);

  useEffect(() => {
    if (!user) {
      setUser(mockUser);
    }
    fetchMessages();
    fetchNotifications();
    fetchStats();
    fetchUsers();
  }, [user, setUser]);

  const fetchMessages = async () => {
    setLoading(true);
    try {
      const filters: MessageFilters = {};
      if (searchQuery) filters.search = searchQuery;
      if (statusFilter === 'unread') filters.read = false;
      if (statusFilter === 'read') filters.read = true;

      const response = await communicationsService.getMessages(filters);
      if (response.success && response.data) {
        setMessages(response.data || []);
      }
    } catch (error: any) {
      console.error('Error fetching messages:', error);
      notifications.show({
        title: 'Error',
        message: error?.message || 'Failed to fetch messages',
        color: 'red',
      });
    } finally {
      setLoading(false);
    }
  };

  const fetchNotifications = async () => {
    try {
      const response = await communicationsService.getNotifications();
      if (response.success && response.data) {
        setNotificationsList(response.data || []);
      }
    } catch (error: any) {
      console.error('Error fetching notifications:', error);
    }
  };

  const fetchStats = async () => {
    try {
      const response = await communicationsService.getStats();
      if (response.success && response.data) {
        setStats(response.data);
      }
    } catch (error: any) {
      console.error('Error fetching stats:', error);
    }
  };

  const fetchUsers = async () => {
    try {
      // Mock users - in production, fetch from users API
      setUsers([
        { id: '2', firstName: 'John', lastName: 'Doe', email: 'john@hospital.com' },
        { id: '3', firstName: 'Jane', lastName: 'Smith', email: 'jane@hospital.com' },
      ]);
    } catch (error: any) {
      console.error('Error fetching users:', error);
    }
  };

  const handleSendMessage = async (data: CreateMessageDto) => {
    try {
      const response = await communicationsService.sendMessage(data);
      if (response.success) {
        notifications.show({
          title: 'Success',
          message: 'Message sent successfully',
          color: 'green',
        });
        fetchMessages();
        fetchStats();
        closeMessageForm();
      }
    } catch (error: any) {
      console.error('Error sending message:', error);
      notifications.show({
        title: 'Error',
        message: error?.message || 'Failed to send message',
        color: 'red',
      });
      throw error;
    }
  };

  const handleMarkAsRead = async (messageId: string) => {
    try {
      const response = await communicationsService.markMessageAsRead(messageId);
      if (response.success) {
        notifications.show({
          title: 'Success',
          message: 'Message marked as read',
          color: 'green',
        });
        fetchMessages();
        fetchStats();
      }
    } catch (error: any) {
      console.error('Error marking message as read:', error);
      notifications.show({
        title: 'Error',
        message: error?.message || 'Failed to mark message as read',
        color: 'red',
      });
    }
  };

  const handleDeleteMessage = async (message: any) => {
    if (!window.confirm('Are you sure you want to delete this message?')) {
      return;
    }

    try {
      const response = await communicationsService.deleteMessage(message.id);
      if (response.success) {
        notifications.show({
          title: 'Success',
          message: 'Message deleted successfully',
          color: 'green',
        });
        fetchMessages();
        fetchStats();
      }
    } catch (error: any) {
      console.error('Error deleting message:', error);
      notifications.show({
        title: 'Error',
        message: error?.message || 'Failed to delete message',
        color: 'red',
      });
    }
  };

  const handleMarkNotificationAsRead = async (notificationId: string) => {
    try {
      const response = await communicationsService.markNotificationAsRead(notificationId);
      if (response.success) {
        fetchNotifications();
        fetchStats();
      }
    } catch (error: any) {
      console.error('Error marking notification as read:', error);
    }
  };

  const handleMarkAllNotificationsAsRead = async () => {
    try {
      const response = await communicationsService.markAllNotificationsAsRead();
      if (response.success) {
        notifications.show({
          title: 'Success',
          message: 'All notifications marked as read',
          color: 'green',
        });
        fetchNotifications();
        fetchStats();
      }
    } catch (error: any) {
      console.error('Error marking all notifications as read:', error);
    }
  };

  const handleDeleteNotification = async (notificationId: string) => {
    try {
      const response = await communicationsService.deleteNotification(notificationId);
      if (response.success) {
        fetchNotifications();
        fetchStats();
      }
    } catch (error: any) {
      console.error('Error deleting notification:', error);
    }
  };

  const handleViewMessage = (message: any) => {
    setSelectedMessage(message);
    openDetails();
    if (!message.read) {
      handleMarkAsRead(message.id);
    }
  };

  const handleReplyMessage = (message: any) => {
    // Set recipient to sender of original message
    openMessageForm();
  };

  const getPriorityColor = (priority: string) => {
    const colors: Record<string, string> = {
      LOW: 'gray',
      NORMAL: 'blue',
      HIGH: 'orange',
      URGENT: 'red'
    };
    return colors[priority] || 'blue';
  };

  const getNotificationColor = (type: string) => {
    const colors: Record<string, string> = {
      SUCCESS: 'green',
      WARNING: 'yellow',
      ERROR: 'red',
      INFO: 'blue',
      APPOINTMENT: 'cyan',
      BILLING: 'orange',
      SYSTEM: 'gray'
    };
    return colors[type] || 'blue';
  };

  const formatDate = (dateString: string) => {
    const date = new Date(dateString);
    return date.toLocaleString();
  };

  const messageColumns: TableColumn[] = [
    {
      key: 'read',
      title: '',
      render: (message: any) => (
        <ActionIcon
          size="sm"
          variant="subtle"
          color={message.read ? 'gray' : 'blue'}
        >
          {message.read ? <IconMailOpened size={16} /> : <IconMail size={16} />}
        </ActionIcon>
      )
    },
    {
      key: 'sender',
      title: 'From',
      sortable: true,
      render: (message: any) => (
        <Group gap="xs">
          <Avatar size="sm" radius="xl" color="blue">
            {message.sender?.firstName?.[0]}{message.sender?.lastName?.[0]}
          </Avatar>
          <div>
            <Text fw={message.read ? 400 : 600} size="sm">
              {message.sender?.firstName} {message.sender?.lastName}
            </Text>
            <Text size="xs" c="dimmed">{message.sender?.email}</Text>
          </div>
        </Group>
      )
    },
    {
      key: 'subject',
      title: 'Subject',
      sortable: true,
      render: (message: any) => (
        <Text fw={message.read ? 400 : 600} lineClamp={1}>
          {message.subject}
        </Text>
      )
    },
    {
      key: 'priority',
      title: 'Priority',
      sortable: true,
      render: (message: any) => (
        <Badge size="sm" color={getPriorityColor(message.priority)}>
          {message.priority}
        </Badge>
      )
    },
    {
      key: 'createdAt',
      title: 'Date',
      sortable: true,
      render: (message: any) => (
        <Text size="sm">{formatDate(message.createdAt)}</Text>
      )
    },
    {
      key: 'actions',
      title: 'Actions',
      render: (message: any) => (
        <Group gap="xs">
          <ActionIcon
            variant="subtle"
            onClick={() => handleViewMessage(message)}
          >
            <IconEye size={16} />
          </ActionIcon>
          {!message.read && (
            <ActionIcon
              variant="subtle"
              color="green"
              onClick={() => handleMarkAsRead(message.id)}
            >
              <IconCheck size={16} />
            </ActionIcon>
          )}
          <Menu position="bottom-end">
            <Menu.Target>
              <ActionIcon variant="subtle">
                <IconDotsVertical size={16} />
              </ActionIcon>
            </Menu.Target>
            <Menu.Dropdown>
              <Menu.Item
                leftSection={<IconEye size={14} />}
                onClick={() => handleViewMessage(message)}
              >
                View
              </Menu.Item>
              {!message.read && (
                <Menu.Item
                  leftSection={<IconCheck size={14} />}
                  onClick={() => handleMarkAsRead(message.id)}
                >
                  Mark as Read
                </Menu.Item>
              )}
              <Menu.Divider />
              <Menu.Item
                leftSection={<IconTrash size={14} />}
                color="red"
                onClick={() => handleDeleteMessage(message)}
              >
                Delete
              </Menu.Item>
            </Menu.Dropdown>
          </Menu>
        </Group>
      )
    }
  ];

  const notificationColumns: TableColumn[] = [
    {
      key: 'title',
      title: 'Title',
      sortable: true,
      render: (notification: any) => (
        <Text fw={notification.read ? 400 : 600}>
          {notification.title}
        </Text>
      )
    },
    {
      key: 'message',
      title: 'Message',
      render: (notification: any) => (
        <Text lineClamp={2}>{notification.message}</Text>
      )
    },
    {
      key: 'type',
      title: 'Type',
      sortable: true,
      render: (notification: any) => (
        <Badge size="sm" color={getNotificationColor(notification.type)}>
          {notification.type}
        </Badge>
      )
    },
    {
      key: 'createdAt',
      title: 'Date',
      sortable: true,
      render: (notification: any) => (
        <Text size="sm">{formatDate(notification.createdAt)}</Text>
      )
    },
    {
      key: 'actions',
      title: 'Actions',
      render: (notification: any) => (
        <Group gap="xs">
          {!notification.read && (
            <ActionIcon
              variant="subtle"
              color="green"
              onClick={() => handleMarkNotificationAsRead(notification.id)}
            >
              <IconCheck size={16} />
            </ActionIcon>
          )}
          <ActionIcon
            variant="subtle"
            color="red"
            onClick={() => handleDeleteNotification(notification.id)}
          >
            <IconTrash size={16} />
          </ActionIcon>
        </Group>
      )
    }
  ];

  const layoutUser = user || mockUser;
  const userForLayout = {
    id: layoutUser.id,
    name: `${layoutUser.firstName} ${layoutUser.lastName}`,
    email: layoutUser.email,
    role: layoutUser.role,
  };

  return (
    <Layout user={userForLayout} notifications={0} onLogout={() => {}}>
      <Container size="xl" py="xl">
        <Stack gap="lg">
          {/* Header */}
          <Group justify="space-between">
            <div>
              <Title order={2}>Communications</Title>
              <Text c="dimmed" size="sm">
                Manage messages and notifications
              </Text>
            </div>
            <Button
              leftSection={<IconPlus size={16} />}
              onClick={openMessageForm}
            >
              New Message
            </Button>
          </Group>

          {/* Statistics Cards */}
          {stats && (
            <SimpleGrid cols={{ base: 1, sm: 2, md: 4 }}>
              <Card withBorder padding="lg">
                <Group justify="space-between">
                  <div>
                    <Text c="dimmed" size="xs" tt="uppercase" fw={700}>
                      Unread Messages
                    </Text>
                    <Text fw={700} size="xl">
                      {stats.unreadMessages}
                    </Text>
                  </div>
                  <IconMail size={32} color="#228be6" />
                </Group>
              </Card>

              <Card withBorder padding="lg">
                <Group justify="space-between">
                  <div>
                    <Text c="dimmed" size="xs" tt="uppercase" fw={700}>
                      Total Messages
                    </Text>
                    <Text fw={700} size="xl">
                      {stats.totalMessages}
                    </Text>
                  </div>
                  <IconInbox size={32} color="#12b886" />
                </Group>
              </Card>

              <Card withBorder padding="lg">
                <Group justify="space-between">
                  <div>
                    <Text c="dimmed" size="xs" tt="uppercase" fw={700}>
                      Unread Notifications
                    </Text>
                    <Text fw={700} size="xl">
                      {stats.unreadNotifications}
                    </Text>
                  </div>
                  <IconBell size={32} color="#fab005" />
                </Group>
              </Card>

              <Card withBorder padding="lg">
                <Group justify="space-between">
                  <div>
                    <Text c="dimmed" size="xs" tt="uppercase" fw={700}>
                      Sent Messages
                    </Text>
                    <Text fw={700} size="xl">
                      {stats.sentMessages}
                    </Text>
                  </div>
                  <IconSend size={32} color="#40c057" />
                </Group>
              </Card>
            </SimpleGrid>
          )}

          {/* Tabs */}
          <Tabs value={activeTab} onChange={(value) => setActiveTab(value || 'messages')}>
            <Tabs.List>
              <Tabs.Tab value="messages" leftSection={<IconMail size={16} />}>
                Messages
              </Tabs.Tab>
              <Tabs.Tab value="notifications" leftSection={<IconBell size={16} />}>
                Notifications
              </Tabs.Tab>
            </Tabs.List>

            <Tabs.Panel value="messages" pt="md">
              {/* Filters */}
              <Paper withBorder p="md" mb="md">
                <Grid>
                  <Grid.Col span={{ base: 12, sm: 6, md: 4 }}>
                    <TextInput
                      placeholder="Search messages..."
                      leftSection={<IconSearch size={16} />}
                      value={searchQuery}
                      onChange={(e) => setSearchQuery(e.target.value)}
                    />
                  </Grid.Col>
                  <Grid.Col span={{ base: 12, sm: 6, md: 4 }}>
                    <Select
                      placeholder="Filter by status"
                      data={[
                        { value: '', title: 'All Messages' },
                        { value: 'unread', title: 'Unread' },
                        { value: 'read', title: 'Read' }
                      ]}
                      value={statusFilter}
                      onChange={(value) => setStatusFilter(value || '')}
                    />
                  </Grid.Col>
                  <Grid.Col span={{ base: 12, sm: 6, md: 4 }}>
                    <Button fullWidth onClick={fetchMessages}>
                      Apply Filters
                    </Button>
                  </Grid.Col>
                </Grid>
              </Paper>

              {/* Messages Table */}
              <Paper withBorder>
                <LoadingOverlay visible={loading} />
                {messages.length === 0 && !loading ? (
                  <Alert icon={<IconAlertCircle size={16} />} title="No messages found" color="blue">
                    No messages match your current filters.
                  </Alert>
                ) : (
                  <DataTable
                    columns={messageColumns}
                    data={messages}
                    loading={loading}
                  />
                )}
              </Paper>
            </Tabs.Panel>

            <Tabs.Panel value="notifications" pt="md">
              {/* Notifications Header */}
              <Group justify="space-between" mb="md">
                <Text size="sm" c="dimmed">
                  {notificationsList.length} notifications
                </Text>
                {stats && stats.unreadNotifications > 0 && (
                  <Button
                    size="xs"
                    variant="subtle"
                    leftSection={<IconCheck size={14} />}
                    onClick={handleMarkAllNotificationsAsRead}
                  >
                    Mark All as Read
                  </Button>
                )}
              </Group>

              {/* Notifications Table */}
              <Paper withBorder>
                <LoadingOverlay visible={loading} />
                {notificationsList.length === 0 && !loading ? (
                  <Alert icon={<IconAlertCircle size={16} />} title="No notifications" color="blue">
                    You have no notifications.
                  </Alert>
                ) : (
                  <DataTable
                    columns={notificationColumns}
                    data={notificationsList}
                    loading={loading}
                  />
                )}
              </Paper>
            </Tabs.Panel>
          </Tabs>
        </Stack>
      </Container>

      {/* Message Form Modal */}
      <MessageForm
        opened={messageFormOpened}
        onClose={closeMessageForm}
        onSubmit={handleSendMessage}
        users={users}
      />

      {/* Message Details Modal */}
      {selectedMessage && (
        <MessageDetails
          opened={detailsOpened}
          onClose={closeDetails}
          message={selectedMessage}
          onDelete={handleDeleteMessage}
          onReply={handleReplyMessage}
        />
      )}
    </Layout>
  );
}

export default CommunicationsPage;

'use client';
import Link from 'next/link';
import { usePathname } from 'next/navigation';
import { useState, useEffect, ReactNode } from 'react';
import type React from 'react';

interface LayoutProps {
  children: ReactNode;
}

interface MenuItem {
  title: string;
  href: string;
  icon: string;
  roles: string[];
  badge?: string;
}

const ResponsiveLayout = ({ children }: LayoutProps) => {
