// Types
interface AIInsight {
  id: string;
  type: 'diagnosis' | 'treatment' | 'drug-interaction' | 'risk-assessment' | 'recommendation' | 'alert';
  title: string;
  description: string;
  confidence: number; // 0-100
  severity: 'low' | 'medium' | 'high' | 'critical';
  category: 'clinical' | 'medication' | 'diagnostic' | 'preventive' | 'emergency';
  patientId?: string;
  patientName?: string;
  generatedBy: string; // AI model name
  generatedDate: string;
  reviewedBy?: string;
  reviewedDate?: string;
  status: 'pending' | 'reviewed' | 'accepted' | 'rejected' | 'implemented';
  evidence: Array<{
    source: string;
    relevance: number;
    description: string;
  }>;
  recommendations: Array<{
    action: string;
    priority: 'low' | 'medium' | 'high';
    timeframe: string;
  }>;
  metadata: {
    symptoms?: string[];
    vitals?: Record<string, any>;
    labResults?: Record<string, any>;
    medications?: string[];
    allergies?: string[];
  };
}

interface AIQuery {
  id: string;
  question: string;
  context: {
    patientId?: string;
    symptoms?: string[];
    vitals?: Record<string, any>;
    history?: string[];
  };
  response: string;
  confidence: number;
  model: string;
  timestamp: string;
  userId: string;
  userName: string;
  category: 'diagnosis' | 'treatment' | 'medication' | 'general' | 'emergency';
  feedback?: {
    rating: number;
    helpful: boolean;
    comments?: string;
  };
}

interface ClinicalGuideline {
  id: string;
  title: string;
  category: 'diagnosis' | 'treatment' | 'prevention' | 'emergency';
  condition: string;
  description: string;
  recommendations: Array<{
    level: 'A' | 'B' | 'C';
    strength: 'strong' | 'moderate' | 'weak';
    recommendation: string;
    evidence: string;
  }>;
  lastUpdated: string;
  source: string;
  version: string;
  applicability: string[];
}

interface DrugInteraction {
  id: string;
  drug1: string;
  drug2: string;
  interactionType: 'major' | 'moderate' | 'minor';
  mechanism: string;
  clinicalEffect: string;
  recommendation: string;
  frequency: 'common' | 'uncommon' | 'rare';
  onset: 'rapid' | 'delayed';
  severity: 'severe' | 'moderate' | 'mild';
  documentation: 'excellent' | 'good' | 'fair' | 'poor';
}

interface MedicalKnowledgeBase {
  id: string;
  title: string;
  category: 'disease' | 'symptom' | 'procedure' | 'medication' | 'anatomy';
  content: string;
  tags: string[];
  lastUpdated: string;
  references: Array<{
    title: string;
    authors: string[];
    journal: string;
    year: number;
    doi?: string;
  }>;
}

