    interactionType: 'moderate',
    mechanism: 'Increased risk of lactic acidosis in presence of contrast-induced nephropathy',
    clinicalEffect: 'Potential for lactic acidosis if kidney function deteriorates after contrast exposure',
    recommendation: 'Hold metformin in patients with eGFR <30. Monitor renal function post-contrast. Resume metformin when creatinine stable.',
    frequency: 'uncommon',
    onset: 'delayed',
    severity: 'severe',
    documentation: 'good'
  },
  {
    id: '3',
    drug1: 'Digoxin',
    drug2: 'Amiodarone',
    interactionType: 'major',
    mechanism: 'Amiodarone inhibits P-glycoprotein, reducing digoxin clearance',
    clinicalEffect: 'Significantly increased digoxin levels leading to potential toxicity',
    recommendation: 'Reduce digoxin dose by 50% when starting amiodarone. Monitor digoxin levels closely.',
    frequency: 'common',
    onset: 'delayed',
    severity: 'severe',
    documentation: 'excellent'
  }
];

const AIAssistant = () => {
  // State management
  const [activeTab, setActiveTab] = useState<string>('insights');
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedCategory, setSelectedCategory] = useState<string>('');
  const [selectedSeverity, setSelectedSeverity] = useState<string>('');
  const [selectedInsight, setSelectedInsight] = useState<AIInsight | null>(null);
  const [_selectedQuery, setSelectedQuery] = useState<AIQuery | null>(null);
  const [newQuery, setNewQuery] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  // Modal states
  const [insightDetailOpened, { open: openInsightDetail, close: closeInsightDetail }] = useDisclosure(false);
  const [_queryDetailOpened, { open: _openQueryDetail, close: _closeQueryDetail }] = useDisclosure(false);
  const [askAIOpened, { open: openAskAI, close: closeAskAI }] = useDisclosure(false);
  const [_guidelineOpened, { open: _openGuideline, close: _closeGuideline }] = useDisclosure(false);

  // Filter insights
  const filteredInsights = useMemo(() => {
    return [].filter /* TODO: Fetch from API */((insight) => {
      const matchesSearch = 
        insight.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
        insight.description.toLowerCase().includes(searchQuery.toLowerCase()) ||
        (insight.patientName && insight.patientName.toLowerCase().includes(searchQuery.toLowerCase()));

      const matchesCategory = !selectedCategory || insight.category === selectedCategory;
      const matchesSeverity = !selectedSeverity || insight.severity === selectedSeverity;

      return matchesSearch && matchesCategory && matchesSeverity;
    });
  }, [searchQuery, selectedCategory, selectedSeverity]);

  // Filter queries
  const filteredQueries = useMemo(() => {
    return [].filter /* TODO: Fetch from API */((query) => {
      const matchesSearch = 
        query.question.toLowerCase().includes(searchQuery.toLowerCase()) ||
        query.response.toLowerCase().includes(searchQuery.toLowerCase());

      return matchesSearch;
    });
  }, [searchQuery]);

  const handleViewInsight = (insight: AIInsight) => {
    setSelectedInsight(insight);
    openInsightDetail();
  };

  const handleViewQuery = (_query: AIQuery) => {
    setSelectedQuery(_query);
    _openQueryDetail();
  };

  const handleAskAI = async () => {
    if (!newQuery.trim()) return;

    setIsLoading(true);
    // Simulate AI processing
    setTimeout(() => {
      setIsLoading(false);
      notifications.show({
        title: 'AI Response Generated',
        message: 'Your question has been processed and the response is ready',
        color: 'green',
      });
      closeAskAI();
      setNewQuery('');
    }, 3000);
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('en-IN', {
      year: 'numeric',
      month: 'short',
      day: 'numeric'
    });
  };

  const formatDateTime = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('en-IN', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  const getSeverityColor = (severity: string) => {
    switch (severity) {
      case 'critical': return 'red';
      case 'high': return 'orange';
      case 'medium': return 'yellow';
      case 'low': return 'green';
      default: return 'gray';
    }
  };

  const getTypeIcon = (type: string) => {
    switch (type) {
      case 'diagnosis': return <IconStethoscope size={16} />;
      case 'treatment': return <IconMedicalCross size={16} />;
      case 'drug-interaction': return <IconPill size={16} />;
      case 'risk-assessment': return <IconShield size={16} />;
      case 'recommendation': return <IconBulb size={16} />;
      case 'alert': return <IconAlertTriangle size={16} />;
      default: return <IconBrain size={16} />;
    }
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'accepted': return 'green';
      case 'implemented': return 'teal';
      case 'reviewed': return 'blue';
      case 'rejected': return 'red';
      case 'pending': return 'yellow';
      default: return 'gray';
    }
  };

  // AI Assistant stats
  const aiStats = {
    totalInsights: 0 /* TODO: Fetch from API */,
    pendingInsights: [].filter /* TODO: Fetch from API */(i => i.status === 'pending').length,
    acceptedInsights: [].filter /* TODO: Fetch from API */(i => i.status === 'accepted').length,
    criticalInsights: [].filter /* TODO: Fetch from API */(i => i.severity === 'critical').length,
    totalQueries: 0 /* TODO: Fetch from API */,
    avgConfidence: Math.round([].reduce /* TODO: Fetch from API */((acc, i) => acc + i.confidence, 0) / 0 /* TODO: Fetch from API */),
    avgRating: [].filter /* TODO: Fetch from API */(q => q.feedback?.rating).reduce((acc, q) => acc + (q.feedback?.rating || 0), 0) / [].filter /* TODO: Fetch from API */(q => q.feedback?.rating).length,
    helpfulResponses: [].filter /* TODO: Fetch from API */(q => q.feedback?.helpful).length
  };

  return (
    <Container size="xl" py="md">
      {/* Header */}
      <Group justify="space-between" mb="lg">
        <div>
          <Group mb="xs">
            <ThemeIcon color="blue" size="xl" variant="light">
              <IconBrain size={24} />
            </ThemeIcon>
            <div>
              <Title order={1}>AI Clinical Assistant</Title>
              <Text c="dimmed" size="sm">
                Intelligent clinical decision support and medical insights
              </Text>
            </div>
          </Group>
        </div>
        <Group>
          <Button variant="light" leftSection={<IconRefresh size={16} />}>
            Refresh Insights
          </Button>
          <Button leftSection={<IconMessageCircle size={16} />} onClick={openAskAI}>
            Ask AI
          </Button>
        </Group>
      </Group>

      {/* Quick Stats */}
      <SimpleGrid cols={{ base: 1, sm: 2, md: 4, lg: 8 }} mb="lg" spacing="sm">
        <Card padding="md" radius="md" withBorder>
          <Group justify="center">
            <ThemeIcon color="blue" size="lg" radius="md" variant="light">
              <IconSparkles size={20} />
            </ThemeIcon>
            <div>
              <Text size="lg" fw={700}>{aiStats.totalInsights}</Text>
              <Text size="xs" c="dimmed">AI Insights</Text>
            </div>
          </Group>
        </Card>

        <Card padding="md" radius="md" withBorder>
          <Group justify="center">
            <ThemeIcon color="yellow" size="lg" radius="md" variant="light">
              <IconClockHour4 size={20} />
            </ThemeIcon>
            <div>
              <Text size="lg" fw={700}>{aiStats.pendingInsights}</Text>
              <Text size="xs" c="dimmed">Pending Review</Text>
            </div>
          </Group>
        </Card>

        <Card padding="md" radius="md" withBorder>
          <Group justify="center">
            <ThemeIcon color="green" size="lg" radius="md" variant="light">
              <IconCheck size={20} />
            </ThemeIcon>
            <div>
              <Text size="lg" fw={700}>{aiStats.acceptedInsights}</Text>
              <Text size="xs" c="dimmed">Accepted</Text>
            </div>
          </Group>
        </Card>

        <Card padding="md" radius="md" withBorder>
          <Group justify="center">
            <ThemeIcon color="red" size="lg" radius="md" variant="light">
              <IconAlertTriangle size={20} />
            </ThemeIcon>
            <div>
              <Text size="lg" fw={700}>{aiStats.criticalInsights}</Text>
              <Text size="xs" c="dimmed">Critical</Text>
            </div>
          </Group>
        </Card>

        <Card padding="md" radius="md" withBorder>
          <Group justify="center">
            <ThemeIcon color="purple" size="lg" radius="md" variant="light">
              <IconQuestionMark size={20} />
            </ThemeIcon>
            <div>
              <Text size="lg" fw={700}>{aiStats.totalQueries}</Text>
              <Text size="xs" c="dimmed">AI Queries</Text>
            </div>
          </Group>
        </Card>

        <Card padding="md" radius="md" withBorder>
          <Group justify="center">
            <ThemeIcon color="teal" size="lg" radius="md" variant="light">
              <IconTarget size={20} />
            </ThemeIcon>
            <div>
              <Text size="lg" fw={700}>{aiStats.avgConfidence}%</Text>
              <Text size="xs" c="dimmed">Avg Confidence</Text>
            </div>
          </Group>
        </Card>

        <Card padding="md" radius="md" withBorder>
          <Group justify="center">
            <ThemeIcon color="orange" size="lg" radius="md" variant="light">
              <IconStar size={20} />
            </ThemeIcon>
            <div>
              <Text size="lg" fw={700}>{aiStats.avgRating.toFixed(1)}</Text>
              <Text size="xs" c="dimmed">Avg Rating</Text>
            </div>
          </Group>
        </Card>

        <Card padding="md" radius="md" withBorder>
          <Group justify="center">
            <ThemeIcon color="lime" size="lg" radius="md" variant="light">
              <IconMoodCheck size={20} />
            </ThemeIcon>
            <div>
              <Text size="lg" fw={700}>{aiStats.helpfulResponses}</Text>
              <Text size="xs" c="dimmed">Helpful</Text>
            </div>
          </Group>
        </Card>
      </SimpleGrid>

      {/* Main Content Tabs */}
      <Tabs value={activeTab} onChange={(value) => setActiveTab(value || 'insights')}>
        <Tabs.List>
          <Tabs.Tab value="insights" leftSection={<IconBulb size={16} />}>
            AI Insights
          </Tabs.Tab>
          <Tabs.Tab value="queries" leftSection={<IconMessageCircle size={16} />}>
            AI Queries
          </Tabs.Tab>
          <Tabs.Tab value="guidelines" leftSection={<IconFileText size={16} />}>
            Clinical Guidelines
          </Tabs.Tab>
          <Tabs.Tab value="interactions" leftSection={<IconPill size={16} />}>
            Drug Interactions
          </Tabs.Tab>
          <Tabs.Tab value="analytics" leftSection={<IconChartBar size={16} />}>
            Analytics
          </Tabs.Tab>
        </Tabs.List>

        {/* AI Insights Tab */}
        <Tabs.Panel value="insights">
          <Paper p="md" radius="md" withBorder mt="md">
            {/* Filters */}
            <Group mb="md">
              <TextInput
                placeholder="Search insights..."
                leftSection={<IconSearch size={16} />}
                value={searchQuery}
                onChange={(event) => setSearchQuery(event.currentTarget.value)}
                style={{ flex: 1 }}
              />
              <Select
                placeholder="Category"
                data={[
                  { value: 'clinical', label: 'Clinical' },
                  { value: 'medication', label: 'Medication' },
                  { value: 'diagnostic', label: 'Diagnostic' },
                  { value: 'preventive', label: 'Preventive' },
                  { value: 'emergency', label: 'Emergency' },
                  { value: 'all', label: 'All' }
                ]}
                value={selectedCategory}
                onChange={(value) => setSelectedCategory(value || 'all')}
                clearable
              />
              <Select
                placeholder="Severity"
                data={[
                  { value: 'critical', label: 'Critical' },
                  { value: 'high', label: 'High' },
                  { value: 'medium', label: 'Medium' },
                  { value: 'low', label: 'Low' }
                ]}
                value={selectedSeverity}
                onChange={(value) => setSelectedSeverity(value || '')}
                clearable
              />
            </Group>

            {/* Insights Cards */}
            <Stack gap="md">
              {filteredInsights.length === 0 ? (
                <EmptyState
                  icon={<IconRobot size={48} />}
                  title="No AI insights"
                  description="Start using AI assistant"
                  size="sm"
                />
              ) : (
                filteredInsights.map((insight) => (
                  <Card key={insight.id} padding="lg" radius="md" withBorder onClick={() => handleViewInsight(insight)} style={{ cursor: 'pointer' }}>
                    <Group justify="space-between" mb="md">
                      <div>
                        <Group mb="xs">
                          <ThemeIcon size="sm" variant="light" color={getSeverityColor(insight.severity)}>
                            {getTypeIcon(insight.type)}
                          </ThemeIcon>
                          <Title order={4}>{insight.title}</Title>
                        </Group>
                        {insight.patientName && (
                          <Text c="dimmed" size="sm">Patient: {insight.patientName}</Text>
                        )}
                      </div>
                      <Group>
                        <Badge color={getSeverityColor(insight.severity)} variant="light" size="lg">
                          {insight.severity.toUpperCase()}
                        </Badge>
                        <Badge color={getStatusColor(insight.status)} variant="outline">
                          {insight.status.toUpperCase()}
                        </Badge>
                      </Group>
                    </Group>

                    <Text size="sm" mb="md" lineClamp={2}>
                      {insight.description}
                    </Text>

                  <Group justify="space-between">
                    <div>
                      <Text size="xs" c="dimmed">
                        Generated by {insight.generatedBy} ‚Ä¢ {formatDateTime(insight.generatedDate)}
                      </Text>
                      {insight.reviewedBy && (
                        <Text size="xs" c="dimmed">
                          Reviewed by {insight.reviewedBy} ‚Ä¢ {formatDateTime(insight.reviewedDate!)}
                        </Text>
                      )}
                    </div>
                    <Group gap="xs">
                      <ActionIcon variant="subtle" color="blue">
                        <IconEye size={16} />
                      </ActionIcon>
                      <ActionIcon variant="subtle" color="green">
                        <IconCheck size={16} />
                      </ActionIcon>
                      <ActionIcon variant="subtle" color="red">
                        <IconX size={16} />
                      </ActionIcon>
                    </Group>
                  </Group>
                </Card>
              )))
              }
            </Stack>
          </Paper>
        </Tabs.Panel>

        {/* AI Queries Tab */}
        <Tabs.Panel value="queries">
          <Paper p="md" radius="md" withBorder mt="md">
            <Group justify="space-between" mb="lg">
              <Title order={3}>Recent AI Queries</Title>
              <Button leftSection={<IconPlus size={16} />} onClick={openAskAI}>
                Ask New Question
              </Button>
            </Group>
            
            <Stack gap="lg">
              {filteredQueries.map((query) => (
                <Card key={query.id} padding="lg" radius="md" withBorder onClick={() => handleViewQuery(query)} style={{ cursor: 'pointer' }}>
                  <Group justify="space-between" mb="md">
                    <div style={{ flex: 1 }}>
                      <Text fw={600} size="md" mb="xs" lineClamp={2}>
                        {query.question}
                      </Text>
                      <Group gap="sm">
                        <Badge variant="light" size="sm" tt="capitalize">
                          {query.category}
                        </Badge>
                        <Text size="xs" c="dimmed">
                          by {query.userName} ‚Ä¢ {formatDateTime(query.timestamp)}
                        </Text>
                      </Group>
                    </div>
                    <Group>
                      <Group gap="xs">
                        <Text size="xs" c="dimmed">Confidence:</Text>
                        <Progress value={query.confidence} size="sm" w={60} color="green" />
                        <Text size="xs" fw={500}>{query.confidence}%</Text>
                      </Group>
                      {query.feedback && (
                        <Group gap="xs">
                          <Rating value={query.feedback.rating} readOnly size="sm" />
                          <Text size="xs" c="dimmed">({query.feedback.rating})</Text>
                        </Group>
                      )}
                    </Group>
                  </Group>

                  <Spoiler maxHeight={60} showLabel="Show more" hideLabel="Hide">
                    <Text size="sm" c="dimmed">
                      {query.response}
                    </Text>
                  </Spoiler>

                  <Group justify="space-between" mt="md">
                    <Group gap="xs">
                      <ThemeIcon size="sm" variant="light" color="blue">
                        <IconRobot size={14} />
                      </ThemeIcon>
                      <Text size="xs" c="dimmed">
                        {query.model}
                      </Text>
                    </Group>
                    <Group gap="xs">
                      <ActionIcon variant="subtle" color="blue">
                        <IconEye size={16} />
                      </ActionIcon>
                      <ActionIcon variant="subtle" color="orange">
                        <IconBookmark size={16} />
                      </ActionIcon>
                      <ActionIcon variant="subtle" color="green">
                        <IconShare size={16} />
                      </ActionIcon>
                    </Group>
                  </Group>
                </Card>
              ))}
            </Stack>
          </Paper>
        </Tabs.Panel>

        {/* Clinical Guidelines Tab */}
        <Tabs.Panel value="guidelines">
          <Paper p="md" radius="md" withBorder mt="md">
            <Title order={3} mb="lg">Evidence-Based Clinical Guidelines</Title>
            
            <Accordion variant="contained">
              {[].map /* TODO: Fetch from API */((guideline) => (
                <Accordion.Item key={guideline.id} value={guideline.id}>
                  <Accordion.Control>
                    <Group justify="space-between">
                      <div>
                        <Text fw={600}>{guideline.title}</Text>
                        <Group gap="sm" mt="xs">
                          <Badge variant="light" size="sm" tt="capitalize">
                            {guideline.category}
                          </Badge>
                          <Text size="sm" c="dimmed">{guideline.condition}</Text>
                        </Group>
                      </div>
                      <Group gap="xs">
                        <Text size="xs" c="dimmed">
                          {guideline.source} ‚Ä¢ v{guideline.version}
                        </Text>
                      </Group>
                    </Group>
                  </Accordion.Control>
                  <Accordion.Panel>
                    <Stack gap="md">
                      <Text size="sm">
                        {guideline.description}
                      </Text>

                      <Divider />

                      <div>
                        <Text size="sm" fw={600} mb="md">Recommendations:</Text>
                        <Stack gap="sm">
                          {guideline.recommendations.map((rec, index) => (
                            <Card key={index} padding="sm" radius="sm" withBorder>
                              <Group justify="space-between" mb="xs">
                                <Group gap="xs">
                                  <Badge size="xs" color="blue" variant="filled">
                                    Level {rec.level}
                                  </Badge>
                                  <Badge size="xs" color="green" variant="outline">
                                    {rec.strength}
                                  </Badge>
                                </Group>
                              </Group>
                              <Text size="sm" mb="xs">
                                {rec.recommendation}
                              </Text>
                              <Text size="xs" c="dimmed">
                                <strong>Evidence:</strong> {rec.evidence}
                              </Text>
                            </Card>
                          ))}
                        </Stack>
                      </div>

                      <Group justify="space-between">
                        <Text size="xs" c="dimmed">
                          Applicable to: {guideline.applicability.join(', ')}
                        </Text>
                        <Text size="xs" c="dimmed">
                          Last updated: {formatDate(guideline.lastUpdated)}
                        </Text>
                      </Group>
                    </Stack>
                  </Accordion.Panel>
                </Accordion.Item>
              ))}
            </Accordion>
          </Paper>
        </Tabs.Panel>

        {/* Drug Interactions Tab */}
        <Tabs.Panel value="interactions">
          <Paper p="md" radius="md" withBorder mt="md">
            <Title order={3} mb="lg">Drug Interaction Checker</Title>
            
            <Stack gap="md">
              {[].map /* TODO: Fetch from API */((interaction) => (
                <Card key={interaction.id} padding="lg" radius="md" withBorder>
                  <Group justify="space-between" mb="md">
                    <div>
                      <Group mb="xs">
                        <Text fw={600} size="lg">
                          {interaction.drug1} + {interaction.drug2}
                        </Text>
                      </Group>
                      <Group gap="sm">
                        <Badge color={getSeverityColor(interaction.severity)} variant="light">
                          {interaction.interactionType.toUpperCase()}
                        </Badge>
                        <Badge variant="outline" size="sm">
                          {interaction.severity}
                        </Badge>
                        <Badge variant="outline" size="sm" color="gray">
                          {interaction.frequency}
                        </Badge>
                      </Group>
                    </div>
                    <Group gap="xs">
                      <Text size="xs" c="dimmed">Documentation:</Text>
                      <Badge size="sm" color={interaction.documentation === 'excellent' ? 'green' : interaction.documentation === 'good' ? 'blue' : 'orange'}>
                        {interaction.documentation}
                      </Badge>
                    </Group>
                  </Group>

                  <Stack gap="sm">
                    <div>
                      <Text size="sm" fw={600} c="dimmed">Mechanism:</Text>
                      <Text size="sm">{interaction.mechanism}</Text>
                    </div>

                    <div>
                      <Text size="sm" fw={600} c="dimmed">Clinical Effect:</Text>
                      <Text size="sm">{interaction.clinicalEffect}</Text>
                    </div>

                    <div>
                      <Text size="sm" fw={600} c="dimmed">Recommendation:</Text>
                      <Text size="sm">{interaction.recommendation}</Text>
                    </div>
                  </Stack>

                  <Group justify="space-between" mt="md">
                    <Group gap="md">
                      <Group gap="xs">
                        <Text size="xs" c="dimmed">Onset:</Text>
                        <Text size="xs" fw={500}>{interaction.onset}</Text>
                      </Group>
                      <Group gap="xs">
                        <Text size="xs" c="dimmed">Severity:</Text>
                        <Text size="xs" fw={500}>{interaction.severity}</Text>
                      </Group>
                    </Group>
                    <Group gap="xs">
                      <ActionIcon variant="subtle" color="blue">
                        <IconInfoCircle size={16} />
                      </ActionIcon>
                      <ActionIcon variant="subtle" color="orange">
                        <IconBookmark size={16} />
                      </ActionIcon>
                    </Group>
                  </Group>
                </Card>
              ))}
            </Stack>
          </Paper>
        </Tabs.Panel>

        {/* Analytics Tab */}
        <Tabs.Panel value="analytics">
          <SimpleGrid cols={{ base: 1, lg: 2 }} spacing="lg" mt="md">
            {/* Insight Types Distribution */}
            <Card padding="lg" radius="md" withBorder>
              <Title order={4} mb="md">AI Insights by Type</Title>
              <Stack gap="md">
                <Group justify="space-between">
                  <Group gap="xs">
                    <ThemeIcon color="blue" size="sm" radius="xl">
                      <IconStethoscope size={14} />
                    </ThemeIcon>
                    <Text size="sm">Diagnosis</Text>
                  </Group>
                  <Badge color="blue" variant="light">1</Badge>
                </Group>
                <Group justify="space-between">
                  <Group gap="xs">
                    <ThemeIcon color="red" size="sm" radius="xl">
                      <IconPill size={14} />
                    </ThemeIcon>
                    <Text size="sm">Drug Interaction</Text>
                  </Group>
                  <Badge color="red" variant="light">1</Badge>
                </Group>
                <Group justify="space-between">
                  <Group gap="xs">
                    <ThemeIcon color="orange" size="sm" radius="xl">
                      <IconShield size={14} />
                    </ThemeIcon>
                    <Text size="sm">Risk Assessment</Text>
                  </Group>
                  <Badge color="orange" variant="light">1</Badge>
                </Group>
              </Stack>
            </Card>

            {/* Confidence Levels */}
            <Card padding="lg" radius="md" withBorder>
              <Title order={4} mb="md">AI Confidence Distribution</Title>
              <Stack gap="sm">
                <div>
                  <Group justify="space-between" mb="xs">
                    <Text size="sm">90-100%</Text>
                    <Text size="sm" fw={500}>2</Text>
                  </Group>
                  <Progress value={100} color="teal" size="lg" />
                </div>
                <div>
                  <Group justify="space-between" mb="xs">
                    <Text size="sm">80-89%</Text>
                    <Text size="sm" fw={500}>1</Text>
                  </Group>
                  <Progress value={50} color="teal" size="lg" />
                </div>
                <div>
                  <Group justify="space-between" mb="xs">
                    <Text size="sm">70-79%</Text>
                    <Text size="sm" fw={500}>0</Text>
                  </Group>
                  <Progress value={0} color="teal" size="lg" />
                </div>
                <div>
                  <Group justify="space-between" mb="xs">
                    <Text size="sm">60-69%</Text>
                    <Text size="sm" fw={500}>0</Text>
                  </Group>
                  <Progress value={0} color="teal" size="lg" />
                </div>
              </Stack>
            </Card>

            {/* Query Categories */}
            <Card padding="lg" radius="md" withBorder>
              <Title order={4} mb="md">AI Queries by Category</Title>
              <Stack gap="md">
                <Group justify="space-between">
                  <Group gap="xs">
                    <ThemeIcon color="blue" size="sm" radius="xl">
                      <IconStethoscope size={14} />
                    </ThemeIcon>
                    <Text size="sm">Diagnosis</Text>
                  </Group>
                  <Badge color="blue" variant="light">1</Badge>
                </Group>
                <Group justify="space-between">
                  <Group gap="xs">
                    <ThemeIcon color="green" size="sm" radius="xl">
                      <IconMedicalCross size={14} />
                    </ThemeIcon>
                    <Text size="sm">Treatment</Text>
                  </Group>
                  <Badge color="green" variant="light">1</Badge>
                </Group>
                <Group justify="space-between">
                  <Group gap="xs">
                    <ThemeIcon color="orange" size="sm" radius="xl">
                      <IconPill size={14} />
                    </ThemeIcon>
                    <Text size="sm">Medication</Text>
                  </Group>
                  <Badge color="orange" variant="light">1</Badge>
                </Group>
              </Stack>
            </Card>

            {/* User Satisfaction */}
            <Card padding="lg" radius="md" withBorder>
              <Title order={4} mb="md">User Satisfaction Trends</Title>
              <Stack gap="sm">
                <div>
                  <Group justify="space-between" mb="xs">
                    <Text size="sm">Week 1</Text>
                    <Group gap="md">
                      <Badge color="blue" variant="light">Rating: 4.2</Badge>
                      <Badge color="green" variant="light">Helpful: 85%</Badge>
                    </Group>
                  </Group>
                  <Progress value={85} color="green" size="md" />
                </div>
                <div>
                  <Group justify="space-between" mb="xs">
                    <Text size="sm">Week 2</Text>
                    <Group gap="md">
                      <Badge color="blue" variant="light">Rating: 4.5</Badge>
                      <Badge color="green" variant="light">Helpful: 88%</Badge>
                    </Group>
                  </Group>
                  <Progress value={88} color="green" size="md" />
                </div>
                <div>
                  <Group justify="space-between" mb="xs">
                    <Text size="sm">Week 3</Text>
                    <Group gap="md">
                      <Badge color="blue" variant="light">Rating: 4.3</Badge>
                      <Badge color="green" variant="light">Helpful: 82%</Badge>
                    </Group>
                  </Group>
                  <Progress value={82} color="green" size="md" />
                </div>
                <div>
                  <Group justify="space-between" mb="xs">
                    <Text size="sm">Week 4</Text>
                    <Group gap="md">
                      <Badge color="blue" variant="light">Rating: 4.7</Badge>
                      <Badge color="green" variant="light">Helpful: 91%</Badge>
                    </Group>
                  </Group>
                  <Progress value={91} color="green" size="md" />
                </div>
              </Stack>
            </Card>
          </SimpleGrid>
        </Tabs.Panel>
      </Tabs>

      {/* Insight Detail Modal */}
      <Modal
        opened={insightDetailOpened}
        onClose={closeInsightDetail}
        title="AI Insight Details"
        size="xl"
      >
        {selectedInsight && (
          <ScrollArea h={600}>
            <Stack gap="md">
              {/* Insight Header */}
              <Card padding="lg" radius="md" withBorder>
                <Group justify="space-between" mb="md">
                  <div>
                    <Group mb="xs">
                      <ThemeIcon variant="light" color={getSeverityColor(selectedInsight.severity)}>
                        {getTypeIcon(selectedInsight.type)}
                      </ThemeIcon>
                      <Title order={3}>{selectedInsight.title}</Title>
                    </Group>
                    {selectedInsight.patientName && (
                      <Text c="dimmed">Patient: {selectedInsight.patientName}</Text>
                    )}
                  </div>
                  <Group>
                    <Badge color={getSeverityColor(selectedInsight.severity)} variant="light" size="lg">
                      {selectedInsight.severity.toUpperCase()}
                    </Badge>
                    <Badge color={getStatusColor(selectedInsight.status)} variant="outline" size="lg">
                      {selectedInsight.status.toUpperCase()}
                    </Badge>
                  </Group>
                </Group>

                <Text mb="md">{selectedInsight.description}</Text>

                <Group gap="md">
                  <Group gap="xs">
                    <Text size="sm" c="dimmed">Confidence:</Text>
                    <Progress value={selectedInsight.confidence} size="lg" w={120} color="blue" />
                    <Text size="sm" fw={600}>{selectedInsight.confidence}%</Text>
                  </Group>
                  <Group gap="xs">
                    <Text size="sm" c="dimmed">Generated by:</Text>
                    <Text size="sm" fw={500}>{selectedInsight.generatedBy}</Text>
                  </Group>
                </Group>
              </Card>

              {/* Evidence */}
              <Card padding="lg" radius="md" withBorder>
                <Title order={5} mb="md">Supporting Evidence</Title>
                <Stack gap="sm">
                  {selectedInsight.evidence.map((evidence, index) => (
                    <Card key={index} padding="sm" radius="sm" withBorder>
                      <Group justify="space-between" mb="xs">
                        <Text size="sm" fw={500}>{evidence.source}</Text>
                        <Group gap="xs">
                          <Text size="xs" c="dimmed">Relevance:</Text>
                          <Progress value={evidence.relevance} size="sm" w={60} color="green" />
                          <Text size="xs" fw={500}>{evidence.relevance}%</Text>
                        </Group>
                      </Group>
                      <Text size="sm" c="dimmed">{evidence.description}</Text>
                    </Card>
                  ))}
                </Stack>
              </Card>

              {/* Recommendations */}
              <Card padding="lg" radius="md" withBorder>
                <Title order={5} mb="md">Recommendations</Title>
                <Stack gap="sm">
                  {selectedInsight.recommendations.map((rec, index) => (
                    <Card key={index} padding="sm" radius="sm" withBorder>
                      <Group justify="space-between" mb="xs">
                        <Text size="sm" fw={500}>{rec.action}</Text>
                        <Group gap="xs">
                          <Badge size="sm" color={getPriorityColor(rec.priority)} variant="light">
                            {rec.priority.toUpperCase()}
                          </Badge>
                          <Text size="xs" c="dimmed">{rec.timeframe}</Text>
                        </Group>
                      </Group>
                    </Card>
                  ))}
                </Stack>
              </Card>

              {/* Clinical Context */}
              {selectedInsight.metadata && (
                <Card padding="lg" radius="md" withBorder>
                  <Title order={5} mb="md">Clinical Context</Title>
                  <SimpleGrid cols={2} spacing="md">
                    {selectedInsight.metadata.symptoms && (
                      <div>
                        <Text size="sm" fw={500} c="dimmed" mb="xs">Symptoms</Text>
                        <Group gap="xs">
                          {selectedInsight.metadata.symptoms.map((symptom, index) => (
                            <Chip key={index} size="sm" variant="light">
                              {symptom}
                            </Chip>
                          ))}
                        </Group>
                      </div>
                    )}
                    {selectedInsight.metadata.medications && (
                      <div>
                        <Text size="sm" fw={500} c="dimmed" mb="xs">Current Medications</Text>
                        <Group gap="xs">
                          {selectedInsight.metadata.medications.map((med, index) => (
                            <Chip key={index} size="sm" variant="light" color="blue">
                              {med}
                            </Chip>
                          ))}
                        </Group>
                      </div>
                    )}
                  </SimpleGrid>
                </Card>
              )}

              {/* Action Buttons */}
              <Group justify="flex-end">
                <Button variant="light" onClick={closeInsightDetail}>
                  Close
                </Button>
                <Button variant="light" color="red" leftSection={<IconX size={16} />}>
                  Reject
                </Button>
                <Button leftSection={<IconCheck size={16} />} color="green">
                  Accept & Implement
                </Button>
              </Group>
            </Stack>
          </ScrollArea>
        )}
      </Modal>

      {/* Ask AI Modal */}
      <Modal
        opened={askAIOpened}
        onClose={closeAskAI}
        title="Ask AI Assistant"
        size="lg"
      >
        <Stack gap="md">
          <Alert icon={<IconInfoCircle size="1rem" />} title="AI Assistant Guidelines" color="blue">
            Ask specific clinical questions for better responses. Include patient context when relevant.
          </Alert>

          <Textarea
            label="Your Question"
            placeholder="Ask about diagnosis, treatment, drug interactions, or any clinical topic..."
            minRows={4}
            value={newQuery}
            onChange={(event) => setNewQuery(event.currentTarget.value)}
            required
          />

          <SimpleGrid cols={2} spacing="md">
            <Select
              label="Question Category"
              placeholder="Select category"
              data={[
                { value: 'diagnosis', label: 'Diagnosis' },
                { value: 'treatment', label: 'Treatment' },
                { value: 'medication', label: 'Medication' },
                { value: 'general', label: 'General Medical' },
                { value: 'emergency', label: 'Emergency' }
              ]}
            />
            <Select
              label="Patient Context (Optional)"
              placeholder="Select patient"
              data={[
                { value: 'P001', label: 'Rajesh Kumar' },
                { value: 'P002', label: 'Sunita Patel' },
                { value: 'P003', label: 'Mohammed Ali' }
              ]}
            />
          </SimpleGrid>

          <Group justify="flex-end">
            <Button variant="light" onClick={closeAskAI}>
              Cancel
            </Button>
            <Button 
              leftSection={isLoading ? <Loader size={16} /> : <IconSend size={16} />}
              onClick={handleAskAI}
              disabled={!newQuery.trim() || isLoading}
            >
              {isLoading ? 'Processing...' : 'Ask AI'}
            </Button>
          </Group>
        </Stack>
      </Modal>
    </Container>
  );
};

const getPriorityColor = (priority: string) => {
  switch (priority) {
    case 'high': return 'red';
    case 'medium': return 'yellow';
    case 'low': return 'green';
    default: return 'gray';
  }
};

export default AIAssistant;

'use client';

import React, { useState, useMemo, useEffect } from 'react';
import {
  Container,
  Paper,
  Title,
  Group,
  Button,
  TextInput,
  Select,
  Badge,
  Table,
  Modal,
  Text,
  Tabs,
  Card,
  Avatar,
  ActionIcon,
  Menu,
  Stack,
  Divider,
  SimpleGrid,
  ScrollArea,
  ThemeIcon,
  Timeline,
  Alert,
  Progress,
  Flex,
  Anchor,
  NumberInput,
  Textarea
} from '@mantine/core';
import { useDisclosure } from '@mantine/hooks';
import EmptyState from '../../../components/EmptyState';
import { notifications } from '@mantine/notifications';
import { Calendar, DatePickerInput } from '@mantine/dates';
// import { AreaChart, BarChart, DonutChart, LineChart } from '@mantine/charts';
import {
  IconPlus,
  IconSearch,
  IconEdit,
  IconEye,
  IconTrash,
  IconCalendar,
  IconClock,
  IconUsers,
  IconChartBar,
  IconPhone,
  IconMail,
  IconAlertCircle,
  IconCheck,
  IconX,
  IconDotsVertical,
  IconCalendarEvent,
  IconStethoscope,
  IconActivity,
  IconTrendingUp,
  IconTrendingDown,
  IconClockHour3,
  IconUserCheck,
  IconUserX,
  IconCurrencyRupee,
  IconVideo,
  IconBell,
  IconHistory,
  IconCalendarStats,
  IconReport
} from '@tabler/icons-react';

// Import types and services
import { 
  Appointment, 
  AppointmentStatus, 
  AppointmentType, 
  AppointmentPriority,
  AppointmentSearchFilters,
  AppointmentStats
} from '../../../types/appointment';
import appointmentsService from '../../../services/appointments.service';

const AppointmentManagement = () => {
  // Utility function for consistent date formatting
  const formatDate = (date: string | Date) => {
    return new Date(date).toLocaleDateString('en-CA'); // YYYY-MM-DD format
  };

  const formatDateTime = (date: string | Date) => {
    return new Date(date).toLocaleString('en-US', {
      year: 'numeric',
      month: '2-digit',
      day: '2-digit',
      hour: '2-digit',
      minute: '2-digit',
      hour12: true
    });
  };

  // State management
  const [isClient, setIsClient] = useState(false);
  const [activeTab, setActiveTab] = useState<string>('appointments');
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedDoctor, setSelectedDoctor] = useState<string>('');
  const [selectedStatus, setSelectedStatus] = useState<string>('');
  const [selectedType, setSelectedType] = useState<string>('');
  const [selectedDate, setSelectedDate] = useState<Date | null>(null);
  const [selectedAppointment, setSelectedAppointment] = useState<Appointment | null>(null);
  const [appointments, setAppointments] = useState<Appointment[]>([]);
  const [appointmentStats, setAppointmentStats] = useState<any>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  // Handle hydration
  useEffect(() => {
    setIsClient(true);
    fetchAppointments();
    fetchStats();
  }, []);

  // Fetch appointments from API
  const fetchAppointments = async () => {
    try {
      setLoading(true);
      const filters = {
        doctorId: selectedDoctor || undefined,
        status: selectedStatus || undefined,
        startDate: selectedDate ? selectedDate.toISOString() : undefined,
        search: searchQuery || undefined
      };
      const response = await appointmentsService.getAppointments(filters);
      console.log('Appointments API response:', response);
      setAppointments(response.data || []);
      setError(null);
    } catch (err: any) {
      const errorMsg = err.response?.data?.message || err.message || 'Failed to fetch appointments';
      console.warn('Error fetching appointments (using empty data):', errorMsg);
      setError(null);
      setAppointments([]);
    } finally {
      setLoading(false);
    }
  };

  // Fetch stats from API
  const fetchStats = async () => {
    try {
      const response = await appointmentsService.getAppointmentStats();
      console.log('Appointment stats API response:', response);
      setAppointmentStats(response.data);
    } catch (err: any) {
      console.warn('Error fetching appointment stats (using default values):', err.response?.data?.message || err.message);
      setAppointmentStats({ 
        total: 0, 
        today: 0, 
        pending: 0, 
        completed: 0,
        scheduled: 0,
        cancelled: 0
      });
    }
  };

  // Refetch when filters change
  useEffect(() => {
    if (isClient) {
      fetchAppointments();
    }
  }, [selectedDoctor, selectedStatus, selectedDate, searchQuery]);

  // Modal states
  const [appointmentDetailOpened, { open: openAppointmentDetail, close: closeAppointmentDetail }] = useDisclosure(false);
  const [bookAppointmentOpened, { open: openBookAppointment, close: closeBookAppointment }] = useDisclosure(false);
  const [rescheduleOpened, { open: openReschedule, close: closeReschedule }] = useDisclosure(false);

  // Filter appointments - now using API data
  const filteredAppointments = useMemo(() => {
    return appointments.filter((appointment) => {
      const matchesType = !selectedType || appointment.appointmentType === selectedType;
      return matchesType; // Other filtering is handled by API
    });
  }, [appointments, selectedType]);

  // Helper functions
  const getStatusColor = (status: AppointmentStatus) => {
    switch (status) {
      case 'SCHEDULED': return 'blue';
      case 'ARRIVED': return 'teal';
      case 'IN_PROGRESS': return 'yellow';
      case 'COMPLETED': return 'green';
      case 'CANCELLED': return 'red';
      case 'NO_SHOW': return 'gray';
      case 'RESCHEDULED': return 'orange';
      default: return 'gray';
    }
  };

  const getTypeColor = (type: AppointmentType) => {
    switch (type) {
      case 'emergency': return 'red';
      case 'consultation': return 'blue';
      case 'follow_up': return 'green';
      case 'surgery_consultation': return 'purple';
      case 'telemedicine': return 'cyan';
      default: return 'gray';
    }
  };

  const getPriorityColor = (priority: AppointmentPriority) => {
    switch (priority) {
      case 'emergency': return 'red';
      case 'urgent': return 'orange';
      case 'high': return 'yellow';
      case 'normal': return 'blue';
      case 'low': return 'gray';
      default: return 'gray';
    }
  };

  const handleViewAppointment = (appointment: Appointment) => {
    setSelectedAppointment(appointment);
    openAppointmentDetail();
  };

  const handleStatusUpdate = async (appointmentId: string, newStatus: AppointmentStatus) => {
    try {
      await appointmentsService.updateAppointmentStatus(appointmentId, newStatus);
      notifications.show({
        title: 'Appointment Updated',
        message: `Appointment status changed to ${newStatus}`,
        color: 'green',
      });
      fetchAppointments(); // Refresh the list
    } catch (error: any) {
      notifications.show({
        title: 'Error',
        message: error.response?.data?.message || 'Failed to update appointment status',
        color: 'red',
      });
    }
  };

  const handleCancelAppointment = async (appointment: Appointment) => {
    if (!window.confirm(`Cancel appointment for ${appointment.patient.firstName} ${appointment.patient.lastName}?`)) {
      return;
    }

    try {
      await appointmentsService.updateAppointmentStatus(appointment.id, 'CANCELLED');
      notifications.show({
        title: 'Appointment Cancelled',
        message: `Appointment for ${appointment.patient.firstName} ${appointment.patient.lastName} has been cancelled`,
        color: 'green',
      });
      fetchAppointments(); // Refresh the list
    } catch (error: any) {
      notifications.show({
        title: 'Error',
        message: error.response?.data?.message || 'Failed to cancel appointment',
        color: 'red',
      });
    }
  };

  // Statistics cards
  const statsCards = appointmentStats ? [
    {
      title: 'Total',
      value: appointmentStats.total || 0,
      icon: IconCalendarEvent,
      color: 'blue',
      trend: null
    },
    {
      title: 'Today',
      value: appointmentStats.today || 0,
      icon: IconClock,
      color: 'green',
      trend: null
    },
    {
      title: 'Pending',
      value: appointmentStats.pending || 0,
      icon: IconClockHour3,
      color: 'yellow',
      trend: null
    },
    {
      title: 'Completed',
      value: appointmentStats.completed || 0,
      icon: IconCheck,
      color: 'teal',
      trend: null
    }
  ] : [];

  return (
    <Container size="xl" py="md">
      {/* Header */}
      <Group justify="space-between" mb="lg">
        <div>
          <Title order={1}>Appointment Management</Title>
          <Text c="dimmed" size="sm">
            Schedule, manage, and track patient appointments
          </Text>
        </div>
        <Group>
          <Button
            leftSection={<IconPlus size={16} />}
            onClick={openBookAppointment}
          >
            Book Appointment
          </Button>
        </Group>
      </Group>

      {/* Error Display */}
      {error && (
        <Alert icon={<IconAlertCircle size="1rem" />} title="Error" color="red" variant="light" mb="lg">
          {error}
        </Alert>
      )}

      {/* Statistics Cards */}
      {appointmentStats && (
        <SimpleGrid cols={{ base: 1, sm: 2, md: 4 }} mb="lg">
          {statsCards.map((stat) => {
            const Icon = stat.icon;
            return (
              <Card key={stat.title} padding="lg" radius="md" withBorder>
                <Group justify="space-between">
                  <div>
                    <Text c="dimmed" size="sm" fw={500}>
                      {stat.title}
                    </Text>
                    <Text fw={700} size="xl">
                      {stat.value}
                    </Text>
                  </div>
                  <ThemeIcon color={stat.color} size="xl" radius="md" variant="light">
                    <Icon size={24} />
                  </ThemeIcon>
                </Group>
              </Card>
            );
          })}
        </SimpleGrid>
      )}

      {/* Main Content Tabs */}
      <Tabs value={activeTab} onChange={(value) => setActiveTab(value || 'appointments')}>
        <Tabs.List>
          <Tabs.Tab value="appointments" leftSection={<IconCalendarEvent size={16} />}>
            Appointments
          </Tabs.Tab>
          <Tabs.Tab value="calendar" leftSection={<IconCalendar size={16} />}>
            Calendar View
          </Tabs.Tab>
          <Tabs.Tab value="queue" leftSection={<IconUsers size={16} />}>
            Queue Management
          </Tabs.Tab>
          <Tabs.Tab value="reminders" leftSection={<IconBell size={16} />}>
            Reminders
          </Tabs.Tab>
          <Tabs.Tab value="analytics" leftSection={<IconChartBar size={16} />}>
            Analytics
          </Tabs.Tab>
        </Tabs.List>

        {/* Appointments Tab */}
        <Tabs.Panel value="appointments">
          <Paper p="md" radius="md" withBorder mt="md">
            {/* Search and Filters */}
            <Group mb="md">
              <TextInput
                placeholder="Search appointments..."
                leftSection={<IconSearch size={16} />}
                value={searchQuery}
                onChange={(event) => setSearchQuery(event.currentTarget.value)}
                style={{ flex: 1 }}
              />
              <Select
                placeholder="Doctor"
                data={[]} // TODO: Fetch from staff API
                value={selectedDoctor}
                onChange={(value) => setSelectedDoctor(value || '')}
                searchable
                clearable
              />
              <Select
                placeholder="Status"
                data={[
                  { value: 'scheduled', label: 'Scheduled' },
                  { value: 'arrived', label: 'Arrived' },
                  { value: 'in_progress', label: 'In Progress' },
                  { value: 'completed', label: 'Completed' },
                  { value: 'cancelled', label: 'Cancelled' },
                  { value: 'no_show', label: 'No Show' },
                  { value: 'rescheduled', label: 'Rescheduled' }
                ]}
                value={selectedStatus}
                onChange={(value) => setSelectedStatus(value || '')}
                clearable
              />
              <Select
                placeholder="Type"
                data={[
                  { value: 'consultation', label: 'Consultation' },
                  { value: 'follow_up', label: 'Follow-up' },
                  { value: 'emergency', label: 'Emergency' },
                  { value: 'telemedicine', label: 'Telemedicine' }
                ]}
                value={selectedType}
                onChange={(value) => setSelectedType(value || '')}
                clearable
              />
              <DatePickerInput
                placeholder="Select date"
                value={selectedDate}
                onChange={setSelectedDate as any}
                clearable
              />
            </Group>

            {/* Appointments Table */}
            <ScrollArea>
              <Table striped highlightOnHover>
                <Table.Thead>
                  <Table.Tr>
                    <Table.Th>Patient</Table.Th>
                    <Table.Th>Doctor</Table.Th>
                    <Table.Th>Date & Time</Table.Th>
                    <Table.Th>Type</Table.Th>
                    <Table.Th>Status</Table.Th>
                    <Table.Th>Priority</Table.Th>
                    <Table.Th>Fee</Table.Th>
                    <Table.Th>Actions</Table.Th>
                  </Table.Tr>
                </Table.Thead>
                <Table.Tbody>
                  {filteredAppointments.length === 0 ? (
                    <Table.Tr>
                      <Table.Td colSpan={7}>
                        <EmptyState
                          icon={<IconCalendar size={48} />}
                          title="No appointments found"
                          description={searchQuery || selectedDoctor || selectedStatus ? 
                            "No appointments match your search criteria. Try adjusting your filters." :
                            "No appointments scheduled yet. Book your first appointment to get started."
                          }
                          action={!searchQuery && !selectedDoctor && !selectedStatus ? {
                            label: "Book Appointment",
                            onClick: openBookAppointment
                          } : undefined}
                          size="sm"
                        />
                      </Table.Td>
                    </Table.Tr>
                  ) : (
                    filteredAppointments.map((appointment) => (
                    <Table.Tr key={appointment.id}>
                      <Table.Td>
                        <Group>
                          <Avatar color="blue" radius="xl">
                            {appointment.patient.firstName[0]}{appointment.patient.lastName[0]}
                          </Avatar>
                          <div>
                            <Text fw={500}>
                              {appointment.patient.firstName} {appointment.patient.lastName}
                            </Text>
                            <Text size="sm" c="dimmed">
                              {appointment.appointmentNumber}
                            </Text>
                          </div>
                        </Group>
                      </Table.Td>
                      <Table.Td>
                        <div>
                          <Text fw={500}>
                            {appointment.doctor.firstName} {appointment.doctor.lastName}
                          </Text>
                          <Text size="sm" c="dimmed">
                            {appointment.department}
                          </Text>
                        </div>
                      </Table.Td>
                      <Table.Td>
                        <div>
                          <Text fw={500}>
                            {isClient ? formatDate(appointment.appointmentDate) : 'Loading...'}
                          </Text>
                          <Text size="sm" c="dimmed">
                            {appointment.appointmentTime} ({appointment.duration} min)
                          </Text>
                        </div>
                      </Table.Td>
                      <Table.Td>
                        <Badge color={getTypeColor(appointment.appointmentType)} variant="light">
                          {appointment.appointmentType.replace('_', ' ')}
                        </Badge>
                      </Table.Td>
                      <Table.Td>
                        <Badge color={getStatusColor(appointment.status)} variant="light">
                          {appointment.status.replace('_', ' ')}
                        </Badge>
                      </Table.Td>
                      <Table.Td>
                        <Badge color={getPriorityColor(appointment.priority)} variant="light" size="sm">
                          {appointment.priority}
                        </Badge>
                      </Table.Td>
                      <Table.Td>
                        <div>
                          <Text fw={500}>‚Çπ{appointment.consultationFee}</Text>
                          <Badge 
                            color={appointment.isPaid ? 'green' : 'red'} 
                            variant="light" 
                            size="xs"
                          >
                            {appointment.isPaid ? 'Paid' : 'Pending'}
                          </Badge>
                        </div>
                      </Table.Td>
                      <Table.Td>
                        <Group gap="xs">
                          <ActionIcon
                            variant="subtle"
                            color="blue"
                            onClick={() => handleViewAppointment(appointment)}
                          >
                            <IconEye size={16} />
                          </ActionIcon>
                          <ActionIcon
                            variant="subtle"
                            color="green"
                            onClick={openReschedule}
                          >
                            <IconEdit size={16} />
                          </ActionIcon>
                          <Menu>
                            <Menu.Target>
                              <ActionIcon variant="subtle" color="gray">
                                <IconDotsVertical size={16} />
                              </ActionIcon>
                            </Menu.Target>
                            <Menu.Dropdown>
                              <Menu.Item 
                                leftSection={<IconCheck size={14} />}
                                onClick={() => handleStatusUpdate(appointment.id, 'ARRIVED')}
                              >
                                Confirm
                              </Menu.Item>
                              <Menu.Item 
                                leftSection={<IconUserCheck size={14} />}
                                onClick={() => handleStatusUpdate(appointment.id, 'ARRIVED')}
                              >
                                Check In
                              </Menu.Item>
                              <Menu.Item 
                                leftSection={<IconX size={14} />}
                                color="red"
                                onClick={() => handleCancelAppointment(appointment)}
                              >
                                Cancel
                              </Menu.Item>
                            </Menu.Dropdown>
                          </Menu>
                        </Group>
                      </Table.Td>
                    </Table.Tr>
                  )))
                  }
                </Table.Tbody>
              </Table>
            </ScrollArea>
          </Paper>
        </Tabs.Panel>

        {/* Calendar Tab */}
        <Tabs.Panel value="calendar">
          <Paper p="md" radius="md" withBorder mt="md">
            <Group justify="space-between" mb="lg">
              <Title order={3}>Appointment Calendar</Title>
              <Group>
                <Select
                  placeholder="Select Doctor"
                  data={[].map /* TODO: Fetch from API */(doctor => ({ 
                    value: doctor.staffId, 
                    label: `${doctor.firstName} ${doctor.lastName}` 
                  }))}
                  value={selectedDoctor}
                  onChange={(value) => setSelectedDoctor(value || '')}
                />
                <Button leftSection={<IconPlus size={16} />}>
                  Add Slot
                </Button>
              </Group>
            </Group>

            <SimpleGrid cols={{ base: 1, lg: 2 }}>
              <Card padding="lg" radius="md" withBorder>
                <Title order={4} mb="md">Calendar</Title>
                <Calendar 
                  size="md"
                  static
                  renderDay={(date) => {
                    const dateObj = new Date(date as any);
                    const hasAppointments = appointments.some(apt => 
                      new Date(apt.appointmentDate).toDateString() === dateObj.toDateString()
                    );
                    return (
                      <div style={{ 
                        width: '100%', 
                        height: '100%',
                        backgroundColor: hasAppointments ? '#e3f2fd' : 'transparent',
                        borderRadius: '4px'
                      }}>
                        {dateObj.getDate()}
                      </div>
                    );
                  }}
                />
              </Card>

              <Card padding="lg" radius="md" withBorder>
                <Title order={4} mb="md">Today&apos;s Schedule</Title>
                <Stack gap="sm">
                  {appointments
                    .filter(apt => 
                      new Date(apt.appointmentDate).toDateString() === new Date().toDateString()
                    )
                    .map((appointment) => (
                    <Group key={appointment.id} justify="space-between" p="sm" 
                           style={{ border: '1px solid #e9ecef', borderRadius: '8px' }}>
                      <div>
                        <Text fw={500}>{appointment.appointmentTime}</Text>
                        <Text size="sm" c="dimmed">
                          {appointment.patient.firstName} {appointment.patient.lastName}
                        </Text>
                        <Text size="xs" c="dimmed">
                          {appointment.doctor.firstName} {appointment.doctor.lastName}
                        </Text>
                      </div>
                      <Badge color={getStatusColor(appointment.status)} variant="light" size="sm">
                        {appointment.status}
                      </Badge>
                    </Group>
                  ))}
                </Stack>
              </Card>
            </SimpleGrid>
          </Paper>
        </Tabs.Panel>

        {/* Queue Management Tab */}
        <Tabs.Panel value="queue">
          <Paper p="md" radius="md" withBorder mt="md">
            <Title order={3} mb="lg">Queue Management</Title>
            
            <SimpleGrid cols={{ base: 1, md: 2, lg: 3 }} mb="lg">
              <Card padding="md" radius="md" withBorder>
                <Group justify="center">
                  <ThemeIcon size="xl" color="blue" variant="light">
                    <IconUsers size={24} />
                  </ThemeIcon>
                </Group>
                <Text ta="center" fw={600} size="lg" mt="sm">8</Text>
                <Text ta="center" size="sm" c="dimmed">Waiting</Text>
              </Card>
              
              <Card padding="md" radius="md" withBorder>
                <Group justify="center">
                  <ThemeIcon size="xl" color="green" variant="light">
                    <IconStethoscope size={24} />
                  </ThemeIcon>
                </Group>
                <Text ta="center" fw={600} size="lg" mt="sm">3</Text>
                <Text ta="center" size="sm" c="dimmed">In Consultation</Text>
              </Card>
              
              <Card padding="md" radius="md" withBorder>
                <Group justify="center">
                  <ThemeIcon size="xl" color="orange" variant="light">
                    <IconClockHour3 size={24} />
                  </ThemeIcon>
                </Group>
                <Text ta="center" fw={600} size="lg" mt="sm">15 min</Text>
                <Text ta="center" size="sm" c="dimmed">Avg Wait Time</Text>
              </Card>
            </SimpleGrid>

            <Card padding="lg" radius="md" withBorder>
              <Title order={4} mb="md">Current Queue</Title>
              {appointments
                .filter(apt => apt.status === 'SCHEDULED' || apt.status === 'ARRIVED')
                .slice(0, 5) // Show first 5 appointments as a simple queue
                .map((appointment, index) => (
                <Group key={appointment.id} justify="space-between" 
                       p="md" style={{ backgroundColor: '#f8f9fa', borderRadius: '8px' }}>
                  <Group>
                    <Badge color="blue" variant="light" size="lg">
                      {index + 1}
                    </Badge>
                    <div>
                      <Text fw={500}>
                        {appointment.patient.firstName} {appointment.patient.lastName}
                      </Text>
                      <Text size="sm" c="dimmed">
                        {appointment.reason || 'Consultation'}
                      </Text>
                    </div>
                  </Group>
                  <Group>
                    <Text size="sm" c="dimmed">
                      Est. {appointment.appointmentTime}
                    </Text>
                    <Badge color={appointment.status === 'ARRIVED' ? 'green' : 'blue'} variant="light">
                      {appointment.status}
                    </Badge>
                  </Group>
                </Group>
              ))}
            </Card>
          </Paper>
        </Tabs.Panel>

        {/* Reminders Tab */}
        <Tabs.Panel value="reminders">
          <Paper p="md" radius="md" withBorder mt="md">
            <Group justify="space-between" mb="lg">
              <Title order={3}>Appointment Reminders</Title>
              <Button leftSection={<IconBell size={16} />}>
                Configure Reminders
              </Button>
            </Group>

            <SimpleGrid cols={{ base: 1, md: 2 }} spacing="lg">
              <Card padding="lg" radius="md" withBorder>
                <Title order={4} mb="md">Reminder Settings</Title>
                <Stack gap="md">
                  <Group justify="space-between">
                    <Text>24-hour reminder</Text>
                    <Badge color="green" variant="light">Enabled</Badge>
                  </Group>
                  <Group justify="space-between">
                    <Text>2-hour reminder</Text>
                    <Badge color="green" variant="light">Enabled</Badge>
                  </Group>
                  <Group justify="space-between">
                    <Text>SMS notifications</Text>
                    <Badge color="blue" variant="light">Active</Badge>
                  </Group>
                  <Group justify="space-between">
                    <Text>Email notifications</Text>
                    <Badge color="blue" variant="light">Active</Badge>
                  </Group>
                </Stack>
              </Card>

              <Card padding="lg" radius="md" withBorder>
                <Title order={4} mb="md">Recent Reminders</Title>
                <Timeline>
                  {[
                    { id: '1', reminderType: 'appointment_reminder', message: 'Upcoming appointment in 2 hours', scheduledTime: new Date(Date.now() + 2 * 60 * 60 * 1000).toISOString(), status: 'sent' },
                    { id: '2', reminderType: 'follow_up', message: 'Follow-up appointment reminder', scheduledTime: new Date(Date.now() + 24 * 60 * 60 * 1000).toISOString(), status: 'pending' },
                    { id: '3', reminderType: 'appointment_reminder', message: 'Appointment confirmation sent', scheduledTime: new Date(Date.now() - 60 * 60 * 1000).toISOString(), status: 'sent' }
                  ].map((reminder) => (
                    <Timeline.Item 
                      key={reminder.id}
                      bullet={<IconBell size={12} />}
                      title={reminder.reminderType.replace('_', ' ').toUpperCase()}
                    >
                      <Text size="sm" c="dimmed">
                        {reminder.message}
                      </Text>
                      <Text size="xs" c="dimmed" mt="xs">
                        {new Date(reminder.scheduledTime).toLocaleString()}
                      </Text>
                      <Badge 
                        color={reminder.status === 'sent' ? 'green' : 'blue'} 
                        variant="light" 
                        size="xs" 
                        mt="xs"
                      >
                        {reminder.status}
                      </Badge>
                    </Timeline.Item>
                  ))}
                </Timeline>
              </Card>
            </SimpleGrid>
          </Paper>
        </Tabs.Panel>

        {/* Analytics Tab */}
        <Tabs.Panel value="analytics">
          <Paper p="md" radius="md" withBorder mt="md">
            <Title order={3} mb="lg">Appointment Analytics</Title>
            
            <SimpleGrid cols={{ base: 1, lg: 2 }} spacing="lg">
              {/* Appointments by Status */}
              <Card padding="lg" radius="md" withBorder>
                <Title order={4} mb="md">Appointments by Status</Title>
                <Text c="dimmed" ta="center" p="xl">
                  Chart component temporarily disabled
                </Text>
              </Card>
              
              {/* Appointments by Type */}
              <Card padding="lg" radius="md" withBorder>
                <Title order={4} mb="md">Appointments by Type</Title>
                <Text c="dimmed" ta="center" p="xl">
                  Chart component temporarily disabled
                </Text>
              </Card>
              
              {/* Daily Appointments Trend */}
              <Card padding="lg" radius="md" withBorder style={{ gridColumn: '1 / -1' }}>
                <Title order={4} mb="md">Daily Appointments</Title>
                <Text c="dimmed" ta="center" p="xl">
                  Chart component temporarily disabled
                </Text>
              </Card>
              
              {/* Peak Hours */}
              <Card padding="lg" radius="md" withBorder>
                <Title order={4} mb="md">Peak Hours</Title>
                <Text c="dimmed" ta="center" p="xl">
                  Chart component temporarily disabled
                </Text>
              </Card>
              
              {/* Revenue Metrics */}
              <Card padding="lg" radius="md" withBorder>
                <Title order={4} mb="md">Revenue Overview</Title>
                <Stack gap="md">
                  <div>
                    <Group justify="space-between" mb="xs">
                      <Text size="sm">Total Revenue</Text>
                      <Text size="sm" fw={500}>‚Çπ{(appointmentStats?.totalRevenue || 0).toLocaleString()}</Text>
                    </Group>
                    <Progress value={85} color="green" />
                  </div>
                  
                  <div>
                    <Group justify="space-between" mb="xs">
                      <Text size="sm">Pending Payments</Text>
                      <Text size="sm" fw={500}>‚Çπ{(appointmentStats?.pendingPayments || 0).toLocaleString()}</Text>
                    </Group>
                    <Progress value={15} color="red" />
                  </div>
                  
                  <div>
                    <Group justify="space-between" mb="xs">
                      <Text size="sm">Average Fee</Text>
                      <Text size="sm" fw={500}>‚Çπ{appointmentStats?.averageFee || 0}</Text>
                    </Group>
                    <Progress value={75} color="blue" />
                  </div>
                </Stack>
              </Card>
            </SimpleGrid>
          </Paper>
        </Tabs.Panel>
      </Tabs>

      {/* Appointment Detail Modal */}
      <Modal
        opened={appointmentDetailOpened}
        onClose={closeAppointmentDetail}
        title="Appointment Details"
        size="lg"
      >
        {selectedAppointment && (
          <Stack gap="md">
            {/* Basic Info */}
            <Group>
              <Avatar size="xl" color="blue" radius="xl">
                {selectedAppointment.patient.firstName[0]}{selectedAppointment.patient.lastName[0]}
              </Avatar>
              <div>
                <Title order={3}>
                  {selectedAppointment.patient.firstName} {selectedAppointment.patient.lastName}
                </Title>
                <Text c="dimmed">{selectedAppointment.appointmentNumber}</Text>
                <Badge color={getStatusColor(selectedAppointment.status)} variant="light" mt="xs">
                  {selectedAppointment.status.replace('_', ' ')}
                </Badge>
              </div>
            </Group>

            <Divider />

            {/* Appointment Details */}
            <SimpleGrid cols={2}>
              <div>
                <Text size="sm" fw={500}>Doctor</Text>
                <Text size="sm" c="dimmed">
                  {selectedAppointment.doctor.firstName} {selectedAppointment.doctor.lastName}
                </Text>
              </div>
              <div>
                <Text size="sm" fw={500}>Department</Text>
                <Text size="sm" c="dimmed">{selectedAppointment.department}</Text>
              </div>
              <div>
                <Text size="sm" fw={500}>Date & Time</Text>
                <Text size="sm" c="dimmed">
                  {new Date(selectedAppointment.appointmentDate).toLocaleDateString()} at {selectedAppointment.appointmentTime}
                </Text>
              </div>
              <div>
                <Text size="sm" fw={500}>Duration</Text>
                <Text size="sm" c="dimmed">{selectedAppointment.duration} minutes</Text>
              </div>
              <div>
                <Text size="sm" fw={500}>Type</Text>
                <Badge color={getTypeColor(selectedAppointment.appointmentType)} variant="light" size="sm">
                  {selectedAppointment.appointmentType.replace('_', ' ')}
                </Badge>
              </div>
              <div>
                <Text size="sm" fw={500}>Priority</Text>
                <Badge color={getPriorityColor(selectedAppointment.priority)} variant="light" size="sm">
                  {selectedAppointment.priority}
                </Badge>
              </div>
            </SimpleGrid>

            {/* Reason and Notes */}
            <div>
              <Text size="sm" fw={500}>Reason for Visit</Text>
              <Text size="sm" c="dimmed">{selectedAppointment.reason}</Text>
            </div>

            {selectedAppointment.notes && (
              <div>
                <Text size="sm" fw={500}>Notes</Text>
                <Text size="sm" c="dimmed">{selectedAppointment.notes}</Text>
              </div>
            )}

            {/* Payment Info */}
            <div>
              <Text size="sm" fw={500}>Consultation Fee</Text>
              <Group>
                <Text size="sm" c="dimmed">‚Çπ{selectedAppointment.consultationFee}</Text>
                <Badge 
                  color={selectedAppointment.isPaid ? 'green' : 'red'} 
                  variant="light" 
                  size="sm"
                >
                  {selectedAppointment.isPaid ? 'Paid' : 'Pending'}
                </Badge>
              </Group>
            </div>

            <Group justify="flex-end">
              <Button variant="light" onClick={closeAppointmentDetail}>
                Close
              </Button>
              <Button onClick={openReschedule}>
                Reschedule
              </Button>
            </Group>
          </Stack>
        )}
      </Modal>

      {/* Book Appointment Modal */}
      <Modal
        opened={bookAppointmentOpened}
        onClose={closeBookAppointment}
        title="Book New Appointment"
        size="lg"
      >
        <Stack gap="md">
          <SimpleGrid cols={2}>
            <Select
              label="Patient"
              placeholder="Select patient"
              data={[]} // TODO: Fetch from patients API
              searchable
              required
            />
            <Select
              label="Doctor"
              placeholder="Select doctor"
              data={[]} // TODO: Fetch from staff API
              searchable
              required
            />
          </SimpleGrid>
          
          <SimpleGrid cols={2}>
            <DatePickerInput
              label="Appointment Date"
              placeholder="Select date"
              required
              value={selectedDate}
              onChange={setSelectedDate as any}
              clearable
            />
            <Select
              label="Time Slot"
              placeholder="Select time"
              data={[
                { value: '09:00', label: '09:00 AM' },
                { value: '09:30', label: '09:30 AM' },
                { value: '10:00', label: '10:00 AM' },
                { value: '10:30', label: '10:30 AM' }
              ]}
              required
            />
          </SimpleGrid>
          
          <SimpleGrid cols={2}>
            <Select
              label="Appointment Type"
              placeholder="Select type"
              data={[
                { value: 'consultation', label: 'Consultation' },
                { value: 'follow_up', label: 'Follow-up' },
                { value: 'emergency', label: 'Emergency' },
                { value: 'routine_checkup', label: 'Routine Checkup' }
              ]}
              required
            />
            <Select
              label="Priority"
              placeholder="Select priority"
              data={[
                { value: 'normal', label: 'Normal' },
                { value: 'high', label: 'High' },
                { value: 'urgent', label: 'Urgent' },
                { value: 'emergency', label: 'Emergency' }
              ]}
              required
            />
          </SimpleGrid>
          
          <Textarea
            label="Reason for Visit"
            placeholder="Describe the reason for the appointment"
            required
          />
          
          <NumberInput
            label="Consultation Fee"
            placeholder="Enter fee amount"
            min={0}
            prefix="‚Çπ"
          />
          
          <Group justify="flex-end">
            <Button variant="light" onClick={closeBookAppointment}>
              Cancel
            </Button>
            <Button onClick={() => {
              // notifications.show({
              //   title: 'Success',
              //   message: 'Appointment booked successfully',
              //   color: 'green',
              // });
              console.log('Appointment booked successfully');
              closeBookAppointment();
            }}>
              Book Appointment
            </Button>
          </Group>
        </Stack>
      </Modal>
    </Container>
  );
};

export default AppointmentManagement;

'use client';

import React, { useState, useMemo, useEffect } from 'react';
import {
  Container,
  Paper,
  Title,
  Group,
  Button,
  TextInput,
  Select,
  Badge,
  Table,
  Modal,
  Text,
  Tabs,
  Card,
  Avatar,
  ActionIcon,
  Menu,
  Stack,
  Divider,
  SimpleGrid,
  ScrollArea,
  ThemeIcon,
  Alert,
  Progress,
  NumberInput,
  Textarea,
  Checkbox,
  List,
  Notification,
  LoadingOverlay,
  Anchor,
  rem
} from '@mantine/core';
import { DatePickerInput } from '@mantine/dates';
import { useDisclosure } from '@mantine/hooks';
import EmptyState from '../../../components/EmptyState';
import { notifications } from '@mantine/notifications';
import { MantineDonutChart, SimpleAreaChart, SimpleBarChart, SimpleLineChart } from '../../../components/MantineChart';
import {
  IconPlus,
  IconSearch,
  IconEdit,
  IconEye,
  IconTrash,
  IconCalendar,
  IconCurrency,
  IconChartBar,
  IconPhone,
  IconMail,
  IconAlertCircle,
  IconCheck,
  IconX,
  IconDotsVertical,
  IconReceipt,
  IconCreditCard,
  IconBuildingBank,
  IconFileText,
  IconDownload,
  IconPrinter,
  IconShare,
  IconCash,
  IconShield,
  IconExclamationMark,
  IconClockHour4,
  IconTrendingUp,
  IconTrendingDown,
  IconUsers,
  IconCalculator,
  IconWallet,
  IconBrandPaypal,
  IconCurrencyDollar,
  IconFileInvoice,
  IconCopyright,
  IconFilter,
  IconRefresh
} from '@tabler/icons-react';

// Import types and services
import {
  Invoice,
  InvoiceStatus,
  Payment,
  PaymentMethod,
  PaymentStatus,
  PaymentTransactionStatus,
  InsuranceClaim,
  ClaimStatus,
  InsuranceProvider,
  PatientInsurance
} from '../../../types/billing';
import { billingService, handleApiError } from '../../../services';

const BillingManagement = () => {
  // State management
  const [activeTab, setActiveTab] = useState<string>('invoices');
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedPatient, setSelectedPatient] = useState<string>('');
  const [selectedStatus, setSelectedStatus] = useState<string>('');
  const [selectedPaymentMethod, setSelectedPaymentMethod] = useState<string>('');
  const [dateRange, setDateRange] = useState<[Date | null, Date | null]>([null, null]);
  const [selectedInvoice, setSelectedInvoice] = useState<Invoice | null>(null);
  const [selectedClaim, setSelectedClaim] = useState<InsuranceClaim | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [statsLoading, setStatsLoading] = useState(true);
  const [statsError, setStatsError] = useState<string | null>(null);

  // Data state
  const [invoices, setInvoices] = useState<Invoice[]>([]);
  const [payments, setPayments] = useState<Payment[]>([]);
  const [insuranceClaims, setInsuranceClaims] = useState<InsuranceClaim[]>([]);
  const [billingStats, setBillingStats] = useState<any>(null);
  const [revenueData, setRevenueData] = useState<any[]>([]);

  // Modal states
  const [invoiceDetailOpened, { open: openInvoiceDetail, close: closeInvoiceDetail }] = useDisclosure(false);
  const [addInvoiceOpened, { open: openAddInvoice, close: closeAddInvoice }] = useDisclosure(false);
  const [addPaymentOpened, { open: openAddPayment, close: closeAddPayment }] = useDisclosure(false);
  const [claimDetailOpened, { open: openClaimDetail, close: closeClaimDetail }] = useDisclosure(false);

  // Load all data on mount
  useEffect(() => {
    loadAllData();
  }, []);

  // Load data based on active tab
  useEffect(() => {
    if (activeTab === 'invoices') {
      loadInvoices();
    } else if (activeTab === 'payments') {
      loadPayments();
    } else if (activeTab === 'insurance') {
      loadInsuranceClaims();
    } else if (activeTab === 'reports') {
      loadReportsData();
    }
  }, [activeTab, searchQuery, selectedPatient, selectedStatus, selectedPaymentMethod]);

  const loadAllData = async () => {
    try {
      setLoading(true);
      setError(null);

      // Load stats and initial data in parallel
      await Promise.all([
        loadStats(),
        loadInvoices(),
        loadPayments(),
        loadInsuranceClaims()
      ]);
    } catch (err) {
      const errorMessage = handleApiError(err);
      setError(errorMessage);
      console.error('Error loading billing data:', err);
      // Fallback to mock data
      setInvoices([] /* TODO: Fetch from API */);
      setPayments([] /* TODO: Fetch from API */);
      setInsuranceClaims([] /* TODO: Fetch from API */);
    } finally {
      setLoading(false);
    }
  };

  const loadStats = async () => {
    try {
      setStatsLoading(true);
      setStatsError(null);
      const response = await billingService.getBillingStats();
      setBillingStats(response.data);
    } catch (err: any) {
      console.warn('Error loading billing stats (using default values):', err.response?.data?.message || err.message);
      setStatsError(null);
      // Set default stats when backend is unavailable
      setBillingStats({
        totalRevenue: 0,
        pendingPayments: 0,
        paidInvoices: 0,
        unpaidInvoices: 0,
        totalInvoices: 0,
        totalPayments: 0
      });
    } finally {
      setStatsLoading(false);
    }
  };

  const loadInvoices = async () => {
    try {
      const filters = {
        search: searchQuery || undefined,
        patientId: selectedPatient || undefined,
        status: selectedStatus || undefined
      };

      const response = await billingService.getInvoices(filters);
      setInvoices(response.data || []);
    } catch (err: any) {
      console.warn('Error loading invoices (using empty data):', err.response?.data?.message || err.message);
      setInvoices([]);
    }
  };

  const loadPayments = async () => {
    try {
      const filters = {
        search: searchQuery || undefined,
        patientId: selectedPatient || undefined,
        paymentMethod: selectedPaymentMethod || undefined
      };

      const response = await billingService.getPayments(filters);
      setPayments(response.data || []);
    } catch (err: any) {
      console.warn('Error loading payments (using empty data):', err.response?.data?.message || err.message);
      setPayments([]);
    }
  };

  const loadInsuranceClaims = async () => {
    try {
      // For now, use mock data as insurance claims API might not be fully implemented
      setInsuranceClaims([] /* TODO: Fetch from API */);
    } catch (err) {
      console.error('Error loading insurance claims:', err);
      setInsuranceClaims([] /* TODO: Fetch from API */);
    }
  };

  const loadReportsData = async () => {
    try {
      // Load revenue data for reports
      const endDate = new Date();
      const startDate = new Date();
      startDate.setMonth(startDate.getMonth() - 12);
      const response = await billingService.getRevenueReport(
        startDate.toISOString(),
        endDate.toISOString()
      );
      setRevenueData(response.data?.invoices || []);
    } catch (err: any) {
      console.warn('Error loading reports data (using empty data):', err.response?.data?.message || err.message);
      setRevenueData([]);
    }
  };

  // Helper functions
  const getStatusColor = (status: InvoiceStatus | PaymentStatus | ClaimStatus | PaymentTransactionStatus) => {
    switch (status) {
      // InvoiceStatus
      case 'DRAFT':
        return 'orange';
      case 'PENDING':
        return 'blue';
      case 'PAID':
        return 'green';
      case 'PARTIALLY_PAID':
        return 'yellow';
      case 'CANCELLED':
      case 'REFUNDED':
        return 'gray';

      // PaymentTransactionStatus
      case 'COMPLETED':
        return 'green';
      case 'FAILED':
        return 'red';

      // ClaimStatus
      case 'submitted':
      case 'acknowledged':
        return 'blue';
      case 'under_review':
        return 'cyan';
      case 'approved':
        return 'green';
      case 'partially_approved':
        return 'yellow';
      case 'denied':
        return 'red';
      case 'appealed':
        return 'orange';
      case 'closed':
        return 'gray';

      default:
        return 'gray';
    }
  };

  const getPaymentMethodIcon = (method: PaymentMethod) => {
    switch (method) {
      case 'CASH': return <IconCash size={16} />;
      case 'CREDIT_CARD': return <IconCreditCard size={16} />;
      case 'DEBIT_CARD': return <IconCreditCard size={16} />;
      case 'BANK_TRANSFER': return <IconBuildingBank size={16} />;
      case 'UPI': return <IconBrandPaypal size={16} />;
      case 'NET_BANKING': return <IconBuildingBank size={16} />;
      case 'CHEQUE': return <IconCopyright size={16} />;
      case 'WALLET': return <IconWallet size={16} />;
      default: return <IconWallet size={16} />;
    }
  };

  const handleViewInvoice = (invoice: Invoice) => {
    setSelectedInvoice(invoice);
    openInvoiceDetail();
  };

  const handleViewClaim = (claim: InsuranceClaim) => {
    setSelectedClaim(claim);
    openClaimDetail();
  };

  const clearFilters = () => {
    setSearchQuery('');
    setSelectedPatient('');
    setSelectedStatus('');
    setSelectedPaymentMethod('');
    setDateRange([null, null]);
  };

  const formatCurrency = (amount: number) => {
    return new Intl.NumberFormat('en-IN', {
      style: 'currency',
      currency: 'INR'
    }).format(amount);
  };

  const formatDate = (date: string | Date) => {
    const d = new Date(date);
    const year = d.getFullYear();
    const month = String(d.getMonth() + 1).padStart(2, '0');
    const day = String(d.getDate()).padStart(2, '0');
    return `${year}-${month}-${day}`;
  };

  // Statistics cards
  const statsCards = billingStats ? [
    {
      title: 'Total Revenue',
      value: formatCurrency(billingStats.totalRevenue || 0),
      icon: IconCurrencyDollar,
      color: 'green',
      trend: '+15.3%'
    },
    {
      title: 'Outstanding Amount',
      value: formatCurrency(billingStats.totalOutstanding || 0),
      icon: IconAlertCircle,
      color: 'red',
      trend: '-8.2%'
    },
    {
      title: 'Insurance Claims',
      value: billingStats.totalClaims || 0,
      icon: IconShield,
      color: 'blue',
      trend: '+12%'
    },
    {
      title: 'Collection Rate',
      value: `${billingStats.collectionRate || 0}%`,
      icon: IconTrendingUp,
      color: 'purple',
      trend: '+2.1%'
    }
  ] : [];

  // Chart data
  const revenueChartData = revenueData.length > 0 ? revenueData.map(item => ({
    month: item.month,
    revenue: item.totalRevenue,
    collections: item.collections
  })) : [];

  const getPaymentMethodColor = (method: PaymentMethod) => {
    switch (method) {
      case 'CASH': return 'green.6';
      case 'CREDIT_CARD': return 'blue.6';
      case 'DEBIT_CARD': return 'cyan.6';
      case 'BANK_TRANSFER': return 'indigo.6';
      case 'UPI': return 'teal.6';
      case 'NET_BANKING': return 'violet.6';
      case 'CHEQUE': return 'orange.6';
      case 'WALLET': return 'grape.6';
      default: return 'gray.6';
    }
  };

  const paymentMethodData = billingStats?.paymentMethodDistribution ?
    Object.entries(billingStats.paymentMethodDistribution)
      .map(([method, amount]) => ({
        name: method.replace('_', ' ').toUpperCase(),
        value: amount as number,
        color: getPaymentMethodColor(method as PaymentMethod)
      })) :
    Object.entries(0 /* TODO: Fetch from API */)
      .map(([method, amount]) => ({
        name: method.replace('_', ' ').toUpperCase(),
        value: amount as number,
        color: getPaymentMethodColor(method as PaymentMethod)
      }));

  const claimStatusCounts: Record<string, number> = {};
  insuranceClaims.forEach((c) => {
    claimStatusCounts[c.status] = (claimStatusCounts[c.status] || 0) + 1;
  });
  const claimStatusData = Object.entries(claimStatusCounts).map(([status, count]) => ({
    status: status.replace('_', ' '),
    count
  }));

  // Filter functions now use API data
  const filteredInvoices = useMemo(() => {
    return invoices.filter((invoice) => {
      const matchesSearch =
        invoice.invoiceNumber?.toLowerCase().includes(searchQuery.toLowerCase()) ||
        invoice.patient?.firstName?.toLowerCase().includes(searchQuery.toLowerCase()) ||
        invoice.patient?.lastName?.toLowerCase().includes(searchQuery.toLowerCase());

      const matchesPatient = !selectedPatient || invoice.patientId === selectedPatient;
      const matchesStatus = !selectedStatus || invoice.status === selectedStatus;

      return matchesSearch && matchesPatient && matchesStatus;
    });
  }, [invoices, searchQuery, selectedPatient, selectedStatus]);

  // Filter payments
  const filteredPayments = useMemo(() => {
    return payments.filter((payment) => {
      const matchesSearch =
        payment.paymentId?.toLowerCase().includes(searchQuery.toLowerCase()) ||
        payment.invoice?.invoiceNumber?.toLowerCase().includes(searchQuery.toLowerCase());

      const matchesMethod = !selectedPaymentMethod || payment.paymentMethod === selectedPaymentMethod;
      const matchesStatus = !selectedStatus || payment.status === selectedStatus;

      return matchesSearch && matchesMethod && matchesStatus;
    });
  }, [payments, searchQuery, selectedPaymentMethod, selectedStatus]);

  // Filter claims
  const filteredClaims = useMemo(() => {
    return insuranceClaims.filter((claim) => {
      const matchesSearch =
        claim.claimNumber?.toLowerCase().includes(searchQuery.toLowerCase()) ||
        claim.patient?.firstName?.toLowerCase().includes(searchQuery.toLowerCase()) ||
        claim.patient?.lastName?.toLowerCase().includes(searchQuery.toLowerCase());

      const matchesStatus = !selectedStatus || claim.status === selectedStatus;

      return matchesSearch && matchesStatus;
    });
  }, [insuranceClaims, searchQuery, selectedStatus]);

  return (
    <Container size="xl" py="md">
      {/* Header */}
      <Group justify="space-between" mb="lg">
        <div>
          <Title order={1}>Billing & Insurance Management</Title>
          <Text c="dimmed" size="sm">
            Manage invoices, payments, insurance claims, and financial reporting
          </Text>
        </div>
        <Group>
          <Button
            leftSection={<IconPlus size={16} />}
            onClick={openAddInvoice}
          >
            New Invoice
          </Button>
          <Button
            variant="light"
            leftSection={<IconCreditCard size={16} />}
            onClick={openAddPayment}
          >
            Record Payment
          </Button>
        </Group>
      </Group>

      {/* Error Display */}
      {error && (
        <Alert icon={<IconAlertCircle size="1rem" />} title="Error" color="red" variant="light" mb="lg">
          {error} - Using cached data
        </Alert>
      )}

      {/* Statistics Cards */}
      {statsLoading ? (
        <SimpleGrid cols={{ base: 1, sm: 2, md: 4 }} mb="lg">
          {[1, 2, 3, 4].map((i) => (
            <Card key={i} padding="lg" radius="md" withBorder>
              <Group justify="space-between">
                <div>
                  <div style={{ height: '1rem', width: '100px', backgroundColor: '#e9ecef', borderRadius: '4px', marginBottom: '8px' }} />
                  <div style={{ height: '2rem', width: '80px', backgroundColor: '#e9ecef', borderRadius: '4px' }} />
                </div>
                <div style={{ width: '40px', height: '40px', backgroundColor: '#e9ecef', borderRadius: '8px' }} />
              </Group>
              <Group justify="space-between" mt="sm">
                <div style={{ height: '1rem', width: '50px', backgroundColor: '#e9ecef', borderRadius: '4px' }} />
                <div style={{ height: '0.75rem', width: '80px', backgroundColor: '#e9ecef', borderRadius: '4px' }} />
              </Group>
            </Card>
          ))}
        </SimpleGrid>
      ) : (
        <SimpleGrid cols={{ base: 1, sm: 2, md: 4 }} mb="lg">
          {statsCards.map((stat) => {
            const Icon = stat.icon;
            return (
              <Card key={stat.title} padding="lg" radius="md" withBorder>
                <Group justify="space-between">
                  <div>
                    <Text c="dimmed" size="sm" fw={500}>
                      {stat.title}
                    </Text>
                    <Text fw={700} size="xl">
                      {stat.value}
                    </Text>
                  </div>
                  <ThemeIcon color={stat.color} size="xl" radius="md" variant="light">
                    <Icon size={24} />
                  </ThemeIcon>
                </Group>
                <Group justify="space-between" mt="sm">
                  <Badge
                    color={stat.trend.startsWith('+') ? 'green' : 'red'}
                    variant="light"
                    size="sm"
                  >
                    {stat.trend}
                  </Badge>
                  <Text size="xs" c="dimmed">vs last month</Text>
                </Group>
              </Card>
            );
          })}
        </SimpleGrid>
      )}

      {/* Main Content Tabs */}
      <Tabs value={activeTab} onChange={(v) => setActiveTab(v || 'invoices')}>
        <Tabs.List>
          <Tabs.Tab value="invoices" leftSection={<IconFileInvoice size={16} />}>
            Invoices
          </Tabs.Tab>
          <Tabs.Tab value="payments" leftSection={<IconCreditCard size={16} />}>
            Payments
          </Tabs.Tab>
          <Tabs.Tab value="insurance" leftSection={<IconShield size={16} />}>
            Insurance Claims
          </Tabs.Tab>
          <Tabs.Tab value="reports" leftSection={<IconChartBar size={16} />}>
            Financial Reports
          </Tabs.Tab>
        </Tabs.List>

        {/* Invoices Tab */}
        <Tabs.Panel value="invoices">
          <Paper p="md" radius="md" withBorder mt="md">
            {/* Search and Filters */}
            <Group mb="md">
              <TextInput
                placeholder="Search invoices..."
                leftSection={<IconSearch size={16} />}
                value={searchQuery}
                onChange={(event) => setSearchQuery(event.currentTarget.value)}
                style={{ flex: 1 }}
              />
              <Select
                placeholder="Patient"
                data={[].map /* TODO: Fetch from API */(patient => ({
                  value: patient.id,
                  label: `${patient.firstName} ${patient.lastName}`
                }))}
                value={selectedPatient}
                onChange={(v) => setSelectedPatient(v || '')}
                clearable
              />
              <Select
                placeholder="Status"
                data={[
                  { value: 'draft', label: 'Draft' },
                  { value: 'pending', label: 'Pending' },
                  { value: 'paid', label: 'Paid' },
                  { value: 'cancelled', label: 'Cancelled' }
                ]}
                value={selectedStatus}
                onChange={(v) => setSelectedStatus(v || '')}
                clearable
              />
              <Button variant="light" onClick={clearFilters}>
                Clear Filters
              </Button>
            </Group>

            {/* Loading State for Invoices */}
            {loading && invoices.length === 0 ? (
              <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '200px' }}>
                <div>
                  <div style={{ width: '40px', height: '40px', border: '3px solid #e9ecef', borderTop: '3px solid #228be6', borderRadius: '50%', animation: 'spin 1s linear infinite', margin: '0 auto 16px' }} />
                  <Text c="dimmed" ta="center">Loading invoices...</Text>
                </div>
              </div>
            ) : (
              <>
                {/* Invoices Table */}
                <ScrollArea>
                  <Table striped highlightOnHover>
                    <Table.Thead>
                      <Table.Tr>
                        <Table.Th>Invoice #</Table.Th>
                        <Table.Th>Patient</Table.Th>
                        <Table.Th>Date</Table.Th>
                        <Table.Th>Due Date</Table.Th>
                        <Table.Th>Amount</Table.Th>
                        <Table.Th>Status</Table.Th>
                        <Table.Th>Payment Status</Table.Th>
                        <Table.Th>Actions</Table.Th>
                      </Table.Tr>
                    </Table.Thead>
                    <Table.Tbody>
                      {filteredInvoices.length === 0 ? (
                        <Table.Tr>
                          <Table.Td colSpan={9}>
                            <EmptyState
                              icon={<IconReceipt size={48} />}
                              title="No bills generated"
                              description="Create your first bill to start billing management"
                              size="sm"
                            />
                          </Table.Td>
                        </Table.Tr>
                      ) : (
                        filteredInvoices.map((invoice) => (
                        <Table.Tr key={invoice.id}>
                          <Table.Td>
                            <Text fw={500}>{invoice.invoiceNumber}</Text>
                          </Table.Td>
                          <Table.Td>
                            <Group>
                              <Avatar color="blue" radius="xl" size="sm">
                                {invoice.patient?.firstName?.[0]}{invoice.patient?.lastName?.[0]}
                              </Avatar>
                              <div>
                                <Text size="sm" fw={500}>
                                  {invoice.patient?.firstName} {invoice.patient?.lastName}
                                </Text>
                                <Text size="xs" c="dimmed">
                                  {invoice.patient?.patientId}
                                </Text>
                              </div>
                            </Group>
                          </Table.Td>
                          <Table.Td>
                            <Text size="sm">
                              {formatDate(invoice.invoiceDate)}
                            </Text>
                          </Table.Td>
                          <Table.Td>
                            <Text
                              size="sm"
                              c={new Date(invoice.dueDate) < new Date() ? 'red' : 'dimmed'}
                            >
                              {formatDate(invoice.dueDate)}
                            </Text>
                          </Table.Td>
                          <Table.Td>
                            <Text fw={600}>{formatCurrency(invoice.totalAmount)}</Text>
                          </Table.Td>
                          <Table.Td>
                            <Badge color={getStatusColor(invoice.status)} variant="light">
                              {invoice.status.replace('_', ' ')}
                            </Badge>
                          </Table.Td>
                          <Table.Td>
                            <Group gap="xs">
                              <Text size="sm">
                                {formatCurrency(invoice.paidAmount || 0)} / {formatCurrency(invoice.totalAmount)}
                              </Text>
                              <Progress
                                value={invoice.totalAmount > 0 ? ((invoice.paidAmount || 0) / invoice.totalAmount) * 100 : 0}
                                size="xs"
                                w={60}
                                color={(invoice.paidAmount || 0) >= invoice.totalAmount ? 'green' : 'blue'}
                              />
                            </Group>
                          </Table.Td>
                          <Table.Td>
                            <Group gap="xs">
                              <ActionIcon
                                variant="subtle"
                                color="blue"
                                onClick={() => handleViewInvoice(invoice)}
                              >
                                <IconEye size={16} />
                              </ActionIcon>
                              <ActionIcon
                                variant="subtle"
                                color="green"
                              >
                                <IconEdit size={16} />
                              </ActionIcon>
                              <Menu>
                                <Menu.Target>
                                  <ActionIcon variant="subtle" color="gray">
                                    <IconDotsVertical size={16} />
                                  </ActionIcon>
                                </Menu.Target>
                                <Menu.Dropdown>
                                  <Menu.Item leftSection={<IconDownload size={14} />}>
                                    Download PDF
                                  </Menu.Item>
                                  <Menu.Item leftSection={<IconPrinter size={14} />}>
                                    Print
                                  </Menu.Item>
                                  <Menu.Item leftSection={<IconMail size={14} />}>
                                    Send Email
                                  </Menu.Item>
                                  <Menu.Divider />
                                  <Menu.Item
                                    leftSection={<IconTrash size={14} />}
                                    color="red"
                                  >
                                    Delete
                                  </Menu.Item>
                                </Menu.Dropdown>
                              </Menu>
                            </Group>
                          </Table.Td>
                        </Table.Tr>
                      )))}
                    </Table.Tbody>
                  </Table>
                </ScrollArea>
              </>
            )}
          </Paper>
        </Tabs.Panel>

        {/* Payments Tab */}
        <Tabs.Panel value="payments">
          <Paper p="md" radius="md" withBorder mt="md">
            <Group justify="space-between" mb="lg">
              <Title order={3}>Payment Records</Title>
              <Button leftSection={<IconPlus size={16} />} onClick={openAddPayment}>
                Record Payment
              </Button>
            </Group>

            {/* Payment Filters */}
            <Group mb="md">
              <TextInput
                placeholder="Search payments..."
                leftSection={<IconSearch size={16} />}
                value={searchQuery}
                onChange={(event) => setSearchQuery(event.currentTarget.value)}
                style={{ flex: 1 }}
              />
              <Select
                placeholder="Payment Method"
                data={[
                  { value: 'cash', label: 'Cash' },
                  { value: 'credit_card', label: 'Credit Card' },
                  { value: 'debit_card', label: 'Debit Card' },
                  { value: 'bank_transfer', label: 'Bank Transfer' },
                  { value: 'upi', label: 'UPI' },
                  { value: 'net_banking', label: 'Net Banking' },
                  { value: 'cheque', label: 'Cheque' },
                  { value: 'wallet', label: 'Wallet' },
                  { value: 'other', label: 'Other' }
                ]}
                value={selectedPaymentMethod}
                onChange={(v) => setSelectedPaymentMethod(v || '')}
                clearable
              />
              <Select
                placeholder="Status"
                data={[
                  { value: 'pending', label: 'Pending' },
                  { value: 'completed', label: 'Completed' },
                  { value: 'failed', label: 'Failed' },
                  { value: 'refunded', label: 'Refunded' },
                  { value: 'cancelled', label: 'Cancelled' }
                ]}
                value={selectedStatus}
                onChange={(v) => setSelectedStatus(v || '')}
                clearable
              />
            </Group>

            {/* Payments Grid */}
            <SimpleGrid cols={{ base: 1, lg: 2 }} spacing="lg">
              {filteredPayments.map((payment) => (
                <Card key={payment.id} padding="lg" radius="md" withBorder>
                  <Group justify="space-between" mb="md">
                    <div>
                      <Text fw={600} size="lg">{payment.paymentId}</Text>
                      <Text size="sm" c="dimmed">
                        {formatDate(payment.paymentDate)}
                      </Text>
                    </div>
                    <Badge color={getStatusColor(payment.status)} variant="light">
                      {payment.status}
                    </Badge>
                  </Group>

                  <Stack gap="sm" mb="md">
                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Amount</Text>
                      <Text fw={600} size="lg">{formatCurrency(payment.amount)}</Text>
                    </Group>
                    
                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Payment Method</Text>
                      <Group gap="xs">
                        {getPaymentMethodIcon(payment.paymentMethod)}
                        <Text size="sm" fw={500}>
                          {payment.paymentMethod.replace('_', ' ').toUpperCase()}
                        </Text>
                      </Group>
                    </Group>

                    {payment.invoiceNumber && (
                      <Group justify="space-between">
                        <Text size="sm" c="dimmed">Invoice</Text>
                        <Anchor size="sm" fw={500}>
                          {payment.invoiceNumber}
                        </Anchor>
                      </Group>
                    )}

                    {payment.transactionId && (
                      <Group justify="space-between">
                        <Text size="sm" c="dimmed">Transaction ID</Text>
                        <Text size="sm" fw={500}>{payment.transactionId}</Text>
                      </Group>
                    )}
                  </Stack>

                  {payment.notes && (
                    <Text size="sm" c="dimmed" style={{ 
                      backgroundColor: '#f8f9fa', 
                      padding: '8px', 
                      borderRadius: '4px' 
                    }}>
                      <strong>Notes:</strong> {payment.notes}
                    </Text>
                  )}
                </Card>
              ))}
            </SimpleGrid>
          </Paper>
        </Tabs.Panel>

        {/* Insurance Claims Tab */}
        <Tabs.Panel value="insurance">
          <Paper p="md" radius="md" withBorder mt="md">
            <Group justify="space-between" mb="lg">
              <Title order={3}>Insurance Claims</Title>
              <Button leftSection={<IconPlus size={16} />}>
                Submit Claim
              </Button>
            </Group>

            {/* Claims Table */}
            <ScrollArea>
              <Table striped highlightOnHover>
                <Table.Thead>
                  <Table.Tr>
                    <Table.Th>Claim #</Table.Th>
                    <Table.Th>Patient</Table.Th>
                    <Table.Th>Insurance Provider</Table.Th>
                    <Table.Th>Claim Amount</Table.Th>
                    <Table.Th>Approved Amount</Table.Th>
                    <Table.Th>Status</Table.Th>
                    <Table.Th>Date Submitted</Table.Th>
                    <Table.Th>Actions</Table.Th>
                  </Table.Tr>
                </Table.Thead>
                <Table.Tbody>
                  {filteredClaims.map((claim) => (
                    <Table.Tr key={claim.id}>
                      <Table.Td>
                        <Text fw={500}>{claim.claimNumber}</Text>
                      </Table.Td>
                      <Table.Td>
                        <Group>
                          <Avatar color="blue" radius="xl" size="sm">
                            {claim.patient.firstName[0]}{claim.patient.lastName[0]}
                          </Avatar>
                          <div>
                            <Text size="sm" fw={500}>
                              {claim.patient.firstName} {claim.patient.lastName}
                            </Text>
                            <Text size="xs" c="dimmed">
                              {claim.patient.patientId}
                            </Text>
                          </div>
                        </Group>
                      </Table.Td>
                      <Table.Td>
                        <div>
                          <Text size="sm" fw={500}>{claim.insuranceProvider?.providerName || claim.insurance.providerName}</Text>
                          {claim.policyNumber && (
                            <Text size="xs" c="dimmed">{claim.policyNumber}</Text>
                          )}
                        </div>
                      </Table.Td>
                      <Table.Td>
                        <Text fw={600}>{formatCurrency((claim.claimAmount ?? claim.claimedAmount) as number)}</Text>
                      </Table.Td>
                      <Table.Td>
                        <Text fw={600} c={claim.approvedAmount ? 'green' : 'dimmed'}>
                          {claim.approvedAmount ? formatCurrency(claim.approvedAmount) : 'Pending'}
                        </Text>
                      </Table.Td>
                      <Table.Td>
                        <Badge color={getStatusColor(claim.status)} variant="light">
                          {claim.status.replace('_', ' ')}
                        </Badge>
                      </Table.Td>
                      <Table.Td>
                        <Text size="sm">
                          {formatDate(claim.submissionDate)}
                        </Text>
                      </Table.Td>
                      <Table.Td>
                        <Group gap="xs">
                          <ActionIcon
                            variant="subtle"
                            color="blue"
                            onClick={() => handleViewClaim(claim)}
                          >
                            <IconEye size={16} />
                          </ActionIcon>
                          <ActionIcon variant="subtle" color="green">
                            <IconEdit size={16} />
                          </ActionIcon>
                          <ActionIcon variant="subtle" color="orange">
                            <IconDownload size={16} />
                          </ActionIcon>
                        </Group>
                      </Table.Td>
                    </Table.Tr>
                  ))}
                </Table.Tbody>
              </Table>
            </ScrollArea>
          </Paper>
        </Tabs.Panel>

        {/* Financial Reports Tab */}
        <Tabs.Panel value="reports">
          <Paper p="md" radius="md" withBorder mt="md">
            <Title order={3} mb="lg">Financial Reports & Analytics</Title>
            
            <SimpleGrid cols={{ base: 1, lg: 2 }} spacing="lg">
              {/* Revenue Trends */}
              <Card padding="lg" radius="md" withBorder style={{ gridColumn: '1 / -1' }}>
                <Title order={4} mb="md">Monthly Revenue & Collections</Title>
                <SimpleAreaChart
                  data={revenueData}
                  dataKey="month"
                  series={[
                    { name: 'revenue', color: 'blue.6' },
                    { name: 'collections', color: 'green.6' }
                  ]}
                />
              </Card>
              
              {/* Payment Methods Distribution */}
              <Card padding="lg" radius="md" withBorder>
                <Title order={4} mb="md">Payment Methods</Title>
                <MantineDonutChart
                  data={paymentMethodData}
                  size={160}
                  thickness={30}
                  withLabels
                />
              </Card>
              
              {/* Claim Status Distribution */}
              <Card padding="lg" radius="md" withBorder>
                <Title order={4} mb="md">Insurance Claims Status</Title>
                <SimpleBarChart
                  data={claimStatusData}
                  dataKey="status"
                  series={[{ name: 'count', color: 'orange.6' }]}
                />
              </Card>
              
              {/* Key Metrics */}
              <Card padding="lg" radius="md" withBorder>
                <Title order={4} mb="md">Key Financial Metrics</Title>
                <Stack gap="md">
                  <Group justify="space-between" p="sm" 
                         style={{ backgroundColor: '#f8f9fa', borderRadius: '6px' }}>
                    <Text size="sm" fw={500}>Average Invoice Amount</Text>
                    <Text size="sm" fw={600}>
                      {formatCurrency(0 /* TODO: Fetch from API */)}
                    </Text>
                  </Group>
                  <Group justify="space-between" p="sm" 
                         style={{ backgroundColor: '#f8f9fa', borderRadius: '6px' }}>
                    <Text size="sm" fw={500}>Days Sales Outstanding</Text>
                    <Text size="sm" fw={600}>
                      {0 /* TODO: Fetch from API */} days
                    </Text>
                  </Group>
                  <Group justify="space-between" p="sm" 
                         style={{ backgroundColor: '#f8f9fa', borderRadius: '6px' }}>
                    <Text size="sm" fw={500}>Insurance Coverage Rate</Text>
                    <Text size="sm" fw={600}>
                      {0 /* TODO: Fetch from API */}%
                    </Text>
                  </Group>
                  <Group justify="space-between" p="sm" 
                         style={{ backgroundColor: '#f8f9fa', borderRadius: '6px' }}>
                    <Text size="sm" fw={500}>Bad Debt Rate</Text>
                    <Text size="sm" fw={600} c="red">
                      {formatCurrency(0 /* TODO: Fetch from API */)}
                    </Text>
                  </Group>
                </Stack>
              </Card>
              
              {/* Quick Actions */}
              <Card padding="lg" radius="md" withBorder>
                <Title order={4} mb="md">Quick Actions</Title>
                <Stack gap="sm">
                  <Button fullWidth leftSection={<IconDownload size={16} />} variant="light">
                    Export Revenue Report
                  </Button>
                  <Button fullWidth leftSection={<IconFileText size={16} />} variant="light">
                    Generate Aging Report
                  </Button>
                  <Button fullWidth leftSection={<IconCalculator size={16} />} variant="light">
                    Tax Summary Report
                  </Button>
                  <Button fullWidth leftSection={<IconChartBar size={16} />} variant="light">
                    Performance Analytics
                  </Button>
                </Stack>
              </Card>
            </SimpleGrid>
          </Paper>
        </Tabs.Panel>
      </Tabs>

      {/* Invoice Detail Modal */}
      <Modal
        opened={invoiceDetailOpened}
        onClose={closeInvoiceDetail}
        title="Invoice Details"
        size="xl"
      >
        {selectedInvoice && (
          <ScrollArea h={600}>
            <Stack gap="md">
              {/* Invoice Header */}
              <Group justify="space-between">
                <div>
                  <Title order={3}>{selectedInvoice.invoiceNumber}</Title>
                  <Text c="dimmed">
                    {formatDate(selectedInvoice.invoiceDate)}
                  </Text>
                </div>
                <Badge color={getStatusColor(selectedInvoice.status)} variant="light" size="lg">
                  {selectedInvoice.status.replace('_', ' ')}
                </Badge>
              </Group>

              <Divider />

              {/* Patient & Billing Info */}
              <SimpleGrid cols={2}>
                <div>
                  <Text size="sm" fw={500} mb="sm">Bill To:</Text>
                  <Stack gap={4}>
                    <Text size="sm">
                      {selectedInvoice.patient.firstName} {selectedInvoice.patient.lastName}
                    </Text>
                    <Text size="sm" c="dimmed">{selectedInvoice.patient.patientId}</Text>
                    {selectedInvoice.billingAddress && (
                      <Text size="sm" c="dimmed">{selectedInvoice.billingAddress.street}</Text>
                    )}
                    {selectedInvoice.billingAddress && (
                      <Text size="sm" c="dimmed">
                        {selectedInvoice.billingAddress.city}, {selectedInvoice.billingAddress.state}
                      </Text>
                    )}
                  </Stack>
                </div>
                <div>
                  <Text size="sm" fw={500} mb="sm">Invoice Details:</Text>
                  <Stack gap={4}>
                    <Text size="sm">Due Date: {formatDate(selectedInvoice.dueDate)}</Text>
                    <Text size="sm">Terms: {selectedInvoice.paymentTerms} days</Text>
                    {selectedInvoice.insuranceClaim && (
                      <Text size="sm">Insurance Claim: {selectedInvoice.insuranceClaim.claimNumber}</Text>
                    )}
                  </Stack>
                </div>
              </SimpleGrid>

              <Divider />

              {/* Invoice Items */}
              <div>
                <Text size="sm" fw={500} mb="sm">Items & Services</Text>
                <Table>
                  <Table.Thead>
                    <Table.Tr>
                      <Table.Th>Description</Table.Th>
                      <Table.Th>Quantity</Table.Th>
                      <Table.Th>Unit Price</Table.Th>
                      <Table.Th>Total</Table.Th>
                    </Table.Tr>
                  </Table.Thead>
                  <Table.Tbody>
                    {selectedInvoice.items.map((item) => (
                      <Table.Tr key={item.id}>
                        <Table.Td>
                          <div>
                            <Text size="sm" fw={500}>{item.description}</Text>
                            {item.itemCode && (
                              <Text size="xs" c="dimmed">Code: {item.itemCode}</Text>
                            )}
                          </div>
                        </Table.Td>
                        <Table.Td>{item.quantity}</Table.Td>
                        <Table.Td>{formatCurrency(item.unitPrice)}</Table.Td>
                        <Table.Td>{formatCurrency((item.totalAmount ?? item.totalPrice) as number)}</Table.Td>
                      </Table.Tr>
                    ))}
                  </Table.Tbody>
                </Table>
              </div>

              <Divider />

              {/* Totals */}
              <Group justify="flex-end">
                <Stack gap="xs" align="flex-end">
                  <Group>
                    <Text size="sm">Subtotal:</Text>
                    <Text size="sm" fw={600}>{formatCurrency(selectedInvoice.subtotal)}</Text>
                  </Group>
                  <Group>
                    <Text size="sm">Tax ({selectedInvoice.taxRate ?? 0}%):</Text>
                    <Text size="sm" fw={600}>{formatCurrency(selectedInvoice.taxAmount ?? 0)}</Text>
                  </Group>
                  <Group>
                    <Text fw={700}>Total Amount:</Text>
                    <Text fw={700} size="lg">{formatCurrency(selectedInvoice.totalAmount)}</Text>
                  </Group>
                  <Group>
                    <Text size="sm" c="green">Paid Amount:</Text>
                    <Text size="sm" fw={600} c="green">{formatCurrency(selectedInvoice.paidAmount)}</Text>
                  </Group>
                  <Group>
                    <Text size="sm" c="red">Outstanding:</Text>
                    <Text size="sm" fw={600} c="red">
                      {formatCurrency(selectedInvoice.totalAmount - selectedInvoice.paidAmount)}
                    </Text>
                  </Group>
                </Stack>
              </Group>

              <Group justify="flex-end" mt="lg">
                <Button variant="light" onClick={closeInvoiceDetail}>
                  Close
                </Button>
                <Button leftSection={<IconDownload size={16} />}>
                  Download PDF
                </Button>
                <Button leftSection={<IconMail size={16} />} variant="outline">
                  Send Email
                </Button>
              </Group>
            </Stack>
          </ScrollArea>
        )}
      </Modal>

      {/* Add Invoice Modal */}
      <Modal
        opened={addInvoiceOpened}
        onClose={closeAddInvoice}
        title="Create New Invoice"
        size="lg"
      >
        <Stack gap="md">
          <SimpleGrid cols={2}>
            <Select
              label="Patient"
              placeholder="Select patient"
              data={[].map /* TODO: Fetch from API */(patient => ({ 
                value: patient.id, 
                label: `${patient.firstName} ${patient.lastName}` 
              }))}
              required
            />
            <DatePickerInput
              label="Due Date"
              placeholder="Select due date"
              required
            />
          </SimpleGrid>
          
          <NumberInput
            label="Payment Terms (Days)"
            placeholder="30"
            defaultValue={30}
            min={0}
            max={365}
          />
          
          <Divider label="Invoice Items" labelPosition="center" />
          
          <SimpleGrid cols={3}>
            <TextInput
              label="Service/Item"
              placeholder="Consultation fee"
              required
            />
            <NumberInput
              label="Quantity"
              placeholder="1"
              min={1}
              defaultValue={1}
            />
            <NumberInput
              label="Unit Price"
              placeholder="500"
              min={0}
              leftSection="‚Çπ"
            />
          </SimpleGrid>
          
          <Button variant="light" leftSection={<IconPlus size={16} />}>
            Add More Items
          </Button>
          
          <Divider />
          
          <SimpleGrid cols={2}>
            <NumberInput
              label="Tax Rate (%)"
              placeholder="18"
              min={0}
              max={100}
              defaultValue={18}
            />
            <NumberInput
              label="Discount Amount"
              placeholder="0"
              min={0}
              leftSection="‚Çπ"
            />
          </SimpleGrid>
          
          <Textarea
            label="Notes"
            placeholder="Additional notes or terms..."
            rows={3}
          />
          
          <Group justify="flex-end">
            <Button variant="light" onClick={closeAddInvoice}>
              Cancel
            </Button>
            <Button onClick={() => {
              notifications.show({
                title: 'Success',
                message: 'Invoice created successfully',
                color: 'green',
              });
              closeAddInvoice();
            }}>
              Create Invoice
            </Button>
          </Group>
        </Stack>
      </Modal>

      {/* Add Payment Modal */}
      <Modal
        opened={addPaymentOpened}
        onClose={closeAddPayment}
        title="Record Payment"
        size="md"
      >
        <Stack gap="md">
          <Select
            label="Invoice"
            placeholder="Select invoice"
            data={[].map /* TODO: Fetch from API */(invoice => ({ 
              value: invoice.id, 
              label: `${invoice.invoiceNumber} - ${formatCurrency(invoice.totalAmount - invoice.paidAmount)}` 
            }))}
            required
          />
          
          <NumberInput
            label="Payment Amount"
            placeholder="Enter amount"
            leftSection="‚Çπ"
            min={0}
            required
          />
          
          <Select
            label="Payment Method"
            placeholder="Select method"
            data={[
              { value: 'cash', label: 'Cash' },
              { value: 'credit_card', label: 'Credit Card' },
              { value: 'debit_card', label: 'Debit Card' },
              { value: 'bank_transfer', label: 'Bank Transfer' },
              { value: 'online', label: 'Online Payment' }
            ]}
            required
          />
          
          <TextInput
            label="Transaction ID"
            placeholder="Enter transaction reference"
          />
          
          <DatePickerInput
            label="Payment Date"
            placeholder="Select date"
            value={new Date()}
          />
          
          <Textarea
            label="Notes"
            placeholder="Payment notes..."
            rows={3}
          />
          
          <Group justify="flex-end">
            <Button variant="light" onClick={closeAddPayment}>
              Cancel
            </Button>
            <Button onClick={() => {
              notifications.show({
                title: 'Success',
                message: 'Payment recorded successfully',
                color: 'green',
              });
              closeAddPayment();
            }}>
              Record Payment
            </Button>
          </Group>
        </Stack>
      </Modal>
    </Container>
  );
};

