// Types
interface CommunicationTemplate {
  id: string;
  name: string;
  type: 'sms' | 'whatsapp' | 'email' | 'push' | 'voice';
  category: 'appointment' | 'reminder' | 'follow-up' | 'emergency' | 'marketing' | 'billing' | 'general';
  subject?: string;
  content: string;
  variables: string[];
  isActive: boolean;
  createdDate: string;
  lastUsed?: string;
  usageCount: number;
  language: string;
}

interface CommunicationMessage {
  id: string;
  templateId?: string;
  templateName?: string;
  type: 'sms' | 'whatsapp' | 'email' | 'push' | 'voice';
  recipient: {
    id: string;
    name: string;
    phone?: string;
    email?: string;
    type: 'patient' | 'doctor' | 'staff' | 'group';
  };
  subject?: string;
  content: string;
  status: 'pending' | 'sent' | 'delivered' | 'read' | 'failed' | 'scheduled';
  priority: 'high' | 'medium' | 'low';
  scheduledTime?: string;
  sentTime?: string;
  deliveredTime?: string;
  readTime?: string;
  attempts: number;
  cost: number;
  errorMessage?: string;
  metadata: {
    patientId?: string;
    appointmentId?: string;
    billId?: string;
    campaignId?: string;
  };
}

interface CommunicationCampaign {
  id: string;
  name: string;
  description: string;
  type: 'sms' | 'whatsapp' | 'email' | 'multi-channel';
  templateId: string;
  templateName: string;
  targetAudience: {
    type: 'all-patients' | 'specific-patients' | 'department' | 'age-group' | 'custom';
    criteria: Record<string, any>;
    count: number;
  };
  status: 'draft' | 'scheduled' | 'running' | 'completed' | 'paused' | 'failed';
  scheduledTime?: string;
  startTime?: string;
  endTime?: string;
  totalRecipients: number;
  sentCount: number;
  deliveredCount: number;
  readCount: number;
  failedCount: number;
  totalCost: number;
  createdBy: string;
  createdDate: string;
}

interface NotificationSettings {
  id: string;
  userId: string;
  userType: 'patient' | 'doctor' | 'admin' | 'staff';
  channels: {
    sms: boolean;
    whatsapp: boolean;
    email: boolean;
    push: boolean;
    voice: boolean;
  };
  categories: {
    appointments: boolean;
    reminders: boolean;
    emergencies: boolean;
    billing: boolean;
    marketing: boolean;
    reports: boolean;
  };
  frequency: 'immediate' | 'hourly' | 'daily' | 'weekly';
  quietHours: {
    enabled: boolean;
    startTime: string;
    endTime: string;
  };
}

