  IconShare,
  IconActivity,
  IconExclamationMark,
  IconClockHour4,
  IconTrendingUp,
  IconTrendingDown,
  IconUsers,
  IconCalculator,
  IconSettings,
  IconRefresh,
  IconFilter,
  IconBarcode,
  IconTemperature,
  IconShieldCheck,
  IconCircleCheck,
  IconClipboard,
  IconReportMedical,
  IconLungs,
  IconHeart,
  IconBrain,
  IconBone,
  IconStethoscope,
  IconMedicalCross,
  IconPackage,
  IconTruck,
  IconCash,
  IconReceipt,
  IconNotes,
  IconClock,
  IconTag,
  IconAlarm,
  IconInfoCircle,
  IconBed,
  IconAmbulance,
  IconBell,
  IconFlask,
  IconDroplet,
  IconNurse,
  IconBandage,
  IconPill,
} from '@tabler/icons-react';

// Import types and mock data
import {
  EmergencyCase,
  Triage,
  TriageLevel,
  CaseStatus,
  ICUBed,
  BedStatus,
  VitalSigns,
  CriticalCareEquipment,
  EquipmentStatus,
  EmergencyProtocol,
  EmergencyStats,
  EmergencyFilters
} from '../../../types/emergency';

// Mock data imports removed - using API data only

const EmergencyManagement = () => {
  // State management
  const [activeTab, setActiveTab] = useState<string>('dashboard');
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedTriage, setSelectedTriage] = useState<string>('');
  const [selectedStatus, setSelectedStatus] = useState<string>('');
  const [selectedBedStatus, setSelectedBedStatus] = useState<string>('');
  const [selectedCase, setSelectedCase] = useState<any | null>(null);
  const [selectedBed, setSelectedBed] = useState<ICUBed | null>(null);

  // Modal states
  const [caseDetailOpened, { open: openCaseDetail, close: closeCaseDetail }] = useDisclosure(false);
  const [addCaseOpened, { open: openAddCase, close: closeAddCase }] = useDisclosure(false);
  const [bedDetailOpened, { open: openBedDetail, close: closeBedDetail }] = useDisclosure(false);
  const [triageOpened, { open: openTriage, close: closeTriage }] = useDisclosure(false);
  const [protocolOpened, { open: openProtocol, close: closeProtocol }] = useDisclosure(false);

  // Filter emergency cases
  const filteredCases = useMemo(() => {
    const cases: any[] = []; // TODO: Fetch from API
    return cases.filter((emergencyCase) => {
      const matchesSearch = 
        emergencyCase.patient.firstName.toLowerCase().includes(searchQuery.toLowerCase()) ||
        emergencyCase.patient.lastName.toLowerCase().includes(searchQuery.toLowerCase()) ||
        emergencyCase.caseNumber.toLowerCase().includes(searchQuery.toLowerCase());
      
      const matchesTriage = !selectedTriage || emergencyCase.triageLevel === selectedTriage;
      const matchesStatus = !selectedStatus || emergencyCase.status === selectedStatus;

      return matchesSearch && matchesTriage && matchesStatus;
    });
  }, [searchQuery, selectedTriage, selectedStatus]);

  // Filter ICU beds
  const filteredBeds = useMemo(() => {
    const beds: any[] = []; // TODO: Fetch from API
    return beds.filter((bed) => {
      const matchesSearch = 
        bed.bedNumber.toLowerCase().includes(searchQuery.toLowerCase()) ||
        (bed.patientName && 
         bed.patientName.toLowerCase().includes(searchQuery.toLowerCase()));
      
      const matchesStatus = !selectedBedStatus || bed.status === selectedBedStatus;

      return matchesSearch && matchesStatus;
    });
  }, [searchQuery, selectedBedStatus]);

  // Helper functions
  const getTriageColor = (level: number | TriageLevel) => {
    switch (level) {
      case 1:
      case 'immediate': return 'red';    // Resuscitation/Immediate
      case 2:
      case 'urgent': return 'orange'; // Emergency/Urgent
      case 3:
      case 'less_urgent': return 'yellow'; // Urgent/Less Urgent
      case 4:
      case 'non_urgent': return 'green';  // Less Urgent/Non-urgent
      case 5: return 'blue';   // Non-urgent
      default: return 'gray';
    }
  };

  const getStatusColor = (status: CaseStatus | BedStatus | EquipmentStatus) => {
    switch (status) {
      case 'waiting':
      case 'available':
      case 'operational':
      case 'available': return 'green';
      case 'in_progress':
      case 'occupied':
      case 'maintenance': return 'orange';
      case 'completed':
      case 'discharged': return 'blue';
      case 'cancelled':
      case 'out_of_service': return 'red';
      case 'transferred': return 'purple';
      default: return 'gray';
    }
  };

  const getTriageLabel = (level: number | TriageLevel) => {
    switch (level) {
      case 1: return 'Resuscitation';
      case 2: return 'Emergency';
      case 3: return 'Urgent';
      case 4: return 'Less Urgent';
      case 5: return 'Non-urgent';
      case 'immediate': return 'Immediate';
      case 'urgent': return 'Urgent';
      case 'less_urgent': return 'Less Urgent';
      case 'non_urgent': return 'Non-urgent';
      default: return 'Unknown';
    }
  };

  const handleViewCase = (emergencyCase: any) => {
    setSelectedCase(emergencyCase);
    openCaseDetail();
  };

  const handleViewBed = (bed: ICUBed) => {
    setSelectedBed(bed);
    openBedDetail();
  };

  const clearFilters = () => {
    setSearchQuery('');
    setSelectedTriage('');
    setSelectedStatus('');
    setSelectedBedStatus('');
  };

  const getVitalStatus = (value: number, normal: { min: number; max: number }) => {
    if (value < normal.min || value > normal.max) return 'critical';
    if (value < normal.min * 1.1 || value > normal.max * 0.9) return 'warning';
    return 'normal';
  };

  const getVitalColor = (status: string) => {
    switch (status) {
      case 'critical': return 'red';
      case 'warning': return 'orange';
      case 'normal': return 'green';
      default: return 'gray';
    }
  };

  // Statistics cards
  const statsCards = [
    {
      title: 'Active Cases',
      value: 0,
      icon: IconUrgent,
      color: 'red',
      trend: '+0'
    },
    {
      title: 'ICU Beds',
      value: `0/0`,
      icon: IconBed,
      color: 'blue',
      trend: '0% occupied'
    },
    {
      title: 'Average Wait Time',
      value: `0min`,
      icon: IconClockHour4,
      color: 'orange',
      trend: '0min'
    },
    {
      title: 'Code Blue Today',
      value: 0,
      icon: IconAlertTriangle,
      color: 'purple',
      trend: '+0'
    }
  ];

  // Chart data
  const triageDistribution = [];

  const hourlyAdmissions = [];
  const bedOccupancy = [];

  return (
    <Container size="xl" py="md">
      {/* Header */}
      <Group justify="space-between" mb="lg">
        <div>
          <Title order={1}>Emergency Management & Disaster Response</Title>
          <Text c="dimmed" size="sm">
            Manage emergency incidents, resources, evacuation procedures, and disaster recovery
          </Text>
        </div>
        <Group>
          <Button
            leftSection={<IconPlus size={16} />}
            onClick={openAddCase}
            color="red"
          >
            Emergency Case
          </Button>
          <Button
            variant="light"
            leftSection={<IconBell size={16} />}
            color="red"
            onClick={openProtocol}
          >
            Code Blue
          </Button>
        </Group>
      </Group>

      {/* Critical Alerts */}
      <Alert 
        variant="light" 
        color="red" 
        title="Critical Alerts" 
        icon={<IconAlertTriangle size={16} />}
        mb="lg"
      >
        <Stack gap="xs">
          <Text size="sm">‚Ä¢ 3 patients waiting in resuscitation bay</Text>
          <Text size="sm">‚Ä¢ ICU Bed #7 - Equipment malfunction</Text>
          <Text size="sm">‚Ä¢ Code Blue - Room 204 (5 minutes ago)</Text>
        </Stack>
      </Alert>

      {/* Statistics Cards */}
      <SimpleGrid cols={{ base: 1, sm: 2, md: 4 }} mb="lg">
        {statsCards.map((stat) => {
          const Icon = stat.icon;
          return (
            <Card key={stat.title} padding="lg" radius="md" withBorder>
              <Group justify="space-between">
                <div>
                  <Text c="dimmed" size="sm" fw={500}>
                    {stat.title}
                  </Text>
                  <Text fw={700} size="xl">
                    {stat.value}
                  </Text>
                </div>
                <ThemeIcon color={stat.color} size="xl" radius="md" variant="light">
                  <Icon size={24} />
                </ThemeIcon>
              </Group>
              <Group justify="space-between" mt="sm">
                <Badge 
                  color={stat.trend.includes('+') || stat.trend.includes('-') ? 
                    (stat.trend.startsWith('+') ? 'red' : 'green') : 'blue'} 
                  variant="light"
                  size="sm"
                >
                  {stat.trend}
                </Badge>
                <Text size="xs" c="dimmed">real-time</Text>
              </Group>
            </Card>
          );
        })}
      </SimpleGrid>

      {/* Main Content Tabs */}
      <Tabs value={activeTab} onChange={setActiveTab}>
        <Tabs.List>
          <Tabs.Tab value="dashboard" leftSection={<IconChartBar size={16} />}>
            Dashboard
          </Tabs.Tab>
          <Tabs.Tab value="triage" leftSection={<IconUrgent size={16} />}>
            Triage Queue
          </Tabs.Tab>
          <Tabs.Tab value="cases" leftSection={<IconAlertTriangle size={16} />}>
            Emergency Cases
          </Tabs.Tab>
          <Tabs.Tab value="icu" leftSection={<IconBed size={16} />}>
            ICU Management
          </Tabs.Tab>
          <Tabs.Tab value="equipment" leftSection={<IconSettings size={16} />}>
            Equipment
          </Tabs.Tab>
          <Tabs.Tab value="protocols" leftSection={<IconClipboardList size={16} />}>
            Protocols
          </Tabs.Tab>
        </Tabs.List>

        {/* Dashboard Tab */}
        <Tabs.Panel value="dashboard">
          <Paper p="md" radius="md" withBorder mt="md">
            <Title order={3} mb="lg">Emergency Department Overview</Title>
            <SimpleGrid cols={{ base: 1, lg: 2 }} spacing="lg">
              {/* Triage Distribution */}
              <Card padding="lg" radius="md" withBorder>
                <Title order={4} mb="md">Triage Distribution</Title>
                <MantineDonutChart
                  data={triageDistribution}
                  size={160}
                  thickness={30}
                />
              </Card>
              
              {/* Hourly Admissions */}
              <Card padding="lg" radius="md" withBorder>
                <Title order={4} mb="md">Hourly Admissions</Title>
                <SimpleAreaChart
                  data={hourlyAdmissions}
                  dataKey="hour"
                  series={[{ name: 'admissions', color: 'red.6' }]}
                />
              </Card>
              
              {/* ICU Bed Occupancy */}
              <Card padding="lg" radius="md" withBorder style={{ gridColumn: '1 / -1' }}>
                <Title order={4} mb="md">ICU Bed Occupancy Trend</Title>
                <SimpleLineChart
                  data={bedOccupancy}
                  dataKey="date"
                  series={[
                    { name: 'occupied', color: 'blue.6', label: 'Occupied' },
                    { name: 'available', color: 'green.6', label: 'Available' }
                  ]}
                />
              </Card>
              
              {/* Quick Stats */}
              <Card padding="lg" radius="md" withBorder>
                <Title order={4} mb="md">Quick Statistics</Title>
                <Stack gap="md">
                  <Group justify="space-between" p="sm" 
                         style={{ backgroundColor: '#f8f9fa', borderRadius: '6px' }}>
                    <Text size="sm" fw={500}>Mortality Rate</Text>
                    <Text size="sm" fw={600} c="red">
                      2.1%
                    </Text>
                  </Group>
                  <Group justify="space-between" p="sm" 
                         style={{ backgroundColor: '#f8f9fa', borderRadius: '6px' }}>
                    <Text size="sm" fw={500}>Left Without Being Seen</Text>
                    <Text size="sm" fw={600} c="orange">
                      3.5%
                    </Text>
                  </Group>
                  <Group justify="space-between" p="sm" 
                         style={{ backgroundColor: '#f8f9fa', borderRadius: '6px' }}>
                    <Text size="sm" fw={500}>Door-to-Doc Time</Text>
                    <Text size="sm" fw={600}>
                      15min
                    </Text>
                  </Group>
                  <Group justify="space-between" p="sm" 
                         style={{ backgroundColor: '#f8f9fa', borderRadius: '6px' }}>
                    <Text size="sm" fw={500}>Return Rate (72h)</Text>
                    <Text size="sm" fw={600} c="yellow">
                      4.2%
                    </Text>
                  </Group>
                </Stack>
              </Card>
              
              {/* Emergency Protocols */}
              <Card padding="lg" radius="md" withBorder>
                <Title order={4} mb="md">Active Protocols</Title>
                <Stack gap="sm">
                  <Button 
                    fullWidth 
                    leftSection={<IconAlertTriangle size={16} />}
                    variant="light" 
                    color="red"
                  >
                    Code Blue Protocol
                  </Button>
                  <Button 
                    fullWidth 
                    leftSection={<IconHeart size={16} />} 
                    variant="light" 
                    color="purple"
                  >
                    Cardiac Arrest
                  </Button>
                  <Button 
                    fullWidth 
                    leftSection={<IconBrain size={16} />} 
                    variant="light" 
                    color="orange"
                  >
                    Stroke Protocol
                  </Button>
                  <Button 
                    fullWidth 
                    leftSection={<IconLungs size={16} />} 
                    variant="light" 
                    color="blue"
                  >
                    Respiratory Distress
                  </Button>
                </Stack>
              </Card>
            </SimpleGrid>
          </Paper>
        </Tabs.Panel>

        {/* Triage Queue Tab */}
        <Tabs.Panel value="triage">
          <Paper p="md" radius="md" withBorder mt="md">
            <Group justify="space-between" mb="lg">
              <Title order={3}>Triage Queue</Title>
              <Button leftSection={<IconPlus size={16} />} onClick={openTriage}>
                Add to Triage
              </Button>
            </Group>

            {/* Triage Queue Display */}
            <Stack gap="md">
              {[].map((patient, index) => (
                <Card key={patient.id} padding="lg" radius="md" withBorder>
                  <Group justify="space-between">
                    <Group>
                      <ThemeIcon 
                        color={getTriageColor(patient.triageLevel)} 
                        size="xl" 
                        radius="md"
                      >
                        <Text fw={700} c="white">
                          {patient.triageLevel}
                        </Text>
                      </ThemeIcon>
                      <div>
                        <Text fw={600} size="lg">
                          {patient.patientName}
                        </Text>
                        <Text size="sm" c="dimmed">
                          {patient.complaint}
                        </Text>
                      </div>
                    </Group>
                    
                    <Group>
                      <div style={{ textAlign: 'right' }}>
                        <Text size="sm" fw={500}>
                          Wait Time: {patient.waitTime}min
                        </Text>
                        <Text size="xs" c="dimmed">
                          Priority: {getTriageLabel(parseInt(patient.triageLevel))}
                        </Text>
                      </div>
                      <Badge 
                        color={getTriageColor(patient.triageLevel)} 
                        variant="light" 
                        size="lg"
                      >
                        #{index + 1}
                      </Badge>
                    </Group>
                  </Group>
                  
                  <Group justify="space-between" mt="md">
                    <Text size="sm" c="dimmed">
                      Assigned: {patient.assignedNurse}
                    </Text>
                    <Group>
                      <ActionIcon variant="subtle" color="blue">
                        <IconEye size={16} />
                      </ActionIcon>
                      <ActionIcon variant="subtle" color="green">
                        <IconCheck size={16} />
                      </ActionIcon>
                    </Group>
                  </Group>
                </Card>
              ))}
            </Stack>
          </Paper>
        </Tabs.Panel>

        {/* Emergency Cases Tab */}
        <Tabs.Panel value="cases">
          <Paper p="md" radius="md" withBorder mt="md">
            <Group justify="space-between" mb="lg">
              <Title order={3}>Emergency Cases</Title>
              <Button leftSection={<IconPlus size={16} />} onClick={openAddCase} color="red">
                New Emergency Case
              </Button>
            </Group>

            {/* Case Filters */}
            <Group mb="md">
              <TextInput
                placeholder="Search cases..."
                leftSection={<IconSearch size={16} />}
                value={searchQuery}
                onChange={(event) => setSearchQuery(event.currentTarget.value)}
                style={{ flex: 1 }}
              />
              <Select
                placeholder="Triage Level"
                data={[
                  { value: '1', label: 'Level 1 - Resuscitation' },
                  { value: '2', label: 'Level 2 - Emergency' },
                  { value: '3', label: 'Level 3 - Urgent' },
                  { value: '4', label: 'Level 4 - Less Urgent' },
                  { value: '5', label: 'Level 5 - Non-urgent' }
                ]}
                value={selectedTriage}
                onChange={setSelectedTriage}
                clearable
              />
              <Select
                placeholder="Status"
                data={[
                  { value: 'waiting', label: 'Waiting' },
                  { value: 'in_progress', label: 'In Progress' },
                  { value: 'completed', label: 'Completed' },
                  { value: 'transferred', label: 'Transferred' },
                  { value: 'discharged', label: 'Discharged' }
                ]}
                value={selectedStatus}
                onChange={setSelectedStatus}
                clearable
              />
              <Button variant="light" onClick={clearFilters}>
                Clear Filters
              </Button>
            </Group>

            {/* Cases Table */}
            <ScrollArea>
              <Table striped highlightOnHover>
                <Table.Thead>
                  <Table.Tr>
                    <Table.Th>Case #</Table.Th>
                    <Table.Th>Patient</Table.Th>
                    <Table.Th>Triage</Table.Th>
                    <Table.Th>Chief Complaint</Table.Th>
                    <Table.Th>Arrival Time</Table.Th>
                    <Table.Th>Assigned Staff</Table.Th>
                    <Table.Th>Status</Table.Th>
                    <Table.Th>Actions</Table.Th>
                  </Table.Tr>
                </Table.Thead>
                <Table.Tbody>
                  {[].length === 0 ? (
                    <Table.Tr>
                      <Table.Td colSpan={9}>
                        <EmptyState
                          icon={<IconAmbulance size={48} />}
                          title="No emergency cases"
                          description="Register emergency cases as they arrive"
                          size="sm"
                        />
                      </Table.Td>
                    </Table.Tr>
                  ) : (
                    [].map((emergencyCase) => (
                    <Table.Tr key={emergencyCase.id}>
                      <Table.Td>
                        <Text fw={500}>{emergencyCase.caseNumber}</Text>
                      </Table.Td>
                      <Table.Td>
                        <Group>
                          <Avatar color="red" radius="xl" size="sm">
                            {emergencyCase.patient.firstName[0]}{emergencyCase.patient.lastName[0]}
                          </Avatar>
                          <div>
                            <Text size="sm" fw={500}>
                              {emergencyCase.patient.firstName} {emergencyCase.patient.lastName}
                            </Text>
                            <Text size="xs" c="dimmed">
                              DOB: {emergencyCase.patient.dateOfBirth}
                            </Text>
                          </div>
                        </Group>
                      </Table.Td>
                      <Table.Td>
                        <Group>
                          <ThemeIcon 
                            color={getTriageColor(emergencyCase.triageLevel)} 
                            size="md" 
                            radius="md"
                          >
                            <Text fw={700} c="white" size="xs">
                              {emergencyCase.triageLevel}
                            </Text>
                          </ThemeIcon>
                          <div>
                            <Text size="sm" fw={500}>
                              {getTriageLabel(emergencyCase.triageLevel)}
                            </Text>
                            <Text size="xs" c="dimmed">
                              {emergencyCase.assignedTo}
                            </Text>
                          </div>
                        </Group>
                      </Table.Td>
                      <Table.Td>
                        <Text size="sm" lineClamp={2} style={{ maxWidth: 200 }}>
                          {emergencyCase.chiefComplaint}
                        </Text>
                      </Table.Td>
                      <Table.Td>
                        <Text size="sm">
                          {new Date(emergencyCase.arrivalTime).toLocaleTimeString()}
                        </Text>
                      </Table.Td>
                      <Table.Td>
                        <div>
                          <Text size="sm" fw={500}>
                            {emergencyCase.assignedTo || 'Not Assigned'}
                          </Text>
                          <Text size="xs" c="dimmed">
                            Priority: {emergencyCase.priority}
                          </Text>
                        </div>
                      </Table.Td>
                      <Table.Td>
                        <Badge color={getStatusColor(emergencyCase.status)} variant="light">
                          {emergencyCase.status.replace('_', ' ')}
                        </Badge>
                      </Table.Td>
                      <Table.Td>
                        <Group gap="xs">
                          <ActionIcon
                            variant="subtle"
                            color="blue"
                            onClick={() => handleViewCase(emergencyCase)}
                          >
                            <IconEye size={16} />
                          </ActionIcon>
                          <ActionIcon variant="subtle" color="green">
                            <IconEdit size={16} />
                          </ActionIcon>
                          <ActionIcon variant="subtle" color="orange">
                            <IconActivity size={16} />
                          </ActionIcon>
                        </Group>
                      </Table.Td>
                    </Table.Tr>
                  )))}
                </Table.Tbody>
              </Table>
            </ScrollArea>
          </Paper>
        </Tabs.Panel>

        {/* ICU Management Tab */}
        <Tabs.Panel value="icu">
          <Paper p="md" radius="md" withBorder mt="md">
            <Group justify="space-between" mb="lg">
              <Title order={3}>ICU Bed Management</Title>
              <Group>
                <Button leftSection={<IconBed size={16} />} variant="light">
                  Bed Assignment
                </Button>
                <Button leftSection={<IconActivity size={16} />}>
                  Monitor Vitals
                </Button>
              </Group>
            </Group>

            {/* Bed Filters */}
            <Group mb="md">
              <TextInput
                placeholder="Search beds..."
                leftSection={<IconSearch size={16} />}
                value={searchQuery}
                onChange={(event) => setSearchQuery(event.currentTarget.value)}
                style={{ flex: 1 }}
              />
              <Select
                placeholder="Bed Status"
                data={[
                  { value: 'available', label: 'Available' },
                  { value: 'occupied', label: 'Occupied' },
                  { value: 'cleaned', label: 'Cleaned' },
                  { value: 'maintenance', label: 'Maintenance' }
                ]}
                value={selectedBedStatus}
                onChange={setSelectedBedStatus}
                clearable
              />
            </Group>

            {/* ICU Beds Grid */}
            <SimpleGrid cols={{ base: 1, md: 2, lg: 3 }} spacing="lg">
              {[].map((bed) => (
                <Card key={bed.id} padding="lg" radius="md" withBorder>
                  <Group justify="space-between" mb="md">
                    <div>
                      <Text fw={600} size="lg">Bed {bed.bedNumber}</Text>
                      <Text size="sm" c="dimmed">{bed.ward} - {bed.roomNumber}</Text>
                    </div>
                    <Badge color={getStatusColor(bed.status)} variant="light">
                      {bed.status}
                    </Badge>
                  </Group>

                  {bed.patient && (
                    <>
                      <Group mb="md">
                        <Avatar color="blue" radius="xl">
                          {bed.patient.firstName[0]}{bed.patient.lastName[0]}
                        </Avatar>
                        <div>
                          <Text fw={500}>
                            {bed.patient.firstName} {bed.patient.lastName}
                          </Text>
                          <Text size="sm" c="dimmed">
                            Age: {bed.patient.age} | ID: {bed.patient.patientId}
                          </Text>
                        </div>
                      </Group>

                      <Text size="sm" fw={500} mb="sm">Current Vitals</Text>
                      <Stack gap="xs" mb="md">
                        <Group justify="space-between" p="xs" 
                               style={{ backgroundColor: '#f8f9fa', borderRadius: '6px' }}>
                          <Group gap="xs">
                            <IconHeart size={16} color="red" />
                            <Text size="sm">Heart Rate</Text>
                          </Group>
                          <Text size="sm" fw={600}>{bed.currentVitals?.heartRate} bpm</Text>
                        </Group>
                        <Group justify="space-between" p="xs" 
                               style={{ backgroundColor: '#f8f9fa', borderRadius: '6px' }}>
                          <Group gap="xs">
                            <IconActivity size={16} color="blue" />
                            <Text size="sm">Blood Pressure</Text>
                          </Group>
                          <Text size="sm" fw={600}>
                            {bed.currentVitals?.bloodPressure.systolic}/{bed.currentVitals?.bloodPressure.diastolic}
                          </Text>
                        </Group>
                        <Group justify="space-between" p="xs" 
                               style={{ backgroundColor: '#f8f9fa', borderRadius: '6px' }}>
                          <Group gap="xs">
                            <IconLungs size={16} color="green" />
                            <Text size="sm">Oxygen</Text>
                          </Group>
                          <Text size="sm" fw={600}>{bed.currentVitals?.oxygenSaturation}%</Text>
                        </Group>
                      </Stack>
                    </>
                  )}

                  <Group justify="space-between">
                    <Text size="xs" c="dimmed">
                      Last Updated: {bed.lastUpdated ? new Date(bed.lastUpdated).toLocaleTimeString() : 'N/A'}
                    </Text>
                    <Group gap="xs">
                      <ActionIcon
                        variant="subtle"
                        color="blue"
                        onClick={() => handleViewBed(bed)}
                      >
                        <IconEye size={16} />
                      </ActionIcon>
                      <ActionIcon variant="subtle" color="green">
                        <IconActivity size={16} />
                      </ActionIcon>
                      <ActionIcon variant="subtle" color="orange">
                        <IconSettings size={16} />
                      </ActionIcon>
                    </Group>
                  </Group>
                </Card>
              ))}
            </SimpleGrid>
          </Paper>
        </Tabs.Panel>

        {/* Equipment Tab */}
        <Tabs.Panel value="equipment">
          <Paper p="md" radius="md" withBorder mt="md">
            <Group justify="space-between" mb="lg">
              <Title order={3}>Critical Care Equipment</Title>
              <Button leftSection={<IconSettings size={16} />}>
                Equipment Check
              </Button>
            </Group>

            {/* Equipment Grid */}
            <SimpleGrid cols={{ base: 1, md: 2, lg: 3 }} spacing="lg">
              {[].map((equipment) => (
                <Card key={equipment.id} padding="lg" radius="md" withBorder>
                  <Group justify="space-between" mb="md">
                    <div>
                      <Text fw={600} size="lg">{equipment.equipmentName}</Text>
                      <Text size="sm" c="dimmed">{equipment.location}</Text>
                    </div>
                    <Badge color={getStatusColor(equipment.status)} variant="light">
                      {equipment.status.replace('_', ' ')}
                    </Badge>
                  </Group>

                  <Stack gap="sm" mb="md">
                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Model</Text>
                      <Text size="sm" fw={500}>{equipment.model}</Text>
                    </Group>
                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Serial</Text>
                      <Text size="sm" fw={500}>{equipment.serialNumber}</Text>
                    </Group>
                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Last Maintenance</Text>
                      <Text size="sm">
                        {equipment.lastMaintenanceDate}
                      </Text>
                    </Group>
                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Next Due</Text>
                      <Text 
                        size="sm" 
                        c="dimmed"
                      >
                        {equipment.nextMaintenanceDate}
                      </Text>
                    </Group>
                  </Stack>

                  {equipment.currentReadings && (
                    <Alert variant="light" color="blue" mb="md">
                      <Text size="sm">
                        <strong>Current Reading:</strong> {equipment.currentReadings}
                      </Text>
                    </Alert>
                  )}

                  <Group justify="space-between">
                    <Text size="xs" c="dimmed">
                      Technician: {equipment.assignedTechnician}
                    </Text>
                    <Group gap="xs">
                      <ActionIcon variant="subtle" color="blue">
                        <IconEye size={16} />
                      </ActionIcon>
                      <ActionIcon variant="subtle" color="green">
                        <IconSettings size={16} />
                      </ActionIcon>
                      <ActionIcon variant="subtle" color="orange">
                        <IconClipboard size={16} />
                      </ActionIcon>
                    </Group>
                  </Group>
                </Card>
              ))}
            </SimpleGrid>
          </Paper>
        </Tabs.Panel>

        {/* Protocols Tab */}
        <Tabs.Panel value="protocols">
          <Paper p="md" radius="md" withBorder mt="md">
            <Group justify="space-between" mb="lg">
              <Title order={3}>Emergency Protocols</Title>
              <Button leftSection={<IconPlus size={16} />}>
                Add Protocol
              </Button>
            </Group>

            {/* Emergency Protocols */}
            <Stack gap="lg">
              {[].map((protocol) => (
                <Card key={protocol.id} padding="lg" radius="md" withBorder>
                  <Group justify="space-between" mb="md">
                    <div>
                      <Text fw={600} size="lg">{protocol.name}</Text>
                      <Text size="sm" c="dimmed">{protocol.category}</Text>
                    </div>
                    <Group>
                      <Badge color="blue" variant="light">
                        Version {protocol.version}
                      </Badge>
                      <Badge color="green" variant="light">
                        Active
                      </Badge>
                    </Group>
                  </Group>

                  <Text size="sm" mb="md">{protocol.description}</Text>

                  <div>
                    <Text size="sm" fw={500} mb="sm">Protocol Steps</Text>
                    <Stepper active={-1} breakpoint="sm">
                      {protocol.steps.map((step, index) => (
                        <Stepper.Step 
                          key={index} 
                          label={`Step ${index + 1}`} 
                          description={step}
                        />
                      ))}
                    </Stepper>
                  </div>

                  <Group justify="space-between" mt="md">
                    <Text size="xs" c="dimmed">
                      Last Updated: {protocol.lastUpdated}
                    </Text>
                    <Group gap="xs">
                      <ActionIcon variant="subtle" color="blue">
                        <IconEye size={16} />
                      </ActionIcon>
                      <ActionIcon variant="subtle" color="green">
                        <IconEdit size={16} />
                      </ActionIcon>
                      <ActionIcon variant="subtle" color="orange">
                        <IconDownload size={16} />
                      </ActionIcon>
                    </Group>
                  </Group>
                </Card>
              ))}
            </Stack>
          </Paper>
        </Tabs.Panel>
      </Tabs>

      {/* Case Detail Modal */}
      <Modal
        opened={caseDetailOpened}
        onClose={closeCaseDetail}
        title="Emergency Case Details"
        size="xl"
      >
        {selectedCase && (
          <ScrollArea h={600}>
            <Stack gap="md">
              <Group>
                <Avatar color="red" size="xl" radius="xl">
                  {selectedCase.patient.firstName[0]}{selectedCase.patient.lastName[0]}
                </Avatar>
                <div>
                  <Title order={3}>
                    {selectedCase.patient.firstName} {selectedCase.patient.lastName}
                  </Title>
                  <Text c="dimmed">Case: {selectedCase.caseNumber}</Text>
                  <Badge color={getStatusColor(selectedCase.status)} variant="light" mt="xs">
                    {selectedCase.status.replace('_', ' ')}
                  </Badge>
                </div>
              </Group>

              <Divider />

              <SimpleGrid cols={2}>
                <div>
                  <Text size="sm" fw={500}>Triage Level</Text>
                  <Group>
                      <ThemeIcon 
                        color={getTriageColor(selectedCase.triageLevel)} 
                        size="sm" 
                        radius="md"
                      >
                        <Text fw={700} c="white" size="xs">
                          {selectedCase.triageLevel}
                        </Text>
                      </ThemeIcon>
                      <Text size="sm">{getTriageLabel(selectedCase.triageLevel)}</Text>
                  </Group>
                </div>
                <div>
                  <Text size="sm" fw={500}>Arrival Time</Text>
                  <Text size="sm" c="dimmed">
                    {new Date(selectedCase.arrivalTime).toLocaleString()}
                  </Text>
                </div>
                <div>
                  <Text size="sm" fw={500}>Chief Complaint</Text>
                  <Text size="sm" c="dimmed">{selectedCase.chiefComplaint}</Text>
                </div>
                <div>
                  <Text size="sm" fw={500}>Assigned To</Text>
                  <Text size="sm" c="dimmed">{selectedCase.assignedTo || 'Not Assigned'}</Text>
                </div>
              </SimpleGrid>

              {/* Vitals not available in simplified mock; display basic info instead */}
              <Divider />
              <div>
                <Text size="sm" fw={500} mb="sm">Summary</Text>
                <SimpleGrid cols={2}>
                  <Group justify="space-between" p="sm" style={{ backgroundColor: '#f8f9fa', borderRadius: '6px' }}>
                    <Text size="sm">Bed</Text>
                    <Text size="sm" fw={600}>{selectedCase.bedNumber || '‚Äî'}</Text>
                  </Group>
                  <Group justify="space-between" p="sm" style={{ backgroundColor: '#f8f9fa', borderRadius: '6px' }}>
                    <Text size="sm">Priority</Text>
                    <Text size="sm" fw={600}>{selectedCase.priority ?? '‚Äî'}</Text>
                  </Group>
                </SimpleGrid>
              </div>

              <Group justify="flex-end">
                <Button variant="light" onClick={closeCaseDetail}>
                  Close
                </Button>
                <Button>
                  Update Case
                </Button>
              </Group>
            </Stack>
          </ScrollArea>
        )}
      </Modal>

      {/* Add Case Modal */}
      <Modal
        opened={addCaseOpened}
        onClose={closeAddCase}
        title="New Emergency Case"
        size="lg"
      >
        <Stack gap="md">
          <SimpleGrid cols={2}>
            <Select
              label="Patient"
              placeholder="Select patient"
              data={[]} // TODO: Fetch from patients API
              searchable
              required
            />
            <Select
              label="Triage Level"
              placeholder="Select triage level"
              data={[
                { value: '1', label: 'Level 1 - Resuscitation' },
                { value: '2', label: 'Level 2 - Emergency' },
                { value: '3', label: 'Level 3 - Urgent' },
                { value: '4', label: 'Level 4 - Less Urgent' },
                { value: '5', label: 'Level 5 - Non-urgent' }
              ]}
              required
            />
          </SimpleGrid>
          
          <Textarea
            label="Chief Complaint"
            placeholder="Enter chief complaint"
            rows={3}
            required
          />
          
          <Select
            label="Mode of Arrival"
            placeholder="Select mode of arrival"
            data={[
              { value: 'ambulance', label: 'Ambulance' },
              { value: 'walk_in', label: 'Walk-in' },
              { value: 'police', label: 'Police' },
              { value: 'helicopter', label: 'Helicopter' }
            ]}
            required
          />
          
          <Text size="sm" fw={500} mt="md">Initial Vital Signs</Text>
          <SimpleGrid cols={3}>
            <NumberInput
              label="Heart Rate (bpm)"
              placeholder="72"
            />
            <TextInput
              label="Blood Pressure"
              placeholder="120/80"
            />
            <NumberInput
              label="Temperature (¬∞F)"
              placeholder="98.6"
            />
            <NumberInput
              label="Oxygen Saturation (%)"
              placeholder="98"
            />
            <NumberInput
              label="Respiratory Rate"
              placeholder="16"
            />
            <NumberInput
              label="Pain Scale (0-10)"
              placeholder="0"
              min={0}
              max={10}
            />
          </SimpleGrid>
          
          <Group justify="flex-end">
            <Button variant="light" onClick={closeAddCase}>
              Cancel
            </Button>
            <Button color="red" onClick={() => {
              notifications.show({
                title: 'Emergency Case Created',
                message: 'New emergency case has been added to the system',
                color: 'green',
              });
              closeAddCase();
            }}>
              Create Case
            </Button>
          </Group>
        </Stack>
      </Modal>
    </Container>
  );
};

export default EmergencyManagement;

'use client';

import React, { useState, useMemo, useEffect } from 'react';
import {
  Container,
  Paper,
  Title,
  Group,
  Button,
  TextInput,
  Select,
  Badge,
  Table,
  Modal,
  Text,
  Tabs,
  Card,
  Avatar,
  ActionIcon,
  Menu,
  Stack,
  Divider,
  SimpleGrid,
  ScrollArea,
  ThemeIcon,
  Timeline,
  Alert,
  Progress,
  Flex,
  Anchor,
  NumberInput,
  Textarea,
  List
} from '@mantine/core';
import { DatePickerInput } from '@mantine/dates';
import { useDisclosure } from '@mantine/hooks';
import EmptyState from '../../../components/EmptyState';
import { notifications } from '@mantine/notifications';
import emrService from '../../../services/emr.service';
import { MantineDonutChart, SimpleAreaChart, SimpleBarChart, SimpleLineChart } from '../../../components/MantineChart';
import {
  IconPlus,
  IconSearch,
  IconEdit,
  IconEye,
  IconTrash,
  IconCalendar,
  IconUsers,
  IconChartBar,
  IconPhone,
  IconMail,
  IconAlertCircle,
  IconCheck,
  IconX,
  IconDotsVertical,
  IconStethoscope,
  IconActivity,
  IconPill,
  IconTestPipe,
  IconFileText,
  IconHeart,
  IconBrain,
  IconLungs,
  IconShield,
  IconAlertTriangle,
  IconTrendingUp,
  IconTrendingDown,
  IconClipboardList,
  IconMedicalCross,
  IconVaccine,
  IconReportMedical,
  IconHistory,
  IconUserCheck,
  IconDownload,
  IconPrinter,
  IconShare
} from '@tabler/icons-react';

// Import types and mock data
import { 
  MedicalRecord,
  MedicalRecordType,
  MedicalRecordStatus,
  LabResult,
  MedicalDocument,
  MedicalHistory,
  Prescription,
  Allergy,
  AllergySeverity
} from '../../../types/medical';
// Mock data imports removed
const EMRManagement = () => {
  // State management
  const [activeTab, setActiveTab] = useState<string>('records');
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedPatient, setSelectedPatient] = useState<string>('');
  const [selectedDoctor, setSelectedDoctor] = useState<string>('');
  const [selectedRecordType, setSelectedRecordType] = useState<string>('');
  const [selectedStatus, setSelectedStatus] = useState<string>('');
  const [selectedRecord, setSelectedRecord] = useState<MedicalRecord | null>(null);

  // API data state
  const [records, setRecords] = useState<MedicalRecord[]>([]);
  const [stats, setStats] = useState<any>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    fetchAllData();
  }, []);

  const fetchAllData = async () => {
    try {
      setLoading(true);
      setError(null);
      await Promise.all([fetchRecords(), fetchStats()]);
    } catch (err: any) {
      console.error('Error loading EMR data:', err);
      setError(err.response?.data?.message || 'Failed to load EMR data');
      setRecords([] /* TODO: Fetch from API */);
    } finally {
      setLoading(false);
    }
  };

  const fetchRecords = async () => {
    try {
      const filters = {
        patientId: selectedPatient || undefined,
        recordType: selectedRecordType || undefined
      };
      const response = await emrService.getRecords(filters);
      // Handle different response structures
      const recordsData = Array.isArray(response.data) 
        ? response.data 
        : (response.data?.records || []);
      setRecords(recordsData as MedicalRecord[]);
    } catch (err: any) {
      console.warn('Error fetching EMR records (using empty data):', err.response?.data?.message || err.message);
      // Don't show error to user if backend is not ready, just use empty data
      setRecords([]);
    }
  };

  const fetchStats = async () => {
    try {
      const response = await emrService.getStats();
      setStats(response.data);
    } catch (err: any) {
      console.warn('Error fetching EMR stats (using default values):', err.response?.data?.message || err.message);
      // Set default stats when backend is unavailable
      setStats({
        totalRecords: 0,
        recordsByType: {},
        recordsByStatus: {},
        commonDiagnoses: [],
        prescriptionTrends: [],
        recentActivity: []
      });
    }
  };

  useEffect(() => {
    if (!loading) {
      fetchRecords();
    }
  }, [searchQuery, selectedPatient, selectedDoctor, selectedRecordType, selectedStatus]);

  // Modal states
  const [recordDetailOpened, { open: openRecordDetail, close: closeRecordDetail }] = useDisclosure(false);
  const [addRecordOpened, { open: openAddRecord, close: closeAddRecord }] = useDisclosure(false);
  const [editRecordOpened, { open: openEditRecord, close: closeEditRecord }] = useDisclosure(false);

  // Filter medical records
  const filteredRecords = useMemo(() => {
    return records.filter((record) => {
      const matchesSearch = 
        record.patient.firstName.toLowerCase().includes(searchQuery.toLowerCase()) ||
        record.patient.lastName.toLowerCase().includes(searchQuery.toLowerCase()) ||
        record.recordId.toLowerCase().includes(searchQuery.toLowerCase()) ||
        record.chiefComplaint.toLowerCase().includes(searchQuery.toLowerCase());
      
      const matchesPatient = !selectedPatient || record.patientId === selectedPatient;
      const matchesDoctor = !selectedDoctor || record.doctorId === selectedDoctor;
      const matchesType = !selectedRecordType || record.recordType === selectedRecordType;
      const matchesStatus = !selectedStatus || record.status === selectedStatus;

      return matchesSearch && matchesPatient && matchesDoctor && matchesType && matchesStatus;
    });
  }, [searchQuery, selectedPatient, selectedDoctor, selectedRecordType, selectedStatus]);

  // Helper functions
  const formatDate = (date: string | Date) => {
    const d = new Date(date);
    const year = d.getFullYear();
    const month = String(d.getMonth() + 1).padStart(2, '0');
    const day = String(d.getDate()).padStart(2, '0');
    return `${year}-${month}-${day}`;
  };

  const getStatusColor = (status: MedicalRecordStatus) => {
    switch (status) {
      case 'draft': return 'gray';
      case 'pending_review': return 'yellow';
      case 'reviewed': return 'blue';
      case 'approved': return 'green';
      case 'amended': return 'orange';
      case 'archived': return 'dark';
      default: return 'gray';
    }
  };

  const getTypeColor = (type: MedicalRecordType) => {
    switch (type) {
      case 'consultation': return 'blue';
      case 'emergency': return 'red';
      case 'surgery': return 'purple';
      case 'follow_up': return 'green';
      case 'lab_result': return 'orange';
      default: return 'gray';
    }
  };

  const getSeverityColor = (severity: AllergySeverity) => {
    switch (severity) {
      case 'mild': return 'green';
      case 'moderate': return 'yellow';
      case 'severe': return 'red';
      case 'life_threatening': return 'dark';
      default: return 'gray';
    }
  };

  const handleViewRecord = (record: MedicalRecord) => {
    setSelectedRecord(record);
    openRecordDetail();
  };

  const handleEditRecord = (record: MedicalRecord) => {
    setSelectedRecord(record);
    openEditRecord();
  };

  const handleDeleteRecord = (record: MedicalRecord) => {
    notifications.show({
      title: 'Record Deleted',
      message: `Medical record ${record.recordId} has been deleted`,
      color: 'red',
    });
  };

  const clearFilters = () => {
    setSearchQuery('');
    setSelectedPatient('');
    setSelectedDoctor('');
    setSelectedRecordType('');
    setSelectedStatus('');
  };

  // Statistics cards
  const statsCards = [
    {
      title: 'Total Records',
      value: 0,
      icon: IconFileText,
      color: 'blue',
      trend: '+0%'
    },
    {
      title: 'Today\'s Records',
      value: 0,
      icon: IconCalendar,
      color: 'green',
      trend: '+0%'
    },
    {
      title: 'Pending Review',
      value: 0,
      icon: IconAlertCircle,
      color: 'orange',
      trend: '0%'
    },
    {
      title: 'Lab Results',
      value: 0,
      icon: IconTestPipe,
      color: 'purple',
      trend: '+0%'
    }
  ];

  // Chart data
  const recordsByTypeData = 0
    ? Object.entries(0 /* TODO: Fetch from API */)
        .filter(([_, count]) => (typeof count === 'number' && count > 0))
        .map(([type, count]) => ({
          name: type.replace('_', ' ').toUpperCase(),
          value: typeof count === 'number' ? count : 0,
          color: getTypeColor(type as MedicalRecordType)
        }))
    : [];

  const recordsByStatusData = 0
    ? Object.entries(0 /* TODO: Fetch from API */).map(
        ([status, count]) => ({ status: status.replace('_', ' '), count })
      )
    : [];

  const commonDiagnosesData = [];
  const prescriptionTrendsData = [];
  const recentActivityData = [];

  return (
    <Container size="xl" py="md">
      {/* Header */}
      <Group justify="space-between" mb="lg">
        <div>
          <Title order={1}>Electronic Medical Records</Title>
          <Text c="dimmed" size="sm">
            Manage patient medical records, lab results, and clinical documentation
          </Text>
        </div>
        <Group>
          <Button
            leftSection={<IconPlus size={16} />}
            onClick={openAddRecord}
          >
            New Record
          </Button>
        </Group>
      </Group>

      {/* Statistics Cards */}
      <SimpleGrid cols={{ base: 1, sm: 2, md: 4 }} mb="lg">
        {statsCards.map((stat) => {
          const Icon = stat.icon;
          return (
            <Card key={stat.title} padding="lg" radius="md" withBorder>
              <Group justify="space-between">
                <div>
                  <Text c="dimmed" size="sm" fw={500}>
                    {stat.title}
                  </Text>
                  <Text fw={700} size="xl">
                    {stat.value}
                  </Text>
                </div>
                <ThemeIcon color={stat.color} size="xl" radius="md" variant="light">
                  <Icon size={24} />
                </ThemeIcon>
              </Group>
              <Group justify="space-between" mt="sm">
                <Badge 
                  color={stat.trend.startsWith('+') ? 'green' : 'red'} 
                  variant="light"
                  size="sm"
                >
                  {stat.trend}
                </Badge>
                <Text size="xs" c="dimmed">vs last month</Text>
              </Group>
            </Card>
          );
        })}
      </SimpleGrid>

      {/* Main Content Tabs */}
      <Tabs value={activeTab} onChange={setActiveTab}>
        <Tabs.List>
          <Tabs.Tab value="records" leftSection={<IconFileText size={16} />}>
            Medical Records
          </Tabs.Tab>
          <Tabs.Tab value="lab_results" leftSection={<IconTestPipe size={16} />}>
            Lab Results
          </Tabs.Tab>
          <Tabs.Tab value="prescriptions" leftSection={<IconPill size={16} />}>
            Prescriptions
          </Tabs.Tab>
          <Tabs.Tab value="documents" leftSection={<IconClipboardList size={16} />}>
            Documents
          </Tabs.Tab>
          <Tabs.Tab value="analytics" leftSection={<IconChartBar size={16} />}>
            Analytics
          </Tabs.Tab>
        </Tabs.List>

        {/* Medical Records Tab */}
        <Tabs.Panel value="records">
          <Paper p="md" radius="md" withBorder mt="md">
            {/* Search and Filters */}
            <Group mb="md">
              <TextInput
                placeholder="Search records..."
                leftSection={<IconSearch size={16} />}
                value={searchQuery}
                onChange={(event) => setSearchQuery(event.currentTarget.value)}
                style={{ flex: 1 }}
              />
              <Select
                placeholder="Patient"
                data={[].map /* TODO: Fetch from API */(patient => ({ 
                  value: patient.id, 
                  label: `${patient.firstName} ${patient.lastName}` 
                }))}
                value={selectedPatient}
                onChange={setSelectedPatient}
                clearable
              />
              <Select
                placeholder="Doctor"
                data={[].map /* TODO: Fetch from API */(doctor => ({ 
                  value: doctor.staffId, 
                  label: `${doctor.firstName} ${doctor.lastName}` 
                }))}
                value={selectedDoctor}
                onChange={setSelectedDoctor}
                clearable
              />
              <Select
                placeholder="Record Type"
                data={[
                  { value: 'consultation', label: 'Consultation' },
                  { value: 'emergency', label: 'Emergency' },
                  { value: 'follow_up', label: 'Follow-up' },
                  { value: 'surgery', label: 'Surgery' },
                  { value: 'lab_result', label: 'Lab Result' }
                ]}
                value={selectedRecordType}
                onChange={setSelectedRecordType}
                clearable
              />
              <Select
                placeholder="Status"
                data={[
                  { value: 'draft', label: 'Draft' },
                  { value: 'pending_review', label: 'Pending Review' },
                  { value: 'approved', label: 'Approved' },
                  { value: 'archived', label: 'Archived' }
                ]}
                value={selectedStatus}
                onChange={setSelectedStatus}
                clearable
              />
              <Button variant="light" onClick={clearFilters}>
                Clear Filters
              </Button>
            </Group>

            {/* Records Table */}
            <ScrollArea>
              <Table striped highlightOnHover>
                <Table.Thead>
                  <Table.Tr>
                    <Table.Th>Patient</Table.Th>
                    <Table.Th>Record ID</Table.Th>
                    <Table.Th>Type</Table.Th>
                    <Table.Th>Doctor</Table.Th>
                    <Table.Th>Date</Table.Th>
                    <Table.Th>Status</Table.Th>
                    <Table.Th>Chief Complaint</Table.Th>
                    <Table.Th>Actions</Table.Th>
                  </Table.Tr>
                </Table.Thead>
                <Table.Tbody>
                  {filteredRecords.length === 0 ? (
                    <Table.Tr>
                      <Table.Td colSpan={7}>
                        <EmptyState
                          icon={<IconFileText size={48} />}
                          title="No medical records"
                          description="Create electronic medical records"
                          size="sm"
                        />
                      </Table.Td>
                    </Table.Tr>
                  ) : (
                    filteredRecords.map((record) => (
                    <Table.Tr key={record.id}>
                      <Table.Td>
                        <Group>
                          <Avatar color="blue" radius="xl">
                            {record.patient.firstName[0]}{record.patient.lastName[0]}
                          </Avatar>
                          <div>
                            <Text fw={500}>
                              {record.patient.firstName} {record.patient.lastName}
                            </Text>
                            <Text size="sm" c="dimmed">
                              ID: {record.patient.patientId}
                            </Text>
                          </div>
                        </Group>
                      </Table.Td>
                      <Table.Td>
                        <Text fw={500}>{record.recordId}</Text>
                      </Table.Td>
                      <Table.Td>
                        <Badge color={getTypeColor(record.recordType)} variant="light">
                          {record.recordType.replace('_', ' ')}
                        </Badge>
                      </Table.Td>
                      <Table.Td>
                        <div>
                          <Text size="sm" fw={500}>
                            {record.doctor.firstName} {record.doctor.lastName}
                          </Text>
                          <Text size="xs" c="dimmed">
                            {record.doctor.department?.name}
                          </Text>
                        </div>
                      </Table.Td>
                      <Table.Td>
                        <Text size="sm">
                          {formatDate(record.recordDate)}
                        </Text>
                      </Table.Td>
                      <Table.Td>
                        <Badge color={getStatusColor(record.status)} variant="light">
                          {record.status.replace('_', ' ')}
                        </Badge>
                      </Table.Td>
                      <Table.Td>
                        <Text size="sm" truncate="end" style={{ maxWidth: 200 }}>
                          {record.chiefComplaint}
                        </Text>
                      </Table.Td>
                      <Table.Td>
                        <Group gap="xs">
                          <ActionIcon
                            variant="subtle"
                            color="blue"
                            onClick={() => handleViewRecord(record)}
                          >
                            <IconEye size={16} />
                          </ActionIcon>
                          <ActionIcon
                            variant="subtle"
                            color="green"
                            onClick={() => handleEditRecord(record)}
                          >
                            <IconEdit size={16} />
                          </ActionIcon>
                          <Menu>
                            <Menu.Target>
                              <ActionIcon variant="subtle" color="gray">
                                <IconDotsVertical size={16} />
                              </ActionIcon>
                            </Menu.Target>
                            <Menu.Dropdown>
                              <Menu.Item leftSection={<IconDownload size={14} />}>
                                Download
                              </Menu.Item>
                              <Menu.Item leftSection={<IconPrinter size={14} />}>
                                Print
                              </Menu.Item>
                              <Menu.Item leftSection={<IconShare size={14} />}>
                                Share
                              </Menu.Item>
                              <Menu.Divider />
                              <Menu.Item 
                                leftSection={<IconTrash size={14} />}
                                color="red"
                                onClick={() => handleDeleteRecord(record)}
                              >
                                Delete
                              </Menu.Item>
                            </Menu.Dropdown>
                          </Menu>
                        </Group>
                      </Table.Td>
                    </Table.Tr>
                  )))
                  }
                </Table.Tbody>
              </Table>
            </ScrollArea>
          </Paper>
        </Tabs.Panel>

        {/* Lab Results Tab */}
        <Tabs.Panel value="lab_results">
          <Paper p="md" radius="md" withBorder mt="md">
            <Group justify="space-between" mb="lg">
              <Title order={3}>Laboratory Results</Title>
              <Button leftSection={<IconPlus size={16} />}>
                Order Lab Test
              </Button>
            </Group>

            <SimpleGrid cols={{ base: 1, lg: 2 }} spacing="lg">
              {[].map /* TODO: Fetch from API */((result) => (
                <Card key={result.id} padding="lg" radius="md" withBorder>
                  <Group justify="space-between" mb="md">
                    <div>
                      <Text fw={600} size="lg">{result.testName}</Text>
                      <Text size="sm" c="dimmed">
                        {result.patient.firstName} {result.patient.lastName}
                      </Text>
                    </div>
                    <Badge 
                      color={result.status === 'verified' ? 'green' : 'orange'} 
                      variant="light"
                    >
                      {result.status}
                    </Badge>
                  </Group>

                  <Stack gap="xs" mb="md">
                    {result.results.map((param) => (
                      <Group key={param.id} justify="space-between" p="xs" 
                             style={{ backgroundColor: '#f8f9fa', borderRadius: '6px' }}>
                        <Text size="sm" fw={500}>{param.parameterName}</Text>
                        <Group gap="xs">
                          <Text size="sm">{param.value} {param.unit}</Text>
                          {param.abnormalFlag && (
                            <Badge 
                              color={param.abnormalFlag === 'high' ? 'red' : 'blue'} 
                              variant="light" 
                              size="xs"
                            >
                              {param.abnormalFlag}
                            </Badge>
                          )}
                        </Group>
                      </Group>
                    ))}
                  </Stack>

                  <Text size="sm" c="dimmed" mb="md">
                    <strong>Interpretation:</strong> {result.interpretation}
                  </Text>

                  <Group justify="space-between">
                    <Text size="xs" c="dimmed">
                      Reported: {formatDate(result.reportDate)}
                    </Text>
                    <Group gap="xs">
                      <ActionIcon variant="subtle" color="blue" size="sm">
                        <IconEye size={14} />
                      </ActionIcon>
                      <ActionIcon variant="subtle" color="green" size="sm">
                        <IconDownload size={14} />
                      </ActionIcon>
                    </Group>
                  </Group>
                </Card>
              ))}
            </SimpleGrid>
          </Paper>
        </Tabs.Panel>

        {/* Prescriptions Tab */}
        <Tabs.Panel value="prescriptions">
          <Paper p="md" radius="md" withBorder mt="md">
            <Group justify="space-between" mb="lg">
              <Title order={3}>Prescriptions</Title>
              <Button leftSection={<IconPlus size={16} />}>
                New Prescription
              </Button>
            </Group>

            <SimpleGrid cols={{ base: 1, lg: 2 }} spacing="lg">
              {[].map /* TODO: Fetch from API */((prescription) => (
                <Card key={prescription.id} padding="lg" radius="md" withBorder>
                  <Group justify="space-between" mb="md">
                    <div>
                      <Text fw={600} size="lg">{prescription.prescriptionNumber}</Text>
                      <Text size="sm" c="dimmed">
                        {formatDate(prescription.prescriptionDate)}
                      </Text>
                    </div>
                    <Badge 
                      color={prescription.status === 'pending' ? 'orange' : 'green'} 
                      variant="light"
                    >
                      {prescription.status}
                    </Badge>
                  </Group>

                  <Stack gap="xs" mb="md">
                    {prescription.medications.map((med) => (
                      <Group key={med.medicationId} justify="space-between" p="xs" 
                             style={{ backgroundColor: '#f8f9fa', borderRadius: '6px' }}>
                        <div>
                          <Text size="sm" fw={500}>{med.medicationName}</Text>
                          <Text size="xs" c="dimmed">
                            {med.dosage} - {med.frequency} for {med.duration}
                          </Text>
                        </div>
                        <Text size="sm" fw={500}>‚Çπ{med.cost}</Text>
                      </Group>
                    ))}
                  </Stack>

                  <Group justify="space-between">
                    <Text size="sm" fw={500}>Total: ‚Çπ{prescription.medications.reduce((sum, med) => sum + (med.cost || 0), 0)}</Text>
                    <Group gap="xs">
                      <Text size="xs" c="dimmed">
                        Refills: {prescription.refillsRemaining}/{prescription.refills}
                      </Text>
                    </Group>
                  </Group>
                </Card>
              ))}
            </SimpleGrid>
          </Paper>
        </Tabs.Panel>

        {/* Documents Tab */}
        <Tabs.Panel value="documents">
          <Paper p="md" radius="md" withBorder mt="md">
            <Group justify="space-between" mb="lg">
              <Title order={3}>Medical Documents</Title>
              <Button leftSection={<IconPlus size={16} />}>
                Upload Document
              </Button>
            </Group>

            <SimpleGrid cols={{ base: 1, md: 2, lg: 3 }} spacing="lg">
              {[].map /* TODO: Fetch from API */((doc) => (
                <Card key={doc.id} padding="md" radius="md" withBorder>
                  <Group justify="space-between" mb="xs">
                    <ThemeIcon color="blue" variant="light">
                      <IconFileText size={20} />
                    </ThemeIcon>
                    <Badge 
                      color={doc.approvalStatus === 'approved' ? 'green' : 'orange'} 
                      variant="light" 
                      size="xs"
                    >
                      {doc.approvalStatus}
                    </Badge>
                  </Group>
                  
                  <Text fw={600} size="sm" mb="xs">{doc.title}</Text>
                  <Text size="xs" c="dimmed" mb="sm" lineClamp={2}>
                    {doc.description}
                  </Text>
                  
                  <Stack gap={4} mb="sm">
                    <Text size="xs" c="dimmed">
                      <strong>Type:</strong> {doc.documentType.replace('_', ' ')}
                    </Text>
                    <Text size="xs" c="dimmed">
                      <strong>Date:</strong> {formatDate(doc.documentDate)}
                    </Text>
                    <Text size="xs" c="dimmed">
                      <strong>Size:</strong> {(doc.fileSize / 1024 / 1024).toFixed(2)} MB
                    </Text>
                  </Stack>
                  
                  <Group justify="space-between">
                    <div>
                      {doc.tags.map((tag) => (
                        <Badge key={tag} size="xs" variant="light" mr="xs">
                          {tag}
                        </Badge>
                      ))}
                    </div>
                    <Group gap="xs">
                      <ActionIcon variant="subtle" color="blue" size="sm">
                        <IconEye size={14} />
                      </ActionIcon>
                      <ActionIcon variant="subtle" color="green" size="sm">
                        <IconDownload size={14} />
                      </ActionIcon>
                    </Group>
                  </Group>
                </Card>
              ))}
            </SimpleGrid>
          </Paper>
        </Tabs.Panel>

        {/* Analytics Tab */}
        <Tabs.Panel value="analytics">
          <Paper p="md" radius="md" withBorder mt="md">
            <Title order={3} mb="lg">Medical Records Analytics</Title>
            
            <SimpleGrid cols={{ base: 1, lg: 2 }} spacing="lg">
              {/* Records by Type */}
              <Card padding="lg" radius="md" withBorder>
                <Title order={4} mb="md">Records by Type</Title>
                <MantineDonutChart
                  data={recordsByTypeData}
                  size={160}
                  thickness={30}
                  withLabels
                />
              </Card>
              
              {/* Records by Status */}
              <Card padding="lg" radius="md" withBorder>
                <Title order={4} mb="md">Records by Status</Title>
                <SimpleBarChart
                  h={200}
                  data={recordsByStatusData}
                  dataKey="status"
                  series={[{ name: 'count', color: 'blue.6' }]}
                />
              </Card>
              
              {/* Common Diagnoses */}
              <Card padding="lg" radius="md" withBorder>
                <Title order={4} mb="md">Common Diagnoses</Title>
                <Stack gap="sm">
                  {commonDiagnosesData.map((diag, index) => (
                    <Group key={diag.diagnosis} justify="space-between" p="sm" 
                           style={{ backgroundColor: '#f8f9fa', borderRadius: '6px' }}>
                      <div>
                        <Text size="sm" fw={500}>{diag.diagnosis}</Text>
                        <Text size="xs" c="dimmed">{diag.icdCode}</Text>
                      </div>
                      <Badge color="blue" variant="light">
                        {diag.count}
                      </Badge>
                    </Group>
                  ))}
                </Stack>
              </Card>
              
              {/* Prescription Trends */}
              <Card padding="lg" radius="md" withBorder>
                <Title order={4} mb="md">Top Prescribed Medications</Title>
                <Stack gap="sm">
                  {prescriptionTrendsData.map((med) => (
                    <Group key={med.medication} justify="space-between" p="sm" 
                           style={{ backgroundColor: '#f8f9fa', borderRadius: '6px' }}>
                      <Text size="sm" fw={500}>{med.medication}</Text>
                      <Group gap="xs">
                        <Badge color="green" variant="light">
                          {med.prescriptionCount}
                        </Badge>
                        <Badge 
                          color={med.trend > 0 ? 'green' : 'red'} 
                          variant="light" 
                          size="xs"
                        >
                          {med.trend > 0 ? '+' : ''}{med.trend}%
                        </Badge>
                      </Group>
                    </Group>
                  ))}
                </Stack>
              </Card>
              
              {/* Recent Activity */}
              <Card padding="lg" radius="md" withBorder style={{ gridColumn: '1 / -1' }}>
                <Title order={4} mb="md">Recent Activity</Title>
                <SimpleAreaChart
                  h={300}
                  data={recentActivityData}
                  dataKey="date"
                  series={[
                    { name: 'recordsCreated', color: 'blue.6' },
                    { name: 'recordsUpdated', color: 'green.6' }
                  ]}
                  curveType="linear"
                />
              </Card>
            </SimpleGrid>
          </Paper>
        </Tabs.Panel>
      </Tabs>

      {/* Record Detail Modal */}
      <Modal
        opened={recordDetailOpened}
        onClose={closeRecordDetail}
        title="Medical Record Details"
        size="xl"
      >
        {selectedRecord && (
          <ScrollArea h={600}>
            <Stack gap="md">
              {/* Patient Info */}
              <Group>
                <Avatar size="xl" color="blue" radius="xl">
                  {selectedRecord.patient.firstName[0]}{selectedRecord.patient.lastName[0]}
                </Avatar>
                <div>
                  <Title order={3}>
                    {selectedRecord.patient.firstName} {selectedRecord.patient.lastName}
                  </Title>
                  <Text c="dimmed">{selectedRecord.recordId}</Text>
                  <Badge color={getStatusColor(selectedRecord.status)} variant="light" mt="xs">
                    {selectedRecord.status.replace('_', ' ')}
                  </Badge>
                </div>
              </Group>

              <Divider />

              {/* Basic Information */}
              <SimpleGrid cols={2}>
                <div>
                  <Text size="sm" fw={500}>Record Type</Text>
                  <Badge color={getTypeColor(selectedRecord.recordType)} variant="light">
                    {selectedRecord.recordType.replace('_', ' ')}
                  </Badge>
                </div>
                <div>
                  <Text size="sm" fw={500}>Record Date</Text>
                  <Text size="sm" c="dimmed">
                    {formatDate(selectedRecord.recordDate)}
                  </Text>
                </div>
                <div>
                  <Text size="sm" fw={500}>Doctor</Text>
                  <Text size="sm" c="dimmed">
                    {selectedRecord.doctor.firstName} {selectedRecord.doctor.lastName}
                  </Text>
                </div>
                <div>
                  <Text size="sm" fw={500}>Department</Text>
                  <Text size="sm" c="dimmed">{selectedRecord.doctor.department?.name}</Text>
                </div>
              </SimpleGrid>

              <Divider />

              {/* Clinical Information */}
              <div>
                <Text size="sm" fw={500} mb="sm">Chief Complaint</Text>
                <Text size="sm">{selectedRecord.chiefComplaint}</Text>
              </div>

              <div>
                <Text size="sm" fw={500} mb="sm">History of Present Illness</Text>
                <Text size="sm">{selectedRecord.historyOfPresentIllness}</Text>
              </div>

              <div>
                <Text size="sm" fw={500} mb="sm">Assessment</Text>
                <Text size="sm">{selectedRecord.assessment}</Text>
              </div>

              <div>
                <Text size="sm" fw={500} mb="sm">Treatment Plan</Text>
                <Text size="sm">{selectedRecord.treatmentPlan}</Text>
              </div>

              {/* Vital Signs */}
              <div>
                <Text size="sm" fw={500} mb="sm">Vital Signs</Text>
                <SimpleGrid cols={3}>
                  <Group>
                    <IconHeart size={16} color="red" />
                    <Text size="sm">
                      BP: {selectedRecord.vitalSigns.bloodPressure.systolic}/{selectedRecord.vitalSigns.bloodPressure.diastolic}
                    </Text>
                  </Group>
                  <Group>
                    <IconActivity size={16} color="blue" />
                    <Text size="sm">HR: {selectedRecord.vitalSigns.heartRate} bpm</Text>
                  </Group>
                  <Group>
                    <IconLungs size={16} color="green" />
                    <Text size="sm">Temp: {selectedRecord.vitalSigns.temperature}¬∞{selectedRecord.vitalSigns.temperatureUnit}</Text>
                  </Group>
                </SimpleGrid>
              </div>

              {/* Allergies */}
              {selectedRecord.allergies && selectedRecord.allergies.length > 0 && (
                <div>
                  <Text size="sm" fw={500} mb="sm">Allergies</Text>
                  <Stack gap="xs">
                    {selectedRecord.allergies.map((allergy) => (
                      <Group key={allergy.id} p="sm" style={{ backgroundColor: '#fff5f5', borderRadius: '6px' }}>
                        <IconAlertTriangle size={16} color="red" />
                        <div>
                          <Text size="sm" fw={500}>{allergy.allergen}</Text>
                          <Text size="xs" c="dimmed">
                            {allergy.reaction} - 
                            <Badge color={getSeverityColor(allergy.severity)} variant="light" size="xs" ml="xs">
                              {allergy.severity}
                            </Badge>
                          </Text>
                        </div>
                      </Group>
                    ))}
                  </Stack>
                </div>
              )}

              {/* Diagnoses */}
              {selectedRecord.diagnosis && selectedRecord.diagnosis.length > 0 && (
                <div>
                  <Text size="sm" fw={500} mb="sm">Diagnoses</Text>
                  <Stack gap="xs">
                    {selectedRecord.diagnosis.map((diagnosis) => (
                      <Group key={diagnosis.id} justify="space-between" p="sm" 
                             style={{ backgroundColor: '#f8f9fa', borderRadius: '6px' }}>
                        <div>
                          <Text size="sm" fw={500}>{diagnosis.diagnosisName}</Text>
                          <Text size="xs" c="dimmed">{diagnosis.diagnosisCode}</Text>
                        </div>
                        <Badge color="blue" variant="light" size="sm">
                          {diagnosis.diagnosisType}
                        </Badge>
                      </Group>
                    ))}
                  </Stack>
                </div>
              )}

              {/* Clinical Notes */}
              <div>
                <Text size="sm" fw={500} mb="sm">Clinical Notes</Text>
                <Text size="sm" style={{ whiteSpace: 'pre-wrap' }}>
                  {selectedRecord.clinicalNotes}
                </Text>
              </div>

              <Group justify="flex-end">
                <Button variant="light" onClick={closeRecordDetail}>
                  Close
                </Button>
                <Button>
                  Edit Record
                </Button>
              </Group>
            </Stack>
          </ScrollArea>
        )}
      </Modal>

      {/* Add Record Modal */}
      <Modal
        opened={addRecordOpened}
        onClose={closeAddRecord}
        title="Create New Medical Record"
        size="lg"
      >
        <Stack gap="md">
          <SimpleGrid cols={2}>
            <Select
              label="Patient"
              placeholder="Select patient"
              data={[].map /* TODO: Fetch from API */(patient => ({ 
                value: patient.id, 
                label: `${patient.firstName} ${patient.lastName}` 
              }))}
              required
            />
            <Select
              label="Record Type"
              placeholder="Select type"
              data={[
                { value: 'consultation', label: 'Consultation' },
                { value: 'follow_up', label: 'Follow-up' },
                { value: 'emergency', label: 'Emergency' },
                { value: 'admission', label: 'Admission' }
              ]}
              required
            />
          </SimpleGrid>
          
          <Textarea
            label="Chief Complaint"
            placeholder="Enter chief complaint"
            required
          />
          
          <Textarea
            label="History of Present Illness"
            placeholder="Enter history of present illness"
            rows={4}
          />
          
          <SimpleGrid cols={3}>
            <TextInput
              label="Blood Pressure"
              placeholder="120/80"
            />
            <NumberInput
              label="Heart Rate (BPM)"
              placeholder="72"
            />
            <NumberInput
              label="Temperature (¬∞C)"
              placeholder="98.6"
            />
          </SimpleGrid>
          
          <Textarea
            label="Assessment"
            placeholder="Enter assessment"
            rows={3}
          />
          
          <Textarea
            label="Treatment Plan"
            placeholder="Enter treatment plan"
            rows={3}
          />
          
          <Group justify="flex-end">
            <Button variant="light" onClick={closeAddRecord}>
              Cancel
            </Button>
            <Button onClick={() => {
              notifications.show({
                title: 'Success',
                message: 'Medical record created successfully',
                color: 'green',
              });
              closeAddRecord();
            }}>
              Create Record
            </Button>
          </Group>
        </Stack>
      </Modal>
    </Container>
  );
};

export default EMRManagement;

'use client';

import Link from "next/link";
import { useState, useEffect, useMemo } from "react";
import { useRouter } from "next/navigation";
import { getModulesForRole, getRoleDisplayName, getRoleBadgeColor, type UserRole, type DashboardModule } from '@/lib/rbac';

export default function EnhancedDashboard() {
  const router = useRouter();
  const [user, setUser] = useState<any>(null);
  const [stats, setStats] = useState({
    totalPatients: 0,
    todaysAppointments: 0,
    pendingBills: 0,
    activeDoctors: 0
  });

  useEffect(() => {
    const storedToken = localStorage.getItem('accessToken');
    const storedUser = localStorage.getItem('user');

    if (!storedToken || !storedUser) {
      router.push('/login');
      return;
    }

    const userData = JSON.parse(storedUser);
    setUser(userData);
    
    // Stats will be fetched from API - showing zeros until implemented
    setStats({
      totalPatients: 0,
      todaysAppointments: 0,
      pendingBills: 0,
      activeDoctors: 0
    });
    
    // TODO: Fetch real stats from API
    // fetchDashboardStats().then(data => setStats(data));
  }, [router]);

  // Get modules accessible to this user's role (RBAC filtering)
  const accessibleModules = useMemo(() => {
    if (!user?.role) return [];
    const filtered = getModulesForRole(user.role as UserRole);
    console.log(`[Dashboard] Modules for ${user.role}:`, filtered.length, 'modules');
    return filtered;
  }, [user]);

  const handleLogout = () => {
    localStorage.removeItem('accessToken');
    localStorage.removeItem('user');
    router.push('/login');
  };

  if (!user) {
    return (
      <div style={{ minHeight: "100vh", background: "linear-gradient(135deg, #667eea 0%, #764ba2 100%)", display: "flex", alignItems: "center", justifyContent: "center" }}>
        <div style={{
          background: "rgba(255, 255, 255, 0.95)",
          padding: "3rem",
          borderRadius: "15px",
          boxShadow: "0 20px 25px -5px rgba(0, 0, 0, 0.1)",
          backdropFilter: "blur(10px)",
          textAlign: "center"
        }}>
          <div>Loading dashboard...</div>
        </div>
      </div>
    );
  }

  const modules = accessibleModules;

  return (
    <div style={{
      background: 'linear-gradient(135deg, #667eea 0%, #764ba2 50%, #f093fb 100%)',
      minHeight: '100%',
      borderRadius: '16px',
      padding: '0',
      backgroundAttachment: 'fixed'
    }}>
      {/* Welcome Section */}
      <div style={{
        background: 'linear-gradient(135deg, #ff9a9e 0%, #fecfef 50%, #fecfef 100%)',
        color: 'white',
        padding: '3rem 2rem',
        borderRadius: '16px 16px 0 0',
        marginBottom: '2rem',
        position: 'relative',
        overflow: 'hidden'
      }}>
        <div style={{
          position: 'absolute',
          top: '-50%',
          right: '-20%',
          width: '200px',
          height: '200px',
          background: 'rgba(255,255,255,0.1)',
          borderRadius: '50%',
          filter: 'blur(50px)'
        }} />
        <div style={{
          position: 'absolute',
          bottom: '-30%',
          left: '-10%',
          width: '150px',
          height: '150px',
          background: 'rgba(255,255,255,0.05)',
          borderRadius: '50%',
          filter: 'blur(30px)'
        }} />

        <div style={{ position: 'relative', zIndex: 2 }}>
          <div style={{ display: 'flex', alignItems: 'center', gap: '1rem', marginBottom: '0.5rem', flexWrap: 'wrap' }}>
            <h1 style={{
              fontSize: '2rem',
              fontWeight: '800',
              margin: 0,
              background: 'linear-gradient(135deg, #ffffff 0%, #f0f9ff 100%)',
              backgroundClip: 'text',
              WebkitBackgroundClip: 'text',
              WebkitTextFillColor: 'transparent'
            }}>
              Welcome back, {user.firstName}! üëã
            </h1>
            <span style={{
              background: getRoleBadgeColor(user.role as UserRole),
              color: 'white',
              padding: '0.4rem 1rem',
              borderRadius: '20px',
              fontSize: '0.85rem',
              fontWeight: '600',
              boxShadow: '0 4px 15px rgba(0,0,0,0.2)'
            }}>
              {getRoleDisplayName(user.role as UserRole)}
            </span>
          </div>
          <p style={{
            fontSize: '1rem',
            opacity: 0.9,
            margin: 0
          }}>
            Here&apos;s what&apos;s happening at {user.tenant?.name} today ‚Ä¢ {modules.length} modules available
          </p>
        </div>
      </div>

      {/* Quick Stats - Role-based */}
      <div style={{
        display: "grid",
        gridTemplateColumns: "repeat(auto-fit, minmax(280px, 1fr))",
        gap: "1.5rem",
        marginBottom: "3rem",
        padding: '0 2rem'
      }}>
        {(user.role === 'PATIENT' ? [
          { label: "My Appointments", value: stats.todaysAppointments, color: "#4ecdc4", icon: "üìÖ", bg: "rgba(78, 205, 196, 0.1)" },
          { label: "Pending Bills", value: stats.pendingBills, color: "#45b7d1", icon: "üí∞", bg: "rgba(69, 183, 209, 0.1)" },
          { label: "Medical Records", value: 0, color: "#ff6b6b", icon: "üìã", bg: "rgba(255, 107, 107, 0.1)" },
          { label: "Prescriptions", value: 0, color: "#96ceb4", icon: "üíä", bg: "rgba(150, 206, 180, 0.1)" }
        ] : [
          { label: "Total Patients", value: stats.totalPatients, color: "#ff6b6b", icon: "üë•", bg: "rgba(255, 107, 107, 0.1)" },
          { label: "Today's Appointments", value: stats.todaysAppointments, color: "#4ecdc4", icon: "üìÖ", bg: "rgba(78, 205, 196, 0.1)" },
          { label: "Pending Bills", value: stats.pendingBills, color: "#45b7d1", icon: "üí∞", bg: "rgba(69, 183, 209, 0.1)" },
          { label: "Active Doctors", value: stats.activeDoctors, color: "#96ceb4", icon: "üë®‚Äç‚öïÔ∏è", bg: "rgba(150, 206, 180, 0.1)" }
        ]).map((stat, index) => (
          <div key={index} style={{
            background: 'rgba(255,255,255,0.95)',
            padding: "1.5rem",
            borderRadius: "16px",
            boxShadow: "0 8px 32px rgba(0,0,0,0.1)",
            border: "1px solid rgba(255,255,255,0.2)",
            transition: 'all 0.3s ease',
            cursor: 'pointer',
            backdropFilter: 'blur(10px)'
          }}
          onMouseEnter={(e) => {
            e.currentTarget.style.transform = 'translateY(-4px)';
            e.currentTarget.style.boxShadow = '0 12px 40px rgba(0,0,0,0.15)';
          }}
          onMouseLeave={(e) => {
            e.currentTarget.style.transform = 'translateY(0)';
            e.currentTarget.style.boxShadow = '0 8px 32px rgba(0,0,0,0.1)';
          }}
          >
            <div style={{ display: "flex", alignItems: "center", justifyContent: "space-between" }}>
              <div>
                <p style={{
                  color: "#64748b",
                  fontSize: "0.85rem",
                  margin: "0 0 0.5rem 0",
                  fontWeight: "500"
                }}>{stat.label}</p>
                <p style={{
                  color: stat.color,
                  fontSize: "2rem",
                  fontWeight: "800",
                  margin: 0,
                  background: `linear-gradient(135deg, ${stat.color} 0%, ${stat.color}dd 100%)`,
                  backgroundClip: 'text',
                  WebkitBackgroundClip: 'text',
                  WebkitTextFillColor: 'transparent'
                }}>{stat.value}</p>
              </div>
              <div style={{
                fontSize: "2.5rem",
                background: stat.bg,
                padding: "0.75rem",
                borderRadius: "12px",
                boxShadow: `0 4px 20px ${stat.color}33`
              }}>
                {stat.icon}
              </div>
            </div>
          </div>
        ))}
      </div>

      {/* HMS Modules - Only for Staff/Admin, not Patients */}
      {user.role !== 'PATIENT' && (
      <div style={{
        marginBottom: "3rem",
        padding: '0 2rem'
      }}>
        <div style={{
          display: "flex",
          justifyContent: "space-between",
          alignItems: "center",
          marginBottom: "2rem"
        }}>
          <h2 style={{
            color: "#1e293b",
            fontSize: "1.5rem",
            fontWeight: "700",
            margin: 0,
            background: 'linear-gradient(135deg, #ff6b6b 0%, #4ecdc4 50%, #45b7d1 100%)',
            backgroundClip: 'text',
            WebkitBackgroundClip: 'text',
            WebkitTextFillColor: 'transparent'
          }}>
            HMS Modules ({modules.filter(m => m.active).length}/{modules.length} Active)
          </h2>
          <div style={{ display: "flex", gap: "1rem", alignItems: "center" }}>
            <div style={{
              display: "flex",
              alignItems: "center",
              gap: "0.5rem",
              background: 'rgba(78, 205, 196, 0.1)',
              padding: '0.5rem 1rem',
              borderRadius: '20px',
              border: '1px solid rgba(78, 205, 196, 0.2)'
            }}>
              <div style={{
                width: "8px",
                height: "8px",
                borderRadius: "50%",
                background: "#4ecdc4"
              }} />
              <span style={{ fontSize: "0.9rem", color: "#059669", fontWeight: "500" }}>Active</span>
            </div>
            <div style={{
              display: "flex",
              alignItems: "center",
              gap: "0.5rem",
              background: 'rgba(156, 163, 175, 0.1)',
              padding: '0.5rem 1rem',
              borderRadius: '20px',
              border: '1px solid rgba(156, 163, 175, 0.2)'
            }}>
              <div style={{
                width: "8px",
                height: "8px",
                borderRadius: "50%",
                background: "#9ca3af"
              }} />
              <span style={{ fontSize: "0.9rem", color: "#6b7280", fontWeight: "500" }}>Coming Soon</span>
            </div>
          </div>
        </div>
        <div style={{
          display: "grid",
          gridTemplateColumns: "repeat(auto-fit, minmax(320px, 1fr))",
          gap: "2rem"
        }}>
          {modules.map((module, index) => (
            <Link
              key={index}
              href={module.active ? module.href : "#"}
              style={{ textDecoration: "none", pointerEvents: module.active ? "auto" : "none" }}
            >
              <div style={{
                background: module.active ? 'rgba(255,255,255,0.95)' : 'rgba(249, 250, 251, 0.9)',
                padding: "1.5rem",
                borderRadius: "16px",
                boxShadow: module.active ? "0 8px 32px rgba(0,0,0,0.1)" : "0 4px 16px rgba(0,0,0,0.05)",
                border: module.active ? "1px solid rgba(255,255,255,0.2)" : "1px solid rgba(229, 231, 235, 0.8)",
                transition: "all 0.3s ease",
                cursor: module.active ? "pointer" : "not-allowed",
                opacity: module.active ? 1 : 0.6,
                position: "relative",
                backdropFilter: 'blur(10px)',
                overflow: 'hidden'
              }}
              onMouseEnter={(e) => {
                if (module.active) {
                  e.currentTarget.style.transform = "translateY(-8px)";
                  e.currentTarget.style.boxShadow = "0 16px 48px rgba(0,0,0,0.15)";
                }
              }}
              onMouseLeave={(e) => {
                if (module.active) {
                  e.currentTarget.style.transform = "translateY(0)";
                  e.currentTarget.style.boxShadow = "0 8px 32px rgba(0,0,0,0.1)";
                }
              }}
              >
                {/* Active Indicator Badge */}
                <div style={{
                  position: "absolute",
                  top: "1.5rem",
                  right: "1.5rem",
                  width: "12px",
                  height: "12px",
                  borderRadius: "50%",
                  background: module.active ? "#4ecdc4" : "#9ca3af",
                  border: '2px solid white',
                  boxShadow: '0 2px 8px rgba(0,0,0,0.1)'
                }} />

                <div style={{
                  position: 'absolute',
                  top: '1rem',
                  left: '1rem',
                  width: '4px',
                  height: '100%',
                  background: module.active ? `linear-gradient(135deg, ${module.color} 0%, ${module.color}dd 100%)` : '#e5e7eb',
                  borderRadius: '2px'
                }} />

                <div style={{ display: "flex", alignItems: "flex-start", gap: "1.5rem", marginLeft: '1rem' }}>
                  <div style={{
                    fontSize: "2.5rem",
                    background: module.active ? `${module.color}15` : '#f3f4f6',
                    padding: "0.75rem",
                    borderRadius: "12px",
                    transition: 'all 0.3s ease',
                    boxShadow: module.active ? `0 4px 20px ${module.color}25` : '0 2px 8px rgba(0,0,0,0.05)'
                  }}>
                    {module.icon}
                  </div>
                  <div style={{ flex: 1 }}>
                    <h3 style={{
                      color: module.active ? "#1f2937" : "#6b7280",
                      fontSize: "1.1rem",
                      fontWeight: "600",
                      margin: "0 0 0.75rem 0",
                      display: "flex",
                      alignItems: "center",
                      gap: "0.5rem"
                    }}>
                      {module.title}
                    </h3>
                    <p style={{
                      color: "#6b7280",
                      fontSize: "0.9rem",
                      margin: "0 0 1.5rem 0",
                      lineHeight: "1.5"
                    }}>
                      {module.description}
                    </p>
                    <div style={{
                      color: module.active ? module.color : "#9ca3af",
                      fontSize: "0.85rem",
                      fontWeight: "600",
                      background: module.active ? `${module.color}15` : "#f3f4f6",
                      padding: "0.5rem 1rem",
                      borderRadius: "8px",
                      display: "inline-block",
                      border: module.active ? `1px solid ${module.color}25` : '1px solid #e5e7eb'
                    }}>
                      {module.stats}
                    </div>
                  </div>
                </div>
              </div>
            </Link>
          ))}
        </div>
      </div>
      )}

      {/* Patient Quick Actions - Only for Patients */}
      {user.role === 'PATIENT' && (
        <div style={{
          marginBottom: "3rem",
          padding: '0 2rem'
        }}>
          <h2 style={{
            color: "#1e293b",
            fontSize: "1.5rem",
            fontWeight: "700",
            marginBottom: "2rem",
            background: 'linear-gradient(135deg, #ff6b6b 0%, #4ecdc4 50%, #45b7d1 100%)',
            backgroundClip: 'text',
            WebkitBackgroundClip: 'text',
            WebkitTextFillColor: 'transparent'
          }}>
            Quick Actions
          </h2>
          <div style={{
            display: "grid",
            gridTemplateColumns: "repeat(auto-fit, minmax(280px, 1fr))",
            gap: "1.5rem"
          }}>
            {[
              { title: "My Health Records", desc: "View your complete medical history", href: "/dashboard/my-records", icon: "üìã", color: "#ff6b6b" },
              { title: "My Appointments", desc: "Manage your upcoming appointments", href: "/dashboard/my-appointments", icon: "üìÖ", color: "#4ecdc4" },
              { title: "My Bills", desc: "View and pay your medical bills", href: "/dashboard/my-bills", icon: "üí∞", color: "#45b7d1" },
              { title: "Book Appointment", desc: "Schedule a new appointment", href: "/dashboard/appointments", icon: "üóìÔ∏è", color: "#96ceb4" },
            ].map((action, index) => (
              <Link key={index} href={action.href} style={{ textDecoration: "none" }}>
                <div style={{
                  background: 'rgba(255,255,255,0.95)',
                  padding: "1.5rem",
                  borderRadius: "16px",
                  boxShadow: "0 8px 32px rgba(0,0,0,0.1)",
                  border: "1px solid rgba(255,255,255,0.2)",
                  transition: "all 0.3s ease",
                  cursor: "pointer",
                  backdropFilter: 'blur(10px)',
                  minHeight: '140px',
                  display: 'flex',
                  flexDirection: 'column',
                  justifyContent: 'space-between'
                }}
                onMouseEnter={(e) => {
                  e.currentTarget.style.transform = "translateY(-4px)";
                  e.currentTarget.style.boxShadow = "0 12px 40px rgba(0,0,0,0.15)";
                }}
                onMouseLeave={(e) => {
                  e.currentTarget.style.transform = "translateY(0)";
                  e.currentTarget.style.boxShadow = "0 8px 32px rgba(0,0,0,0.1)";
                }}
                >
                  <div style={{ display: "flex", alignItems: "center", gap: "1rem", marginBottom: "1rem" }}>
                    <div style={{
                      fontSize: "2rem",
                      background: `${action.color}15`,
                      padding: "0.75rem",
                      borderRadius: "12px",
                      boxShadow: `0 4px 20px ${action.color}25`
                    }}>
                      {action.icon}
                    </div>
                    <h3 style={{
                      color: "#1f2937",
