                <Card key={account.id} padding="lg" radius="md" withBorder>
                  <Group justify="space-between" mb="md">
                    <div>
                      <Text fw={600} size="lg">{account.name}</Text>
                      <Text size="sm" c="dimmed">{account.code}</Text>
