interface InsuranceProvider {
  id: string;
  name: string;
  code: string;
  type: 'mediclaim' | 'corporate' | 'government' | 'tpa';
  contactPerson: string;
  phone: string;
  email: string;
  address: string;
  cashlessLimit: number;
