          </Tabs.Tab>
          <Tabs.Tab value="dashboards" leftSection={<IconDashboard size={16} />}>
            Dashboards
          </Tabs.Tab>
          <Tabs.Tab value="analytics" leftSection={<IconDeviceDesktopAnalytics size={16} />}>
            Analytics
          </Tabs.Tab>
        </Tabs.List>

        {/* Overview Tab */}
        <Tabs.Panel value="overview">
          <SimpleGrid cols={{ base: 1, lg: 2 }} spacing="lg" mt="md">
