                  <Timeline.Item
                    key={integration.id}
                    bullet={
                      <ThemeIcon color={getStatusColor(integration.status)} size={24} radius="xl">
                        <IconArrowsRightLeft size={12} />
                      </ThemeIcon>
                    }
                    title={integration.name}
                  >
                    <Text size="sm" c="dimmed">{integration.type}</Text>
                    <Text size="xs" c="dimmed">{formatDate(integration.lastSync)}</Text>
