                })}
              </Stack>
            </Card>
          </SimpleGrid>
        </Tabs.Panel>

        {/* API Management Tab */}
        <Tabs.Panel value="api">
          <Paper p="md" radius="md" withBorder mt="md">
            <Group justify="space-between" mb="lg">
              <Title order={3}>API Endpoints</Title>
              <Group>
                <Button leftSection={<IconPlus size={16} />}>
                  Add Endpoint
