                <Button variant="light" leftSection={<IconDownload size={16} />}>
                  Export Swagger
                </Button>
              </Group>
            </Group>

            {/* Search */}
            <TextInput
              placeholder="Search endpoints..."
              leftSection={<IconSearch size={16} />}
              value={searchQuery}
              onChange={(event) => setSearchQuery(event.currentTarget.value)}
              mb="md"
            />

            {/* API Endpoints Table */}
            <ScrollArea>
              <Table striped highlightOnHover>
                <Table.Thead>
                  <Table.Tr>
                    <Table.Th>Path</Table.Th>
                    <Table.Th>Method</Table.Th>
                    <Table.Th>Version</Table.Th>
                    <Table.Th>Description</Table.Th>
                    <Table.Th>Auth</Table.Th>
                    <Table.Th>Status</Table.Th>
                    <Table.Th>Actions</Table.Th>
                  </Table.Tr>
                </Table.Thead>
                <Table.Tbody>
                  {filteredEndpoints.length === 0 ? (
                    <Table.Tr>
                      <Table.Td colSpan={7}>
                        <EmptyState
                          icon={<IconPlug size={48} />}
                          title="No integrations"
                          description="Connect external systems"
                          size="sm"
                        />
                      </Table.Td>
                    </Table.Tr>
                  ) : (
                    filteredEndpoints.map((endpoint) => (
                      <Table.Tr key={endpoint.id}>
                        <Table.Td>
                          <Code>{endpoint.path}</Code>
                        </Table.Td>
                        <Table.Td>
                          <Badge 
                            color={
                              endpoint.method === 'GET' ? 'blue' :
                              endpoint.method === 'POST' ? 'green' :
                              endpoint.method === 'PUT' ? 'orange' :
                              endpoint.method === 'DELETE' ? 'red' : 'gray'
                            }
                          >
                            {endpoint.method}
                          </Badge>
                        </Table.Td>
                        <Table.Td>{endpoint.version}</Table.Td>
                        <Table.Td>
                          <Text size="sm" lineClamp={2}>{endpoint.description}</Text>
                        </Table.Td>
                        <Table.Td>
                          <Group>
                            {endpoint.requiresAuth && <IconLock size={16} />}
                            <Text size="sm">{endpoint.authType}</Text>
                          </Group>
                        </Table.Td>
                        <Table.Td>
                          <Badge color={getStatusColor(endpoint.status)} variant="light">
                            {endpoint.status.toUpperCase()}
                          </Badge>
                        </Table.Td>
                        <Table.Td>
                          <Group gap="xs">
                            <ActionIcon
                              variant="subtle"
                              color="blue"
                              onClick={() => handleViewEndpoint(endpoint)}
                            >
                              <IconEye size={16} />
                            </ActionIcon>
                            <ActionIcon variant="subtle" color="green">
                              <IconEdit size={16} />
                            </ActionIcon>
                            <ActionIcon variant="subtle" color="gray">
                              <IconExternalLink size={16} />
                            </ActionIcon>
                          </Group>
                        </Table.Td>
                      </Table.Tr>
                    ))
                  )}
                </Table.Tbody>
              </Table>
            </ScrollArea>
          </Paper>
        </Tabs.Panel>

        {/* Integrations Tab */}
        <Tabs.Panel value="integrations">
          <Paper p="md" radius="md" withBorder mt="md">
            <Group justify="space-between" mb="md">
              <Title order={3}>System Integrations</Title>
              <Button leftSection={<IconPlus size={16} />} onClick={openCreateIntegration}>
                New Integration
              </Button>
            </Group>

            {/* Filters */}
            <Group mb="md">
              <TextInput
                placeholder="Search integrations..."
                leftSection={<IconSearch size={16} />}
                value={searchQuery}
                onChange={(event) => setSearchQuery(event.currentTarget.value)}
                style={{ flex: 1 }}
              />
              <Select
                placeholder="Status"
                data={[
                  { value: 'active', label: 'Active' },
                  { value: 'inactive', label: 'Inactive' },
                  { value: 'error', label: 'Error' },
                  { value: 'warning', label: 'Warning' },
                  { value: 'pending', label: 'Pending' },
                ]}
                value={selectedStatus}
                onChange={setSelectedStatus}
                clearable
              />
              <Select
                placeholder="Type"
                data={[
                  { value: 'ehr', label: 'EHR System' },
                  { value: 'payment', label: 'Payment Gateway' },
                  { value: 'analytics', label: 'Analytics' },
                  { value: 'lab', label: 'Laboratory' },
                  { value: 'pharmacy', label: 'Pharmacy' },
                  { value: 'imaging', label: 'Imaging' },
                ]}
                value={selectedType}
                onChange={setSelectedType}
                clearable
              />
            </Group>

            {/* Integrations Grid */}
            <SimpleGrid cols={{ base: 1, md: 2, lg: 3 }} spacing="lg">
              {filteredIntegrations.map((integration) => (
                <Card key={integration.id} padding="lg" radius="md" withBorder>
                  <Group justify="space-between" mb="md">
                    <div>
                      <Text fw={600} size="lg">{integration.name}</Text>
                      <Text size="sm" c="dimmed">{integration.type}</Text>
                    </div>
                    <Badge color={getStatusColor(integration.status)} variant="light">
                      {integration.status.toUpperCase()}
                    </Badge>
                  </Group>

                  <Text size="sm" c="dimmed" lineClamp={2} mb="md">
                    {integration.description}
                  </Text>

                  <Stack gap="xs" mb="md">
                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Last Sync</Text>
                      <Text size="sm">{formatDate(integration.lastSync)}</Text>
                    </Group>
                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Data Flow</Text>
                      <Text size="sm">{integration.dataDirection}</Text>
                    </Group>
                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Authentication</Text>
                      <Text size="sm">{integration.authMethod}</Text>
                    </Group>
                  </Stack>

                  <Group justify="space-between">
                    <Button 
                      variant="light" 
                      size="xs"
                      leftSection={<IconRefresh size={14} />}
                    >
                      Sync Now
                    </Button>
                    <Group gap="xs">
                      <ActionIcon
                        variant="subtle"
                        color="blue"
                        onClick={() => handleViewIntegration(integration)}
                      >
                        <IconEye size={16} />
                      </ActionIcon>
                      <ActionIcon variant="subtle" color="green">
                        <IconEdit size={16} />
                      </ActionIcon>
                    </Group>
                  </Group>
                </Card>
              ))}
            </SimpleGrid>
          </Paper>
        </Tabs.Panel>

        {/* Data Warehouse Tab */}
        <Tabs.Panel value="datawarehouse">
          <Paper p="md" radius="md" withBorder mt="md">
            <Group justify="space-between" mb="lg">
              <Title order={3}>Data Warehouse</Title>
              <Group>
                <Button leftSection={<IconPlus size={16} />}>
                  Add Data Source
                </Button>
                <Button 
                  variant="light" 
                  leftSection={<IconCloudUpload size={16} />}
                >
                  ETL Jobs
                </Button>
              </Group>
            </Group>

            {/* Data Sources and Warehouses */}
            <SimpleGrid cols={{ base: 1, md: 2 }} spacing="lg" mb="lg">
              {/* Data Sources */}
              <Card padding="lg" radius="md" withBorder>
                <Title order={4} mb="md">Data Sources</Title>
                <Stack gap="md">
                  {[].map /* TODO: Fetch from API */((source) => (
                    <Group key={source.id} justify="space-between" p="sm" style={{ backgroundColor: '#f8f9fa', borderRadius: '8px' }}>
                      <Group>
                        <ThemeIcon color={getStatusColor(source.status)} size="md" radius="xl">
                          <IconDatabase size={16} />
                        </ThemeIcon>
                        <div>
                          <Text size="sm" fw={500}>{source.name}</Text>
                          <Text size="xs" c="dimmed">{source.type}</Text>
                        </div>
                      </Group>
                      <Badge color={getStatusColor(source.status)}>
                        {source.status}
                      </Badge>
                    </Group>
                  ))}
                </Stack>
              </Card>
              
              {/* Warehouses */}
              <Card padding="lg" radius="md" withBorder>
                <Title order={4} mb="md">Data Warehouses</Title>
                <Stack gap="md">
                  {[].map /* TODO: Fetch from API */((warehouse) => (
                    <Group key={warehouse.id} justify="space-between" p="sm" style={{ backgroundColor: '#f8f9fa', borderRadius: '8px' }}>
                      <Group>
                        <ThemeIcon color="blue" size="md" radius="xl">
                          <IconBuildingWarehouse size={16} />
                        </ThemeIcon>
                        <div>
                          <Text size="sm" fw={500}>{warehouse.name}</Text>
                          <Text size="xs" c="dimmed">{warehouse.provider}</Text>
                        </div>
                      </Group>
                      <Group>
                        <Text size="xs" c="dimmed">Size: {warehouse.size}</Text>
                        <Badge color={getStatusColor(warehouse.status)}>
                          {warehouse.status}
                        </Badge>
                      </Group>
                    </Group>
                  ))}
                </Stack>
              </Card>
            </SimpleGrid>

            {/* Data Pipelines */}
            <Card padding="lg" radius="md" withBorder mb="lg">
              <Title order={4} mb="md">Data Pipelines</Title>
              <ScrollArea>
                <Table striped highlightOnHover>
                  <Table.Thead>
                    <Table.Tr>
                      <Table.Th>Pipeline Name</Table.Th>
                      <Table.Th>Source</Table.Th>
                      <Table.Th>Destination</Table.Th>
                      <Table.Th>Frequency</Table.Th>
                      <Table.Th>Last Run</Table.Th>
                      <Table.Th>Status</Table.Th>
                      <Table.Th>Actions</Table.Th>
                    </Table.Tr>
                  </Table.Thead>
                  <Table.Tbody>
                    {[].map /* TODO: Fetch from API */((pipeline) => (
                      <Table.Tr key={pipeline.id}>
                        <Table.Td>
                          <Text fw={500}>{pipeline.name}</Text>
                        </Table.Td>
                        <Table.Td>{pipeline.source}</Table.Td>
                        <Table.Td>{pipeline.destination}</Table.Td>
                        <Table.Td>{pipeline.frequency}</Table.Td>
                        <Table.Td>
                          <Group>
                            <Text size="sm">{formatDate(pipeline.lastRun)}</Text>
                          </Group>
                        </Table.Td>
                        <Table.Td>
                          <Badge color={getStatusColor(pipeline.status)} variant="light">
                            {pipeline.status.toUpperCase()}
                          </Badge>
                        </Table.Td>
                        <Table.Td>
                          <Group gap="xs">
                            <ActionIcon variant="subtle" color="blue">
                              <IconEye size={16} />
                            </ActionIcon>
                            <ActionIcon variant="subtle" color="green">
                              <IconEdit size={16} />
                            </ActionIcon>
                            <ActionIcon variant="subtle" color="orange">
                              <IconRefresh size={16} />
                            </ActionIcon>
                          </Group>
                        </Table.Td>
                      </Table.Tr>
                    ))}
                  </Table.Tbody>
                </Table>
              </ScrollArea>
            </Card>

            {/* Schema Browser Teaser */}
            <Group justify="center">
              <Button variant="outline" leftSection={<IconSchema size={16} />}>
                Open Schema Browser
              </Button>
            </Group>
          </Paper>
        </Tabs.Panel>

        {/* Dashboards Tab */}
        <Tabs.Panel value="dashboards">
          <Paper p="md" radius="md" withBorder mt="md">
            <Group justify="space-between" mb="lg">
              <Title order={3}>Analytics Dashboards</Title>
              <Button leftSection={<IconPlus size={16} />}>
                Create Dashboard
              </Button>
            </Group>

            {/* Dashboards Grid */}
            <SimpleGrid cols={{ base: 1, md: 2, lg: 3 }} spacing="lg">
              {[].map /* TODO: Fetch from API */((dashboard) => (
                <Card key={dashboard.id} padding="lg" radius="md" withBorder>
                  <Group justify="space-between" mb="md">
                    <div>
                      <Text fw={600} size="lg">{dashboard.title}</Text>
                      <Text size="sm" c="dimmed">{dashboard.category}</Text>
                    </div>
                    <ThemeIcon color="blue" radius="xl" variant="light">
                      <IconDashboard size={18} />
                    </ThemeIcon>
                  </Group>

                  {dashboard.thumbnail && (
                    <div style={{ 
                      height: 120, 
                      background: `url(${dashboard.thumbnail})`, 
                      backgroundSize: 'cover', 
                      backgroundPosition: 'center', 
                      borderRadius: '8px',
                      marginBottom: '16px'
                    }} />
                  )}

                  <Text size="sm" c="dimmed" lineClamp={2} mb="md">
                    {dashboard.description}
                  </Text>

                  <Group justify="space-between" mb="xs">
                    <Text size="sm" c="dimmed">Last Updated</Text>
                    <Text size="sm">{formatDate(dashboard.lastUpdated)}</Text>
                  </Group>

                  <Group justify="space-between">
                    <Group gap="xs">
                      <IconUserCircle size={16} />
                      <Text size="sm">{dashboard.owner}</Text>
                    </Group>
                    <Group gap="xs">
                      <ActionIcon
                        variant="subtle"
                        color="blue"
                        onClick={() => handleViewDashboard(dashboard)}
                      >
                        <IconEye size={16} />
                      </ActionIcon>
                      <ActionIcon variant="subtle" color="green">
                        <IconEdit size={16} />
                      </ActionIcon>
                      <ActionIcon variant="subtle" color="orange">
                        <IconExternalLink size={16} />
                      </ActionIcon>
                    </Group>
                  </Group>
                </Card>
              ))}
            </SimpleGrid>

            {/* Reports Section */}
            <Title order={3} mt="xl" mb="lg">Reports</Title>
            <ScrollArea>
              <Table striped highlightOnHover>
                <Table.Thead>
                  <Table.Tr>
                    <Table.Th>Report Name</Table.Th>
                    <Table.Th>Category</Table.Th>
                    <Table.Th>Schedule</Table.Th>
                    <Table.Th>Last Generated</Table.Th>
                    <Table.Th>Format</Table.Th>
                    <Table.Th>Actions</Table.Th>
                  </Table.Tr>
                </Table.Thead>
                <Table.Tbody>
                  {[].map /* TODO: Fetch from API */((report) => (
                    <Table.Tr key={report.id}>
                      <Table.Td>
                        <Text fw={500}>{report.name}</Text>
                      </Table.Td>
                      <Table.Td>{report.category}</Table.Td>
                      <Table.Td>{report.schedule}</Table.Td>
                      <Table.Td>{formatDate(report.lastGenerated)}</Table.Td>
                      <Table.Td>
                        <Group>
                          {report.format === 'PDF' && <IconFileText size={16} />}
                          {report.format === 'CSV' && <IconTable size={16} />}
                          {report.format === 'EXCEL' && <IconClipboardData size={16} />}
                          <Text size="sm">{report.format}</Text>
                        </Group>
                      </Table.Td>
                      <Table.Td>
                        <Group gap="xs">
                          <ActionIcon variant="subtle" color="green">
                            <IconDownload size={16} />
                          </ActionIcon>
                          <ActionIcon variant="subtle" color="blue">
                            <IconEdit size={16} />
                          </ActionIcon>
                          <ActionIcon variant="subtle" color="gray">
                            <IconRefresh size={16} />
                          </ActionIcon>
                        </Group>
                      </Table.Td>
                    </Table.Tr>
                  ))}
                </Table.Tbody>
              </Table>
            </ScrollArea>
          </Paper>
        </Tabs.Panel>

        {/* Analytics & ML Tab */}
        <Tabs.Panel value="analytics">
          <Paper p="md" radius="md" withBorder mt="md">
            <Group justify="space-between" mb="lg">
              <Title order={3}>Predictive Analytics</Title>
              <Button leftSection={<IconBrain size={16} />}>
                Deploy New Model
              </Button>
            </Group>

            {/* Predictive Models */}
            <SimpleGrid cols={{ base: 1, md: 2 }} spacing="lg">
              {[].map /* TODO: Fetch from API */((model) => (
                <Card key={model.id} padding="lg" radius="md" withBorder>
                  <Group justify="space-between" mb="md">
                    <div>
                      <Text fw={600} size="lg">{model.name}</Text>
                      <Text size="sm" c="dimmed">{model.modelType}</Text>
                    </div>
                    <Badge color={getStatusColor(model.status)} variant="light">
                      {model.status.toUpperCase()}
                    </Badge>
                  </Group>

                  <Text size="sm" c="dimmed" lineClamp={2} mb="md">
                    {model.description}
                  </Text>

                  <Group justify="space-between" mb="xs">
                    <Text size="sm" c="dimmed">Accuracy</Text>
                    <Group>
                      <Progress 
                        value={model.accuracy * 100} 
                        size="sm" 
                        style={{ width: 100 }}
                        color={model.accuracy > 0.9 ? 'green' : model.accuracy > 0.75 ? 'blue' : 'orange'}
                      />
                      <Text size="sm">{(model.accuracy * 100).toFixed(1)}%</Text>
                    </Group>
                  </Group>

                  <Group justify="space-between" mb="xs">
                    <Text size="sm" c="dimmed">Last Trained</Text>
                    <Text size="sm">{formatDate(model.lastTrained)}</Text>
                  </Group>

                  <Group justify="space-between" mb="md">
                    <Text size="sm" c="dimmed">Framework</Text>
                    <Group>
                      {model.framework === 'TensorFlow' && <IconBrandPython size={16} />}
                      {model.framework === 'PyTorch' && <IconBrandPython size={16} />}
                      {model.framework === 'scikit-learn' && <IconBrandPython size={16} />}
                      <Text size="sm">{model.framework}</Text>
                    </Group>
                  </Group>

                  <Group justify="space-between">
                    <Button 
                      variant="light" 
                      size="xs"
                      leftSection={<IconWand size={14} />}
                    >
                      Test Predict
                    </Button>
                    <Group gap="xs">
                      <ActionIcon
                        variant="subtle"
                        color="blue"
                        onClick={() => handleViewModel(model)}
                      >
                        <IconEye size={16} />
                      </ActionIcon>
                      <ActionIcon variant="subtle" color="green">
                        <IconEdit size={16} />
                      </ActionIcon>
                      <ActionIcon variant="subtle" color="orange">
                        <IconRefresh size={16} />
                      </ActionIcon>
                    </Group>
                  </Group>
                </Card>
              ))}
            </SimpleGrid>

            {/* ML Model Metrics Chart */}
            <Card padding="lg" radius="md" withBorder mt="xl">
              <Title order={4} mb="md">Model Performance Metrics</Title>
              <SimpleGrid cols={2}>
                <Stack gap="md">
                  {[
                    { model: 'Readmission', accuracy: 0.92, precision: 0.89, recall: 0.87 },
                    { model: 'LOS Prediction', accuracy: 0.85, precision: 0.83, recall: 0.81 },
                    { model: 'Disease Risk', accuracy: 0.88, precision: 0.86, recall: 0.84 },
                    { model: 'Cost Prediction', accuracy: 0.79, precision: 0.76, recall: 0.75 }
                  ].map((item) => (
                    <div key={item.model}>
                      <Text size="sm" fw={500} mb="xs">{item.model}</Text>
                      <Stack gap={4}>
                        <Group justify="space-between">
                          <Text size="xs" c="dimmed">Accuracy</Text>
                          <Group gap="xs">
                            <Progress value={item.accuracy * 100} color="green" style={{ width: 80 }} size="xs" />
                            <Text size="xs">{(item.accuracy * 100).toFixed(0)}%</Text>
                          </Group>
                        </Group>
                        <Group justify="space-between">
                          <Text size="xs" c="dimmed">Precision</Text>
                          <Group gap="xs">
                            <Progress value={item.precision * 100} color="blue" style={{ width: 80 }} size="xs" />
                            <Text size="xs">{(item.precision * 100).toFixed(0)}%</Text>
                          </Group>
                        </Group>
                        <Group justify="space-between">
                          <Text size="xs" c="dimmed">Recall</Text>
                          <Group gap="xs">
                            <Progress value={item.recall * 100} color="violet" style={{ width: 80 }} size="xs" />
                            <Text size="xs">{(item.recall * 100).toFixed(0)}%</Text>
                          </Group>
                        </Group>
                      </Stack>
                    </div>
                  ))}
                </Stack>
                <Stack gap="md" justify="center">
                  <Alert color="blue" variant="light" title="AI-Driven Insights">
                    <Text size="sm">
                      The readmission prediction model has shown a 15% improvement in accuracy after the latest training with expanded patient history features.
                    </Text>
                  </Alert>
                  <Alert color="green" variant="light" title="Action Recommended">
                    <Text size="sm">
                      Consider deploying the Cost Prediction model to production as it has reached stable performance metrics in the last 3 evaluation cycles.
                    </Text>
                  </Alert>
                </Stack>
              </SimpleGrid>
            </Card>
          </Paper>
        </Tabs.Panel>
      </Tabs>

      {/* API Key Modal */}
      <Modal
        opened={apiKeyOpened}
        onClose={closeApiKey}
        title="API Key Management"
        size="lg"
      >
        <Stack gap="md">
          <Alert color="blue" variant="light">
            <Text size="sm">
              API keys are used to authenticate requests to the API. Keep your API keys secure and never share them publicly.
            </Text>
          </Alert>

          <Title order={4}>Your API Keys</Title>
          <ScrollArea>
            <Table>
              <Table.Thead>
                <Table.Tr>
                  <Table.Th>Name</Table.Th>
                  <Table.Th>Type</Table.Th>
                  <Table.Th>Created</Table.Th>
                  <Table.Th>Last Used</Table.Th>
                  <Table.Th>Actions</Table.Th>
                </Table.Tr>
              </Table.Thead>
              <Table.Tbody>
                {[].map /* TODO: Fetch from API */((key) => (
                  <Table.Tr key={key.id}>
                    <Table.Td>{key.name}</Table.Td>
                    <Table.Td>
                      <Badge color={key.keyType === 'production' ? 'red' : 'blue'}>
                        {key.keyType}
                      </Badge>
                    </Table.Td>
                    <Table.Td>{formatDate(key.created)}</Table.Td>
                    <Table.Td>{formatDate(key.lastUsed)}</Table.Td>
                    <Table.Td>
                      <Group gap="xs">
                        <Tooltip label="Revoke Key">
                          <ActionIcon color="red" variant="light">
                            <IconX size={16} />
                          </ActionIcon>
                        </Tooltip>
                        <Tooltip label="View Usage">
                          <ActionIcon color="blue" variant="light">
                            <IconChartBar size={16} />
                          </ActionIcon>
                        </Tooltip>
                      </Group>
                    </Table.Td>
                  </Table.Tr>
                ))}
              </Table.Tbody>
            </Table>
          </ScrollArea>

          <Divider />

          <Title order={4}>Generate New API Key</Title>
          <SimpleGrid cols={2}>
            <TextInput
              label="Key Name"
              placeholder="My Application Key"
              required
            />
            <Select
              label="Key Type"
              placeholder="Select type"
              data={[
                { value: 'development', label: 'Development' },
                { value: 'production', label: 'Production' }
              ]}
              required
            />
          </SimpleGrid>

          <Select
            label="Permissions"
            placeholder="Select permissions"
            data={[
              { value: 'read', label: 'Read Only' },
              { value: 'write', label: 'Read & Write' },
              { value: 'admin', label: 'Admin (Full Access)' }
            ]}
            required
          />

          <Group justify="flex-end">
            <Button variant="light" onClick={closeApiKey}>
              Cancel
            </Button>
            <Button onClick={() => {
              notifications.show({
                title: 'API Key Generated',
                message: 'Your new API key has been created. Make sure to copy it now as it won\'t be shown again',
                color: 'green',
              });
              // Simulate API key generation (in real app, would come from server)
              const simulatedKey = 'hcm_' + Math.random().toString(36).substring(2, 15);
              // Would show this key in a separate modal or in UI
            }}>
              Generate Key
            </Button>
          </Group>
        </Stack>
      </Modal>

      {/* Create Integration Modal */}
      <Modal
        opened={createIntegrationOpened}
        onClose={closeCreateIntegration}
        title="Create New Integration"
        size="lg"
      >
        <Stack gap="md">
          <TextInput
            label="Integration Name"
            placeholder="Enter integration name"
            required
          />
          
          <Select
            label="Integration Type"
            placeholder="Select type"
            data={[
              { value: 'ehr', label: 'EHR System' },
              { value: 'payment', label: 'Payment Gateway' },
              { value: 'analytics', label: 'Analytics' },
              { value: 'lab', label: 'Laboratory' },
              { value: 'pharmacy', label: 'Pharmacy' },
              { value: 'imaging', label: 'Imaging' },
            ]}
            required
          />
          
          <Textarea
            label="Description"
            placeholder="Describe this integration"
            rows={3}
          />
          
          <SimpleGrid cols={2}>
            <Select
              label="Data Direction"
              placeholder="Select direction"
              data={[
                { value: 'inbound', label: 'Inbound' },
                { value: 'outbound', label: 'Outbound' },
                { value: 'bidirectional', label: 'Bidirectional' }
              ]}
            />
            
            <Select
              label="Authentication Method"
              placeholder="Select method"
              data={[
                { value: 'api_key', label: 'API Key' },
                { value: 'oauth2', label: 'OAuth 2.0' },
                { value: 'jwt', label: 'JWT' },
                { value: 'basic', label: 'Basic Auth' }
              ]}
            />
          </SimpleGrid>
          
          <TextInput
            label="API Endpoint URL"
            placeholder="https://api.example.com/v1/"
          />
          
          <PasswordInput
            label="API Secret/Key"
            placeholder="Enter API key or leave blank to configure later"
          />
          
          <Switch
            label="Enable Webhook Notifications"
            description="Receive real-time notifications when data changes"
          />
          
          <Group justify="flex-end">
            <Button variant="light" onClick={closeCreateIntegration}>
              Cancel
            </Button>
            <Button onClick={() => {
              notifications.show({
                title: 'Integration Created',
                message: 'Your new integration has been successfully created',
                color: 'green',
              });
              closeCreateIntegration();
            }}>
              Create Integration
            </Button>
          </Group>
        </Stack>
      </Modal>
    </Container>
  );
};

export default IntegrationHub;

'use client';

import React, { useState, useMemo, useEffect } from 'react';
import {
  Container,
  Paper,
  Title,
  Group,
  Button,
  TextInput,
  Select,
  Badge,
  Table,
  Modal,
  Text,
  Tabs,
  Card,
  Avatar,
  ActionIcon,
  Menu,
  Stack,
  Divider,
  SimpleGrid,
  ScrollArea,
  ThemeIcon,
  Alert,
  Progress,
  NumberInput,
  Textarea,
  Checkbox,
  Image,
  Indicator,
  RingProgress,
  Box
} from '@mantine/core';
import { DatePickerInput } from '@mantine/dates';
import { useDisclosure } from '@mantine/hooks';
import EmptyState from '../../../components/EmptyState';
import { notifications } from '@mantine/notifications';
import { MantineDonutChart, SimpleAreaChart, SimpleBarChart, SimpleLineChart } from '../../../components/MantineChart';
import {
  IconPlus,
  IconSearch,
  IconEdit,
  IconEye,
  IconTrash,
  IconCalendar,
  IconPackage,
  IconChartBar,
  IconPhone,
  IconMail,
  IconAlertCircle,
  IconCheck,
  IconX,
  IconDotsVertical,
  IconTruck,
  IconClipboardList,
  IconFileText,
  IconDownload,
  IconPrinter,
  IconShare,
  IconFlask,
  IconStethoscope,
  IconExclamationMark,
  IconClockHour4,
  IconTrendingUp,
  IconTrendingDown,
  IconUsers,
  IconCalculator,
  IconWallet,
  IconSettings,
  IconBuildingWarehouse,
  IconShoppingCart,
  IconRefresh,
  IconFilter,
  IconBarcode,
  IconTemperature,
  IconShieldCheck,
  IconAlertTriangle,
  IconCircleCheck,
  IconClipboard
} from '@tabler/icons-react';

// Import types, services and mock data
import {
  InventoryItem,
  ItemCategory,
  StockTransaction,
  TransactionType,
  PurchaseOrder,
  OrderStatus,
  Supplier,
  Requisition,
  Equipment,
  EquipmentStatus,
  MaintenanceRecord,
  MaintenanceType,
  InventoryAlert,
  AlertType,
  InventoryStats
} from '../../../types/inventory';
import inventoryService from '../../../services/inventory.service';
// Mock data imports removed

const InventoryManagement = () => {
  // State management
  const [activeTab, setActiveTab] = useState<string>('items');
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedCategory, setSelectedCategory] = useState<string>('');
  const [selectedStatus, setSelectedStatus] = useState<string>('');
  const [selectedSupplier, setSelectedSupplier] = useState<string>('');
  const [selectedItem, setSelectedItem] = useState<InventoryItem | null>(null);
  const [selectedOrder, setSelectedOrder] = useState<PurchaseOrder | null>(null);
  const [selectedEquipment, setSelectedEquipment] = useState<Equipment | null>(null);

  // API data state
  const [inventoryItems, setInventoryItems] = useState<InventoryItem[]>([]);
  const [inventoryStats, setInventoryStats] = useState<any>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  // Modal states
  const [itemDetailOpened, { open: openItemDetail, close: closeItemDetail }] = useDisclosure(false);
  const [addItemOpened, { open: openAddItem, close: closeAddItem }] = useDisclosure(false);
  const [orderDetailOpened, { open: openOrderDetail, close: closeOrderDetail }] = useDisclosure(false);
  const [addOrderOpened, { open: openAddOrder, close: closeAddOrder }] = useDisclosure(false);
  const [equipmentDetailOpened, { open: openEquipmentDetail, close: closeEquipmentDetail }] = useDisclosure(false);

  useEffect(() => {
    fetchAllData();
  }, []);

  const fetchAllData = async () => {
    try {
      setLoading(true);
      setError(null);
      await Promise.all([
        fetchInventoryItems(),
        fetchInventoryStats()
      ]);
    } catch (err: any) {
      console.error('Error loading inventory data:', err);
      setError(err.response?.data?.message || err.message || 'Failed to load inventory data');
      // Fallback to mock data
      setInventoryItems([] /* TODO: Fetch from API */);
      setInventoryStats([] /* TODO: Fetch from API */);
    } finally {
      setLoading(false);
    }
  };

  const fetchInventoryItems = async () => {
    try {
      const filters = {
        category: selectedCategory || undefined,
        supplier: selectedSupplier || undefined,
        search: searchQuery || undefined
      };
      const response = await inventoryService.getItems(filters);
      const itemsData = Array.isArray(response.data) ? response.data : (response.data?.items || []);
      setInventoryItems(itemsData as InventoryItem[]);
    } catch (err: any) {
      console.warn('Error fetching inventory items (using empty data):', err.response?.data?.message || err.message);
      setInventoryItems([]);
    }
  };

  const fetchInventoryStats = async () => {
    try {
      const response = await inventoryService.getStats();
      setInventoryStats(response.data);
    } catch (err: any) {
      console.warn('Error fetching inventory stats (using default values):', err.response?.data?.message || err.message);
      setInventoryStats({
        totalItems: 0,
        lowStockItems: 0,
        outOfStockItems: 0,
        expiringSoon: 0,
        totalValue: 0,
        categoriesCount: 0
      });
    }
  };

  // Refetch when filters change
  useEffect(() => {
    if (!loading) {
      fetchInventoryItems();
    }
  }, [searchQuery, selectedCategory, selectedSupplier]);

  // Filter inventory items
  const filteredItems = useMemo(() => {
    return inventoryItems.filter((item) => {
      const matchesSearch = 
        ((item as any).itemName || (item as any).name || '').toLowerCase().includes(searchQuery.toLowerCase()) ||
        (item.itemCode || '').toLowerCase().includes(searchQuery.toLowerCase()) ||
        (item.description || '').toLowerCase().includes(searchQuery.toLowerCase());
      
      const matchesCategory = !selectedCategory || (item as any).category === selectedCategory;
      const matchesStatus = !selectedStatus || (item as any).status === selectedStatus;

      return matchesSearch && matchesCategory && matchesStatus;
    });
  }, [searchQuery, selectedCategory, selectedStatus, selectedSupplier]);

  // Filter purchase orders
  const filteredOrders = useMemo(() => {
    return [].filter /* TODO: Fetch from API */((order) => {
      const matchesSearch = 
        ((order as any).orderNumber || order.id || '').toLowerCase().includes(searchQuery.toLowerCase()) ||
        ((order.supplier as any)?.companyName || (order.supplier as any)?.name || '').toLowerCase().includes(searchQuery.toLowerCase());
      
      const matchesStatus = !selectedStatus || order.status === selectedStatus;

      return matchesSearch && matchesStatus;
    });
  }, [searchQuery, selectedStatus]);

  // Filter equipment
  const filteredEquipment = useMemo(() => {
    return [].filter /* TODO: Fetch from API */((equipment) => {
      const matchesSearch = 
        ((equipment as any).equipmentName || equipment.name || '').toLowerCase().includes(searchQuery.toLowerCase()) ||
        ((equipment as any).model || '').toLowerCase().includes(searchQuery.toLowerCase()) ||
        ((equipment as any).serialNumber || '').toLowerCase().includes(searchQuery.toLowerCase());
      
      const matchesStatus = !selectedStatus || equipment.status === selectedStatus;

      return matchesSearch && matchesStatus;
    });
  }, [searchQuery, selectedStatus]);

  // Helper functions
  const getStatusColor = (status: string) => {
    switch (status) {
      case 'in_stock':
      case 'delivered':
      case 'operational':
      case 'available': return 'green';
      case 'low_stock':
      case 'pending':
      case 'maintenance':
      case 'in_use': return 'orange';
      case 'out_of_stock':
      case 'cancelled':
      case 'out_of_service':
      case 'retired': return 'red';
      case 'expired':
      case 'ordered': return 'yellow';
      case 'damaged': return 'dark';
      default: return 'gray';
    }
  };

  const getCategoryColor = (category: string) => {
    switch (category) {
      case 'medication': return 'blue';
      case 'medical_supplies': return 'green';
      case 'surgical_instruments': return 'purple';
      case 'laboratory': return 'orange';
      case 'radiology': return 'cyan';
      case 'consumables': return 'yellow';
      case 'equipment': return 'red';
      case 'medicines': return 'blue';
      case 'supplies': return 'green';
      default: return 'gray';
    }
  };

  const getAlertColor = (type: string) => {
    switch (type) {
      case 'low_stock': return 'orange';
      case 'expired': return 'red';
      case 'expiring_soon': return 'yellow';
      case 'out_of_stock': return 'red';
      case 'maintenance_due': return 'blue';
      case 'overdue_order': return 'red';
      case 'reorder': return 'orange';
      default: return 'gray';
    }
  };

  const handleViewItem = (item: InventoryItem) => {
    setSelectedItem(item);
    openItemDetail();
  };

  const handleViewOrder = (order: PurchaseOrder) => {
    setSelectedOrder(order);
    openOrderDetail();
  };

  const handleViewEquipment = (equipment: Equipment) => {
    setSelectedEquipment(equipment);
    openEquipmentDetail();
  };

  const clearFilters = () => {
    setSearchQuery('');
    setSelectedCategory('');
    setSelectedStatus('');
    setSelectedSupplier('');
  };

  const formatCurrency = (amount: number) => {
    return new Intl.NumberFormat('en-IN', {
      style: 'currency',
      currency: 'INR'
    }).format(amount);
  };

  const getStockLevel = (current: number, minimum: number, maximum: number) => {
    if (current === 0) return { level: 'Out of Stock', color: 'red', percentage: 0 };
    if (current <= minimum) return { level: 'Low Stock', color: 'orange', percentage: (current / maximum) * 100 };
    if (current >= maximum * 0.8) return { level: 'Well Stocked', color: 'green', percentage: (current / maximum) * 100 };
    return { level: 'Normal', color: 'blue', percentage: (current / maximum) * 100 };
  };

  // Statistics cards
  const statsCards = [
    {
      title: 'Total Items',
      value: 0 /* TODO: Fetch from API */,
      icon: IconPackage,
      color: 'blue',
      trend: '+5.2%'
    },
    {
      title: 'Low Stock Alerts',
      value: 0 /* TODO: Fetch from API */,
      icon: IconAlertCircle,
      color: 'orange',
      trend: '-12%'
    },
    {
      title: 'Total Value',
      value: formatCurrency(0 /* TODO: Fetch from API */),
      icon: IconCalculator,
      color: 'green',
      trend: '+8.7%'
    },
    {
      title: 'Equipment',
      value: 0 /* TODO: Fetch from API */,
      icon: IconStethoscope,
      color: 'purple',
      trend: '+3.1%'
    }
  ];

  // Chart data
  const categoryDistribution = [];

  const stockLevelsData = [];
  const monthlyConsumption = [];

  return (
    <Container size="xl" py="md">
      {/* Header */}
      <Group justify="space-between" mb="lg">
        <div>
          <Title order={1}>Inventory Management</Title>
          <Text c="dimmed" size="sm">
            Manage medical supplies, equipment, and procurement
          </Text>
        </div>
        <Group>
          <Button
            leftSection={<IconPlus size={16} />}
            onClick={openAddItem}
          >
            Add Item
          </Button>
          <Button
            variant="light"
            leftSection={<IconShoppingCart size={16} />}
            onClick={openAddOrder}
          >
            New Order
          </Button>
        </Group>
      </Group>

      {/* Statistics Cards */}
      <SimpleGrid cols={{ base: 1, sm: 2, md: 4 }} mb="lg">
        {statsCards.map((stat) => {
          const Icon = stat.icon;
          return (
            <Card key={stat.title} padding="lg" radius="md" withBorder>
              <Group justify="space-between">
                <div>
                  <Text c="dimmed" size="sm" fw={500}>
                    {stat.title}
                  </Text>
                  <Text fw={700} size="xl">
                    {stat.value}
                  </Text>
                </div>
                <ThemeIcon color={stat.color} size="xl" radius="md" variant="light">
                  <Icon size={24} />
                </ThemeIcon>
              </Group>
              <Group justify="space-between" mt="sm">
                <Badge 
                  color={stat.trend.startsWith('+') ? 'green' : 'red'} 
                  variant="light"
                  size="sm"
                >
                  {stat.trend}
                </Badge>
                <Text size="xs" c="dimmed">vs last month</Text>
              </Group>
            </Card>
          );
        })}
      </SimpleGrid>

      {/* Alerts Section - Hidden until API data available */}

      {/* Main Content Tabs */}
      <Tabs value={activeTab} onChange={setActiveTab}>
        <Tabs.List>
          <Tabs.Tab value="items" leftSection={<IconPackage size={16} />}>
            Inventory Items
          </Tabs.Tab>
          <Tabs.Tab value="orders" leftSection={<IconShoppingCart size={16} />}>
            Purchase Orders
          </Tabs.Tab>
          <Tabs.Tab value="equipment" leftSection={<IconStethoscope size={16} />}>
            Equipment
          </Tabs.Tab>
          <Tabs.Tab value="reports" leftSection={<IconChartBar size={16} />}>
            Reports & Analytics
          </Tabs.Tab>
        </Tabs.List>

        {/* Inventory Items Tab */}
        <Tabs.Panel value="items">
          <Paper p="md" radius="md" withBorder mt="md">
            {/* Search and Filters */}
            <Group mb="md">
              <TextInput
                placeholder="Search items..."
                leftSection={<IconSearch size={16} />}
                value={searchQuery}
                onChange={(event) => setSearchQuery(event.currentTarget.value)}
                style={{ flex: 1 }}
              />
              <Select
                placeholder="Category"
                data={[
                  { value: 'medication', label: 'Medication' },
                  { value: 'medical_supplies', label: 'Medical Supplies' },
                  { value: 'surgical_instruments', label: 'Surgical Instruments' },
                  { value: 'laboratory', label: 'Laboratory' },
                  { value: 'radiology', label: 'Radiology' },
                  { value: 'consumables', label: 'Consumables' },
                  { value: 'equipment', label: 'Equipment' }
                ]}
                value={selectedCategory}
                onChange={setSelectedCategory}
                clearable
              />
              <Select
                placeholder="Status"
                data={[
                  { value: 'in_stock', label: 'In Stock' },
                  { value: 'low_stock', label: 'Low Stock' },
                  { value: 'out_of_stock', label: 'Out of Stock' },
                  { value: 'expired', label: 'Expired' }
                ]}
                value={selectedStatus}
                onChange={setSelectedStatus}
                clearable
              />
              <Select
                placeholder="Supplier"
                data={[].map /* TODO: Fetch from API */(supplier => ({ 
                  value: supplier.id, 
                  label: (supplier as any).companyName || (supplier as any).name || 'Unknown' 
                }))}
                value={selectedSupplier}
                onChange={setSelectedSupplier}
                clearable
              />
              <Button variant="light" onClick={clearFilters}>
                Clear Filters
              </Button>
            </Group>

            {/* Items Grid */}
            <SimpleGrid cols={{ base: 1, md: 2, lg: 3 }} spacing="lg">
              {filteredItems.map((item) => {
                const stockInfo = getStockLevel(
                  item.currentStock, 
                  item.minimumStock, 
                  item.maximumStock
                );
                
                return (
                  <Card key={item.id} padding="lg" radius="md" withBorder>
                    <Group justify="space-between" mb="md">
                      <div style={{ flex: 1 }}>
                        <Group>
                          <ThemeIcon 
                            color={getCategoryColor((item as any).category || item.category)} 
                            variant="light"
                            size="lg"
                          >
                            <IconPackage size={20} />
                          </ThemeIcon>
                          <div>
                            <Text fw={600} size="sm" lineClamp={1}>
                              {(item as any).itemName || (item as any).name}
                            </Text>
                            <Text size="xs" c="dimmed">
                              {(item as any).itemCode || 'N/A'}
                            </Text>
                          </div>
                        </Group>
                      </div>
                  <Badge color={getStatusColor((item as any).status)} variant="light" size="sm">
                    {(item as any).status?.replace('_', ' ') || 'N/A'}
                  </Badge>
                </Group>

                <Stack gap="sm" mb="md">
                  <Group justify="space-between">
                    <Text size="sm" c="dimmed">Current Stock</Text>
                    <Text size="sm" fw={600}>
                      {(item as any).currentStock || 0} {(item as any).unit || 'units'}
                    </Text>
                  </Group>
                  
                  <div>
                    <Group justify="space-between" mb="xs">
                      <Text size="xs" c="dimmed">Stock Level</Text>
                      <Text size="xs" c={stockInfo.color} fw={500}>
                        {stockInfo.level}
                      </Text>
                    </Group>
                    <Progress 
                      value={stockInfo.percentage} 
                      color={stockInfo.color} 
                      size="sm" 
                    />
                  </div>

                  <Group justify="space-between">
                    <Text size="sm" c="dimmed">Unit Price</Text>
                    <Text size="sm" fw={600}>
                      {formatCurrency((item as any).unitPrice || item.unitCost || 0)}
                    </Text>
                  </Group>

                  <Group justify="space-between">
                    <Text size="sm" c="dimmed">Total Value</Text>
                    <Text size="sm" fw={600} c="green">
                      {formatCurrency((item as any).currentStock * ((item as any).unitPrice || item.unitCost || 0))}
                    </Text>
                  </Group>

                  {(item as any).expiryDate && (
                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Expiry Date</Text>
                      <Text 
                        size="sm" 
                        c={new Date((item as any).expiryDate) < new Date() ? 'red' : 'dimmed'}
                      >
                        {new Date((item as any).expiryDate).toLocaleDateString()}
                      </Text>
                    </Group>
                  )}
                </Stack>

                <Group justify="space-between">
                  <Badge color={getCategoryColor((item as any).category || item.category)} variant="light" size="xs">
                    {(item as any).category?.replace('_', ' ') || 'N/A'}
                  </Badge>
                  <Group gap="xs">
                    <ActionIcon
                      variant="subtle"
                      color="blue"
                      onClick={() => handleViewItem(item as any)}
                    >
                      <IconEye size={16} />
                    </ActionIcon>
                    <ActionIcon variant="subtle" color="green">
                      <IconEdit size={16} />
                    </ActionIcon>
                    <Menu>
                      <Menu.Target>
                        <ActionIcon variant="subtle" color="gray">
                          <IconDotsVertical size={16} />
                        </ActionIcon>
                      </Menu.Target>
                      <Menu.Dropdown>
                        <Menu.Item leftSection={<IconClipboard size={14} />}>
                          Stock Adjustment
                        </Menu.Item>
                        <Menu.Item leftSection={<IconShoppingCart size={14} />}>
                          Reorder
                        </Menu.Item>
                        <Menu.Item leftSection={<IconDownload size={14} />}>
                          Export Data
                        </Menu.Item>
                        <Menu.Divider />
                        <Menu.Item 
                          leftSection={<IconTrash size={14} />}
                          color="red"
                        >
                          Delete
                        </Menu.Item>
                      </Menu.Dropdown>
                    </Menu>
                  </Group>
                    </Group>
                  </Card>
                );
              })}
            </SimpleGrid>
          </Paper>
        </Tabs.Panel>

        {/* Purchase Orders Tab */}
        <Tabs.Panel value="orders">
          <Paper p="md" radius="md" withBorder mt="md">
            <Group justify="space-between" mb="lg">
              <Title order={3}>Purchase Orders</Title>
              <Button leftSection={<IconPlus size={16} />} onClick={openAddOrder}>
                Create Order
              </Button>
            </Group>

            {/* Order Filters */}
            <Group mb="md">
              <TextInput
                placeholder="Search orders..."
                leftSection={<IconSearch size={16} />}
                value={searchQuery}
                onChange={(event) => setSearchQuery(event.currentTarget.value)}
                style={{ flex: 1 }}
              />
              <Select
                placeholder="Status"
                data={[
                  { value: 'pending', label: 'Pending' },
                  { value: 'approved', label: 'Approved' },
                  { value: 'ordered', label: 'Ordered' },
                  { value: 'delivered', label: 'Delivered' },
                  { value: 'cancelled', label: 'Cancelled' }
                ]}
                value={selectedStatus}
                onChange={setSelectedStatus}
                clearable
              />
            </Group>

            {/* Orders Table */}
            <ScrollArea>
              <Table striped highlightOnHover>
                <Table.Thead>
                  <Table.Tr>
                    <Table.Th>Order #</Table.Th>
                    <Table.Th>Supplier</Table.Th>
                    <Table.Th>Order Date</Table.Th>
                    <Table.Th>Expected Delivery</Table.Th>
                    <Table.Th>Items</Table.Th>
                    <Table.Th>Total Amount</Table.Th>
                    <Table.Th>Status</Table.Th>
                    <Table.Th>Actions</Table.Th>
                  </Table.Tr>
                </Table.Thead>
                <Table.Tbody>
                  {filteredOrders.length === 0 ? (
                    <Table.Tr>
                      <Table.Td colSpan={9}>
                        <EmptyState
                          icon={<IconPackage size={48} />}
                          title="No inventory items"
                          description="Add items to your inventory"
                          size="sm"
                        />
                      </Table.Td>
                    </Table.Tr>
                  ) : (
                    filteredOrders.map((order) => (
                    <Table.Tr key={order.id}>
                      <Table.Td>
                        <Text fw={500}>{(order as any).orderNumber || order.id}</Text>
                      </Table.Td>
                      <Table.Td>
                        <div>
                          <Text size="sm" fw={500}>
                            {(order.supplier as any).companyName || (order.supplier as any).name || 'Unknown'}
                          </Text>
                          <Text size="xs" c="dimmed">
                            {(order.supplier as any).contactPerson || 'N/A'}
                          </Text>
                        </div>
                      </Table.Td>
                      <Table.Td>
                        <Text size="sm">
                          {new Date(order.orderDate).toLocaleDateString()}
                        </Text>
                      </Table.Td>
                      <Table.Td>
                        <Text size="sm">
                          {new Date(order.expectedDeliveryDate).toLocaleDateString()}
                        </Text>
                      </Table.Td>
                      <Table.Td>
                        <Text size="sm">{order.items.length} items</Text>
                      </Table.Td>
                      <Table.Td>
                        <Text fw={600}>{formatCurrency(order.totalAmount)}</Text>
                      </Table.Td>
                      <Table.Td>
                        <Badge color={getStatusColor(order.status)} variant="light">
                          {order.status}
                        </Badge>
                      </Table.Td>
                      <Table.Td>
                        <Group gap="xs">
                          <ActionIcon
                            variant="subtle"
                            color="blue"
                            onClick={() => handleViewOrder(order)}
                          >
                            <IconEye size={16} />
                          </ActionIcon>
                          <ActionIcon variant="subtle" color="green">
                            <IconEdit size={16} />
                          </ActionIcon>
                          <ActionIcon variant="subtle" color="orange">
                            <IconDownload size={16} />
                          </ActionIcon>
                        </Group>
                      </Table.Td>
                    </Table.Tr>
                  )))}
                </Table.Tbody>
              </Table>
            </ScrollArea>
          </Paper>
        </Tabs.Panel>

        {/* Equipment Tab */}
        <Tabs.Panel value="equipment">
          <Paper p="md" radius="md" withBorder mt="md">
            <Group justify="space-between" mb="lg">
              <Title order={3}>Medical Equipment</Title>
              <Button leftSection={<IconPlus size={16} />}>
                Add Equipment
              </Button>
            </Group>

            {/* Equipment Grid */}
            <SimpleGrid cols={{ base: 1, md: 2, lg: 3 }} spacing="lg">
              {filteredEquipment.map((equipment) => (
                <Card key={equipment.id} padding="lg" radius="md" withBorder>
                  <Group justify="space-between" mb="md">
                    <div style={{ flex: 1 }}>
                      <Text fw={600} size="lg" lineClamp={1}>
                        {(equipment as any).equipmentName || equipment.name}
                      </Text>
                      <Text size="sm" c="dimmed">
                        {(equipment as any).manufacturer || 'Unknown'} - {(equipment as any).model || 'N/A'}
                      </Text>
                    </div>
                    <Badge color={getStatusColor(equipment.status)} variant="light">
                      {equipment.status.replace('_', ' ')}
                    </Badge>
                  </Group>

                  <Stack gap="sm" mb="md">
                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Serial Number</Text>
                      <Text size="sm" fw={500}>{(equipment as any).serialNumber || 'N/A'}</Text>
                    </Group>

                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Location</Text>
                      <Text size="sm">{equipment.location}</Text>
                    </Group>

                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Purchase Date</Text>
                      <Text size="sm">
                        {(equipment as any).purchaseDate ? new Date((equipment as any).purchaseDate).toLocaleDateString() : 'N/A'}
                      </Text>
                    </Group>

                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Warranty</Text>
                      <Text 
                        size="sm" 
                        c={(equipment as any).warrantyExpiry && new Date((equipment as any).warrantyExpiry) < new Date() ? 'red' : 'green'}
                      >
                        {(equipment as any).warrantyExpiry ? (new Date((equipment as any).warrantyExpiry) < new Date() ? 'Expired' : 'Valid') : 'N/A'}
                      </Text>
                    </Group>

                    {(equipment as any).nextMaintenanceDate && (
                      <Group justify="space-between">
                        <Text size="sm" c="dimmed">Next Maintenance</Text>
                        <Text 
                          size="sm" 
                          c={new Date((equipment as any).nextMaintenanceDate) < new Date() ? 'red' : 'dimmed'}
                        >
                          {new Date((equipment as any).nextMaintenanceDate).toLocaleDateString()}
                        </Text>
                      </Group>
                    )}
                  </Stack>

                  <Group justify="space-between">
                    <Text size="sm" fw={600} c="blue">
                      {formatCurrency((equipment as any).purchasePrice || 0)}
                    </Text>
                    <Group gap="xs">
                      <ActionIcon
                        variant="subtle"
                        color="blue"
                        onClick={() => handleViewEquipment(equipment as any)}
                      >
                        <IconEye size={16} />
                      </ActionIcon>
                      <ActionIcon variant="subtle" color="green">
                        <IconEdit size={16} />
                      </ActionIcon>
                      <ActionIcon variant="subtle" color="orange">
                        <IconSettings size={16} />
                      </ActionIcon>
                    </Group>
                  </Group>
                </Card>
              ))}
            </SimpleGrid>
          </Paper>
        </Tabs.Panel>

        {/* Reports & Analytics Tab */}
        <Tabs.Panel value="reports">
          <Paper p="md" radius="md" withBorder mt="md">
            <Title order={3} mb="lg">Inventory Reports & Analytics</Title>
            <SimpleGrid cols={{ base: 1, lg: 2 }} spacing="lg">
              {/* Category Distribution */}
              <Card padding="lg" radius="md" withBorder>
                <Title order={4} mb="md">Items by Category</Title>
                <MantineDonutChart
                  data={categoryDistribution}
                  size={160}
                  thickness={30}
                  withLabels
                />
              </Card>
              
              {/* Stock Levels */}
              <Card padding="lg" radius="md" withBorder>
                <Title order={4} mb="md">Stock Level Distribution</Title>
                <SimpleBarChart
                  h={200}
                  data={stockLevelsData}
                  dataKey="level"
                  series={[{ name: 'count', color: 'blue.6' }]}
                />
              </Card>
              
              {/* Monthly Consumption */}
              <Card padding="lg" radius="md" withBorder style={{ gridColumn: '1 / -1' }}>
                <Title order={4} mb="md">Monthly Consumption Trends</Title>
                <SimpleLineChart
                  h={300}
                  data={monthlyConsumption}
                  dataKey="month"
                  series={[
                    { name: 'consumed', color: 'blue.6', label: 'Consumed' },
                    { name: 'restocked', color: 'green.6', label: 'Restocked' }
                  ]}
                />
              </Card>
            </SimpleGrid>
          </Paper>
        </Tabs.Panel>
      </Tabs>

      {/* Add Item Modal */}
      <Modal
        opened={addItemOpened}
        onClose={closeAddItem}
        title="Add New Item"
        size="lg"
      >
        <Stack gap="md">
          <SimpleGrid cols={2}>
            <TextInput
              label="Item Name"
              placeholder="Enter item name"
              required
            />
            <TextInput
              label="Item Code"
              placeholder="Enter item code"
              required
            />
          </SimpleGrid>
          
          <Select
            label="Category"
            placeholder="Select category"
            data={[
              { value: 'medication', label: 'Medication' },
              { value: 'medical_supplies', label: 'Medical Supplies' },
              { value: 'surgical_instruments', label: 'Surgical Instruments' },
              { value: 'laboratory', label: 'Laboratory' },
              { value: 'consumables', label: 'Consumables' }
            ]}
            required
          />
          
          <Textarea
            label="Description"
            placeholder="Enter item description"
            rows={3}
          />
          
          <SimpleGrid cols={3}>
            <NumberInput
              label="Unit Price"
              placeholder="Enter price"
              leftSection="‚Çπ"
              min={0}
              required
            />
            <NumberInput
              label="Minimum Stock"
              placeholder="Min stock level"
              min={0}
              required
            />
            <NumberInput
              label="Maximum Stock"
              placeholder="Max stock level"
              min={0}
              required
            />
          </SimpleGrid>
          
          <SimpleGrid cols={2}>
            <TextInput
              label="Unit"
              placeholder="e.g., pcs, mg, ml"
              required
            />
            <Select
              label="Supplier"
              placeholder="Select supplier"
              data={[].map /* TODO: Fetch from API */(supplier => ({ 
                value: supplier.id, 
                label: (supplier as any).companyName || (supplier as any).name || 'Unknown' 
              }))}
            />
          </SimpleGrid>
          
          <DatePickerInput
            label="Expiry Date (Optional)"
            placeholder="Select expiry date"
          />
          
          <Group justify="flex-end">
            <Button variant="light" onClick={closeAddItem}>
              Cancel
            </Button>
            <Button onClick={() => {
              notifications.show({
                title: 'Success',
                message: 'Item added successfully',
                color: 'green',
              });
              closeAddItem();
            }}>
              Add Item
            </Button>
          </Group>
        </Stack>
      </Modal>

      {/* Add Order Modal */}
      <Modal
        opened={addOrderOpened}
        onClose={closeAddOrder}
        title="Create Purchase Order"
        size="lg"
      >
        <Stack gap="md">
          <Select
            label="Supplier"
            placeholder="Select supplier"
            data={[].map /* TODO: Fetch from API */(supplier => ({ 
              value: supplier.id, 
              label: (supplier as any).companyName || (supplier as any).name || 'Unknown' 
            }))}
            required
          />
          
          <DatePickerInput
            label="Expected Delivery Date"
            placeholder="Select delivery date"
            required
          />
          
          <Divider label="Order Items" labelPosition="center" />
          
          <SimpleGrid cols={3}>
            <Select
              label="Item"
              placeholder="Select item"
              data={[].map /* TODO: Fetch from API */(item => ({ 
                value: item.id, 
                label: (item as any).itemName || item.name 
              }))}
              required
            />
            <NumberInput
              label="Quantity"
              placeholder="Enter quantity"
              min={1}
              required
            />
            <NumberInput
              label="Unit Price"
              placeholder="Enter price"
              leftSection="‚Çπ"
              min={0}
              required
            />
          </SimpleGrid>
          
          <Button variant="light" leftSection={<IconPlus size={16} />}>
            Add More Items
          </Button>
          
          <Textarea
            label="Notes"
            placeholder="Additional notes..."
            rows={3}
          />
          
          <Group justify="flex-end">
            <Button variant="light" onClick={closeAddOrder}>
              Cancel
            </Button>
            <Button onClick={() => {
              notifications.show({
                title: 'Success',
                message: 'Purchase order created successfully',
                color: 'green',
              });
              closeAddOrder();
            }}>
              Create Order
            </Button>
          </Group>
        </Stack>
      </Modal>
    </Container>
  );
};

export default InventoryManagement;

'use client';

import React, { useState, useMemo, useEffect } from 'react';
import {
  Container,
  Paper,
  Title,
  Group,
  Button,
  TextInput,
  Select,
  Badge,
  Table,
  Modal,
  Text,
  Tabs,
  Card,
  Avatar,
  ActionIcon,
  SimpleGrid,
  ScrollArea,
  ThemeIcon,
  Progress,
  NumberInput,
  Textarea,
  Switch,
  Divider,
  Alert,
  Timeline,
  List,
  Indicator,
  Stack
} from '@mantine/core';
import { useDisclosure } from '@mantine/hooks';
import EmptyState from '../../../components/EmptyState';
import { notifications } from '@mantine/notifications';
import { DatePickerInput } from '@mantine/dates';
import ipdService from '../../../services/ipd.service';
// import { LineChart, BarChart, DonutChart, AreaChart } from '@mantine/charts';
import {
  IconActivity,
  IconAlertCircle,
  IconArrowDown,
  IconArrowUp,
  IconBed,
  IconBedFilled,
  IconCalendar,
  IconCalendarEvent,
  IconCash,
  IconChartBar,
  IconCheck,
  IconClipboard,
  IconClock,
  IconCreditCard,
  IconDownload,
  IconDroplet,
  IconEdit,
  IconEmergencyBed,
  IconEye,
  IconFileText,
  IconHeart,
  IconHome,
  IconLungs,
  IconMail,
  IconMapPin,
  IconMedicalCross,
  IconNurse,
  IconPhone,
  IconPill,
  IconPlus,
  IconPrinter,
  IconReceipt,
  IconRefresh,
  IconReport,
  IconSearch,
  IconStethoscope,
  IconThermometer,
  IconTransfer,
  IconTrash,
  IconTrendingDown,
  IconTrendingUp,
  IconUser,
  IconUserCheck,
  IconUsers,
  IconX,
  IconBedOff,
  IconAlertTriangle,
  IconCurrencyRupee,
  IconBuilding
} from '@tabler/icons-react';

// Types
interface IPDPatient {
  id: string;
  admissionNumber: string;
  patientId: string;
  patientName: string;
  patientAge: number;
  patientGender: string;
  patientPhone: string;
  bedNumber: string;
  wardName: string;
  roomNumber: string;
  admissionDate: string;
  expectedDischargeDate?: string;
  actualDischargeDate?: string;
  admissionType: 'emergency' | 'elective' | 'transfer';
  status: 'admitted' | 'critical' | 'stable' | 'discharged' | 'transferred';
  primaryDoctor: string;
  consultingDoctors: string[];
  assignedNurse: string;
  diagnosis: string;
  procedure?: string;
  insurance: {
    provider: string;
    policyNumber: string;
    approvalAmount: number;
  } | null;
  lengthOfStay: number;
  dailyCharges: number;
  totalCharges: number;
  pendingAmount: number;
  vitalSigns: {
    bloodPressure: string;
    heartRate: number;
    temperature: number;
    respiratoryRate: number;
    oxygenSaturation: number;
    painScale: number;
    lastUpdated: string;
  };
  medications: Array<{
    name: string;
    dosage: string;
    frequency: string;
    route: string;
    startDate: string;
    endDate?: string;
  }>;
  nursingNotes: Array<{
    timestamp: string;
    note: string;
    nurseName: string;
  }>;
}

interface Bed {
  id: string;
  bedNumber: string;
  wardName: string;
  roomNumber: string;
  bedType: 'general' | 'private' | 'icu' | 'hdu' | 'isolation';
  status: 'occupied' | 'vacant' | 'maintenance' | 'reserved';
  patientId?: string;
  patientName?: string;
  dailyRate: number;
  amenities: string[];
  lastCleaned?: string;
}

interface Ward {
  id: string;
  name: string;
  department: string;
  totalBeds: number;
  occupiedBeds: number;
  availableBeds: number;
  maintenanceBeds: number;
  nursesOnDuty: number;
  headNurse: string;
}

// Mock data removed - using API data only
const mockIPDPatients: IPDPatient[] = [];
const mockBeds: Bed[] = [];
const mockWards: Ward[] = [];

const IPDManagement = () => {
  // State management
  const [activeTab, setActiveTab] = useState<string>('patients');
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedWard, setSelectedWard] = useState<string>('');
  const [selectedStatus, setSelectedStatus] = useState<string>('');
  const [selectedPatient, setSelectedPatient] = useState<IPDPatient | null>(null);
  const [selectedBed, setSelectedBed] = useState<Bed | null>(null);

  // API state
  const [admissions, setAdmissions] = useState<any[]>([]);
  const [ipdStatsAPI, setIpdStatsAPI] = useState<any>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  // Fetch data
  useEffect(() => {
    fetchAllData();
  }, []);

  const fetchAllData = async () => {
    try {
      setLoading(true);
      setError(null);
      await Promise.all([fetchAdmissions(), fetchStats()]);
    } catch (err: any) {
      console.error('Error loading IPD data:', err);
      setError(err.response?.data?.message || err.message || 'Failed to load IPD data');
    } finally {
      setLoading(false);
    }
  };

  const fetchAdmissions = async () => {
    try {
      // IPD service doesn't have getAdmissions - using empty data
      console.warn('IPD admissions API not implemented - showing empty state');
      setAdmissions([]);
    } catch (err: any) {
      console.warn('Error fetching admissions:', err.response?.data?.message || err.message);
      setAdmissions([]);
    }
  };

  const fetchStats = async () => {
    try {
      const stats = await ipdService.getStats();
      setIpdStatsAPI(stats);
    } catch (err: any) {
      console.warn('Error fetching IPD stats:', err.response?.data?.message || err.message);
      setIpdStatsAPI(null);
    }
  };

  // Modal handlers
  const handleViewPatient = (patient: IPDPatient) => {
    setSelectedPatient(patient);
  };

  const handleViewBed = (bed: Bed) => {
    setSelectedBed(bed);
  };

  // Filter patients
  const filteredPatients = useMemo(() => {
    const patientsList = admissions.length > 0 ? admissions : [];
    return patientsList.filter((patient: any) => {
      const matchesSearch = 
        patient.patientName.toLowerCase().includes(searchQuery.toLowerCase()) ||
        patient.admissionNumber.toLowerCase().includes(searchQuery.toLowerCase()) ||
        patient.bedNumber.toLowerCase().includes(searchQuery.toLowerCase());
      
      const matchesWard = !selectedWard || patient.wardName === selectedWard;
      const matchesStatus = !selectedStatus || patient.status === selectedStatus;

      return matchesSearch && matchesWard && matchesStatus;
    });
  }, [admissions, searchQuery, selectedWard, selectedStatus]);

  // Quick stats
  const ipdStats = {
    totalPatients: ipdStatsAPI?.totalAdmissions || 0,
    totalBeds: ipdStatsAPI?.totalBeds || 0,
    occupiedBeds: ipdStatsAPI?.occupiedBeds || 0,
    availableBeds: ipdStatsAPI?.availableBeds || 0,
    criticalPatients: ipdStatsAPI?.criticalPatients || 0,
    averageLOS: ipdStatsAPI?.averageLengthOfStay || 0,
    occupancyRate: ipdStatsAPI?.occupancyRate || 0,
    totalRevenue: ipdStatsAPI?.totalRevenue || 0
  };

  return (
    <Container size="xl" py="md">
      {/* Header */}
      <Group justify="space-between" mb="lg">
        <div>
          <Title order={2}>IPD Management</Title>
          <Text size="sm" c="dimmed">Inpatient department care and bed management system</Text>
        </div>
        <Group>
          <Button
            leftSection={<IconRefresh size={16} />}
            variant="light"
            onClick={fetchAllData}
            loading={loading}
          >
            Refresh Status
          </Button>
          <Button
            leftSection={<IconPlus size={16} />}
            onClick={() => {/* TODO: Open admission modal */}}
          >
            New Admission
          </Button>
        </Group>
      </Group>

      {/* Quick Stats */}
      <SimpleGrid cols={{ base: 1, sm: 2, md: 4, lg: 8 }} spacing="lg" mb="xl">
        <Card padding="md" radius="md" withBorder>
          <Group justify="apart">
            <div>
              <Text size="xs" c="dimmed" tt="uppercase" fw={700}>Total Patients</Text>
              <Text size="xl" fw={700}>{ipdStats.totalPatients}</Text>
            </div>
            <ThemeIcon size="lg" radius="md" variant="light" color="blue">
              <IconUsers size={20} />
            </ThemeIcon>
          </Group>
        </Card>

        <Card padding="md" radius="md" withBorder>
          <Group justify="apart">
            <div>
              <Text size="xs" c="dimmed" tt="uppercase" fw={700}>Total Beds</Text>
              <Text size="xl" fw={700}>{ipdStats.totalBeds}</Text>
            </div>
            <ThemeIcon size="lg" radius="md" variant="light" color="green">
              <IconBed size={20} />
            </ThemeIcon>
          </Group>
        </Card>

        <Card padding="md" radius="md" withBorder>
          <Group justify="apart">
            <div>
              <Text size="xs" c="dimmed" tt="uppercase" fw={700}>Occupied</Text>
              <Text size="xl" fw={700}>{ipdStats.occupiedBeds}</Text>
            </div>
            <ThemeIcon size="lg" radius="md" variant="light" color="red">
              <IconBedOff size={20} />
            </ThemeIcon>
          </Group>
        </Card>

        <Card padding="md" radius="md" withBorder>
          <Group justify="apart">
            <div>
              <Text size="xs" c="dimmed" tt="uppercase" fw={700}>Available</Text>
              <Text size="xl" fw={700}>{ipdStats.availableBeds}</Text>
            </div>
            <ThemeIcon size="lg" radius="md" variant="light" color="teal">
              <IconCheck size={20} />
            </ThemeIcon>
          </Group>
        </Card>

        <Card padding="md" radius="md" withBorder>
          <Group justify="apart">
            <div>
              <Text size="xs" c="dimmed" tt="uppercase" fw={700}>Critical</Text>
              <Text size="xl" fw={700}>{ipdStats.criticalPatients}</Text>
            </div>
            <ThemeIcon size="lg" radius="md" variant="light" color="orange">
              <IconAlertTriangle size={20} />
            </ThemeIcon>
          </Group>
        </Card>

        <Card padding="md" radius="md" withBorder>
          <Group justify="apart">
            <div>
              <Text size="xs" c="dimmed" tt="uppercase" fw={700}>Avg LOS</Text>
              <Text size="xl" fw={700}>{ipdStats.averageLOS}</Text>
            </div>
            <ThemeIcon size="lg" radius="md" variant="light" color="violet">
              <IconCalendar size={20} />
            </ThemeIcon>
          </Group>
        </Card>

        <Card padding="md" radius="md" withBorder>
          <Group justify="apart">
            <div>
              <Text size="xs" c="dimmed" tt="uppercase" fw={700}>Occupancy</Text>
              <Text size="xl" fw={700}>{ipdStats.occupancyRate}%</Text>
            </div>
            <ThemeIcon size="lg" radius="md" variant="light" color="indigo">
              <IconChartBar size={20} />
            </ThemeIcon>
          </Group>
        </Card>

        <Card padding="md" radius="md" withBorder>
          <Group justify="apart">
            <div>
              <Text size="xs" c="dimmed" tt="uppercase" fw={700}>Revenue</Text>
              <Text size="xl" fw={700}>‚Çπ{(ipdStats.totalRevenue / 100000).toFixed(2)}L</Text>
            </div>
            <ThemeIcon size="lg" radius="md" variant="light" color="green">
              <IconCurrencyRupee size={20} />
            </ThemeIcon>
          </Group>
        </Card>
      </SimpleGrid>

      {/* Tabs */}
      <Tabs value={activeTab} onChange={setActiveTab as any}>
        <Tabs.List mb="md">
          <Tabs.Tab value="patients" leftSection={<IconUsers size={16} />}>
            IPD Patients
          </Tabs.Tab>
          <Tabs.Tab value="beds" leftSection={<IconBed size={16} />}>
            Bed Status
          </Tabs.Tab>
          <Tabs.Tab value="wards" leftSection={<IconBuilding size={16} />}>
            Ward Management
          </Tabs.Tab>
          <Tabs.Tab value="analytics" leftSection={<IconChartBar size={16} />}>
            Analytics
          </Tabs.Tab>
        </Tabs.List>

        {/* IPD Patients Tab */}
        <Tabs.Panel value="patients">
          <Card padding="lg" radius="md" withBorder>
            {/* Filters */}
            <Group mb="md">
              <TextInput
                placeholder="Search patients..."
                leftSection={<IconSearch size={16} />}
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                style={{ flex: 1 }}
              />
              <Select
                placeholder="Ward"
                data={[
                  { value: '', label: 'All Wards' },
                  { value: 'ICU', label: 'ICU' },
                  { value: 'General Ward', label: 'General Ward' },
                  { value: 'Private Ward', label: 'Private Ward' }
                ]}
                value={selectedWard}
                onChange={setSelectedWard as any}
                clearable
              />
              <Select
                placeholder="Status"
                data={[
                  { value: '', label: 'All Status' },
                  { value: 'critical', label: 'Critical' },
                  { value: 'stable', label: 'Stable' },
                  { value: 'recovering', label: 'Recovering' }
                ]}
                value={selectedStatus}
                onChange={setSelectedStatus as any}
                clearable
              />
            </Group>

            {/* Patients Table */}
            <Table striped highlightOnHover>
              <Table.Thead>
                <Table.Tr>
                  <Table.Th>Admission #</Table.Th>
                  <Table.Th>Patient</Table.Th>
                  <Table.Th>Bed/Ward</Table.Th>
                  <Table.Th>Doctor</Table.Th>
                  <Table.Th>Admission Date</Table.Th>
                  <Table.Th>LOS</Table.Th>
                  <Table.Th>Status</Table.Th>
                  <Table.Th>Charges</Table.Th>
                  <Table.Th>Actions</Table.Th>
                </Table.Tr>
              </Table.Thead>
              <Table.Tbody>
                {filteredPatients.length === 0 ? (
                  <Table.Tr>
                    <Table.Td colSpan={9}>
                      <EmptyState
                        icon={<IconBed size={48} />}
                        title="No IPD patients found"
                        description={searchQuery || selectedWard || selectedStatus ?
                          "No patients match your search criteria. Try adjusting your filters." :
                          "No patients admitted yet. Add your first IPD admission to get started."
                        }
                        size="sm"
                      />
                    </Table.Td>
                  </Table.Tr>
                ) : (
                  filteredPatients.map((patient: any) => (
                    <Table.Tr key={patient.id}>
                      <Table.Td>
                        <Group gap="xs">
                          <IconFileText size={16} />
                          <Text size="sm" fw={500}>{patient.admissionNumber}</Text>
                        </Group>
                      </Table.Td>
                      <Table.Td>
                        <div>
                          <Text size="sm" fw={500}>{patient.patientName}</Text>
                          <Text size="xs" c="dimmed">{patient.patientAge}y, {patient.patientGender}</Text>
                        </div>
                      </Table.Td>
                      <Table.Td>
                        <div>
                          <Text size="sm">{patient.bedNumber}</Text>
                          <Text size="xs" c="dimmed">{patient.wardName}</Text>
                        </div>
                      </Table.Td>
                      <Table.Td>
                        <Text size="sm">{patient.primaryDoctor}</Text>
                      </Table.Td>
                      <Table.Td>
                        <Text size="sm">{new Date(patient.admissionDate).toLocaleDateString()}</Text>
                      </Table.Td>
                      <Table.Td>
                        <Text size="sm">{patient.lengthOfStay} days</Text>
                      </Table.Td>
                      <Table.Td>
                        <Badge
                          color={
                            patient.status === 'critical' ? 'red' :
                            patient.status === 'stable' ? 'green' :
                            patient.status === 'recovering' ? 'blue' : 'gray'
                          }
                          variant="light"
                        >
                          {patient.status.toUpperCase()}
                        </Badge>
                      </Table.Td>
                      <Table.Td>
                        <div>
                          <Text size="sm" fw={500}>‚Çπ{patient.totalCharges.toLocaleString()}</Text>
                          {patient.pendingAmount > 0 && (
                            <Text size="xs" c="red">Pending: ‚Çπ{patient.pendingAmount.toLocaleString()}</Text>
                          )}
                        </div>
                      </Table.Td>
                      <Table.Td>
                        <Group gap="xs">
                          <ActionIcon
                            variant="light"
                            color="blue"
                            onClick={() => handleViewPatient(patient)}
                          >
                            <IconEye size={16} />
                          </ActionIcon>
                          <ActionIcon variant="light" color="green">
                            <IconEdit size={16} />
                          </ActionIcon>
                        </Group>
                      </Table.Td>
                    </Table.Tr>
                  ))
                )}
              </Table.Tbody>
            </Table>
          </Card>
        </Tabs.Panel>

        {/* Bed Status Tab */}
        <Tabs.Panel value="beds">
          <Card padding="lg" radius="md" withBorder>
            <Title order={3} mb="lg">Bed Status Overview</Title>
            
            <SimpleGrid cols={{ base: 1, md: 2, lg: 4 }} spacing="lg">
              {0 /* TODO: Fetch from API */ === 0 ? (
                <Text c="dimmed">No bed data available</Text>
              ) : (
                [].map /* TODO: Fetch from API */((bed) => (
                  <Card key={bed.id} padding="lg" radius="md" withBorder onClick={() => handleViewBed(bed)} style={{ cursor: 'pointer' }}>
                    <Group justify="space-between" mb="md">
                      <div>
                        <Text size="lg" fw={700}>{bed.bedNumber}</Text>
                        <Text size="sm" c="dimmed">{bed.wardName}</Text>
                      </div>
                      <Badge
                        color={
                          bed.status === 'occupied' ? 'red' :
                          bed.status === 'vacant' ? 'green' :
                          'yellow'
                        }
                        variant="filled"
                      >
                        {bed.status}
                      </Badge>
                    </Group>
                    {bed.patientName && (
                      <Text size="sm" mb="xs">Patient: {bed.patientName}</Text>
                    )}
                    <Text size="xs" c="dimmed">Rate: ‚Çπ{bed.dailyRate}/day</Text>
                  </Card>
                ))
              )}
            </SimpleGrid>
          </Card>
        </Tabs.Panel>

        {/* Ward Management Tab */}
        <Tabs.Panel value="wards">
          <Card padding="lg" radius="md" withBorder>
            <Title order={3} mb="lg">Ward Overview</Title>
            
            <SimpleGrid cols={{ base: 1, md: 2, lg: 3 }} spacing="lg">
              {0 /* TODO: Fetch from API */ === 0 ? (
                <Text c="dimmed">No ward data available</Text>
              ) : (
                [].map /* TODO: Fetch from API */((ward) => (
                  <Card key={ward.id} padding="lg" radius="md" withBorder>
                    <Group justify="space-between" mb="md">
                      <div>
                        <Text size="lg" fw={700}>{ward.name}</Text>
                        <Text size="sm" c="dimmed">{ward.department}</Text>
                      </div>
                    </Group>
                    <Stack gap="xs">
                      <Group justify="space-between">
                        <Text size="sm">Total Beds:</Text>
                        <Text size="sm" fw={500}>{ward.totalBeds}</Text>
                      </Group>
                      <Group justify="space-between">
                        <Text size="sm">Occupied:</Text>
                        <Text size="sm" fw={500} c="red">{ward.occupiedBeds}</Text>
                      </Group>
                      <Group justify="space-between">
                        <Text size="sm">Available:</Text>
                        <Text size="sm" fw={500} c="green">{ward.availableBeds}</Text>
                      </Group>
                      <Group justify="space-between">
                        <Text size="sm">Nurses on Duty:</Text>
                        <Text size="sm" fw={500}>{ward.nursesOnDuty}</Text>
                      </Group>
                      <Text size="xs" c="dimmed" mt="xs">Head Nurse: {ward.headNurse}</Text>
                    </Stack>
                  </Card>
                ))
              )}
            </SimpleGrid>
          </Card>
        </Tabs.Panel>

        {/* Analytics Tab */}
        <Tabs.Panel value="analytics">
          <Card padding="lg" radius="md" withBorder>
            <Title order={3} mb="lg">IPD Analytics</Title>
            <Text c="dimmed">Analytics dashboard coming soon...</Text>
          </Card>
        </Tabs.Panel>
      </Tabs>

      {/* Patient Details Modal */}
      {selectedPatient && (
        <Modal
          opened={!!selectedPatient}
          onClose={() => setSelectedPatient(null)}
          title={`Patient Details - ${selectedPatient.admissionNumber}`}
          size="xl"
        >
          <Stack gap="md">
            <div>
              <Text size="lg" fw={700}>{selectedPatient.patientName}</Text>
              <Text size="sm" c="dimmed">{selectedPatient.patientAge}y, {selectedPatient.patientGender}</Text>
            </div>
            <Group>
              <Text size="sm"><strong>Bed:</strong> {selectedPatient.bedNumber}</Text>
              <Text size="sm"><strong>Ward:</strong> {selectedPatient.wardName}</Text>
            </Group>
            <Text size="sm"><strong>Diagnosis:</strong> {selectedPatient.diagnosis}</Text>
            <Text size="sm"><strong>Doctor:</strong> {selectedPatient.primaryDoctor}</Text>
            <Group>
              <Button onClick={() => setSelectedPatient(null)}>Close</Button>
            </Group>
          </Stack>
        </Modal>
      )}
    </Container>
  );
};

export default IPDManagement;

'use client';

import React, { useState, useMemo, useEffect } from 'react';
import {
  Container,
  Paper,
  Title,
  Group,
  Button,
  TextInput,
  Select,
  Badge,
  Table,
  Modal,
  Text,
  Tabs,
  Card,
  Avatar,
  ActionIcon,
  Menu,
  Stack,
  Divider,
  SimpleGrid,
  ScrollArea,
  ThemeIcon,
  Alert,
  Progress,
  NumberInput,
  Textarea,
  Timeline,
  Stepper,
  Checkbox
} from '@mantine/core';
import { useDisclosure } from '@mantine/hooks';
import EmptyState from '../../../components/EmptyState';
import { notifications } from '@mantine/notifications';
import { MantineDonutChart, SimpleAreaChart, SimpleLineChart, SimpleBarChart } from '../../../components/MantineChart';
import {
  IconPlus,
  IconSearch,
  IconEdit,
  IconEye,
  IconTrash,
  IconCalendar,
  IconTestPipe,
  IconChartBar,
  IconPhone,
  IconMail,
  IconAlertCircle,
  IconCheck,
  IconX,
  IconDotsVertical,
  IconFlask,
  IconMicroscope,
  IconClipboardList,
  IconFileText,
  IconDownload,
  IconPrinter,
  IconShare,
  IconDroplet,
  IconDna,
  IconExclamationMark,
  IconClockHour4,
  IconTrendingUp,
  IconTrendingDown,
  IconUsers,
  IconCalculator,
  IconSettings,
  IconRefresh,
  IconFilter,
  IconBarcode,
  IconTemperature,
  IconShieldCheck,
  IconAlertTriangle,
  IconCircleCheck,
  IconClipboard,
  IconReportMedical,
  IconAtom,
  IconHeartbeat,
  IconBrain,
  IconBone,
  IconActivity,
  IconMedicalCross
} from '@tabler/icons-react';

// Import types, services and mock data
import {
  LabTest,
  TestStatus,
  TestType,
  TestCategory,
  LabOrder,
  OrderStatus,
  LabResult,
  TestParameter,
  Sample,
  SampleStatus,
  SampleType,
  LabEquipment,
  EquipmentStatus as LabEquipmentStatus,
  QualityControl,
  QCStatus,
  LabStats,
  LabFilters
} from '../../../types/laboratory';
import laboratoryService from '../../../services/laboratory.service';
// Mock data imports removed
const LaboratoryManagement = () => {
  // State management
  const [activeTab, setActiveTab] = useState<string>('tests');
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedCategory, setSelectedCategory] = useState<string>('');
  const [selectedStatus, setSelectedStatus] = useState<string>('');
  const [selectedType, setSelectedType] = useState<string>('');
  const [selectedPatient, setSelectedPatient] = useState<string>('');
  const [selectedTest, setSelectedTest] = useState<LabTest | null>(null);
  const [selectedOrder, setSelectedOrder] = useState<LabOrder | null>(null);
  const [selectedSample, setSelectedSample] = useState<Sample | null>(null);

  // API data state
  const [labTests, setLabTests] = useState<LabTest[]>([]);
  const [labOrders, setLabOrders] = useState<LabOrder[]>([]);
  const [labStats, setLabStats] = useState<any>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  // Modal states
  const [testDetailOpened, { open: openTestDetail, close: closeTestDetail }] = useDisclosure(false);
  const [addTestOpened, { open: openAddTest, close: closeAddTest }] = useDisclosure(false);
  const [orderDetailOpened, { open: openOrderDetail, close: closeOrderDetail }] = useDisclosure(false);
  const [addOrderOpened, { open: openAddOrder, close: closeAddOrder }] = useDisclosure(false);
  const [sampleDetailOpened, { open: openSampleDetail, close: closeSampleDetail }] = useDisclosure(false);

  useEffect(() => {
    fetchAllData();
  }, []);

  const fetchAllData = async () => {
    try {
      setLoading(true);
      setError(null);
      await Promise.all([
        fetchLabTests(),
        fetchLabOrders(),
        fetchLabStats()
      ]);
    } catch (err: any) {
      console.error('Error loading laboratory data:', err);
      setError(err.response?.data?.message || err.message || 'Failed to load laboratory data');
      // Fallback to mock data
      setLabTests([] /* TODO: Fetch from API */);
      setLabOrders([] /* TODO: Fetch from API */);
    } finally {
      setLoading(false);
    }
  };

  const fetchLabTests = async () => {
    try {
      const filters = {
        category: selectedCategory || undefined,
        search: searchQuery || undefined
      };
      const response = await laboratoryService.getLabTests(filters) as any;
      setLabTests(response.data?.items || response.data || []);
    } catch (err: any) {
      console.warn('Error fetching lab tests (using empty data):', err.message || err);
      setLabTests([]);
    }
  };

  const fetchLabOrders = async () => {
    try {
      const filters = {
        patientId: selectedPatient || undefined,
        status: selectedStatus || undefined,
        search: searchQuery || undefined
      };
      const response = await laboratoryService.getLabOrders(filters) as any;
      setLabOrders(response.data?.items || response.data || []);
    } catch (err: any) {
      console.warn('Error fetching lab orders (using empty data):', err.message || err);
      setLabOrders([]);
    }
  };

  const fetchLabStats = async () => {
    try {
      const response = await laboratoryService.getLabStats() as any;
      setLabStats(response.data);
    } catch (err: any) {
      console.warn('Error fetching lab stats (using default values):', err.message || err);
      setLabStats({
        totalTests: 0,
        pendingTests: 0,
        completedTests: 0,
        totalOrders: 0,
        pendingOrders: 0,
        completedOrders: 0
      });
    }
  };

  // Refetch when filters change
  useEffect(() => {
    if (!loading) {
      if (activeTab === 'tests') {
        fetchLabTests();
      } else if (activeTab === 'orders') {
        fetchLabOrders();
      }
    }
  }, [activeTab, searchQuery, selectedCategory, selectedStatus, selectedPatient]);

  // Filter lab tests
  const filteredTests = useMemo(() => {
    return labTests.filter((test) => {
      const matchesSearch = 
        test.testName.toLowerCase().includes(searchQuery.toLowerCase()) ||
        test.testCode.toLowerCase().includes(searchQuery.toLowerCase());
      
      const matchesCategory = !selectedCategory || test.category === selectedCategory;
      const matchesStatus = !selectedStatus || test.status === selectedStatus;
      const matchesType = !selectedType || test.testType === selectedType;

      return matchesSearch && matchesCategory && matchesStatus && matchesType;
    });
  }, [searchQuery, selectedCategory, selectedStatus, selectedType]);

  // Filter lab orders
  const filteredOrders = useMemo(() => {
    return labOrders.filter((order) => {
      const matchesSearch = 
        order.orderNumber.toLowerCase().includes(searchQuery.toLowerCase()) ||
        order.patient.firstName.toLowerCase().includes(searchQuery.toLowerCase()) ||
        order.patient.lastName.toLowerCase().includes(searchQuery.toLowerCase());
      
      const matchesStatus = !selectedStatus || order.status === selectedStatus;
      const matchesPatient = !selectedPatient || order.patientId === selectedPatient;

      return matchesSearch && matchesStatus && matchesPatient;
    });
  }, [searchQuery, selectedStatus, selectedPatient]);

  // Filter samples
  const filteredSamples = useMemo(() => {
    return [].filter /* TODO: Fetch from API */((sample) => {
      const matchesSearch = 
        sample.sampleId.toLowerCase().includes(searchQuery.toLowerCase()) ||
        sample.patientName.toLowerCase().includes(searchQuery.toLowerCase());
      
      const matchesStatus = !selectedStatus || sample.status === selectedStatus;
      const matchesType = !selectedType || sample.sampleType === selectedType;

      return matchesSearch && matchesStatus && matchesType;
    });
  }, [searchQuery, selectedStatus, selectedType]);

  // Helper functions
  const getStatusColor = (status: string) => {
    switch (status) {
      case 'active':
      case 'completed':
      case 'processed':
      case 'operational':
      case 'passed': return 'green';
      case 'pending':
      case 'in_progress':
      case 'collected':
      case 'maintenance':
      case 'in_review': return 'orange';
      case 'cancelled':
      case 'rejected':
      case 'contaminated':
      case 'out_of_service':
      case 'failed': return 'red';
      case 'draft':
      case 'ordered':
      case 'received':
      case 'calibration': return 'blue';
      case 'expired': return 'dark';
      default: return 'gray';
    }
  };

  const getCategoryColor = (category: TestCategory) => {
    switch (category) {
      case 'hematology': return 'red';
      case 'biochemistry': return 'blue';
      case 'microbiology': return 'green';
      case 'immunology': return 'purple';
      case 'pathology': return 'orange';
      case 'molecular': return 'cyan';
      case 'genetics': return 'pink';
      default: return 'gray';
    }
  };

  const getCategoryIcon = (category: TestCategory) => {
    switch (category) {
      case 'hematology': return <IconDroplet size={16} />;
      case 'biochemistry': return <IconFlask size={16} />;
      case 'microbiology': return <IconMicroscope size={16} />;
      case 'immunology': return <IconShieldCheck size={16} />;
      case 'pathology': return <IconReportMedical size={16} />;
      case 'molecular': return <IconDna size={16} />;
      case 'genetics': return <IconAtom size={16} />;
      default: return <IconTestPipe size={16} />;
    }
  };

  const handleViewTest = (test: LabTest) => {
    setSelectedTest(test);
    openTestDetail();
  };

  const handleViewOrder = (order: LabOrder) => {
    setSelectedOrder(order);
    openOrderDetail();
  };

  const handleViewSample = (sample: Sample) => {
    setSelectedSample(sample);
    openSampleDetail();
  };

  const clearFilters = () => {
    setSearchQuery('');
    setSelectedCategory('');
    setSelectedStatus('');
    setSelectedType('');
    setSelectedPatient('');
  };

  const formatCurrency = (amount: number) => {
    return new Intl.NumberFormat('en-IN', {
      style: 'currency',
      currency: 'INR'
    }).format(amount);
  };

  // Statistics cards
  const statsCards = [
    {
      title: 'Total Tests',
      value: labStats?.totalTests || 0 /* TODO: Fetch from API */,
      icon: IconTestPipe,
      color: 'blue',
      trend: '+0%'
    },
    {
      title: 'Pending Results',
      value: labStats?.pendingTests || 0 /* TODO: Fetch from API */,
      icon: IconClockHour4,
      color: 'orange',
      trend: '0%'
    },
    {
      title: 'Completed Today',
      value: labStats?.completedToday || 0,
      icon: IconCheck,
      color: 'green',
      trend: '+0%'
    },
    {
      title: 'Equipment Status',
      value: labStats?.equipmentOperational && labStats?.totalEquipment 
        ? `${labStats.equipmentOperational}/${labStats.totalEquipment}`
        : `${0 /* TODO: Fetch from API */}/${0 /* TODO: Fetch from API */}`,
      icon: IconSettings,
      color: 'purple',
      trend: '0%'
    }
  ];

  // Chart data
  const testCategoryData = (labStats?.testsByCategory && typeof labStats.testsByCategory === 'object')
    ? Object.entries(labStats.testsByCategory)
        .map(([category, count]) => ({
          name: category.replace('_', ' ').toUpperCase(),
          value: count as number,
          color: getCategoryColor(category as TestCategory)
        }))
    : [];

  const dailyTestsData = Array.isArray(labStats?.dailyTestVolume) ? labStats.dailyTestVolume : [];
  const turnaroundTimeData = Array.isArray(labStats?.averageTurnaroundTime) ? labStats.averageTurnaroundTime : [];

  return (
    <Container size="xl" py="md">
      {/* Header */}
      <Group justify="space-between" mb="lg">
        <div>
          <Title order={1}>Laboratory Management</Title>
          <Text c="dimmed" size="sm">
            Manage lab tests, samples, results, and equipment
          </Text>
        </div>
        <Group>
          <Button
            leftSection={<IconPlus size={16} />}
            onClick={openAddTest}
          >
            Add Test
          </Button>
          <Button
            variant="light"
            leftSection={<IconFlask size={16} />}
            onClick={openAddOrder}
          >
            New Order
          </Button>
        </Group>
      </Group>

      {/* Loading State */}
      {loading && (
        <Alert icon={<IconAlertCircle size={16} />} color="blue" mb="lg">
          Loading laboratory data...
        </Alert>
      )}

      {/* Error State */}
      {error && (
        <Alert icon={<IconAlertTriangle size={16} />} color="red" mb="lg" withCloseButton onClose={() => setError(null)}>
          {error}
        </Alert>
      )}

      {/* Statistics Cards */}
      <SimpleGrid cols={{ base: 1, sm: 2, md: 4 }} mb="lg">
        {statsCards.map((stat) => {
          const Icon = stat.icon;
          return (
            <Card key={stat.title} padding="lg" radius="md" withBorder>
              <Group justify="space-between">
                <div>
                  <Text c="dimmed" size="sm" fw={500}>
                    {stat.title}
                  </Text>
                  <Text fw={700} size="xl">
                    {stat.value}
                  </Text>
                </div>
                <ThemeIcon color={stat.color} size="xl" radius="md" variant="light">
                  <Icon size={24} />
                </ThemeIcon>
              </Group>
              <Group justify="space-between" mt="sm">
                <Badge 
                  color={stat.trend.includes('%') && stat.trend.startsWith('+') ? 'green' : stat.trend.includes('%') ? 'red' : 'blue'} 
                  variant="light"
                  size="sm"
                >
                  {stat.trend}
                </Badge>
                <Text size="xs" c="dimmed">
                  {stat.trend.includes('%') ? 'vs last month' : 'operational'}
                </Text>
              </Group>
            </Card>
          );
        })}
      </SimpleGrid>

      {/* Main Content Tabs */}
      <Tabs value={activeTab} onChange={setActiveTab}>
        <Tabs.List>
          <Tabs.Tab value="tests" leftSection={<IconTestPipe size={16} />}>
            Lab Tests
          </Tabs.Tab>
          <Tabs.Tab value="orders" leftSection={<IconClipboardList size={16} />}>
            Lab Orders
          </Tabs.Tab>
          <Tabs.Tab value="samples" leftSection={<IconFlask size={16} />}>
            Samples
          </Tabs.Tab>
          <Tabs.Tab value="equipment" leftSection={<IconSettings size={16} />}>
            Equipment
          </Tabs.Tab>
          <Tabs.Tab value="qc" leftSection={<IconShieldCheck size={16} />}>
            Quality Control
          </Tabs.Tab>
          <Tabs.Tab value="reports" leftSection={<IconChartBar size={16} />}>
            Reports
          </Tabs.Tab>
        </Tabs.List>

        {/* Lab Tests Tab */}
        <Tabs.Panel value="tests">
          <Paper p="md" radius="md" withBorder mt="md">
            {/* Search and Filters */}
            <Group mb="md">
              <TextInput
                placeholder="Search tests..."
                leftSection={<IconSearch size={16} />}
                value={searchQuery}
                onChange={(event) => setSearchQuery(event.currentTarget.value)}
                style={{ flex: 1 }}
              />
              <Select
                placeholder="Category"
                data={[
                  { value: 'hematology', label: 'Hematology' },
                  { value: 'biochemistry', label: 'Biochemistry' },
                  { value: 'microbiology', label: 'Microbiology' },
                  { value: 'immunology', label: 'Immunology' },
                  { value: 'pathology', label: 'Pathology' },
                  { value: 'molecular', label: 'Molecular' },
                  { value: 'genetics', label: 'Genetics' }
                ]}
                value={selectedCategory}
                onChange={setSelectedCategory}
                clearable
              />
              <Select
                placeholder="Status"
                data={[
                  { value: 'active', label: 'Active' },
                  { value: 'pending', label: 'Pending' },
                  { value: 'completed', label: 'Completed' },
                  { value: 'cancelled', label: 'Cancelled' }
                ]}
                value={selectedStatus}
                onChange={setSelectedStatus}
                clearable
              />
              <Select
                placeholder="Type"
                data={[
                  { value: 'routine', label: 'Routine' },
                  { value: 'urgent', label: 'Urgent' },
                  { value: 'stat', label: 'STAT' },
                  { value: 'research', label: 'Research' }
                ]}
                value={selectedType}
                onChange={setSelectedType}
                clearable
              />
              <Button variant="light" onClick={clearFilters}>
                Clear Filters
              </Button>
            </Group>

            {/* Tests Grid */}
            <SimpleGrid cols={{ base: 1, md: 2, lg: 3 }} spacing="lg">
              {filteredTests.map((test) => (
                <Card key={test.id} padding="lg" radius="md" withBorder>
                  <Group justify="space-between" mb="md">
                    <div style={{ flex: 1 }}>
                      <Group>
                        <ThemeIcon 
                          color={getCategoryColor(test.category)} 
                          variant="light" 
                          size="lg"
                        >
                          {getCategoryIcon(test.category)}
                        </ThemeIcon>
                        <div>
                          <Text fw={600} size="sm" lineClamp={1}>
                            {test.testName}
                          </Text>
                          <Text size="xs" c="dimmed">
                            {test.testCode}
                          </Text>
                        </div>
                      </Group>
                    </div>
                    <Badge color={getStatusColor(test.status)} variant="light" size="sm">
                      {test.status}
                    </Badge>
                  </Group>

                  <Stack gap="sm" mb="md">
                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Category</Text>
                      <Badge color={getCategoryColor(test.category)} variant="light" size="xs">
                        {test.category}
                      </Badge>
                    </Group>
                    
                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Type</Text>
                      <Text size="sm" fw={500}>
                        {test.testType.toUpperCase()}
                      </Text>
                    </Group>

                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Price</Text>
                      <Text size="sm" fw={600}>
                        {formatCurrency(test.price)}
                      </Text>
                    </Group>

                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Turnaround Time</Text>
                      <Text size="sm">
                        {test.turnaroundTime}
                      </Text>
                    </Group>

                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Sample Type</Text>
                      <Text size="sm">
                        {test.sampleType.replace('_', ' ')}
                      </Text>
                    </Group>
                  </Stack>

                  <Group justify="space-between">
                    <Text size="xs" c="dimmed" lineClamp={2}>
                      {test.description}
                    </Text>
                    <Group gap="xs">
                      <ActionIcon
                        variant="subtle"
                        color="blue"
                        onClick={() => handleViewTest(test)}
                      >
                        <IconEye size={16} />
                      </ActionIcon>
                      <ActionIcon variant="subtle" color="green">
                        <IconEdit size={16} />
                      </ActionIcon>
                      <Menu>
                        <Menu.Target>
                          <ActionIcon variant="subtle" color="gray">
                            <IconDotsVertical size={16} />
                          </ActionIcon>
                        </Menu.Target>
                        <Menu.Dropdown>
                          <Menu.Item leftSection={<IconClipboard size={14} />}>
                            Order Test
                          </Menu.Item>
                          <Menu.Item leftSection={<IconDownload size={14} />}>
                            Export Details
                          </Menu.Item>
                          <Menu.Divider />
                          <Menu.Item 
                            leftSection={<IconTrash size={14} />}
                            color="red"
                          >
                            Delete
                          </Menu.Item>
                        </Menu.Dropdown>
                      </Menu>
                    </Group>
                  </Group>
                </Card>
              ))}
            </SimpleGrid>
          </Paper>
        </Tabs.Panel>

        {/* Lab Orders Tab */}
        <Tabs.Panel value="orders">
          <Paper p="md" radius="md" withBorder mt="md">
            <Group justify="space-between" mb="lg">
              <Title order={3}>Laboratory Orders</Title>
              <Button leftSection={<IconPlus size={16} />} onClick={openAddOrder}>
                New Order
              </Button>
            </Group>

            {/* Order Filters */}
            <Group mb="md">
              <TextInput
                placeholder="Search orders..."
                leftSection={<IconSearch size={16} />}
                value={searchQuery}
                onChange={(event) => setSearchQuery(event.currentTarget.value)}
                style={{ flex: 1 }}
              />
              <Select
                placeholder="Patient"
                data={[].map /* TODO: Fetch from API */(patient => ({ 
                  value: patient.id, 
                  label: `${patient.firstName} ${patient.lastName}` 
                }))}
                value={selectedPatient}
                onChange={setSelectedPatient}
                clearable
              />
              <Select
                placeholder="Status"
                data={[
                  { value: 'pending', label: 'Pending' },
                  { value: 'in_progress', label: 'In Progress' },
                  { value: 'completed', label: 'Completed' },
                  { value: 'cancelled', label: 'Cancelled' }
                ]}
                value={selectedStatus}
                onChange={setSelectedStatus}
                clearable
              />
            </Group>

            {/* Orders Table */}
            <ScrollArea>
              <Table striped highlightOnHover>
                <Table.Thead>
                  <Table.Tr>
                    <Table.Th>Order #</Table.Th>
                    <Table.Th>Patient</Table.Th>
                    <Table.Th>Doctor</Table.Th>
                    <Table.Th>Tests</Table.Th>
                    <Table.Th>Order Date</Table.Th>
                    <Table.Th>Priority</Table.Th>
                    <Table.Th>Status</Table.Th>
                    <Table.Th>Actions</Table.Th>
                  </Table.Tr>
                </Table.Thead>
                <Table.Tbody>
                  {filteredOrders.length === 0 ? (
                    <Table.Tr>
                      <Table.Td colSpan={9}>
                        <EmptyState
                          icon={<IconTestPipe size={48} />}
                          title="No lab tests"
                          description="Order your first lab test to begin diagnostics"
                          size="sm"
                        />
                      </Table.Td>
                    </Table.Tr>
                  ) : (
                    filteredOrders.map((order) => (
                      <Table.Tr key={order.id}>
                        <Table.Td>
                          <Text fw={500}>{order.orderNumber}</Text>
                        </Table.Td>
                        <Table.Td>
                          <Group>
                            <Avatar color="blue" radius="xl" size="sm">
                              {order.patient.firstName[0]}{order.patient.lastName[0]}
                            </Avatar>
                            <div>
                              <Text size="sm" fw={500}>
                                {order.patient.firstName} {order.patient.lastName}
                              </Text>
                              <Text size="xs" c="dimmed">
                                {order.patient.patientId}
                              </Text>
                            </div>
                          </Group>
                        </Table.Td>
                        <Table.Td>
                          <div>
                            <Text size="sm" fw={500}>
                              {order.orderingDoctor.firstName} {order.orderingDoctor.lastName}
                            </Text>
                            <Text size="xs" c="dimmed">
                              {order.orderingDoctor.department?.name}
                            </Text>
                          </div>
                        </Table.Td>
                        <Table.Td>
                          <Text size="sm">{order.tests.length} tests</Text>
                        </Table.Td>
                        <Table.Td>
                          <Text size="sm">
                            {typeof order.orderDate === 'string' ? order.orderDate : new Date(order.orderDate).toISOString().split('T')[0]}
                          </Text>
                        </Table.Td>
                        <Table.Td>
                          <Badge 
                            color={
                              order.priority === 'stat' ? 'red' : 
                              order.priority === 'urgent' ? 'orange' : 'blue'
                            } 
                            variant="light"
                            size="sm"
                          >
                            {order.priority.toUpperCase()}
                          </Badge>
                        </Table.Td>
                        <Table.Td>
                          <Badge color={getStatusColor(order.status)} variant="light">
                            {order.status.replace('_', ' ')}
                          </Badge>
                        </Table.Td>
                        <Table.Td>
                          <Group gap="xs">
                            <ActionIcon
                              variant="subtle"
                              color="blue"
                              onClick={() => handleViewOrder(order)}
                            >
                              <IconEye size={16} />
                            </ActionIcon>
                            <ActionIcon variant="subtle" color="green">
                              <IconEdit size={16} />
                            </ActionIcon>
                            <ActionIcon variant="subtle" color="orange">
                              <IconDownload size={16} />
                            </ActionIcon>
                          </Group>
                        </Table.Td>
                      </Table.Tr>
                    ))
                  )}
                </Table.Tbody>
              </Table>
            </ScrollArea>
          </Paper>
        </Tabs.Panel>

        {/* Samples Tab */}
        <Tabs.Panel value="samples">
          <Paper p="md" radius="md" withBorder mt="md">
            <Group justify="space-between" mb="lg">
              <Title order={3}>Sample Management</Title>
              <Group>
                <Button leftSection={<IconBarcode size={16} />} variant="light">
                  Scan Sample
                </Button>
                <Button leftSection={<IconFlask size={16} />}>
                  Register Sample
                </Button>
              </Group>
            </Group>

            {/* Sample Filters */}
            <Group mb="md">
              <TextInput
                placeholder="Search samples..."
                leftSection={<IconSearch size={16} />}
                value={searchQuery}
                onChange={(event) => setSearchQuery(event.currentTarget.value)}
                style={{ flex: 1 }}
              />
              <Select
                placeholder="Sample Type"
                data={[
                  { value: 'blood', label: 'Blood' },
                  { value: 'urine', label: 'Urine' },
                  { value: 'stool', label: 'Stool' },
                  { value: 'sputum', label: 'Sputum' },
                  { value: 'csf', label: 'CSF' },
                  { value: 'tissue', label: 'Tissue' },
                  { value: 'swab', label: 'Swab' }
                ]}
                value={selectedType}
                onChange={setSelectedType}
                clearable
              />
              <Select
                placeholder="Status"
                data={[
                  { value: 'collected', label: 'Collected' },
                  { value: 'received', label: 'Received' },
                  { value: 'processed', label: 'Processed' },
                  { value: 'rejected', label: 'Rejected' }
                ]}
                value={selectedStatus}
                onChange={setSelectedStatus}
                clearable
              />
            </Group>

            {/* Samples Grid */}
            <SimpleGrid cols={{ base: 1, md: 2, lg: 3 }} spacing="lg">
              {filteredSamples.map((sample) => (
                <Card key={sample.id} padding="lg" radius="md" withBorder>
                  <Group justify="space-between" mb="md">
                    <div>
                      <Text fw={600} size="lg">{sample.sampleId}</Text>
                      <Text size="sm" c="dimmed">{sample.patientName}</Text>
                    </div>
                    <Badge color={getStatusColor(sample.status)} variant="light">
                      {sample.status}
                    </Badge>
                  </Group>

                  <Stack gap="sm" mb="md">
                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Sample Type</Text>
                      <Group gap="xs">
                        <IconFlask size={16} />
                        <Text size="sm" fw={500}>
                          {sample.sampleType.replace('_', ' ').toUpperCase()}
                        </Text>
                      </Group>
                    </Group>

                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Collection Date</Text>
                      <Text size="sm">
                        {typeof sample.collectionDate === 'string' ? sample.collectionDate : new Date(sample.collectionDate).toISOString().split('T')[0]}
                      </Text>
                    </Group>

                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Collection Time</Text>
                      <Text size="sm">
                        {(sample as any).collectionTime || 'N/A'}
                      </Text>
                    </Group>

                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Volume</Text>
                      <Text size="sm">{sample.volume} {sample.unit}</Text>
                    </Group>

                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Container</Text>
                      <Text size="sm">{sample.containerType}</Text>
                    </Group>

                    {sample.storageConditions && (
                      <Group justify="space-between">
                        <Text size="sm" c="dimmed">Storage</Text>
                        <Group gap="xs">
                          <IconTemperature size={14} />
                          <Text size="sm">{sample.storageConditions}</Text>
                        </Group>
                      </Group>
                    )}
                  </Stack>

                  {sample.notes && (
                    <Alert variant="light" color="blue" mb="md">
                      <Text size="sm">{sample.notes}</Text>
                    </Alert>
                  )}

                  <Group justify="space-between">
                    <Text size="xs" c="dimmed">
                      Collected by: {sample.collectedBy}
                    </Text>
                    <Group gap="xs">
                      <ActionIcon
                        variant="subtle"
                        color="blue"
                        onClick={() => handleViewSample(sample)}
                      >
                        <IconEye size={16} />
                      </ActionIcon>
                      <ActionIcon variant="subtle" color="green">
                        <IconEdit size={16} />
                      </ActionIcon>
                      <ActionIcon variant="subtle" color="orange">
                        <IconBarcode size={16} />
                      </ActionIcon>
                    </Group>
                  </Group>
                </Card>
              ))}
            </SimpleGrid>
          </Paper>
        </Tabs.Panel>

        {/* Equipment Tab */}
        <Tabs.Panel value="equipment">
          <Paper p="md" radius="md" withBorder mt="md">
            <Group justify="space-between" mb="lg">
              <Title order={3}>Laboratory Equipment</Title>
              <Button leftSection={<IconPlus size={16} />}>
                Add Equipment
              </Button>
            </Group>

            {/* Equipment Grid */}
            <SimpleGrid cols={{ base: 1, md: 2, lg: 3 }} spacing="lg">
              {[].map /* TODO: Fetch from API */((equipment) => (
                <Card key={equipment.id} padding="lg" radius="md" withBorder>
                  <Group justify="space-between" mb="md">
                    <div style={{ flex: 1 }}>
                      <Text fw={600} size="lg" lineClamp={1}>
                        {equipment.equipmentName}
                      </Text>
                      <Text size="sm" c="dimmed">
                        {equipment.manufacturer} - {equipment.model}
                      </Text>
                    </div>
                    <Badge color={getStatusColor(equipment.status)} variant="light">
                      {equipment.status.replace('_', ' ')}
                    </Badge>
                  </Group>

                  <Stack gap="sm" mb="md">
                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Serial Number</Text>
                      <Text size="sm" fw={500}>{equipment.serialNumber}</Text>
                    </Group>

                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Location</Text>
                      <Text size="sm">{equipment.location}</Text>
                    </Group>

                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Installation Date</Text>
                      <Text size="sm">
                        {typeof equipment.installationDate === 'string' ? equipment.installationDate : new Date(equipment.installationDate).toISOString().split('T')[0]}
                      </Text>
                    </Group>

                    {equipment.lastMaintenanceDate && (
                      <Group justify="space-between">
                        <Text size="sm" c="dimmed">Last Maintenance</Text>
                        <Text size="sm">
                          {typeof equipment.lastMaintenanceDate === 'string' ? equipment.lastMaintenanceDate : new Date(equipment.lastMaintenanceDate).toISOString().split('T')[0]}
                        </Text>
                      </Group>
                    )}

                    {equipment.nextMaintenanceDate && (
                      <Group justify="space-between">
                        <Text size="sm" c="dimmed">Next Maintenance</Text>
                        <Text 
                          size="sm" 
                          c="dimmed"
                        >
                          {typeof equipment.nextMaintenanceDate === 'string' ? equipment.nextMaintenanceDate : new Date(equipment.nextMaintenanceDate).toISOString().split('T')[0]}
                        </Text>
                      </Group>
                    )}

                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Warranty</Text>
                      <Text 
                        size="sm" 
                        c={new Date(equipment.warrantyExpiry) < new Date() ? 'red' : 'green'}
                      >
                        {new Date(equipment.warrantyExpiry) < new Date() ? 'Expired' : 'Valid'}
                      </Text>
                    </Group>
                  </Stack>

                  <Group justify="space-between">
                    <Text size="sm" fw={600} c="blue">
                      {formatCurrency(equipment.purchasePrice)}
                    </Text>
                    <Group gap="xs">
                      <ActionIcon variant="subtle" color="blue">
                        <IconEye size={16} />
                      </ActionIcon>
                      <ActionIcon variant="subtle" color="green">
                        <IconEdit size={16} />
                      </ActionIcon>
                      <ActionIcon variant="subtle" color="orange">
                        <IconSettings size={16} />
                      </ActionIcon>
                    </Group>
                  </Group>
                </Card>
              ))}
            </SimpleGrid>
          </Paper>
        </Tabs.Panel>

        {/* Quality Control Tab */}
        <Tabs.Panel value="qc">
          <Paper p="md" radius="md" withBorder mt="md">
            <Group justify="space-between" mb="lg">
              <Title order={3}>Quality Control</Title>
              <Button leftSection={<IconPlus size={16} />}>
                Add QC Test
              </Button>
            </Group>

            {/* QC Grid */}
            <SimpleGrid cols={{ base: 1, md: 2 }} spacing="lg">
              {[].map /* TODO: Fetch from API */((qc) => (
                <Card key={qc.id} padding="lg" radius="md" withBorder>
                  <Group justify="space-between" mb="md">
                    <div>
                      <Text fw={600} size="lg">{qc.testName}</Text>
                      <Text size="sm" c="dimmed">{qc.controlLotNumber}</Text>
                    </div>
                    <Badge color={getStatusColor(qc.status)} variant="light">
                      {qc.status}
                    </Badge>
                  </Group>

                  <Stack gap="sm" mb="md">
                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Test Date</Text>
                      <Text size="sm">
                        {typeof qc.testDate === 'string' ? qc.testDate : new Date(qc.testDate).toISOString().split('T')[0]}
                      </Text>
                    </Group>

                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Expected Value</Text>
                      <Text size="sm" fw={500}>{qc.expectedValue}</Text>
                    </Group>

                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Actual Value</Text>
                      <Text size="sm" fw={500}>{qc.actualValue}</Text>
                    </Group>

                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Acceptable Range</Text>
                      <Text size="sm">{qc.acceptableRange}</Text>
                    </Group>

                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Performed By</Text>
                      <Text size="sm">{qc.performedBy}</Text>
                    </Group>
                  </Stack>

                  {qc.comments && (
                    <Alert 
                      variant="light" 
                      color={qc.status === 'failed' ? 'red' : 'blue'} 
                      mb="md"
                    >
                      <Text size="sm">{qc.comments}</Text>
                    </Alert>
                  )}

                  <Group justify="space-between">
                    <Text size="xs" c="dimmed">
                      Control Type: {qc.controlType}
                    </Text>
                    <Group gap="xs">
                      <ActionIcon variant="subtle" color="blue">
                        <IconEye size={16} />
                      </ActionIcon>
                      <ActionIcon variant="subtle" color="orange">
                        <IconDownload size={16} />
                      </ActionIcon>
                    </Group>
                  </Group>
                </Card>
              ))}
            </SimpleGrid>
          </Paper>
        </Tabs.Panel>

        {/* Reports Tab */}
        <Tabs.Panel value="reports">
          <Paper p="md" radius="md" withBorder mt="md">
            <Title order={3} mb="lg">Laboratory Reports & Analytics</Title>
            
            <SimpleGrid cols={{ base: 1, lg: 2 }} spacing="lg">
              {/* Test Distribution by Category */}
              <Card padding="lg" radius="md" withBorder>
                <Title order={4} mb="md">Tests by Category</Title>
                <MantineDonutChart
                  data={testCategoryData}
                  size={160}
                  thickness={30}
                  withLabels
                />
              </Card>
              
              {/* Daily Test Volume */}
              <Card padding="lg" radius="md" withBorder>
                <Title order={4} mb="md">Daily Test Volume</Title>
                <SimpleAreaChart
                  data={dailyTestsData}
                  dataKey="date"
                  series={[{ name: 'tests', color: 'blue.6' }]}
                />
              </Card>
              
              {/* Turnaround Time Analysis */}
              <Card padding="lg" radius="md" withBorder style={{ gridColumn: '1 / -1' }}>
                <Title order={4} mb="md">Average Turnaround Time by Category</Title>
                <SimpleBarChart
                  data={turnaroundTimeData}
                  dataKey="category"
                  series={[{ name: 'hours', color: 'orange.6' }]}
                />
              </Card>
              
              {/* Key Performance Indicators */}
              <Card padding="lg" radius="md" withBorder>
                <Title order={4} mb="md">Key Performance Indicators</Title>
                <Stack gap="md">
                  <Group justify="space-between" p="sm" 
                         style={{ backgroundColor: '#f8f9fa', borderRadius: '6px' }}>
                    <Text size="sm" fw={500}>Test Accuracy Rate</Text>
                    <Text size="sm" fw={600} c="green">
                      {0 /* TODO: Fetch from API */}%
                    </Text>
                  </Group>
                  <Group justify="space-between" p="sm" 
                         style={{ backgroundColor: '#f8f9fa', borderRadius: '6px' }}>
                    <Text size="sm" fw={500}>Average TAT</Text>
                    <Text size="sm" fw={600}>
                      {0 /* TODO: Fetch from API */} hours
                    </Text>
                  </Group>
                  <Group justify="space-between" p="sm" 
                         style={{ backgroundColor: '#f8f9fa', borderRadius: '6px' }}>
                    <Text size="sm" fw={500}>Sample Rejection Rate</Text>
                    <Text size="sm" fw={600} c="red">
                      {0 /* TODO: Fetch from API */}%
                    </Text>
                  </Group>
                  <Group justify="space-between" p="sm" 
                         style={{ backgroundColor: '#f8f9fa', borderRadius: '6px' }}>
                    <Text size="sm" fw={500}>Equipment Uptime</Text>
                    <Text size="sm" fw={600} c="green">
                      {0 /* TODO: Fetch from API */}%
                    </Text>
                  </Group>
                </Stack>
              </Card>
              
              {/* Quick Actions */}
              <Card padding="lg" radius="md" withBorder>
                <Title order={4} mb="md">Quick Reports</Title>
                <Stack gap="sm">
                  <Button fullWidth leftSection={<IconDownload size={16} />} variant="light">
                    Export Test Results
                  </Button>
                  <Button fullWidth leftSection={<IconFileText size={16} />} variant="light">
                    Quality Control Report
                  </Button>
                  <Button fullWidth leftSection={<IconChartBar size={16} />} variant="light">
                    Workload Analysis
                  </Button>
                  <Button fullWidth leftSection={<IconSettings size={16} />} variant="light">
                    Equipment Performance
                  </Button>
                </Stack>
              </Card>
            </SimpleGrid>
          </Paper>
        </Tabs.Panel>
      </Tabs>

      {/* Test Detail Modal */}
      <Modal
        opened={testDetailOpened}
        onClose={closeTestDetail}
        title="Test Details"
        size="lg"
      >
        {selectedTest && (
          <ScrollArea h={500}>
            <Stack gap="md">
              <Group>
                <ThemeIcon 
                  color={getCategoryColor(selectedTest.category)} 
                  size="xl" 
                  variant="light"
                >
                  {getCategoryIcon(selectedTest.category)}
                </ThemeIcon>
                <div>
                  <Title order={3}>{selectedTest.testName}</Title>
                  <Text c="dimmed">{selectedTest.testCode}</Text>
                  <Badge color={getStatusColor(selectedTest.status)} variant="light" mt="xs">
                    {selectedTest.status}
                  </Badge>
                </div>
              </Group>

              <Divider />

              <SimpleGrid cols={2}>
                <div>
                  <Text size="sm" fw={500}>Category</Text>
                  <Text size="sm" c="dimmed">{selectedTest.category}</Text>
                </div>
                <div>
                  <Text size="sm" fw={500}>Type</Text>
                  <Text size="sm" c="dimmed">{selectedTest.testType}</Text>
                </div>
                <div>
                  <Text size="sm" fw={500}>Price</Text>
                  <Text size="sm" fw={600}>{formatCurrency(selectedTest.price)}</Text>
                </div>
                <div>
                  <Text size="sm" fw={500}>Turnaround Time</Text>
                  <Text size="sm" c="dimmed">{selectedTest.turnaroundTime}</Text>
                </div>
                <div>
                  <Text size="sm" fw={500}>Sample Type</Text>
                  <Text size="sm" c="dimmed">{selectedTest.sampleType.replace('_', ' ')}</Text>
                </div>
                <div>
                  <Text size="sm" fw={500}>Sample Volume</Text>
                  <Text size="sm" c="dimmed">{selectedTest.sampleVolume}</Text>
                </div>
              </SimpleGrid>

              <div>
                <Text size="sm" fw={500} mb="sm">Description</Text>
                <Text size="sm">{selectedTest.description}</Text>
              </div>

              {selectedTest.parameters && selectedTest.parameters.length > 0 && (
                <div>
                  <Text size="sm" fw={500} mb="sm">Test Parameters</Text>
                  <Stack gap="xs">
                    {selectedTest.parameters.map((param) => (
                      <Group key={param.id} justify="space-between" p="sm" 
                             style={{ backgroundColor: '#f8f9fa', borderRadius: '6px' }}>
                        <Text size="sm" fw={500}>{param.parameterName}</Text>
                        <Text size="sm" c="dimmed">
                          {param.normalRange} {param.unit}
                        </Text>
                      </Group>
                    ))}
                  </Stack>
                </div>
              )}

              <Group justify="flex-end">
                <Button variant="light" onClick={closeTestDetail}>
                  Close
                </Button>
                <Button>
                  Edit Test
                </Button>
              </Group>
            </Stack>
          </ScrollArea>
        )}
      </Modal>

      {/* Add Test Modal */}
      <Modal
        opened={addTestOpened}
        onClose={closeAddTest}
        title="Add New Test"
        size="lg"
      >
        <Stack gap="md">
          <SimpleGrid cols={2}>
            <TextInput
              label="Test Name"
              placeholder="Enter test name"
              required
            />
            <TextInput
              label="Test Code"
              placeholder="Enter test code"
              required
            />
          </SimpleGrid>
          
          <SimpleGrid cols={2}>
            <Select
              label="Category"
              placeholder="Select category"
              data={[
                { value: 'hematology', label: 'Hematology' },
                { value: 'biochemistry', label: 'Biochemistry' },
                { value: 'microbiology', label: 'Microbiology' },
                { value: 'immunology', label: 'Immunology' },
                { value: 'pathology', label: 'Pathology' },
                { value: 'molecular', label: 'Molecular' },
                { value: 'genetics', label: 'Genetics' }
              ]}
              required
            />
            <Select
              label="Test Type"
              placeholder="Select type"
              data={[
                { value: 'routine', label: 'Routine' },
                { value: 'urgent', label: 'Urgent' },
                { value: 'stat', label: 'STAT' },
                { value: 'research', label: 'Research' }
              ]}
              required
            />
          </SimpleGrid>
          
          <Textarea
            label="Description"
            placeholder="Enter test description"
            rows={3}
          />
          
          <SimpleGrid cols={3}>
            <NumberInput
              label="Price"
              placeholder="Enter price"
              leftSection="‚Çπ"
              min={0}
              required
            />
            <TextInput
              label="Turnaround Time"
              placeholder="e.g., 2-4 hours"
              required
            />
            <Select
              label="Sample Type"
              placeholder="Select sample type"
              data={[
                { value: 'blood', label: 'Blood' },
                { value: 'urine', label: 'Urine' },
                { value: 'stool', label: 'Stool' },
                { value: 'sputum', label: 'Sputum' },
                { value: 'tissue', label: 'Tissue' }
              ]}
              required
            />
          </SimpleGrid>
          
          <TextInput
            label="Sample Volume"
            placeholder="e.g., 5ml"
            required
          />
          
          <Group justify="flex-end">
            <Button variant="light" onClick={closeAddTest}>
              Cancel
            </Button>
            <Button onClick={() => {
              notifications.show({
                title: 'Success',
                message: 'Test added successfully',
                color: 'green',
              });
              closeAddTest();
            }}>
              Add Test
            </Button>
          </Group>
        </Stack>
      </Modal>
    </Container>
  );
};

export default LaboratoryManagement;

'use client';

import { ReactNode, useState, useEffect, Suspense } from 'react';
import { usePathname, useRouter } from 'next/navigation';
import dynamic from 'next/dynamic';
import LoadingSpinner from '@/components/ui/LoadingSpinner';
import {
  AppShell,
  AppShellNavbar,
  AppShellHeader,
  Group,
  Text,
  Button,
  UnstyledButton,
  Tooltip,
  Badge,
  Avatar,
  Menu,
  ActionIcon,
  NavLink,
  ScrollArea
} from '@mantine/core';
import {
  IconLayoutDashboard,
  IconUsers,
  IconCalendarEvent,
  IconStethoscope,
  IconBuildingHospital,
  IconAmbulance,
  IconFlask,
  IconMicroscope,
  IconPills,
  IconVaccine,
  IconScissors,
  IconShield,
  IconCalculator,
  IconCash,
  IconShieldCheck,
  IconUser,
  IconHeart,
  IconPackage,
  IconDeviceComputerCamera,
  IconKey,
  IconMessage,
  IconChartBar,
  IconSearch,
  IconPlug,
  IconRobot,
  IconChevronDown,
  IconLogout,
  IconSettings,
  IconBell,
  IconUserCircle
} from '@tabler/icons-react';
import { UserRole } from '@/lib/rbac';

interface DashboardLayoutProps {
  children: ReactNode;
}

export default function DashboardLayout({ children }: DashboardLayoutProps) {
  const pathname = usePathname();
