const PatientPortal = () => {
  // Current logged-in patient (mock data)
  const currentPatient = []; // TODO: Fetch from API
  
  // State management
  const [activeTab, setActiveTab] = useState<string>('dashboard');
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedDate, setSelectedDate] = useState<Date | null>(new Date());
  const [selectedAppointmentType, setSelectedAppointmentType] = useState<string>('');
  const [selectedStatus, setSelectedStatus] = useState<string>('');
  const [selectedDoctor, setSelectedDoctor] = useState<string>('');
  const [selectedAppointment, setSelectedAppointment] = useState<any | null>(null);
  const [selectedPrescription, setSelectedPrescription] = useState<any | null>(null);
  const [selectedTestResult, setSelectedTestResult] = useState<any | null>(null);
  const [selectedMedicalRecord, setSelectedMedicalRecord] = useState<any | null>(null);
  const [newMessage, setNewMessage] = useState('');

  // API data state
  const [appointments, setAppointments] = useState<any[]>([]);
  const [prescriptions, setPrescriptions] = useState<any[]>([]);
  const [labResults, setLabResults] = useState<any[]>([]);
  const [medicalRecords, setMedicalRecords] = useState<any[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    fetchAllData();
  }, []);

  const fetchAllData = async () => {
    try {
      setLoading(true);
      setError(null);
      await Promise.all([
        fetchAppointments(),
        fetchPrescriptions(),
        fetchLabResults(),
        fetchMedicalRecords()
      ]);
    } catch (err: any) {
      console.error('Error loading patient portal data:', err);
      setError(err.response?.data?.message || 'Failed to load data');
      setAppointments([] /* TODO: Fetch from API */);
      setPrescriptions([] /* TODO: Fetch from API */);
      setLabResults([] /* TODO: Fetch from API */);
      setMedicalRecords([] /* TODO: Fetch from API */);
    } finally {
      setLoading(false);
    }
  };

  const fetchAppointments = async () => {
    try {
      const response = await patientPortalService.getAppointments();
      setAppointments(response.data || []);
    } catch (err) {
      console.error('Error fetching appointments:', err);
      setAppointments([] /* TODO: Fetch from API */);
    }
  };

  const fetchPrescriptions = async () => {
    try {
      const response = await patientPortalService.getPrescriptions();
      setPrescriptions(response.data || []);
    } catch (err) {
      console.error('Error fetching prescriptions:', err);
      setPrescriptions([] /* TODO: Fetch from API */);
    }
  };

  const fetchLabResults = async () => {
    try {
      const response = await patientPortalService.getLabResults();
      setLabResults(response.data || []);
    } catch (err) {
      console.error('Error fetching lab results:', err);
      setLabResults([] /* TODO: Fetch from API */);
    }
  };

  const fetchMedicalRecords = async () => {
    try {
      const response = await patientPortalService.getMedicalRecords();
      setMedicalRecords(response.data || []);
    } catch (err) {
      console.error('Error fetching medical records:', err);
      setMedicalRecords([] /* TODO: Fetch from API */);
    }
  };

  // Modal states
  const [appointmentDetailOpened, { open: openAppointmentDetail, close: closeAppointmentDetail }] = useDisclosure(false);
  const [bookAppointmentOpened, { open: openBookAppointment, close: closeBookAppointment }] = useDisclosure(false);
  const [prescriptionDetailOpened, { open: openPrescriptionDetail, close: closePrescriptionDetail }] = useDisclosure(false);
  const [testResultDetailOpened, { open: openTestResultDetail, close: closeTestResultDetail }] = useDisclosure(false);
  const [medicalRecordDetailOpened, { open: openMedicalRecordDetail, close: closeMedicalRecordDetail }] = useDisclosure(false);
  const [profileSettingsOpened, { open: openProfileSettings, close: closeProfileSettings }] = useDisclosure(false);

  // Filter appointments
  const filteredAppointments = useMemo(() => {
    return appointments.filter((appointment) => {
      const matchesSearch = 
        (appointment.doctor?.toLowerCase().includes(searchQuery.toLowerCase()) || false) ||
        (appointment.department?.toLowerCase().includes(searchQuery.toLowerCase()) || false) ||
        (appointment.type?.toLowerCase().includes(searchQuery.toLowerCase()) || false);
      
      const matchesType = !selectedAppointmentType || appointment.type === selectedAppointmentType;
      const matchesStatus = !selectedStatus || appointment.status === selectedStatus;
      const matchesDoctor = !selectedDoctor || appointment.doctor === selectedDoctor;

      return matchesSearch && matchesType && matchesStatus && matchesDoctor;
    });
  }, [searchQuery, selectedAppointmentType, selectedStatus, selectedDoctor]);

  // Helper functions
  const getStatusColor = (status: string) => {
    switch (status) {
      case 'scheduled': return 'blue';
      case 'confirmed': return 'green';
      case 'in_progress': return 'orange';
      case 'completed': return 'teal';
      case 'cancelled': return 'red';
      case 'no_show': return 'gray';
      default: return 'gray';
    }
  };

  const getAppointmentTypeColor = (type: string) => {
    switch (type) {
      case 'consultation': return 'blue';
      case 'follow_up': return 'green';
      case 'emergency': return 'red';
      case 'procedure': return 'purple';
      case 'diagnostic': return 'orange';
      case 'vaccination': return 'teal';
      default: return 'gray';
    }
  };

  const getPriorityColor = (priority: string) => {
    switch (priority) {
      case 'urgent': return 'red';
      case 'high': return 'orange';
      case 'normal': return 'blue';
      case 'low': return 'gray';
      default: return 'gray';
    }
  };

  const handleViewAppointment = (appointment: any) => {
    setSelectedAppointment(appointment);
    openAppointmentDetail();
  };

  const handleViewPrescription = (prescription: any) => {
    setSelectedPrescription(prescription);
    openPrescriptionDetail();
  };

  const handleViewTestResult = (testResult: any) => {
    setSelectedTestResult(testResult);
    openTestResultDetail();
  };

  const handleViewMedicalRecord = (record: any) => {
    setSelectedMedicalRecord(record);
    openMedicalRecordDetail();
  };

  const handleSendMessage = () => {
    if (newMessage.trim()) {
      notifications.show({
        title: 'Message Sent',
        message: 'Your message has been sent to your healthcare provider',
        color: 'green',
      });
      setNewMessage('');
    }
  };

  const formatDate = (date: string | Date) => {
    const d = new Date(date);
    const year = d.getFullYear();
    const month = String(d.getMonth() + 1).padStart(2, '0');
    const day = String(d.getDate()).padStart(2, '0');
    return `${year}-${month}-${day}`;
  };

  const formatDateTime = (date: string | Date) => {
    const d = new Date(date);
    const year = d.getFullYear();
    const month = String(d.getMonth() + 1).padStart(2, '0');
    const day = String(d.getDate()).padStart(2, '0');
    const hours = String(d.getHours()).padStart(2, '0');
    const minutes = String(d.getMinutes()).padStart(2, '0');
    return `${year}-${month}-${day} ${hours}:${minutes}`;
  };

  // Quick stats for dashboard
  const quickStats = [
    {
      title: 'Upcoming Appointments',
      value: 0 /* TODO: Fetch from API */,
