    {
      title: 'Active Prescriptions',
      value: 0 /* TODO: Fetch from API */,
      icon: IconPill,
      color: 'green'
    },
    {
      title: 'Test Results',
      value: 0 /* TODO: Fetch from API */,
      icon: IconFlask,
      color: 'orange'
    },
    {
      title: 'Medical Records',
      value: 0 /* TODO: Fetch from API */,
      icon: IconMessage,
      color: 'red'
    }
  ];

  return (
    <Container size="xl" py="md">
      {/* Header */}
      <Group justify="space-between" mb="lg">
        <div>
          <Group>
            <Avatar 
              size="lg" 
              color="blue"
            >
              PT
            </Avatar>
            <div>
              <Title order={1}>Welcome, Patient!</Title>
              <Text c="dimmed" size="sm">
                Patient Portal Dashboard
              </Text>
            </div>
          </Group>
        </div>
        <Group>
          <Indicator inline processing color="red" size={12} disabled={false}>
            <ActionIcon variant="light" size="lg" color="blue">
              <IconBell size={20} />
            </ActionIcon>
          </Indicator>
          <Button
            leftSection={<IconCalendarEvent size={16} />}
            onClick={openBookAppointment}
            color="blue"
          >
            Book Appointment
          </Button>
          <Button
            variant="light"
            leftSection={<IconSettings size={16} />}
            onClick={openProfileSettings}
          >
            Profile Settings
          </Button>
        </Group>
      </Group>

      {/* Quick Stats */}
      <SimpleGrid cols={{ base: 1, sm: 2, md: 4 }} mb="lg">
        {quickStats.map((stat) => {
          const Icon = stat.icon;
          return (
            <Card key={stat.title} padding="lg" radius="md" withBorder>
              <Group justify="space-between">
                <div>
                  <Text c="dimmed" size="sm" fw={500}>
                    {stat.title}
                  </Text>
                  <Text fw={700} size="xl">
                    {stat.value}
                  </Text>
                </div>
                <ThemeIcon color={stat.color} size="xl" radius="md" variant="light">
                  <Icon size={24} />
                </ThemeIcon>
              </Group>
            </Card>
          );
        })}
      </SimpleGrid>

      {/* Main Content Tabs */}
      <Tabs value={activeTab} onChange={setActiveTab}>
        <Tabs.List>
          <Tabs.Tab value="dashboard" leftSection={<IconChartBar size={16} />}>
            Dashboard
          </Tabs.Tab>
          <Tabs.Tab value="appointments" leftSection={<IconCalendar size={16} />}>
            Appointments
          </Tabs.Tab>
          <Tabs.Tab value="prescriptions" leftSection={<IconPill size={16} />}>
            Prescriptions
          </Tabs.Tab>
          <Tabs.Tab value="test-results" leftSection={<IconFlask size={16} />}>
            Test Results
          </Tabs.Tab>
          <Tabs.Tab value="medical-records" leftSection={<IconFileText size={16} />}>
            Medical Records
          </Tabs.Tab>
          <Tabs.Tab value="messages" leftSection={<IconMessage size={16} />}>
            <Group gap="xs">
              Messages
            </Group>
          </Tabs.Tab>
        </Tabs.List>

        {/* Dashboard Tab */}
        <Tabs.Panel value="dashboard">
          <SimpleGrid cols={{ base: 1, lg: 2 }} spacing="lg" mt="md">
            {/* Recent Notifications */}
            <Card padding="lg" radius="md" withBorder>
              <Title order={4} mb="md">Recent Notifications</Title>
              <Stack gap="sm">
                {0 /* TODO: Fetch from API */(0, 5).map((notification) => (
                  <Alert
                    key={notification.id}
                    variant="light"
                    color={getPriorityColor(notification.priority)}
                    icon={
                      notification.type === 'appointment' ? <IconCalendar size={16} /> :
