            <SimpleGrid cols={{ base: 1, lg: 2 }} spacing="lg">
              {filteredAppointments.length === 0 ? (
                <div style={{ gridColumn: '1 / -1' }}>
                  <EmptyState
                    icon={<IconUserCircle size={48} />}
                    title="No portal activity"
                    description="Patient portal activity will appear here"
                    size="sm"
                  />
                </div>
              ) : (
                filteredAppointments.map((appointment) => (
                  <Card key={appointment.id} padding="lg" radius="md" withBorder>
                    <Group justify="space-between" mb="md">
                      <div>
                        <Text fw={600} size="lg">{appointment.doctor}</Text>
                        <Text size="sm" c="dimmed">{appointment.department}</Text>
                      </div>
                      <Badge color={getStatusColor(appointment.status)} variant="light">
                        {appointment.status.replace('_', ' ').toUpperCase()}
                      </Badge>
                    </Group>

                  <Stack gap="sm" mb="md">
                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Date & Time</Text>
                      <Text size="sm" fw={500}>
                        {formatDate(appointment.date)} at {appointment.time}
                      </Text>
                    </Group>
                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Type</Text>
                      <Badge color={getAppointmentTypeColor(appointment.type)} variant="light" size="sm">
                        {appointment.type.replace('_', ' ')}
                      </Badge>
                    </Group>
                  </Stack>

                  <Group justify="space-between">
                    <Text size="xs" c="dimmed">
                      {appointment.department}
                    </Text>
                    <Group gap="xs">
                      <ActionIcon
                        variant="subtle"
                        color="blue"
                        onClick={() => handleViewAppointment(appointment)}
                      >
                        <IconEye size={16} />
                      </ActionIcon>
                      {appointment.status === 'scheduled' && (
                        <ActionIcon variant="subtle" color="green">
                          <IconEdit size={16} />
                        </ActionIcon>
                      )}
                      {(appointment.status === 'scheduled' || appointment.status === 'confirmed') && (
                        <ActionIcon variant="subtle" color="red">
                          <IconX size={16} />
                        </ActionIcon>
                      )}
                    </Group>
                  </Group>
                </Card>
              )))
              }
            </SimpleGrid>
          </Paper>
        </Tabs.Panel>

        {/* Prescriptions Tab */}
        <Tabs.Panel value="prescriptions">
          <Paper p="md" radius="md" withBorder mt="md">
            <Group justify="space-between" mb="lg">
              <Title order={3}>My Prescriptions</Title>
              <Button leftSection={<IconDownload size={16} />} variant="light">
                Download All
              </Button>
            </Group>

            {/* Prescriptions Grid */}
            <SimpleGrid cols={{ base: 1, lg: 2 }} spacing="lg">
              {[].map /* TODO: Fetch from API */((prescription) => (
                <Card key={prescription.id} padding="lg" radius="md" withBorder>
                  <Group justify="space-between" mb="md">
                    <div>
                      <Text fw={600} size="lg">{prescription.medicationName}</Text>
                      <Text size="sm" c="dimmed">
                        Prescribed by: Dr. {prescription.doctorName}
                      </Text>
                    </div>
                    <Badge 
                      color={prescription.isActive ? 'green' : 'gray'} 
                      variant="light"
                    >
                      {prescription.isActive ? 'Active' : 'Inactive'}
                    </Badge>
                  </Group>

                  <Stack gap="sm" mb="md">
                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Dosage</Text>
                      <Text size="sm" fw={500}>{prescription.dosage}</Text>
                    </Group>
                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Frequency</Text>
                      <Text size="sm">{prescription.frequency}</Text>
                    </Group>
                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Duration</Text>
                      <Text size="sm">{prescription.duration}</Text>
                    </Group>
                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Refills Left</Text>
                      <Text size="sm" fw={500} c={prescription.refillsLeft === 0 ? 'red' : 'blue'}>
                        {prescription.refillsLeft}
                      </Text>
                    </Group>
                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Prescribed Date</Text>
                      <Text size="sm">{formatDate(prescription.prescribedDate)}</Text>
                    </Group>
                  </Stack>

                  {prescription.instructions && (
                    <Text size="sm" c="dimmed" lineClamp={2} mb="md">
                      Instructions: {prescription.instructions}
                    </Text>
                  )}

                  <Group justify="space-between">
                    <Text size="xs" c="dimmed">
                      Pharmacy: {prescription.pharmacyName || 'Any Pharmacy'}
                    </Text>
                    <Group gap="xs">
                      <ActionIcon
                        variant="subtle"
                        color="blue"
                        onClick={() => handleViewPrescription(prescription)}
                      >
                        <IconEye size={16} />
                      </ActionIcon>
                      <ActionIcon variant="subtle" color="green">
                        <IconDownload size={16} />
                      </ActionIcon>
                      {prescription.refillsLeft > 0 && prescription.isActive && (
                        <ActionIcon variant="subtle" color="orange">
                          <IconRefresh size={16} />
                        </ActionIcon>
                      )}
                    </Group>
                  </Group>
                </Card>
              ))}
            </SimpleGrid>
          </Paper>
        </Tabs.Panel>

        {/* Test Results Tab */}
        <Tabs.Panel value="test-results">
          <Paper p="md" radius="md" withBorder mt="md">
            <Group justify="space-between" mb="lg">
              <Title order={3}>Test Results</Title>
              <Button leftSection={<IconDownload size={16} />} variant="light">
                Download All
              </Button>
            </Group>

            {/* Test Results Grid */}
            <SimpleGrid cols={{ base: 1, lg: 2 }} spacing="lg">
              {[].map /* TODO: Fetch from API */((result) => (
                <Card key={result.id} padding="lg" radius="md" withBorder>
                  <Group justify="space-between" mb="md">
                    <div>
                      <Text fw={600} size="lg">{result.testName}</Text>
                      <Text size="sm" c="dimmed">
                        {result.category} - {result.testCode}
                      </Text>
                    </div>
                    <Badge 
                      color={
                        result.status === 'completed' ? 'green' : 
                        result.status === 'pending' ? 'orange' : 
                        result.status === 'in_progress' ? 'blue' : 'gray'
                      } 
                      variant="light"
                    >
                      {result.status.replace('_', ' ').toUpperCase()}
                    </Badge>
                  </Group>

                  <Stack gap="sm" mb="md">
                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Test Date</Text>
                      <Text size="sm" fw={500}>{formatDate(result.testDate)}</Text>
                    </Group>
                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Ordered by</Text>
                      <Text size="sm">Dr. {result.orderedBy}</Text>
                    </Group>
                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Lab/Facility</Text>
                      <Text size="sm">{result.labName}</Text>
                    </Group>
                    {result.status === 'completed' && result.resultDate && (
                      <Group justify="space-between">
                        <Text size="sm" c="dimmed">Result Date</Text>
                        <Text size="sm">{formatDate(result.resultDate)}</Text>
                      </Group>
                    )}
                    {result.priority && (
                      <Group justify="space-between">
                        <Text size="sm" c="dimmed">Priority</Text>
                        <Badge color={getPriorityColor(result.priority)} variant="light" size="sm">
                          {result.priority.toUpperCase()}
                        </Badge>
                      </Group>
                    )}
                  </Stack>

                  {result.notes && (
                    <Text size="sm" c="dimmed" lineClamp={2} mb="md">
                      Notes: {result.notes}
                    </Text>
                  )}

                  <Group justify="space-between">
                    <Text size="xs" c="dimmed">
                      Reference ID: {result.id.slice(-8).toUpperCase()}
                    </Text>
                    <Group gap="xs">
                      <ActionIcon
                        variant="subtle"
                        color="blue"
                        onClick={() => handleViewTestResult(result)}
                      >
                        <IconEye size={16} />
                      </ActionIcon>
                      {result.status === 'completed' && (
                        <>
                          <ActionIcon variant="subtle" color="green">
                            <IconDownload size={16} />
                          </ActionIcon>
                          <ActionIcon variant="subtle" color="orange">
                            <IconShare size={16} />
                          </ActionIcon>
                        </>
                      )}
                    </Group>
                  </Group>
                </Card>
              ))}
            </SimpleGrid>
          </Paper>
        </Tabs.Panel>

        {/* Medical Records Tab */}
        <Tabs.Panel value="medical-records">
          <Paper p="md" radius="md" withBorder mt="md">
            <Group justify="space-between" mb="lg">
              <Title order={3}>Medical Records</Title>
              <Button leftSection={<IconDownload size={16} />} variant="light">
                Export Records
              </Button>
            </Group>

            {/* Medical Records Timeline */}
            <Timeline active={0 /* TODO: Fetch from API */} bulletSize={24} lineWidth={2}>
              {[].map /* TODO: Fetch from API */((record, index) => (
                <Timeline.Item
                  key={record.id}
                  bullet={
                    <ThemeIcon
                      size={24}
                      variant="filled"
                      color={
                        record.type === 'consultation' ? 'blue' :
                        record.type === 'diagnosis' ? 'red' :
                        record.type === 'treatment' ? 'green' :
                        record.type === 'surgery' ? 'purple' :
                        record.type === 'vaccination' ? 'teal' : 'gray'
                      }
                    >
                      {record.type === 'consultation' ? <IconStethoscope size={14} /> :
                       record.type === 'diagnosis' ? <IconReportMedical size={14} /> :
                       record.type === 'treatment' ? <IconPill size={14} /> :
                       record.type === 'surgery' ? <IconCut size={14} /> :
                       record.type === 'vaccination' ? <IconPill size={14} /> :
                       <IconFileText size={14} />}
                    </ThemeIcon>
                  }
                  title={
                    <Group justify="space-between">
                      <Text fw={500}>{record.title}</Text>
                      <Text size="sm" c="dimmed">{formatDate(record.date)}</Text>
                    </Group>
                  }
                >
                  <Card withBorder p="md" mb="md">
                    <Group justify="space-between" mb="sm">
                      <div>
                        <Text size="sm" c="dimmed">Provider: Dr. {record.providerName}</Text>
                        <Text size="sm" c="dimmed">{record.department}</Text>
                      </div>
                      <Badge
                        color={
                          record.type === 'consultation' ? 'blue' :
                          record.type === 'diagnosis' ? 'red' :
                          record.type === 'treatment' ? 'green' :
                          record.type === 'surgery' ? 'purple' :
                          record.type === 'vaccination' ? 'teal' : 'gray'
                        }
                        variant="light"
                      >
                        {record.type.toUpperCase()}
                      </Badge>
                    </Group>
                    
                    <Text size="sm" lineClamp={3} mb="sm">
                      {record.description}
                    </Text>
                    
                    {record.diagnosis && (
                      <Text size="sm" c="red" mb="sm">
                        Diagnosis: {record.diagnosis}
                      </Text>
                    )}
                    
                    {record.treatment && (
                      <Text size="sm" c="green" mb="sm">
                        Treatment: {record.treatment}
                      </Text>
                    )}
                    
                    <Group justify="space-between" mt="sm">
                      <Text size="xs" c="dimmed">
                        Visit ID: {record.id.slice(-8).toUpperCase()}
                      </Text>
                      <Group gap="xs">
                        <ActionIcon
                          variant="subtle"
                          color="blue"
                          onClick={() => handleViewMedicalRecord(record)}
                        >
                          <IconEye size={16} />
                        </ActionIcon>
                        <ActionIcon variant="subtle" color="green">
                          <IconDownload size={16} />
                        </ActionIcon>
                      </Group>
                    </Group>
                  </Card>
                </Timeline.Item>
              ))}
            </Timeline>
          </Paper>
        </Tabs.Panel>

        {/* Messages Tab */}
        <Tabs.Panel value="messages">
          <SimpleGrid cols={{ base: 1, lg: 3 }} spacing="lg" mt="md">
            {/* Message List */}
            <div style={{ gridColumn: 'span 2' }}>
              <Paper p="md" radius="md" withBorder>
                <Title order={4} mb="md">Messages</Title>
                <Stack gap="sm">
                  {[].map /* TODO: Fetch from API */((communication) => (
                    <Card key={communication.id} padding="md" withBorder>
                      <Group justify="space-between" mb="sm">
                        <Group>
                          <Avatar size="sm" color="blue">
                            {communication.senderType === 'doctor' ? 'DR' : 'ME'}
                          </Avatar>
                          <div>
                            <Text fw={500} size="sm">
                              {communication.senderType === 'doctor' 
                                ? `Dr. ${communication.senderName}` 
                                : 'You'}
                            </Text>
                            <Text size="xs" c="dimmed">
                              {communication.subject}
                            </Text>
                          </div>
                        </Group>
                        <div style={{ textAlign: 'right' }}>
                          <Text size="xs" c="dimmed">
                            {formatDateTime(communication.sentDate)}
                          </Text>
                          {!communication.isRead && communication.senderType === 'doctor' && (
                            <Badge size="xs" color="red">New</Badge>
                          )}
                        </div>
                      </Group>
                      
                      <Text size="sm" lineClamp={2} mb="sm">
                        {communication.message}
                      </Text>
                      
                      <Group justify="space-between">
                        <Badge 
                          variant="light" 
                          color={
                            communication.type === 'appointment' ? 'blue' :
                            communication.type === 'prescription' ? 'green' :
                            communication.type === 'test_result' ? 'orange' :
                            communication.type === 'general' ? 'purple' : 'gray'
                          }
                          size="xs"
                        >
                          {communication.type.replace('_', ' ')}
                        </Badge>
                        <Group gap="xs">
                          <ActionIcon variant="subtle" color="blue" size="sm">
                            <IconEye size={14} />
                          </ActionIcon>
                          <ActionIcon variant="subtle" color="green" size="sm">
                            <IconMessage size={14} />
                          </ActionIcon>
                        </Group>
                      </Group>
                    </Card>
                  ))}
                </Stack>
              </Paper>
            </div>

            {/* Compose Message */}
            <Paper p="md" radius="md" withBorder>
              <Title order={4} mb="md">Send Message</Title>
              <Stack gap="md">
                <Select
                  label="To"
                  placeholder="Select healthcare provider"
                  data={[].map /* TODO: Fetch from API */(doctor => ({
                    value: doctor.id,
                    label: `${doctor.name} - ${doctor.specialization}`
                  }))}
                />
                
                <TextInput
                  label="Subject"
                  placeholder="Enter subject"
                />
                
                <Textarea
                  label="Message"
                  placeholder="Type your message here..."
                  rows={6}
                  value={newMessage}
                  onChange={(event) => setNewMessage(event.currentTarget.value)}
                />
                
                <Button 
                  leftSection={<IconSend size={16} />}
                  onClick={handleSendMessage}
                  disabled={!newMessage.trim()}
                >
                  Send Message
                </Button>
              </Stack>
            </Paper>
          </SimpleGrid>
        </Tabs.Panel>
      </Tabs>

      {/* Book Appointment Modal */}
      <Modal
        opened={bookAppointmentOpened}
        onClose={closeBookAppointment}
        title="Book New Appointment"
        size="lg"
      >
        <Stack gap="md">
          <SimpleGrid cols={2}>
            <Select
              label="Doctor"
              placeholder="Select doctor"
              data={[].map /* TODO: Fetch from API */(doctor => ({
                value: doctor.id,
                label: `${doctor.name} - ${doctor.specialization}`
              }))}
              required
            />
            <Select
              label="Appointment Type"
              placeholder="Select type"
              data={[
                { value: 'consultation', label: 'Consultation' },
                { value: 'follow_up', label: 'Follow-up' },
                { value: 'emergency', label: 'Emergency' },
                { value: 'procedure', label: 'Procedure' },
                { value: 'diagnostic', label: 'Diagnostic' },
                { value: 'vaccination', label: 'Vaccination' }
              ]}
              required
            />
          </SimpleGrid>
          
          <SimpleGrid cols={2}>
            <DatePickerInput
              label="Preferred Date"
              placeholder="Select date"
              minDate={new Date()}
              required
            />
            <Select
              label="Time Slot"
              placeholder="Select time"
              data={[
                { value: '09:00', label: '9:00 AM' },
                { value: '10:00', label: '10:00 AM' },
                { value: '11:00', label: '11:00 AM' },
                { value: '14:00', label: '2:00 PM' },
                { value: '15:00', label: '3:00 PM' },
                { value: '16:00', label: '4:00 PM' }
              ]}
              required
            />
          </SimpleGrid>
          
          <Textarea
            label="Reason for Visit"
            placeholder="Describe your symptoms or reason for appointment"
            rows={3}
            required
          />
          
          <Textarea
            label="Additional Notes"
            placeholder="Any additional information (optional)"
            rows={2}
          />
          
          <Group justify="flex-end">
            <Button variant="light" onClick={closeBookAppointment}>
              Cancel
            </Button>
            <Button onClick={() => {
              notifications.show({
                title: 'Appointment Requested',
                message: 'Your appointment request has been submitted for approval',
                color: 'green',
              });
              closeBookAppointment();
            }}>
              Book Appointment
            </Button>
          </Group>
        </Stack>
      </Modal>
    </Container>
  );
};

export default PatientPortal;

'use client';

import React, { useState, useEffect } from 'react';
import {
  Container,
  Text,
  Group,
  Badge,
  SimpleGrid,
  Stack,
  Button,
  Card,
  Avatar,
  Title,
  Alert
} from '@mantine/core';
import { useDisclosure } from '@mantine/hooks';
import {
  IconUsers,
  IconPhone,
  IconCalendar,
  IconHeart,
  IconAlertCircle,
  IconShield,
  IconUserPlus,
  IconSearch,
  IconChartBar,
  IconFileExport,
  IconUser
} from '@tabler/icons-react';
import DataTable from '../../../components/shared/DataTable';
import PatientForm from '../../../components/patients/PatientForm';
import PatientDetails from '../../../components/patients/PatientDetails';
import MedicalHistoryManager from '../../../components/patients/MedicalHistoryManager';
import DocumentManager from '../../../components/patients/DocumentManager';
import PatientSearch from '../../../components/patients/PatientSearch';
import PatientAnalytics from '../../../components/patients/PatientAnalytics';
import PatientExportReport from '../../../components/patients/PatientExportReport';
import PatientPortalAccess from '../../../components/patients/PatientPortalAccess';
import { useAppStore } from '../../../stores/appStore';
import { User, UserRole, TableColumn, FilterOption, Status } from '../../../types/common';
import { Patient, PatientStats, PatientListItem, CreatePatientDto, UpdatePatientDto, PatientSearchParams, InsuranceInfo } from '../../../types/patient';
import { formatDate, formatPhoneNumber } from '../../../lib/utils';
import { notifications } from '@mantine/notifications';
import patientsService from '../../../services/patients.service';

// Mock user
const mockUser: User = {
  id: '1',
  username: 'sjohnson',
  email: 'sarah.johnson@hospital.com',
  firstName: 'Sarah',
  lastName: 'Johnson',
  role: UserRole.DOCTOR,
  permissions: [],
  isActive: true,
  tenantInfo: {
    tenantId: 'T001',
    tenantName: 'Main Hospital',
  },
  createdAt: new Date(),
  updatedAt: new Date(),
};

export default function PatientManagement() {
  const { user, setUser } = useAppStore();
  const [patients, setPatients] = useState<Patient[]>([]);
  const [patientStats, setPatientStats] = useState<any>(null);
  const [selectedPatient, setSelectedPatient] = useState<Patient | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [, setSearchQuery] = useState('');
  const [, setFilters] = useState<Record<string, unknown>>({});
  const [opened, { open, close }] = useDisclosure(false);
  const [viewModalOpened, { open: openView, close: closeView }] = useDisclosure(false);
  const [historyModalOpened, { open: openHistory, close: closeHistory }] = useDisclosure(false);
  const [documentsModalOpened, { open: openDocuments, close: closeDocuments }] = useDisclosure(false);
  const [searchModalOpened, { open: openSearch, close: closeSearch }] = useDisclosure(false);
  const [analyticsModalOpened, { open: openAnalytics, close: closeAnalytics }] = useDisclosure(false);
  const [exportModalOpened, { open: openExport, close: closeExport }] = useDisclosure(false);
  const [portalModalOpened, { open: openPortal, close: closePortal }] = useDisclosure(false);

  useEffect(() => {
    if (!user) {
      setUser([] /* TODO: Fetch from API */);
    }
    fetchPatients();
    fetchStats();
  }, [user, setUser]);

  const fetchPatients = async () => {
    try {
      setLoading(true);
      const response = await patientsService.getPatients();
      console.log('Patients API response:', response);
      setPatients(response.data?.patients || []);
      setError(null);
    } catch (err: any) {
      const errorMsg = err.response?.data?.message || err.message || 'Failed to fetch patients';
      console.warn('Error fetching patients (using empty data):', errorMsg);
      // Don't show error to user if backend is not ready, just use empty data
      setError(null);
      setPatients([]);
    } finally {
      setLoading(false);
    }
  };

  const fetchStats = async () => {
    try {
      const response = await patientsService.getPatientStats();
      console.log('Patient stats API response:', response);
      setPatientStats(response.data);
    } catch (err: any) {
      console.warn('Error fetching patient stats (using default values):', err.response?.data?.message || err.message);
      // Set default stats when backend is unavailable
      setPatientStats({ 
        totalPatients: 0, 
        activePatients: 0, 
        todaysPatients: 0, 
        weekPatients: 0,
        averageAge: 0,
        genderDistribution: { male: 0, female: 0, other: 0 },
        insuranceDistribution: { insured: 0, uninsured: 0 }
      });
    }
  };

  // Convert patients to list items for table
  const patientListItems: PatientListItem[] = patients.map(patient => ({
    id: patient.id,
    patientId: patient.patientId,
    fullName: `${patient.firstName} ${patient.lastName}`,
    age: patient.age,
    gender: patient.gender,
    phoneNumber: patient.contactInfo.phone,
    lastVisitDate: patient.lastVisitDate,
    totalVisits: patient.totalVisits,
    status: patient.status,
    hasInsurance: !!patient.insuranceInfo?.isActive,
    emergencyFlag: patient.chronicDiseases.length > 0
  }));

  // Table columns configuration
  const columns: TableColumn[] = [
    {
      key: 'patientId',
      title: 'Patient ID',
      sortable: true,
      width: '120px',
      render: (value) => (
        <Text fw={500} c="blue">
          {value as string}
        </Text>
      )
    },
    {
      key: 'fullName',
      title: 'Patient Name',
      sortable: true,
      render: (value, record) => (
        <Group gap="sm">
          <Avatar size="sm" name={value as string} color="blue" />
          <div>
            <Text fw={500}>{value as string}</Text>
            <Text size="xs" c="dimmed">
              {record.age as string} years ‚Ä¢ {record.gender as string}
            </Text>
          </div>
        </Group>
      )
    },
    {
      key: 'phoneNumber',
      title: 'Contact',
      render: (value) => (
        <div>
          <Group gap="xs">
            <IconPhone size="1rem" />
            <Text size="sm">{formatPhoneNumber(value as string)}</Text>
          </Group>
        </div>
      )
    },
    {
      key: 'lastVisitDate',
      title: 'Last Visit',
      sortable: true,
      render: (value) => value ? formatDate(value as string | Date) : 'Never'
    },
    {
      key: 'totalVisits',
      title: 'Visits',
      sortable: true,
      width: '80px',
      render: (value) => (
        <Badge variant="light" color="blue">
          {value as string}
        </Badge>
      )
    },
    {
      key: 'status',
      title: 'Status',
      render: (value) => (
        <Badge 
          color={value === 'active' ? 'green' : 'red'}
          variant="light"
        >
          {value as string}
        </Badge>
      )
    },
    {
      key: 'hasInsurance',
      title: 'Insurance',
      render: (value, record) => (
        <Group gap="xs">
          {value ? (
            <Badge color="green" variant="light" leftSection={<IconShield size="0.8rem" />}>
              Insured
            </Badge>
          ) : (
            <Badge color="gray" variant="light">
              Self Pay
            </Badge>
          )}
          {record.emergencyFlag && (
            <IconAlertCircle size="1rem" color="red" />
          )}
        </Group>
      )
    }
  ];

  // Filter options
  const filterOptions: FilterOption[] = [
    {
      key: 'status',
      title: 'Status',
      type: 'select',
      options: [
        { value: 'active', title: 'Active' },
        { value: 'inactive', title: 'Inactive' }
      ]
    },
    {
      key: 'gender',
      title: 'Gender',
      type: 'select',
      options: [
        { value: 'male', title: 'Male' },
        { value: 'female', title: 'Female' },
        { value: 'other', title: 'Other' }
      ]
    },
    {
      key: 'hasInsurance',
      title: 'Insurance',
      type: 'select',
      options: [
        { value: 'true', title: 'Insured' },
        { value: 'false', title: 'Self Pay' }
      ]
    }
  ];

  // Handle patient actions
  const handleViewPatient = (patient: PatientListItem) => {
    const fullPatient = patients.find(p => p.id === patient.id);
    if (fullPatient) {
      setSelectedPatient(fullPatient);
      openView();
    }
  };

  const handleEditPatient = (patient: PatientListItem) => {
    const fullPatient = patients.find(p => p.id === patient.id);
    if (fullPatient) {
      setSelectedPatient(fullPatient);
      open();
    }
  };

  const handleDeletePatient = async (patient: PatientListItem) => {
    if (!window.confirm(`Are you sure you want to delete patient ${patient.fullName} (${patient.patientId})?`)) {
      return;
    }

    try {
      await patientsService.deletePatient(patient.id);
      notifications.show({
        title: 'Success',
        message: `Patient ${patient.fullName} deleted successfully!`,
        color: 'green'
      });
      fetchPatients(); // Refresh the list
    } catch (error) {
      notifications.show({
        title: 'Error',
        message: 'Failed to delete patient',
        color: 'red'
      });
    }
  };

  // Patient CRUD operations
  const handleCreatePatient = async (data: CreatePatientDto) => {
    try {
      // Convert Date to string for API
      const apiData = {
        ...data,
        dateOfBirth: data.dateOfBirth instanceof Date 
          ? data.dateOfBirth.toISOString().split('T')[0] 
          : data.dateOfBirth
      };
      const response = await patientsService.createPatient(apiData as any);
      const newPatient = response.data;
      notifications.show({
        title: 'Success',
        message: `Patient ${newPatient.firstName} ${newPatient.lastName} registered successfully!`,
        color: 'green'
      });
      
      // Refresh the patients list and stats
      await fetchPatients();
      await fetchStats();
    } catch (error: any) {
      const errorMsg = error.response?.data?.message || error.message || 'Failed to create patient';
      notifications.show({
        title: 'Error',
        message: errorMsg,
        color: 'red'
      });
      throw error;
    }
  };

  const handleUpdatePatient = async (data: UpdatePatientDto) => {
    try {
      // Convert Date to string for API
      const apiData = {
        ...data,
        dateOfBirth: data.dateOfBirth instanceof Date 
          ? data.dateOfBirth.toISOString().split('T')[0] 
          : data.dateOfBirth
      };
      const response = await patientsService.updatePatient(data.id!, apiData as any);
      const updatedPatient = response.data;
      
      notifications.show({
        title: 'Success',
        message: `Patient ${updatedPatient.firstName} ${updatedPatient.lastName} updated successfully!`,
        color: 'green'
      });
      
      // Refresh the patients list
      await fetchPatients();
    } catch (error: any) {
      const errorMsg = error.response?.data?.message || error.message || 'Failed to update patient';
      notifications.show({
        title: 'Error',
        message: errorMsg,
        color: 'red'
      });
      throw error;
    }
  };

  // Medical history operations
  const handleSaveMedicalHistory = async (history: any) => {
    console.log('Saving medical history:', history);
    await new Promise(resolve => setTimeout(resolve, 1000));
  };

  const handleUpdateMedicalHistory = async (id: string, history: any) => {
    console.log('Updating medical history:', id, history);
    await new Promise(resolve => setTimeout(resolve, 1000));
  };

  const handleDeleteMedicalHistory = async (id: string) => {
    console.log('Deleting medical history:', id);
    await new Promise(resolve => setTimeout(resolve, 1000));
  };

  // Document operations
  const handleUploadDocument = async (document: any, file: File) => {
    console.log('Uploading document:', document, file);
    await new Promise(resolve => setTimeout(resolve, 1000));
  };

  const handleUpdateDocument = async (id: string, document: any) => {
    console.log('Updating document:', id, document);
    await new Promise(resolve => setTimeout(resolve, 1000));
  };

  const handleDeleteDocument = async (id: string) => {
    console.log('Deleting document:', id);
    await new Promise(resolve => setTimeout(resolve, 1000));
  };

  const handleDownloadDocument = async (document: any) => {
    console.log('Downloading document:', document);
    await new Promise(resolve => setTimeout(resolve, 1000));
  };

  const handleViewDocument = async (document: any) => {
    console.log('Viewing document:', document);
    await new Promise(resolve => setTimeout(resolve, 1000));
  };

  // Search operations
  const handleSearch = (criteria: PatientSearchParams) => {
    console.log('Searching patients:', criteria);
    // In real implementation, filter patients based on criteria
  };

  const handleSaveSearch = (name: string, criteria: PatientSearchParams) => {
    console.log('Saving search:', name, criteria);
  };

  // Export operations
  const handleExportPatients = async (options: any) => {
    console.log('Exporting patients:', options);
    await new Promise(resolve => setTimeout(resolve, 2000));
  };

  const handleGenerateReport = async (reportType: string, patientIds?: string[]) => {
    console.log('Generating report:', reportType, patientIds);
    await new Promise(resolve => setTimeout(resolve, 2000));
    return {
      reportType: reportType as 'demographics' | 'visit_summary' | 'medical_summary' | 'insurance_summary',
      patientId: 'all',
      generatedAt: new Date(),
      generatedBy: 'current_user',
      data: {},
      format: 'pdf' as const
    };
  };

  // Portal operations
  const handleEnablePortalAccess = async (patientId: string, preferences: any) => {
    console.log('Enabling portal access:', patientId, preferences);
    await new Promise(resolve => setTimeout(resolve, 1000));
  };

  const handleDisablePortalAccess = async (patientId: string) => {
    console.log('Disabling portal access:', patientId);
    await new Promise(resolve => setTimeout(resolve, 1000));
  };

  const handleUpdatePortalPreferences = async (patientId: string, preferences: any) => {
    console.log('Updating portal preferences:', patientId, preferences);
    await new Promise(resolve => setTimeout(resolve, 1000));
  };

  const handleResetPortalPassword = async (patientId: string) => {
    console.log('Resetting portal password:', patientId);
    await new Promise(resolve => setTimeout(resolve, 1000));
  };

  const handleSendPortalCredentials = async (patientId: string, method: 'email' | 'sms') => {
    console.log('Sending portal credentials:', patientId, method);
    await new Promise(resolve => setTimeout(resolve, 1000));
  };

  // Additional handlers
  const handleScheduleAppointment = (patientId: string) => {
    console.log('Schedule appointment for patient:', patientId);
    // Would navigate to appointment scheduling
  };

  const handleOpenHistory = (patient: PatientListItem) => {
    const fullPatient = patients.find(p => p.id === patient.id);
    if (fullPatient) {
      setSelectedPatient(fullPatient);
      openHistory();
    }
  };

  const handleOpenDocuments = (patient: PatientListItem) => {
    const fullPatient = patients.find(p => p.id === patient.id);
    if (fullPatient) {
      setSelectedPatient(fullPatient);
      openDocuments();
    }
  };

  const handleOpenPortal = (patient: PatientListItem) => {
    const fullPatient = patients.find(p => p.id === patient.id);
    if (fullPatient) {
      setSelectedPatient(fullPatient);
      openPortal();
    }
  };

  const handleNewPatient = () => {
    setSelectedPatient(null);
    open();
  };

  const handleCloseForm = () => {
    setSelectedPatient(null);
    close();
  };

  // Wrapper function for PatientDetails onEdit prop
  const handleEditFromDetails = (patient: Patient) => {
    // Convert Patient to PatientListItem format for the existing handler
    const patientListItem: PatientListItem = {
      id: patient.id,
      patientId: patient.patientId,
      fullName: `${patient.firstName} ${patient.lastName}`,
      age: patient.age,
      gender: patient.gender,
      phoneNumber: patient.contactInfo.phone,
      lastVisitDate: patient.lastVisitDate,
      totalVisits: patient.totalVisits,
      status: patient.status,
      hasInsurance: !!patient.insuranceInfo?.isActive,
      emergencyFlag: patient.chronicDiseases.length > 0
    };
    handleEditPatient(patientListItem);
  };

  // Statistics cards
  const StatCard = ({ title, value, icon, color, subtitle }: { title: string; value: string; icon: React.ReactNode; color: string; subtitle?: string }) => (
    <Card shadow="sm" padding="lg" radius="md" withBorder>
      <Group justify="space-between" mb="md">
        <div style={{ color: `var(--mantine-color-${color}-6)` }}>
          {icon}
        </div>
      </Group>
      
      <Text size="xl" fw={700} mb="xs">
        {value}
      </Text>
      
      <Text size="sm" c="dimmed" mb="sm">
        {title}
      </Text>
      
      {subtitle && (
        <Text size="xs" c="dimmed">
          {subtitle}
        </Text>
      )}
    </Card>
  );

  return (
    <Container fluid>
      <Stack gap="lg">
        {/* Header */}
        <Group justify="space-between">
          <div>
            <Title order={2}>Patient Management</Title>
            <Text c="dimmed">
              Manage patient registration, medical records, and healthcare information
            </Text>
          </div>
          <Group>
            <Button
              variant="outline"
              leftSection={<IconSearch size="1rem" />}
              onClick={openSearch}
            >
              Advanced Search
            </Button>
            <Button
              variant="outline"
              leftSection={<IconChartBar size="1rem" />}
              onClick={openAnalytics}
            >
              Analytics
            </Button>
            <Button
              variant="outline"
              leftSection={<IconFileExport size="1rem" />}
              onClick={openExport}
            >
              Export
            </Button>
            <Button
              leftSection={<IconUserPlus size="1rem" />}
              onClick={handleNewPatient}
            >
              New Patient
            </Button>
          </Group>
        </Group>

        {/* Error Display */}
        {error && (
          <Alert icon={<IconAlertCircle size="1rem" />} title="Error" color="red" variant="light">
            {error}
          </Alert>
        )}

        {/* Statistics Cards */}
        {patientStats && (
          <SimpleGrid cols={{ base: 1, sm: 2, lg: 4 }} spacing="lg">
            <StatCard
              title="Total Patients"
              value={(patientStats.totalPatients || 0).toLocaleString()}
              icon={<IconUsers size="2rem" />}
              color="blue"
              subtitle={patientStats.newPatientsThisMonth ? `+${patientStats.newPatientsThisMonth} this month` : undefined}
            />
            <StatCard
              title="New Today"
              value={(patientStats.newPatientsToday || patientStats.todaysPatients || 0).toString()}
              icon={<IconUserPlus size="2rem" />}
              color="green"
              subtitle="New registrations today"
            />
            <StatCard
              title="Active Patients"
              value={(patientStats.activePatients || 0).toLocaleString()}
              icon={<IconHeart size="2rem" />}
              color="red"
              subtitle="Currently under care"
            />
            <StatCard
              title="Average Age"
              value={patientStats.averageAge ? `${patientStats.averageAge} years` : 'N/A'}
              icon={<IconCalendar size="2rem" />}
              color="purple"
              subtitle="Patient demographics"
            />
          </SimpleGrid>
        )}

        {/* Patient List Table */}
        <DataTable
          data={patientListItems}
          columns={columns}
          loading={loading}
          searchable={true}
          filterable={true}
          sortable={true}
          filters={filterOptions}
          onSearch={(query) => setSearchQuery(query)}
          onFilter={(filters) => setFilters(filters)}
          pagination={{
            page: 1,
            limit: 10,
            total: patientListItems.length,
            onPageChange: (page) => console.log('Page:', page),
            onLimitChange: (limit) => console.log('Limit:', limit)
          }}
          actions={{
            view: handleViewPatient,
            edit: handleEditPatient,
            delete: handleDeletePatient,
            custom: [
              {
                title: 'Documents',
                icon: <IconFileExport size="1rem" />,
                action: (patient: PatientListItem) => handleOpenDocuments(patient)
              },
              {
                title: 'Portal',
                icon: <IconUser size="1rem" />,
                action: (patient: PatientListItem) => handleOpenPortal(patient)
              }
            ]
          }}
          emptyMessage="No patients found"
        />

        {/* Enhanced Patient Details Modal */}
        <PatientDetails
          opened={viewModalOpened}
          onClose={closeView}
          patient={selectedPatient}
          visits={[]}
          documents={[]}
          medicalHistory={[]}
          appointments={[]}
          onEdit={handleEditFromDetails}
          onScheduleAppointment={handleScheduleAppointment}
        />

        {/* Enhanced Patient Form Modal */}
        <PatientForm
          opened={opened}
          onClose={handleCloseForm}
          patient={selectedPatient}
          onSubmit={selectedPatient ? handleUpdatePatient : handleCreatePatient}
        />

        {/* Medical History Manager */}
        {selectedPatient && (
          <MedicalHistoryManager
            opened={historyModalOpened}
            onClose={closeHistory}
            patientId={selectedPatient.patientId}
            patientName={`${selectedPatient.firstName} ${selectedPatient.lastName}`}
            medicalHistory={[]}
            onSave={handleSaveMedicalHistory}
            onUpdate={handleUpdateMedicalHistory}
            onDelete={handleDeleteMedicalHistory}
          />
        )}

        {/* Document Manager */}
        {selectedPatient && (
          <DocumentManager
            opened={documentsModalOpened}
            onClose={closeDocuments}
            patientId={selectedPatient.patientId}
            patientName={`${selectedPatient.firstName} ${selectedPatient.lastName}`}
            documents={[]}
            onUpload={handleUploadDocument}
            onUpdate={handleUpdateDocument}
            onDelete={handleDeleteDocument}
            onDownload={handleDownloadDocument}
            onView={handleViewDocument}
          />
        )}

        {/* Patient Search */}
        <PatientSearch
          opened={searchModalOpened}
          onClose={closeSearch}
          onSearch={handleSearch}
          onSaveSearch={handleSaveSearch}
          currentCriteria={{}}
        />

        {/* Patient Analytics */}
        <PatientAnalytics
          opened={analyticsModalOpened}
          onClose={closeAnalytics}
          patients={patients}
          stats={patientStats}
        />

        {/* Export & Reporting */}
        <PatientExportReport
          opened={exportModalOpened}
          onClose={closeExport}
          onExport={handleExportPatients}
          onGenerateReport={handleGenerateReport}
          patientCount={patients.length}
        />

        {/* Patient Portal Access */}
        {selectedPatient && (
          <PatientPortalAccess
            opened={portalModalOpened}
            onClose={closePortal}
            patient={selectedPatient}
            onEnableAccess={handleEnablePortalAccess}
            onDisableAccess={handleDisablePortalAccess}
            onUpdatePreferences={handleUpdatePortalPreferences}
            onResetPassword={handleResetPortalPassword}
            onSendCredentials={handleSendPortalCredentials}
          />
        )}
      </Stack>
    </Container>
  );
}

