type Accreditation = any;
type AccreditationStatus = any;
type Policy = any;
type PolicyStatus = any;
type ComplianceItem = any;
type ComplianceStatus = any;
type QualityIncident = any;
type IncidentSeverity = any;
type CorrectiveAction = any;
type ActionStatus = any;
type QualityStats = any;
type RiskAssessment = any;
type RiskLevel = any;
type PolicyCategory = any;
type AuditFinding = any;
type FindingSeverity = any;
type QualityIndicator = any;
// Mock data imports removed
import qualityService from '../../../services/quality.service';

const QualityAssurance = () => {
  // State management
  const [activeTab, setActiveTab] = useState<string>('overview');
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedMetricStatus, setSelectedMetricStatus] = useState<string>('');
  const [selectedAuditStatus, setSelectedAuditStatus] = useState<string>('');
  const [selectedPolicyCategory, setSelectedPolicyCategory] = useState<string>('');
  const [selectedRiskLevel, setSelectedRiskLevel] = useState<string>('');
  const [selectedMetric, setSelectedMetric] = useState<QualityMetric | null>(null);
  const [selectedAudit, setSelectedAudit] = useState<Audit | null>(null);
  const [selectedPolicy, setSelectedPolicy] = useState<Policy | null>(null);
  const [selectedIncident, setSelectedIncident] = useState<QualityIncident | null>(null);
  const [selectedAccreditation, setSelectedAccreditation] = useState<Accreditation | null>(null);
  const [selectedRisk, setSelectedRisk] = useState<RiskAssessment | null>(null);

  // API state
  const [qualityMetrics, setQualityMetrics] = useState<any[]>([]);
  const [incidents, setIncidents] = useState<any[]>([]);
  const [qualityStats, setQualityStats] = useState<any>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  // Fetch data
  useEffect(() => {
    fetchAllData();
  }, []);

  const fetchAllData = async () => {
    try {
      setLoading(true);
      setError(null);
      await Promise.all([fetchMetrics(), fetchIncidents(), fetchStats()]);
    } catch (err: any) {
      console.error('Error loading quality data:', err);
      setError(err.response?.data?.message || err.message || 'Failed to load quality data');
    } finally {
      setLoading(false);
    }
  };

  const fetchMetrics = async () => {
    try {
      const response = await qualityService.getMetrics();
      setQualityMetrics(response.data || []);
    } catch (err: any) {
      console.error('Error fetching quality metrics:', err);
      setQualityMetrics([] /* TODO: Fetch from API */);
    }
  };

  const fetchIncidents = async () => {
    try {
      const response = await qualityService.getIncidents();
      setIncidents(response.data || []);
    } catch (err: any) {
      console.error('Error fetching incidents:', err);
      setIncidents([] /* TODO: Fetch from API */);
    }
  };

  const fetchStats = async () => {
    try {
      const response = await qualityService.getStats();
      setQualityStats(response.data);
    } catch (err: any) {
      console.error('Error fetching quality stats:', err);
      setQualityStats([] /* TODO: Fetch from API */);
    }
  };

  // Modal states
  const [metricDetailOpened, { open: openMetricDetail, close: closeMetricDetail }] = useDisclosure(false);
  const [auditDetailOpened, { open: openAuditDetail, close: closeAuditDetail }] = useDisclosure(false);
  const [policyDetailOpened, { open: openPolicyDetail, close: closePolicyDetail }] = useDisclosure(false);
  const [incidentDetailOpened, { open: openIncidentDetail, close: closeIncidentDetail }] = useDisclosure(false);
  const [accreditationDetailOpened, { open: openAccreditationDetail, close: closeAccreditationDetail }] = useDisclosure(false);
  const [riskDetailOpened, { open: openRiskDetail, close: closeRiskDetail }] = useDisclosure(false);
  const [createAuditOpened, { open: openCreateAudit, close: closeCreateAudit }] = useDisclosure(false);
  const [reportIncidentOpened, { open: openReportIncident, close: closeReportIncident }] = useDisclosure(false);
  const [createPolicyOpened, { open: openCreatePolicy, close: closeCreatePolicy }] = useDisclosure(false);

  // Filter functions
  const filteredMetrics = useMemo(() => {
    return [].filter /* TODO: Fetch from API */((metric) => {
      const matchesSearch = 
        metric.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
        metric.description.toLowerCase().includes(searchQuery.toLowerCase());
      
      const matchesStatus = !selectedMetricStatus || metric.status === selectedMetricStatus;

      return matchesSearch && matchesStatus;
    });
  }, [searchQuery, selectedMetricStatus]);

  const filteredAudits = useMemo(() => {
    return [].filter /* TODO: Fetch from API */((audit) => {
      const matchesSearch = 
        ((audit as any).auditType || (audit as any).title || '').toLowerCase().includes(searchQuery.toLowerCase()) ||
        ((audit as any).auditor || '').toLowerCase().includes(searchQuery.toLowerCase()) ||
        ((audit as any).department || '').toLowerCase().includes(searchQuery.toLowerCase());
      
      const matchesStatus = !selectedAuditStatus || (audit as any).status === selectedAuditStatus;

      return matchesSearch && matchesStatus;
    });
  }, [searchQuery, selectedAuditStatus]);

  const filteredPolicies = useMemo(() => {
    return [].filter /* TODO: Fetch from API */((policy) => {
      const matchesSearch = 
        policy.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
        policy.description.toLowerCase().includes(searchQuery.toLowerCase());
      
      const matchesCategory = !selectedPolicyCategory || policy.category === selectedPolicyCategory;

      return matchesSearch && matchesCategory;
    });
  }, [searchQuery, selectedPolicyCategory]);

  // Helper functions
  const getStatusColor = (status: string) => {
    switch (status) {
      case 'compliant':
      case 'approved':
      case 'active':
      case 'completed':
      case 'closed': return 'green';
      case 'non_compliant':
      case 'rejected':
      case 'expired':
      case 'high':
      case 'critical': return 'red';
      case 'pending':
      case 'in_progress':
      case 'scheduled':
      case 'medium':
      case 'moderate': return 'orange';
      case 'draft':
      case 'planned':
      case 'low':
      case 'minor': return 'blue';
      default: return 'gray';
    }
  };

  const getRiskLevelColor = (level: RiskLevel) => {
    switch (level) {
      case 'critical': return 'red';
      case 'high': return 'orange';
      case 'medium': return 'yellow';
      case 'low': return 'green';
      default: return 'gray';
    }
  };

  const getSeverityColor = (severity: IncidentSeverity | FindingSeverity) => {
    switch (severity) {
      case 'critical': return 'red';
      case 'high': return 'orange';
      case 'medium': return 'yellow';
      case 'low': return 'green';
      default: return 'gray';
    }
  };

  const handleViewMetric = (metric: QualityMetric) => {
    setSelectedMetric(metric);
    openMetricDetail();
  };

  const handleViewAudit = (audit: Audit) => {
    setSelectedAudit(audit);
    openAuditDetail();
  };

  const handleViewPolicy = (policy: Policy) => {
    setSelectedPolicy(policy);
    openPolicyDetail();
  };

  const handleViewIncident = (incident: QualityIncident) => {
    setSelectedIncident(incident);
    openIncidentDetail();
  };

  const handleViewAccreditation = (accreditation: Accreditation) => {
    setSelectedAccreditation(accreditation);
    openAccreditationDetail();
  };

  const handleViewRisk = (risk: RiskAssessment) => {
    setSelectedRisk(risk);
    openRiskDetail();
  };

  const formatDate = (date: string) => {
    return new Date(date).toLocaleDateString('en-IN', {
      year: 'numeric',
      month: 'short',
      day: 'numeric'
    });
  };

  const formatDateTime = (date: string) => {
    return new Date(date).toLocaleString('en-IN');
  };

  // Quick stats for overview
  const quickStats = [
    {
      title: 'Overall Compliance',
      value: `${0 /* TODO: Fetch from API */}%`,
      icon: IconShieldCheck,
      color: 'green'
    },
    {
      title: 'Active Audits',
      value: 0 /* TODO: Fetch from API */,
      icon: IconClipboardList,
      color: 'blue'
    },
    {
      title: 'Quality Incidents',
      value: 0 /* TODO: Fetch from API */,
      icon: IconAlertTriangle,
      color: 'orange'
    },
    {
      title: 'Policy Updates',
      value: 0 /* TODO: Fetch from API */,
      icon: IconFileText,
      color: 'purple'
    }
  ];

  return (
    <Container size="xl" py="md">
      {/* Header */}
      <Group justify="space-between" mb="lg">
        <div>
          <Title order={1}>Quality Assurance & Compliance</Title>
          <Text c="dimmed" size="sm">
            Monitor compliance, manage audits, track quality metrics, and ensure regulatory adherence
          </Text>
        </div>
        <Group>
          <Button
            leftSection={<IconPlus size={16} />}
            onClick={openCreateAudit}
            color="blue"
          >
            Schedule Audit
          </Button>
          <Button
            variant="light"
            leftSection={<IconAlertTriangle size={16} />}
            onClick={openReportIncident}
          >
            Report Incident
          </Button>
          <Button
            variant="light"
            leftSection={<IconFileText size={16} />}
            onClick={openCreatePolicy}
          >
            Create Policy
          </Button>
        </Group>
      </Group>

      {/* Quick Stats */}
      <SimpleGrid cols={{ base: 1, sm: 2, md: 4 }} mb="lg">
        {quickStats.map((stat) => {
          const Icon = stat.icon;
          return (
            <Card key={stat.title} padding="lg" radius="md" withBorder>
              <Group justify="space-between">
                <div>
                  <Text c="dimmed" size="sm" fw={500}>
                    {stat.title}
                  </Text>
                  <Text fw={700} size="xl">
                    {stat.value}
                  </Text>
                </div>
                <ThemeIcon color={stat.color} size="xl" radius="md" variant="light">
                  <Icon size={24} />
                </ThemeIcon>
              </Group>
            </Card>
          );
        })}
      </SimpleGrid>

      {/* Main Content Tabs */}
      <Tabs value={activeTab} onChange={setActiveTab}>
        <Tabs.List>
          <Tabs.Tab value="overview" leftSection={<IconChartBar size={16} />}>
            Overview
          </Tabs.Tab>
          <Tabs.Tab value="metrics" leftSection={<IconTarget size={16} />}>
            Quality Metrics
          </Tabs.Tab>
          <Tabs.Tab value="audits" leftSection={<IconClipboardList size={16} />}>
            Audits
          </Tabs.Tab>
          <Tabs.Tab value="compliance" leftSection={<IconShieldCheck size={16} />}>
            Compliance
          </Tabs.Tab>
          <Tabs.Tab value="accreditation" leftSection={<IconCertificate size={16} />}>
            Accreditation
          </Tabs.Tab>
          <Tabs.Tab value="policies" leftSection={<IconFileText size={16} />}>
            Policies
          </Tabs.Tab>
          <Tabs.Tab value="incidents" leftSection={<IconAlertTriangle size={16} />}>
            Incidents
          </Tabs.Tab>
          <Tabs.Tab value="risk" leftSection={<IconAlertCircle size={16} />}>
            Risk Assessment
          </Tabs.Tab>
        </Tabs.List>

        {/* Overview Tab */}
        <Tabs.Panel value="overview">
          <SimpleGrid cols={{ base: 1, lg: 2 }} spacing="lg" mt="md">
            {/* Compliance Score */}
            <Card padding="lg" radius="md" withBorder>
              <Title order={4} mb="md">Compliance Dashboard</Title>
              <Center>
                <RingProgress
                  size={180}
                  thickness={16}
                  sections={[
                    { value: 0 /* TODO: Fetch from API */, color: 'green' }
                  ]}
                  label={
                    <div style={{ textAlign: 'center' }}>
                      <Text size="xl" fw={700}>
                        {0 /* TODO: Fetch from API */}%
                      </Text>
                      <Text size="sm" c="dimmed">
                        Overall Compliance
                      </Text>
                    </div>
                  }
                />
              </Center>
            </Card>

            {/* Quality Metrics Performance */}
            <Card padding="lg" radius="md" withBorder>
              <Title order={4} mb="md">Quality Metrics Performance</Title>
              <MantineDonutChart
                data={[]}
              />
            </Card>

            {/* Audit Timeline */}
            <Card padding="lg" radius="md" withBorder>
              <Title order={4} mb="md">Recent Audit Activity</Title>
              <Timeline active={3} bulletSize={24} lineWidth={2}>
                {[].map((audit, index) => (
                  <Timeline.Item
                    key={audit.id}
                    bullet={
                      <ThemeIcon color={getStatusColor(audit.status)} size={24} radius="xl">
                        <IconClipboardList size={12} />
                      </ThemeIcon>
                    }
                    title={(audit as any).auditType || (audit as any).title || 'Audit'}
                  >
                    <Text size="sm" c="dimmed">
                      Auditor: {audit.auditor}
                    </Text>
                    <Text size="xs" c="dimmed">
                      {audit.status === 'completed' ? 'Completed' : 'Scheduled'}: {formatDate(audit.scheduledDate)}
                    </Text>
                  </Timeline.Item>
                ))}
              </Timeline>
            </Card>

            {/* Risk Assessment Overview */}
            <Card padding="lg" radius="md" withBorder>
              <Title order={4} mb="md">Risk Assessment Status</Title>
              <Stack gap="md">
                {['critical', 'high', 'medium', 'low'].map((level) => {
                  const count = [].filter /* TODO: Fetch from API */(r => (r as any).level === level || (r as any).riskLevel === level).length;
                  return (
                    <Group key={level} justify="space-between">
                      <Group gap="xs">
                        <ThemeIcon color={getRiskLevelColor(level as RiskLevel)} size="sm" radius="xl">
                          <IconCircleDot size={12} />
                        </ThemeIcon>
                        <Text size="sm" fw={500} tt="capitalize">
                          {level} Risk
                        </Text>
                      </Group>
                      <Badge color={getRiskLevelColor(level as RiskLevel)} variant="light">
                        {count}
                      </Badge>
                    </Group>
                  );
                })}
              </Stack>
            </Card>

            {/* Accreditation Status */}
            <Card padding="lg" radius="md" withBorder>
              <Title order={4} mb="md">Accreditation Status</Title>
              <Stack gap="sm">
                {[].map((acc) => (
                  <Group key={acc.id} justify="space-between">
                    <div>
                      <Text size="sm" fw={500}>{(acc as any).issuingBody || (acc as any).accreditingBody}</Text>
                      <Text size="xs" c="dimmed">
                        Expires: {formatDate(acc.expiryDate)}
                      </Text>
                    </div>
                    <Badge color={getStatusColor(acc.status)} variant="light" size="sm">
                      {acc.status}
                    </Badge>
                  </Group>
                ))}
              </Stack>
            </Card>

            {/* Recent Incidents */}
            <Card padding="lg" radius="md" withBorder>
              <Title order={4} mb="md">Recent Quality Incidents</Title>
              <Stack gap="sm">
                {[].map((incident) => (
                  <Alert
                    key={incident.id}
                    variant="light"
                    color={getSeverityColor(incident.severity)}
                    icon={<IconAlertTriangle size={16} />}
                  >
                    <Group justify="space-between">
                      <div>
                        <Text size="sm" fw={500}>{(incident as any).incidentType || (incident as any).title}</Text>
                        <Text size="xs" c="dimmed">
                          {(incident as any).department} ‚Ä¢ {formatDate((incident as any).reportDate || (incident as any).dateReported)}
                        </Text>
                      </div>
                      <Badge color={getSeverityColor(incident.severity)} variant="light" size="sm">
                        {incident.severity}
                      </Badge>
                    </Group>
                  </Alert>
                ))}
              </Stack>
            </Card>
          </SimpleGrid>
        </Tabs.Panel>

        {/* Quality Metrics Tab */}
        <Tabs.Panel value="metrics">
          <Paper p="md" radius="md" withBorder mt="md">
            {/* Search and Filters */}
            <Group mb="md">
              <TextInput
                placeholder="Search metrics..."
                leftSection={<IconSearch size={16} />}
                value={searchQuery}
                onChange={(event) => setSearchQuery(event.currentTarget.value)}
                style={{ flex: 1 }}
              />
              <Select
                placeholder="Status"
                data={[
                  { value: 'excellent', label: 'Excellent' },
                  { value: 'good', label: 'Good' },
                  { value: 'acceptable', label: 'Acceptable' },
                  { value: 'poor', label: 'Poor' }
                ]}
                value={selectedMetricStatus}
                onChange={setSelectedMetricStatus}
                clearable
              />
              <Button leftSection={<IconPlus size={16} />}>
                Add Metric
              </Button>
            </Group>

            {/* Quality Metrics Grid */}
            <SimpleGrid cols={{ base: 1, lg: 2 }} spacing="lg">
              {filteredMetrics.length === 0 ? (
                <div style={{ gridColumn: '1 / -1' }}>
                  <EmptyState
                    icon={<IconChartBar size={48} />}
                    title="No quality metrics"
                    description="Quality metrics will appear here"
                    size="sm"
                  />
                </div>
              ) : (
                filteredMetrics.map((metric) => (
                  <Card key={metric.id} padding="lg" radius="md" withBorder>
                    <Group justify="space-between" mb="md">
                      <div>
                        <Text fw={600} size="lg">{metric.name}</Text>
                        <Text size="sm" c="dimmed">{metric.category}</Text>
                      </div>
                      <Badge color={getStatusColor(metric.status)} variant="light">
                        {metric.status.toUpperCase()}
                      </Badge>
                    </Group>

                  <Stack gap="sm" mb="md">
                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Current Value</Text>
                      <Text size="lg" fw={700} c={getStatusColor(metric.status)}>
                        {(metric as any).current || (metric as any).currentValue}{(metric as any).unit || '%'}
                      </Text>
                    </Group>
                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Target</Text>
                      <Text size="sm" fw={500}>{(metric as any).target || (metric as any).targetValue}{(metric as any).unit || '%'}</Text>
                    </Group>
                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Performance</Text>
                      <Group gap="xs">
                        <Progress
                          value={((metric as any).current || (metric as any).currentValue) / ((metric as any).target || (metric as any).targetValue) * 100}
                          size="sm"
                          color={getStatusColor(metric.status)}
                          style={{ width: '100px' }}
                        />
                        <Text size="sm" fw={500}>
                          {(((metric as any).current || (metric as any).currentValue) / ((metric as any).target || (metric as any).targetValue) * 100).toFixed(0)}%
                        </Text>
                      </Group>
                    </Group>
                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Last Updated</Text>
                      <Text size="sm">{formatDate(metric.lastUpdated)}</Text>
                    </Group>
                  </Stack>

                  <Text size="sm" c="dimmed" lineClamp={2} mb="md">
                    {metric.description}
                  </Text>

                  <Group justify="space-between">
                    <Text size="xs" c="dimmed">
                      Category: {(metric as any).category}
                    </Text>
                    <Group gap="xs">
                      <ActionIcon
                        variant="subtle"
                        color="blue"
                        onClick={() => handleViewMetric(metric)}
                      >
                        <IconEye size={16} />
                      </ActionIcon>
                      <ActionIcon variant="subtle" color="green">
                        <IconEdit size={16} />
                      </ActionIcon>
                      <ActionIcon variant="subtle" color="orange">
                        <IconChartLine size={16} />
                      </ActionIcon>
                    </Group>
                  </Group>
                </Card>
              ))
              )}
            </SimpleGrid>
          </Paper>
        </Tabs.Panel>

        {/* Audits Tab */}
        <Tabs.Panel value="audits">
          <Paper p="md" radius="md" withBorder mt="md">
            {/* Search and Filters */}
            <Group mb="md">
              <TextInput
                placeholder="Search audits..."
                leftSection={<IconSearch size={16} />}
                value={searchQuery}
                onChange={(event) => setSearchQuery(event.currentTarget.value)}
                style={{ flex: 1 }}
              />
              <Select
                placeholder="Status"
                data={[
                  { value: 'planned', label: 'Planned' },
                  { value: 'in_progress', label: 'In Progress' },
                  { value: 'completed', label: 'Completed' },
                  { value: 'cancelled', label: 'Cancelled' }
                ]}
                value={selectedAuditStatus}
                onChange={setSelectedAuditStatus}
                clearable
              />
              <Button leftSection={<IconPlus size={16} />} onClick={openCreateAudit}>
                Schedule Audit
              </Button>
            </Group>

            {/* Audits Grid */}
            <SimpleGrid cols={{ base: 1, lg: 2 }} spacing="lg">
              {filteredAudits.map((audit) => (
                <Card key={audit.id} padding="lg" radius="md" withBorder>
                  <Group justify="space-between" mb="md">
                    <div>
                      <Text fw={600} size="lg" lineClamp={1}>{(audit as any).auditType || (audit as any).title || 'Audit'}</Text>
                      <Text size="sm" c="dimmed">{((audit as any).auditType || (audit as any).type || 'audit').replace('_', ' ').toUpperCase()}</Text>
                    </div>
                    <Badge color={getStatusColor(audit.status)} variant="light">
                      {audit.status.replace('_', ' ').toUpperCase()}
                    </Badge>
                  </Group>

                  <Stack gap="sm" mb="md">
                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Auditor</Text>
                      <Text size="sm" fw={500}>{audit.auditor}</Text>
                    </Group>
                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Department</Text>
                      <Text size="sm">{audit.department}</Text>
                    </Group>
                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Scheduled Date</Text>
                      <Text size="sm">{formatDate(audit.scheduledDate)}</Text>
                    </Group>
                    {audit.completedDate && (
                      <Group justify="space-between">
                        <Text size="sm" c="dimmed">Completed Date</Text>
                        <Text size="sm">{formatDate(audit.completedDate)}</Text>
                      </Group>
                    )}
                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Score</Text>
                      <Group gap="xs">
                        <Progress
                          value={audit.score || 0}
                          size="sm"
                          color={audit.score >= 80 ? 'green' : audit.score >= 60 ? 'orange' : 'red'}
                          style={{ width: '80px' }}
                        />
                        <Text size="sm" fw={500}>
                          {audit.score || 0}%
                        </Text>
                      </Group>
                    </Group>
                  </Stack>

                  {audit.scope && (
                    <Text size="sm" c="dimmed" lineClamp={2} mb="md">
                      Scope: {audit.scope}
                    </Text>
                  )}

                  <Group justify="space-between">
                    <Group gap="xs">
                      {audit.findings && (
                        <Badge variant="outline" size="xs" color="orange">
                          {audit.findings.length} Finding{audit.findings.length !== 1 ? 's' : ''}
                        </Badge>
                      )}
                      <Badge variant="outline" size="xs" color="blue">
                        {audit.duration} days
                      </Badge>
                    </Group>
                    <Group gap="xs">
                      <ActionIcon
                        variant="subtle"
                        color="blue"
                        onClick={() => handleViewAudit(audit)}
                      >
                        <IconEye size={16} />
                      </ActionIcon>
                      <ActionIcon variant="subtle" color="green">
                        <IconEdit size={16} />
                      </ActionIcon>
                      <ActionIcon variant="subtle" color="orange">
                        <IconDownload size={16} />
                      </ActionIcon>
                    </Group>
                  </Group>
                </Card>
              ))}
            </SimpleGrid>
          </Paper>
        </Tabs.Panel>

        {/* Compliance Tab */}
        <Tabs.Panel value="compliance">
          <Paper p="md" radius="md" withBorder mt="md">
            <Group justify="space-between" mb="lg">
              <Title order={3}>Compliance Monitoring</Title>
              <Button leftSection={<IconDownload size={16} />} variant="light">
                Export Report
              </Button>
            </Group>

            {/* Compliance Overview Cards */}
            <SimpleGrid cols={{ base: 1, sm: 2, lg: 4 }} mb="lg">
              <Card padding="md" radius="md" withBorder>
                <Group justify="space-between">
                  <div>
                    <Text size="sm" c="dimmed">Regulatory Compliance</Text>
                    <Text fw={700} size="lg">95%</Text>
                  </div>
                  <ThemeIcon color="green" variant="light">
                    <IconShieldCheck size={20} />
                  </ThemeIcon>
                </Group>
              </Card>
              <Card padding="md" radius="md" withBorder>
                <Group justify="space-between">
                  <div>
                    <Text size="sm" c="dimmed">Safety Standards</Text>
                    <Text fw={700} size="lg">88%</Text>
                  </div>
                  <ThemeIcon color="orange" variant="light">
                    <IconShield size={20} />
                  </ThemeIcon>
                </Group>
              </Card>
              <Card padding="md" radius="md" withBorder>
                <Group justify="space-between">
                  <div>
                    <Text size="sm" c="dimmed">Documentation</Text>
                    <Text fw={700} size="lg">92%</Text>
                  </div>
                  <ThemeIcon color="blue" variant="light">
                    <IconFileCheck size={20} />
                  </ThemeIcon>
                </Group>
              </Card>
              <Card padding="md" radius="md" withBorder>
                <Group justify="space-between">
                  <div>
                    <Text size="sm" c="dimmed">Training Compliance</Text>
                    <Text fw={700} size="lg">97%</Text>
                  </div>
                  <ThemeIcon color="teal" variant="light">
                    <IconSchool size={20} />
                  </ThemeIcon>
                </Group>
              </Card>
            </SimpleGrid>

            {/* Compliance Items */}
            <SimpleGrid cols={{ base: 1, lg: 2 }} spacing="lg">
              {[].map /* TODO: Fetch from API */((item) => (
                <Card key={item.id} padding="lg" radius="md" withBorder>
                  <Group justify="space-between" mb="md">
                    <div>
                      <Text fw={600} size="lg">{item.title}</Text>
                      <Text size="sm" c="dimmed">{item.category}</Text>
                    </div>
                    <Badge color={getStatusColor(item.status)} variant="light">
                      {item.status.replace('_', ' ').toUpperCase()}
                    </Badge>
                  </Group>

                  <Stack gap="sm" mb="md">
                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Compliance Score</Text>
                      <Group gap="xs">
                        <Progress
                          value={item.complianceScore}
                          size="sm"
                          color={item.complianceScore >= 90 ? 'green' : item.complianceScore >= 70 ? 'orange' : 'red'}
                          style={{ width: '100px' }}
                        />
                        <Text size="sm" fw={500}>{item.complianceScore}%</Text>
                      </Group>
                    </Group>
                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Last Review</Text>
                      <Text size="sm">N/A</Text>
                    </Group>
                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Next Review</Text>
                      <Text size="sm" c={new Date(item.nextReviewDate) < new Date() ? 'red' : undefined}>
                        {formatDate(item.nextReviewDate)}
                      </Text>
                    </Group>
                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Responsible Person</Text>
                      <Text size="sm">{item.responsiblePerson}</Text>
                    </Group>
                  </Stack>

                  <Text size="sm" c="dimmed" lineClamp={2} mb="md">
                    {item.description}
                  </Text>

                  <Group justify="space-between">
                    <Text size="xs" c="dimmed">
                      Priority: {item.priority}
                    </Text>
                    <Group gap="xs">
                      <ActionIcon variant="subtle" color="blue">
                        <IconEye size={16} />
                      </ActionIcon>
                      <ActionIcon variant="subtle" color="green">
                        <IconEdit size={16} />
                      </ActionIcon>
                      <ActionIcon variant="subtle" color="orange">
                        <IconFileCheck size={16} />
                      </ActionIcon>
                    </Group>
                  </Group>
                </Card>
              ))}
            </SimpleGrid>
          </Paper>
        </Tabs.Panel>

        {/* Accreditation Tab */}
        <Tabs.Panel value="accreditation">
          <Paper p="md" radius="md" withBorder mt="md">
            <Group justify="space-between" mb="lg">
              <Title order={3}>Accreditation Management</Title>
              <Button leftSection={<IconPlus size={16} />}>
                Add Accreditation
              </Button>
            </Group>

            {/* Accreditations Grid */}
            <SimpleGrid cols={{ base: 1, lg: 2 }} spacing="lg">
              {[].map /* TODO: Fetch from API */((accreditation) => (
                <Card key={accreditation.id} padding="lg" radius="md" withBorder>
                  <Group justify="space-between" mb="md">
                    <div>
                      <Text fw={600} size="lg">{(accreditation as any).issuingBody || (accreditation as any).accreditingBody || accreditation.name}</Text>
                      <Text size="sm" c="dimmed">{(accreditation as any).accreditationType || 'Accreditation'}</Text>
                    </div>
                    <Badge color={getStatusColor(accreditation.status)} variant="light">
                      {accreditation.status.toUpperCase()}
                    </Badge>
                  </Group>

                  <Stack gap="sm" mb="md">
                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Certificate Number</Text>
                      <Text size="sm" fw={500}>{(accreditation as any).certificate || (accreditation as any).certificateNumber || 'N/A'}</Text>
                    </Group>
                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Issue Date</Text>
                      <Text size="sm">{formatDate(accreditation.issueDate)}</Text>
                    </Group>
                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Expiry Date</Text>
                      <Text 
                        size="sm" 
                        c={new Date(accreditation.expiryDate) < new Date() ? 'red' : undefined}
                      >
                        {formatDate(accreditation.expiryDate)}
                      </Text>
                    </Group>
                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Scope</Text>
                      <Text size="sm">{accreditation.scope}</Text>
                    </Group>
                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Next Assessment</Text>
                      <Text size="sm">
                        {(accreditation as any).renewalDue ? formatDate((accreditation as any).renewalDue) : ((accreditation as any).nextAssessmentDate ? formatDate((accreditation as any).nextAssessmentDate) : 'TBD')}
                      </Text>
                    </Group>
                  </Stack>

                  {(accreditation as any).scope && (
                    <>
                      <Text size="sm" c="dimmed" mb="xs">Scope:</Text>
                      <Text size="sm" mb="md">{(accreditation as any).scope}</Text>
                    </>
                  )}

                  <Group justify="space-between">
                    <Text size="xs" c="dimmed">
                      Accreditation ID: {accreditation.id}
                    </Text>
                    <Group gap="xs">
                      <ActionIcon
                        variant="subtle"
                        color="blue"
                        onClick={() => handleViewAccreditation(accreditation)}
                      >
                        <IconEye size={16} />
                      </ActionIcon>
                      <ActionIcon variant="subtle" color="green">
                        <IconEdit size={16} />
                      </ActionIcon>
                      <ActionIcon variant="subtle" color="orange">
                        <IconDownload size={16} />
                      </ActionIcon>
                    </Group>
                  </Group>
                </Card>
              ))}
            </SimpleGrid>
          </Paper>
        </Tabs.Panel>

        {/* Policies Tab */}
        <Tabs.Panel value="policies">
          <Paper p="md" radius="md" withBorder mt="md">
            {/* Search and Filters */}
            <Group mb="md">
              <TextInput
                placeholder="Search policies..."
                leftSection={<IconSearch size={16} />}
                value={searchQuery}
                onChange={(event) => setSearchQuery(event.currentTarget.value)}
                style={{ flex: 1 }}
              />
              <Select
                placeholder="Category"
                data={[
                  { value: 'safety', label: 'Safety' },
                  { value: 'quality', label: 'Quality' },
                  { value: 'privacy', label: 'Privacy' },
                  { value: 'clinical', label: 'Clinical' },
                  { value: 'administrative', label: 'Administrative' }
                ]}
                value={selectedPolicyCategory}
                onChange={setSelectedPolicyCategory}
                clearable
              />
              <Button leftSection={<IconPlus size={16} />} onClick={openCreatePolicy}>
                Create Policy
              </Button>
            </Group>

            {/* Policies Grid */}
            <SimpleGrid cols={{ base: 1, lg: 2 }} spacing="lg">
              {filteredPolicies.map((policy) => (
                <Card key={policy.id} padding="lg" radius="md" withBorder>
                  <Group justify="space-between" mb="md">
                    <div>
                      <Text fw={600} size="lg" lineClamp={1}>{policy.title}</Text>
                      <Text size="sm" c="dimmed">{(policy as any).category || 'Policy'}</Text>
                    </div>
                    <Badge color={getStatusColor(policy.status)} variant="light">
                      {policy.status.toUpperCase()}
                    </Badge>
                  </Group>

                  <Stack gap="sm" mb="md">
                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Category</Text>
                      <Badge variant="outline" size="sm">
                        {policy.category.toUpperCase()}
                      </Badge>
                    </Group>
                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Effective Date</Text>
                      <Text size="sm">{formatDate((policy as any).lastReviewDate || new Date().toISOString())}</Text>
                    </Group>
                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Review Date</Text>
                      <Text size="sm" c={new Date((policy as any).nextReviewDate) < new Date() ? 'red' : undefined}>
                        {formatDate((policy as any).nextReviewDate)}
                      </Text>
                    </Group>
                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Owner</Text>
                      <Text size="sm">{(policy as any).responsiblePerson || 'N/A'}</Text>
                    </Group>
                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Approver</Text>
                      <Text size="sm">{(policy as any).responsiblePerson || 'N/A'}</Text>
                    </Group>
                  </Stack>

                  <Text size="sm" c="dimmed" lineClamp={2} mb="md">
                    {policy.description}
                  </Text>

                  <Group justify="space-between">
                    <Group gap="xs">
                      <Badge variant="outline" size="xs" color="blue">
                        {(policy as any).priority || 'Normal'}
                      </Badge>
                      <Badge variant="outline" size="xs" color="green">
                        Score: {(policy as any).complianceScore || 0}%
                      </Badge>
                    </Group>
                    <Group gap="xs">
                      <ActionIcon
                        variant="subtle"
                        color="blue"
                        onClick={() => handleViewPolicy(policy)}
                      >
                        <IconEye size={16} />
                      </ActionIcon>
                      <ActionIcon variant="subtle" color="green">
                        <IconEdit size={16} />
                      </ActionIcon>
                      <ActionIcon variant="subtle" color="orange">
                        <IconDownload size={16} />
                      </ActionIcon>
                    </Group>
                  </Group>
                </Card>
              ))}
            </SimpleGrid>
          </Paper>
        </Tabs.Panel>

        {/* Incidents Tab */}
        <Tabs.Panel value="incidents">
          <Paper p="md" radius="md" withBorder mt="md">
            <Group justify="space-between" mb="lg">
              <Title order={3}>Quality Incidents</Title>
              <Group>
                <Button leftSection={<IconPlus size={16} />} onClick={openReportIncident}>
                  Report Incident
                </Button>
                <Button variant="light" leftSection={<IconDownload size={16} />}>
                  Export Report
                </Button>
              </Group>
            </Group>

            {/* Incidents Grid */}
            <SimpleGrid cols={{ base: 1, lg: 2 }} spacing="lg">
              {[].map /* TODO: Fetch from API */((incident) => (
                <Card key={incident.id} padding="lg" radius="md" withBorder>
                  <Group justify="space-between" mb="md">
                    <div>
                      <Text fw={600} size="lg" lineClamp={1}>{(incident as any).incidentType || (incident as any).title}</Text>
                      <Text size="sm" c="dimmed">ID: {incident.id}</Text>
                    </div>
                    <Group>
                      <Badge color={getSeverityColor(incident.severity)} variant="light">
                        {incident.severity.toUpperCase()}
                      </Badge>
                      <Badge color={getStatusColor(incident.status)} variant="light">
                        {incident.status.replace('_', ' ').toUpperCase()}
                      </Badge>
                    </Group>
                  </Group>

                  <Stack gap="sm" mb="md">
                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Department</Text>
                      <Text size="sm" fw={500}>{incident.department}</Text>
                    </Group>
                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Reported By</Text>
                      <Text size="sm">{incident.reportedBy}</Text>
                    </Group>
                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Date Reported</Text>
                      <Text size="sm">{formatDate((incident as any).reportDate || (incident as any).dateReported)}</Text>
                    </Group>
                    {(incident as any).status === 'resolved' && (
                      <Group justify="space-between">
                        <Text size="sm" c="dimmed">Status</Text>
                        <Text size="sm">Resolved</Text>
                      </Group>
                    )}
                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Assigned To</Text>
                      <Text size="sm">{incident.assignedTo}</Text>
                    </Group>
                  </Stack>

                  <Text size="sm" c="dimmed" lineClamp={2} mb="md">
                    {incident.description}
                  </Text>

                  {incident.correctiveActions && (
                    <Alert variant="light" color="blue" icon={<IconClipboardList size={16} />} mb="md">
                      <Text size="sm">
                        {incident.correctiveActions.length} corrective action{incident.correctiveActions.length !== 1 ? 's' : ''} planned
                      </Text>
                    </Alert>
                  )}

                  <Group justify="space-between">
                    <Text size="xs" c="dimmed">
                      Type: {(incident as any).incidentType || 'Incident'}
                    </Text>
                    <Group gap="xs">
                      <ActionIcon
                        variant="subtle"
                        color="blue"
                        onClick={() => handleViewIncident(incident)}
                      >
                        <IconEye size={16} />
                      </ActionIcon>
                      <ActionIcon variant="subtle" color="green">
                        <IconEdit size={16} />
                      </ActionIcon>
                      <ActionIcon variant="subtle" color="orange">
                        <IconClipboardList size={16} />
                      </ActionIcon>
                    </Group>
                  </Group>
                </Card>
              ))}
            </SimpleGrid>
          </Paper>
        </Tabs.Panel>

        {/* Risk Assessment Tab */}
        <Tabs.Panel value="risk">
          <Paper p="md" radius="md" withBorder mt="md">
            <Group justify="space-between" mb="lg">
              <Title order={3}>Risk Assessment</Title>
              <Group>
                <Button leftSection={<IconPlus size={16} />}>
                  Add Risk Assessment
                </Button>
                <Button variant="light" leftSection={<IconDownload size={16} />}>
                  Risk Report
                </Button>
              </Group>
            </Group>

            {/* Risk Assessments Grid */}
            <SimpleGrid cols={{ base: 1, lg: 2 }} spacing="lg">
              {[].map /* TODO: Fetch from API */((risk) => (
                <Card key={risk.id} padding="lg" radius="md" withBorder>
                  <Group justify="space-between" mb="md">
                    <div>
                      <Text fw={600} size="lg" lineClamp={1}>{(risk as any).risk || (risk as any).riskTitle}</Text>
                      <Text size="sm" c="dimmed">{(risk as any).area || (risk as any).category}</Text>
                    </div>
                    <Badge color={getRiskLevelColor((risk as any).level || (risk as any).riskLevel)} variant="light">
                      {((risk as any).level || (risk as any).riskLevel || 'unknown').toUpperCase()} RISK
                    </Badge>
                  </Group>

                  <Stack gap="sm" mb="md">
                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Risk Score</Text>
                      <Group gap="xs">
                        <Progress
                          value={50}
                          size="sm"
                          color={getRiskLevelColor((risk as any).level || (risk as any).riskLevel)}
                          style={{ width: '80px' }}
                        />
                        <Text size="sm" fw={500}>N/A</Text>
                      </Group>
                    </Group>
                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Probability</Text>
                      <Text size="sm">N/A</Text>
                    </Group>
                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Impact</Text>
                      <Text size="sm">N/A</Text>
                    </Group>
                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Department</Text>
                      <Text size="sm">{(risk as any).area || 'N/A'}</Text>
                    </Group>
                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Last Review</Text>
                      <Text size="sm">N/A</Text>
                    </Group>
                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Risk Owner</Text>
                      <Text size="sm">N/A</Text>
                    </Group>
                  </Stack>

                  <Text size="sm" c="dimmed" lineClamp={2} mb="md">
                    Risk: {(risk as any).risk} in {(risk as any).area}
                  </Text>

                  <Text size="sm" c="dimmed" mb="xs">Status:</Text>
                  <Text size="sm" mb="md">{(risk as any).status}</Text>

                  <Group justify="space-between">
                    <Text size="xs" c="dimmed">
                      Next Review: N/A
                    </Text>
                    <Group gap="xs">
                      <ActionIcon
                        variant="subtle"
                        color="blue"
                        onClick={() => handleViewRisk(risk)}
                      >
                        <IconEye size={16} />
                      </ActionIcon>
                      <ActionIcon variant="subtle" color="green">
                        <IconEdit size={16} />
                      </ActionIcon>
                      <ActionIcon variant="subtle" color="orange">
                        <IconAlertTriangle size={16} />
                      </ActionIcon>
                    </Group>
                  </Group>
                </Card>
              ))}
            </SimpleGrid>
          </Paper>
        </Tabs.Panel>
      </Tabs>

      {/* Create Audit Modal */}
      <Modal
        opened={createAuditOpened}
        onClose={closeCreateAudit}
        title="Schedule New Audit"
        size="lg"
      >
        <Stack gap="md">
          <SimpleGrid cols={2}>
            <TextInput
              label="Audit Title"
              placeholder="Enter audit title"
              required
            />
            <Select
              label="Audit Type"
              placeholder="Select type"
              data={[
                { value: 'internal', label: 'Internal Audit' },
                { value: 'external', label: 'External Audit' },
                { value: 'compliance', label: 'Compliance Audit' },
                { value: 'quality', label: 'Quality Audit' }
              ]}
              required
            />
          </SimpleGrid>
          
          <SimpleGrid cols={2}>
            <Select
              label="Auditor"
              placeholder="Select auditor"
              data={[].map /* TODO: Fetch from API */(doctor => ({
                value: doctor.id,
                label: doctor.name || `Dr. ${(doctor as any).firstName || ''} ${(doctor as any).lastName || ''}`
              }))}
              required
            />
            <Select
              label="Department"
              placeholder="Select department"
              data={[
                { value: 'cardiology', label: 'Cardiology' },
                { value: 'emergency', label: 'Emergency' },
                { value: 'surgery', label: 'Surgery' },
                { value: 'pediatrics', label: 'Pediatrics' },
                { value: 'radiology', label: 'Radiology' }
              ]}
              required
            />
          </SimpleGrid>
          
          <SimpleGrid cols={2}>
            <DatePickerInput
              label="Scheduled Date"
              placeholder="Select date"
              minDate={new Date()}
              required
            />
            <NumberInput
              label="Duration (days)"
              placeholder="Enter duration"
              min={1}
              max={30}
              defaultValue={3}
              required
            />
          </SimpleGrid>
          
          <Textarea
            label="Audit Scope"
            placeholder="Describe the scope and objectives of this audit"
            rows={3}
            required
          />
          
          <Group justify="flex-end">
            <Button variant="light" onClick={closeCreateAudit}>
              Cancel
            </Button>
            <Button onClick={() => {
              notifications.show({
                title: 'Audit Scheduled',
                message: 'Quality audit has been successfully scheduled',
                color: 'green',
              });
              closeCreateAudit();
            }}>
              Schedule Audit
            </Button>
          </Group>
        </Stack>
      </Modal>

      {/* Report Incident Modal */}
      <Modal
        opened={reportIncidentOpened}
        onClose={closeReportIncident}
        title="Report Quality Incident"
        size="lg"
      >
        <Stack gap="md">
          <SimpleGrid cols={2}>
            <TextInput
              label="Incident Title"
              placeholder="Brief incident title"
              required
            />
            <Select
              label="Severity"
              placeholder="Select severity"
              data={[
                { value: 'low', label: 'Low' },
                { value: 'medium', label: 'Medium' },
                { value: 'high', label: 'High' },
                { value: 'critical', label: 'Critical' }
              ]}
              required
            />
          </SimpleGrid>
          
          <SimpleGrid cols={2}>
            <Select
              label="Department"
              placeholder="Select department"
              data={[
                { value: 'cardiology', label: 'Cardiology' },
                { value: 'emergency', label: 'Emergency' },
                { value: 'surgery', label: 'Surgery' },
                { value: 'pediatrics', label: 'Pediatrics' },
                { value: 'radiology', label: 'Radiology' }
              ]}
              required
            />
            <Select
              label="Priority"
              placeholder="Select priority"
              data={[
                { value: 'low', label: 'Low' },
                { value: 'medium', label: 'Medium' },
                { value: 'high', label: 'High' },
                { value: 'urgent', label: 'Urgent' }
              ]}
              required
            />
          </SimpleGrid>
          
          <Textarea
            label="Incident Description"
            placeholder="Provide detailed description of the quality incident"
            rows={4}
            required
          />
          
          <Textarea
            label="Immediate Actions Taken"
            placeholder="Describe any immediate actions taken to address the incident"
            rows={3}
          />
          
          <Group justify="flex-end">
            <Button variant="light" onClick={closeReportIncident}>
              Cancel
            </Button>
            <Button onClick={() => {
              notifications.show({
                title: 'Incident Reported',
                message: 'Quality incident has been successfully reported',
                color: 'orange',
              });
              closeReportIncident();
            }}>
              Report Incident
            </Button>
          </Group>
        </Stack>
      </Modal>

      {/* Create Policy Modal */}
      <Modal
        opened={createPolicyOpened}
        onClose={closeCreatePolicy}
        title="Create New Policy"
        size="lg"
      >
        <Stack gap="md">
          <SimpleGrid cols={2}>
            <TextInput
              label="Policy Title"
              placeholder="Enter policy title"
              required
            />
            <Select
              label="Category"
              placeholder="Select category"
              data={[
                { value: 'safety', label: 'Safety' },
                { value: 'quality', label: 'Quality' },
                { value: 'privacy', label: 'Privacy' },
                { value: 'clinical', label: 'Clinical' },
                { value: 'administrative', label: 'Administrative' }
              ]}
              required
            />
          </SimpleGrid>
          
          <SimpleGrid cols={2}>
            <TextInput
              label="Policy Owner"
              placeholder="Enter policy owner"
              required
            />
            <DatePickerInput
              label="Effective Date"
              placeholder="Select effective date"
              required
            />
          </SimpleGrid>
          
          <Textarea
            label="Policy Description"
            placeholder="Provide a brief description of the policy"
            rows={3}
            required
          />
          
          <MultiSelect
            label="Applicable Departments"
            placeholder="Select departments"
            data={[
              { value: 'all', label: 'All Departments' },
              { value: 'cardiology', label: 'Cardiology' },
              { value: 'emergency', label: 'Emergency' },
              { value: 'surgery', label: 'Surgery' },
              { value: 'pediatrics', label: 'Pediatrics' },
              { value: 'radiology', label: 'Radiology' }
            ]}
          />
          
          <Group justify="flex-end">
            <Button variant="light" onClick={closeCreatePolicy}>
              Cancel
            </Button>
            <Button onClick={() => {
              notifications.show({
                title: 'Policy Created',
                message: 'New policy has been successfully created',
                color: 'green',
              });
              closeCreatePolicy();
            }}>
              Create Policy
            </Button>
          </Group>
        </Stack>
      </Modal>
    </Container>
  );
};

export default QualityAssurance;

'use client';

import React, { useState, useMemo, useEffect } from 'react';
import {
  Container,
  Paper,
  Title,
  Group,
  Button,
  TextInput,
  Select,
  Badge,
  Table,
  Modal,
  Text,
  Tabs,
  Card,
  Avatar,
  ActionIcon,
  Menu,
  Stack,
  Divider,
  SimpleGrid,
  ScrollArea,
  ThemeIcon,
  Alert,
  Progress,
  NumberInput,
  Textarea,
  Timeline,
  Stepper,
  RingProgress,
  Tooltip,
  List,
  Image,
  Loader,
  Highlight,
  Accordion,
  FileButton
} from '@mantine/core';
import { useDisclosure } from '@mantine/hooks';
import EmptyState from '../../../components/EmptyState';
import { notifications } from '@mantine/notifications';
import { MantineDonutChart, SimpleAreaChart, SimpleLineChart, SimpleBarChart } from '../../../components/MantineChart';
import {
  IconPlus,
  IconSearch,
  IconEdit,
  IconEye,
  IconTrash,
  IconCalendar,
  IconRadioactive,
  IconChartBar,
  IconPhone,
  IconMail,
  IconAlertCircle,
  IconCheck,
  IconX,
  IconDotsVertical,
  IconReportMedical,
  IconClock,
  IconClipboardList,
  IconFileText,
  IconDownload,
  IconPrinter,
  IconShare,
  IconActivity,
  IconExclamationMark,
  IconClockHour4,
  IconTrendingUp,
  IconTrendingDown,
  IconUsers,
  IconCalculator,
  IconSettings,
  IconRefresh,
  IconFilter,
  IconBarcode,
  IconTemperature,
  IconShieldCheck,
  IconAlertTriangle,
  IconCircleCheck,
  IconClipboard,
  IconLungs,
  IconHeart,
  IconBrain,
  IconBone,
  IconStethoscope,
  IconMedicalCross,
  IconPackage,
  IconTruck,
  IconCash,
  IconReceipt,
  IconNotes,
  IconTag,
  IconAlarm,
  IconInfoCircle,
  IconBed,
  IconAmbulance,
  IconFlask,
  IconDroplet,
  IconNurse,
  IconBandage,
  IconPill,
  IconMask,
  IconBolt,
  IconZoom,
  IconCut,
  IconTool,
  IconPhoto,
  IconScan,
  IconDeviceDesktop,
  IconCamera,
  IconUpload,
  IconMicroscope,
  IconTarget,
  IconFocus,
  IconColorPicker,
  IconRuler,
  IconRotate,
  IconContrast,
  IconBrightness,
  IconAdjustments,
  IconMaximize,
  IconMinimize,
  IconPlayerPlay,
  IconPlayerPause,
  IconVolume,
  IconFileUpload,
  IconCloudUpload
} from '@tabler/icons-react';

// Import types and mock data - using any for now due to type mismatches
// Mock data imports removed
import radiologyService from '../../../services/radiology.service';

const RadiologyManagement = () => {
  // State management
  const [activeTab, setActiveTab] = useState<string>('requests');
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedType, setSelectedType] = useState<string>('');
  const [selectedStatus, setSelectedStatus] = useState<string>('');
  const [selectedPriority, setSelectedPriority] = useState<string>('');
  const [selectedEquipmentStatus, setSelectedEquipmentStatus] = useState<string>('');
  const [selectedRequest, setSelectedRequest] = useState<any>(null);
  const [selectedReport, setSelectedReport] = useState<any>(null);
  const [selectedEquipment, setSelectedEquipment] = useState<any>(null);
  const [selectedStudy, setSelectedStudy] = useState<any>(null);

  // API state
  const [studies, setStudies] = useState<any[]>([]);
  const [reports, setReports] = useState<any[]>([]);
  const [orders, setOrders] = useState<any[]>([]);
  const [radiologyStats, setRadiologyStats] = useState<any>(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  // Fetch radiology studies
  const fetchStudies = async () => {
    try {
      setLoading(true);
      setError(null);
      const response = await radiologyService.getStudies({ limit: 100 });
      if (response.success) {
        setStudies(response.data.items);
      }
    } catch (err: any) {
      console.warn('Error fetching studies (using empty data):', err.response?.data?.message || err.message);
      setError(null);
      setStudies([]);
    } finally {
      setLoading(false);
    }
  };

  // Fetch radiology reports
  const fetchReports = async () => {
    try {
      setLoading(true);
      setError(null);
      const response = await radiologyService.getReports({ limit: 100 });
      if (response.success) {
        setReports(response.data.items);
      }
    } catch (err: any) {
      console.warn('Error fetching reports (using empty data):', err.response?.data?.message || err.message);
      setError(null);
      setReports([]);
    } finally {
      setLoading(false);
    }
  };

  // Fetch radiology orders
  const fetchOrders = async () => {
    try {
      setLoading(true);
      setError(null);
      const response = await radiologyService.getOrders({ limit: 100 });
      if (response.success) {
        setOrders(response.data.items);
      }
    } catch (err: any) {
      console.warn('Error fetching orders (using empty data):', err.response?.data?.message || err.message);
      setError(null);
      setOrders([]);
    } finally {
      setLoading(false);
    }
  };

  // Fetch radiology stats
  const fetchRadiologyStats = async () => {
    try {
      const response = await radiologyService.getStats();
      if (response.success) {
        setRadiologyStats(response.data);
      }
    } catch (err: any) {
      console.warn('Error fetching radiology stats (using default values):', err.response?.data?.message || err.message);
      setRadiologyStats({
        totalStudies: 0,
        pendingStudies: 0,
        completedStudies: 0,
        totalReports: 0,
        pendingReports: 0,
        completedReports: 0
      });
    }
  };

  // Initial data fetch
  useEffect(() => {
    fetchStudies();
    fetchReports();
    fetchOrders();
    fetchRadiologyStats();
  }, []);

  // Modal states
  const [requestDetailOpened, { open: openRequestDetail, close: closeRequestDetail }] = useDisclosure(false);
  const [addRequestOpened, { open: openAddRequest, close: closeAddRequest }] = useDisclosure(false);
  const [reportDetailOpened, { open: openReportDetail, close: closeReportDetail }] = useDisclosure(false);
  const [equipmentDetailOpened, { open: openEquipmentDetail, close: closeEquipmentDetail }] = useDisclosure(false);
  const [studyViewerOpened, { open: openStudyViewer, close: closeStudyViewer }] = useDisclosure(false);
  const [createReportOpened, { open: openCreateReport, close: closeCreateReport }] = useDisclosure(false);

  // Filter imaging requests
  const filteredRequests = useMemo(() => {
    return [].filter /* TODO: Fetch from API */((request: any) => {
      const matchesSearch = 
        request.patient?.firstName?.toLowerCase().includes(searchQuery.toLowerCase()) ||
        request.patient?.lastName?.toLowerCase().includes(searchQuery.toLowerCase()) ||
        request.requestId?.toLowerCase().includes(searchQuery.toLowerCase()) ||
        request.appointmentId?.toLowerCase().includes(searchQuery.toLowerCase()) ||
        request.examType?.toLowerCase().includes(searchQuery.toLowerCase()) ||
        request.modality?.toLowerCase().includes(searchQuery.toLowerCase());
      
      const matchesType = !selectedType || request.imagingType === selectedType || request.modality === selectedType;
      const matchesStatus = !selectedStatus || request.status === selectedStatus;
      const matchesPriority = !selectedPriority || request.priority === selectedPriority;

      return matchesSearch && matchesType && matchesStatus && matchesPriority;
    });
  }, [searchQuery, selectedType, selectedStatus, selectedPriority]);

  // Filter equipment
  const filteredEquipment = useMemo(() => {
    return [].filter /* TODO: Fetch from API */((equipment: any) => {
      const matchesSearch = 
        equipment.equipmentName.toLowerCase().includes(searchQuery.toLowerCase()) ||
        equipment.model.toLowerCase().includes(searchQuery.toLowerCase()) ||
        equipment.manufacturer.toLowerCase().includes(searchQuery.toLowerCase());
      
      const matchesStatus = !selectedEquipmentStatus || equipment.status === selectedEquipmentStatus;

      return matchesSearch && matchesStatus;
    });
  }, [searchQuery, selectedEquipmentStatus]);

  // Helper functions
  const getStatusColor = (status: string) => {
    switch (status) {
      case 'pending':
      case 'scheduled':
      case 'draft':
      case 'operational': return 'blue';
      case 'in_progress':
      case 'scanning':
      case 'reviewing':
      case 'in_use': return 'orange';
      case 'completed':
      case 'finalized':
      case 'available': return 'green';
      case 'cancelled':
      case 'rejected':
      case 'out_of_service': return 'red';
      case 'on_hold':
      case 'maintenance': return 'yellow';
      default: return 'gray';
    }
  };

  const getPriorityColor = (priority: string) => {
    switch (priority) {
      case 'emergency': return 'red';
      case 'urgent': return 'orange';
      case 'routine': return 'blue';
      case 'elective': return 'green';
      default: return 'gray';
    }
  };

  const getImagingTypeColor = (type: string) => {
    switch (type) {
      case 'xray': return 'blue';
      case 'ct': return 'green';
      case 'mri': return 'purple';
      case 'ultrasound': return 'cyan';
      case 'mammography': return 'pink';
      case 'nuclear_medicine': return 'orange';
      case 'fluoroscopy': return 'indigo';
      case 'pet': return 'red';
      default: return 'gray';
    }
  };

  const handleViewRequest = (request: any) => {
    setSelectedRequest(request);
    openRequestDetail();
  };

  const handleViewReport = (report: any) => {
    setSelectedReport(report);
    openReportDetail();
  };

  const handleViewEquipment = (equipment: any) => {
    setSelectedEquipment(equipment);
    openEquipmentDetail();
  };

  const handleViewStudy = (study: any) => {
    setSelectedStudy(study);
    openStudyViewer();
  };

  const clearFilters = () => {
    setSearchQuery('');
    setSelectedType('');
    setSelectedStatus('');
    setSelectedPriority('');
    setSelectedEquipmentStatus('');
  };

  const formatCurrency = (amount: number) => {
    return new Intl.NumberFormat('en-IN', {
      style: 'currency',
      currency: 'INR'
    }).format(amount);
  };

  // Statistics cards
  const statsCards = [
    {
      title: 'Total Studies',
      value: radiologyStats?.totalStudies || 0,
      icon: IconClipboardList,
      color: 'blue',
      trend: '+0%'
    },
    {
      title: 'Completed Studies',
      value: radiologyStats?.completedStudies || 0,
      icon: IconScan,
      color: 'green',
      trend: '+0'
    },
    {
      title: 'Pending Reports',
      value: radiologyStats?.totalReports || 0,
      icon: IconReportMedical,
      color: 'orange',
      trend: '0'
    },
    {
      title: 'Pending Orders',
      value: radiologyStats?.pendingOrders || 0,
      icon: IconDeviceDesktop,
      color: 'purple',
      trend: '0% uptime'
    }
  ];

  // Chart data
  const imagingTypeData = [];

  const monthlyVolume = [];
  const equipmentUtilization = [];
  const turnaroundTimes = [];

  return (
    <Container size="xl" py="md">
      {/* Header */}
      <Group justify="space-between" mb="lg">
        <div>
          <Title order={1}>Radiology & Imaging</Title>
          <Text c="dimmed" size="sm">
            Manage imaging requests, radiology reports, and imaging equipment
          </Text>
        </div>
        <Group>
          <Button
            leftSection={<IconPlus size={16} />}
            onClick={openAddRequest}
            color="blue"
          >
            New Request
          </Button>
          <Button
            variant="light"
            leftSection={<IconRadioactive size={16} />}
            color="red"
          >
            Emergency Scan
          </Button>
        </Group>
      </Group>

      {/* Statistics Cards */}
      <SimpleGrid cols={{ base: 1, sm: 2, md: 4 }} mb="lg">
        {statsCards.map((stat) => {
          const Icon = stat.icon;
          return (
            <Card key={stat.title} padding="lg" radius="md" withBorder>
              <Group justify="space-between">
                <div>
                  <Text c="dimmed" size="sm" fw={500}>
                    {stat.title}
                  </Text>
                  <Text fw={700} size="xl">
                    {stat.value}
                  </Text>
                </div>
                <ThemeIcon color={stat.color} size="xl" radius="md" variant="light">
                  <Icon size={24} />
                </ThemeIcon>
              </Group>
              <Group justify="space-between" mt="sm">
                <Badge 
                  color={stat.trend.includes('+') ? 'green' : stat.trend.includes('-') ? 'red' : 'blue'} 
                  variant="light"
                  size="sm"
                >
                  {stat.trend}
                </Badge>
                <Text size="xs" c="dimmed">vs last month</Text>
              </Group>
            </Card>
          );
        })}
      </SimpleGrid>

      {/* Main Content Tabs */}
      <Tabs value={activeTab} onChange={setActiveTab}>
        <Tabs.List>
          <Tabs.Tab value="requests" leftSection={<IconClipboardList size={16} />}>
            Imaging Requests
          </Tabs.Tab>
          <Tabs.Tab value="reports" leftSection={<IconReportMedical size={16} />}>
            Radiology Reports
          </Tabs.Tab>
          <Tabs.Tab value="studies" leftSection={<IconPhoto size={16} />}>
            Image Studies
          </Tabs.Tab>
          <Tabs.Tab value="equipment" leftSection={<IconDeviceDesktop size={16} />}>
            Equipment
          </Tabs.Tab>
          <Tabs.Tab value="radiologists" leftSection={<IconUsers size={16} />}>
            Radiologists
          </Tabs.Tab>
          <Tabs.Tab value="analytics" leftSection={<IconChartBar size={16} />}>
            Analytics
          </Tabs.Tab>
        </Tabs.List>

        {/* Imaging Requests Tab */}
        <Tabs.Panel value="requests">
          <Paper p="md" radius="md" withBorder mt="md">
            {loading && (
              <Group justify="center" mb="md">
                <Loader size="sm" />
                <Text size="sm" c="dimmed">Loading radiology data...</Text>
              </Group>
            )}
            {error && (
              <Alert icon={<IconAlertCircle size={16} />} color="red" mb="md" onClose={() => setError(null)} withCloseButton>
                {error}
              </Alert>
            )}
            {/* Search and Filters */}
            <Group mb="md">
              <TextInput
                placeholder="Search requests..."
                leftSection={<IconSearch size={16} />}
                value={searchQuery}
                onChange={(event) => setSearchQuery(event.currentTarget.value)}
                style={{ flex: 1 }}
              />
              <Select
                placeholder="Imaging Type"
                data={[
                  { value: 'xray', label: 'X-Ray' },
                  { value: 'ct', label: 'CT Scan' },
                  { value: 'mri', label: 'MRI' },
                  { value: 'ultrasound', label: 'Ultrasound' },
                  { value: 'mammography', label: 'Mammography' },
                  { value: 'nuclear_medicine', label: 'Nuclear Medicine' },
                  { value: 'fluoroscopy', label: 'Fluoroscopy' },
                  { value: 'pet', label: 'PET Scan' }
                ]}
                value={selectedType}
                onChange={setSelectedType}
                clearable
              />
              <Select
                placeholder="Status"
                data={[
                  { value: 'pending', label: 'Pending' },
                  { value: 'scheduled', label: 'Scheduled' },
                  { value: 'in_progress', label: 'In Progress' },
                  { value: 'completed', label: 'Completed' },
                  { value: 'cancelled', label: 'Cancelled' }
                ]}
                value={selectedStatus}
                onChange={setSelectedStatus}
                clearable
              />
              <Select
                placeholder="Priority"
                data={[
                  { value: 'emergency', label: 'Emergency' },
                  { value: 'urgent', label: 'Urgent' },
                  { value: 'routine', label: 'Routine' },
                  { value: 'elective', label: 'Elective' }
                ]}
                value={selectedPriority}
                onChange={setSelectedPriority}
                clearable
              />
              <Button variant="light" onClick={clearFilters}>
                Clear Filters
              </Button>
            </Group>

            {/* Requests Table */}
            <ScrollArea>
              <Table striped highlightOnHover>
                <Table.Thead>
                  <Table.Tr>
                    <Table.Th>Request ID</Table.Th>
                    <Table.Th>Patient</Table.Th>
                    <Table.Th>Exam Type</Table.Th>
                    <Table.Th>Imaging Type</Table.Th>
                    <Table.Th>Ordered By</Table.Th>
                    <Table.Th>Scheduled Date</Table.Th>
                    <Table.Th>Priority</Table.Th>
                    <Table.Th>Status</Table.Th>
                    <Table.Th>Actions</Table.Th>
                  </Table.Tr>
                </Table.Thead>
                <Table.Tbody>
                  {filteredRequests.length === 0 ? (
                    <Table.Tr>
                      <Table.Td colSpan={10}>
                        <EmptyState
                          icon={<IconRadioactive size={48} />}
                          title="No radiology orders"
                          description="Order your first imaging study to get started"
                          size="sm"
                        />
                      </Table.Td>
                    </Table.Tr>
                  ) : (
                    filteredRequests.map((request) => (
                      <Table.Tr key={request.id}>
                        <Table.Td>
                          <Text fw={500}>{(request as any).requestId || (request as any).appointmentId || 'N/A'}</Text>
                        </Table.Td>
                        <Table.Td>
                          <Group>
                            <Avatar color="blue" radius="xl" size="sm">
                              {request.patient.firstName[0]}{request.patient.lastName[0]}
                            </Avatar>
                            <div>
                              <Text size="sm" fw={500}>
                                {request.patient.firstName} {request.patient.lastName}
                              </Text>
                              <Text size="xs" c="dimmed">
                                {(request.patient as any).medicalRecordNumber ? `MRN: ${(request.patient as any).medicalRecordNumber}` : request.patient.id}
                              </Text>
                            </div>
                          </Group>
                        </Table.Td>
                        <Table.Td>
                          <div>
                            <Text size="sm" fw={500} lineClamp={1}>
                              {(request as any).examType || (request as any).modality || 'N/A'}
                            </Text>
                            {(request as any).bodyPart && (
                              <Text size="xs" c="dimmed">{(request as any).bodyPart}</Text>
                            )}
                          </div>
                        </Table.Td>
                        <Table.Td>
                          <Badge color={getImagingTypeColor((request as any).imagingType || (request as any).modality)} variant="light">
                            {((request as any).imagingType || (request as any).modality)?.replace('_', ' ').toUpperCase() || 'N/A'}
                          </Badge>
                        </Table.Td>
                        <Table.Td>
                          <div>
                            <Text size="sm" fw={500}>
                              {(request as any).orderingPhysician?.lastName ? `Dr. ${(request as any).orderingPhysician.lastName}` : 'N/A'}
                            </Text>
                            <Text size="xs" c="dimmed">
                              {(request as any).orderingPhysician?.department?.name || 'N/A'}
                            </Text>
                          </div>
                        </Table.Td>
                        <Table.Td>
                          <div>
                            <Text size="sm" fw={500}>
                              {request.scheduledDate ? (typeof request.scheduledDate === 'string' ? request.scheduledDate : new Date(request.scheduledDate).toISOString().split('T')[0]) : 'Not scheduled'}
                            </Text>
                            {request.scheduledDate && (
                              <Text size="xs" c="dimmed">
                                {typeof request.scheduledDate === 'string' ? '' : new Date(request.scheduledDate).toLocaleTimeString()}
                              </Text>
                            )}
                          </div>
                        </Table.Td>
                        <Table.Td>
                          <Badge color={getPriorityColor((request as any).priority)} variant="light">
                            {(request as any).priority?.toUpperCase() || 'ROUTINE'}
                          </Badge>
                        </Table.Td>
                        <Table.Td>
                          <Badge color={getStatusColor(request.status)} variant="light">
                            {request.status.replace('_', ' ')}
                          </Badge>
                        </Table.Td>
                        <Table.Td>
                          <Group gap="xs">
                            <ActionIcon
                              variant="subtle"
                              color="blue"
                              onClick={() => handleViewRequest(request)}
                            >
                              <IconEye size={16} />
                            </ActionIcon>
                            <ActionIcon variant="subtle" color="green">
                              <IconEdit size={16} />
                            </ActionIcon>
                            <Menu>
                              <Menu.Target>
                                <ActionIcon variant="subtle" color="gray">
                                  <IconDotsVertical size={16} />
                                </ActionIcon>
                              </Menu.Target>
                              <Menu.Dropdown>
                                <Menu.Item leftSection={<IconCalendar size={14} />}>
                                  Schedule Exam
                                </Menu.Item>
                                <Menu.Item leftSection={<IconScan size={14} />}>
                                  Start Scan
                                </Menu.Item>
                                <Menu.Item leftSection={<IconReportMedical size={14} />}>
                                  Create Report
                                </Menu.Item>
                                <Menu.Divider />
                                <Menu.Item 
                                  leftSection={<IconX size={14} />}
                                  color="red"
                                >
                                  Cancel Request
                                </Menu.Item>
                              </Menu.Dropdown>
                            </Menu>
                          </Group>
                        </Table.Td>
                      </Table.Tr>
                    ))
                  )}
                </Table.Tbody>
              </Table>
            </ScrollArea>
          </Paper>
        </Tabs.Panel>

        {/* Radiology Reports Tab */}
        <Tabs.Panel value="reports">
          <Paper p="md" radius="md" withBorder mt="md">
            <Group justify="space-between" mb="lg">
              <Title order={3}>Radiology Reports</Title>
              <Group>
                <Button leftSection={<IconPlus size={16} />} onClick={openCreateReport}>
                  Create Report
                </Button>
                <Button variant="light" leftSection={<IconDownload size={16} />}>
                  Export Reports
                </Button>
              </Group>
            </Group>

            {/* Reports Grid */}
            <SimpleGrid cols={{ base: 1, lg: 2 }} spacing="lg">
              {[].map /* TODO: Fetch from API */((report) => (
                <Card key={report.id} padding="lg" radius="md" withBorder>
                  <Group justify="space-between" mb="md">
                    <div>
                      <Text fw={600} size="lg">{report.reportId}</Text>
                      <Text size="sm" c="dimmed">{(report as any).examType || report.modality || 'N/A'}</Text>
                    </div>
                    <Badge color={getStatusColor(report.status)} variant="light">
                      {report.status}
                    </Badge>
                  </Group>

                  <Stack gap="sm" mb="md">
                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Patient</Text>
                      <Text size="sm" fw={500}>
                        {report.patient.firstName} {report.patient.lastName}
                      </Text>
                    </Group>
                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Radiologist</Text>
                      <Text size="sm" fw={500}>
                        {(report as any).radiologist?.lastName ? `Dr. ${(report as any).radiologist.lastName}` : 'N/A'}
                      </Text>
                    </Group>
                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Exam Date</Text>
                      <Text size="sm">
                        {(report as any).examDate ? (typeof (report as any).examDate === 'string' ? (report as any).examDate : new Date((report as any).examDate).toISOString().split('T')[0]) : 'N/A'}
                      </Text>
                    </Group>
                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Report Date</Text>
                      <Text size="sm">
                        {report.reportDate ? (typeof report.reportDate === 'string' ? report.reportDate : new Date(report.reportDate).toISOString().split('T')[0]) : 'Pending'}
                      </Text>
                    </Group>
                  </Stack>

                  {report.findings && (
                    <div>
                      <Text size="sm" fw={500} mb="xs">Findings</Text>
                      <Text size="sm" fw={500}>
                        {(report as any).numberOfImages || 0}
                      </Text>
                    </div>
                  )}

                  <Group justify="space-between" mt="md">
                    <Text size="xs" c="dimmed">
                      Priority: {(report as any).priority?.toUpperCase() || 'ROUTINE'}
                    </Text>
                    <Group gap="xs">
                      <ActionIcon
                        variant="subtle"
                        color="blue"
                        onClick={() => handleViewReport(report)}
                      >
                        <IconEye size={16} />
                      </ActionIcon>
                      <ActionIcon variant="subtle" color="green">
                        <IconEdit size={16} />
                      </ActionIcon>
                      <ActionIcon variant="subtle" color="orange">
                        <IconDownload size={16} />
                      </ActionIcon>
                    </Group>
                  </Group>
                </Card>
              ))}
            </SimpleGrid>
          </Paper>
        </Tabs.Panel>

        {/* Image Studies Tab */}
        <Tabs.Panel value="studies">
          <Paper p="md" radius="md" withBorder mt="md">
            <Group justify="space-between" mb="lg">
              <Title order={3}>Image Studies</Title>
              <Group>
                <Button leftSection={<IconUpload size={16} />}>
                  Upload Images
                </Button>
                <Button variant="light" leftSection={<IconPhoto size={16} />}>
                  Image Viewer
                </Button>
              </Group>
            </Group>

            {/* Studies Grid */}
            <SimpleGrid cols={{ base: 1, md: 2, lg: 3 }} spacing="lg">
              {[].map /* TODO: Fetch from API */((study) => (
                <Card key={study.id} padding="lg" radius="md" withBorder>
                  <Group justify="space-between" mb="md">
                    <div>
                      <Text fw={600} size="lg">{(study as any).studyId || study.reportId || 'N/A'}</Text>
                      <Text size="sm" c="dimmed">{(study as any).studyDescription || (study as any).modality || 'N/A'}</Text>
                    </div>
                    <Badge color={getStatusColor(study.status)} variant="light">
                      {study.status}
                    </Badge>
                  </Group>

                  {/* Study Thumbnail */}
                  <div
                    style={{
                      height: '120px',
                      backgroundColor: '#f1f3f4',
                      borderRadius: '8px',
                      display: 'flex',
                      alignItems: 'center',
                      justifyContent: 'center',
                      marginBottom: '1rem'
                    }}
                  >
                    <IconPhoto size={48} color="#868e96" />
                  </div>

                  <Stack gap="sm" mb="md">
                    <Group justify="space-between">
                      <Text size="sm">{(study as any).studySize || 'N/A'}</Text>
                      <Text size="sm" fw={500}>{(study as any).model || 'N/A'}</Text>
                    </Group>
                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Location</Text>
                      <Text size="sm">{(study as any).location || 'N/A'}</Text>
                    </Group>
                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Study Date</Text>
                      <Text size="sm">
                        {(study as any).studyDate ? (typeof (study as any).studyDate === 'string' ? (study as any).studyDate : new Date((study as any).studyDate).toISOString().split('T')[0]) : 'N/A'}
                      </Text>
                    </Group>
                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Modality</Text>
                      <Badge color={getImagingTypeColor((study as any).modality)} variant="light" size="sm">
                        {((study as any).modality || 'N/A').toUpperCase()}
                      </Badge>
                    </Group>
                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Images</Text>
                      <Text size="sm" fw={500}>{(study as any).numberOfImages || 0}</Text>
                    </Group>
                  </Stack>

                  <Group justify="space-between">
                    <Text size="xs" c="dimmed">
                      Size: {((study as any).studySize ? ((study as any).studySize / (1024 * 1024)).toFixed(1) : '0')} MB
                    </Text>
                    <Group gap="xs">
                      <ActionIcon
                        variant="subtle"
                        color="blue"
                        onClick={() => handleViewStudy(study)}
                      >
                        <IconEye size={16} />
                      </ActionIcon>
                      <ActionIcon variant="subtle" color="green">
                        <IconDownload size={16} />
                      </ActionIcon>
                      <ActionIcon variant="subtle" color="orange">
                        <IconShare size={16} />
                      </ActionIcon>
                    </Group>
                  </Group>
                </Card>
              ))}
            </SimpleGrid>
          </Paper>
        </Tabs.Panel>

        {/* Equipment Tab */}
        <Tabs.Panel value="equipment">
          <Paper p="md" radius="md" withBorder mt="md">
            <Group justify="space-between" mb="lg">
              <Title order={3}>Imaging Equipment</Title>
              <Group>
                <Button leftSection={<IconPlus size={16} />}>
                  Add Equipment
                </Button>
                <Button variant="light" leftSection={<IconSettings size={16} />}>
                  Maintenance Schedule
                </Button>
              </Group>
            </Group>

            {/* Equipment Filters */}
            <Group mb="md">
              <TextInput
                placeholder="Search equipment..."
                leftSection={<IconSearch size={16} />}
                value={searchQuery}
                onChange={(event) => setSearchQuery(event.currentTarget.value)}
                style={{ flex: 1 }}
              />
              <Select
                placeholder="Status"
                data={[
                  { value: 'operational', label: 'Operational' },
                  { value: 'maintenance', label: 'Maintenance' },
                  { value: 'in_use', label: 'In Use' },
                  { value: 'out_of_service', label: 'Out of Service' }
                ]}
                value={selectedEquipmentStatus}
                onChange={setSelectedEquipmentStatus}
                clearable
              />
            </Group>

            {/* Equipment Grid */}
            <SimpleGrid cols={{ base: 1, md: 2, lg: 3 }} spacing="lg">
              {filteredEquipment.map((equipment) => (
                <Card key={equipment.id} padding="lg" radius="md" withBorder>
                  <Group justify="space-between" mb="md">
                    <div>
                      <Text fw={600} size="lg">{equipment.equipmentName}</Text>
                      <Text size="sm" c="dimmed">{equipment.manufacturer}</Text>
                    </div>
                    <Badge color={getStatusColor(equipment.status)} variant="light">
                      {equipment.status.replace('_', ' ')}
                    </Badge>
                  </Group>

                  <Stack gap="sm" mb="md">
                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Model</Text>
                      <Text size="sm" fw={500}>{equipment.model}</Text>
                    </Group>
                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Location</Text>
                      <Text size="sm">{(equipment as any).location || 'N/A'}</Text>
                    </Group>
                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Installed</Text>
                      <Text size="sm">
                        {(equipment as any).installationDate ? (typeof (equipment as any).installationDate === 'string' ? (equipment as any).installationDate : new Date((equipment as any).installationDate).toISOString().split('T')[0]) : 'N/A'}
                      </Text>
                    </Group>
                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Last Maintenance</Text>
                      <Text size="sm">
                        {equipment.lastMaintenance || (equipment as any).lastMaintenanceDate || 'N/A'}
                      </Text>
                    </Group>
                  </Stack>

                  {(equipment as any).utilizationRate && (
                    <div>
                      <Group justify="space-between" mb="xs">
                        <Text size="sm" c="dimmed">Utilization</Text>
                        <Text size="sm" fw={500}>{(equipment as any).utilizationRate}%</Text>
                      </Group>
                      <Progress 
                        value={(equipment as any).utilizationRate} 
                        size="sm" 
                        color={(equipment as any).utilizationRate > 80 ? 'red' : (equipment as any).utilizationRate > 60 ? 'yellow' : 'green'}
                      />
                    </div>
                  )}

                  <Group justify="space-between" mt="md">
                    <Text size="xs" c="dimmed">
                      Type: {equipment.equipmentType}
                    </Text>
                    <Group gap="xs">
                      <ActionIcon
                        variant="subtle"
                        color="blue"
                        onClick={() => handleViewEquipment(equipment)}
                      >
                        <IconEye size={16} />
                      </ActionIcon>
                      <ActionIcon variant="subtle" color="green">
                        <IconSettings size={16} />
                      </ActionIcon>
                      <ActionIcon variant="subtle" color="orange">
                        <IconTool size={16} />
                      </ActionIcon>
                    </Group>
                  </Group>
                </Card>
              ))}
            </SimpleGrid>
          </Paper>
        </Tabs.Panel>

        {/* Radiologists Tab */}
        <Tabs.Panel value="radiologists">
          <Paper p="md" radius="md" withBorder mt="md">
            <Group justify="space-between" mb="lg">
              <Title order={3}>Radiologists</Title>
              <Button leftSection={<IconPlus size={16} />}>
                Add Radiologist
              </Button>
            </Group>

            {/* Radiologists Grid */}
            <SimpleGrid cols={{ base: 1, md: 2, lg: 3 }} spacing="lg">
              {[].map /* TODO: Fetch from API */((radiologist) => (
                <Card key={radiologist.id} padding="lg" radius="md" withBorder>
                  <Group mb="md">
                    <Avatar size="lg" color="blue" radius="xl">
                      {((radiologist as any).firstName || radiologist.name || 'R')[0]}{((radiologist as any).lastName || '')[0] || 'R'}
                    </Avatar>
                    <div>
                      <Text fw={600} size="lg">
                        {radiologist.name || `Dr. ${(radiologist as any).firstName || ''} ${(radiologist as any).lastName || ''}`}
                      </Text>
                      <Text size="sm" c="dimmed">{(radiologist as any).specialization || 'Radiology'}</Text>
                      <Badge 
                        color={(radiologist as any).isAvailable ? 'green' : 'red'} 
                        variant="light" 
                        size="sm"
                      >
                        {(radiologist as any).isAvailable ? 'Available' : 'Busy'}
                      </Badge>
                    </div>
                  </Group>

                  <Stack gap="sm" mb="md">
                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">License</Text>
                      <Text size="sm" fw={500}>{(radiologist as any).licenseNumber || 'N/A'}</Text>
                    </Group>
                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Experience</Text>
                      <Text size="sm">{(radiologist as any).yearsOfExperience || 0} years</Text>
                    </Group>
                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Reports (Month)</Text>
                      <Text size="sm" fw={500}>{(radiologist as any).reportsThisMonth || 0}</Text>
                    </Group>
                  </Stack>

                  <div>
                    <Text size="sm" fw={500} mb="xs">Specialties</Text>
                    <Group gap="xs">
                      {((radiologist as any).subspecialties || []).slice(0, 3).map((specialty: string) => (
                        <Badge key={specialty} size="xs" variant="light">
                          {specialty}
                        </Badge>
                      ))}
                      {((radiologist as any).subspecialties || []).length > 3 && (
                        <Badge size="xs" variant="light" color="gray">
                          +{((radiologist as any).subspecialties || []).length - 3}
                        </Badge>
                      )}
                    </Group>
                  </div>

                  <Group justify="space-between" mt="md">
                    <Text size="xs" c="dimmed">
                      Avg Turnaround: {(radiologist as any).averageTurnaroundTime || 0}h
                    </Text>
                    <Group gap="xs">
                      <ActionIcon variant="subtle" color="blue">
                        <IconEye size={16} />
                      </ActionIcon>
                      <ActionIcon variant="subtle" color="green">
                        <IconMail size={16} />
                      </ActionIcon>
                      <ActionIcon variant="subtle" color="orange">
                        <IconPhone size={16} />
                      </ActionIcon>
                    </Group>
                  </Group>
                </Card>
              ))}
            </SimpleGrid>
          </Paper>
        </Tabs.Panel>

        {/* Analytics Tab */}
        <Tabs.Panel value="analytics">
          <Paper p="md" radius="md" withBorder mt="md">
            <Title order={3} mb="lg">Radiology Analytics</Title>
            
            <SimpleGrid cols={{ base: 1, lg: 2 }} spacing="lg">
              {/* Imaging Types Distribution */}
              <Card padding="lg" radius="md" withBorder>
                <Title order={4} mb="md">Requests by Imaging Type</Title>
                <MantineDonutChart
                  data={imagingTypeData}
                  size={160}
                  thickness={30}
                  withLabels
                />
              </Card>
              
              {/* Monthly Volume */}
              <Card padding="lg" radius="md" withBorder>
                <Title order={4} mb="md">Monthly Imaging Volume</Title>
                <SimpleAreaChart
                  data={monthlyVolume}
                  dataKey="month"
                  series={[{ name: 'volume', color: 'blue.6' }]}
                />
              </Card>
              
              {/* Turnaround Times */}
              <Card padding="lg" radius="md" withBorder>
                <Title order={4} mb="md">Average Turnaround Times</Title>
                <SimpleBarChart
                  data={turnaroundTimes}
                  dataKey="type"
                  series={[
                    { name: 'hours', color: 'orange.6' }
                  ]}
                />
              </Card>
              
              {/* Equipment Utilization */}
              <Card padding="lg" radius="md" withBorder>
                <Title order={4} mb="md">Equipment Utilization</Title>
                <SimpleBarChart
                  data={equipmentUtilization}
                  dataKey="equipment"
                  series={[
                    { name: 'utilization', color: 'green.6' }
                  ]}
                />
              </Card>
              
              {/* Key Metrics */}
              <Card padding="lg" radius="md" withBorder style={{ gridColumn: '1 / -1' }}>
                <Title order={4} mb="md">Key Performance Indicators</Title>
                <SimpleGrid cols={{ base: 1, sm: 2, md: 4 }}>
                  <div style={{ textAlign: 'center' }}>
                    <RingProgress
                      size={120}
                      thickness={12}
                      sections={[{ value: 0, color: 'green' }]}
                      label={
                        <Text size="lg" fw={700} ta="center">
                          0%
                        </Text>
                      }
                    />
                    <Text size="sm" c="dimmed" mt="xs">Report Completion Rate</Text>
                  </div>
                  
                  <div style={{ textAlign: 'center' }}>
                    <RingProgress
                      size={120}
                      thickness={12}
                      sections={[{ value: 0, color: 'blue' }]}
                      label={
                        <Text size="lg" fw={700} ta="center">
                          0h
                        </Text>
                      }
                    />
                    <Text size="sm" c="dimmed" mt="xs">Avg Report Time</Text>
                  </div>
                  
                  <div style={{ textAlign: 'center' }}>
                    <RingProgress
                      size={120}
                      thickness={12}
                      sections={[{ value: 0, color: 'orange' }]}
                      label={
                        <Text size="lg" fw={700} ta="center">
                          0%
                        </Text>
                      }
                    />
                    <Text size="sm" c="dimmed" mt="xs">Equipment Uptime</Text>
                  </div>
                  
                  <div style={{ textAlign: 'center' }}>
                    <RingProgress
                      size={120}
                      thickness={12}
                      sections={[{ value: 0, color: 'purple' }]}
                      label={
                        <Text size="lg" fw={700} ta="center">
                          0%
                        </Text>
                      }
                    />
                    <Text size="sm" c="dimmed" mt="xs">Patient Satisfaction</Text>
                  </div>
                </SimpleGrid>
              </Card>
            </SimpleGrid>
          </Paper>
        </Tabs.Panel>
      </Tabs>

      {/* Request Detail Modal */}
      <Modal
        opened={requestDetailOpened}
        onClose={closeRequestDetail}
        title="Imaging Request Details"
        size="xl"
      >
        {selectedRequest && (
          <ScrollArea h={600}>
            <Stack gap="md">
              <Group>
                <ThemeIcon color="blue" size="xl" variant="light">
                  <IconClipboardList size={24} />
                </ThemeIcon>
                <div>
                  <Title order={3}>{selectedRequest.examType}</Title>
                  <Text c="dimmed">Request ID: {selectedRequest.requestId}</Text>
                  <Badge color={getStatusColor(selectedRequest.status)} variant="light" mt="xs">
                    {selectedRequest.status.replace('_', ' ')}
                  </Badge>
                </div>
              </Group>

              <Divider />

              <SimpleGrid cols={2}>
                <div>
                  <Text size="sm" fw={500}>Patient</Text>
                  <Text size="sm" c="dimmed">
                    {selectedRequest.patient.firstName} {selectedRequest.patient.lastName}
                  </Text>
                </div>
                <div>
                  <Text size="sm" fw={500}>Medical Record Number</Text>
                  <Text size="sm" c="dimmed">{selectedRequest.patient.medicalRecordNumber}</Text>
                </div>
                <div>
                  <Text size="sm" fw={500}>Imaging Type</Text>
                  <Badge color={getImagingTypeColor(selectedRequest.imagingType)} variant="light">
                    {selectedRequest.imagingType.replace('_', ' ').toUpperCase()}
                  </Badge>
                </div>
                <div>
                  <Text size="sm" fw={500}>Body Part</Text>
                  <Text size="sm" c="dimmed">{selectedRequest.bodyPart || 'N/A'}</Text>
                </div>
                <div>
                  <Text size="sm" fw={500}>Ordering Physician</Text>
                  <Text size="sm" c="dimmed">
                    Dr. {selectedRequest.orderingPhysician.firstName} {selectedRequest.orderingPhysician.lastName}
                  </Text>
                </div>
                <div>
                  <Text size="sm" fw={500}>Priority</Text>
                  <Badge color={getPriorityColor(selectedRequest.priority)} variant="light">
                    {selectedRequest.priority.toUpperCase()}
                  </Badge>
                </div>
                <div>
                  <Text size="sm" fw={500}>Request Date</Text>
                  <Text size="sm" c="dimmed">
                    {new Date(selectedRequest.requestDate).toLocaleString()}
                  </Text>
                </div>
                <div>
                  <Text size="sm" fw={500}>Scheduled Date</Text>
                  <Text size="sm" c="dimmed">
                    {selectedRequest.scheduledDate ? 
                      new Date(selectedRequest.scheduledDate).toLocaleString() : 
                      'Not scheduled'
                    }
                  </Text>
                </div>
              </SimpleGrid>

              {selectedRequest.clinicalHistory && (
                <>
                  <Divider />
                  <div>
                    <Text size="sm" fw={500} mb="sm">Clinical History</Text>
                    <Text size="sm">{selectedRequest.clinicalHistory}</Text>
                  </div>
                </>
              )}

              {selectedRequest.specialInstructions && (
                <>
                  <Divider />
                  <div>
                    <Text size="sm" fw={500} mb="sm">Special Instructions</Text>
                    <Text size="sm">{selectedRequest.specialInstructions}</Text>
                  </div>
                </>
              )}

              <Group justify="flex-end">
                <Button variant="light" onClick={closeRequestDetail}>
                  Close
                </Button>
                <Button leftSection={<IconCalendar size={16} />}>
                  Schedule Exam
                </Button>
                <Button leftSection={<IconEdit size={16} />}>
                  Edit Request
                </Button>
              </Group>
            </Stack>
          </ScrollArea>
        )}
      </Modal>

      {/* Add Request Modal */}
      <Modal
        opened={addRequestOpened}
        onClose={closeAddRequest}
        title="New Imaging Request"
        size="lg"
      >
        <Stack gap="md">
          <SimpleGrid cols={2}>
            <Select
              label="Patient"
              placeholder="Select patient"
              data={[].map /* TODO: Fetch from API */(patient => ({ 
                value: patient.id, 
                label: `${patient.firstName} ${patient.lastName}` 
              }))}
              required
            />
            <Select
              label="Ordering Physician"
              placeholder="Select physician"
              data={[].filter /* TODO: Fetch from API */((staff: any) => staff.role === 'Doctor' || staff.role === 'doctor').map((doctor: any) => ({ 
                value: doctor.staffId, 
                label: `Dr. ${doctor.firstName} ${doctor.lastName}` 
              }))}
              required
            />
          </SimpleGrid>
          
          <TextInput
            label="Exam Type"
            placeholder="Enter exam type"
            required
          />
          
          <SimpleGrid cols={2}>
            <Select
              label="Imaging Type"
              placeholder="Select imaging type"
              data={[
                { value: 'xray', label: 'X-Ray' },
                { value: 'ct', label: 'CT Scan' },
                { value: 'mri', label: 'MRI' },
                { value: 'ultrasound', label: 'Ultrasound' },
                { value: 'mammography', label: 'Mammography' },
                { value: 'nuclear_medicine', label: 'Nuclear Medicine' },
                { value: 'fluoroscopy', label: 'Fluoroscopy' },
                { value: 'pet', label: 'PET Scan' }
              ]}
              required
            />
            <Select
              label="Priority"
              placeholder="Select priority"
              data={[
                { value: 'emergency', label: 'Emergency' },
                { value: 'urgent', label: 'Urgent' },
                { value: 'routine', label: 'Routine' },
                { value: 'elective', label: 'Elective' }
              ]}
              required
            />
          </SimpleGrid>
          
          <TextInput
            label="Body Part"
            placeholder="Enter body part to be imaged"
          />
          
          <Textarea
            label="Clinical History"
            placeholder="Enter clinical history"
            rows={3}
            required
          />
          
          <Textarea
            label="Special Instructions"
            placeholder="Enter any special instructions"
            rows={2}
          />
          
          <Group justify="flex-end">
            <Button variant="light" onClick={closeAddRequest}>
              Cancel
            </Button>
            <Button onClick={() => {
              notifications.show({
                title: 'Request Created',
                message: 'Imaging request has been successfully created',
                color: 'green',
              });
              closeAddRequest();
            }}>
              Create Request
            </Button>
          </Group>
        </Stack>
      </Modal>

      {/* Study Viewer Modal */}
      <Modal
        opened={studyViewerOpened}
        onClose={closeStudyViewer}
        title="Image Study Viewer"
        size="xl"
        fullScreen
      >
        {selectedStudy && (
          <div style={{ height: 'calc(100vh - 120px)' }}>
            <Group justify="space-between" mb="md">
              <div>
                <Text fw={600} size="lg">{selectedStudy.studyId}</Text>
                <Text size="sm" c="dimmed">
                  {selectedStudy.patient.firstName} {selectedStudy.patient.lastName} - {selectedStudy.studyDescription}
                </Text>
              </div>
              <Group>
                <Button variant="light" leftSection={<IconZoom size={16} />}>
                  Zoom
                </Button>
                <Button variant="light" leftSection={<IconAdjustments size={16} />}>
                  Adjust
                </Button>
                <Button variant="light" leftSection={<IconRuler size={16} />}>
                  Measure
                </Button>
                <Button variant="light" leftSection={<IconDownload size={16} />}>
                  Download
                </Button>
              </Group>
            </Group>
            
            <div
              style={{
                height: 'calc(100% - 60px)',
                backgroundColor: '#1a1b1e',
                borderRadius: '8px',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                position: 'relative'
              }}
            >
              <div style={{ textAlign: 'center', color: '#868e96' }}>
                <IconPhoto size={120} />
                <Text size="lg" mt="md">Medical Image Viewer</Text>
                <Text size="sm" c="dimmed">
                  {selectedStudy.numberOfImages} images available
                </Text>
              </div>
              
              {/* Image viewer controls */}
              <div
                style={{
                  position: 'absolute',
                  bottom: '20px',
                  left: '50%',
                  transform: 'translateX(-50%)',
                  display: 'flex',
                  gap: '10px'
                }}
              >
                <ActionIcon color="white" variant="filled">
                  <IconPlayerPlay size={16} />
                </ActionIcon>
                <ActionIcon color="white" variant="filled">
                  <IconPlayerPause size={16} />
                </ActionIcon>
                <ActionIcon color="white" variant="filled">
                  <IconRotate size={16} />
                </ActionIcon>
                <ActionIcon color="white" variant="filled">
                  <IconContrast size={16} />
                </ActionIcon>
                <ActionIcon color="white" variant="filled">
                  <IconBrightness size={16} />
                </ActionIcon>
              </div>
            </div>
          </div>
        )}
      </Modal>
    </Container>
  );
};

export default RadiologyManagement;

'use client';

import React, { useState, useMemo } from 'react';
import { useRouter } from 'next/navigation';
import {
  Container,
  Paper,
  Title,
  Group,
  Button,
  TextInput,
  Select,
  Badge,
  Table,
  Modal,
  Text,
  Tabs,
  Card,
  ActionIcon,
  Stack,
  SimpleGrid,
  ScrollArea,
  ThemeIcon,
  Progress,
  MultiSelect,
  Divider,
  Alert,
  Timeline,
  Switch,
  NumberInput
} from '@mantine/core';
import { useDisclosure } from '@mantine/hooks';
import { notifications } from '@mantine/notifications';
import { DatePickerInput } from '@mantine/dates';
import EmptyState from '../../../components/EmptyState';
import { MantineDonutChart, SimpleAreaChart, SimpleBarChart, SimpleLineChart } from '../../../components/MantineChart';
import {
  IconPlus,
  IconSearch,
  IconEdit,
  IconEye,
  IconTrash,
  IconDownload,
  IconChartBar,
  IconReportAnalytics,
  IconFileText,
  IconFileReport,
  IconCalendar,
  IconUsers,
  IconCurrencyDollar,
  IconBed,
  IconStethoscope,
  IconActivity,
  IconTrendingUp,
  IconTrendingDown,
  IconClipboard,
  IconPrinter,
  IconShare,
  IconFilter,
  IconRefresh,
  IconFileSpreadsheet,
  IconFileTypePdf,
  IconMail,
  IconClock,
  IconCheck,
  IconX,
  IconAlertCircle,
  IconChartLine,
  IconChartPie,
  IconDatabase,
  IconSettings,
  IconCalendarEvent,
  IconTarget,
  IconDeviceAnalytics
} from '@tabler/icons-react';

// Types
interface Report {
  id: string;
  name: string;
  description: string;
  category: string;
  type: 'financial' | 'operational' | 'clinical' | 'quality' | 'administrative';
  format: 'PDF' | 'Excel' | 'CSV' | 'Dashboard';
  frequency: 'daily' | 'weekly' | 'monthly' | 'quarterly' | 'yearly' | 'on-demand';
  status: 'active' | 'inactive' | 'scheduled' | 'running' | 'completed' | 'failed';
  lastGenerated: string;
  nextRun?: string;
  createdBy: string;
  createdAt: string;
  parameters: Array<{
    name: string;
    type: string;
    value: any;
  }>;
  recipients: string[];
  size?: string;
  executionTime?: number;
}

interface Analytics {
  id: string;
  title: string;
  description: string;
  category: string;
  metrics: Array<{
    name: string;
    value: number | string;
    change?: {
      value: number;
      type: 'increase' | 'decrease';
      period: string;
    };
  }>;
  chartType: 'line' | 'bar' | 'pie' | 'area';
  chartData: any[];
  lastUpdated: string;
}

