const ReportsAnalytics = () => {
  const router = useRouter();
  
  // State management
  const [activeTab, setActiveTab] = useState<string>('reports');
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedCategory, setSelectedCategory] = useState<string>('');
  const [selectedStatus, setSelectedStatus] = useState<string>('');
  const [selectedReport, setSelectedReport] = useState<Report | null>(null);

  // Modal states
  const [reportDetailOpened, { open: openReportDetail, close: closeReportDetail }] = useDisclosure(false);
  const [createReportOpened, { open: openCreateReport, close: closeCreateReport }] = useDisclosure(false);
  const [scheduleReportOpened, { open: openScheduleReport, close: closeScheduleReport }] = useDisclosure(false);

  // Filter reports
  const filteredReports = useMemo(() => {
    return [].filter /* TODO: Fetch from API */((report) => {
      const matchesSearch = 
        report.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
        report.description.toLowerCase().includes(searchQuery.toLowerCase());
      
      const matchesCategory = !selectedCategory || report.category === selectedCategory;
      const matchesStatus = !selectedStatus || report.status === selectedStatus;

      return matchesSearch && matchesCategory && matchesStatus;
    });
  }, [searchQuery, selectedCategory, selectedStatus]);

  const handleViewReport = (report: Report) => {
    setSelectedReport(report);
    openReportDetail();
  };

  const formatDate = (date: string) => {
    return new Date(date).toLocaleDateString('en-IN', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'completed': return 'green';
      case 'active': return 'blue';
      case 'running': return 'orange';
      case 'scheduled': return 'cyan';
      case 'failed': return 'red';
      case 'inactive': return 'gray';
      default: return 'gray';
    }
  };

  const getFormatIcon = (format: string) => {
    switch (format) {
      case 'PDF': return <IconFileTypePdf size={16} />;
      case 'Excel': return <IconFileSpreadsheet size={16} />;
      case 'CSV': return <IconFileText size={16} />;
      case 'Dashboard': return <IconChartBar size={16} />;
      default: return <IconFileText size={16} />;
    }
  };

  // Quick stats
  const reportStats = {
    total: 0 /* TODO: Fetch from API */,
    active: [].filter /* TODO: Fetch from API */(r => r.status === 'active' || r.status === 'scheduled').length,
    completed: [].filter /* TODO: Fetch from API */(r => r.status === 'completed').length,
    failed: [].filter /* TODO: Fetch from API */(r => r.status === 'failed').length
  };

  return (
    <Container size="xl" py="md">
      {/* Header */}
      <Group justify="space-between" mb="lg">
        <div>
          <Title order={1}>Reports & Analytics</Title>
          <Text c="dimmed" size="sm">
            Comprehensive reporting and business intelligence dashboard
          </Text>
        </div>
        <Group>
          <Button variant="light" leftSection={<IconRefresh size={16} />}>
            Refresh All
          </Button>
          <Button leftSection={<IconPlus size={16} />} onClick={openCreateReport}>
            Create Report
          </Button>
        </Group>
      </Group>

      {/* Quick Stats */}
      <SimpleGrid cols={{ base: 1, sm: 2, md: 4 }} mb="lg">
        <Card padding="lg" radius="md" withBorder>
          <Group justify="space-between">
            <div>
              <Text c="dimmed" size="sm" fw={500}>Total Reports</Text>
              <Text fw={700} size="xl">{reportStats.total}</Text>
            </div>
            <ThemeIcon color="blue" size="xl" radius="md" variant="light">
              <IconFileText size={24} />
            </ThemeIcon>
          </Group>
        </Card>

        <Card padding="lg" radius="md" withBorder>
          <Group justify="space-between">
            <div>
              <Text c="dimmed" size="sm" fw={500}>Active Reports</Text>
              <Text fw={700} size="xl">{reportStats.active}</Text>
            </div>
            <ThemeIcon color="green" size="xl" radius="md" variant="light">
              <IconActivity size={24} />
            </ThemeIcon>
          </Group>
        </Card>

        <Card padding="lg" radius="md" withBorder>
          <Group justify="space-between">
            <div>
              <Text c="dimmed" size="sm" fw={500}>Completed Today</Text>
              <Text fw={700} size="xl">{reportStats.completed}</Text>
            </div>
            <ThemeIcon color="cyan" size="xl" radius="md" variant="light">
              <IconCheck size={24} />
            </ThemeIcon>
          </Group>
        </Card>

        <Card padding="lg" radius="md" withBorder>
          <Group justify="space-between">
            <div>
              <Text c="dimmed" size="sm" fw={500}>Failed Reports</Text>
              <Text fw={700} size="xl">{reportStats.failed}</Text>
            </div>
            <ThemeIcon color="red" size="xl" radius="md" variant="light">
              <IconAlertCircle size={24} />
            </ThemeIcon>
          </Group>
        </Card>
      </SimpleGrid>

      {/* Main Content Tabs */}
      <Tabs value={activeTab} onChange={setActiveTab}>
        <Tabs.List>
          <Tabs.Tab value="reports" leftSection={<IconFileText size={16} />}>
            All Reports
          </Tabs.Tab>
          <Tabs.Tab value="analytics" leftSection={<IconChartBar size={16} />}>
            Analytics
          </Tabs.Tab>
          <Tabs.Tab value="dashboards" leftSection={<IconDeviceAnalytics size={16} />}>
            Dashboards
          </Tabs.Tab>
        </Tabs.List>

        {/* Reports Tab */}
        <Tabs.Panel value="reports">
          <Paper p="md" radius="md" withBorder mt="md">
            {/* Filters */}
            <Group mb="md">
              <TextInput
                placeholder="Search reports..."
                leftSection={<IconSearch size={16} />}
                value={searchQuery}
                onChange={(event) => setSearchQuery(event.currentTarget.value)}
                style={{ flex: 1 }}
              />
              <Select
                placeholder="Category"
                data={[
                  { value: 'Financial Reports', label: 'Financial Reports' },
                  { value: 'Operational Reports', label: 'Operational Reports' },
                  { value: 'Clinical Reports', label: 'Clinical Reports' },
                  { value: 'Quality Reports', label: 'Quality Reports' }
                ]}
                value={selectedCategory}
                onChange={setSelectedCategory}
                clearable
              />
              <Select
                placeholder="Status"
                data={[
                  { value: 'active', label: 'Active' },
                  { value: 'completed', label: 'Completed' },
                  { value: 'running', label: 'Running' },
                  { value: 'scheduled', label: 'Scheduled' },
                  { value: 'failed', label: 'Failed' },
                  { value: 'inactive', label: 'Inactive' }
                ]}
                value={selectedStatus}
                onChange={setSelectedStatus}
                clearable
              />
            </Group>

            {/* Reports Table */}
            <ScrollArea>
              <Table striped highlightOnHover>
                <Table.Thead>
                  <Table.Tr>
                    <Table.Th>Report Name</Table.Th>
                    <Table.Th>Category</Table.Th>
                    <Table.Th>Format</Table.Th>
                    <Table.Th>Frequency</Table.Th>
                    <Table.Th>Last Generated</Table.Th>
                    <Table.Th>Status</Table.Th>
                    <Table.Th>Actions</Table.Th>
                  </Table.Tr>
                </Table.Thead>
                <Table.Tbody>
                  {filteredReports.length === 0 ? (
                    <Table.Tr>
                      <Table.Td colSpan={7}>
                        <EmptyState
                          icon={<IconFileReport size={48} />}
                          title="No reports generated"
                          description="Generate your first report"
                          size="sm"
                        />
                      </Table.Td>
                    </Table.Tr>
                  ) : (
                    filteredReports.map((report) => (
                      <Table.Tr key={report.id}>
                        <Table.Td>
                          <div>
                            <Text fw={500} size="sm">{report.name}</Text>
                            <Text size="xs" c="dimmed" lineClamp={1}>{report.description}</Text>
                          </div>
                        </Table.Td>
                        <Table.Td>
                          <Badge variant="light" size="sm">
                            {report.category}
                          </Badge>
                        </Table.Td>
                        <Table.Td>
                          <Group gap="xs">
                            {getFormatIcon(report.format)}
                            <Text size="sm">{report.format}</Text>
                          </Group>
                        </Table.Td>
                        <Table.Td>
                          <Text size="sm" tt="capitalize">{report.frequency}</Text>
                        </Table.Td>
                        <Table.Td>
                          <Text size="sm">{formatDate(report.lastGenerated)}</Text>
                        </Table.Td>
                        <Table.Td>
                          <Badge color={getStatusColor(report.status)} variant="light">
                            {report.status.toUpperCase()}
                          </Badge>
                        </Table.Td>
                        <Table.Td>
                          <Group gap="xs">
                            <ActionIcon
                              variant="subtle"
                              color="blue"
                              onClick={() => handleViewReport(report)}
                            >
                              <IconEye size={16} />
                            </ActionIcon>
                            <ActionIcon 
                              variant="subtle" 
                              color="green"
                              onClick={() => {
                                notifications.show({
                                  title: 'Downloading Report',
                                  message: `${report.name} is being downloaded...`,
                                  color: 'green',
                                });
                              }}
                            >
                              <IconDownload size={16} />
                            </ActionIcon>
                            <ActionIcon 
                              variant="subtle" 
                              color="orange"
                              onClick={() => {
                                notifications.show({
                                  title: 'Edit Report',
                                  message: `Opening editor for ${report.name}...`,
                                  color: 'orange',
                                });
                              }}
                            >
                              <IconEdit size={16} />
                            </ActionIcon>
                            <ActionIcon 
                              variant="subtle" 
                              color="purple"
                              onClick={() => {
                                openScheduleReport();
                                setSelectedReport(report);
                              }}
                            >
                              <IconCalendarEvent size={16} />
                            </ActionIcon>
                          </Group>
                        </Table.Td>
                      </Table.Tr>
                    ))
                  )}
              </Table.Tbody>
            </Table>
          </ScrollArea>
        </Paper>
      </Tabs.Panel>

        {/* Analytics Tab */}
        <Tabs.Panel value="analytics">
          <SimpleGrid cols={{ base: 1, lg: 2 }} spacing="lg" mt="md">
            {[].map /* TODO: Fetch from API */((analytics) => (
              <Card key={analytics.id} padding="lg" radius="md" withBorder>
                <Group justify="space-between" mb="md">
                  <div>
                    <Title order={4}>{analytics.title}</Title>
                    <Text size="sm" c="dimmed">{analytics.description}</Text>
                  </div>
                  <Badge variant="light">{analytics.category}</Badge>
                </Group>

                {/* Metrics */}
                <SimpleGrid cols={3} spacing="md" mb="md">
                  {analytics.metrics.map((metric, index) => (
                    <div key={index} style={{ textAlign: 'center' }}>
                      <Text size="xl" fw={700} mb="xs">{metric.value}</Text>
                      <Text size="xs" c="dimmed" mb="xs">{metric.name}</Text>
                      {metric.change && (
                        <Group justify="center" gap="xs">
                          {metric.change.type === 'increase' ? 
                            <IconTrendingUp size={14} color="green" /> : 
                            <IconTrendingDown size={14} color="red" />
                          }
                          <Text 
                            size="xs" 
                            c={metric.change.type === 'increase' ? 'green' : 'red'}
                            fw={500}
                          >
                            {metric.change.value}%
                          </Text>
                        </Group>
                      )}
                    </div>
                  ))}
                </SimpleGrid>

                {/* Chart */}
                {analytics.chartType === 'line' && (
                  <SimpleLineChart
                    h={200}
                    data={analytics.chartData}
                    dataKey="date"
                    series={[
                      { name: 'admissions', color: 'blue.6', label: 'Admissions' },
                      { name: 'discharges', color: 'red.6', label: 'Discharges' }
                    ]}
                    curveType="linear"
                  />
                )}

                {analytics.chartType === 'bar' && (
                  <SimpleBarChart
                    h={200}
                    data={analytics.chartData}
                    dataKey="department"
                    series={[{ name: 'revenue', color: 'green.6' }]}
                  />
                )}

                <Text size="xs" c="dimmed" ta="right" mt="sm">
                  Last updated: {formatDate(analytics.lastUpdated)}
                </Text>
              </Card>
            ))}
          </SimpleGrid>
        </Tabs.Panel>

        {/* Dashboards Tab */}
        <Tabs.Panel value="dashboards">
          <Paper p="md" radius="md" withBorder mt="md">
            <Title order={3} mb="lg">Interactive Dashboards</Title>
            
            <SimpleGrid cols={{ base: 1, md: 2, lg: 3 }} spacing="lg">
              {/* Executive Dashboard */}
              <Card padding="lg" radius="md" withBorder>
                <ThemeIcon size="xl" color="blue" mb="md">
                  <IconChartPie size={24} />
                </ThemeIcon>
                <Title order={5} mb="xs">Executive Dashboard</Title>
                <Text size="sm" c="dimmed" mb="md">
                  High-level KPIs and strategic metrics for leadership
                </Text>
                <Group justify="space-between">
                  <Badge variant="light" color="green">Live</Badge>
                  <Button size="xs" variant="light" onClick={() => {
                    console.log('Executive Dashboard button clicked');
                    notifications.show({
                      title: 'Opening Dashboard',
                      message: 'Executive Dashboard is loading...',
                      color: 'blue',
                    });
                    // Navigate to main dashboard with executive view
                    setTimeout(() => {
                      console.log('Navigating to /dashboard');
                      router.push('/dashboard');
                    }, 1000);
                  }}>Open Dashboard</Button>
                </Group>
              </Card>

              {/* Operations Dashboard */}
              <Card padding="lg" radius="md" withBorder>
                <ThemeIcon size="xl" color="orange" mb="md">
                  <IconActivity size={24} />
                </ThemeIcon>
                <Title order={5} mb="xs">Operations Dashboard</Title>
                <Text size="sm" c="dimmed" mb="md">
                  Real-time operational metrics and bed management
                </Text>
                <Group justify="space-between">
                  <Badge variant="light" color="green">Live</Badge>
                  <Button size="xs" variant="light" onClick={() => {
                    console.log('Operations Dashboard button clicked');
                    notifications.show({
                      title: 'Opening Dashboard',
                      message: 'Operations Dashboard is loading...',
                      color: 'orange',
                    });
                    // Navigate to IPD for operations metrics
                    setTimeout(() => {
                      console.log('Navigating to /dashboard/ipd');
                      router.push('/dashboard/ipd');
                    }, 1000);
                  }}>Open Dashboard</Button>
                </Group>
              </Card>

              {/* Financial Dashboard */}
              <Card padding="lg" radius="md" withBorder>
                <ThemeIcon size="xl" color="green" mb="md">
                  <IconCurrencyDollar size={24} />
                </ThemeIcon>
                <Title order={5} mb="xs">Financial Dashboard</Title>
                <Text size="sm" c="dimmed" mb="md">
                  Revenue, billing, and financial performance tracking
                </Text>
                <Group justify="space-between">
                  <Badge variant="light" color="green">Live</Badge>
                  <Button size="xs" variant="light" onClick={() => {
                    console.log('Financial Dashboard button clicked');
                    notifications.show({
                      title: 'Opening Dashboard',
                      message: 'Financial Dashboard is loading...',
                      color: 'green',
                    });
                    // Navigate to finance dashboard
                    setTimeout(() => {
                      console.log('Navigating to /dashboard/finance');
                      router.push('/dashboard/finance');
                    }, 1000);
                  }}>Open Dashboard</Button>
                </Group>
              </Card>

              {/* Quality Dashboard */}
              <Card padding="lg" radius="md" withBorder>
                <ThemeIcon size="xl" color="purple" mb="md">
                  <IconTarget size={24} />
                </ThemeIcon>
                <Title order={5} mb="xs">Quality Dashboard</Title>
                <Text size="sm" c="dimmed" mb="md">
                  Patient safety, satisfaction, and quality indicators
                </Text>
                <Group justify="space-between">
                  <Badge variant="light" color="green">Live</Badge>
                  <Button size="xs" variant="light" onClick={() => {
                    console.log('Quality Dashboard button clicked');
                    notifications.show({
                      title: 'Opening Dashboard',
                      message: 'Quality Dashboard is loading...',
                      color: 'purple',
                    });
                    // Navigate to quality management
                    setTimeout(() => {
                      console.log('Navigating to /dashboard/quality');
                      router.push('/dashboard/quality');
                    }, 1000);
                  }}>Open Dashboard</Button>
                </Group>
              </Card>

              {/* Clinical Dashboard */}
              <Card padding="lg" radius="md" withBorder>
                <ThemeIcon size="xl" color="red" mb="md">
                  <IconStethoscope size={24} />
                </ThemeIcon>
                <Title order={5} mb="xs">Clinical Dashboard</Title>
                <Text size="sm" c="dimmed" mb="md">
                  Clinical outcomes, patient flow, and care metrics
                </Text>
                <Group justify="space-between">
                  <Badge variant="light" color="green">Live</Badge>
                  <Button size="xs" variant="light" onClick={() => {
                    console.log('Clinical Dashboard button clicked');
                    notifications.show({
                      title: 'Opening Dashboard',
                      message: 'Clinical Dashboard is loading...',
                      color: 'red',
                    });
                    // Navigate to EMR for clinical metrics
                    setTimeout(() => {
                      console.log('Navigating to /dashboard/emr');
                      router.push('/dashboard/emr');
                    }, 1000);
                  }}>Open Dashboard</Button>
                </Group>
              </Card>

              {/* Custom Dashboard */}
              <Card padding="lg" radius="md" withBorder style={{ border: '2px dashed #e9ecef' }}>
                <ThemeIcon size="xl" color="gray" mb="md">
                  <IconPlus size={24} />
                </ThemeIcon>
                <Title order={5} mb="xs">Create Custom Dashboard</Title>
                <Text size="sm" c="dimmed" mb="md">
                  Build your own dashboard with custom metrics
                </Text>
                <Button size="xs" variant="outline" fullWidth onClick={() => {
                  console.log('Create Custom Dashboard button clicked');
                  notifications.show({
                    title: 'Create Dashboard',
                    message: 'Custom dashboard builder coming soon!',
                    color: 'blue',
                  });
                  // Future: Open custom dashboard builder modal
                }}>
                  Create Dashboard
                </Button>
              </Card>
            </SimpleGrid>
          </Paper>
        </Tabs.Panel>
      </Tabs>

      {/* Report Detail Modal */}
      <Modal
        opened={reportDetailOpened}
        onClose={closeReportDetail}
        title="Report Details"
        size="lg"
      >
        {selectedReport && (
          <Stack gap="md">
            <Group justify="space-between">
              <div>
                <Title order={3}>{selectedReport.name}</Title>
                <Text c="dimmed">{selectedReport.description}</Text>
              </div>
              <Badge color={getStatusColor(selectedReport.status)} variant="light">
                {selectedReport.status.toUpperCase()}
              </Badge>
            </Group>

            <Divider />

            <SimpleGrid cols={2} spacing="md">
              <div>
                <Text size="sm" c="dimmed" fw={500}>Category</Text>
                <Text>{selectedReport.category}</Text>
              </div>
              <div>
                <Text size="sm" c="dimmed" fw={500}>Format</Text>
                <Text>{selectedReport.format}</Text>
              </div>
              <div>
                <Text size="sm" c="dimmed" fw={500}>Frequency</Text>
                <Text tt="capitalize">{selectedReport.frequency}</Text>
              </div>
              <div>
                <Text size="sm" c="dimmed" fw={500}>Created By</Text>
                <Text>{selectedReport.createdBy}</Text>
              </div>
              <div>
                <Text size="sm" c="dimmed" fw={500}>Last Generated</Text>
                <Text>{formatDate(selectedReport.lastGenerated)}</Text>
              </div>
              {selectedReport.nextRun && (
                <div>
                  <Text size="sm" c="dimmed" fw={500}>Next Run</Text>
                  <Text>{formatDate(selectedReport.nextRun)}</Text>
                </div>
              )}
            </SimpleGrid>

            <div>
              <Text size="sm" c="dimmed" fw={500} mb="xs">Recipients</Text>
              <Group gap="xs">
                {selectedReport.recipients.map((recipient, index) => (
                  <Badge key={index} variant="outline" size="sm">
                    {recipient}
                  </Badge>
                ))}
              </Group>
            </div>

            <div>
              <Text size="sm" c="dimmed" fw={500} mb="xs">Parameters</Text>
              <Stack gap="xs">
                {selectedReport.parameters.map((param, index) => (
                  <Group key={index} justify="space-between" p="xs" style={{ backgroundColor: '#f8f9fa', borderRadius: '4px' }}>
                    <Text size="sm" fw={500}>{param.name}</Text>
                    <Text size="sm">{param.value}</Text>
                  </Group>
                ))}
              </Stack>
            </div>

            <Group justify="flex-end">
              <Button variant="light" onClick={closeReportDetail}>Close</Button>
              <Button variant="light" leftSection={<IconDownload size={16} />}>
                Download
              </Button>
              <Button leftSection={<IconShare size={16} />}>
                Share
              </Button>
            </Group>
          </Stack>
        )}
      </Modal>

      {/* Create Report Modal */}
      <Modal
        opened={createReportOpened}
        onClose={closeCreateReport}
        title="Create New Report"
        size="lg"
      >
        <Stack gap="md">
          <SimpleGrid cols={2} spacing="md">
            <TextInput
              label="Report Name"
              placeholder="Enter report name"
              required
            />
            <Select
              label="Category"
              placeholder="Select category"
              data={[
                { value: 'financial', label: 'Financial' },
                { value: 'operational', label: 'Operational' },
                { value: 'clinical', label: 'Clinical' },
                { value: 'quality', label: 'Quality' }
              ]}
              required
            />
          </SimpleGrid>

          <TextInput
            label="Description"
            placeholder="Enter report description"
          />

          <SimpleGrid cols={2} spacing="md">
            <Select
              label="Format"
              placeholder="Select format"
              data={[
                { value: 'PDF', label: 'PDF' },
                { value: 'Excel', label: 'Excel' },
                { value: 'CSV', label: 'CSV' },
                { value: 'Dashboard', label: 'Dashboard' }
              ]}
              required
            />
            <Select
              label="Frequency"
              placeholder="Select frequency"
              data={[
                { value: 'daily', label: 'Daily' },
                { value: 'weekly', label: 'Weekly' },
                { value: 'monthly', label: 'Monthly' },
                { value: 'quarterly', label: 'Quarterly' },
                { value: 'yearly', label: 'Yearly' },
                { value: 'on-demand', label: 'On Demand' }
              ]}
              required
            />
          </SimpleGrid>

          <MultiSelect
            label="Recipients"
            placeholder="Select recipients"
            data={[
              { value: 'finance@hospital.com', label: 'Finance Team' },
              { value: 'operations@hospital.com', label: 'Operations Team' },
              { value: 'clinical@hospital.com', label: 'Clinical Team' },
              { value: 'admin@hospital.com', label: 'Administration' }
            ]}
            searchable
          />

          <Group justify="flex-end">
            <Button variant="light" onClick={closeCreateReport}>
              Cancel
            </Button>
            <Button onClick={() => {
              notifications.show({
                title: 'Report Created',
                message: 'New report has been successfully created',
                color: 'green',
              });
              closeCreateReport();
            }}>
              Create Report
            </Button>
          </Group>
        </Stack>
      </Modal>
    </Container>
  );
};

export default ReportsAnalytics;

'use client';

import React, { useState, useMemo, useEffect } from 'react';
import {
  Container,
  Paper,
  Title,
  Group,
  Button,
  TextInput,
  Select,
  Badge,
  Table,
  Modal,
  Text,
  Tabs,
  Card,
  Stack,
  SimpleGrid,
  ThemeIcon,
  Progress,
  ActionIcon,
  Menu,
  ScrollArea,
  Box,
  Divider,
  NumberInput,
  Textarea
} from '@mantine/core';
import { useDisclosure } from '@mantine/hooks';
import EmptyState from '../../../components/EmptyState';
import { notifications } from '@mantine/notifications';
import {
  IconPlus,
  IconSearch,
  IconEdit,
  IconEye,
  IconTrash,
  IconUsers,
  IconFlask,
  IconFileText,
  IconCalendar,
  IconTrendingUp,
  IconAlertCircle,
  IconCheck,
  IconX,
  IconDotsVertical,
  IconClipboardList,
  IconBooks,
  IconActivity,
  IconDownload
} from '@tabler/icons-react';
// Mock data imports removed
import researchService from '../../../services/research.service';

export default function ResearchPage() {
  const [searchQuery, setSearchQuery] = useState('');
  const [statusFilter, setStatusFilter] = useState<string | null>('all');
  const [activeTab, setActiveTab] = useState('trials');
  const [selectedTrial, setSelectedTrial] = useState<any>(null);
  
  const [trialModalOpened, { open: openTrialModal, close: closeTrialModal }] = useDisclosure(false);
  const [projectModalOpened, { open: openProjectModal, close: closeProjectModal }] = useDisclosure(false);

  // API state
  const [projects, setProjects] = useState<any[]>([]);
  const [researchStats, setResearchStats] = useState<any>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  // Fetch data
  useEffect(() => {
    fetchAllData();
  }, []);

  const fetchAllData = async () => {
    try {
      setLoading(true);
      setError(null);
      await Promise.all([fetchProjects(), fetchStats()]);
    } catch (err: any) {
      console.error('Error loading research data:', err);
      setError(err.response?.data?.message || err.message || 'Failed to load research data');
    } finally {
      setLoading(false);
    }
  };

  const fetchProjects = async () => {
    try {
      const response = await researchService.getProjects();
      setProjects(response.data || []);
    } catch (err: any) {
      console.error('Error fetching research projects:', err);
      setProjects([] /* TODO: Fetch from API */);
    }
  };

  const fetchStats = async () => {
    try {
      const response = await researchService.getStats();
      setResearchStats(response.data);
    } catch (err: any) {
      console.error('Error fetching research stats:', err);
      setResearchStats([] /* TODO: Fetch from API */);
    }
  };

  // Filter trials
  const filteredTrials = useMemo(() => {
    return [].filter /* TODO: Fetch from API */(trial => {
      const matchesSearch = trial.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
                          trial.trialId.toLowerCase().includes(searchQuery.toLowerCase());
      const matchesStatus = statusFilter === 'all' || trial.status === statusFilter;
      return matchesSearch && matchesStatus;
    });
  }, [searchQuery, statusFilter]);

  // Filter projects
  const filteredProjects = useMemo(() => {
    return [].filter /* TODO: Fetch from API */(project => {
      const matchesSearch = project.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
                          project.projectId.toLowerCase().includes(searchQuery.toLowerCase());
      const matchesStatus = statusFilter === 'all' || project.status === statusFilter;
      return matchesSearch && matchesStatus;
    });
  }, [searchQuery, statusFilter]);

  const getStatusColor = (status: string) => {
    const colors: Record<string, string> = {
      recruiting: 'blue',
      active: 'green',
      completed: 'gray',
      suspended: 'orange',
      terminated: 'red',
      planning: 'cyan',
      on_hold: 'yellow',
      cancelled: 'red'
    };
    return colors[status] || 'gray';
  };

  const getPhaseLabel = (phase: string) => {
    const labels: Record<string, string> = {
      phase_i: 'Phase I',
      phase_ii: 'Phase II',
      phase_iii: 'Phase III',
      phase_iv: 'Phase IV',
      na: 'N/A'
    };
    return labels[phase] || phase;
  };

  const quickStats = [
    {
      title: 'Active Trials',
      value: 0 /* TODO: Fetch from API */,
      icon: IconFlask,
      color: 'blue'
    },
    {
      title: 'Total Participants',
      value: 0 /* TODO: Fetch from API */,
      icon: IconUsers,
      color: 'green'
    },
    {
      title: 'Research Projects',
      value: 0 /* TODO: Fetch from API */,
      icon: IconBooks,
      color: 'orange'
    },
    {
      title: 'Adverse Events',
      value: 0 /* TODO: Fetch from API */,
      icon: IconAlertCircle,
      color: 'red'
    }
  ];

  return (
    <Container size="xl" py="md">
      <Stack gap="lg">
        {/* Header */}
        <Group justify="space-between">
          <div>
            <Title order={2}>Medical Research & Clinical Trials</Title>
            <Text c="dimmed" size="sm">Manage clinical trials, research projects, and participant data</Text>
          </div>
          <Group>
            <Button leftSection={<IconPlus size={16} />} onClick={openTrialModal}>
              New Clinical Trial
            </Button>
            <Button leftSection={<IconPlus size={16} />} variant="light" onClick={openProjectModal}>
              New Research Project
            </Button>
          </Group>
        </Group>

        {/* Quick Stats */}
        <SimpleGrid cols={{ base: 1, sm: 2, md: 4 }}>
          {quickStats.map((stat) => {
            const Icon = stat.icon;
            return (
              <Card key={stat.title} shadow="sm" padding="lg" radius="md" withBorder>
                <Group justify="space-between">
                  <div>
                    <Text size="xs" c="dimmed" tt="uppercase" fw={700}>
                      {stat.title}
                    </Text>
                    <Text size="xl" fw={700} mt="xs">
                      {stat.value}
                    </Text>
                  </div>
                  <ThemeIcon size="xl" radius="md" variant="light" color={stat.color}>
                    <Icon size={24} />
                  </ThemeIcon>
                </Group>
              </Card>
            );
          })}
        </SimpleGrid>

        {/* Main Content */}
        <Paper shadow="sm" p="md" radius="md" withBorder>
          <Tabs value={activeTab} onChange={(value) => setActiveTab(value || 'trials')}>
            <Tabs.List>
              <Tabs.Tab value="trials" leftSection={<IconFlask size={16} />}>
                Clinical Trials
              </Tabs.Tab>
              <Tabs.Tab value="projects" leftSection={<IconBooks size={16} />}>
                Research Projects
              </Tabs.Tab>
              <Tabs.Tab value="participants" leftSection={<IconUsers size={16} />}>
                Participants
              </Tabs.Tab>
              <Tabs.Tab value="adverse" leftSection={<IconAlertCircle size={16} />}>
                Adverse Events
              </Tabs.Tab>
            </Tabs.List>

            {/* Filters */}
            <Group mt="md" mb="md">
              <TextInput
                placeholder="Search..."
                leftSection={<IconSearch size={16} />}
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.currentTarget.value)}
                style={{ flex: 1 }}
              />
              <Select
                placeholder="Filter by status"
                data={[
                  { value: 'all', label: 'All Status' },
                  { value: 'recruiting', label: 'Recruiting' },
                  { value: 'active', label: 'Active' },
                  { value: 'completed', label: 'Completed' },
                  { value: 'suspended', label: 'Suspended' }
                ]}
                value={statusFilter}
                onChange={setStatusFilter}
                clearable
                style={{ width: 200 }}
              />
            </Group>

            {/* Clinical Trials Tab */}
            <Tabs.Panel value="trials">
              <ScrollArea>
                <Table striped highlightOnHover>
                  <Table.Thead>
                    <Table.Tr>
                      <Table.Th>Trial ID</Table.Th>
                      <Table.Th>Title</Table.Th>
                      <Table.Th>Phase</Table.Th>
                      <Table.Th>Status</Table.Th>
                      <Table.Th>Investigator</Table.Th>
                      <Table.Th>Enrollment</Table.Th>
                      <Table.Th>Actions</Table.Th>
                    </Table.Tr>
                  </Table.Thead>
                  <Table.Tbody>
                    {filteredTrials.map((trial) => (
                      <Table.Tr key={trial.id}>
                        <Table.Td>
                          <Text size="sm" fw={500}>{trial.trialId}</Text>
                        </Table.Td>
                        <Table.Td>
                          <Text size="sm">{trial.title}</Text>
                        </Table.Td>
                        <Table.Td>
                          <Badge variant="light" size="sm">
                            {getPhaseLabel(trial.phase)}
                          </Badge>
                        </Table.Td>
                        <Table.Td>
                          <Badge color={getStatusColor(trial.status)} variant="light" size="sm">
                            {trial.status}
                          </Badge>
                        </Table.Td>
                        <Table.Td>
                          <Text size="sm">{trial.investigator}</Text>
                        </Table.Td>
                        <Table.Td>
                          <Group gap="xs">
                            <Text size="sm">
                              {trial.currentEnrollment}/{trial.targetEnrollment}
                            </Text>
                            <Progress
                              value={(trial.currentEnrollment / trial.targetEnrollment) * 100}
                              size="sm"
                              style={{ width: 60 }}
                            />
                          </Group>
                        </Table.Td>
                        <Table.Td>
                          <Group gap="xs">
                            <ActionIcon
                              variant="subtle"
                              color="blue"
                              onClick={() => {
                                setSelectedTrial(trial);
                                openTrialModal();
                              }}
                            >
                              <IconEye size={16} />
                            </ActionIcon>
                            <ActionIcon variant="subtle" color="gray">
                              <IconEdit size={16} />
                            </ActionIcon>
                            <Menu position="bottom-end">
                              <Menu.Target>
                                <ActionIcon variant="subtle" color="gray">
                                  <IconDotsVertical size={16} />
                                </ActionIcon>
                              </Menu.Target>
                              <Menu.Dropdown>
                                <Menu.Item leftSection={<IconDownload size={14} />}>
                                  Export Data
                                </Menu.Item>
                                <Menu.Item leftSection={<IconFileText size={14} />}>
                                  View Protocol
                                </Menu.Item>
                                <Menu.Divider />
                                <Menu.Item color="red" leftSection={<IconTrash size={14} />}>
                                  Delete
                                </Menu.Item>
                              </Menu.Dropdown>
                            </Menu>
                          </Group>
                        </Table.Td>
                      </Table.Tr>
                    ))}
                  </Table.Tbody>
                </Table>
              </ScrollArea>
            </Tabs.Panel>

            {/* Research Projects Tab */}
            <Tabs.Panel value="projects">
              <ScrollArea>
                <Table striped highlightOnHover>
                  <Table.Thead>
                    <Table.Tr>
                      <Table.Th>Project ID</Table.Th>
                      <Table.Th>Title</Table.Th>
                      <Table.Th>Status</Table.Th>
                      <Table.Th>Principal Investigator</Table.Th>
                      <Table.Th>Department</Table.Th>
                      <Table.Th>Budget</Table.Th>
                      <Table.Th>Actions</Table.Th>
                    </Table.Tr>
                  </Table.Thead>
                  <Table.Tbody>
                    {filteredProjects.length === 0 ? (
                      <Table.Tr>
                        <Table.Td colSpan={7}>
                          <EmptyState
                            icon={<IconFlask size={48} />}
                            title="No research projects"
                            description="Add your first research project"
                            size="sm"
                          />
                        </Table.Td>
                      </Table.Tr>
                    ) : (
                      filteredProjects.map((project) => (
                      <Table.Tr key={project.id}>
                        <Table.Td>
                          <Text size="sm" fw={500}>{project.projectId}</Text>
                        </Table.Td>
                        <Table.Td>
                          <Text size="sm">{project.title}</Text>
                        </Table.Td>
                        <Table.Td>
                          <Badge color={getStatusColor(project.status)} variant="light" size="sm">
                            {project.status}
                          </Badge>
                        </Table.Td>
                        <Table.Td>
                          <Text size="sm">{project.principalInvestigator}</Text>
                        </Table.Td>
                        <Table.Td>
                          <Text size="sm">{project.department}</Text>
                        </Table.Td>
                        <Table.Td>
                          <Text size="sm">‚Çπ{project.budget.toLocaleString()}</Text>
                        </Table.Td>
                        <Table.Td>
                          <Group gap="xs">
                            <ActionIcon variant="subtle" color="blue">
                              <IconEye size={16} />
                            </ActionIcon>
                            <ActionIcon variant="subtle" color="gray">
                              <IconEdit size={16} />
                            </ActionIcon>
                          </Group>
                        </Table.Td>
                      </Table.Tr>
                    )))}
                  </Table.Tbody>
                </Table>
              </ScrollArea>
            </Tabs.Panel>

            {/* Participants Tab */}
            <Tabs.Panel value="participants">
              <ScrollArea>
                <Table striped highlightOnHover>
                  <Table.Thead>
                    <Table.Tr>
                      <Table.Th>Participant ID</Table.Th>
                      <Table.Th>Trial ID</Table.Th>
                      <Table.Th>Name</Table.Th>
                      <Table.Th>Status</Table.Th>
                      <Table.Th>Study Arm</Table.Th>
                      <Table.Th>Adherence</Table.Th>
                      <Table.Th>Actions</Table.Th>
                    </Table.Tr>
                  </Table.Thead>
                  <Table.Tbody>
                    {[].map /* TODO: Fetch from API */((participant) => (
                      <Table.Tr key={participant.id}>
                        <Table.Td>
                          <Text size="sm" fw={500}>{participant.participantId}</Text>
                        </Table.Td>
                        <Table.Td>
                          <Text size="sm">{participant.trialId}</Text>
                        </Table.Td>
                        <Table.Td>
                          <Text size="sm">{participant.patientName}</Text>
                        </Table.Td>
                        <Table.Td>
                          <Badge color={getStatusColor(participant.status)} variant="light" size="sm">
                            {participant.status}
                          </Badge>
                        </Table.Td>
                        <Table.Td>
                          <Text size="sm">{participant.studyArm}</Text>
                        </Table.Td>
                        <Table.Td>
                          <Group gap="xs">
                            <Progress value={participant.adherenceRate} size="sm" style={{ width: 60 }} />
                            <Text size="sm">{participant.adherenceRate}%</Text>
                          </Group>
                        </Table.Td>
                        <Table.Td>
                          <Group gap="xs">
                            <ActionIcon variant="subtle" color="blue">
                              <IconEye size={16} />
                            </ActionIcon>
                            <ActionIcon variant="subtle" color="gray">
                              <IconEdit size={16} />
                            </ActionIcon>
                          </Group>
                        </Table.Td>
                      </Table.Tr>
                    ))}
                  </Table.Tbody>
                </Table>
              </ScrollArea>
            </Tabs.Panel>

            {/* Adverse Events Tab */}
            <Tabs.Panel value="adverse">
              <ScrollArea>
                <Table striped highlightOnHover>
                  <Table.Thead>
                    <Table.Tr>
                      <Table.Th>Event ID</Table.Th>
                      <Table.Th>Trial ID</Table.Th>
                      <Table.Th>Participant</Table.Th>
                      <Table.Th>Event Type</Table.Th>
                      <Table.Th>Severity</Table.Th>
                      <Table.Th>Status</Table.Th>
                      <Table.Th>Actions</Table.Th>
                    </Table.Tr>
                  </Table.Thead>
                  <Table.Tbody>
                    {[].map /* TODO: Fetch from API */((event) => (
                      <Table.Tr key={event.id}>
                        <Table.Td>
                          <Text size="sm" fw={500}>{event.eventId}</Text>
                        </Table.Td>
                        <Table.Td>
                          <Text size="sm">{event.trialId}</Text>
                        </Table.Td>
                        <Table.Td>
                          <Text size="sm">{event.participantId}</Text>
                        </Table.Td>
                        <Table.Td>
                          <Text size="sm">{event.eventType.replace(/_/g, ' ')}</Text>
                        </Table.Td>
                        <Table.Td>
                          <Badge
                            color={event.severity === 'mild' ? 'yellow' : event.severity === 'moderate' ? 'orange' : 'red'}
                            variant="light"
                            size="sm"
                          >
                            {event.severity}
                          </Badge>
                        </Table.Td>
                        <Table.Td>
                          <Badge color={event.ongoing ? 'orange' : 'green'} variant="light" size="sm">
                            {event.ongoing ? 'Ongoing' : 'Resolved'}
                          </Badge>
                        </Table.Td>
                        <Table.Td>
                          <Group gap="xs">
                            <ActionIcon variant="subtle" color="blue">
                              <IconEye size={16} />
                            </ActionIcon>
                            <ActionIcon variant="subtle" color="gray">
                              <IconEdit size={16} />
                            </ActionIcon>
                          </Group>
                        </Table.Td>
                      </Table.Tr>
                    ))}
                  </Table.Tbody>
                </Table>
              </ScrollArea>
            </Tabs.Panel>
          </Tabs>
        </Paper>
      </Stack>

      {/* New Trial Modal */}
      <Modal
        opened={trialModalOpened}
        onClose={closeTrialModal}
        title={selectedTrial ? "Trial Details" : "New Clinical Trial"}
        size="lg"
      >
        <Stack gap="md">
          <TextInput label="Trial ID" placeholder="TRIAL-2024-XXX" required />
          <TextInput label="Title" placeholder="Enter trial title" required />
          <Select
            label="Phase"
            placeholder="Select phase"
            data={[
              { value: 'phase_i', label: 'Phase I' },
              { value: 'phase_ii', label: 'Phase II' },
              { value: 'phase_iii', label: 'Phase III' },
              { value: 'phase_iv', label: 'Phase IV' }
            ]}
            required
          />
          <Select
            label="Status"
            placeholder="Select status"
            data={[
              { value: 'recruiting', label: 'Recruiting' },
              { value: 'active', label: 'Active' },
              { value: 'suspended', label: 'Suspended' },
              { value: 'completed', label: 'Completed' }
            ]}
            required
          />
          <TextInput label="Principal Investigator" placeholder="Dr. Name" required />
          <NumberInput label="Target Enrollment" placeholder="Enter number" required />
          <Textarea label="Description" placeholder="Enter trial description" rows={4} />
          
          <Group justify="flex-end" mt="md">
            <Button variant="light" onClick={closeTrialModal}>
              Cancel
            </Button>
            <Button
              onClick={() => {
                notifications.show({
                  title: 'Success',
                  message: 'Clinical trial saved successfully',
                  color: 'green',
                  icon: <IconCheck size={16} />
                });
                closeTrialModal();
              }}
            >
              Save Trial
            </Button>
          </Group>
        </Stack>
      </Modal>

      {/* New Project Modal */}
      <Modal
        opened={projectModalOpened}
        onClose={closeProjectModal}
        title="New Research Project"
        size="lg"
      >
        <Stack gap="md">
          <TextInput label="Project ID" placeholder="PROJ-2024-XXX" required />
          <TextInput label="Title" placeholder="Enter project title" required />
          <Select
            label="Status"
            placeholder="Select status"
            data={[
              { value: 'planning', label: 'Planning' },
              { value: 'active', label: 'Active' },
              { value: 'on_hold', label: 'On Hold' },
              { value: 'completed', label: 'Completed' }
            ]}
            required
          />
          <TextInput label="Principal Investigator" placeholder="Dr. Name" required />
          <TextInput label="Department" placeholder="Enter department" required />
          <NumberInput label="Budget" placeholder="Enter budget" required />
          <TextInput label="Funding Source" placeholder="Enter funding source" required />
          <Textarea label="Description" placeholder="Enter project description" rows={4} />
          
          <Group justify="flex-end" mt="md">
            <Button variant="light" onClick={closeProjectModal}>
              Cancel
            </Button>
            <Button
              onClick={() => {
                notifications.show({
                  title: 'Success',
                  message: 'Research project saved successfully',
                  color: 'green',
                  icon: <IconCheck size={16} />
                });
                closeProjectModal();
              }}
            >
              Save Project
            </Button>
          </Group>
        </Stack>
      </Modal>
    </Container>
  );
}

