  IconScale
} from '@tabler/icons-react';
import { DatePickerInput } from '@mantine/dates';

// Import types and mock data
// Types will be defined inline since they don't exist yet
// Mock data removed - using API data only

const SurgeryManagement = () => {
  // State management
  const [activeTab, setActiveTab] = useState<string>('schedule');
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedType, setSelectedType] = useState<string>('');
  const [selectedStatus, setSelectedStatus] = useState<string>('');
  const [selectedPriority, setSelectedPriority] = useState<string>('');
  const [selectedORStatus, setSelectedORStatus] = useState<string>('');
  const [selectedSurgery, setSelectedSurgery] = useState<any>(null);
  const [selectedOR, setSelectedOR] = useState<any>(null);

  // API data state
  const [surgeries, setSurgeries] = useState<any[]>([]);
  const [stats, setStats] = useState<any>(null);
  const [theaters, setTheaters] = useState<any[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    fetchAllData();
  }, []);

  const fetchAllData = async () => {
    try {
      setLoading(true);
      setError(null);
      await Promise.all([
        fetchSurgeries(),
        fetchStats(),
        fetchTheaters()
      ]);
    } catch (err: any) {
      console.error('Error loading surgery data:', err);
      setError(err.response?.data?.message || 'Failed to load surgery data');
      setSurgeries([] /* TODO: Fetch from API */);
    } finally {
      setLoading(false);
    }
  };

  const fetchSurgeries = async () => {
    try {
      const filters = {
        status: selectedStatus || undefined,
        priority: selectedPriority || undefined
      };
      const response = await surgeryService.getSurgeries(filters);
      // Handle different response structures
      const surgeriesData = Array.isArray(response.data) 
        ? response.data 
        : (response.data?.items || []);
      setSurgeries(surgeriesData);
    } catch (err: any) {
      console.warn('Error fetching surgeries (using empty data):', err.response?.data?.message || err.message);
      setSurgeries([]);
    }
  };

  const fetchStats = async () => {
    try {
      const response = await surgeryService.getStats();
      setStats(response.data);
    } catch (err: any) {
      console.warn('Error fetching surgery stats (using default values):', err.response?.data?.message || err.message);
      setStats({
        totalSurgeries: 0,
        scheduledSurgeries: 0,
        completedSurgeries: 0,
        cancelledSurgeries: 0,
        upcomingSurgeries: 0,
        availableTheaters: 0
      });
    }
  };

  const fetchTheaters = async () => {
    try {
      const response = await surgeryService.getAvailableTheaters();
      setTheaters(response.data || []);
    } catch (err: any) {
      console.warn('Error fetching theaters (using empty data):', err.response?.data?.message || err.message);
      setTheaters([]);
    }
  };

  useEffect(() => {
    if (!loading) {
      fetchSurgeries();
    }
  }, [searchQuery, selectedType, selectedStatus, selectedPriority]);

  // Modal states
  const [surgeryDetailOpened, { open: openSurgeryDetail, close: closeSurgeryDetail }] = useDisclosure(false);
  const [addSurgeryOpened, { open: openAddSurgery, close: closeAddSurgery }] = useDisclosure(false);
  const [orDetailOpened, { open: openORDetail, close: closeORDetail }] = useDisclosure(false);
  const [preOpOpened, { open: openPreOp, close: closePreOp }] = useDisclosure(false);
  const [postOpOpened, { open: openPostOp, close: closePostOp }] = useDisclosure(false);

  // Filter surgeries
  const filteredSurgeries = useMemo(() => {
    // Ensure surgeries is an array before filtering
    if (!Array.isArray(surgeries)) return [];
    return surgeries.filter((surgery: any) => {
      const matchesSearch = 
        surgery.patientName?.toLowerCase().includes(searchQuery.toLowerCase()) ||
        surgery.surgeryId?.toLowerCase().includes(searchQuery.toLowerCase()) ||
        surgery.procedure?.toLowerCase().includes(searchQuery.toLowerCase());
      
      const matchesType = !selectedType || surgery.surgeryType === selectedType;
      const matchesStatus = !selectedStatus || surgery.status === selectedStatus;
      const matchesPriority = !selectedPriority || surgery.priority === selectedPriority;

      return matchesSearch && matchesType && matchesStatus && matchesPriority;
    });
  }, [searchQuery, selectedType, selectedStatus, selectedPriority]);

  // Filter operating rooms
  const filteredORs = useMemo(() => {
    // Ensure theaters is an array before filtering
    if (!Array.isArray(theaters)) return [];
    return theaters.filter((or: any) => {
      const matchesSearch = 
        or.roomNumber?.toLowerCase().includes(searchQuery.toLowerCase()) ||
        or.roomName?.toLowerCase().includes(searchQuery.toLowerCase());
      
      const matchesStatus = !selectedORStatus || or.status === selectedORStatus;

      return matchesSearch && matchesStatus;
    });
  }, [searchQuery, selectedORStatus]);

  // Helper functions
  const getStatusColor = (status: string) => {
    switch (status) {
      case 'scheduled':
      case 'available':
      case 'operational': return 'blue';
      case 'in_progress':
      case 'occupied':
      case 'in_use': return 'orange';
      case 'completed':
      case 'cleaned': return 'green';
      case 'cancelled':
      case 'out_of_service': return 'red';
      case 'delayed':
      case 'maintenance': return 'yellow';
      case 'recovery': return 'purple';
      default: return 'gray';
    }
  };

  const getPriorityColor = (priority: string) => {
    switch (priority) {
      case 'emergency': return 'red';
      case 'urgent': return 'orange';
      case 'elective': return 'blue';
      case 'routine': return 'green';
      default: return 'gray';
    }
  };

  const getSurgeryTypeColor = (type: string) => {
    switch (type) {
      case 'cardiac': return 'red';
      case 'neurological': return 'purple';
      case 'orthopedic': return 'blue';
      case 'general': return 'green';
      case 'plastic': return 'pink';
      case 'pediatric': return 'cyan';
      case 'trauma': return 'orange';
      case 'transplant': return 'indigo';
      default: return 'gray';
    }
  };

  const handleViewSurgery = (surgery: any) => {
    setSelectedSurgery(surgery);
    openSurgeryDetail();
  };

  const handleViewOR = (or: any) => {
    setSelectedOR(or);
    openORDetail();
  };

  const clearFilters = () => {
    setSearchQuery('');
    setSelectedType('');
    setSelectedStatus('');
    setSelectedPriority('');
    setSelectedORStatus('');
  };

  const formatDuration = (minutes: number) => {
    const hours = Math.floor(minutes / 60);
    const mins = minutes % 60;
    return `${hours}h ${mins}m`;
  };

  const formatCurrency = (amount: number) => {
    return new Intl.NumberFormat('en-IN', {
      style: 'currency',
      currency: 'INR'
    }).format(amount);
  };

  // Statistics cards
  const statsCards = [
    {
      title: 'Total Surgeries',
      value: 0 /* TODO: Fetch from API */,
      icon: IconScissors,
      color: 'blue',
      trend: '+0%'
    },
    {
      title: 'Today\'s Surgeries',
      value: 0,
      icon: IconCalendar,
      color: 'green',
      trend: '+0'
    },
    {
      title: 'Active ORs',
      value: `${0 /* TODO: Fetch from API */}/${0 /* TODO: Fetch from API */}`,
      icon: IconBed,
      color: 'orange',
      trend: '0% utilization'
    },
    {
      title: 'Average Duration',
      value: `${0 /* TODO: Fetch from API */}min`,
      icon: IconClock,
      color: 'purple',
      trend: '0min'
    }
  ];

  // Chart data
  const surgeryTypeData = Object.entries(0 /* TODO: Fetch from API */ || {})
    .map(([type, count]) => ({
      name: type.replace('_', ' ').toUpperCase(),
      value: typeof count === 'number' ? count : 0,
      color: getSurgeryTypeColor(type)
    }));

  const monthlyVolume = [];
  const orUtilization = [];

  return (
    <Container size="xl" py="md">
      {/* Header */}
      <Group justify="space-between" mb="lg">
        <div>
          <Title order={1}>Surgery & Operation Theater</Title>
          <Text c="dimmed" size="sm">
            Manage surgical procedures, operating rooms, and surgical teams
          </Text>
        </div>
        <Group>
          <Button
            leftSection={<IconPlus size={16} />}
            onClick={openAddSurgery}
            color="blue"
          >
            Schedule Surgery
          </Button>
          <Button
            variant="light"
            leftSection={<IconMedicalCross size={16} />}
            color="red"
          >
            Emergency Surgery
          </Button>
        </Group>
      </Group>

      {/* Statistics Cards */}
      <SimpleGrid cols={{ base: 1, sm: 2, md: 4 }} mb="lg">
        {statsCards.map((stat) => {
          const Icon = stat.icon;
          return (
            <Card key={stat.title} padding="lg" radius="md" withBorder>
              <Group justify="space-between">
                <div>
                  <Text c="dimmed" size="sm" fw={500}>
                    {stat.title}
                  </Text>
                  <Text fw={700} size="xl">
                    {stat.value}
                  </Text>
                </div>
                <ThemeIcon color={stat.color} size="xl" radius="md" variant="light">
                  <Icon size={24} />
                </ThemeIcon>
              </Group>
              <Group justify="space-between" mt="sm">
                <Badge 
                  color={stat.trend.includes('+') ? 'green' : stat.trend.includes('-') ? 'red' : 'blue'} 
                  variant="light"
                  size="sm"
                >
                  {stat.trend}
                </Badge>
                <Text size="xs" c="dimmed">vs last month</Text>
              </Group>
            </Card>
          );
        })}
      </SimpleGrid>

      {/* Main Content Tabs */}
      <Tabs value={activeTab} onChange={setActiveTab}>
        <Tabs.List>
          <Tabs.Tab value="schedule" leftSection={<IconCalendar size={16} />}>
            Surgery Schedule
          </Tabs.Tab>
          <Tabs.Tab value="theaters" leftSection={<IconBed size={16} />}>
            Operating Theaters
          </Tabs.Tab>
          <Tabs.Tab value="equipment" leftSection={<IconTools size={16} />}>
            Equipment
          </Tabs.Tab>
          <Tabs.Tab value="teams" leftSection={<IconUsers size={16} />}>
            Surgical Teams
          </Tabs.Tab>
          <Tabs.Tab value="reports" leftSection={<IconChartBar size={16} />}>
            Reports & Analytics
          </Tabs.Tab>
        </Tabs.List>

        {/* Surgery Schedule Tab */}
        <Tabs.Panel value="schedule">
          <Paper p="md" radius="md" withBorder mt="md">
            {/* Search and Filters */}
            <Group mb="md">
              <TextInput
                placeholder="Search surgeries..."
                leftSection={<IconSearch size={16} />}
                value={searchQuery}
                onChange={(event) => setSearchQuery(event.currentTarget.value)}
                style={{ flex: 1 }}
              />
              <Select
                placeholder="Surgery Type"
                data={[
                  { value: 'cardiac', label: 'Cardiac' },
                  { value: 'neurological', label: 'Neurological' },
                  { value: 'orthopedic', label: 'Orthopedic' },
                  { value: 'general', label: 'General' },
                  { value: 'plastic', label: 'Plastic' },
                  { value: 'pediatric', label: 'Pediatric' },
                  { value: 'trauma', label: 'Trauma' }
                ]}
                value={selectedType}
                onChange={setSelectedType}
                clearable
              />
              <Select
                placeholder="Status"
                data={[
                  { value: 'scheduled', label: 'Scheduled' },
                  { value: 'in_progress', label: 'In Progress' },
                  { value: 'completed', label: 'Completed' },
                  { value: 'cancelled', label: 'Cancelled' },
                  { value: 'delayed', label: 'Delayed' }
                ]}
                value={selectedStatus}
                onChange={setSelectedStatus}
                clearable
              />
              <Select
                placeholder="Priority"
                data={[
                  { value: 'emergency', label: 'Emergency' },
                  { value: 'urgent', label: 'Urgent' },
                  { value: 'elective', label: 'Elective' },
                  { value: 'routine', label: 'Routine' }
                ]}
                value={selectedPriority}
                onChange={setSelectedPriority}
                clearable
              />
              <Button variant="light" onClick={clearFilters}>
                Clear Filters
              </Button>
            </Group>

            {/* Surgery Schedule Table */}
            <ScrollArea>
              <Table striped highlightOnHover>
                <Table.Thead>
                  <Table.Tr>
                    <Table.Th>Surgery ID</Table.Th>
                    <Table.Th>Patient</Table.Th>
                    <Table.Th>Procedure</Table.Th>
                    <Table.Th>Surgeon</Table.Th>
                    <Table.Th>Date & Time</Table.Th>
                    <Table.Th>Duration</Table.Th>
                    <Table.Th>OR</Table.Th>
                    <Table.Th>Priority</Table.Th>
                    <Table.Th>Status</Table.Th>
                    <Table.Th>Actions</Table.Th>
                  </Table.Tr>
                </Table.Thead>
                <Table.Tbody>
                  {filteredSurgeries.length === 0 ? (
                    <Table.Tr>
                      <Table.Td colSpan={10}>
                        <EmptyState
                          icon={<IconScale size={48} />}
                          title="No surgeries scheduled"
                          description="Schedule your first surgery"
                          size="sm"
                        />
                      </Table.Td>
                    </Table.Tr>
                  ) : (
                    filteredSurgeries.map((surgery: any) => (
                      <Table.Tr key={surgery.id}>
                        <Table.Td>
                          <Text fw={500}>{surgery.surgeryId}</Text>
                        </Table.Td>
                        <Table.Td>
                          <Group>
                            <Avatar color="blue" radius="xl" size="sm">
                              {surgery.patientName?.[0] || 'P'}
                            </Avatar>
                            <div>
                              <Text size="sm" fw={500}>
                                {surgery.patientName || 'N/A'}
                              </Text>
                              <Text size="xs" c="dimmed">
                                ID: {(surgery as any).patientId || 'N/A'}
                              </Text>
                            </div>
                          </Group>
                        </Table.Td>
                        <Table.Td>
                          <div>
                            <Text size="sm" fw={500} lineClamp={1}>
                              {surgery.procedure}
                            </Text>
                            <Badge color={getSurgeryTypeColor((surgery as any).surgeryType || 'general')} variant="light" size="xs">
                              {(surgery as any).surgeryType || 'General'}
                            </Badge>
                          </div>
                        </Table.Td>
                        <Table.Td>
                          <div>
                            <Text size="sm" fw={500}>
                              {surgery.surgeon || 'N/A'}
                            </Text>
                            <Text size="xs" c="dimmed">
                              {(surgery as any).department || 'N/A'}
                            </Text>
                          </div>
                        </Table.Td>
                        <Table.Td>
                          <div>
                            <Text size="sm" fw={500}>
                              {surgery.date || 'N/A'}
                            </Text>
                            <Text size="xs" c="dimmed">
                              {surgery.time || 'N/A'}
                            </Text>
                          </div>
                        </Table.Td>
                        <Table.Td>
                          <Text size="sm">
                            {surgery.duration || 0} min
                          </Text>
                        </Table.Td>
                        <Table.Td>
                          <Badge color="cyan" variant="light">
                            {surgery.operatingRoom || 'N/A'}
                          </Badge>
                        </Table.Td>
                        <Table.Td>
                          <Badge color={getPriorityColor((surgery as any).priority || 'routine')} variant="light" size="xs">
                            {((surgery as any).priority || 'routine').toUpperCase()}
                          </Badge>
                        </Table.Td>
                        <Table.Td>
                          <Badge color={getStatusColor(surgery.status)} variant="light">
                            {surgery.status.replace('_', ' ')}
                          </Badge>
                        </Table.Td>
                        <Table.Td>
                          <Group gap="xs">
                            <ActionIcon
                              variant="subtle"
                              color="blue"
                              onClick={() => handleViewSurgery(surgery)}
                            >
                              <IconEye size={16} />
                            </ActionIcon>
                            <ActionIcon variant="subtle" color="green">
                              <IconEdit size={16} />
                            </ActionIcon>
                            <Menu>
                              <Menu.Target>
                                <ActionIcon variant="subtle" color="gray">
                                  <IconDotsVertical size={16} />
                                </ActionIcon>
                              </Menu.Target>
                              <Menu.Dropdown>
                                <Menu.Item leftSection={<IconClipboardList size={14} />}>
                                  Pre-Op Checklist
                                </Menu.Item>
                                <Menu.Item leftSection={<IconReportMedical size={14} />}>
                                  Post-Op Notes
                                </Menu.Item>
                                <Menu.Item leftSection={<IconDownload size={14} />}>
                                  Download Report
                                </Menu.Item>
                                <Menu.Divider />
                                <Menu.Item 
                                  leftSection={<IconX size={14} />}
                                  color="red"
                                >
                                  Cancel Surgery
                                </Menu.Item>
                              </Menu.Dropdown>
                            </Menu>
                          </Group>
                        </Table.Td>
                      </Table.Tr>
                    ))
                  )}
                </Table.Tbody>
              </Table>
            </ScrollArea>
          </Paper>
        </Tabs.Panel>

        {/* Operating Theaters Tab */}
        <Tabs.Panel value="theaters">
          <Paper p="md" radius="md" withBorder mt="md">
            <Group justify="space-between" mb="lg">
              <Title order={3}>Operating Theaters</Title>
              <Group>
                <Button leftSection={<IconSettings size={16} />} variant="light">
                  OR Maintenance
                </Button>
                <Button leftSection={<IconActivity size={16} />}>
                  Monitor Status
                </Button>
              </Group>
            </Group>

            {/* OR Filters */}
            <Group mb="md">
              <TextInput
                placeholder="Search ORs..."
                leftSection={<IconSearch size={16} />}
                value={searchQuery}
                onChange={(event) => setSearchQuery(event.currentTarget.value)}
                style={{ flex: 1 }}
              />
              <Select
                placeholder="Status"
                data={[
                  { value: 'available', label: 'Available' },
                  { value: 'occupied', label: 'Occupied' },
                  { value: 'cleaned', label: 'Cleaned' },
                  { value: 'maintenance', label: 'Maintenance' }
                ]}
                value={selectedORStatus}
                onChange={setSelectedORStatus}
                clearable
              />
            </Group>

            {/* Operating Rooms Grid */}
            <SimpleGrid cols={{ base: 1, md: 2, lg: 3 }} spacing="lg">
              {filteredORs.map((or: any) => (
                <Card key={or.id} padding="lg" radius="md" withBorder>
                  <Group justify="space-between" mb="md">
                    <div>
                      <Text fw={600} size="lg">OR {or.roomNumber}</Text>
                      <Text size="sm" c="dimmed">{or.roomName}</Text>
                    </div>
                    <Badge color={getStatusColor(or.status)} variant="light">
                      {or.status}
                    </Badge>
                  </Group>

                  <Stack gap="sm" mb="md">
                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Location</Text>
                      <Text size="sm" fw={500}>{or.location}</Text>
                    </Group>
                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Capacity</Text>
                      <Text size="sm">{or.capacity} people</Text>
                    </Group>
                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Specialties</Text>
                      <div>
                        {or.specialties.slice(0, 2).map((specialty) => (
                          <Badge key={specialty} size="xs" variant="light" mr="xs">
                            {specialty}
                          </Badge>
                        ))}
                        {or.specialties.length > 2 && (
                          <Badge size="xs" variant="light" color="gray">
                            +{or.specialties.length - 2}
                          </Badge>
                        )}
                      </div>
                    </Group>
                  </Stack>

                  {(or as any).currentSurgery && (
                    <Alert variant="light" color="blue" mb="md">
                      <Text size="sm" fw={500}>Current Surgery:</Text>
                      <Text size="sm">{(or as any).currentSurgery || 'N/A'}</Text>
                    </Alert>
                  )}

                  <Group justify="space-between">
                    <Text size="xs" c="dimmed">
                      Equipment: {or.equipment.length} items
                    </Text>
                    <Group gap="xs">
                      <ActionIcon
                        variant="subtle"
                        color="blue"
                        onClick={() => handleViewOR(or)}
                      >
                        <IconEye size={16} />
                      </ActionIcon>
                      <ActionIcon variant="subtle" color="green">
                        <IconSettings size={16} />
                      </ActionIcon>
                      <ActionIcon variant="subtle" color="orange">
                        <IconActivity size={16} />
                      </ActionIcon>
                    </Group>
                  </Group>
                </Card>
              ))}
            </SimpleGrid>
          </Paper>
        </Tabs.Panel>

        {/* Equipment Tab */}
        <Tabs.Panel value="equipment">
          <Paper p="md" radius="md" withBorder mt="md">
            <Group justify="space-between" mb="lg">
              <Title order={3}>Surgical Equipment</Title>
              <Button leftSection={<IconPlus size={16} />}>
                Add Equipment
              </Button>
            </Group>

            {/* Equipment Grid */}
            <SimpleGrid cols={{ base: 1, md: 2, lg: 3 }} spacing="lg">
              {[].map /* TODO: Fetch from API */((equipment) => (
                <Card key={equipment.id} padding="lg" radius="md" withBorder>
                  <Group justify="space-between" mb="md">
                    <div>
                      <Text fw={600} size="lg">{equipment.equipmentName}</Text>
                      <Text size="sm" c="dimmed">{equipment.manufacturer}</Text>
                    </div>
                    <Badge color={getStatusColor(equipment.status)} variant="light">
                      {equipment.status.replace('_', ' ')}
                    </Badge>
                  </Group>

                  <Stack gap="sm" mb="md">
                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Model</Text>
                      <Text size="sm" fw={500}>{equipment.model}</Text>
                    </Group>
                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Serial</Text>
                      <Text size="sm" fw={500}>{equipment.serialNumber}</Text>
                    </Group>
                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Location</Text>
                      <Text size="sm">OR {equipment.assignedOR}</Text>
                    </Group>
                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Last Calibrated</Text>
                      <Text size="sm">
                        {new Date(equipment.lastCalibrationDate).toLocaleDateString()}
                      </Text>
                    </Group>
                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Next Due</Text>
                      <Text 
                        size="sm" 
                        c={new Date(equipment.nextCalibrationDate) < new Date() ? 'red' : 'dimmed'}
                      >
                        {new Date(equipment.nextCalibrationDate).toLocaleDateString()}
                      </Text>
                    </Group>
                  </Stack>

                  {equipment.usageHours && (
                    <div>
                      <Group justify="space-between" mb="xs">
                        <Text size="sm" c="dimmed">Usage Hours</Text>
                        <Text size="sm" fw={500}>{equipment.usageHours}h</Text>
                      </Group>
                      <Progress 
                        value={(equipment.usageHours / 8760) * 100} 
                        size="sm" 
                        color="blue"
                      />
                    </div>
                  )}

                  <Group justify="space-between">
                    <Text size="xs" c="dimmed">
                      {equipment.equipmentType}
                    </Text>
                    <Group gap="xs">
                      <ActionIcon variant="subtle" color="blue">
                        <IconEye size={16} />
                      </ActionIcon>
                      <ActionIcon variant="subtle" color="green">
                        <IconSettings size={16} />
                      </ActionIcon>
                      <ActionIcon variant="subtle" color="orange">
                        <IconTools size={16} />
                      </ActionIcon>
                    </Group>
                  </Group>
                </Card>
              ))}
            </SimpleGrid>
          </Paper>
        </Tabs.Panel>

        {/* Surgical Teams Tab */}
        <Tabs.Panel value="teams">
          <Paper p="md" radius="md" withBorder mt="md">
            <Group justify="space-between" mb="lg">
              <Title order={3}>Surgical Teams</Title>
              <Button leftSection={<IconPlus size={16} />}>
                Create Team
              </Button>
            </Group>

            {/* Surgical Teams Grid */}
            <SimpleGrid cols={{ base: 1, lg: 2 }} spacing="lg">
              {[].map /* TODO: Fetch from API */((team) => (
                <Card key={team.id} padding="lg" radius="md" withBorder>
                  <Group justify="space-between" mb="md">
                    <div>
                      <Text fw={600} size="lg">{team.teamName}</Text>
                      <Text size="sm" c="dimmed">{team.specialization}</Text>
                    </div>
                    <Badge color={team.isActive ? 'green' : 'gray'} variant="light">
                      {team.isActive ? 'Active' : 'Inactive'}
                    </Badge>
                  </Group>

                  <Stack gap="md" mb="md">
                    <div>
                      <Text size="sm" fw={500} mb="sm">Lead Surgeon</Text>
                      <Group>
                        <Avatar color="blue" radius="xl">
                          {team.leadSurgeon.firstName[0]}{team.leadSurgeon.lastName[0]}
                        </Avatar>
                        <div>
                          <Text size="sm" fw={500}>
                            Dr. {team.leadSurgeon.firstName} {team.leadSurgeon.lastName}
                          </Text>
                          <Text size="xs" c="dimmed">
                            {team.leadSurgeon.department?.name}
                          </Text>
                        </div>
                      </Group>
                    </div>

                    <div>
                      <Text size="sm" fw={500} mb="sm">Team Members</Text>
                      <Stack gap="xs">
                        {team.teamMembers.slice(0, 3).map((member) => (
                          <Group key={member.id} gap="sm">
                            <Avatar size="sm" color="cyan" radius="xl">
                              {member.firstName[0]}{member.lastName[0]}
                            </Avatar>
                            <div>
                              <Text size="sm">{member.firstName} {member.lastName}</Text>
                              <Text size="xs" c="dimmed">{member.role}</Text>
                            </div>
                          </Group>
                        ))}
                        {team.teamMembers.length > 3 && (
                          <Text size="xs" c="dimmed">
                            +{team.teamMembers.length - 3} more members
                          </Text>
                        )}
                      </Stack>
                    </div>
                  </Stack>

                  <Group justify="space-between">
                    <Text size="xs" c="dimmed">
                      Created: {new Date(team.createdDate).toLocaleDateString()}
                    </Text>
                    <Group gap="xs">
                      <ActionIcon variant="subtle" color="blue">
                        <IconEye size={16} />
                      </ActionIcon>
                      <ActionIcon variant="subtle" color="green">
                        <IconEdit size={16} />
                      </ActionIcon>
                      <ActionIcon variant="subtle" color="orange">
                        <IconUsers size={16} />
                      </ActionIcon>
                    </Group>
                  </Group>
                </Card>
              ))}
            </SimpleGrid>
          </Paper>
        </Tabs.Panel>

        {/* Reports & Analytics Tab */}
        <Tabs.Panel value="reports">
          <Paper p="md" radius="md" withBorder mt="md">
            <Title order={3} mb="lg">Surgery Reports & Analytics</Title>
            
            <SimpleGrid cols={{ base: 1, lg: 2 }} spacing="lg">
              {/* Surgery Types Distribution */}
              <Card padding="lg" radius="md" withBorder>
                <Title order={4} mb="md">Surgeries by Type</Title>
                <MantineDonutChart
                  data={surgeryTypeData}
                  size={160}
                  thickness={30}
                  withLabels
                />
              </Card>
              
              {/* Monthly Surgery Volume */}
              <Card padding="lg" radius="md" withBorder>
                <Title order={4} mb="md">Monthly Surgery Volume</Title>
                <SimpleAreaChart
                  data={monthlyVolume}
                  dataKey="month"
                  series={[{ name: 'surgeries', color: 'blue.6' }]}
                />
              </Card>
              
              {/* OR Utilization */}
              <Card padding="lg" radius="md" withBorder style={{ gridColumn: '1 / -1' }}>
                <Title order={4} mb="md">Operating Room Utilization</Title>
                <SimpleBarChart
                  data={orUtilization}
                  dataKey="or"
                  series={[
                    { name: 'utilization', color: 'orange.6' }
                  ]}
                />
              </Card>
              
              {/* Key Performance Indicators */}
              <Card padding="lg" radius="md" withBorder>
                <Title order={4} mb="md">Key Performance Indicators</Title>
                <Stack gap="md">
                  <Group justify="space-between" p="sm" 
                         style={{ backgroundColor: '#f8f9fa', borderRadius: '6px' }}>
                    <Text size="sm" fw={500}>Surgery Success Rate</Text>
                    <Text size="sm" fw={600} c="green">
                      {0 /* TODO: Fetch from API */}%
                    </Text>
                  </Group>
                  <Group justify="space-between" p="sm" 
                         style={{ backgroundColor: '#f8f9fa', borderRadius: '6px' }}>
                    <Text size="sm" fw={500}>Average Turnover Time</Text>
                    <Text size="sm" fw={600}>
                      {0 /* TODO: Fetch from API */}min
                    </Text>
                  </Group>
                  <Group justify="space-between" p="sm" 
                         style={{ backgroundColor: '#f8f9fa', borderRadius: '6px' }}>
                    <Text size="sm" fw={500}>On-Time Start Rate</Text>
                    <Text size="sm" fw={600} c="green">
                      {0 /* TODO: Fetch from API */}%
                    </Text>
                  </Group>
                  <Group justify="space-between" p="sm" 
                         style={{ backgroundColor: '#f8f9fa', borderRadius: '6px' }}>
                    <Text size="sm" fw={500}>Complication Rate</Text>
                    <Text size="sm" fw={600} c="red">
                      {0 /* TODO: Fetch from API */}%
                    </Text>
                  </Group>
                </Stack>
              </Card>
              
              {/* Quick Actions */}
              <Card padding="lg" radius="md" withBorder>
                <Title order={4} mb="md">Quick Reports</Title>
                <Stack gap="sm">
                  <Button fullWidth leftSection={<IconDownload size={16} />} variant="light">
                    Export Surgery Log
                  </Button>
                  <Button fullWidth leftSection={<IconFileText size={16} />} variant="light">
                    OR Utilization Report
                  </Button>
                  <Button fullWidth leftSection={<IconChartBar size={16} />} variant="light">
                    Performance Analytics
                  </Button>
                  <Button fullWidth leftSection={<IconReportMedical size={16} />} variant="light">
                    Quality Metrics
                  </Button>
                </Stack>
              </Card>
            </SimpleGrid>
          </Paper>
        </Tabs.Panel>
      </Tabs>

      {/* Surgery Detail Modal */}
      <Modal
        opened={surgeryDetailOpened}
        onClose={closeSurgeryDetail}
        title="Surgery Details"
        size="xl"
      >
        {selectedSurgery && (
          <ScrollArea h={600}>
            <Stack gap="md">
              <Group>
                <ThemeIcon color="blue" size="xl" variant="light">
                  <IconScissors size={24} />
                </ThemeIcon>
                <div>
                  <Title order={3}>{selectedSurgery.procedure}</Title>
                  <Text c="dimmed">Surgery ID: {selectedSurgery.surgeryId}</Text>
                  <Badge color={getStatusColor(selectedSurgery.status)} variant="light" mt="xs">
                    {selectedSurgery.status.replace('_', ' ')}
                  </Badge>
                </div>
              </Group>

              <Divider />

              <SimpleGrid cols={2}>
                <div>
                  <Text size="sm" fw={500}>Patient</Text>
                  <Text size="sm" c="dimmed">
                    {selectedSurgery.patientName || 'N/A'}
                  </Text>
                </div>
                <div>
                  <Text size="sm" fw={500}>Surgery Type</Text>
                  <Badge color={getSurgeryTypeColor(selectedSurgery.surgeryType || 'general')} variant="light">
                    {selectedSurgery.surgeryType || 'General'}
                  </Badge>
                </div>
                <div>
                  <Text size="sm" fw={500}>Primary Surgeon</Text>
                  <Text size="sm" c="dimmed">
                    {selectedSurgery.surgeon || 'N/A'}
                  </Text>
                </div>
                <div>
                  <Text size="sm" fw={500}>Operating Room</Text>
                  <Text size="sm" c="dimmed">{selectedSurgery.operatingRoom || 'N/A'}</Text>
                </div>
                <div>
                  <Text size="sm" fw={500}>Scheduled Date</Text>
                  <Text size="sm" c="dimmed">
                    {selectedSurgery.date || 'N/A'} {selectedSurgery.time || ''}
                  </Text>
                </div>
                <div>
                  <Text size="sm" fw={500}>Estimated Duration</Text>
                  <Text size="sm" c="dimmed">
                    {selectedSurgery.duration || 0} minutes
                  </Text>
                </div>
                <div>
                  <Text size="sm" fw={500}>Priority</Text>
                  <Badge color={getPriorityColor(selectedSurgery.priority || 'routine')} variant="light">
                    {(selectedSurgery.priority || 'routine').toUpperCase()}
                  </Badge>
                </div>
                <div>
                  <Text size="sm" fw={500}>Anesthesiologist</Text>
                  <Text size="sm" c="dimmed">{selectedSurgery.anesthesiologist || 'N/A'}</Text>
                </div>
              </SimpleGrid>

              {selectedSurgery.specialInstructions && (
                <>
                  <Divider />
                  <div>
                    <Text size="sm" fw={500} mb="sm">Special Instructions</Text>
                    <Text size="sm">{selectedSurgery.specialInstructions}</Text>
                  </div>
                </>
              )}

              {selectedSurgery.assistants && selectedSurgery.assistants.length > 0 && (
                <>
                  <Divider />
                  <div>
                    <Text size="sm" fw={500} mb="sm">Assistants</Text>
                    <Stack gap="xs">
                      {selectedSurgery.assistants.map((assistant: string, index: number) => (
                        <Group key={index}>
                          <Avatar size="sm" color="blue" radius="xl">
                            {assistant[0]}
                          </Avatar>
                          <Text size="sm">{assistant}</Text>
                        </Group>
                      ))}
                    </Stack>
                  </div>
                </>
              )}

              <Group justify="flex-end">
                <Button variant="light" onClick={closeSurgeryDetail}>
                  Close
                </Button>
                <Button leftSection={<IconClipboardList size={16} />}>
                  Pre-Op Checklist
                </Button>
                <Button leftSection={<IconEdit size={16} />}>
                  Edit Surgery
                </Button>
              </Group>
            </Stack>
          </ScrollArea>
        )}
      </Modal>

      {/* Add Surgery Modal */}
      <Modal
        opened={addSurgeryOpened}
        onClose={closeAddSurgery}
        title="Schedule New Surgery"
        size="lg"
      >
        <Stack gap="md">
          <SimpleGrid cols={2}>
            <Select
              label="Patient"
              placeholder="Select patient"
              data={[].map /* TODO: Fetch from API */(patient => ({ 
                value: patient.id, 
                label: `${patient.firstName} ${patient.lastName}` 
              }))}
              required
            />
            <Select
              label="Primary Surgeon"
              placeholder="Select surgeon"
              data={[].filter /* TODO: Fetch from API */((staff: any) => staff.role === 'Doctor' || staff.role === 'doctor').map((surgeon: any) => ({ 
                value: surgeon.staffId, 
                label: `Dr. ${surgeon.firstName} ${surgeon.lastName}` 
              }))}
              required
            />
          </SimpleGrid>
          
          <TextInput
            label="Procedure"
            placeholder="Enter surgical procedure"
            required
          />
          
          <SimpleGrid cols={2}>
            <Select
              label="Surgery Type"
              placeholder="Select type"
              data={[
                { value: 'cardiac', label: 'Cardiac' },
                { value: 'neurological', label: 'Neurological' },
                { value: 'orthopedic', label: 'Orthopedic' },
                { value: 'general', label: 'General' },
                { value: 'plastic', label: 'Plastic' },
                { value: 'pediatric', label: 'Pediatric' }
              ]}
              required
            />
            <Select
              label="Priority"
              placeholder="Select priority"
              data={[
                { value: 'emergency', label: 'Emergency' },
                { value: 'urgent', label: 'Urgent' },
                { value: 'elective', label: 'Elective' },
                { value: 'routine', label: 'Routine' }
              ]}
              required
            />
          </SimpleGrid>
          
          <SimpleGrid cols={2}>
            <DatePickerInput
              label="Surgery Date"
              placeholder="Select date"
              required
            />
            <NumberInput
              label="Estimated Duration (minutes)"
              placeholder="120"
              min={30}
              required
            />
          </SimpleGrid>
          
          <SimpleGrid cols={2}>
            <Select
              label="Operating Room"
              placeholder="Select OR"
              data={[].map /* TODO: Fetch from API */(or => ({ 
                value: or.id, 
                label: `OR ${or.roomNumber} - ${or.roomName}` 
              }))}
              required
            />
            <Select
              label="Anesthesia Type"
              placeholder="Select anesthesia"
              data={[
                { value: 'general', label: 'General' },
                { value: 'local', label: 'Local' },
                { value: 'regional', label: 'Regional' },
                { value: 'spinal', label: 'Spinal' }
              ]}
              required
            />
          </SimpleGrid>
          
          <Textarea
            label="Special Instructions"
            placeholder="Enter any special instructions"
            rows={3}
          />
          
          <Group justify="flex-end">
            <Button variant="light" onClick={closeAddSurgery}>
              Cancel
            </Button>
            <Button onClick={() => {
              notifications.show({
                title: 'Surgery Scheduled',
                message: 'Surgery has been successfully scheduled',
                color: 'green',
              });
              closeAddSurgery();
            }}>
              Schedule Surgery
            </Button>
          </Group>
        </Stack>
      </Modal>
    </Container>
  );
};

export default SurgeryManagement;

'use client';

import React, { useState, useMemo, useEffect } from 'react';
import {
  Container,
  Paper,
  Title,
  Group,
  Button,
  TextInput,
  Select,
  Badge,
  Modal,
  Text,
  Tabs,
  Card,
  ActionIcon,
  Stack,
  SimpleGrid,
  ScrollArea,
  ThemeIcon,
  Progress,
  Textarea
} from '@mantine/core';
import { useDisclosure } from '@mantine/hooks';
import EmptyState from '../../../components/EmptyState';
import { notifications } from '@mantine/notifications';
import { MantineDonutChart, SimpleBarChart } from '../../../components/MantineChart';
import {
  IconPlus,
  IconSearch,
  IconEye,
  IconChartBar,
  IconDownload,
  IconShare,
  IconActivity,
  IconSettings,
  IconAlertTriangle,
  IconLungs,
  IconHeart,
  IconStethoscope,
  IconVideo,
  IconCalendarEvent,
  IconChartLine,
  IconThermometer,
  IconBrandZoom,
  IconPrescription,
  IconDeviceHeartMonitor
} from '@tabler/icons-react';

// Import types and mock data
import {
  TelemedicineSession,
  SessionStatus,
  SessionType,
  RemoteMonitoringData,
  VitalSigns,
  DigitalPrescription,
  TelemedicineStats,
  PatientMonitoring,
  VirtualConsultation,
  ConsultationStatus
} from '../../../types/telemedicine';
import telemedicineService from '../../../services/telemedicine.service';

const Telemedicine = () => {
  // State management
  const [activeTab, setActiveTab] = useState<string>('overview');
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedSessionType, setSelectedSessionType] = useState<string>('');
  const [selectedStatus, setSelectedStatus] = useState<string>('');
  const [selectedDoctor, setSelectedDoctor] = useState<string>('');
  const [_selectedSession, setSelectedSession] = useState<TelemedicineSession | null>(null);
  const [_selectedPrescription, setSelectedPrescription] = useState<DigitalPrescription | null>(null);
  const [_selectedPatientMonitoring, setSelectedPatientMonitoring] = useState<PatientMonitoring | null>(null);
  const [_isInCall, _setIsInCall] = useState(false);
  const [_callDuration, _setCallDuration] = useState(0);
  const [_isVideoEnabled, _setIsVideoEnabled] = useState(true);
  const [_isAudioEnabled, _setIsAudioEnabled] = useState(true);
  const [_isScreenSharing, _setIsScreenSharing] = useState(false);
  const [_isRecording, _setIsRecording] = useState(false);

  // API state
  const [consultations, setConsultations] = useState<any[]>([]);
  const [telemedicineStats, setTelemedicineStats] = useState<any>(null);
  const [_loading, setLoading] = useState(true);
  const [_error, setError] = useState<string | null>(null);

  // Fetch data
  useEffect(() => {
    fetchAllData();
  // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  const fetchAllData = async () => {
    try {
      setLoading(true);
      setError(null);
      await Promise.all([fetchConsultations(), fetchStats()]);
    } catch (err: any) {
      console.error('Error loading telemedicine data:', err);
      setError(err.response?.data?.message || err.message || 'Failed to load telemedicine data');
    } finally {
      setLoading(false);
    }
  };

  const fetchConsultations = async () => {
    try {
      const response = await telemedicineService.getConsultations({ limit: 100 });
      const consultationsData = Array.isArray(response.data) 
        ? response.data 
        : (response.data?.items || []);
      setConsultations(consultationsData);
    } catch (err: any) {
      console.warn('Error fetching consultations (using empty data):', err.response?.data?.message || err.message);
      setConsultations([]);
    }
  };

  const fetchStats = async () => {
    try {
      const response = await telemedicineService.getStats();
      setTelemedicineStats(response.data);
    } catch (err: any) {
      console.warn('Error fetching telemedicine stats (using default values):', err.response?.data?.message || err.message);
      setTelemedicineStats({
        totalSessions: 0,
        activeSessions: 0,
        completedSessions: 0,
        scheduledSessions: 0,
        cancelledSessions: 0,
        totalPatients: 0,
        averageSessionDuration: 0,
        patientSatisfaction: 0
      });
    }
  };

  // Modal states
  const [_sessionDetailOpened, { open: _openSessionDetail, close: _closeSessionDetail }] = useDisclosure(false);
  const [_startSessionOpened, { open: openStartSession, close: _closeStartSession }] = useDisclosure(false);
  const [_prescriptionDetailOpened, { open: _openPrescriptionDetail, close: _closePrescriptionDetail }] = useDisclosure(false);
  const [_monitoringDetailOpened, { open: _openMonitoringDetail, close: _closeMonitoringDetail }] = useDisclosure(false);
  const [_createPrescriptionOpened, { open: openCreatePrescription, close: _closeCreatePrescription }] = useDisclosure(false);
  const [_videoCallOpened, { open: _openVideoCall, close: _closeVideoCall }] = useDisclosure(false);

  // Timer effect for call duration
  useEffect(() => {
    let interval: NodeJS.Timeout;
    if (_isInCall) {
      interval = setInterval(() => {
        _setCallDuration(prev => prev + 1);
      }, 1000);
    }
    return () => clearInterval(interval);
  }, [_isInCall]);

  // Filter sessions
  const filteredSessions = useMemo(() => {
    const sessionsList = consultations.length > 0 ? consultations : [];
    return sessionsList.filter((session: any) => {
      const matchesSearch = 
        session.patientName.toLowerCase().includes(searchQuery.toLowerCase()) ||
        session.doctorName.toLowerCase().includes(searchQuery.toLowerCase()) ||
        (session as any).sessionId?.toLowerCase().includes(searchQuery.toLowerCase());

      const matchesType = !selectedSessionType || session.type === selectedSessionType;
      const matchesStatus = !selectedStatus || session.status === selectedStatus;
      const matchesDoctor = !selectedDoctor || (session as any).doctorId === selectedDoctor;

      return matchesSearch && matchesType && matchesStatus && matchesDoctor;
    });
  }, [searchQuery, selectedSessionType, selectedStatus, selectedDoctor]);

  // Helper functions
  const getStatusColor = (status: SessionStatus | ConsultationStatus) => {
    switch (status) {
      case 'scheduled': return 'blue';
      case 'in_progress':
      case 'active': return 'green';
      case 'completed': return 'teal';
      case 'cancelled': return 'red';
      case 'no_show': return 'gray';
      case 'waiting': return 'orange';
      default: return 'gray';
    }
  };

  const getSessionTypeColor = (type: SessionType) => {
    switch (type) {
      case 'consultation': return 'blue';
      case 'follow_up': return 'green';
      case 'emergency': return 'red';
      case 'therapy': return 'purple';
      case 'monitoring': return 'orange';
      default: return 'gray';
    }
  };

  const getPriorityColor = (priority: string) => {
    switch (priority) {
      case 'urgent': return 'red';
      case 'high': return 'orange';
      case 'normal': return 'blue';
      case 'low': return 'gray';
      default: return 'gray';
    }
  };

  const handleViewSession = (_session: TelemedicineSession) => {
    setSelectedSession(_session);
    _openSessionDetail();
  };

  const handleViewPrescription = (_prescription: DigitalPrescription) => {
    setSelectedPrescription(_prescription);
    _openPrescriptionDetail();
  };

  const handleViewMonitoring = (_monitoring: PatientMonitoring) => {
    setSelectedPatientMonitoring(_monitoring);
    _openMonitoringDetail();
  };

  const handleStartVideoCall = (_session?: TelemedicineSession) => {
    _setIsInCall(true);
    _setCallDuration(0);
    _openVideoCall();
    notifications.show({
      title: 'Call Started',
      message: 'Video consultation session has begun',
      color: 'green',
    });
  };

  const handleEndCall = () => {
    _setIsInCall(false);
    _setCallDuration(0);
    _closeVideoCall();
    notifications.show({
      title: 'Call Ended',
      message: 'Video consultation session has ended',
      color: 'blue',
    });
  };

  const toggleVideo = () => {
    _setIsVideoEnabled(!_isVideoEnabled);
    notifications.show({
      title: _isVideoEnabled ? 'Video Off' : 'Video On',
      message: `Camera has been turned ${_isVideoEnabled ? 'off' : 'on'}`,
      color: 'blue',
    });
  };

  const toggleAudio = () => {
    _setIsAudioEnabled(!_isAudioEnabled);
    notifications.show({
      title: _isAudioEnabled ? 'Audio Off' : 'Audio On',
      message: `Microphone has been turned ${_isAudioEnabled ? 'off' : 'on'}`,
      color: 'blue',
    });
  };

  const toggleScreenShare = () => {
    _setIsScreenSharing(!_isScreenSharing);
    notifications.show({
      title: _isScreenSharing ? 'Screen Share Stopped' : 'Screen Share Started',
      message: `Screen sharing has been ${_isScreenSharing ? 'stopped' : 'started'}`,
      color: 'blue',
    });
  };

  const toggleRecording = () => {
    _setIsRecording(!_isRecording);
    notifications.show({
      title: _isRecording ? 'Recording Stopped' : 'Recording Started',
      message: `Session recording has been ${_isRecording ? 'stopped' : 'started'}`,
      color: _isRecording ? 'red' : 'green',
    });
  };

  const formatDuration = (seconds: number) => {
    const hours = Math.floor(seconds / 3600);
    const minutes = Math.floor((seconds % 3600) / 60);
    const secs = seconds % 60;
    return `${hours.toString().padStart(2, '0')}:${minutes.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
  };

  const formatDate = (date: string | Date) => {
    const d = new Date(date);
    const year = d.getFullYear();
    const month = String(d.getMonth() + 1).padStart(2, '0');
    const day = String(d.getDate()).padStart(2, '0');
    return `${year}-${month}-${day}`;
  };

  const formatDateTime = (date: string | Date) => {
    const d = new Date(date);
    const year = d.getFullYear();
    const month = String(d.getMonth() + 1).padStart(2, '0');
    const day = String(d.getDate()).padStart(2, '0');
    const hours = String(d.getHours()).padStart(2, '0');
    const minutes = String(d.getMinutes()).padStart(2, '0');
    return `${year}-${month}-${day} ${hours}:${minutes}`;
  };

  // Quick stats for overview
  const quickStats = [
    {
      title: 'Active Sessions',
      value: (telemedicineStats as any)?.activeSessions || 0,
      icon: IconVideo,
      color: 'green'
    },
    {
      title: 'Scheduled Sessions',
      value: (telemedicineStats as any)?.scheduledSessions || 0,
      icon: IconCalendarEvent,
      color: 'blue'
    },
    {
      title: 'Monitored Patients',
      value: (telemedicineStats as any)?.monitoredPatients || 0,
      icon: IconDeviceHeartMonitor,
      color: 'purple'
    },
    {
      title: 'Digital Prescriptions',
      value: (telemedicineStats as any)?.digitalPrescriptions || 0,
      icon: IconPrescription,
      color: 'orange'
    }
  ];

  return (
    <Container size="xl" py="md">
      {/* Header */}
      <Group justify="space-between" mb="lg">
        <div>
          <Title order={1}>Telemedicine & Virtual Care</Title>
          <Text c="dimmed" size="sm">
            Manage virtual consultations, remote monitoring, and digital healthcare delivery
          </Text>
        </div>
        <Group>
          <Button
            leftSection={<IconVideo size={16} />}
            onClick={() => handleStartVideoCall()}
            color="green"
          >
            Start Session
          </Button>
          <Button
            variant="light"
            leftSection={<IconCalendarEvent size={16} />}
            onClick={openStartSession}
          >
            Schedule Session
          </Button>
          <Button
            variant="light"
            leftSection={<IconPrescription size={16} />}
            onClick={openCreatePrescription}
          >
            Digital Prescription
          </Button>
        </Group>
      </Group>

      {/* Quick Stats */}
      <SimpleGrid cols={{ base: 1, sm: 2, md: 4 }} mb="lg">
        {quickStats.map((stat) => {
          const Icon = stat.icon;
          return (
            <Card key={stat.title} padding="lg" radius="md" withBorder>
              <Group justify="space-between">
                <div>
                  <Text c="dimmed" size="sm" fw={500}>
                    {stat.title}
                  </Text>
                  <Text fw={700} size="xl">
                    {stat.value}
                  </Text>
                </div>
                <ThemeIcon color={stat.color} size="xl" radius="md" variant="light">
                  <Icon size={24} />
                </ThemeIcon>
              </Group>
            </Card>
          );
        })}
      </SimpleGrid>

      {/* Main Content Tabs */}
      <Tabs value={activeTab} onChange={setActiveTab}>
        <Tabs.List>
          <Tabs.Tab value="overview" leftSection={<IconChartBar size={16} />}>
            Overview
          </Tabs.Tab>
          <Tabs.Tab value="sessions" leftSection={<IconVideo size={16} />}>
            Sessions
          </Tabs.Tab>
          <Tabs.Tab value="monitoring" leftSection={<IconDeviceHeartMonitor size={16} />}>
            Remote Monitoring
          </Tabs.Tab>
          <Tabs.Tab value="prescriptions" leftSection={<IconPrescription size={16} />}>
            Digital Prescriptions
          </Tabs.Tab>
          <Tabs.Tab value="consultations" leftSection={<IconStethoscope size={16} />}>
            Virtual Consultations
          </Tabs.Tab>
        </Tabs.List>

        {/* Overview Tab */}
        <Tabs.Panel value="overview">
          <SimpleGrid cols={{ base: 1, lg: 2 }} spacing="lg" mt="md">
            {/* Session Analytics */}
            <Card padding="lg" radius="md" withBorder>
              <Title order={4} mb="md">Session Statistics</Title>
              <MantineDonutChart
                data={[]}
                size={200}
                thickness={40}
                withLabels
              />
            </Card>

            {/* Active Monitoring Alerts */}
            <Card padding="lg" radius="md" withBorder>
              <Title order={4} mb="md">Monitoring Alerts</Title>
              <Stack gap="sm">
                <Text size="sm" c="dimmed" ta="center" py="xl">
                  No active alerts
                </Text>
              </Stack>
            </Card>

            {/* Today's Sessions */}
            <Card padding="lg" radius="md" withBorder>
              <Title order={4} mb="md">Today&apos;s Sessions</Title>
              <Stack gap="sm">
                {[].map((session) => (
                  <Card key={session.id} padding="sm" withBorder>
                    <Group justify="space-between">
                      <div>
                        <Text fw={500} size="sm">{session.patientName}</Text>
                        <Text size="xs" c="dimmed">Dr. {session.doctorName}</Text>
                      </div>
                      <div style={{ textAlign: 'right' }}>
                        <Badge color={getStatusColor((session as any).status)} size="sm">
                          {session.status}
                        </Badge>
                        <Text size="xs" c="dimmed" mt="xs">
                          {session.scheduledTime}
                        </Text>
                      </div>
                    </Group>
                  </Card>
                ))}
              </Stack>
            </Card>

            {/* Equipment Status */}
            <Card padding="lg" radius="md" withBorder>
              <Title order={4} mb="md">System Status</Title>
              <Stack gap="sm">
                <Text size="sm" c="dimmed" ta="center" py="xl">
                  System status unavailable
                </Text>
              </Stack>
            </Card>
          </SimpleGrid>
        </Tabs.Panel>

        {/* Sessions Tab */}
        <Tabs.Panel value="sessions">
          <Paper p="md" radius="md" withBorder mt="md">
            {/* Search and Filters */}
            <Group mb="md">
              <TextInput
                placeholder="Search sessions..."
                leftSection={<IconSearch size={16} />}
                value={searchQuery}
                onChange={(event) => setSearchQuery(event.currentTarget.value)}
                style={{ flex: 1 }}
              />
              <Select
                placeholder="Type"
                data={[
                  { value: 'consultation', label: 'Consultation' },
                  { value: 'follow_up', label: 'Follow-up' },
                  { value: 'emergency', label: 'Emergency' },
                  { value: 'therapy', label: 'Therapy' },
                  { value: 'monitoring', label: 'Monitoring' }
                ]}
                value={selectedSessionType}
                onChange={setSelectedSessionType}
                clearable
              />
              <Select
                placeholder="Status"
                data={[
                  { value: 'scheduled', label: 'Scheduled' },
                  { value: 'in_progress', label: 'In Progress' },
                  { value: 'completed', label: 'Completed' },
                  { value: 'cancelled', label: 'Cancelled' },
                  { value: 'no_show', label: 'No Show' },
                  { value: 'waiting', label: 'Waiting' }
                ]}
                value={selectedStatus}
                onChange={setSelectedStatus}
                clearable
              />
              <Select
                placeholder="Doctor"
                data={[].map /* TODO: Fetch from API */(doctor => ({
                  value: doctor.id,
                  label: `Dr. ${(doctor as any).firstName || doctor.name.split(' ')[0]} ${(doctor as any).lastName || doctor.name.split(' ')[1] || ''}`
                }))}
                value={selectedDoctor}
                onChange={setSelectedDoctor}
                clearable
              />
            </Group>

            {/* Sessions Grid */}
            <SimpleGrid cols={{ base: 1, lg: 2 }} spacing="lg">
              {filteredSessions.length === 0 ? (
                <div style={{ gridColumn: '1 / -1' }}>
                  <EmptyState
                    icon={<IconVideo size={48} />}
                    title="No telemedicine sessions"
                    description="Schedule your first virtual consultation"
                    size="sm"
                  />
                </div>
              ) : (
                filteredSessions.map((session) => (
                  <Card key={session.id} padding="lg" radius="md" withBorder>
                    <Group justify="space-between" mb="md">
                      <div>
                        <Text fw={600} size="lg">{session.patientName}</Text>
                        <Text size="sm" c="dimmed">Dr. {session.doctorName}</Text>
                      </div>
                      <Badge color={getStatusColor((session as any).status)} variant="light">
                        {session.status.replace('_', ' ').toUpperCase()}
                      </Badge>
                    </Group>

                  <Stack gap="sm" mb="md">
                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Session ID</Text>
                      <Text size="sm" fw={500}>{(session as any).sessionId || (session as any).consultationId}</Text>
                    </Group>
                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Type</Text>
                      <Badge color={getSessionTypeColor((session as any).type)} variant="light" size="sm">
                        {session.type}
                      </Badge>
                    </Group>
                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Scheduled</Text>
                      <Text size="sm">{formatDateTime((session as any).scheduledDate || new Date().toISOString())} at {session.scheduledTime}</Text>
                    </Group>
                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Duration</Text>
                      <Text size="sm">{session.duration} minutes</Text>
                    </Group>
                    {(session as any).actualDuration && (
                      <Group justify="space-between">
                        <Text size="sm" c="dimmed">Actual Duration</Text>
                        <Text size="sm">{(session as any).actualDuration} minutes</Text>
                      </Group>
                    )}
                  </Stack>

                  {(session as any).notes && (
                    <Text size="sm" c="dimmed" lineClamp={2} mb="md">
                      Notes: {(session as any).notes}
                    </Text>
                  )}

                  <Group justify="space-between">
                    <Text size="xs" c="dimmed">
                      Platform: {(session as any).platform || 'Web'}
                    </Text>
                    <Group gap="xs">
                      <ActionIcon
                        variant="subtle"
                        color="blue"
                        onClick={() => handleViewSession(session as any)}
                      >
                        <IconEye size={16} />
                      </ActionIcon>
                      {(session as any).status === 'scheduled' && (
                        <ActionIcon
                          variant="subtle"
                          color="green"
                          onClick={() => handleStartVideoCall(session as any)}
                        >
                          <IconVideo size={16} />
                        </ActionIcon>
                      )}
                      {(session as any).status === 'in_progress' && (
                        <ActionIcon
                          variant="subtle"
                          color="orange"
                          onClick={() => openVideoCall()}
                        >
                          <IconBrandZoom size={16} />
                        </ActionIcon>
                      )}
                      <ActionIcon variant="subtle" color="teal">
                        <IconDownload size={16} />
                      </ActionIcon>
                    </Group>
                  </Group>
                </Card>
              )))
              }
            </SimpleGrid>
          </Paper>
        </Tabs.Panel>

        {/* Remote Monitoring Tab */}
        <Tabs.Panel value="monitoring">
          <Paper p="md" radius="md" withBorder mt="md">
            <Group justify="space-between" mb="lg">
              <Title order={3}>Remote Patient Monitoring</Title>
              <Button leftSection={<IconPlus size={16} />} variant="light">
                Add Patient
              </Button>
            </Group>

            {/* Monitoring Grid */}
            <SimpleGrid cols={{ base: 1, lg: 2 }} spacing="lg">
              {[].map /* TODO: Fetch from API */((monitoring) => (
                <Card key={monitoring.id} padding="lg" radius="md" withBorder>
                  <Group justify="space-between" mb="md">
                    <div>
                      <Group>
                        <Avatar color="blue" size="md">
                          {monitoring.patientName.split(' ').map(n => n[0]).join('')}
                        </Avatar>
                        <div>
                          <Text fw={600} size="lg">{monitoring.patientName}</Text>
                          <Text size="sm" c="dimmed">ID: {(monitoring as any).patientId || monitoring.id}</Text>
                        </div>
                      </Group>
                    </div>
                    <Badge 
                      color={(monitoring as any).isActive ? 'green' : 'gray'} 
                      variant={(monitoring as any).isActive ? 'filled' : 'light'}
                    >
                      {(monitoring as any).isActive ? 'ACTIVE' : 'INACTIVE'}
                    </Badge>
                  </Group>

                  <Stack gap="sm" mb="md">
                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Device</Text>
                      <Group gap="xs">
                        <Text size="sm">{(monitoring as any).deviceType || 'N/A'}</Text>
                        <Badge 
                          color={(monitoring as any).deviceStatus === 'connected' ? 'green' : 'red'} 
                          variant="light" 
                          size="xs"
                        >
                          {(monitoring as any).deviceStatus || 'Unknown'}
                        </Badge>
                      </Group>
                    </Group>
                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Last Reading</Text>
                      <Text size="sm">{formatDateTime((monitoring as any).lastReading || new Date().toISOString())}</Text>
                    </Group>
                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Monitoring Since</Text>
                      <Text size="sm">{formatDate((monitoring as any).startDate || new Date().toISOString())}</Text>
                    </Group>
                  </Stack>

                  {/* Vital Signs */}
                  <Divider label="Current Vitals" labelPosition="center" mb="md" />
                  <SimpleGrid cols={2} spacing="sm" mb="md">
                    <div style={{ textAlign: 'center' }}>
                      <ThemeIcon color="red" variant="light" size="lg" mx="auto" mb="xs">
                        <IconHeart size={20} />
                      </ThemeIcon>
                      <Text size="lg" fw={600} c="red">
                        {(monitoring as any).vitals?.heartRate || 'N/A'} bpm
                      </Text>
                      <Text size="xs" c="dimmed">Heart Rate</Text>
                    </div>
                    <div style={{ textAlign: 'center' }}>
                      <ThemeIcon color="blue" variant="light" size="lg" mx="auto" mb="xs">
                        <IconActivity size={20} />
                      </ThemeIcon>
                      <Text size="lg" fw={600} c="blue">
                        {(monitoring as any).vitals?.bloodPressure || 'N/A'}
                      </Text>
                      <Text size="xs" c="dimmed">Blood Pressure</Text>
                    </div>
                    <div style={{ textAlign: 'center' }}>
                      <ThemeIcon color="orange" variant="light" size="lg" mx="auto" mb="xs">
                        <IconThermometer size={20} />
                      </ThemeIcon>
                      <Text size="lg" fw={600} c="orange">
                        {(monitoring as any).vitals?.temperature || 'N/A'}¬∞F
                      </Text>
                      <Text size="xs" c="dimmed">Temperature</Text>
                    </div>
                    <div style={{ textAlign: 'center' }}>
                      <ThemeIcon color="cyan" variant="light" size="lg" mx="auto" mb="xs">
                        <IconLungs size={20} />
                      </ThemeIcon>
                      <Text size="lg" fw={600} c="cyan">
                        {(monitoring as any).vitals?.oxygenSaturation || 'N/A'}%
                      </Text>
                      <Text size="xs" c="dimmed">O2 Saturation</Text>
                    </div>
                  </SimpleGrid>

                  {/* Alert Status */}
                  {(monitoring as any).alerts && (monitoring as any).alerts.length > 0 && (
                    <Alert variant="light" color="red" icon={<IconAlertTriangle size={16} />} mb="md">
                      <Text size="sm" fw={500}>Active Alerts: {(monitoring as any).alerts.length}</Text>
                      <Text size="xs">Latest: {(monitoring as any).alerts[0]}</Text>
                    </Alert>
                  )}

                  <Group justify="space-between">
                    <Text size="xs" c="dimmed">
                      Battery: {(monitoring as any).batteryLevel || 'N/A'}%
                    </Text>
                    <Group gap="xs">
                      <ActionIcon
                        variant="subtle"
                        color="blue"
                        onClick={() => handleViewMonitoring(monitoring as any)}
                      >
                        <IconEye size={16} />
                      </ActionIcon>
                      <ActionIcon variant="subtle" color="green">
                        <IconChartLine size={16} />
                      </ActionIcon>
                      <ActionIcon variant="subtle" color="orange">
                        <IconSettings size={16} />
                      </ActionIcon>
                    </Group>
                  </Group>
                </Card>
              ))}
            </SimpleGrid>
          </Paper>
        </Tabs.Panel>

        {/* Digital Prescriptions Tab */}
        <Tabs.Panel value="prescriptions">
          <Paper p="md" radius="md" withBorder mt="md">
            <Group justify="space-between" mb="lg">
              <Title order={3}>Digital Prescriptions</Title>
              <Group>
                <Button leftSection={<IconPlus size={16} />} onClick={openCreatePrescription}>
                  New Prescription
                </Button>
                <Button variant="light" leftSection={<IconDownload size={16} />}>
                  Export All
                </Button>
              </Group>
            </Group>

            {/* Prescriptions Grid */}
            <SimpleGrid cols={{ base: 1, lg: 2 }} spacing="lg">
              {[].map /* TODO: Fetch from API */((prescription) => (
                <Card key={prescription.id} padding="lg" radius="md" withBorder>
                  <Group justify="space-between" mb="md">
                    <div>
                      <Text fw={600} size="lg">{prescription.patientName}</Text>
                      <Text size="sm" c="dimmed">ID: {(prescription as any).prescriptionId || prescription.id}</Text>
                    </div>
                    <Badge 
                      color={
                        (prescription as any).status === 'active' ? 'green' : 
                        (prescription as any).status === 'pending' ? 'orange' : 
                        (prescription as any).status === 'completed' ? 'blue' : 'red'
                      } 
                      variant="light"
                    >
                      {((prescription as any).status || 'pending').toUpperCase()}
                    </Badge>
                  </Group>

                  <Stack gap="sm" mb="md">
                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Prescribed by</Text>
                      <Text size="sm">Dr. {(prescription as any).doctorName || 'N/A'}</Text>
                    </Group>
                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Date Issued</Text>
                      <Text size="sm">{formatDate((prescription as any).issuedDate || (prescription as any).date || new Date().toISOString())}</Text>
                    </Group>
                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Valid Until</Text>
                      <Text size="sm" c={(prescription as any).expiryDate && new Date((prescription as any).expiryDate) < new Date() ? 'red' : undefined}>
                        {formatDate((prescription as any).expiryDate || new Date().toISOString())}
                      </Text>
                    </Group>
                  </Stack>

                  <Divider label="Medications" labelPosition="center" mb="md" />
                  
                  <Stack gap="xs" mb="md">
                    {(Array.isArray((prescription as any).medications) ? (prescription as any).medications : []).map((med: any, index: number) => (
                      <Card key={index} padding="xs" withBorder>
                        <Group justify="space-between">
                          <div>
                            <Text size="sm" fw={500}>{med.name}</Text>
                            <Text size="xs" c="dimmed">{med.dosage} - {med.frequency}</Text>
                          </div>
                          <Badge variant="light" size="xs">
                            {med.duration}
                          </Badge>
                        </Group>
                      </Card>
                    ))}
                  </Stack>

                  {(prescription as any).pharmacyInfo && (
                    <Group justify="space-between" mb="md">
                      <Text size="sm" c="dimmed">Pharmacy</Text>
                      <Text size="sm">{(prescription as any).pharmacyInfo}</Text>
                    </Group>
                  )}

                  <Group justify="space-between">
                    <Text size="xs" c="dimmed">
                      Digital Signature: ‚úì Verified
                    </Text>
                    <Group gap="xs">
                      <ActionIcon
                        variant="subtle"
                        color="blue"
                        onClick={() => handleViewPrescription(prescription as any)}
                      >
                        <IconEye size={16} />
                      </ActionIcon>
                      <ActionIcon variant="subtle" color="green">
                        <IconDownload size={16} />
                      </ActionIcon>
                      <ActionIcon variant="subtle" color="orange">
                        <IconShare size={16} />
                      </ActionIcon>
                    </Group>
                  </Group>
                </Card>
              ))}
            </SimpleGrid>
          </Paper>
        </Tabs.Panel>

        {/* Virtual Consultations Tab */}
        <Tabs.Panel value="consultations">
          <Paper p="md" radius="md" withBorder mt="md">
            <Group justify="space-between" mb="lg">
              <Title order={3}>Virtual Consultations</Title>
              <Button leftSection={<IconCalendarEvent size={16} />} variant="light">
                Schedule Consultation
              </Button>
            </Group>

            {/* Consultations Grid */}
            <SimpleGrid cols={{ base: 1, lg: 2 }} spacing="lg">
              {[].map /* TODO: Fetch from API */((consultation) => (
                <Card key={consultation.id} padding="lg" radius="md" withBorder>
                  <Group justify="space-between" mb="md">
                    <div>
                      <Group>
                        <Avatar color="teal" size="md">
                          {consultation.patientName.split(' ').map(n => n[0]).join('')}
                        </Avatar>
                        <div>
                          <Text fw={600} size="lg">{consultation.patientName}</Text>
                          <Text size="sm" c="dimmed">with Dr. {consultation.doctorName}</Text>
                        </div>
                      </Group>
                    </div>
                    <Badge color={getStatusColor((consultation as any).status)} variant="light">
                      {consultation.status.replace('_', ' ').toUpperCase()}
                    </Badge>
                  </Group>

                  <Stack gap="sm" mb="md">
                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Specialty</Text>
                      <Text size="sm">{consultation.specialty}</Text>
                    </Group>
                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Appointment</Text>
                      <Text size="sm">{formatDateTime((consultation as any).appointmentDate || new Date().toISOString())}</Text>
                    </Group>
                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Duration</Text>
                      <Text size="sm">{(consultation as any).estimatedDuration || consultation.duration} minutes</Text>
                    </Group>
                    <Group justify="space-between">
                      <Text size="sm" c="dimmed">Consultation Fee</Text>
                      <Text size="sm" fw={500}>‚Çπ{(consultation as any).consultationFee || 0}</Text>
                    </Group>
                  </Stack>

                  {((consultation as any).symptoms || (consultation as any).reason) && (
                    <>
                      <Text size="sm" c="dimmed" mb="xs">Chief Complaints:</Text>
                      <Text size="sm" lineClamp={2} mb="md">
                        {(consultation as any).symptoms || (consultation as any).reason}
                      </Text>
                    </>
                  )}

                  {(consultation as any).followUpRequired && (
                    <Alert variant="light" color="blue" icon={<IconCalendarEvent size={16} />} mb="md">
                      <Text size="sm">Follow-up consultation recommended</Text>
                    </Alert>
                  )}

                  <Group justify="space-between">
                    <Text size="xs" c="dimmed">
                      Session ID: {(consultation as any).sessionId || (consultation as any).consultationId}
                    </Text>
                    <Group gap="xs">
                      <ActionIcon variant="subtle" color="blue">
                        <IconEye size={16} />
                      </ActionIcon>
                      {(consultation as any).status === 'active' && (
                        <ActionIcon
                          variant="subtle"
                          color="green"
                          onClick={() => handleStartVideoCall()}
                        >
                          <IconVideo size={16} />
                        </ActionIcon>
                      )}
                      <ActionIcon variant="subtle" color="orange">
                        <IconMessage size={16} />
                      </ActionIcon>
                    </Group>
                  </Group>
                </Card>
              ))}
            </SimpleGrid>
          </Paper>
        </Tabs.Panel>
      </Tabs>

      {/* Video Call Modal */}
      <Modal
        opened={videoCallOpened}
        onClose={() => {
          if (isInCall) {
            handleEndCall();
          } else {
            closeVideoCall();
          }
        }}
        title="Video Consultation"
        size="xl"
        fullScreen
        withCloseButton={false}
      >
        <div style={{ position: 'relative', height: '100vh', backgroundColor: '#000' }}>
          {/* Main Video Area */}
          <div style={{ 
            display: 'flex', 
            justifyContent: 'center', 
            alignItems: 'center', 
            height: 'calc(100vh - 120px)',
            position: 'relative'
          }}>
            {/* Doctor's Video (Main) */}
            <div style={{ 
              width: '70%', 
              height: '80%', 
              backgroundColor: '#1a1a1a', 
              borderRadius: '12px',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              position: 'relative'
            }}>
              {isVideoEnabled ? (
                <div style={{ textAlign: 'center', color: 'white' }}>
                  <IconVideo size={80} />
                  <Text size="xl" mt="md">Dr. Smith</Text>
                  <Text size="sm" c="dimmed">Cardiology Consultation</Text>
                </div>
              ) : (
                <div style={{ textAlign: 'center', color: 'white' }}>
                  <IconVideoOff size={80} />
                  <Text size="xl" mt="md">Camera Off</Text>
                </div>
              )}
              
              {/* Screen Share Indicator */}
              {isScreenSharing && (
                <Badge 
                  style={{ position: 'absolute', top: 10, left: 10 }}
                  color="blue"
                  variant="filled"
                >
                  Screen Sharing
                </Badge>
              )}
              
              {/* Recording Indicator */}
              {isRecording && (
                <Badge 
                  style={{ position: 'absolute', top: 10, right: 10 }}
                  color="red"
                  variant="filled"
                >
                  ‚óè Recording
                </Badge>
              )}
            </div>

            {/* Patient's Video (Picture in Picture) */}
            <div style={{ 
              position: 'absolute', 
              top: 20, 
              right: 20, 
              width: '200px', 
              height: '150px',
              backgroundColor: '#2a2a2a', 
              borderRadius: '8px',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center'
            }}>
              <div style={{ textAlign: 'center', color: 'white' }}>
                <IconUser size={40} />
                <Text size="sm" mt="xs">You</Text>
              </div>
            </div>

            {/* Call Duration */}
            <div style={{ 
              position: 'absolute', 
              top: 20, 
              left: 20,
              backgroundColor: 'rgba(0,0,0,0.7)',
              padding: '8px 16px',
              borderRadius: '8px',
              color: 'white'
            }}>
              <Text size="lg" fw={600}>
                {formatDuration(callDuration)}
              </Text>
            </div>
          </div>

          {/* Control Bar */}
          <div style={{ 
            position: 'absolute', 
            bottom: 0, 
            left: 0, 
            right: 0,
            backgroundColor: 'rgba(0,0,0,0.8)',
            padding: '20px',
            display: 'flex',
            justifyContent: 'center',
            gap: '16px'
          }}>
            <ActionIcon
              size="xl"
              variant={isAudioEnabled ? 'filled' : 'light'}
              color={isAudioEnabled ? 'blue' : 'red'}
              onClick={toggleAudio}
            >
              {isAudioEnabled ? <IconMicrophone size={24} /> : <IconMicrophoneOff size={24} />}
            </ActionIcon>
            
            <ActionIcon
              size="xl"
              variant={isVideoEnabled ? 'filled' : 'light'}
              color={isVideoEnabled ? 'blue' : 'red'}
              onClick={toggleVideo}
            >
              {isVideoEnabled ? <IconVideo size={24} /> : <IconVideoOff size={24} />}
            </ActionIcon>
            
            <ActionIcon
              size="xl"
              variant={isScreenSharing ? 'filled' : 'light'}
              color="green"
              onClick={toggleScreenShare}
            >
              {isScreenSharing ? <IconScreenShareOff size={24} /> : <IconScreenShare size={24} />}
            </ActionIcon>
            
            <ActionIcon
              size="xl"
              variant={isRecording ? 'filled' : 'light'}
              color="red"
              onClick={toggleRecording}
            >
              {isRecording ? <IconX size={24} /> : <IconCircleCheck size={24} />}
            </ActionIcon>
            
            <ActionIcon
              size="xl"
              variant="light"
              color="gray"
            >
              <IconMessage size={24} />
            </ActionIcon>
            
            <ActionIcon
              size="xl"
              variant="filled"
              color="red"
              onClick={handleEndCall}
            >
              <IconPhoneOff size={24} />
            </ActionIcon>
          </div>
        </div>
      </Modal>

      {/* Start Session Modal */}
      <Modal
        opened={startSessionOpened}
        onClose={closeStartSession}
        title="Schedule Telemedicine Session"
        size="lg"
      >
        <Stack gap="md">
          <SimpleGrid cols={2}>
            <Select
              label="Patient"
              placeholder="Select patient"
              data={[].map /* TODO: Fetch from API */(patient => ({
                value: patient.id,
                label: `${patient.firstName} ${patient.lastName}`
              }))}
              required
            />
            <Select
              label="Doctor"
              placeholder="Select doctor"
              data={[].map /* TODO: Fetch from API */(doctor => ({
                value: doctor.id,
                label: `Dr. ${(doctor as any).firstName || doctor.name.split(' ')[0]} ${(doctor as any).lastName || doctor.name.split(' ')[1] || ''} - ${(doctor as any).specialty || doctor.specialization}`
              }))}
              required
            />
          </SimpleGrid>
          
          <SimpleGrid cols={2}>
            <Select
              label="Session Type"
              placeholder="Select type"
              data={[
                { value: 'consultation', label: 'Consultation' },
                { value: 'follow_up', label: 'Follow-up' },
                { value: 'emergency', label: 'Emergency' },
                { value: 'therapy', label: 'Therapy' },
                { value: 'monitoring', label: 'Monitoring' }
              ]}
              required
            />
            <NumberInput
              label="Duration (minutes)"
              placeholder="Enter duration"
              min={15}
              max={120}
              defaultValue={30}
              required
            />
          </SimpleGrid>
          
          <SimpleGrid cols={2}>
            <DatePickerInput
              label="Date"
              placeholder="Select date"
              minDate={new Date()}
              required
            />
            <Select
              label="Time"
              placeholder="Select time"
              data={[
                { value: '09:00', label: '9:00 AM' },
                { value: '10:00', label: '10:00 AM' },
                { value: '11:00', label: '11:00 AM' },
                { value: '14:00', label: '2:00 PM' },
                { value: '15:00', label: '3:00 PM' },
                { value: '16:00', label: '4:00 PM' }
              ]}
              required
            />
          </SimpleGrid>
          
          <Select
            label="Platform"
            placeholder="Select platform"
            data={[
              { value: 'zoom', label: 'Zoom' },
              { value: 'teams', label: 'Microsoft Teams' },
              { value: 'webex', label: 'Cisco Webex' },
              { value: 'custom', label: 'Custom Platform' }
            ]}
            defaultValue="zoom"
            required
          />
          
          <Textarea
            label="Session Notes"
            placeholder="Enter any special instructions or notes"
            rows={3}
          />
          
          <Group justify="flex-end">
            <Button variant="light" onClick={closeStartSession}>
              Cancel
            </Button>
            <Button onClick={() => {
              notifications.show({
                title: 'Session Scheduled',
                message: 'Telemedicine session has been scheduled successfully',
                color: 'green',
              });
              closeStartSession();
            }}>
              Schedule Session
            </Button>
          </Group>
        </Stack>
      </Modal>
    </Container>
  );
};

export default Telemedicine;

'use client';

import React, { useState, useEffect } from 'react';
import {
  Container,
  Grid,
  Paper,
  Text,
  Group,
  Badge,
  SimpleGrid,
  Stack,
  Button,
  Title,
  Card,
  TextInput,
  Select,
  LoadingOverlay,
  Alert,
  Tabs,
  ActionIcon,
  Menu,
  Avatar,
  Progress
} from '@mantine/core';
import { useDisclosure } from '@mantine/hooks';
import { notifications } from '@mantine/notifications';
import {
  IconPlus,
  IconSearch,
  IconAlertTriangle,
  IconClock,
  IconActivity,
  IconEdit,
  IconEye,
  IconDotsVertical,
  IconAlertCircle,
  IconUrgent,
  IconStethoscope,
  IconAmbulance
} from '@tabler/icons-react';
import Layout from '../../components/shared/Layout';
import DataTable from '../../components/shared/DataTable';
import EmergencyCaseForm from '../../components/emergency/EmergencyCaseForm';
import TriageForm from '../../components/emergency/TriageForm';
import EmergencyCaseDetails from '../../components/emergency/EmergencyCaseDetails';
import { useAppStore } from '../../stores/appStore';
import { User, UserRole, TableColumn } from '../../types/common';
import emergencyService from '../../services/emergency.service';
import patientsService from '../../services/patients.service';
import type { CreateEmergencyCaseDto, UpdateEmergencyCaseDto, UpdateTriageDto, EmergencyFilters } from '../../services/emergency.service';

const mockUser: User = {
  id: '1',
  username: 'admin',
  email: 'admin@hospital.com',
  firstName: 'Admin',
  lastName: 'User',
  role: UserRole.ADMIN,
  permissions: [],
  isActive: true,
  tenantInfo: {
    tenantId: 'T001',
    tenantName: 'Main Hospital',
  },
  createdAt: new Date(),
  updatedAt: new Date(),
};

function EmergencyPage() {
  const { user, setUser } = useAppStore();
  const [activeTab, setActiveTab] = useState('cases');
  const [cases, setCases] = useState<any[]>([]);
  const [queue, setQueue] = useState<any[]>([]);
  const [patients, setPatients] = useState<any[]>([]);
  const [doctors, setDoctors] = useState<any[]>([]);
  const [stats, setStats] = useState<any>(null);
  const [loading, setLoading] = useState(true);
  const [selectedCase, setSelectedCase] = useState<any>(null);
  const [searchQuery, setSearchQuery] = useState('');
  const [statusFilter, setStatusFilter] = useState('');
  const [triageFilter, setTriageFilter] = useState('');

  const [caseFormOpened, { open: openCaseForm, close: closeCaseForm }] = useDisclosure(false);
  const [triageFormOpened, { open: openTriageForm, close: closeTriageForm }] = useDisclosure(false);
  const [detailsOpened, { open: openDetails, close: closeDetails }] = useDisclosure(false);

  useEffect(() => {
    if (!user) {
      setUser(mockUser);
    }
    fetchCases();
    fetchQueue();
    fetchStats();
    fetchPatients();
    fetchDoctors();
  }, [user, setUser]);

  const fetchCases = async () => {
    setLoading(true);
    try {
      const filters: EmergencyFilters = {};
      if (searchQuery) filters.search = searchQuery;
      if (statusFilter) filters.status = statusFilter;
      if (triageFilter) filters.triageLevel = triageFilter;

      const response = await emergencyService.getCases(filters);
      if (response.success && response.data) {
        setCases(response.data.items || []);
      }
    } catch (error: any) {
      console.error('Error fetching cases:', error);
      notifications.show({
        title: 'Error',
        message: error?.message || 'Failed to fetch emergency cases',
        color: 'red',
      });
    } finally {
      setLoading(false);
    }
  };

  const fetchQueue = async () => {
    try {
      const response = await emergencyService.getQueue();
      if (response.success && response.data) {
        setQueue(response.data || []);
      }
    } catch (error: any) {
      console.error('Error fetching queue:', error);
    }
  };

  const fetchStats = async () => {
    try {
      const response = await emergencyService.getStats();
      if (response.success && response.data) {
        setStats(response.data);
      }
    } catch (error: any) {
      console.error('Error fetching stats:', error);
    }
  };

  const fetchPatients = async () => {
    try {
      const response = await patientsService.getPatients();
      if (response.success && response.data) {
        setPatients(response.data.patients || []);
      }
    } catch (error: any) {
      console.error('Error fetching patients:', error);
    }
  };

  const fetchDoctors = async () => {
    try {
      // Mock doctors - in production, fetch from staff API
      setDoctors([
        { id: '1', firstName: 'John', lastName: 'Smith', specialization: 'Emergency Medicine' },
        { id: '2', firstName: 'Sarah', lastName: 'Johnson', specialization: 'Trauma Surgery' },
      ]);
    } catch (error: any) {
      console.error('Error fetching doctors:', error);
    }
  };

  const handleCreateCase = async (data: CreateEmergencyCaseDto) => {
    try {
      const response = await emergencyService.createCase(data);
      if (response.success) {
        notifications.show({
          title: 'Success',
          message: 'Emergency case registered successfully',
          color: 'green',
        });
        fetchCases();
        fetchQueue();
        fetchStats();
        closeCaseForm();
      }
    } catch (error: any) {
      console.error('Error creating case:', error);
      notifications.show({
        title: 'Error',
        message: error?.message || 'Failed to register emergency case',
        color: 'red',
      });
      throw error;
    }
  };

  const handleUpdateCase = async (data: UpdateEmergencyCaseDto) => {
    if (!selectedCase) return;

    try {
      const response = await emergencyService.updateCase(selectedCase.id, data);
      if (response.success) {
        notifications.show({
          title: 'Success',
          message: 'Emergency case updated successfully',
          color: 'green',
        });
        fetchCases();
        fetchQueue();
        fetchStats();
        closeCaseForm();
        setSelectedCase(null);
      }
    } catch (error: any) {
      console.error('Error updating case:', error);
      notifications.show({
        title: 'Error',
        message: error?.message || 'Failed to update emergency case',
        color: 'red',
      });
      throw error;
    }
  };

  const handleUpdateTriage = async (data: UpdateTriageDto) => {
    if (!selectedCase) return;

    try {
      const response = await emergencyService.updateTriage(selectedCase.id, data);
      if (response.success) {
        notifications.show({
          title: 'Success',
          message: 'Triage level updated successfully',
          color: 'green',
        });
        fetchCases();
        fetchQueue();
        fetchStats();
        closeTriageForm();
        setSelectedCase(null);
      }
    } catch (error: any) {
      console.error('Error updating triage:', error);
      notifications.show({
        title: 'Error',
        message: error?.message || 'Failed to update triage level',
        color: 'red',
      });
      throw error;
    }
  };

  const handleViewCase = (emergencyCase: any) => {
    setSelectedCase(emergencyCase);
    openDetails();
  };

  const handleEditCase = (emergencyCase: any) => {
    setSelectedCase(emergencyCase);
    openCaseForm();
  };

  const handleUpdateTriageClick = (emergencyCase: any) => {
    setSelectedCase(emergencyCase);
    openTriageForm();
  };

  const handleNewCase = () => {
    setSelectedCase(null);
    openCaseForm();
  };

  const getTriageColor = (level: string) => {
    const colors: Record<string, string> = {
      CRITICAL: 'red',
      URGENT: 'orange',
      SEMI_URGENT: 'yellow',
      NON_URGENT: 'green'
    };
    return colors[level] || 'gray';
  };

  const getStatusColor = (status: string) => {
    const colors: Record<string, string> = {
      WAITING: 'blue',
      IN_TREATMENT: 'yellow',
      DISCHARGED: 'green',
      ADMITTED: 'cyan',
      TRANSFERRED: 'grape'
    };
    return colors[status] || 'gray';
  };

  const formatDate = (dateString: string) => {
    const date = new Date(dateString);
    return date.toLocaleString();
  };

  const calculateWaitTime = (arrivalTime: string) => {
    const arrival = new Date(arrivalTime);
    const now = new Date();
    const diff = now.getTime() - arrival.getTime();
    const minutes = Math.floor(diff / 60000);
    const hours = Math.floor(minutes / 60);
    const mins = minutes % 60;
    return hours > 0 ? `${hours}h ${mins}m` : `${mins}m`;
  };

  const caseColumns: TableColumn[] = [
    {
      key: 'triageLevel',
      title: 'Triage',
      sortable: true,
      render: (emergencyCase: any) => (
        <Badge color={getTriageColor(emergencyCase.triageLevel)} size="lg">
          {emergencyCase.triageLevel}
        </Badge>
      )
    },
    {
      key: 'patient',
      title: 'Patient',
      sortable: true,
      render: (emergencyCase: any) => (
        <Group gap="xs">
          <Avatar size="sm" radius="xl" color="blue">
            {emergencyCase.patient?.firstName?.[0]}{emergencyCase.patient?.lastName?.[0]}
          </Avatar>
          <div>
            <Text fw={500} size="sm">
              {emergencyCase.patient?.firstName} {emergencyCase.patient?.lastName}
            </Text>
            <Text size="xs" c="dimmed">
              {emergencyCase.patient?.medicalRecordNumber || emergencyCase.patient?.id}
            </Text>
          </div>
        </Group>
      )
    },
    {
      key: 'chiefComplaint',
      title: 'Chief Complaint',
      render: (emergencyCase: any) => (
        <Text lineClamp={2}>{emergencyCase.chiefComplaint}</Text>
      )
    },
    {
      key: 'status',
      title: 'Status',
      sortable: true,
      render: (emergencyCase: any) => (
        <Badge color={getStatusColor(emergencyCase.status)}>
          {emergencyCase.status}
        </Badge>
      )
    },
    {
      key: 'arrivalTime',
      title: 'Arrival',
      sortable: true,
      render: (emergencyCase: any) => (
        <div>
          <Text size="sm">{formatDate(emergencyCase.arrivalTime)}</Text>
          <Text size="xs" c="dimmed">
            Wait: {calculateWaitTime(emergencyCase.arrivalTime)}
          </Text>
        </div>
      )
    },
    {
      key: 'assignedDoctor',
      title: 'Doctor',
      render: (emergencyCase: any) => (
        emergencyCase.assignedDoctor ? (
          <Text size="sm">
            Dr. {emergencyCase.assignedDoctor.firstName} {emergencyCase.assignedDoctor.lastName}
          </Text>
        ) : (
          <Text size="sm" c="dimmed">Not assigned</Text>
        )
      )
    },
    {
      key: 'actions',
      title: 'Actions',
      render: (emergencyCase: any) => (
        <Group gap="xs">
          <ActionIcon
            variant="subtle"
            onClick={() => handleViewCase(emergencyCase)}
          >
            <IconEye size={16} />
          </ActionIcon>
          <ActionIcon
            variant="subtle"
            onClick={() => handleEditCase(emergencyCase)}
          >
            <IconEdit size={16} />
          </ActionIcon>
          <Menu position="bottom-end">
            <Menu.Target>
              <ActionIcon variant="subtle">
                <IconDotsVertical size={16} />
              </ActionIcon>
            </Menu.Target>
            <Menu.Dropdown>
              <Menu.Item
                leftSection={<IconEye size={14} />}
                onClick={() => handleViewCase(emergencyCase)}
              >
                View Details
              </Menu.Item>
              <Menu.Item
                leftSection={<IconEdit size={14} />}
                onClick={() => handleEditCase(emergencyCase)}
              >
                Edit Case
              </Menu.Item>
              <Menu.Item
                leftSection={<IconAlertTriangle size={14} />}
                onClick={() => handleUpdateTriageClick(emergencyCase)}
              >
                Update Triage
              </Menu.Item>
            </Menu.Dropdown>
          </Menu>
        </Group>
      )
    }
  ];

  return (
    <Layout user={user || mockUser} notifications={0} onLogout={() => {}}>
      <Container size="xl" py="xl">
        <Stack gap="lg">
          {/* Header */}
          <Group justify="space-between">
            <div>
              <Title order={2}>Emergency Department</Title>
              <Text c="dimmed" size="sm">
                Manage emergency cases and triage
              </Text>
            </div>
            <Button
              leftSection={<IconPlus size={16} />}
              onClick={handleNewCase}
              color="red"
            >
              Register Emergency Case
            </Button>
          </Group>

          {/* Statistics Cards */}
          {stats && (
            <SimpleGrid cols={{ base: 1, sm: 2, md: 4 }}>
              <Card withBorder padding="lg">
                <Group justify="space-between">
                  <div>
                    <Text c="dimmed" size="xs" tt="uppercase" fw={700}>
                      Critical Cases
                    </Text>
                    <Text fw={700} size="xl" c="red">
                      {stats.criticalCases}
                    </Text>
                  </div>
                  <IconUrgent size={32} color="#fa5252" />
                </Group>
              </Card>

              <Card withBorder padding="lg">
                <Group justify="space-between">
                  <div>
                    <Text c="dimmed" size="xs" tt="uppercase" fw={700}>
                      Waiting
                    </Text>
                    <Text fw={700} size="xl">
                      {stats.waiting}
                    </Text>
                  </div>
                  <IconClock size={32} color="#228be6" />
                </Group>
              </Card>

              <Card withBorder padding="lg">
                <Group justify="space-between">
                  <div>
                    <Text c="dimmed" size="xs" tt="uppercase" fw={700}>
                      In Treatment
                    </Text>
                    <Text fw={700} size="xl">
                      {stats.inTreatment}
                    </Text>
                  </div>
                  <IconStethoscope size={32} color="#fab005" />
                </Group>
              </Card>

              <Card withBorder padding="lg">
                <Group justify="space-between">
                  <div>
                    <Text c="dimmed" size="xs" tt="uppercase" fw={700}>
                      Total Cases
                    </Text>
                    <Text fw={700} size="xl">
                      {stats.total}
                    </Text>
                  </div>
                  <IconAmbulance size={32} color="#40c057" />
                </Group>
              </Card>
            </SimpleGrid>
          )}

          {/* Tabs */}
          <Tabs value={activeTab} onChange={(value) => setActiveTab(value || 'cases')}>
            <Tabs.List>
              <Tabs.Tab value="cases" leftSection={<IconActivity size={16} />}>
                All Cases
              </Tabs.Tab>
              <Tabs.Tab value="queue" leftSection={<IconClock size={16} />}>
                Emergency Queue
              </Tabs.Tab>
            </Tabs.List>

            <Tabs.Panel value="cases" pt="md">
              {/* Filters */}
              <Paper withBorder p="md" mb="md">
                <Grid>
                  <Grid.Col span={{ base: 12, sm: 6, md: 3 }}>
                    <TextInput
                      placeholder="Search cases..."
                      leftSection={<IconSearch size={16} />}
                      value={searchQuery}
                      onChange={(e) => setSearchQuery(e.target.value)}
                    />
                  </Grid.Col>
                  <Grid.Col span={{ base: 12, sm: 6, md: 3 }}>
                    <Select
                      placeholder="Filter by status"
                      data={[
                        { value: '', title: 'All Statuses' },
                        { value: 'WAITING', title: 'Waiting' },
                        { value: 'IN_TREATMENT', title: 'In Treatment' },
                        { value: 'DISCHARGED', title: 'Discharged' },
                        { value: 'ADMITTED', title: 'Admitted' }
                      ]}
                      value={statusFilter}
                      onChange={(value) => setStatusFilter(value || '')}
                    />
                  </Grid.Col>
                  <Grid.Col span={{ base: 12, sm: 6, md: 3 }}>
                    <Select
                      placeholder="Filter by triage"
                      data={[
                        { value: '', title: 'All Levels' },
                        { value: 'CRITICAL', title: 'Critical' },
                        { value: 'URGENT', title: 'Urgent' },
                        { value: 'SEMI_URGENT', title: 'Semi-Urgent' },
                        { value: 'NON_URGENT', title: 'Non-Urgent' }
                      ]}
                      value={triageFilter}
                      onChange={(value) => setTriageFilter(value || '')}
                    />
                  </Grid.Col>
                  <Grid.Col span={{ base: 12, sm: 6, md: 3 }}>
                    <Button fullWidth onClick={fetchCases}>
                      Apply Filters
                    </Button>
                  </Grid.Col>
                </Grid>
              </Paper>

              {/* Cases Table */}
              <Paper withBorder>
                <LoadingOverlay visible={loading} />
                {cases.length === 0 && !loading ? (
                  <Alert icon={<IconAlertCircle size={16} />} title="No cases found" color="blue">
                    No emergency cases match your current filters.
                  </Alert>
                ) : (
                  <DataTable
                    columns={caseColumns}
                    data={cases}
                    loading={loading}
                  />
                )}
              </Paper>
            </Tabs.Panel>

            <Tabs.Panel value="queue" pt="md">
              {/* Emergency Queue */}
              <Stack gap="md">
                {queue.length === 0 ? (
                  <Alert icon={<IconAlertCircle size={16} />} title="Queue is empty" color="green">
                    No patients currently waiting in the emergency queue.
                  </Alert>
                ) : (
                  queue.map((emergencyCase) => (
                    <Card key={emergencyCase.id} withBorder padding="md">
                      <Group justify="space-between" wrap="nowrap">
                        <Group>
                          <Badge color={getTriageColor(emergencyCase.triageLevel)} size="xl">
                            {emergencyCase.triageLevel}
                          </Badge>
                          <div>
                            <Text fw={600}>
                              {emergencyCase.patient?.firstName} {emergencyCase.patient?.lastName}
                            </Text>
                            <Text size="sm" c="dimmed">
                              {emergencyCase.chiefComplaint}
                            </Text>
                            <Text size="xs" c="dimmed">
                              Waiting: {calculateWaitTime(emergencyCase.arrivalTime)}
                            </Text>
                          </div>
                        </Group>
                        <Group>
                          <Badge color={getStatusColor(emergencyCase.status)}>
                            {emergencyCase.status}
                          </Badge>
                          <Button
                            size="xs"
                            onClick={() => handleViewCase(emergencyCase)}
                          >
                            View
                          </Button>
                        </Group>
                      </Group>
                    </Card>
                  ))
                )}
              </Stack>
            </Tabs.Panel>
          </Tabs>
        </Stack>
      </Container>

      {/* Emergency Case Form Modal */}
      <EmergencyCaseForm
        opened={caseFormOpened}
        onClose={closeCaseForm}
        emergencyCase={selectedCase}
        onSubmit={selectedCase ? handleUpdateCase : handleCreateCase}
        patients={patients}
        doctors={doctors}
      />

      {/* Triage Form Modal */}
      {selectedCase && (
        <TriageForm
          opened={triageFormOpened}
          onClose={closeTriageForm}
          emergencyCase={selectedCase}
          onSubmit={handleUpdateTriage}
        />
      )}

      {/* Emergency Case Details Modal */}
      {selectedCase && (
        <EmergencyCaseDetails
          opened={detailsOpened}
          onClose={closeDetails}
          emergencyCase={selectedCase}
          onEdit={handleEditCase}
          onUpdateTriage={handleUpdateTriageClick}
        />
      )}
    </Layout>
  );
}

