  const [records, setRecords] = useState<any[]>([]);
  const [patients, setPatients] = useState<any[]>([]);
  const [doctors, setDoctors] = useState<any[]>([]);
  const [stats, setStats] = useState<any>(null);
  const [loading, setLoading] = useState(true);
  const [selectedRecord, setSelectedRecord] = useState<any>(null);
  const [searchQuery, setSearchQuery] = useState('');
  const [patientFilter, setPatientFilter] = useState('');
  const [recordTypeFilter, setRecordTypeFilter] = useState('');

  const [recordFormOpened, { open: openRecordForm, close: closeRecordForm }] = useDisclosure(false);
  const [detailsOpened, { open: openDetails, close: closeDetails }] = useDisclosure(false);

  useEffect(() => {
    if (!user) {
      setUser(mockUser);
    }
    fetchRecords();
    fetchStats();
    fetchPatients();
    fetchDoctors();
  }, [user, setUser]);

  const fetchRecords = async () => {
    setLoading(true);
    try {
      const filters: EmrFilters = {};
      if (patientFilter) filters.patientId = patientFilter;
      if (recordTypeFilter) filters.recordType = recordTypeFilter;

      const response = await emrService.getRecords(filters);
      if (response.success && response.data) {
        setRecords(response.data.records || []);
      }
    } catch (error: any) {
      console.error('Error fetching records:', error);
      notifications.show({
        title: 'Error',
        message: error?.message || 'Failed to fetch medical records',
        color: 'red',
      });
    } finally {
      setLoading(false);
    }
  };

  const fetchStats = async () => {
    try {
      const response = await emrService.getStats();
      if (response.success && response.data) {
        setStats(response.data);
      }
    } catch (error: any) {
      console.error('Error fetching stats:', error);
    }
  };

  const fetchPatients = async () => {
    try {
      const response = await patientsService.getPatients();
      if (response.success && response.data) {
        setPatients(response.data.patients || []);
      }
    } catch (error: any) {
      console.error('Error fetching patients:', error);
    }
  };

  const fetchDoctors = async () => {
    try {
      // Mock doctors - in production, fetch from staff API
      setDoctors([
        { id: '1', firstName: 'John', lastName: 'Smith', specialization: 'General Medicine' },
        { id: '2', firstName: 'Sarah', lastName: 'Johnson', specialization: 'Cardiology' },
      ]);
    } catch (error: any) {
      console.error('Error fetching doctors:', error);
    }
  };

  const handleCreateRecord = async (data: CreateMedicalRecordDto) => {
    try {
      const response = await emrService.createRecord(data);
      if (response.success) {
        notifications.show({
          title: 'Success',
          message: 'Medical record created successfully',
          color: 'green',
        });
        fetchRecords();
        fetchStats();
        closeRecordForm();
      }
    } catch (error: any) {
      console.error('Error creating record:', error);
      notifications.show({
        title: 'Error',
        message: error?.message || 'Failed to create medical record',
        color: 'red',
      });
      throw error;
    }
  };

  const handleUpdateRecord = async (data: UpdateMedicalRecordDto) => {
    if (!selectedRecord) return;

    try {
      const response = await emrService.updateRecord(selectedRecord.id, data);
      if (response.success) {
        notifications.show({
          title: 'Success',
          message: 'Medical record updated successfully',
          color: 'green',
        });
        fetchRecords();
        fetchStats();
        closeRecordForm();
        setSelectedRecord(null);
      }
    } catch (error: any) {
      console.error('Error updating record:', error);
      notifications.show({
        title: 'Error',
        message: error?.message || 'Failed to update medical record',
        color: 'red',
      });
      throw error;
    }
  };

  const handleDeleteRecord = async (record: any) => {
    if (!window.confirm('Are you sure you want to delete this medical record?')) {
      return;
    }

    try {
      const response = await emrService.deleteRecord(record.id);
      if (response.success) {
        notifications.show({
          title: 'Success',
          message: 'Medical record deleted successfully',
          color: 'green',
        });
        fetchRecords();
        fetchStats();
      }
    } catch (error: any) {
      console.error('Error deleting record:', error);
      notifications.show({
        title: 'Error',
        message: error?.message || 'Failed to delete medical record',
        color: 'red',
      });
    }
  };

  const handleViewRecord = (record: any) => {
    setSelectedRecord(record);
    openDetails();
  };

  const handleEditRecord = (record: any) => {
    setSelectedRecord(record);
    openRecordForm();
  };

  const handleNewRecord = () => {
    setSelectedRecord(null);
    openRecordForm();
  };

  const getRecordTypeColor = (type: string) => {
    const colors: Record<string, string> = {
      CONSULTATION: 'blue',
      DIAGNOSIS: 'red',
      PRESCRIPTION: 'green',
      LAB_RESULT: 'cyan',
      IMAGING: 'grape',
      PROCEDURE: 'orange',
      VACCINATION: 'teal',
      ALLERGY: 'pink',
      OTHER: 'gray'
    };
    return colors[type] || 'gray';
  };

  const formatDate = (dateString: string) => {
    const date = new Date(dateString);
    return date.toLocaleDateString();
  };

  const recordColumns: TableColumn[] = [
    {
      key: 'recordType',
      title: 'Type',
      sortable: true,
      render: (record: any) => (
        <Badge color={getRecordTypeColor(record.recordType)}>
          {record.recordType}
        </Badge>
      )
    },
    {
      key: 'patient',
      title: 'Patient',
      sortable: true,
      render: (record: any) => (
        <Group gap="xs">
          <Avatar size="sm" radius="xl" color="blue">
            {record.patient?.firstName?.[0]}{record.patient?.lastName?.[0]}
          </Avatar>
          <div>
            <Text fw={500} size="sm">
              {record.patient?.firstName} {record.patient?.lastName}
            </Text>
            <Text size="xs" c="dimmed">
              {record.patient?.medicalRecordNumber || record.patient?.id}
            </Text>
          </div>
        </Group>
      )
    },
    {
      key: 'title',
      title: 'Title',
      sortable: true,
      render: (record: any) => (
        <Text fw={500}>{record.title}</Text>
      )
    },
    {
      key: 'description',
      title: 'Description',
      render: (record: any) => (
        <Text lineClamp={2}>{record.description}</Text>
      )
    },
    {
      key: 'doctor',
      title: 'Doctor',
      render: (record: any) => (
        record.doctor ? (
          <Text size="sm">
            Dr. {record.doctor.firstName} {record.doctor.lastName}
          </Text>
        ) : (
          <Text size="sm" c="dimmed">Not assigned</Text>
        )
      )
    },
    {
      key: 'date',
      title: 'Date',
      sortable: true,
      render: (record: any) => (
        <Text size="sm">{formatDate(record.date)}</Text>
      )
    },
    {
      key: 'actions',
      title: 'Actions',
      render: (record: any) => (
        <Group gap="xs">
          <ActionIcon
            variant="subtle"
            onClick={() => handleViewRecord(record)}
          >
            <IconEye size={16} />
          </ActionIcon>
          <ActionIcon
            variant="subtle"
            onClick={() => handleEditRecord(record)}
          >
            <IconEdit size={16} />
          </ActionIcon>
          <Menu position="bottom-end">
            <Menu.Target>
              <ActionIcon variant="subtle">
                <IconDotsVertical size={16} />
              </ActionIcon>
            </Menu.Target>
            <Menu.Dropdown>
              <Menu.Item
                leftSection={<IconEye size={14} />}
                onClick={() => handleViewRecord(record)}
              >
                View Details
              </Menu.Item>
              <Menu.Item
                leftSection={<IconEdit size={14} />}
                onClick={() => handleEditRecord(record)}
              >
                Edit Record
              </Menu.Item>
              <Menu.Divider />
              <Menu.Item
                leftSection={<IconTrash size={14} />}
                color="red"
                onClick={() => handleDeleteRecord(record)}
              >
                Delete Record
              </Menu.Item>
            </Menu.Dropdown>
          </Menu>
        </Group>
      )
    }
  ];

  return (
    <Layout user={user || mockUser} notifications={0} onLogout={() => {}}>
      <Container size="xl" py="xl">
        <Stack gap="lg">
          {/* Header */}
          <Group justify="space-between">
            <div>
              <Title order={2}>Electronic Medical Records</Title>
              <Text c="dimmed" size="sm">
                Manage patient medical records and history
              </Text>
            </div>
            <Button
              leftSection={<IconPlus size={16} />}
              onClick={handleNewRecord}
            >
              Create Medical Record
            </Button>
          </Group>

          {/* Statistics Cards */}
          {stats && (
            <SimpleGrid cols={{ base: 1, sm: 2, md: 3 }}>
              <Card withBorder padding="lg">
                <Group justify="space-between">
                  <div>
                    <Text c="dimmed" size="xs" tt="uppercase" fw={700}>
                      Total Records
                    </Text>
                    <Text fw={700} size="xl">
                      {stats.total}
                    </Text>
                  </div>
                  <IconFiles size={32} color="#228be6" />
                </Group>
              </Card>

              <Card withBorder padding="lg">
                <Group justify="space-between">
                  <div>
                    <Text c="dimmed" size="xs" tt="uppercase" fw={700}>
                      Recent Records
                    </Text>
                    <Text fw={700} size="xl">
                      {stats.recentRecords}
                    </Text>
                    <Text size="xs" c="dimmed">Last 30 days</Text>
                  </div>
                  <IconActivity size={32} color="#40c057" />
                </Group>
              </Card>

              <Card withBorder padding="lg">
                <Group justify="space-between">
                  <div>
                    <Text c="dimmed" size="xs" tt="uppercase" fw={700}>
                      Record Types
                    </Text>
                    <Text fw={700} size="xl">
                      {stats.byType?.length || 0}
                    </Text>
                  </div>
                  <IconClipboardList size={32} color="#fab005" />
                </Group>
              </Card>
            </SimpleGrid>
          )}

          {/* Filters */}
          <Paper withBorder p="md">
            <Grid>
              <Grid.Col span={{ base: 12, sm: 6, md: 4 }}>
                <TextInput
                  placeholder="Search records..."
                  leftSection={<IconSearch size={16} />}
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                />
              </Grid.Col>
              <Grid.Col span={{ base: 12, sm: 6, md: 4 }}>
                <Select
                  placeholder="Filter by patient"
                  data={[
                    { value: '', title: 'All Patients' },
                    ...patients.map(p => ({
                      value: p.id,
                      title: `${p.firstName} ${p.lastName}`
                    }))
                  ]}
                  value={patientFilter}
                  onChange={(value) => setPatientFilter(value || '')}
                  searchable
                  clearable
                />
              </Grid.Col>
              <Grid.Col span={{ base: 12, sm: 6, md: 4 }}>
                <Select
                  placeholder="Filter by type"
                  data={[
                    { value: '', title: 'All Types' },
                    { value: 'CONSULTATION', title: 'Consultation' },
                    { value: 'DIAGNOSIS', title: 'Diagnosis' },
                    { value: 'PRESCRIPTION', title: 'Prescription' },
                    { value: 'LAB_RESULT', title: 'Lab Result' },
                    { value: 'IMAGING', title: 'Imaging' },
                    { value: 'PROCEDURE', title: 'Procedure' },
                    { value: 'VACCINATION', title: 'Vaccination' },
                    { value: 'ALLERGY', title: 'Allergy' }
                  ]}
                  value={recordTypeFilter}
                  onChange={(value) => setRecordTypeFilter(value || '')}
                />
              </Grid.Col>
            </Grid>
            <Group justify="flex-end" mt="md">
              <Button onClick={fetchRecords}>
                Apply Filters
              </Button>
            </Group>
          </Paper>

          {/* Records Table */}
          <Paper withBorder>
            <LoadingOverlay visible={loading} />
            {records.length === 0 && !loading ? (
              <Alert icon={<IconAlertCircle size={16} />} title="No records found" color="blue">
                No medical records match your current filters.
              </Alert>
            ) : (
              <DataTable
                columns={recordColumns}
                data={records}
                loading={loading}
              />
            )}
          </Paper>
        </Stack>
      </Container>

      {/* Medical Record Form Modal */}
      <MedicalRecordForm
        opened={recordFormOpened}
        onClose={closeRecordForm}
        record={selectedRecord}
        onSubmit={selectedRecord ? handleUpdateRecord : handleCreateRecord}
        patients={patients}
        doctors={doctors}
      />

      {/* Medical Record Details Modal */}
      {selectedRecord && (
        <MedicalRecordDetails
          opened={detailsOpened}
          onClose={closeDetails}
          record={selectedRecord}
          onEdit={handleEditRecord}
          onDelete={handleDeleteRecord}
        />
      )}
    </Layout>
  );
}

export default EmrPage;

'use client';

import React, { useState, useEffect } from 'react';
import {
  Container,
  Grid,
  Paper,
  Text,
  Group,
  Badge,
  SimpleGrid,
  Stack,
  Button,
  Title,
  Card,
  TextInput,
  Select,
  LoadingOverlay,
  Alert,
  Tabs,
  ActionIcon,
  Menu,
  Progress
} from '@mantine/core';
import { useDisclosure } from '@mantine/hooks';
import { notifications } from '@mantine/notifications';
import { DatePickerInput } from '@mantine/dates';
import {
  IconPlus,
  IconSearch,
  IconCurrency,
  IconTrendingUp,
  IconTrendingDown,
  IconEdit,
  IconEye,
  IconTrash,
  IconDotsVertical,
  IconAlertCircle,
  IconReceipt,
  IconCash,
  IconCalendar,
  IconReport
} from '@tabler/icons-react';
import Layout from '../../components/shared/Layout';
import DataTable from '../../components/shared/DataTable';
import FinanceTransactionForm from '../../components/finance/FinanceTransactionForm';
import FinanceTransactionDetails from '../../components/finance/FinanceTransactionDetails';
import { useAppStore } from '../../stores/appStore';
import { User, UserRole, TableColumn } from '../../types/common';
import financeService from '../../services/finance.service';
import type { CreateTransactionDto, UpdateTransactionDto, FinanceFilters } from '../../services/finance.service';

const mockUser: User = {
  id: '1',
  name: 'Admin User',
  username: 'admin',
  email: 'admin@hospital.com',
  firstName: 'Admin',
  lastName: 'User',
  role: UserRole.ADMIN,
  permissions: [],
  isActive: true,
  tenantInfo: {
    tenantId: 'T001',
    tenantName: 'Main Hospital',
  },
  createdAt: new Date(),
  updatedAt: new Date(),
};

function FinancePage() {
  const { user, setUser } = useAppStore();
  const [activeTab, setActiveTab] = useState('transactions');
  const [transactions, setTransactions] = useState<any[]>([]);
  const [invoices, setInvoices] = useState<any[]>([]);
  const [payments, setPayments] = useState<any[]>([]);
  const [stats, setStats] = useState<any>(null);
  const [loading, setLoading] = useState(true);
  const [selectedTransaction, setSelectedTransaction] = useState<any>(null);
  const [searchQuery, setSearchQuery] = useState('');
  const [typeFilter, setTypeFilter] = useState('');
  const [categoryFilter, setCategoryFilter] = useState('');
  const [dateRange, setDateRange] = useState<[Date | null, Date | null]>([null, null]);

  const [transactionFormOpened, { open: openTransactionForm, close: closeTransactionForm }] = useDisclosure(false);
  const [detailsOpened, { open: openDetails, close: closeDetails }] = useDisclosure(false);

  useEffect(() => {
    if (!user) {
      setUser(mockUser);
    }
    fetchTransactions();
    fetchInvoices();
    fetchPayments();
    fetchStats();
  }, [user, setUser]);

  const fetchTransactions = async () => {
    setLoading(true);
    try {
      // Since backend doesn't have transaction endpoints yet, we'll use payments data
      // to simulate transactions and show real data from the API
      const response = await financeService.getPayments();
      
      if (response.success && response.data) {
        // Transform payments into transaction-like format
        const transformedTransactions = response.data.items.map((payment: any) => ({
          id: payment.id,
          type: 'INCOME', // Payments are income
          category: 'PAYMENT_RECEIVED',
          amount: payment.amount,
          description: `Payment for Invoice ${payment.invoice?.invoiceNumber || 'N/A'}`,
          date: payment.paymentDate || payment.createdAt,
          paymentMethod: payment.paymentMethod,
          referenceNumber: payment.paymentNumber,
          createdAt: payment.createdAt,
          updatedAt: payment.updatedAt,
          relatedType: 'INVOICE',
          relatedId: payment.invoiceId
        }));

        setTransactions(transformedTransactions);
      } else {
        // Fallback to mock data if no real payments exist
        const mockTransactions = [
          {
            id: '1',
            type: 'INCOME',
            category: 'CONSULTATION_FEES',
            amount: 1500,
            description: 'Consultation fee for patient John Doe',
            date: new Date().toISOString(),
            paymentMethod: 'CASH',
            referenceNumber: 'REF001',
            createdAt: new Date().toISOString(),
            updatedAt: new Date().toISOString()
          },
          {
            id: '2',
            type: 'EXPENSE',
            category: 'MEDICAL_SUPPLIES',
            amount: 5000,
            description: 'Purchase of medical supplies',
            date: new Date(Date.now() - 86400000).toISOString(),
            paymentMethod: 'BANK_TRANSFER',
            referenceNumber: 'REF002',
            createdAt: new Date().toISOString(),
            updatedAt: new Date().toISOString()
          }
        ];
        setTransactions(mockTransactions);
      }
    } catch (error: any) {
      console.error('Error fetching transactions:', error);
      
      // Show user-friendly error message
      notifications.show({
        title: 'Error Loading Transactions',
        message: error?.response?.data?.message || error?.message || 'Failed to fetch financial transactions. Please try again.',
        color: 'red',
        autoClose: 5000,
      });

      // Fallback to mock data on error
      const mockTransactions = [
        {
          id: '1',
          type: 'INCOME',
          category: 'CONSULTATION_FEES',
          amount: 1500,
          description: 'Consultation fee for patient John Doe',
          date: new Date().toISOString(),
          paymentMethod: 'CASH',
          referenceNumber: 'REF001',
          createdAt: new Date().toISOString(),
          updatedAt: new Date().toISOString()
        }
      ];
      setTransactions(mockTransactions);
    } finally {
      setLoading(false);
    }
  };

  const fetchInvoices = async () => {
    try {
      const response = await financeService.getInvoices();
      if (response.success && response.data) {
        setInvoices(response.data.items || []);
      }
    } catch (error: any) {
      console.error('Error fetching invoices:', error);
      notifications.show({
        title: 'Error Loading Invoices',
        message: error?.response?.data?.message || error?.message || 'Failed to fetch invoices. Please try again.',
        color: 'red',
        autoClose: 5000,
      });
    }
  };

  const fetchPayments = async () => {
    try {
      const response = await financeService.getPayments();
      if (response.success && response.data) {
        setPayments(response.data.items || []);
      }
    } catch (error: any) {
      console.error('Error fetching payments:', error);
      notifications.show({
        title: 'Error Loading Payments',
        message: error?.response?.data?.message || error?.message || 'Failed to fetch payments. Please try again.',
        color: 'red',
        autoClose: 5000,
      });
    }
  };

  const fetchStats = async () => {
    try {
      const response = await financeService.getStats();
      if (response.success && response.data) {
        setStats(response.data);
      }
    } catch (error: any) {
      console.error('Error fetching stats:', error);
      notifications.show({
        title: 'Error Loading Statistics',
        message: error?.response?.data?.message || error?.message || 'Failed to fetch financial statistics. Please try again.',
        color: 'red',
        autoClose: 5000,
      });
    }
  };

  const handleCreateTransaction = async (data: CreateTransactionDto) => {
    try {
      // Mock creation since backend doesn't have transaction endpoints yet
      const newTransaction = {
        id: Date.now().toString(),
        ...data,
        createdAt: new Date().toISOString(),
        updatedAt: new Date().toISOString()
      };

      setTransactions(prev => [newTransaction, ...prev]);
      
      notifications.show({
        title: 'Success',
        message: 'Transaction created successfully',
        color: 'green',
      });
      
      closeTransactionForm();
    } catch (error: any) {
      console.error('Error creating transaction:', error);
      notifications.show({
        title: 'Error',
        message: error?.message || 'Failed to create transaction',
        color: 'red',
      });
      throw error;
    }
  };

  const handleUpdateTransaction = async (data: UpdateTransactionDto) => {
    if (!selectedTransaction) return;

    try {
      const updatedTransaction = {
        ...selectedTransaction,
        ...data,
        updatedAt: new Date().toISOString()
      };

      setTransactions(prev => 
        prev.map(t => t.id === selectedTransaction.id ? updatedTransaction : t)
      );

      notifications.show({
        title: 'Success',
        message: 'Transaction updated successfully',
        color: 'green',
      });
      
      closeTransactionForm();
      setSelectedTransaction(null);
    } catch (error: any) {
      console.error('Error updating transaction:', error);
      notifications.show({
        title: 'Error',
        message: error?.message || 'Failed to update transaction',
        color: 'red',
      });
      throw error;
    }
  };

  const handleDeleteTransaction = async (transaction: any) => {
    if (!window.confirm('Are you sure you want to delete this transaction?')) {
      return;
    }

    try {
      setTransactions(prev => prev.filter(t => t.id !== transaction.id));
      
      notifications.show({
        title: 'Success',
        message: 'Transaction deleted successfully',
        color: 'green',
      });
    } catch (error: any) {
      console.error('Error deleting transaction:', error);
      notifications.show({
        title: 'Error',
        message: error?.message || 'Failed to delete transaction',
        color: 'red',
      });
    }
  };

  const handleViewTransaction = (transaction: any) => {
    setSelectedTransaction(transaction);
    openDetails();
  };

  const handleEditTransaction = (transaction: any) => {
    setSelectedTransaction(transaction);
    openTransactionForm();
  };

  const handleNewTransaction = () => {
    setSelectedTransaction(null);
    openTransactionForm();
  };

  const getTypeColor = (type: string) => {
    return type === 'INCOME' ? 'green' : 'red';
  };

  const formatCurrency = (amount: number) => {
    return `‚Çπ${amount.toFixed(2)}`;
  };

  const formatDate = (dateString: string) => {
    const date = new Date(dateString);
    return date.toLocaleDateString();
  };

  const formatCategory = (category: string) => {
    return category.replace(/_/g, ' ').replace(/\b\w/g, l => l.toUpperCase());
  };

  const transactionColumns: TableColumn[] = [
    {
      key: 'type',
      title: 'Type',
      sortable: true,
      render: (value: unknown, record: Record<string, unknown>) => {
        const transaction = record as any;
        return (
          <Badge color={getTypeColor(transaction.type)}>
            {transaction.type}
          </Badge>
        );
      }
    },
    {
      key: 'category',
      title: 'Category',
      sortable: true,
      render: (value: unknown, record: Record<string, unknown>) => {
        const transaction = record as any;
        return (
          <Text fw={500}>{formatCategory(transaction.category)}</Text>
        );
      }
    },
    {
      key: 'amount',
      title: 'Amount',
      sortable: true,
      render: (value: unknown, record: Record<string, unknown>) => {
        const transaction = record as any;
        return (
          <Text fw={600} c={getTypeColor(transaction.type)}>
            {transaction.type === 'EXPENSE' ? '-' : '+'}{formatCurrency(transaction.amount)}
          </Text>
        );
      }
    },
    {
      key: 'description',
      title: 'Description',
      render: (value: unknown, record: Record<string, unknown>) => {
        const transaction = record as any;
        return (
          <Text lineClamp={2}>{transaction.description}</Text>
        );
      }
    },
    {
      key: 'paymentMethod',
      title: 'Method',
      render: (value: unknown, record: Record<string, unknown>) => {
        const transaction = record as any;
        return (
          <Badge variant="light">
            {transaction.paymentMethod?.replace(/_/g, ' ')}
          </Badge>
        );
      }
    },
    {
      key: 'date',
      title: 'Date',
      sortable: true,
      render: (value: unknown, record: Record<string, unknown>) => {
        const transaction = record as any;
        return (
          <Text size="sm">{formatDate(transaction.date)}</Text>
        );
      }
    },
    {
      key: 'actions',
      title: 'Actions',
      render: (value: unknown, record: Record<string, unknown>) => {
        const transaction = record as any;
        return (
          <Group gap="xs">
            <ActionIcon
              variant="subtle"
              onClick={() => handleViewTransaction(transaction)}
            >
              <IconEye size={16} />
            </ActionIcon>
            <ActionIcon
              variant="subtle"
              onClick={() => handleEditTransaction(transaction)}
            >
              <IconEdit size={16} />
            </ActionIcon>
            <Menu position="bottom-end">
              <Menu.Target>
                <ActionIcon variant="subtle">
                  <IconDotsVertical size={16} />
                </ActionIcon>
              </Menu.Target>
              <Menu.Dropdown>
                <Menu.Item
                  leftSection={<IconEye size={14} />}
                  onClick={() => handleViewTransaction(transaction)}
                >
                  View Details
                </Menu.Item>
                <Menu.Item
                  leftSection={<IconEdit size={14} />}
                  onClick={() => handleEditTransaction(transaction)}
                >
                  Edit Transaction
                </Menu.Item>
                <Menu.Divider />
                <Menu.Item
                  leftSection={<IconTrash size={14} />}
                  color="red"
                  onClick={() => handleDeleteTransaction(transaction)}
                >
                  Delete Transaction
                </Menu.Item>
              </Menu.Dropdown>
            </Menu>
          </Group>
        );
      }
    }
  ];

  // Calculate mock stats from transactions
  const totalIncome = transactions.filter(t => t.type === 'INCOME').reduce((sum, t) => sum + t.amount, 0);
  const totalExpenses = transactions.filter(t => t.type === 'EXPENSE').reduce((sum, t) => sum + t.amount, 0);
  const netProfit = totalIncome - totalExpenses;

  return (
    <Layout user={user || mockUser} notifications={0} onLogout={() => {}}>
      <Container size="xl" py="xl">
        <Stack gap="lg">
          {/* Header */}
          <Group justify="space-between">
            <div>
              <Title order={2}>Finance Management</Title>
              <Text c="dimmed" size="sm">
                Manage financial transactions, invoices, and reports
              </Text>
            </div>
            <Button
              leftSection={<IconPlus size={16} />}
              onClick={handleNewTransaction}
            >
              Add Transaction
            </Button>
          </Group>

          {/* Statistics Cards */}
          <SimpleGrid cols={{ base: 1, sm: 2, md: 4 }}>
            <Card withBorder padding="lg">
              <Group justify="space-between">
                <div>
                  <Text c="dimmed" size="xs" tt="uppercase" fw={700}>
                    Total Income
                  </Text>
                  <Text fw={700} size="xl" c="green">
                    {formatCurrency(totalIncome)}
                  </Text>
                </div>
                <IconTrendingUp size={32} color="#40c057" />
              </Group>
            </Card>

            <Card withBorder padding="lg">
              <Group justify="space-between">
                <div>
                  <Text c="dimmed" size="xs" tt="uppercase" fw={700}>
                    Total Expenses
                  </Text>
                  <Text fw={700} size="xl" c="red">
                    {formatCurrency(totalExpenses)}
                  </Text>
                </div>
                <IconTrendingDown size={32} color="#fa5252" />
              </Group>
            </Card>

            <Card withBorder padding="lg">
              <Group justify="space-between">
                <div>
                  <Text c="dimmed" size="xs" tt="uppercase" fw={700}>
                    Net Profit
                  </Text>
                  <Text fw={700} size="xl" c={netProfit >= 0 ? 'green' : 'red'}>
                    {formatCurrency(netProfit)}
                  </Text>
                </div>
                <IconReport size={32} color={netProfit >= 0 ? '#40c057' : '#fa5252'} />
              </Group>
            </Card>

            <Card withBorder padding="lg">
              <Group justify="space-between">
                <div>
                  <Text c="dimmed" size="xs" tt="uppercase" fw={700}>
                    Outstanding
                  </Text>
                  <Text fw={700} size="xl">
                    {formatCurrency(stats?.revenue?.outstanding || 0)}
                  </Text>
                </div>
                <IconCash size={32} color="#fab005" />
              </Group>
            </Card>
          </SimpleGrid>

          {/* Tabs */}
          <Tabs value={activeTab} onChange={(value) => setActiveTab(value || 'transactions')}>
            <Tabs.List>
              <Tabs.Tab value="transactions" leftSection={<IconCurrency size={16} />}>
                Transactions
              </Tabs.Tab>
              <Tabs.Tab value="invoices" leftSection={<IconReceipt size={16} />}>
                Invoices
              </Tabs.Tab>
              <Tabs.Tab value="payments" leftSection={<IconCash size={16} />}>
                Payments
              </Tabs.Tab>
            </Tabs.List>

            <Tabs.Panel value="transactions" pt="md">
              {/* Filters */}
              <Paper withBorder p="md" mb="md">
                <Grid>
                  <Grid.Col span={{ base: 12, sm: 6, md: 3 }}>
                    <TextInput
                      placeholder="Search transactions..."
                      leftSection={<IconSearch size={16} />}
                      value={searchQuery}
                      onChange={(e) => setSearchQuery(e.target.value)}
                    />
                  </Grid.Col>
                  <Grid.Col span={{ base: 12, sm: 6, md: 3 }}>
                    <Select
                      placeholder="Filter by type"
                      data={[
                        { value: '', label: 'All Types' },
                        { value: 'INCOME', label: 'Income' },
                        { value: 'EXPENSE', label: 'Expense' }
                      ]}
                      value={typeFilter}
                      onChange={(value) => setTypeFilter(value || '')}
                    />
                  </Grid.Col>
                  <Grid.Col span={{ base: 12, sm: 6, md: 3 }}>
                    <Select
                      placeholder="Filter by category"
                      data={[
                        { value: '', label: 'All Categories' },
                        { value: 'CONSULTATION_FEES', label: 'Consultation Fees' },
                        { value: 'MEDICAL_SUPPLIES', label: 'Medical Supplies' },
                        { value: 'STAFF_SALARIES', label: 'Staff Salaries' }
                      ]}
                      value={categoryFilter}
                      onChange={(value) => setCategoryFilter(value || '')}
                    />
                  </Grid.Col>
                  <Grid.Col span={{ base: 12, sm: 6, md: 3 }}>
                    <Button fullWidth onClick={fetchTransactions}>
                      Apply Filters
                    </Button>
                  </Grid.Col>
                </Grid>
              </Paper>

              {/* Transactions Table */}
              <Paper withBorder>
                <LoadingOverlay visible={loading} />
                {transactions.length === 0 && !loading ? (
                  <Alert icon={<IconAlertCircle size={16} />} title="No transactions found" color="blue">
                    No financial transactions match your current filters.
                  </Alert>
                ) : (
                  <DataTable
                    columns={transactionColumns}
                    data={transactions}
                    loading={loading}
                  />
                )}
              </Paper>
            </Tabs.Panel>

            <Tabs.Panel value="invoices" pt="md">
              <Alert color="blue" title="Invoices">
                Invoice management is handled in the Billing module. This section shows financial overview of invoices.
              </Alert>
            </Tabs.Panel>

            <Tabs.Panel value="payments" pt="md">
              <Alert color="blue" title="Payments">
                Payment management is handled in the Billing module. This section shows financial overview of payments.
              </Alert>
            </Tabs.Panel>
          </Tabs>
        </Stack>
      </Container>

      {/* Transaction Form Modal */}
      <FinanceTransactionForm
        opened={transactionFormOpened}
        onClose={closeTransactionForm}
        transaction={selectedTransaction}
        onSubmit={selectedTransaction ? handleUpdateTransaction : handleCreateTransaction}
      />

      {/* Transaction Details Modal */}
      {selectedTransaction && (
        <FinanceTransactionDetails
          opened={detailsOpened}
          onClose={closeDetails}
          transaction={selectedTransaction}
          onEdit={handleEditTransaction}
          onDelete={handleDeleteTransaction}
        />
      )}
    </Layout>
  );
}

export default FinancePage;

'use client';

import React, { useState, useEffect } from 'react';
import {
  Container,
  Grid,
  Paper,
  Text,
  Group,
  Badge,
  SimpleGrid,
  Stack,
  Button,
  Title,
  Card,
  TextInput,
  Select,
  LoadingOverlay,
  Alert,
  ActionIcon,
  Menu,
} from '@mantine/core';
import { useDisclosure } from '@mantine/hooks';
import { notifications } from '@mantine/notifications';
import {
  IconPlus,
  IconSearch,
  IconUsers,
  IconUserCheck,
  IconUserX,
  IconBuilding,
  IconEdit,
  IconEye,
  IconTrash,
  IconDotsVertical,
  IconAlertCircle,
} from '@tabler/icons-react';
import Layout from '../../components/shared/Layout';
import DataTable from '../../components/shared/DataTable';
import StaffForm from '../../components/hr/StaffForm';
import StaffDetails from '../../components/hr/StaffDetails';
import { useAppStore } from '../../stores/appStore';
import { User, UserRole, TableColumn } from '../../types/common';
import hrService from '../../services/hr.service';
import type { CreateStaffDto, UpdateStaffDto, HrFilters } from '../../services/hr.service';

const mockUser: User = {
  id: '1',
  name: 'Admin User',
  username: 'admin',
  email: 'admin@hospital.com',
  firstName: 'Admin',
  lastName: 'User',
  role: UserRole.ADMIN,
  permissions: [],
  isActive: true,
  tenantInfo: {
    tenantId: 'T001',
    tenantName: 'Main Hospital',
  },
  createdAt: new Date(),
  updatedAt: new Date(),
};

function HrPage() {
  const { user, setUser } = useAppStore();
  const [staff, setStaff] = useState<any[]>([]);
  const [stats, setStats] = useState<any>(null);
  const [loading, setLoading] = useState(true);
  const [selectedStaff, setSelectedStaff] = useState<any>(null);
  const [searchQuery, setSearchQuery] = useState('');
  const [departmentFilter, setDepartmentFilter] = useState('');
  const [designationFilter, setDesignationFilter] = useState('');
  const [statusFilter, setStatusFilter] = useState('');
  const [departments, setDepartments] = useState<any[]>([]);

  const [staffFormOpened, { open: openStaffForm, close: closeStaffForm }] = useDisclosure(false);
  const [detailsOpened, { open: openDetails, close: closeDetails }] = useDisclosure(false);

  useEffect(() => {
    if (!user) {
      setUser(mockUser);
    }
    fetchStaff();
    fetchStats();
    fetchDepartments();
  }, [user, setUser]);

  const fetchStaff = async () => {
    setLoading(true);
    try {
      const filters: HrFilters = {
        page: 1,
        limit: 100,
      };
      if (departmentFilter) filters.departmentId = departmentFilter;
      if (designationFilter) filters.designation = designationFilter;
      if (statusFilter) filters.isActive = statusFilter;

      const response = await hrService.getStaff(filters);
      if (response.success && response.data) {
        let filteredStaff = response.data.items;

        // Apply search filter
        if (searchQuery) {
          filteredStaff = filteredStaff.filter(s =>
            `${s.firstName} ${s.lastName}`.toLowerCase().includes(searchQuery.toLowerCase()) ||
            s.email.toLowerCase().includes(searchQuery.toLowerCase()) ||
            s.phone.includes(searchQuery) ||
            (s.employeeId && s.employeeId.toLowerCase().includes(searchQuery.toLowerCase()))
          );
        }

        setStaff(filteredStaff);
      }
    } catch (error: any) {
      console.error('Error fetching staff:', error);
      notifications.show({
        title: 'Error Loading Staff',
        message: error?.response?.data?.message || error?.message || 'Failed to fetch staff members. Please try again.',
        color: 'red',
        autoClose: 5000,
      });
    } finally {
      setLoading(false);
    }
  };

  const fetchStats = async () => {
    try {
      const response = await hrService.getStats();
      if (response.success && response.data) {
        setStats(response.data);
      }
    } catch (error: any) {
      console.error('Error fetching stats:', error);
      notifications.show({
        title: 'Error Loading Statistics',
        message: error?.response?.data?.message || error?.message || 'Failed to fetch HR statistics. Please try again.',
        color: 'red',
        autoClose: 5000,
      });
    }
  };

  const fetchDepartments = async () => {
    try {
      const response = await hrService.getDepartments({ limit: 100 });
      if (response.success && response.data) {
        setDepartments(response.data.items);
      }
    } catch (error: any) {
      console.error('Error fetching departments:', error);
    }
  };

  const handleCreateStaff = async (data: CreateStaffDto) => {
    try {
      const response = await hrService.createStaff(data);
      
      if (response.success) {
        notifications.show({
          title: 'Success',
          message: 'Staff member added successfully',
          color: 'green',
          autoClose: 3000,
        });
        
        closeStaffForm();
        fetchStaff();
        fetchStats();
      }
    } catch (error: any) {
      console.error('Error creating staff:', error);
      notifications.show({
        title: 'Error Creating Staff',
        message: error?.response?.data?.message || error?.message || 'Failed to create staff member. Please try again.',
        color: 'red',
        autoClose: 5000,
      });
      throw error;
    }
  };

  const handleUpdateStaff = async (data: UpdateStaffDto) => {
    if (!selectedStaff) return;

    try {
      const response = await hrService.updateStaff(selectedStaff.id, data);
      
      if (response.success) {
        notifications.show({
          title: 'Success',
          message: 'Staff member updated successfully',
          color: 'green',
          autoClose: 3000,
        });
        
        closeStaffForm();
        setSelectedStaff(null);
        fetchStaff();
        fetchStats();
      }
    } catch (error: any) {
      console.error('Error updating staff:', error);
      notifications.show({
        title: 'Error Updating Staff',
        message: error?.response?.data?.message || error?.message || 'Failed to update staff member. Please try again.',
        color: 'red',
        autoClose: 5000,
      });
      throw error;
    }
  };

  const handleDeleteStaff = async (staffMember: any) => {
    if (!window.confirm(`Are you sure you want to deactivate ${staffMember.firstName} ${staffMember.lastName}?`)) {
      return;
    }

    try {
      const response = await hrService.deleteStaff(staffMember.id);
      
      if (response.success) {
        notifications.show({
          title: 'Success',
          message: 'Staff member deactivated successfully',
          color: 'green',
          autoClose: 3000,
        });
        
        fetchStaff();
        fetchStats();
      }
    } catch (error: any) {
      console.error('Error deleting staff:', error);
      notifications.show({
        title: 'Error Deactivating Staff',
        message: error?.response?.data?.message || error?.message || 'Failed to deactivate staff member. Please try again.',
        color: 'red',
        autoClose: 5000,
      });
    }
  };

  const handleViewStaff = (staffMember: any) => {
    setSelectedStaff(staffMember);
    openDetails();
  };

  const handleEditStaff = (staffMember: any) => {
    setSelectedStaff(staffMember);
    openStaffForm();
  };

  const handleNewStaff = () => {
    setSelectedStaff(null);
    openStaffForm();
  };

  const formatDesignation = (designation: string) => {
    return designation.replace(/_/g, ' ').replace(/\b\w/g, l => l.toUpperCase());
  };

  const staffColumns: TableColumn[] = [
    {
      key: 'employeeId',
      title: 'Employee ID',
      sortable: true,
      render: (value: unknown, record: Record<string, unknown>) => {
        const staffMember = record as any;
        return (
          <Text fw={500}>{staffMember.employeeId || 'N/A'}</Text>
        );
      }
    },
    {
      key: 'name',
      title: 'Name',
      sortable: true,
      render: (value: unknown, record: Record<string, unknown>) => {
        const staffMember = record as any;
        return (
          <div>
            <Text fw={600}>{staffMember.firstName} {staffMember.lastName}</Text>
            <Text size="xs" c="dimmed">{staffMember.email}</Text>
          </div>
        );
      }
    },
    {
      key: 'department',
      title: 'Department',
      sortable: true,
      render: (value: unknown, record: Record<string, unknown>) => {
        const staffMember = record as any;
        return (
          <Text>{staffMember.department?.name || 'N/A'}</Text>
        );
      }
    },
    {
      key: 'designation',
      title: 'Designation',
      sortable: true,
      render: (value: unknown, record: Record<string, unknown>) => {
        const staffMember = record as any;
        return (
          <Badge variant="light">
            {formatDesignation(staffMember.designation)}
          </Badge>
        );
      }
    },
    {
      key: 'phone',
      title: 'Contact',
      render: (value: unknown, record: Record<string, unknown>) => {
        const staffMember = record as any;
        return (
          <Text size="sm">{staffMember.phone}</Text>
        );
      }
    },
    {
      key: 'isActive',
      title: 'Status',
      sortable: true,
      render: (value: unknown, record: Record<string, unknown>) => {
        const staffMember = record as any;
        return (
          <Badge color={staffMember.isActive ? 'green' : 'red'}>
            {staffMember.isActive ? 'Active' : 'Inactive'}
          </Badge>
        );
      }
    },
    {
      key: 'actions',
      title: 'Actions',
      render: (value: unknown, record: Record<string, unknown>) => {
        const staffMember = record as any;
        return (
          <Group gap="xs">
            <ActionIcon
              variant="subtle"
              onClick={() => handleViewStaff(staffMember)}
            >
              <IconEye size={16} />
            </ActionIcon>
            <ActionIcon
              variant="subtle"
              onClick={() => handleEditStaff(staffMember)}
            >
              <IconEdit size={16} />
            </ActionIcon>
            <Menu position="bottom-end">
              <Menu.Target>
                <ActionIcon variant="subtle">
                  <IconDotsVertical size={16} />
                </ActionIcon>
              </Menu.Target>
              <Menu.Dropdown>
                <Menu.Item
                  leftSection={<IconEye size={14} />}
                  onClick={() => handleViewStaff(staffMember)}
                >
                  View Details
                </Menu.Item>
                <Menu.Item
                  leftSection={<IconEdit size={14} />}
                  onClick={() => handleEditStaff(staffMember)}
                >
                  Edit Staff
                </Menu.Item>
                <Menu.Divider />
                <Menu.Item
                  leftSection={<IconTrash size={14} />}
                  color="red"
                  onClick={() => handleDeleteStaff(staffMember)}
                >
                  Deactivate Staff
                </Menu.Item>
              </Menu.Dropdown>
            </Menu>
          </Group>
        );
      }
    }
  ];

  return (
    <Layout user={user || mockUser} notifications={0} onLogout={() => {}}>
      <Container size="xl" py="xl">
        <Stack gap="lg">
          {/* Header */}
          <Group justify="space-between">
            <div>
              <Title order={2}>Human Resources Management</Title>
              <Text c="dimmed" size="sm">
                Manage staff members, departments, and HR operations
              </Text>
            </div>
            <Button
              leftSection={<IconPlus size={16} />}
              onClick={handleNewStaff}
            >
              Add Staff Member
            </Button>
          </Group>

          {/* Statistics Cards */}
          <SimpleGrid cols={{ base: 1, sm: 2, md: 4 }}>
            <Card withBorder padding="lg">
              <Group justify="space-between">
                <div>
                  <Text c="dimmed" size="xs" tt="uppercase" fw={700}>
                    Total Staff
                  </Text>
                  <Text fw={700} size="xl">
                    {stats?.staff?.total || 0}
                  </Text>
                </div>
                <IconUsers size={32} color="#228be6" />
              </Group>
            </Card>

            <Card withBorder padding="lg">
              <Group justify="space-between">
                <div>
                  <Text c="dimmed" size="xs" tt="uppercase" fw={700}>
                    Active Staff
                  </Text>
                  <Text fw={700} size="xl" c="green">
                    {stats?.staff?.active || 0}
                  </Text>
                </div>
                <IconUserCheck size={32} color="#40c057" />
              </Group>
            </Card>

            <Card withBorder padding="lg">
              <Group justify="space-between">
                <div>
                  <Text c="dimmed" size="xs" tt="uppercase" fw={700}>
                    Inactive Staff
                  </Text>
                  <Text fw={700} size="xl" c="red">
                    {stats?.staff?.inactive || 0}
                  </Text>
                </div>
                <IconUserX size={32} color="#fa5252" />
              </Group>
            </Card>

            <Card withBorder padding="lg">
              <Group justify="space-between">
                <div>
                  <Text c="dimmed" size="xs" tt="uppercase" fw={700}>
                    Departments
                  </Text>
                  <Text fw={700} size="xl">
                    {stats?.departments?.total || 0}
                  </Text>
                </div>
                <IconBuilding size={32} color="#fab005" />
              </Group>
            </Card>
          </SimpleGrid>

          {/* Filters */}
          <Paper withBorder p="md">
            <Grid>
              <Grid.Col span={{ base: 12, sm: 6, md: 3 }}>
                <TextInput
                  placeholder="Search staff..."
                  leftSection={<IconSearch size={16} />}
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                />
              </Grid.Col>
              <Grid.Col span={{ base: 12, sm: 6, md: 3 }}>
                <Select
                  placeholder="Filter by department"
                  data={[
                    { value: '', label: 'All Departments' },
                    ...departments.map(dept => ({
                      value: dept.id,
                      label: dept.name,
                    })),
                  ]}
                  value={departmentFilter}
                  onChange={(value) => setDepartmentFilter(value || '')}
                  clearable
                />
              </Grid.Col>
              <Grid.Col span={{ base: 12, sm: 6, md: 3 }}>
                <Select
                  placeholder="Filter by designation"
                  data={[
                    { value: '', label: 'All Designations' },
                    { value: 'DOCTOR', label: 'Doctor' },
                    { value: 'NURSE', label: 'Nurse' },
                    { value: 'RECEPTIONIST', label: 'Receptionist' },
                    { value: 'PHARMACIST', label: 'Pharmacist' },
                    { value: 'LAB_TECHNICIAN', label: 'Lab Technician' },
                    { value: 'RADIOLOGIST', label: 'Radiologist' },
                    { value: 'ACCOUNTANT', label: 'Accountant' },
                    { value: 'MANAGER', label: 'Manager' },
                    { value: 'ADMIN', label: 'Admin' },
                    { value: 'OTHER', label: 'Other' },
                  ]}
                  value={designationFilter}
                  onChange={(value) => setDesignationFilter(value || '')}
                  clearable
                />
              </Grid.Col>
              <Grid.Col span={{ base: 12, sm: 6, md: 3 }}>
                <Select
                  placeholder="Filter by status"
                  data={[
                    { value: '', label: 'All Status' },
                    { value: 'true', label: 'Active' },
                    { value: 'false', label: 'Inactive' },
                  ]}
                  value={statusFilter}
                  onChange={(value) => setStatusFilter(value || '')}
                  clearable
                />
              </Grid.Col>
            </Grid>
            <Group justify="flex-end" mt="md">
              <Button onClick={fetchStaff}>
                Apply Filters
              </Button>
            </Group>
          </Paper>

          {/* Staff Table */}
          <Paper withBorder>
            <LoadingOverlay visible={loading} />
            {staff.length === 0 && !loading ? (
              <Alert icon={<IconAlertCircle size={16} />} title="No staff members found" color="blue">
                No staff members match your current filters. Try adjusting your search criteria.
              </Alert>
            ) : (
              <DataTable
                columns={staffColumns}
                data={staff}
                loading={loading}
              />
            )}
          </Paper>
        </Stack>
      </Container>

      {/* Staff Form Modal */}
      <StaffForm
        opened={staffFormOpened}
        onClose={closeStaffForm}
        staff={selectedStaff}
        onSubmit={selectedStaff ? handleUpdateStaff : handleCreateStaff}
      />

      {/* Staff Details Modal */}
      {selectedStaff && (
        <StaffDetails
          opened={detailsOpened}
          onClose={closeDetails}
          staff={selectedStaff}
          onEdit={handleEditStaff}
          onDelete={handleDeleteStaff}
        />
      )}
    </Layout>
  );
}

export default HrPage;

'use client';

import React, { useState, useEffect } from 'react';
import {
  Container,
  Grid,
  Paper,
  Text,
  Group,
  Badge,
  SimpleGrid,
  Stack,
  Button,
  Title,
  Card,
  TextInput,
  Select,
  LoadingOverlay,
  Alert,
  ActionIcon,
  Menu,
} from '@mantine/core';
import { useDisclosure } from '@mantine/hooks';
import { notifications } from '@mantine/notifications';
import {
  IconPlus,
  IconSearch,
  IconFileText,
  IconCheck,
  IconClock,
  IconCurrency,
  IconEdit,
  IconEye,
  IconDotsVertical,
  IconAlertCircle,
} from '@tabler/icons-react';
import Layout from '../../components/shared/Layout';
import DataTable from '../../components/shared/DataTable';
import InsuranceClaimForm from '../../components/insurance/InsuranceClaimForm';
import InsuranceClaimDetails from '../../components/insurance/InsuranceClaimDetails';
import { useAppStore } from '../../stores/appStore';
import { User, UserRole, TableColumn } from '../../types/common';
import insuranceService from '../../services/insurance.service';
import type { CreateInsuranceClaimDto, UpdateInsuranceClaimDto, InsuranceFilters } from '../../services/insurance.service';

const mockUser: User = {
  id: '1',
  username: 'admin',
  email: 'admin@hospital.com',
  firstName: 'Admin',
  lastName: 'User',
  role: UserRole.ADMIN,
  permissions: [],
  isActive: true,
  tenantInfo: {
    tenantId: 'T001',
    tenantName: 'Main Hospital',
  },
  createdAt: new Date(),
  updatedAt: new Date(),
};

function InsurancePage() {
  const { user, setUser } = useAppStore();
  const [claims, setClaims] = useState<any[]>([]);
  const [stats, setStats] = useState<any>(null);
  const [loading, setLoading] = useState(true);
  const [selectedClaim, setSelectedClaim] = useState<any>(null);
  const [searchQuery, setSearchQuery] = useState('');
  const [statusFilter, setStatusFilter] = useState('');

  const [claimFormOpened, { open: openClaimForm, close: closeClaimForm }] = useDisclosure(false);
  const [detailsOpened, { open: openDetails, close: closeDetails }] = useDisclosure(false);

  useEffect(() => {
    if (!user) {
      setUser(mockUser);
    }
    fetchClaims();
    fetchStats();
  }, [user, setUser]);

  const fetchClaims = async () => {
    setLoading(true);
    try {
      const filters: InsuranceFilters = {
        page: 1,
        limit: 100,
      };
      if (statusFilter) filters.status = statusFilter;

      const response = await insuranceService.getClaims(filters);
      if (response.success && response.data) {
        let filteredClaims = response.data.items;

        // Apply search filter
        if (searchQuery) {
          filteredClaims = filteredClaims.filter(c =>
            `${c.patient?.firstName} ${c.patient?.lastName}`.toLowerCase().includes(searchQuery.toLowerCase()) ||
            c.policyNumber.toLowerCase().includes(searchQuery.toLowerCase()) ||
            (c.claimNumber && c.claimNumber.toLowerCase().includes(searchQuery.toLowerCase())) ||
            c.insuranceProvider.toLowerCase().includes(searchQuery.toLowerCase())
          );
        }

        setClaims(filteredClaims);
      }
    } catch (error: any) {
      console.error('Error fetching claims:', error);
      notifications.show({
        title: 'Error Loading Claims',
        message: error?.response?.data?.message || error?.message || 'Failed to fetch insurance claims. Please try again.',
        color: 'red',
        autoClose: 5000,
      });
    } finally {
      setLoading(false);
    }
  };

  const fetchStats = async () => {
    try {
      const response = await insuranceService.getStats();
      if (response.success && response.data) {
        setStats(response.data);
      }
    } catch (error: any) {
      console.error('Error fetching stats:', error);
      notifications.show({
        title: 'Error Loading Statistics',
        message: error?.response?.data?.message || error?.message || 'Failed to fetch insurance statistics. Please try again.',
        color: 'red',
        autoClose: 5000,
      });
    }
  };

  const handleCreateClaim = async (data: CreateInsuranceClaimDto) => {
    try {
      const response = await insuranceService.createClaim(data);
      
      if (response.success) {
        notifications.show({
          title: 'Success',
          message: 'Insurance claim created successfully',
          color: 'green',
          autoClose: 3000,
        });
        
        closeClaimForm();
        fetchClaims();
        fetchStats();
      }
    } catch (error: any) {
      console.error('Error creating claim:', error);
      notifications.show({
        title: 'Error Creating Claim',
        message: error?.response?.data?.message || error?.message || 'Failed to create insurance claim. Please try again.',
        color: 'red',
        autoClose: 5000,
      });
      throw error;
    }
  };

  const handleUpdateClaim = async (data: UpdateInsuranceClaimDto) => {
    if (!selectedClaim) return;

    try {
      const response = await insuranceService.updateClaim(selectedClaim.id, data);
      
      if (response.success) {
        notifications.show({
          title: 'Success',
          message: 'Insurance claim updated successfully',
          color: 'green',
          autoClose: 3000,
        });
        
        closeClaimForm();
        setSelectedClaim(null);
        fetchClaims();
        fetchStats();
      }
    } catch (error: any) {
      console.error('Error updating claim:', error);
      notifications.show({
        title: 'Error Updating Claim',
        message: error?.response?.data?.message || error?.message || 'Failed to update insurance claim. Please try again.',
        color: 'red',
        autoClose: 5000,
      });
      throw error;
    }
  };

  const handleStatusChange = async (claim: any, status: string) => {
    try {
      const response = await insuranceService.updateClaimStatus(claim.id, status as any);
      
      if (response.success) {
        notifications.show({
          title: 'Success',
          message: 'Claim status updated successfully',
          color: 'green',
          autoClose: 3000,
        });
        
        fetchClaims();
        fetchStats();
      }
    } catch (error: any) {
      console.error('Error updating status:', error);
      notifications.show({
        title: 'Error Updating Status',
        message: error?.response?.data?.message || error?.message || 'Failed to update claim status. Please try again.',
        color: 'red',
        autoClose: 5000,
      });
    }
  };

  const handleViewClaim = (claim: any) => {
    setSelectedClaim(claim);
    openDetails();
  };

  const handleEditClaim = (claim: any) => {
    setSelectedClaim(claim);
    openClaimForm();
  };

  const handleNewClaim = () => {
    setSelectedClaim(null);
    openClaimForm();
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'SUBMITTED':
        return 'blue';
      case 'UNDER_REVIEW':
        return 'yellow';
      case 'APPROVED':
        return 'green';
      case 'REJECTED':
        return 'red';
      case 'PAID':
        return 'teal';
      default:
        return 'gray';
    }
  };

  const formatCurrency = (amount: number) => {
    return `‚Çπ${amount.toLocaleString('en-IN')}`;
  };

  const formatDate = (dateString: string) => {
    const date = new Date(dateString);
    return date.toLocaleDateString();
  };

  const formatProvider = (provider: string) => {
    return provider.replace(/_/g, ' ').replace(/\b\w/g, l => l.toUpperCase());
  };

  const claimColumns: TableColumn[] = [
    {
      key: 'claimNumber',
      title: 'Claim #',
      sortable: true,
      render: (value: unknown, record: Record<string, unknown>) => {
        const claim = record as any;
        return (
          <Text fw={500}>{claim.claimNumber || 'N/A'}</Text>
        );
      }
    },
    {
      key: 'patient',
      title: 'Patient',
      sortable: true,
      render: (value: unknown, record: Record<string, unknown>) => {
        const claim = record as any;
        return (
          <div>
            <Text fw={600}>{claim.patient?.firstName} {claim.patient?.lastName}</Text>
            <Text size="xs" c="dimmed">{claim.policyNumber}</Text>
          </div>
        );
      }
    },
    {
      key: 'insuranceProvider',
      title: 'Provider',
      sortable: true,
      render: (value: unknown, record: Record<string, unknown>) => {
        const claim = record as any;
        return (
          <Text>{formatProvider(claim.insuranceProvider)}</Text>
        );
      }
    },
    {
      key: 'amount',
      title: 'Amount',
      sortable: true,
      render: (value: unknown, record: Record<string, unknown>) => {
        const claim = record as any;
        return (
          <Text fw={600} c="blue">
            {formatCurrency(claim.amount)}
          </Text>
        );
      }
    },
    {
      key: 'status',
      title: 'Status',
      sortable: true,
      render: (value: unknown, record: Record<string, unknown>) => {
        const claim = record as any;
        return (
          <Badge color={getStatusColor(claim.status)}>
            {claim.status.replace(/_/g, ' ')}
          </Badge>
        );
      }
    },
    {
      key: 'submittedAt',
      title: 'Submitted',
      sortable: true,
      render: (value: unknown, record: Record<string, unknown>) => {
        const claim = record as any;
        return (
          <Text size="sm">{formatDate(claim.submittedAt)}</Text>
        );
      }
    },
    {
      key: 'actions',
      title: 'Actions',
      render: (value: unknown, record: Record<string, unknown>) => {
        const claim = record as any;
        return (
          <Group gap="xs">
            <ActionIcon
              variant="subtle"
              onClick={() => handleViewClaim(claim)}
            >
              <IconEye size={16} />
            </ActionIcon>
            <ActionIcon
              variant="subtle"
              onClick={() => handleEditClaim(claim)}
            >
              <IconEdit size={16} />
            </ActionIcon>
            <Menu position="bottom-end">
              <Menu.Target>
                <ActionIcon variant="subtle">
                  <IconDotsVertical size={16} />
                </ActionIcon>
              </Menu.Target>
              <Menu.Dropdown>
                <Menu.Item
                  leftSection={<IconEye size={14} />}
                  onClick={() => handleViewClaim(claim)}
                >
                  View Details
                </Menu.Item>
                <Menu.Item
                  leftSection={<IconEdit size={14} />}
                  onClick={() => handleEditClaim(claim)}
                >
                  Edit Claim
                </Menu.Item>
              </Menu.Dropdown>
            </Menu>
          </Group>
        );
      }
    }
  ];

  const layoutUser = user || [];
  const userForLayout = {
    id: layoutUser.id,
    name: `${layoutUser.firstName} ${layoutUser.lastName}`,
    email: layoutUser.email,
    role: layoutUser.role,
  };

  return (
    <Layout user={userForLayout} notifications={0} onLogout={() => {}}>
      <Container size="xl" py="xl">
        <Stack gap="lg">
          {/* Header */}
          <Group justify="space-between">
            <div>
              <Title order={2}>Insurance Management</Title>
              <Text c="dimmed" size="sm">
                Manage insurance claims and track reimbursements
              </Text>
            </div>
            <Button
              leftSection={<IconPlus size={16} />}
              onClick={handleNewClaim}
            >
              New Claim
            </Button>
          </Group>

          {/* Statistics Cards */}
          <SimpleGrid cols={{ base: 1, sm: 2, md: 5 }}>
            <Card withBorder padding="lg">
              <Group justify="space-between">
                <div>
                  <Text c="dimmed" size="xs" tt="uppercase" fw={700}>
                    Total Claims
                  </Text>
                  <Text fw={700} size="xl">
                    {stats?.total || 0}
                  </Text>
                </div>
                <IconFileText size={32} color="#228be6" />
              </Group>
            </Card>

            <Card withBorder padding="lg">
              <Group justify="space-between">
                <div>
                  <Text c="dimmed" size="xs" tt="uppercase" fw={700}>
                    Submitted
                  </Text>
                  <Text fw={700} size="xl" c="blue">
                    {stats?.submitted || 0}
                  </Text>
                </div>
                <IconClock size={32} color="#228be6" />
              </Group>
            </Card>

            <Card withBorder padding="lg">
              <Group justify="space-between">
                <div>
                  <Text c="dimmed" size="xs" tt="uppercase" fw={700}>
                    Approved
                  </Text>
                  <Text fw={700} size="xl" c="green">
                    {stats?.approved || 0}
                  </Text>
                </div>
                <IconCheck size={32} color="#40c057" />
              </Group>
            </Card>

            <Card withBorder padding="lg">
              <Group justify="space-between">
                <div>
                  <Text c="dimmed" size="xs" tt="uppercase" fw={700}>
                    Paid
                  </Text>
                  <Text fw={700} size="xl" c="teal">
                    {stats?.paid || 0}
                  </Text>
                </div>
                <IconCheck size={32} color="#20c997" />
              </Group>
            </Card>

            <Card withBorder padding="lg">
              <Group justify="space-between">
                <div>
                  <Text c="dimmed" size="xs" tt="uppercase" fw={700}>
                    Total Paid
                  </Text>
                  <Text fw={700} size="xl" c="teal">
                    {formatCurrency(stats?.totalAmount || 0)}
                  </Text>
                </div>
                <IconCurrency size={32} color="#20c997" />
              </Group>
            </Card>
          </SimpleGrid>

          {/* Filters */}
          <Paper withBorder p="md">
            <Grid>
              <Grid.Col span={{ base: 12, sm: 6, md: 4 }}>
                <TextInput
                  placeholder="Search claims..."
                  leftSection={<IconSearch size={16} />}
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                />
              </Grid.Col>
              <Grid.Col span={{ base: 12, sm: 6, md: 4 }}>
                <Select
                  placeholder="Filter by status"
                  data={[
                    { value: '', label: 'All Status' },
                    { value: 'SUBMITTED', label: 'Submitted' },
                    { value: 'UNDER_REVIEW', label: 'Under Review' },
                    { value: 'APPROVED', label: 'Approved' },
                    { value: 'REJECTED', label: 'Rejected' },
                    { value: 'PAID', label: 'Paid' },
                  ]}
                  value={statusFilter}
                  onChange={(value) => setStatusFilter(value || '')}
                  clearable
                />
              </Grid.Col>
              <Grid.Col span={{ base: 12, sm: 6, md: 4 }}>
                <Button fullWidth onClick={fetchClaims}>
                  Apply Filters
                </Button>
              </Grid.Col>
            </Grid>
          </Paper>

          {/* Claims Table */}
          <Paper withBorder>
            <LoadingOverlay visible={loading} />
            {claims.length === 0 && !loading ? (
              <Alert icon={<IconAlertCircle size={16} />} title="No claims found" color="blue">
                No insurance claims match your current filters. Try adjusting your search criteria.
              </Alert>
            ) : (
              <DataTable
                columns={claimColumns}
                data={claims}
                loading={loading}
              />
            )}
          </Paper>
        </Stack>
      </Container>

      {/* Claim Form Modal */}
      <InsuranceClaimForm
        opened={claimFormOpened}
        onClose={closeClaimForm}
        claim={selectedClaim}
        onSubmit={selectedClaim ? handleUpdateClaim : handleCreateClaim}
      />

      {/* Claim Details Modal */}
      {selectedClaim && (
        <InsuranceClaimDetails
          opened={detailsOpened}
          onClose={closeDetails}
          claim={selectedClaim}
          onEdit={handleEditClaim}
          onStatusChange={handleStatusChange}
        />
      )}
    </Layout>
  );
}

