              <div>
                <strong>Current Session</strong>
                <br />
                Chrome on Windows ‚Ä¢ New York, NY
              </div>
              <span style={{ color: '#10b981', fontSize: '0.75rem' }}>Active Now</span>
            </div>
          </div>
          <Button variant="outline" size="sm">
            Manage Sessions
          </Button>
        </div>

        <div style={{
          padding: '1.5rem',
          border: '1px solid #fee2e2',
          borderRadius: '8px',
          backgroundColor: '#fef2f2'
        }}>
          <h4 style={{ margin: '0 0 1rem 0', fontSize: '1rem', fontWeight: '600', color: '#dc2626' }}>
            Danger Zone
          </h4>
          <p style={{ margin: '0 0 1rem 0', fontSize: '0.875rem', color: '#6b7280' }}>
            These actions are irreversible and will permanently affect your account.
          </p>
          <div style={{ display: 'flex', gap: '1rem' }}>
            <Button variant="outline" size="sm">
              Deactivate Account
            </Button>
            <Button variant="outline" size="sm" style={{ color: '#dc2626', borderColor: '#dc2626' }}>
              Delete Account
            </Button>
          </div>
        </div>
      </div>
    </Card>
  );

  return (
    <Layout>
      <div style={{ maxWidth: '1200px', margin: '0 auto' }}>
        {/* Header */}
        <div style={{ 
          display: 'flex', 
          justifyContent: 'space-between', 
          alignItems: 'center', 
          marginBottom: '2rem' 
        }}>
          <div>
            <h1 style={{ fontSize: '2rem', fontWeight: 'bold', color: '#1f2937', marginBottom: '0.5rem' }}>
              Profile Settings
            </h1>
            <p style={{ color: '#6b7280', fontSize: '1rem' }}>
              Manage your account information and preferences
            </p>
          </div>
          {currentTab === 'profile' && !isEditing && (
            <Button onClick={() => setIsEditing(true)}>
              ‚úèÔ∏è Edit Profile
            </Button>
          )}
        </div>

        {/* Profile Overview Card */}
        <Card style={{ marginBottom: '2rem' }}>
          <div style={{ display: 'flex', alignItems: 'center', gap: '1.5rem' }}>
            <div style={{
              width: '80px',
              height: '80px',
              borderRadius: '50%',
              backgroundColor: '#667eea',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              fontSize: '2rem',
              color: 'white',
              fontWeight: 'bold'
            }}>
              {profile.firstName.charAt(0)}{profile.lastName.charAt(0)}
            </div>
            
            <div style={{ flex: 1 }}>
              <h2 style={{ 
                fontSize: '1.5rem', 
                fontWeight: '600', 
                color: '#1f2937', 
                marginBottom: '0.5rem' 
              }}>
                {profile.firstName} {profile.lastName}
              </h2>
              
              <div style={{ display: 'flex', alignItems: 'center', gap: '1rem', marginBottom: '0.5rem' }}>
                <span style={{
                  padding: '0.25rem 0.75rem',
                  backgroundColor: `${getRoleColor(profile.role)}15`,
                  color: getRoleColor(profile.role),
                  borderRadius: '12px',
                  fontSize: '0.75rem',
                  fontWeight: '600'
                }}>
                  {profile.role}
                </span>
                <span style={{ fontSize: '0.875rem', color: '#6b7280' }}>
                  {profile.department}
                </span>
              </div>
              
              <div style={{ display: 'flex', alignItems: 'center', gap: '2rem', fontSize: '0.875rem', color: '#6b7280' }}>
                <span>üìß {profile.email}</span>
                <span>üìû {profile.phone}</span>
                <span>üìÖ Joined {new Date(profile.joinedDate).toLocaleDateString()}</span>
              </div>
            </div>
          </div>
        </Card>

        {/* Tab Navigation */}
        <div style={{ marginBottom: '2rem' }}>
          <div style={{ display: 'flex', gap: '0.5rem', borderBottom: '1px solid #e5e7eb' }}>
            {[
              { key: 'profile', label: 'üë§ Profile', desc: 'Personal Information' },
              { key: 'password', label: 'üîí Password', desc: 'Security Settings' },
              { key: 'notifications', label: 'üîî Notifications', desc: 'Preferences' },
              { key: 'security', label: 'üõ°Ô∏è Security', desc: 'Account Security' }
            ].map(tab => (
              <button
                key={tab.key}
                onClick={() => setCurrentTab(tab.key as string)}
                style={{
                  padding: '1rem 1.5rem',
                  border: 'none',
                  background: 'none',
                  fontSize: '1rem',
                  fontWeight: '500',
                  color: currentTab === tab.key ? '#667eea' : '#6b7280',
                  borderBottom: currentTab === tab.key ? '2px solid #667eea' : '2px solid transparent',
                  cursor: 'pointer',
                  transition: 'all 0.2s ease'
                }}
              >
                <div>
                  {tab.label}
                  <div style={{ fontSize: '0.75rem', opacity: 0.8 }}>
                    {tab.desc}
                  </div>
                </div>
              </button>
            ))}
          </div>
        </div>

        {/* Tab Content */}
        {currentTab === 'profile' && (
          <Card>
            <ProfileForm />
          </Card>
        )}
        
        {currentTab === 'password' && <PasswordForm />}
        {currentTab === 'notifications' && <NotificationsForm />}
        {currentTab === 'security' && <SecuritySettings />}
      </div>
    </Layout>
  );
};

