              <Button variant="outline">üìß Send to Physician</Button>
              <Button variant="secondary" onClick={() => setShowReportModal(false)}>
                Close
              </Button>
            </div>
          </div>
        )}
      </div>
    </div>
  );

  return (
    <Layout>
      <div style={{ maxWidth: '1400px', margin: '0 auto' }}>
        {/* Header */}
        <div style={{ 
          display: 'flex', 
          justifyContent: 'space-between', 
          alignItems: 'center', 
          marginBottom: '2rem' 
        }}>
          <div>
            <h1 style={{ fontSize: '2rem', fontWeight: 'bold', color: '#1f2937', marginBottom: '0.5rem' }}>
              Radiology & Imaging
            </h1>
            <p style={{ color: '#6b7280', fontSize: '1rem' }}>
              Manage imaging orders, reports, and radiology workflow
            </p>
          </div>
          <div style={{ display: 'flex', gap: '0.5rem' }}>
            <Button variant="primary">
              ‚ûï New Order
            </Button>
            <Button variant="outline">
              üìä Statistics
            </Button>
          </div>
        </div>

        {/* Tab Navigation */}
        <div style={{ marginBottom: '2rem' }}>
          <div style={{ display: 'flex', gap: '0.5rem', borderBottom: '1px solid #e5e7eb' }}>
            {[
              { key: 'orders', label: 'üìã Orders', desc: 'All Orders' },
              { key: 'worklist', label: '‚ö° Worklist', desc: 'Today\'s Schedule' },
              { key: 'reports', label: 'üìÑ Reports', desc: 'Radiology Reports' },
              { key: 'schedule', label: 'üìÖ Schedule', desc: 'Equipment Calendar' },
              { key: 'equipment', label: 'üè• Equipment', desc: 'Machine Status' }
            ].map(tab => (
              <button
                key={tab.key}
                onClick={() => setCurrentTab(tab.key as string)}
                style={{
                  padding: '1rem 1.5rem',
                  border: 'none',
                  background: 'none',
                  fontSize: '1rem',
                  fontWeight: '500',
                  color: currentTab === tab.key ? '#667eea' : '#6b7280',
                  borderBottom: currentTab === tab.key ? '2px solid #667eea' : '2px solid transparent',
                  cursor: 'pointer',
                  transition: 'all 0.2s ease'
                }}
              >
                <div>{tab.label}</div>
                <div style={{ fontSize: '0.75rem', opacity: 0.8 }}>
                  {tab.desc}
                </div>
              </button>
            ))}
          </div>
        </div>

        {/* Orders Tab */}
        {currentTab === 'orders' && (
          <>
            {/* Search and Filters */}
            <Card style={{ marginBottom: '1.5rem' }}>
              <div style={{ display: 'flex', gap: '1rem', alignItems: 'center', flexWrap: 'wrap' }}>
                <div style={{ flex: 1, minWidth: '250px' }}>
                  <Input
                    placeholder="Search by patient name, order ID, or exam type..."
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                  />
                </div>
                
                <select
                  value={statusFilter}
                  onChange={(e) => setStatusFilter(e.target.value)}
                  style={{
                    padding: '0.75rem 1rem',
                    border: '1px solid #d1d5db',
                    borderRadius: '8px',
                    fontSize: '1rem',
                    backgroundColor: 'white',
                    minWidth: '150px'
                  }}
                >
                  <option value="ALL">All Status</option>
                  <option value="SCHEDULED">Scheduled</option>
                  <option value="IN_PROGRESS">In Progress</option>
                  <option value="COMPLETED">Completed</option>
                  <option value="REPORTED">Reported</option>
                  <option value="CANCELLED">Cancelled</option>
                </select>

                <select
                  value={urgencyFilter}
                  onChange={(e) => setUrgencyFilter(e.target.value)}
                  style={{
                    padding: '0.75rem 1rem',
                    border: '1px solid #d1d5db',
                    borderRadius: '8px',
                    fontSize: '1rem',
                    backgroundColor: 'white',
                    minWidth: '150px'
                  }}
                >
                  <option value="ALL">All Urgency</option>
                  <option value="STAT">STAT</option>
                  <option value="URGENT">Urgent</option>
                  <option value="ROUTINE">Routine</option>
                </select>

                <Button variant="outline">
                  üîÑ Refresh
                </Button>
              </div>
            </Card>

            {/* Orders List */}
            <div style={{ display: 'grid', gap: '1rem' }}>
              {filteredOrders.map(order => (
                <Card key={order.id} style={{ cursor: 'pointer' }}>
                  <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start' }}>
                    <div style={{ flex: 1 }}>
                      <div style={{ display: 'flex', gap: '1rem', alignItems: 'center', marginBottom: '0.75rem' }}>
                        <h3 style={{ fontSize: '1.25rem', fontWeight: '600', color: '#1f2937', margin: 0 }}>
                          {order.patientName}
                        </h3>
                        <span style={{
                          padding: '0.25rem 0.75rem',
                          borderRadius: '9999px',
                          fontSize: '0.75rem',
                          fontWeight: '600',
                          color: 'white',
                          backgroundColor: getUrgencyColor(order.urgency)
                        }}>
                          {order.urgency}
                        </span>
                        <span style={{
                          padding: '0.25rem 0.75rem',
                          borderRadius: '9999px',
                          fontSize: '0.75rem',
                          fontWeight: '600',
                          color: 'white',
                          backgroundColor: getStatusColor(order.status)
                        }}>
                          {order.status.replace('_', ' ')}
                        </span>
                      </div>
                      
                      <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))', gap: '0.5rem', marginBottom: '0.75rem' }}>
                        <div style={{ color: '#6b7280' }}>
                          <span style={{ fontWeight: '600' }}>Order ID:</span> {order.orderId}
                        </div>
                        <div style={{ color: '#6b7280' }}>
                          <span style={{ fontWeight: '600' }}>Exam:</span> {order.examType}
                        </div>
                        <div style={{ color: '#6b7280' }}>
                          <span style={{ fontWeight: '600' }}>Scheduled:</span> {new Date(order.scheduledDate).toLocaleString()}
                        </div>
                        <div style={{ color: '#6b7280' }}>
                          <span style={{ fontWeight: '600' }}>Physician:</span> {order.orderingPhysician}
                        </div>
                      </div>
                      
                      <p style={{ color: '#6b7280', fontSize: '0.875rem', margin: 0 }}>
                        {order.notes}
                      </p>
                    </div>
                    
                    <div style={{ display: 'flex', gap: '0.5rem', marginLeft: '1rem' }}>
                      <Button
                        size="sm"
                        variant="outline"
                        onClick={() => {
                          setSelectedOrder(order);
                          setShowOrderModal(true);
                        }}
                      >
                        View Details
                      </Button>
                      {order.status === 'REPORTED' && (
                        <Button
                          size="sm"
                          variant="primary"
                          onClick={() => {
                            const report = [].find /* TODO: API */(r => r.orderId === order.orderId);
                            if (report) {
                              setSelectedReport(report);
                              setShowReportModal(true);
                            }
                          }}
                        >
                          View Report
                        </Button>
                      )}
                    </div>
                  </div>
                </Card>
              ))}
            </div>
          </>
        )}

        {/* Worklist Tab */}
        {currentTab === 'worklist' && (
          <Card>
            <div style={{ textAlign: 'center', padding: '2rem' }}>
              <div style={{ fontSize: '3rem', marginBottom: '1rem' }}>‚ö°</div>
              <h3 style={{ fontSize: '1.25rem', fontWeight: '600', color: '#1f2937', marginBottom: '0.5rem' }}>
                Today&apos;s Imaging Worklist
              </h3>
              <p style={{ color: '#6b7280', marginBottom: '1rem' }}>
                View and manage today&apos;s scheduled imaging exams by technician and equipment. This interface provides a streamlined workflow for radiology staff.
              </p>
              <div style={{ display: 'flex', gap: '1rem', justifyContent: 'center' }}>
                <Button variant="primary">
                  üìã View Worklist
                </Button>
                <Button variant="outline">
                  ‚è∞ Schedule Management
                </Button>
              </div>
            </div>
          </Card>
        )}

        {/* Reports Tab */}
        {currentTab === 'reports' && (
          <>
            <div style={{ display: 'grid', gap: '1rem' }}>
              {[].map /* TODO: API */(report => (
                <Card key={report.id} style={{ cursor: 'pointer' }}>
                  <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start' }}>
                    <div style={{ flex: 1 }}>
                      <div style={{ display: 'flex', gap: '1rem', alignItems: 'center', marginBottom: '0.75rem' }}>
                        <h3 style={{ fontSize: '1.25rem', fontWeight: '600', color: '#1f2937', margin: 0 }}>
                          {report.patientName}
                        </h3>
                        <span style={{
                          padding: '0.25rem 0.75rem',
                          borderRadius: '9999px',
                          fontSize: '0.75rem',
                          fontWeight: '600',
                          color: 'white',
                          backgroundColor: report.status === 'FINAL' ? '#10b981' : '#f59e0b'
                        }}>
                          {report.status}
                        </span>
                        {report.criticalFindings && (
                          <span style={{
                            padding: '0.25rem 0.75rem',
                            borderRadius: '9999px',
                            fontSize: '0.75rem',
                            fontWeight: '600',
                            color: 'white',
                            backgroundColor: '#ef4444'
                          }}>
                            CRITICAL
                          </span>
                        )}
                      </div>
                      
                      <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))', gap: '0.5rem', marginBottom: '0.75rem' }}>
                        <div style={{ color: '#6b7280' }}>
                          <span style={{ fontWeight: '600' }}>Exam:</span> {report.examType}
                        </div>
                        <div style={{ color: '#6b7280' }}>
                          <span style={{ fontWeight: '600' }}>Exam Date:</span> {new Date(report.examDate).toLocaleDateString()}
                        </div>
                        <div style={{ color: '#6b7280' }}>
                          <span style={{ fontWeight: '600' }}>Radiologist:</span> {report.radiologist}
                        </div>
                        <div style={{ color: '#6b7280' }}>
                          <span style={{ fontWeight: '600' }}>Images:</span> {report.images}
                        </div>
                      </div>
                      
                      <p style={{ color: '#6b7280', fontSize: '0.875rem', margin: 0 }}>
                        {report.impression.substring(0, 150)}...
                      </p>
                    </div>
                    
                    <div style={{ display: 'flex', gap: '0.5rem', marginLeft: '1rem' }}>
                      <Button
                        size="sm"
                        variant="primary"
                        onClick={() => {
                          setSelectedReport(report);
                          setShowReportModal(true);
                        }}
                      >
                        View Report
                      </Button>
                    </div>
                  </div>
                </Card>
              ))}
            </div>
          </>
        )}

        {/* Schedule Tab */}
        {currentTab === 'schedule' && (
          <Card>
            <div style={{ textAlign: 'center', padding: '2rem' }}>
              <div style={{ fontSize: '3rem', marginBottom: '1rem' }}>üìÖ</div>
              <h3 style={{ fontSize: '1.25rem', fontWeight: '600', color: '#1f2937', marginBottom: '0.5rem' }}>
                Equipment Schedule & Calendar
              </h3>
              <p style={{ color: '#6b7280', marginBottom: '1rem' }}>
                View and manage equipment schedules, maintenance windows, and availability. Coordinate imaging appointments across multiple modalities.
              </p>
              <div style={{ display: 'flex', gap: '1rem', justifyContent: 'center' }}>
                <Button variant="primary">
                  üìÖ View Calendar
                </Button>
                <Button variant="outline">
                  üîß Maintenance Schedule
                </Button>
              </div>
            </div>
          </Card>
        )}

        {/* Equipment Tab */}
        {currentTab === 'equipment' && (
          <Card>
            <div style={{ textAlign: 'center', padding: '2rem' }}>
              <div style={{ fontSize: '3rem', marginBottom: '1rem' }}>üè•</div>
              <h3 style={{ fontSize: '1.25rem', fontWeight: '600', color: '#1f2937', marginBottom: '0.5rem' }}>
                Imaging Equipment Status
              </h3>
              <p style={{ color: '#6b7280', marginBottom: '1rem' }}>
                Monitor imaging equipment status, utilization, maintenance schedules, and quality control metrics for optimal department operations.
              </p>
              <div style={{ display: 'flex', gap: '1rem', justifyContent: 'center' }}>
                <Button variant="primary">
                  üè• Equipment Dashboard
                </Button>
                <Button variant="outline">
                  üîß Maintenance Logs
                </Button>
                <Button variant="secondary">
                  üìä Utilization Reports
                </Button>
              </div>
            </div>
          </Card>
        )}

        {/* Modals */}
        {showOrderModal && <OrderModal />}
        {showReportModal && <ReportModal />}
      </div>
    </Layout>
  );
};

export default RadiologyPage;

'use client';

import { useState } from 'react';
import { 
  Stepper, Card, Title, Text, Stack, Group, Button, 
  TextInput, Select, Textarea, Radio, Checkbox 
} from '@mantine/core';
import { 
  IconBuilding, IconUser, IconCreditCard, IconCheck 
} from '@tabler/icons-react';

export default function RegisterHospitalPage() {
  const [active, setActive] = useState(0);
  const [formData, setFormData] = useState({
    // Step 1: Hospital Information
    hospitalName: '',
    hospitalType: '',
    registrationNumber: '',
    licenseNumber: '',
    email: '',
    phone: '',
    website: '',
    
    // Step 2: Address
    addressLine1: '',
    addressLine2: '',
    city: '',
    state: '',
    postalCode: '',
    country: '',
    
    // Step 3: Admin User
    adminFirstName: '',
    adminLastName: '',
    adminEmail: '',
    adminPhone: '',
    adminPassword: '',
    confirmPassword: '',
    
    // Step 4: Subscription
    plan: '',
    billingCycle: 'monthly',
    agreeToTerms: false,
  });

  const nextStep = () => setActive((current) => (current < 4 ? current + 1 : current));
  const prevStep = () => setActive((current) => (current > 0 ? current - 1 : current));

  const handleSubmit = async () => {
    try {
      // Step 1: Create tenant
      const tenantPayload = {
        name: formData.hospitalName,
        type: formData.hospitalType,
        email: formData.email,
        phone: formData.phone,
        subscriptionPlan: formData.plan,
        settings: {
          website: formData.website,
          licenseNumber: formData.licenseNumber,
          registrationNumber: formData.registrationNumber,
          address: {
            addressLine1: formData.addressLine1,
            addressLine2: formData.addressLine2,
            city: formData.city,
            state: formData.state,
            postalCode: formData.postalCode,
            country: formData.country,
          }
        }
      };

      const tenantResponse = await fetch('/api/tenants', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(tenantPayload),
      });

      if (!tenantResponse.ok) {
        throw new Error('Failed to create tenant');
      }

      const tenant = await tenantResponse.json();

      // Step 2: Register admin user
      const userPayload = {
        email: formData.adminEmail,
        password: formData.adminPassword,
        firstName: formData.adminFirstName,
        lastName: formData.adminLastName,
        phone: formData.adminPhone,
        tenantId: tenant.id,
        role: 'HOSPITAL_ADMIN'
      };

      const userResponse = await fetch('/api/auth/register', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(userPayload),
      });

      if (!userResponse.ok) {
        throw new Error('Failed to register admin user');
      }

      // Move to completion step
      nextStep();
    } catch (error) {
      console.error('Registration error:', error);
      alert('Registration failed. Please try again.');
    }
  };

  return (
    <div style={{
      minHeight: '100vh',
      background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
      padding: '2rem',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center'
    }}>
      <Card 
        shadow="xl" 
        padding="xl" 
        radius="md" 
        style={{ 
          width: '100%', 
          maxWidth: '900px',
          background: 'rgba(255,255,255,0.98)',
          backdropFilter: 'blur(10px)'
        }}
      >
        {/* Header */}
        <div style={{ textAlign: 'center', marginBottom: '2rem' }}>
          <div style={{ fontSize: '3rem', marginBottom: '1rem' }}>üè•</div>
          <Title order={1} mb="xs">Register Your Hospital</Title>
          <Text c="dimmed">Join HMS SAAS and modernize your healthcare facility</Text>
        </div>

        {/* Stepper */}
        <Stepper active={active} onStepClick={setActive} mb="xl">
          <Stepper.Step 
            label="Hospital Info" 
            description="Basic details"
            icon={<IconBuilding size={18} />}
          >
            <Stack gap="md" mt="xl">
              <TextInput
                label="Hospital/Clinic Name"
                placeholder="Enter your hospital name"
                value={formData.hospitalName}
                onChange={(e) => setFormData({ ...formData, hospitalName: e.target.value })}
                required
              />
              
              <Select
                label="Facility Type"
                placeholder="Select type"
                data={[
                  { value: 'hospital', label: 'Hospital' },
                  { value: 'clinic', label: 'Clinic' },
                  { value: 'diagnostic_center', label: 'Diagnostic Center' },
                  { value: 'pharmacy', label: 'Pharmacy' },
                  { value: 'laboratory', label: 'Laboratory' },
                ]}
                value={formData.hospitalType}
                onChange={(value) => setFormData({ ...formData, hospitalType: value || '' })}
                required
              />

              <Group grow>
                <TextInput
                  label="Registration Number"
                  placeholder="Hospital registration no."
                  value={formData.registrationNumber}
                  onChange={(e) => setFormData({ ...formData, registrationNumber: e.target.value })}
                />
                <TextInput
                  label="License Number"
                  placeholder="Medical license no."
                  value={formData.licenseNumber}
                  onChange={(e) => setFormData({ ...formData, licenseNumber: e.target.value })}
                />
              </Group>

              <Group grow>
                <TextInput
                  label="Email"
                  placeholder="contact@hospital.com"
                  type="email"
                  value={formData.email}
                  onChange={(e) => setFormData({ ...formData, email: e.target.value })}
                  required
                />
                <TextInput
                  label="Phone"
                  placeholder="+1 (555) 123-4567"
                  value={formData.phone}
                  onChange={(e) => setFormData({ ...formData, phone: e.target.value })}
                  required
                />
              </Group>

              <TextInput
                label="Website (Optional)"
                placeholder="https://www.yourhospital.com"
                value={formData.website}
                onChange={(e) => setFormData({ ...formData, website: e.target.value })}
              />
            </Stack>
          </Stepper.Step>

          <Stepper.Step 
            label="Address" 
            description="Location details"
          >
            <Stack gap="md" mt="xl">
              <TextInput
                label="Address Line 1"
                placeholder="Street address"
                value={formData.addressLine1}
                onChange={(e) => setFormData({ ...formData, addressLine1: e.target.value })}
                required
              />

              <TextInput
                label="Address Line 2 (Optional)"
                placeholder="Apartment, suite, etc."
                value={formData.addressLine2}
                onChange={(e) => setFormData({ ...formData, addressLine2: e.target.value })}
              />

              <Group grow>
                <TextInput
                  label="City"
                  placeholder="City"
                  value={formData.city}
                  onChange={(e) => setFormData({ ...formData, city: e.target.value })}
                  required
                />
                <TextInput
                  label="State/Province"
                  placeholder="State"
                  value={formData.state}
                  onChange={(e) => setFormData({ ...formData, state: e.target.value })}
                  required
                />
              </Group>

              <Group grow>
                <TextInput
                  label="Postal Code"
                  placeholder="ZIP/Postal code"
                  value={formData.postalCode}
                  onChange={(e) => setFormData({ ...formData, postalCode: e.target.value })}
                  required
                />
                <Select
                  label="Country"
                  placeholder="Select country"
                  data={[
                    { value: 'US', label: 'United States' },
                    { value: 'UK', label: 'United Kingdom' },
                    { value: 'CA', label: 'Canada' },
                    { value: 'AU', label: 'Australia' },
                    { value: 'IN', label: 'India' },
                  ]}
                  value={formData.country}
                  onChange={(value) => setFormData({ ...formData, country: value || '' })}
                  required
                />
              </Group>
            </Stack>
          </Stepper.Step>

          <Stepper.Step 
            label="Admin User" 
            description="Create account"
            icon={<IconUser size={18} />}
          >
            <Stack gap="md" mt="xl">
              <Text size="sm" c="dimmed">
                Create the main administrator account for your hospital
              </Text>

              <Group grow>
                <TextInput
                  label="First Name"
                  placeholder="John"
                  value={formData.adminFirstName}
                  onChange={(e) => setFormData({ ...formData, adminFirstName: e.target.value })}
                  required
                />
                <TextInput
                  label="Last Name"
                  placeholder="Doe"
                  value={formData.adminLastName}
                  onChange={(e) => setFormData({ ...formData, adminLastName: e.target.value })}
                  required
                />
              </Group>

              <TextInput
                label="Admin Email"
                placeholder="admin@hospital.com"
                type="email"
                value={formData.adminEmail}
                onChange={(e) => setFormData({ ...formData, adminEmail: e.target.value })}
                required
              />

              <TextInput
                label="Phone Number"
                placeholder="+1 (555) 123-4567"
                value={formData.adminPhone}
                onChange={(e) => setFormData({ ...formData, adminPhone: e.target.value })}
                required
              />

              <TextInput
                label="Password"
                placeholder="Create a strong password"
                type="password"
                value={formData.adminPassword}
                onChange={(e) => setFormData({ ...formData, adminPassword: e.target.value })}
                required
              />

              <TextInput
                label="Confirm Password"
                placeholder="Re-enter password"
                type="password"
                value={formData.confirmPassword}
                onChange={(e) => setFormData({ ...formData, confirmPassword: e.target.value })}
                required
              />
            </Stack>
          </Stepper.Step>

          <Stepper.Step 
            label="Subscription" 
            description="Choose plan"
            icon={<IconCreditCard size={18} />}
          >
            <Stack gap="md" mt="xl">
              <Text size="sm" c="dimmed" mb="md">
                Select a subscription plan that fits your needs. You can upgrade or downgrade anytime.
              </Text>

              <Radio.Group
                value={formData.plan}
                onChange={(value) => setFormData({ ...formData, plan: value })}
                label="Select Your Plan"
                required
              >
                <Stack mt="xs" gap="sm">
                  <Card shadow="sm" padding="md" withBorder>
                    <Radio 
                      value="free" 
                      label={
                        <div>
                          <Text fw={600}>Free Trial - $0/month</Text>
                          <Text size="sm" c="dimmed">
                            30 days free ‚Ä¢ Up to 5 users ‚Ä¢ 100 patients ‚Ä¢ Basic features
                          </Text>
                        </div>
                      } 
                    />
                  </Card>

                  <Card shadow="sm" padding="md" withBorder>
                    <Radio 
                      value="basic" 
                      label={
                        <div>
                          <Text fw={600}>Basic - $99/month</Text>
                          <Text size="sm" c="dimmed">
                            Up to 20 users ‚Ä¢ 1,000 patients ‚Ä¢ Core HMS features
                          </Text>
                        </div>
                      } 
                    />
                  </Card>

                  <Card shadow="sm" padding="md" withBorder>
                    <Radio 
                      value="professional" 
                      label={
                        <div>
                          <Text fw={600}>Professional - $299/month</Text>
                          <Text size="sm" c="dimmed">
                            Up to 100 users ‚Ä¢ 10,000 patients ‚Ä¢ Advanced features ‚Ä¢ Priority support
                          </Text>
                        </div>
                      } 
                    />
                  </Card>

                  <Card shadow="sm" padding="md" withBorder>
                    <Radio 
                      value="enterprise" 
                      label={
                        <div>
                          <Text fw={600}>Enterprise - Custom Pricing</Text>
                          <Text size="sm" c="dimmed">
                            Unlimited users ‚Ä¢ Unlimited patients ‚Ä¢ All features ‚Ä¢ Dedicated support
                          </Text>
                        </div>
                      } 
                    />
                  </Card>
                </Stack>
              </Radio.Group>

              <Radio.Group
                value={formData.billingCycle}
                onChange={(value) => setFormData({ ...formData, billingCycle: value })}
                label="Billing Cycle"
                mt="xl"
              >
                <Group mt="xs">
                  <Radio value="monthly" label="Monthly" />
                  <Radio value="yearly" label="Yearly (Save 20%)" />
                </Group>
              </Radio.Group>

              <Checkbox
                mt="xl"
                label="I agree to the Terms of Service and Privacy Policy"
                checked={formData.agreeToTerms}
                onChange={(e) => setFormData({ ...formData, agreeToTerms: e.target.checked })}
                required
              />
            </Stack>
          </Stepper.Step>

          <Stepper.Completed>
            <Stack align="center" gap="md" mt="xl" style={{ textAlign: 'center' }}>
              <div style={{ 
                width: '80px', 
                height: '80px', 
                background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
                borderRadius: '50%',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center'
              }}>
                <IconCheck size={48} color="white" />
              </div>
              <Title order={2}>Registration Complete!</Title>
              <Text c="dimmed">
                Your hospital has been successfully registered. You will receive a confirmation email shortly.
              </Text>
              <Button
                size="lg"
                mt="md"
                style={{ background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)' }}
                onClick={() => window.location.href = '/login'}
              >
                Go to Login
              </Button>
            </Stack>
          </Stepper.Completed>
        </Stepper>

        {/* Navigation Buttons */}
        {active < 4 && (
          <Group justify="space-between" mt="xl">
            <Button variant="default" onClick={prevStep} disabled={active === 0}>
              Back
            </Button>
            <Button 
              onClick={active === 3 ? handleSubmit : nextStep}
              style={{ background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)' }}
            >
              {active === 3 ? 'Complete Registration' : 'Next Step'}
            </Button>
          </Group>
        )}
      </Card>
    </div>
  );
}

'use client';
import Layout from '../components/Layout';
import Card from '../components/ui/Card';
import Button from '../components/ui/Button';
import Input from '../components/ui/Input';
import React, { useState } from 'react';

interface SystemSettings {
  hospitalName: string;
  hospitalAddress: string;
  hospitalPhone: string;
  hospitalEmail: string;
  hospitalWebsite: string;
  licenseNumber: string;
  timeZone: string;
  language: string;
  currency: string;
  dateFormat: string;
  maintenanceMode: boolean;
  allowRegistrations: boolean;
  requireEmailVerification: boolean;
  sessionTimeout: number;
  maxLoginAttempts: number;
  passwordMinLength: number;
  passwordRequireSymbols: boolean;
  twoFactorEnabled: boolean;
  backupFrequency: 'DAILY' | 'WEEKLY' | 'MONTHLY';
  retentionPeriod: number;
}

interface NotificationSettings {
  emailNotifications: boolean;
  smsNotifications: boolean;
  pushNotifications: boolean;
  appointmentReminders: boolean;
  labResultAlerts: boolean;
  emergencyAlerts: boolean;
  systemMaintenanceNotices: boolean;
  billingReminders: boolean;
}

interface IntegrationSettings {
  labIntegration: {
    enabled: boolean;
    provider: string;
    apiKey: string;
    endpoint: string;
  };
  radiologyIntegration: {
    enabled: boolean;
    provider: string;
    apiKey: string;
    endpoint: string;
  };
  paymentGateway: {
    enabled: boolean;
    provider: string;
    publicKey: string;
    secretKey: string;
  };
  smsGateway: {
    enabled: boolean;
    provider: string;
    apiKey: string;
    sender: string;
  };
}

const SettingsPage = () => {
  const [currentTab, setCurrentTab] = useState<'general' | 'security' | 'notifications' | 'integrations' | 'backup' | 'users'>('general');
  const [isLoading, setIsLoading] = useState(false);

  const [systemSettings, setSystemSettings] = useState<SystemSettings>({
    hospitalName: 'Central Medical Hospital',
    hospitalAddress: '123 Medical Plaza, Healthcare City, HC 12345',
    hospitalPhone: '+1 (555) 123-4567',
    hospitalEmail: 'admin@centralmedical.com',
    hospitalWebsite: 'www.centralmedical.com',
    licenseNumber: 'MED-LIC-2024-001',
    timeZone: 'America/New_York',
    language: 'English',
    currency: 'USD',
    dateFormat: 'MM/DD/YYYY',
    maintenanceMode: false,
    allowRegistrations: true,
    requireEmailVerification: true,
    sessionTimeout: 30,
    maxLoginAttempts: 5,
    passwordMinLength: 8,
    passwordRequireSymbols: true,
    twoFactorEnabled: false,
    backupFrequency: 'DAILY',
    retentionPeriod: 365
  });

  const [notificationSettings, setNotificationSettings] = useState<NotificationSettings>({
    emailNotifications: true,
    smsNotifications: false,
    pushNotifications: true,
    appointmentReminders: true,
    labResultAlerts: true,
    emergencyAlerts: true,
    systemMaintenanceNotices: true,
    billingReminders: true
  });

  const [integrationSettings, setIntegrationSettings] = useState<IntegrationSettings>({
    labIntegration: {
      enabled: false,
      provider: 'LabCorp',
      apiKey: '',
      endpoint: 'https://api.labcorp.com/v1'
    },
    radiologyIntegration: {
      enabled: false,
      provider: 'RadiologyPartners',
      apiKey: '',
      endpoint: 'https://api.radiologypartners.com/v1'
    },
    paymentGateway: {
      enabled: true,
      provider: 'Stripe',
      publicKey: 'pk_test_...',
      secretKey: '‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢'
    },
    smsGateway: {
      enabled: true,
      provider: 'Twilio',
      apiKey: '‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢',
      sender: 'HMS-ALERT'
    }
  });

  const handleSaveSettings = async (category: string) => {
    setIsLoading(true);
    try {
      // Simulate API call
      await new Promise(resolve => setTimeout(resolve, 1000));
      alert(`${category} settings saved successfully!`);
    } catch (error) {
      alert('Failed to save settings. Please try again.');
    } finally {
      setIsLoading(false);
    }
  };

  const SettingGroup = ({ title, children }: { title: string; children: React.ReactNode }) => (
    <div style={{ marginBottom: '2rem' }}>
      <h3 style={{ fontSize: '1.25rem', fontWeight: '600', color: '#1f2937', marginBottom: '1rem' }}>
        {title}
      </h3>
      <div style={{ display: 'grid', gap: '1rem' }}>
        {children}
      </div>
    </div>
  );

  const ToggleSetting = ({ 
    label, 
    description, 
    checked, 
    onChange, 
    disabled = false 
  }: { 
    label: string; 
    description?: string; 
    checked: boolean; 
    onChange: (checked: boolean) => void;
    disabled?: boolean;
  }) => (
    <div style={{
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'flex-start',
      padding: '1rem',
      border: '1px solid #e5e7eb',
      borderRadius: '8px',
      backgroundColor: disabled ? '#f9fafb' : 'white'
    }}>
      <div style={{ flex: 1 }}>
        <h4 style={{ fontSize: '1rem', fontWeight: '600', color: '#1f2937', marginBottom: '0.25rem' }}>
          {label}
        </h4>
        {description && (
          <p style={{ fontSize: '0.875rem', color: '#6b7280', margin: 0 }}>
            {description}
          </p>
        )}
      </div>
      <label style={{ display: 'flex', alignItems: 'center', cursor: disabled ? 'not-allowed' : 'pointer' }}>
        <input
          type="checkbox"
          checked={checked}
          onChange={(e) => onChange(e.target.checked)}
          disabled={disabled}
          style={{
            width: '1.5rem',
            height: '1.5rem',
            marginLeft: '1rem'
          }}
        />
      </label>
    </div>
  );

  return (
    <Layout>
      <div style={{ maxWidth: '1400px', margin: '0 auto' }}>
        {/* Header */}
        <div style={{ 
          display: 'flex', 
          justifyContent: 'space-between', 
          alignItems: 'center', 
          marginBottom: '2rem' 
        }}>
          <div>
            <h1 style={{ fontSize: '2rem', fontWeight: 'bold', color: '#1f2937', marginBottom: '0.5rem' }}>
              Settings & Configuration
            </h1>
            <p style={{ color: '#6b7280', fontSize: '1rem' }}>
              Manage system settings, integrations, and hospital configuration
            </p>
          </div>
          <div style={{ display: 'flex', gap: '0.5rem' }}>
            <Button variant="secondary">
              üì§ Export Config
            </Button>
            <Button variant="outline">
              üîÑ Reset to Defaults
            </Button>
          </div>
        </div>

        {/* Tab Navigation */}
        <div style={{ marginBottom: '2rem' }}>
          <div style={{ display: 'flex', gap: '0.5rem', borderBottom: '1px solid #e5e7eb' }}>
            {[
              { key: 'general', label: 'üè• General', desc: 'Hospital Info' },
              { key: 'security', label: 'üîê Security', desc: 'Access & Auth' },
              { key: 'notifications', label: 'üîî Notifications', desc: 'Alerts & Messages' },
              { key: 'integrations', label: 'üîó Integrations', desc: 'Third-party APIs' },
              { key: 'backup', label: 'üíæ Backup', desc: 'Data Protection' },
              { key: 'users', label: 'üë• User Management', desc: 'Roles & Permissions' }
            ].map(tab => (
              <button
                key={tab.key}
                onClick={() => setCurrentTab(tab.key as string)}
                style={{
                  padding: '1rem 1.5rem',
                  border: 'none',
                  background: 'none',
                  fontSize: '1rem',
                  fontWeight: '500',
                  color: currentTab === tab.key ? '#667eea' : '#6b7280',
                  borderBottom: currentTab === tab.key ? '2px solid #667eea' : '2px solid transparent',
                  cursor: 'pointer',
                  transition: 'all 0.2s ease',
                  textAlign: 'left'
                }}
              >
                <div>{tab.label}</div>
                <div style={{ fontSize: '0.75rem', opacity: 0.8 }}>
                  {tab.desc}
                </div>
              </button>
            ))}
          </div>
        </div>

        {/* General Settings Tab */}
        {currentTab === 'general' && (
          <Card>
            <SettingGroup title="Hospital Information">
              <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))', gap: '1rem' }}>
                <Input
                  label="Hospital Name"
                  value={systemSettings.hospitalName}
                  onChange={(e) => setSystemSettings(prev => ({ ...prev, hospitalName: e.target.value }))}
                  required
                />
                <Input
                  label="License Number"
                  value={systemSettings.licenseNumber}
                  onChange={(e) => setSystemSettings(prev => ({ ...prev, licenseNumber: e.target.value }))}
                  required
                />
              </div>
              
              <Input
                label="Hospital Address"
                value={systemSettings.hospitalAddress}
                onChange={(e) => setSystemSettings(prev => ({ ...prev, hospitalAddress: e.target.value }))}
                required
              />
              
              <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))', gap: '1rem' }}>
                <Input
                  type="tel"
                  label="Phone Number"
                  value={systemSettings.hospitalPhone}
                  onChange={(e) => setSystemSettings(prev => ({ ...prev, hospitalPhone: e.target.value }))}
                  required
                />
                <Input
                  type="email"
                  label="Email Address"
                  value={systemSettings.hospitalEmail}
                  onChange={(e) => setSystemSettings(prev => ({ ...prev, hospitalEmail: e.target.value }))}
                  required
                />
                <Input
                  label="Website"
                  value={systemSettings.hospitalWebsite}
                  onChange={(e) => setSystemSettings(prev => ({ ...prev, hospitalWebsite: e.target.value }))}
                />
              </div>
            </SettingGroup>

            <SettingGroup title="Regional Settings">
              <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))', gap: '1rem' }}>
                <div>
                  <label style={{ display: 'block', marginBottom: '0.5rem', fontSize: '0.875rem', fontWeight: '600', color: '#374151' }}>
                    Time Zone
                  </label>
                  <select
                    value={systemSettings.timeZone}
                    onChange={(e) => setSystemSettings(prev => ({ ...prev, timeZone: e.target.value }))}
                    style={{
                      width: '100%',
                      padding: '0.75rem 1rem',
                      border: '1px solid #d1d5db',
                      borderRadius: '8px',
                      fontSize: '1rem',
                      backgroundColor: 'white',
                      color: '#374151'
                    }}
                  >
                    <option value="America/New_York">Eastern Time</option>
                    <option value="America/Chicago">Central Time</option>
                    <option value="America/Denver">Mountain Time</option>
                    <option value="America/Los_Angeles">Pacific Time</option>
                  </select>
                </div>

                <div>
                  <label style={{ display: 'block', marginBottom: '0.5rem', fontSize: '0.875rem', fontWeight: '600', color: '#374151' }}>
                    Language
                  </label>
                  <select
                    value={systemSettings.language}
                    onChange={(e) => setSystemSettings(prev => ({ ...prev, language: e.target.value }))}
                    style={{
                      width: '100%',
                      padding: '0.75rem 1rem',
                      border: '1px solid #d1d5db',
                      borderRadius: '8px',
                      fontSize: '1rem',
                      backgroundColor: 'white',
                      color: '#374151'
                    }}
                  >
                    <option value="English">English</option>
                    <option value="Spanish">Spanish</option>
                    <option value="French">French</option>
                  </select>
                </div>

                <div>
                  <label style={{ display: 'block', marginBottom: '0.5rem', fontSize: '0.875rem', fontWeight: '600', color: '#374151' }}>
                    Currency
                  </label>
                  <select
                    value={systemSettings.currency}
                    onChange={(e) => setSystemSettings(prev => ({ ...prev, currency: e.target.value }))}
                    style={{
                      width: '100%',
                      padding: '0.75rem 1rem',
                      border: '1px solid #d1d5db',
                      borderRadius: '8px',
                      fontSize: '1rem',
                      backgroundColor: 'white',
                      color: '#374151'
                    }}
                  >
                    <option value="USD">USD ($)</option>
                    <option value="EUR">EUR (‚Ç¨)</option>
                    <option value="GBP">GBP (¬£)</option>
                    <option value="INR">INR (‚Çπ)</option>
                  </select>
                </div>

                <div>
                  <label style={{ display: 'block', marginBottom: '0.5rem', fontSize: '0.875rem', fontWeight: '600', color: '#374151' }}>
                    Date Format
                  </label>
                  <select
                    value={systemSettings.dateFormat}
                    onChange={(e) => setSystemSettings(prev => ({ ...prev, dateFormat: e.target.value }))}
                    style={{
                      width: '100%',
                      padding: '0.75rem 1rem',
                      border: '1px solid #d1d5db',
                      borderRadius: '8px',
                      fontSize: '1rem',
                      backgroundColor: 'white',
                      color: '#374151'
                    }}
                  >
                    <option value="MM/DD/YYYY">MM/DD/YYYY</option>
                    <option value="DD/MM/YYYY">DD/MM/YYYY</option>
                    <option value="YYYY-MM-DD">YYYY-MM-DD</option>
                  </select>
                </div>
              </div>
            </SettingGroup>

            <SettingGroup title="System Options">
              <ToggleSetting
                label="Maintenance Mode"
                description="Put the system in maintenance mode to prevent user access during updates"
                checked={systemSettings.maintenanceMode}
                onChange={(checked) => setSystemSettings(prev => ({ ...prev, maintenanceMode: checked }))}
              />
              
              <ToggleSetting
                label="Allow New Registrations"
                description="Allow new users to register accounts"
                checked={systemSettings.allowRegistrations}
                onChange={(checked) => setSystemSettings(prev => ({ ...prev, allowRegistrations: checked }))}
              />
              
              <ToggleSetting
                label="Require Email Verification"
                description="Require users to verify their email address before accessing the system"
                checked={systemSettings.requireEmailVerification}
                onChange={(checked) => setSystemSettings(prev => ({ ...prev, requireEmailVerification: checked }))}
              />
            </SettingGroup>

            <div style={{ display: 'flex', justifyContent: 'flex-end', paddingTop: '1rem', borderTop: '1px solid #e5e7eb' }}>
              <Button
                onClick={() => handleSaveSettings('General')}
                disabled={isLoading}
              >
                {isLoading ? 'Saving...' : 'Save General Settings'}
              </Button>
            </div>
          </Card>
        )}

        {/* Security Settings Tab */}
        {currentTab === 'security' && (
          <Card>
            <SettingGroup title="Authentication Settings">
              <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))', gap: '1rem' }}>
                <Input
                  type="number"
                  label="Session Timeout (minutes)"
                  value={systemSettings.sessionTimeout.toString()}
                  onChange={(e) => setSystemSettings(prev => ({ ...prev, sessionTimeout: parseInt(e.target.value) }))}
                  min={5}
                  max={480}
                />
                
                <Input
                  type="number"
                  label="Max Login Attempts"
                  value={systemSettings.maxLoginAttempts.toString()}
