interface InvoiceDetailsProps {
  opened: boolean;
  onClose: () => void;
  invoice: any;
  onEdit?: (invoice: any) => void;
  onDelete?: (invoice: any) => void;
  onRecordPayment?: (invoice: any) => void;
  onPrint?: (invoice: any) => void;
}

export default function InvoiceDetails({
  opened,
  onClose,
  invoice,
  onEdit,
  onDelete,
  onRecordPayment,
  onPrint
}: InvoiceDetailsProps) {
  if (!invoice) return null;

  const getStatusColor = (status: string) => {
    const colors: Record<string, string> = {
      PENDING: 'yellow',
      PARTIALLY_PAID: 'orange',
      PAID: 'green',
      OVERDUE: 'red',
      CANCELLED: 'gray'
    };
    return colors[status] || 'gray';
  };

  const formatDate = (dateString: string) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });
  };

  const formatCurrency = (amount: number) => {
    return `‚Çπ${amount.toFixed(2)}`;
  };

  const patient = invoice.patient || {};
  const items = invoice.invoiceItems || [];
  const payments = invoice.payments || [];

  const totalPaid = payments.reduce((sum: number, payment: any) => {
    if (payment.status === 'COMPLETED') {
      return sum + payment.amount;
    }
    return sum;
  }, 0);

  const remainingAmount = invoice.totalAmount - totalPaid;

  return (
    <Modal
      opened={opened}
      onClose={onClose}
      title={
        <Group justify="space-between" style={{ width: '100%' }}>
          <Group>
            <IconReceipt size={24} />
            <div>
              <Text size="lg" fw={600}>
                Invoice Details
              </Text>
              <Text size="xs" c="dimmed">
                {invoice.invoiceNumber}
              </Text>
            </div>
          </Group>
          <Badge color={getStatusColor(invoice.status)} size="lg">
            {invoice.status}
          </Badge>
        </Group>
      }
      size="xl"
      padding="md"
    >
      <Stack gap="md">
        {/* Patient Information */}
        <Card withBorder padding="md">
          <Group mb="xs">
            <IconUser size={20} />
            <Text fw={600}>Patient Information</Text>
          </Group>
          <Divider mb="sm" />
          <Grid>
            <Grid.Col span={6}>
              <Stack gap="xs">
                <Group justify="space-between">
                  <Text size="sm" c="dimmed">Name:</Text>
                  <Text fw={500}>
                    {patient.firstName} {patient.lastName}
                  </Text>
                </Group>
                {patient.medicalRecordNumber && (
                  <Group justify="space-between">
                    <Text size="sm" c="dimmed">MRN:</Text>
                    <Text fw={500}>{patient.medicalRecordNumber}</Text>
                  </Group>
                )}
              </Stack>
            </Grid.Col>
            <Grid.Col span={6}>
              <Stack gap="xs">
                {patient.phone && (
                  <Group justify="space-between">
                    <Text size="sm" c="dimmed">Phone:</Text>
                    <Text fw={500}>{patient.phone}</Text>
                  </Group>
                )}
                {patient.email && (
                  <Group justify="space-between">
                    <Text size="sm" c="dimmed">Email:</Text>
                    <Text fw={500}>{patient.email}</Text>
                  </Group>
                )}
              </Stack>
            </Grid.Col>
          </Grid>
        </Card>

        {/* Invoice Dates */}
        <Card withBorder padding="md">
          <Group mb="xs">
            <IconCalendar size={20} />
            <Text fw={600}>Invoice Dates</Text>
          </Group>
          <Divider mb="sm" />
          <Grid>
            <Grid.Col span={6}>
              <Group justify="space-between">
                <Text size="sm" c="dimmed">Invoice Date:</Text>
                <Text fw={500}>{formatDate(invoice.date)}</Text>
              </Group>
            </Grid.Col>
            <Grid.Col span={6}>
              <Group justify="space-between">
                <Text size="sm" c="dimmed">Due Date:</Text>
                <Text fw={500}>{formatDate(invoice.dueDate)}</Text>
              </Group>
            </Grid.Col>
          </Grid>
        </Card>

        {/* Invoice Items */}
        <Card withBorder padding="md">
          <Text fw={600} mb="sm">Invoice Items</Text>
          <Table>
            <Table.Thead>
              <Table.Tr>
                <Table.Th>Description</Table.Th>
                <Table.Th>Type</Table.Th>
                <Table.Th>Qty</Table.Th>
                <Table.Th>Price</Table.Th>
                <Table.Th>Discount</Table.Th>
                <Table.Th>Tax</Table.Th>
                <Table.Th>Total</Table.Th>
              </Table.Tr>
            </Table.Thead>
            <Table.Tbody>
              {items.map((item: any, index: number) => (
                <Table.Tr key={index}>
                  <Table.Td>{item.description}</Table.Td>
                  <Table.Td>
                    <Badge size="sm" variant="light">
                      {item.itemType}
                    </Badge>
                  </Table.Td>
                  <Table.Td>{item.quantity}</Table.Td>
                  <Table.Td>{formatCurrency(item.unitPrice)}</Table.Td>
                  <Table.Td>{formatCurrency(item.discount)}</Table.Td>
                  <Table.Td>{item.taxRate}%</Table.Td>
                  <Table.Td>
                    <Text fw={500}>{formatCurrency(item.totalAmount)}</Text>
                  </Table.Td>
                </Table.Tr>
              ))}
            </Table.Tbody>
          </Table>
        </Card>

        {/* Totals */}
        <Card withBorder padding="md" bg="gray.0">
          <Stack gap="xs">
            <Group justify="space-between">
              <Text size="sm">Subtotal:</Text>
              <Text size="sm" fw={500}>{formatCurrency(invoice.subTotal)}</Text>
            </Group>
            <Group justify="space-between">
              <Text size="sm">Tax:</Text>
              <Text size="sm" fw={500}>{formatCurrency(invoice.taxAmount)}</Text>
            </Group>
            <Group justify="space-between">
              <Text size="sm">Discount:</Text>
              <Text size="sm" fw={500}>-{formatCurrency(invoice.discountAmount)}</Text>
            </Group>
            <Divider />
            <Group justify="space-between">
              <Text size="lg" fw={700}>Total Amount:</Text>
              <Badge size="xl" color="blue">
                {formatCurrency(invoice.totalAmount)}
              </Badge>
            </Group>
            <Group justify="space-between">
              <Text size="sm" c="green">Paid:</Text>
              <Text size="sm" fw={600} c="green">{formatCurrency(totalPaid)}</Text>
            </Group>
            <Group justify="space-between">
              <Text size="sm" c="orange">Remaining:</Text>
              <Text size="sm" fw={600} c="orange">{formatCurrency(remainingAmount)}</Text>
            </Group>
          </Stack>
        </Card>

        {/* Payment History */}
        {payments.length > 0 && (
          <Card withBorder padding="md">
            <Group mb="xs">
              <IconCash size={20} />
              <Text fw={600}>Payment History</Text>
            </Group>
            <Divider mb="sm" />
            <Timeline active={payments.length} bulletSize={24}>
              {payments.map((payment: any, index: number) => (
                <Timeline.Item
                  key={index}
                  bullet={<IconCurrencyRupee size={12} />}
                  title={formatCurrency(payment.amount)}
                >
                  <Text size="xs" c="dimmed">
                    {payment.paymentMethod} - {formatDate(payment.paymentDate)}
                  </Text>
                  {payment.referenceNumber && (
                    <Text size="xs" c="dimmed">
                      Ref: {payment.referenceNumber}
                    </Text>
                  )}
                  <Badge size="xs" color={payment.status === 'COMPLETED' ? 'green' : 'gray'} mt={4}>
                    {payment.status}
                  </Badge>
                </Timeline.Item>
              ))}
            </Timeline>
          </Card>
        )}

        {/* Notes */}
        {invoice.notes && (
          <Card withBorder padding="md">
            <Text fw={600} mb="xs">Notes</Text>
            <Text size="sm">{invoice.notes}</Text>
          </Card>
        )}

        {/* Action Buttons */}
        <Group justify="space-between" mt="md">
          <Group>
            {onPrint && (
              <Button
                variant="light"
                leftSection={<IconPrinter size={16} />}
                onClick={() => onPrint(invoice)}
              >
                Print
              </Button>
            )}
            <Button
              variant="light"
              leftSection={<IconDownload size={16} />}
            >
              Download PDF
            </Button>
          </Group>

          <Group>
            {onDelete && invoice.status !== 'PAID' && (
              <Button
                variant="subtle"
                color="red"
                leftSection={<IconTrash size={16} />}
                onClick={() => {
                  if (window.confirm('Are you sure you want to cancel this invoice?')) {
                    onDelete(invoice);
                    onClose();
                  }
                }}
              >
                Cancel Invoice
              </Button>
            )}
            {onRecordPayment && remainingAmount > 0 && (
              <Button
                leftSection={<IconCurrencyRupee size={16} />}
                onClick={() => {
                  onRecordPayment(invoice);
                  onClose();
                }}
              >
                Record Payment
              </Button>
            )}
            {onEdit && invoice.status === 'PENDING' && (
              <Button
                leftSection={<IconEdit size={16} />}
                onClick={() => {
                  onEdit(invoice);
                  onClose();
                }}
              >
                Edit Invoice
              </Button>
            )}
            <Button variant="default" onClick={onClose}>
              Close
            </Button>
          </Group>
        </Group>
      </Stack>
    </Modal>
  );
}

import React, { useState, useEffect } from 'react';
import {
  Modal,
  TextInput,
  Textarea,
  Button,
  Group,
  Stack,
  Select,
  NumberInput,
  Grid,
  Text,
  LoadingOverlay,
  Divider,
  Card
} from '@mantine/core';
import { IconAlertTriangle, IconUser, IconActivity } from '@tabler/icons-react';
import type { CreateEmergencyCaseDto, UpdateEmergencyCaseDto, VitalSigns } from '../../services/emergency.service';

interface EmergencyCaseFormProps {
  opened: boolean;
  onClose: () => void;
  emergencyCase?: any;
  onSubmit: (data: CreateEmergencyCaseDto | UpdateEmergencyCaseDto) => Promise<void>;
  loading?: boolean;
  patients?: any[];
  doctors?: any[];
}

export default function EmergencyCaseForm({
  opened,
  onClose,
  emergencyCase,
  onSubmit,
  loading = false,
  patients = [],
  doctors = []
}: EmergencyCaseFormProps) {
  const [formData, setFormData] = useState({
    patientId: '',
    chiefComplaint: '',
    triageLevel: 'NON_URGENT',
    status: 'WAITING',
    assignedDoctorId: '',
    treatmentNotes: '',
    vitalSigns: {
      bloodPressure: '',
      heartRate: 0,
      temperature: 0,
      respiratoryRate: 0,
      oxygenSaturation: 0
    }
  });

  const [errors, setErrors] = useState<Record<string, string>>({});

  useEffect(() => {
    if (emergencyCase) {
      const vitalSigns = typeof emergencyCase.vitalSigns === 'string' 
        ? JSON.parse(emergencyCase.vitalSigns) 
        : emergencyCase.vitalSigns || {};
      
      setFormData({
        patientId: emergencyCase.patientId || '',
        chiefComplaint: emergencyCase.chiefComplaint || '',
        triageLevel: emergencyCase.triageLevel || 'NON_URGENT',
        status: emergencyCase.status || 'WAITING',
        assignedDoctorId: emergencyCase.assignedDoctorId || '',
        treatmentNotes: emergencyCase.treatmentNotes || '',
        vitalSigns: {
          bloodPressure: vitalSigns.bloodPressure || '',
          heartRate: vitalSigns.heartRate || 0,
          temperature: vitalSigns.temperature || 0,
          respiratoryRate: vitalSigns.respiratoryRate || 0,
          oxygenSaturation: vitalSigns.oxygenSaturation || 0
        }
      });
    } else {
      resetForm();
    }
  }, [emergencyCase, opened]);

  const resetForm = () => {
    setFormData({
      patientId: '',
      chiefComplaint: '',
      triageLevel: 'NON_URGENT',
      status: 'WAITING',
      assignedDoctorId: '',
      treatmentNotes: '',
      vitalSigns: {
        bloodPressure: '',
        heartRate: 0,
        temperature: 0,
        respiratoryRate: 0,
        oxygenSaturation: 0
      }
    });
    setErrors({});
  };

  const validateForm = (): boolean => {
    const newErrors: Record<string, string> = {};

    if (!emergencyCase && !formData.patientId) {
      newErrors.patientId = 'Patient is required';
    }
    if (!formData.chiefComplaint || formData.chiefComplaint.trim().length < 5) {
      newErrors.chiefComplaint = 'Chief complaint must be at least 5 characters';
    }
    if (!formData.triageLevel) {
      newErrors.triageLevel = 'Triage level is required';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!validateForm()) {
      return;
    }

    try {
      const submitData: any = {
        chiefComplaint: formData.chiefComplaint,
        triageLevel: formData.triageLevel,
        vitalSigns: formData.vitalSigns
      };

      if (!emergencyCase) {
        submitData.patientId = formData.patientId;
      } else {
        submitData.status = formData.status;
        submitData.assignedDoctorId = formData.assignedDoctorId || undefined;
        submitData.treatmentNotes = formData.treatmentNotes || undefined;
      }

      await onSubmit(submitData);
      resetForm();
      onClose();
    } catch (error) {
      console.error('Error submitting emergency case:', error);
    }
  };

  const handleClose = () => {
    resetForm();
    onClose();
  };

  const patientOptions = patients.map(p => ({
    value: p.id,
    label: `${p.firstName} ${p.lastName} - ${p.medicalRecordNumber || p.id}`
  }));

  const doctorOptions = doctors.map(d => ({
    value: d.id,
    label: `Dr. ${d.firstName} ${d.lastName}`
  }));

  const triageLevelOptions = [
    { value: 'CRITICAL', label: 'Critical (Red)', color: 'red' },
    { value: 'URGENT', label: 'Urgent (Orange)', color: 'orange' },
    { value: 'SEMI_URGENT', label: 'Semi-Urgent (Yellow)', color: 'yellow' },
    { value: 'NON_URGENT', label: 'Non-Urgent (Green)', color: 'green' }
  ];

  const statusOptions = [
    { value: 'WAITING', label: 'Waiting' },
    { value: 'IN_TREATMENT', label: 'In Treatment' },
    { value: 'DISCHARGED', label: 'Discharged' },
    { value: 'ADMITTED', label: 'Admitted' },
    { value: 'TRANSFERRED', label: 'Transferred' }
  ];

  return (
    <Modal
      opened={opened}
      onClose={handleClose}
      title={
        <Group>
          <IconAlertTriangle size={24} />
          <Text size="lg" fw={600}>
            {emergencyCase ? 'Update Emergency Case' : 'Register Emergency Case'}
          </Text>
        </Group>
      }
      size="lg"
      padding="md"
    >
      <LoadingOverlay visible={loading} />
      
      <form onSubmit={handleSubmit}>
        <Stack gap="md">
          {/* Patient Selection (only for new cases) */}
          {!emergencyCase && (
            <Select
              label="Patient"
              placeholder="Select patient"
              data={patientOptions}
              value={formData.patientId}
              onChange={(value) => setFormData({ ...formData, patientId: value || '' })}
              error={errors.patientId}
              required
              searchable
              leftSection={<IconUser size={16} />}
            />
          )}

          {/* Chief Complaint */}
          <Textarea
            label="Chief Complaint"
            placeholder="Describe the emergency condition"
            value={formData.chiefComplaint}
            onChange={(e) => setFormData({ ...formData, chiefComplaint: e.target.value })}
            error={errors.chiefComplaint}
            required
            minRows={3}
            maxRows={5}
          />

          {/* Triage Level */}
          <Select
            label="Triage Level"
            placeholder="Select triage level"
            data={triageLevelOptions}
            value={formData.triageLevel}
            onChange={(value) => setFormData({ ...formData, triageLevel: value || 'NON_URGENT' })}
            error={errors.triageLevel}
            required
            leftSection={<IconAlertTriangle size={16} />}
          />

          {/* Status (only for updates) */}
          {emergencyCase && (
            <>
              <Select
                label="Status"
                placeholder="Select status"
                data={statusOptions}
                value={formData.status}
                onChange={(value) => setFormData({ ...formData, status: value || 'WAITING' })}
              />

              <Select
                label="Assigned Doctor"
                placeholder="Select doctor"
                data={doctorOptions}
                value={formData.assignedDoctorId}
                onChange={(value) => setFormData({ ...formData, assignedDoctorId: value || '' })}
                searchable
                clearable
              />

              <Textarea
                label="Treatment Notes"
                placeholder="Enter treatment notes"
                value={formData.treatmentNotes}
                onChange={(e) => setFormData({ ...formData, treatmentNotes: e.target.value })}
                minRows={3}
                maxRows={5}
              />
            </>
          )}

          <Divider label="Vital Signs" labelPosition="center" />

          {/* Vital Signs */}
          <Card withBorder padding="md">
            <Stack gap="sm">
              <Grid>
                <Grid.Col span={6}>
                  <TextInput
                    label="Blood Pressure"
                    placeholder="120/80"
                    value={formData.vitalSigns.bloodPressure}
                    onChange={(e) => setFormData({
                      ...formData,
                      vitalSigns: { ...formData.vitalSigns, bloodPressure: e.target.value }
                    })}
                  />
                </Grid.Col>
                <Grid.Col span={6}>
                  <NumberInput
                    label="Heart Rate (bpm)"
                    placeholder="72"
                    value={formData.vitalSigns.heartRate}
                    onChange={(value) => setFormData({
                      ...formData,
                      vitalSigns: { ...formData.vitalSigns, heartRate: value || 0 }
                    })}
                    min={0}
                    max={300}
                  />
                </Grid.Col>
                <Grid.Col span={6}>
                  <NumberInput
                    label="Temperature (¬∞F)"
                    placeholder="98.6"
                    value={formData.vitalSigns.temperature}
                    onChange={(value) => setFormData({
                      ...formData,
                      vitalSigns: { ...formData.vitalSigns, temperature: value || 0 }
                    })}
                    min={0}
                    max={120}
                    decimalScale={1}
                  />
                </Grid.Col>
                <Grid.Col span={6}>
                  <NumberInput
                    label="Respiratory Rate"
                    placeholder="16"
                    value={formData.vitalSigns.respiratoryRate}
                    onChange={(value) => setFormData({
                      ...formData,
                      vitalSigns: { ...formData.vitalSigns, respiratoryRate: value || 0 }
                    })}
                    min={0}
                    max={100}
                  />
                </Grid.Col>
                <Grid.Col span={6}>
                  <NumberInput
                    label="O2 Saturation (%)"
                    placeholder="98"
                    value={formData.vitalSigns.oxygenSaturation}
                    onChange={(value) => setFormData({
                      ...formData,
                      vitalSigns: { ...formData.vitalSigns, oxygenSaturation: value || 0 }
                    })}
                    min={0}
                    max={100}
                  />
                </Grid.Col>
              </Grid>
            </Stack>
          </Card>

          {/* Action Buttons */}
          <Group justify="flex-end" mt="md">
            <Button variant="subtle" onClick={handleClose} disabled={loading}>
              Cancel
            </Button>
            <Button type="submit" loading={loading}>
              {emergencyCase ? 'Update Case' : 'Register Case'}
            </Button>
          </Group>
        </Stack>
      </form>
    </Modal>
  );
}

import React from 'react';
import {
  Modal,
  Text,
  Group,
  Stack,
  Badge,
  Button,
  Divider,
  Card,
  Avatar
} from '@mantine/core';
import {
  IconFileText,
  IconUser,
  IconCalendar,
  IconEdit,
  IconTrash,
  IconStethoscope
} from '@tabler/icons-react';

interface MedicalRecordDetailsProps {
  opened: boolean;
  onClose: () => void;
  record: any;
  onEdit?: (record: any) => void;
  onDelete?: (record: any) => void;
}

export default function MedicalRecordDetails({
  opened,
  onClose,
  record,
  onEdit,
  onDelete
}: MedicalRecordDetailsProps) {
  if (!record) return null;

  const getRecordTypeColor = (type: string) => {
    const colors: Record<string, string> = {
      CONSULTATION: 'blue',
      DIAGNOSIS: 'red',
      PRESCRIPTION: 'green',
      LAB_RESULT: 'cyan',
      IMAGING: 'grape',
      PROCEDURE: 'orange',
      VACCINATION: 'teal',
      ALLERGY: 'pink',
      OTHER: 'gray'
    };
    return colors[type] || 'gray';
  };

  const formatDate = (dateString: string) => {
    const date = new Date(dateString);
    return date.toLocaleString('en-US', {
      year: 'numeric',
      month: 'long',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  const patient = record.patient || {};
  const doctor = record.doctor || {};

  return (
    <Modal
      opened={opened}
      onClose={onClose}
      title={
        <Group justify="space-between" style={{ width: '100%' }}>
          <Group>
            <IconFileText size={24} />
            <div>
              <Text size="lg" fw={600}>
                Medical Record Details
              </Text>
              <Badge color={getRecordTypeColor(record.recordType)} size="sm" mt={4}>
                {record.recordType}
              </Badge>
            </div>
          </Group>
        </Group>
      }
      size="md"
      padding="md"
    >
      <Stack gap="md">
        {/* Patient Information */}
        <Card withBorder padding="md">
          <Group mb="xs">
            <IconUser size={20} />
            <Text fw={600}>Patient Information</Text>
          </Group>
          <Divider mb="sm" />
          <Group>
            <Avatar color="blue" radius="xl">
              {patient.firstName?.[0]}{patient.lastName?.[0]}
            </Avatar>
            <div>
              <Text fw={500}>
                {patient.firstName} {patient.lastName}
              </Text>
              <Text size="sm" c="dimmed">
                MRN: {patient.medicalRecordNumber || 'N/A'}
              </Text>
              {patient.phone && (
                <Text size="sm" c="dimmed">
                  Phone: {patient.phone}
                </Text>
              )}
            </div>
          </Group>
        </Card>

        {/* Record Information */}
        <Card withBorder padding="md">
          <Group mb="xs">
            <IconFileText size={20} />
            <Text fw={600}>Record Information</Text>
          </Group>
          <Divider mb="sm" />
          
          <Stack gap="sm">
            <div>
              <Text size="xs" c="dimmed" mb={4}>Title:</Text>
              <Text fw={600}>{record.title}</Text>
            </div>
            
            <div>
              <Text size="xs" c="dimmed" mb={4}>Description:</Text>
              <Text style={{ whiteSpace: 'pre-wrap' }}>
                {record.description}
              </Text>
            </div>
            
            <div>
              <Text size="xs" c="dimmed" mb={4}>Date:</Text>
              <Text size="sm">{formatDate(record.date)}</Text>
            </div>
          </Stack>
        </Card>

        {/* Doctor Information */}
        {doctor.id && (
          <Card withBorder padding="md">
            <Group mb="xs">
              <IconStethoscope size={20} />
              <Text fw={600}>Doctor Information</Text>
            </Group>
            <Divider mb="sm" />
            <Group>
              <Avatar color="green" radius="xl">
                {doctor.firstName?.[0]}{doctor.lastName?.[0]}
              </Avatar>
              <div>
                <Text fw={500}>
                  Dr. {doctor.firstName} {doctor.lastName}
                </Text>
                <Text size="sm" c="dimmed">
                  {doctor.specialization || 'General Medicine'}
                </Text>
                {doctor.licenseNumber && (
                  <Text size="xs" c="dimmed">
                    License: {doctor.licenseNumber}
                  </Text>
                )}
              </div>
            </Group>
          </Card>
        )}

        {/* Action Buttons */}
        <Group justify="space-between" mt="md">
          <Group>
            {onDelete && (
              <Button
                variant="subtle"
                color="red"
                leftSection={<IconTrash size={16} />}
                onClick={() => {
                  if (window.confirm('Are you sure you want to delete this medical record?')) {
                    onDelete(record);
                    onClose();
                  }
                }}
              >
                Delete
              </Button>
            )}
          </Group>
          
          <Group>
            {onEdit && (
              <Button
                leftSection={<IconEdit size={16} />}
                onClick={() => {
                  onEdit(record);
                  onClose();
                }}
              >
                Edit Record
              </Button>
            )}
            <Button variant="default" onClick={onClose}>
              Close
            </Button>
          </Group>
        </Group>
      </Stack>
    </Modal>
  );
}

'use client';

import React, { useState } from 'react';
import {
  Modal,
  Stack,
  Group,
  Text,
  TextInput,
  Textarea,
  Select,
  Button,
  Paper,
  Title,
  Badge,
  ActionIcon,
  Grid,
  Alert,
  Tabs,
  FileInput,
  Progress,
  Image,
  Card,
  Spotlight,
  Menu,
  ThemeIcon,
  Table
} from '@mantine/core';
import { useForm } from '@mantine/form';
import { Dropzone, FileWithPath, IMAGE_MIME_TYPE, PDF_MIME_TYPE } from '@mantine/dropzone';
import { DatePickerInput } from '@mantine/dates';
import {
  IconFileText,
  IconUpload,
  IconEye,
  IconDownload,
  IconEdit,
  IconTrash,
  IconShare,
  IconFileUpload,
  IconCalendar,
  IconPlus,
  IconDeviceFloppy,
  IconX,
  IconPhoto,
  // IconFilePdf, // not available
  IconFileWord,
  IconFile,
  IconLock,
  IconShield,
  IconWorld,
  IconCheck,
  IconExternalLink,
  IconSearch,
  IconFilter,
  IconSortAscending,
  IconDotsVertical
} from '@tabler/icons-react';
import { notifications } from '@mantine/notifications';
import { PatientDocument } from '../../types/patient';
import { formatDate } from '../../lib/utils';

interface DocumentManagerProps {
  opened: boolean;
  onClose: () => void;
  patientId: string;
  patientName: string;
  documents: PatientDocument[];
  onUpload: (document: Partial<PatientDocument>, file: File) => Promise<void>;
  onUpdate: (id: string, document: Partial<PatientDocument>) => Promise<void>;
  onDelete: (id: string) => Promise<void>;
  onDownload: (document: PatientDocument) => Promise<void>;
  onView: (document: PatientDocument) => Promise<void>;
}

interface DocumentFormData {
  documentType: 'id_proof' | 'medical_report' | 'lab_result' | 'radiology' | 'prescription' | 'insurance' | 'consent' | 'discharge_summary' | 'vaccination_record' | 'other';
  title: string;
  description?: string;
  accessLevel: 'public' | 'restricted' | 'confidential';
  tags?: string[];
  expirationDate?: Date;
}

const documentTypes = [
  { value: 'id_proof', label: 'ID Proof', color: 'blue' },
  { value: 'medical_report', label: 'Medical Report', color: 'green' },
  { value: 'lab_result', label: 'Lab Result', color: 'orange' },
  { value: 'radiology', label: 'Radiology', color: 'purple' },
  { value: 'prescription', label: 'Prescription', color: 'pink' },
  { value: 'insurance', label: 'Insurance', color: 'teal' },
  { value: 'consent', label: 'Consent Form', color: 'red' },
  { value: 'discharge_summary', label: 'Discharge Summary', color: 'cyan' },
  { value: 'vaccination_record', label: 'Vaccination Record', color: 'lime' },
  { value: 'other', label: 'Other', color: 'gray' }
];

export default function DocumentManager({
  opened,
  onClose,
  patientId,
  patientName,
  documents,
  onUpload,
  onUpdate,
  onDelete,
  onDownload,
  onView
}: DocumentManagerProps) {
  const [activeTab, setActiveTab] = useState('all');
  const [uploadModalOpened, setUploadModalOpened] = useState(false);
  const [editModalOpened, setEditModalOpened] = useState(false);
  const [editingDocument, setEditingDocument] = useState<PatientDocument | null>(null);
  const [uploading, setUploading] = useState(false);
  const [selectedFiles, setSelectedFiles] = useState<FileWithPath[]>([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [sortBy, setSortBy] = useState<'title' | 'date' | 'type' | 'size'>('date');
  const [sortOrder, setSortOrder] = useState<'asc' | 'desc'>('desc');

  const uploadForm = useForm<DocumentFormData>({
    initialValues: {
      documentType: 'other',
      title: '',
      description: '',
      accessLevel: 'restricted',
      tags: [],
      expirationDate: undefined
    },
    validate: {
      title: (value) => (value.trim().length < 2 ? 'Title must be at least 2 characters' : null),
      documentType: (value) => (!value ? 'Document type is required' : null)
    }
  });

  const editForm = useForm<DocumentFormData>({
    initialValues: {
      documentType: 'other',
      title: '',
      description: '',
      accessLevel: 'restricted',
      tags: [],
      expirationDate: undefined
    }
  });

  const handleUpload = async (values: DocumentFormData) => {
    if (selectedFiles.length === 0) {
      notifications.show({
        title: 'Error',
        message: 'Please select at least one file to upload.',
        color: 'red'
      });
      return;
    }

    try {
      setUploading(true);
      
      for (const file of selectedFiles) {
        const documentData = {
          ...values,
          fileName: file.name,
          fileSize: file.size,
          mimeType: file.type,
          uploadedAt: new Date(),
          uploadedBy: 'current_user', // Would come from auth context
          isActive: true,
          patientId
        };

        await onUpload(documentData, file);
      }

      notifications.show({
        title: 'Upload Successful',
        message: `${selectedFiles.length} document(s) uploaded successfully.`,
        color: 'green'
      });

      handleUploadModalClose();
    } catch (error) {
      notifications.show({
        title: 'Upload Failed',
        message: 'Failed to upload documents. Please try again.',
        color: 'red'
      });
    } finally {
      setUploading(false);
    }
  };

  const handleEdit = async (values: DocumentFormData) => {
    if (!editingDocument) return;

    try {
      await onUpdate(editingDocument.id, values);
      notifications.show({
        title: 'Document Updated',
        message: 'Document information has been updated successfully.',
        color: 'green'
      });
      handleEditModalClose();
    } catch (error) {
      notifications.show({
        title: 'Update Failed',
        message: 'Failed to update document. Please try again.',
        color: 'red'
      });
    }
  };

  const handleDelete = async (id: string, title: string) => {
    if (window.confirm(`Are you sure you want to delete "${title}"?`)) {
      try {
        await onDelete(id);
        notifications.show({
          title: 'Document Deleted',
          message: 'Document has been deleted successfully.',
          color: 'green'
        });
      } catch (error) {
        notifications.show({
          title: 'Delete Failed',
          message: 'Failed to delete document. Please try again.',
          color: 'red'
        });
      }
    }
  };

  const handleUploadModalClose = () => {
    uploadForm.reset();
    setSelectedFiles([]);
    setUploadModalOpened(false);
  };

  const handleEditModalClose = () => {
    editForm.reset();
    setEditingDocument(null);
    setEditModalOpened(false);
  };

  const openEditModal = (document: PatientDocument) => {
    setEditingDocument(document);
    editForm.setValues({
      documentType: document.documentType,
      title: document.title,
      description: document.description || '',
      accessLevel: document.accessLevel,
      tags: document.tags || [],
      expirationDate: document.expirationDate ? new Date(document.expirationDate) : undefined
    });
    setEditModalOpened(true);
  };

  const getFileIcon = (mimeType: string, size = '1.5rem') => {
    if (mimeType.startsWith('image/')) {
      return <IconPhoto size={size} />;
    } else if (mimeType === 'application/pdf') {
      return <IconFileText size={size} />;
    } else if (mimeType.includes('word')) {
      return <IconFileWord size={size} />;
    } else {
      return <IconFile size={size} />;
    }
  };

  const getAccessIcon = (level: string) => {
    switch (level) {
      case 'public': return <IconWorld size="1rem" />;
      case 'restricted': return <IconShield size="1rem" />;
      case 'confidential': return <IconLock size="1rem" />;
      default: return <IconShield size="1rem" />;
    }
  };

  const getAccessColor = (level: string) => {
    switch (level) {
      case 'public': return 'green';
      case 'restricted': return 'blue';
      case 'confidential': return 'red';
      default: return 'gray';
    }
  };

  const filterAndSortDocuments = (docs: PatientDocument[]) => {
    let filtered = docs;

    // Filter by search query
    if (searchQuery.trim()) {
      filtered = filtered.filter(doc => 
        doc.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
        doc.description?.toLowerCase().includes(searchQuery.toLowerCase()) ||
        doc.documentType.toLowerCase().includes(searchQuery.toLowerCase())
      );
    }

    // Filter by tab
    if (activeTab !== 'all') {
      filtered = filtered.filter(doc => doc.documentType === activeTab);
    }

    // Sort documents
    filtered.sort((a, b) => {
      let comparison = 0;
      
      switch (sortBy) {
        case 'title':
          comparison = a.title.localeCompare(b.title);
          break;
        case 'date':
          comparison = new Date(b.uploadedAt).getTime() - new Date(a.uploadedAt).getTime();
          break;
        case 'type':
          comparison = a.documentType.localeCompare(b.documentType);
          break;
        case 'size':
          comparison = b.fileSize - a.fileSize;
          break;
        default:
          comparison = 0;
      }

      return sortOrder === 'asc' ? comparison : -comparison;
    });

    return filtered;
  };

  const getDocumentTypeStats = () => {
    const stats = documentTypes.map(type => ({
      ...type,
      count: documents.filter(doc => doc.documentType === type.value).length
    }));
    return [
      { value: 'all', label: 'All Documents', count: documents.length, color: 'gray' },
      ...stats.filter(stat => stat.count > 0)
    ];
  };

  const DocumentCard = ({ document }: { document: PatientDocument }) => (
    <Paper p="md" withBorder>
      <Group justify="space-between" align="flex-start" mb="sm">
        <Group>
          <ThemeIcon
            size="lg"
            variant="light"
            color={documentTypes.find(t => t.value === document.documentType)?.color || 'gray'}
          >
            {getFileIcon(document.mimeType)}
          </ThemeIcon>
          <div>
            <Text fw={500} size="sm">{document.title}</Text>
            <Text size="xs" c="dimmed">{document.documentType.replace('_', ' ')}</Text>
          </div>
        </Group>
        
        <Menu shadow="md" width={200}>
          <Menu.Target>
            <ActionIcon variant="subtle" size="sm">
              <IconDotsVertical size="0.8rem" />
            </ActionIcon>
          </Menu.Target>
          <Menu.Dropdown>
            <Menu.Item
              leftSection={<IconEye size="0.9rem" />}
              onClick={() => onView(document)}
            >
              View
            </Menu.Item>
            <Menu.Item
              leftSection={<IconDownload size="0.9rem" />}
              onClick={() => onDownload(document)}
            >
              Download
            </Menu.Item>
            <Menu.Item
              leftSection={<IconEdit size="0.9rem" />}
              onClick={() => openEditModal(document)}
            >
              Edit
            </Menu.Item>
            <Menu.Item
              leftSection={<IconShare size="0.9rem" />}
            >
              Share
            </Menu.Item>
            <Menu.Divider />
            <Menu.Item
              color="red"
              leftSection={<IconTrash size="0.9rem" />}
              onClick={() => handleDelete(document.id, document.title)}
            >
              Delete
            </Menu.Item>
          </Menu.Dropdown>
        </Menu>
      </Group>

      {document.description && (
        <Text size="sm" c="dimmed" mb="sm">{document.description}</Text>
      )}

      <Group justify="space-between" align="center" mb="sm">
        <Badge
          color={getAccessColor(document.accessLevel)}
          variant="light"
          size="sm"
          leftSection={getAccessIcon(document.accessLevel)}
        >
          {document.accessLevel}
        </Badge>
        <Text size="xs" c="dimmed">
          {(document.fileSize / 1024).toFixed(1)} KB
        </Text>
      </Group>

      {document.tags && document.tags.length > 0 && (
        <Group gap="xs" mb="sm">
          {document.tags.map((tag, index) => (
            <Badge key={index} size="xs" variant="outline">
              {tag}
            </Badge>
          ))}
        </Group>
      )}

      <Group justify="space-between" align="center">
        <Text size="xs" c="dimmed">
          {formatDate(document.uploadedAt)}
        </Text>
        <Text size="xs" c="dimmed">
          By {document.uploadedBy}
        </Text>
      </Group>

      {document.expirationDate && (
        <Alert color="orange" mt="sm" p="xs">
          <Text size="xs">
            Expires: {formatDate(document.expirationDate)}
          </Text>
        </Alert>
      )}
    </Paper>
  );

  return (
    <>
      <Modal
        opened={opened}
        onClose={onClose}
        title={
          <Group>
            <IconFileText size="1.2rem" />
            <div>
              <Text fw={600}>Document Manager</Text>
              <Text size="sm" c="dimmed">{patientName}</Text>
            </div>
          </Group>
        }
        size="xl"
      >
        <Stack gap="lg">
          {/* Header Controls */}
          <Group justify="space-between">
            <Group>
              <TextInput
                placeholder="Search documents..."
                leftSection={<IconSearch size="1rem" />}
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.currentTarget.value)}
                style={{ width: 250 }}
              />
              <Select
                placeholder="Sort by"
                leftSection={<IconSortAscending size="1rem" />}
                data={[
                  { value: 'date', label: 'Upload Date' },
                  { value: 'title', label: 'Title' },
                  { value: 'type', label: 'Type' },
                  { value: 'size', label: 'File Size' }
                ]}
                value={sortBy}
                onChange={(value) => setSortBy(value as any)}
                style={{ width: 150 }}
              />
              <Button
                variant="subtle"
                size="sm"
                onClick={() => setSortOrder(sortOrder === 'asc' ? 'desc' : 'asc')}
              >
                {sortOrder === 'asc' ? '‚Üë' : '‚Üì'}
              </Button>
            </Group>
            
            <Button
              leftSection={<IconUpload size="1rem" />}
              onClick={() => setUploadModalOpened(true)}
            >
              Upload Document
            </Button>
          </Group>

          {/* Document Type Tabs */}
          <Tabs value={activeTab} onChange={(value) => setActiveTab(value || 'all')}>
            <Tabs.List>
              {getDocumentTypeStats().map((stat) => (
                <Tabs.Tab key={stat.value} value={stat.value}>
                  {stat.label} ({stat.count})
                </Tabs.Tab>
              ))}
            </Tabs.List>

            <Tabs.Panel value={activeTab} pt="md">
              {filterAndSortDocuments(documents).length === 0 ? (
                <Paper p="xl" withBorder style={{ textAlign: 'center' }}>
                  <IconFileText size="3rem" color="var(--mantine-color-gray-5)" />
                  <Text mt="md" c="dimmed">
                    {searchQuery.trim() ? 'No documents match your search' : 'No documents found'}
                  </Text>
                </Paper>
              ) : (
                <Grid>
                  {filterAndSortDocuments(documents).map((document) => (
                    <Grid.Col key={document.id} span={{ base: 12, md: 6, lg: 4 }}>
                      <DocumentCard document={document} />
                    </Grid.Col>
                  ))}
                </Grid>
              )}
            </Tabs.Panel>
          </Tabs>
        </Stack>
      </Modal>

      {/* Upload Modal */}
      <Modal
        opened={uploadModalOpened}
        onClose={handleUploadModalClose}
        title={
          <Group>
            <IconUpload size="1.2rem" />
            <Text fw={600}>Upload Documents</Text>
          </Group>
        }
        size="lg"
      >
        <form onSubmit={uploadForm.onSubmit(handleUpload)}>
          <Stack gap="md">
            <Dropzone
              onDrop={setSelectedFiles}
              accept={[...IMAGE_MIME_TYPE, ...PDF_MIME_TYPE, 'application/msword', 'application/vnd.openxmlformats-officedocument.wordprocessingml.document']}
              maxSize={10 * 1024 * 1024} // 10MB
              multiple
            >
              <Group justify="center" gap="xl" mih={220} style={{ pointerEvents: 'none' }}>
                <Dropzone.Accept>
                  <IconUpload size="3.2rem" stroke={1.5} />
                </Dropzone.Accept>
                <Dropzone.Reject>
                  <IconX size="3.2rem" stroke={1.5} />
                </Dropzone.Reject>
                <Dropzone.Idle>
                  <IconFileUpload size="3.2rem" stroke={1.5} />
                </Dropzone.Idle>

                <div>
                  <Text size="xl" inline>
                    Drag files here or click to select files
                  </Text>
                  <Text size="sm" c="dimmed" inline mt={7}>
                    Attach up to 5 files, each file should not exceed 10MB
                  </Text>
                </div>
              </Group>
            </Dropzone>

            {selectedFiles.length > 0 && (
              <Stack gap="xs">
                <Text size="sm" fw={500}>Selected Files:</Text>
                {selectedFiles.map((file, index) => (
                  <Group key={index} justify="space-between" p="xs" bg="gray.0" style={{ borderRadius: 4 }}>
                    <Group>
                      {getFileIcon(file.type, '1rem')}
                      <div>
                        <Text size="sm" fw={500}>{file.name}</Text>
                        <Text size="xs" c="dimmed">{(file.size / 1024).toFixed(1)} KB</Text>
                      </div>
                    </Group>
                    <ActionIcon
                      variant="subtle"
                      size="sm"
                      onClick={() => setSelectedFiles(files => files.filter((_, i) => i !== index))}
                    >
                      <IconX size="0.8rem" />
                    </ActionIcon>
                  </Group>
                ))}
              </Stack>
            )}

            <Grid>
              <Grid.Col span={{ base: 12, md: 6 }}>
                <Select
                  label="Document Type"
                  placeholder="Select document type"
                  required
                  data={documentTypes.map(type => ({ value: type.value, label: type.label }))}
                  {...uploadForm.getInputProps('documentType')}
                />
              </Grid.Col>
              
              <Grid.Col span={{ base: 12, md: 6 }}>
                <Select
                  label="Access Level"
                  placeholder="Select access level"
                  required
                  data={[
                    { value: 'public', label: 'Public - Everyone can view' },
                    { value: 'restricted', label: 'Restricted - Healthcare staff only' },
                    { value: 'confidential', label: 'Confidential - Authorized personnel only' }
                  ]}
                  {...uploadForm.getInputProps('accessLevel')}
                />
              </Grid.Col>
            </Grid>

            <TextInput
              label="Document Title"
              placeholder="Enter document title"
              required
              {...uploadForm.getInputProps('title')}
            />

            <Textarea
              label="Description"
              placeholder="Enter document description"
              minRows={2}
              {...uploadForm.getInputProps('description')}
            />

            <DatePickerInput
              label="Expiration Date"
              placeholder="Select expiration date (optional)"
              minDate={new Date()}
              {...uploadForm.getInputProps('expirationDate')}
              leftSection={<IconCalendar size="1rem" />}
            />

            <Group justify="flex-end" mt="xl">
              <Button
                variant="outline"
                onClick={handleUploadModalClose}
                disabled={uploading}
              >
                Cancel
              </Button>
              <Button
                type="submit"
                loading={uploading}
                leftSection={<IconUpload size="1rem" />}
              >
                Upload Documents
              </Button>
            </Group>
          </Stack>
        </form>
      </Modal>

      {/* Edit Modal */}
      <Modal
        opened={editModalOpened}
        onClose={handleEditModalClose}
        title={
          <Group>
            <IconEdit size="1.2rem" />
            <Text fw={600}>Edit Document</Text>
          </Group>
        }
        size="lg"
      >
        <form onSubmit={editForm.onSubmit(handleEdit)}>
          <Stack gap="md">
            <Grid>
              <Grid.Col span={{ base: 12, md: 6 }}>
                <Select
                  label="Document Type"
                  placeholder="Select document type"
                  required
                  data={documentTypes.map(type => ({ value: type.value, label: type.label }))}
                  {...editForm.getInputProps('documentType')}
                />
              </Grid.Col>
              
              <Grid.Col span={{ base: 12, md: 6 }}>
                <Select
                  label="Access Level"
                  placeholder="Select access level"
                  required
                  data={[
                    { value: 'public', label: 'Public - Everyone can view' },
                    { value: 'restricted', label: 'Restricted - Healthcare staff only' },
                    { value: 'confidential', label: 'Confidential - Authorized personnel only' }
                  ]}
                  {...editForm.getInputProps('accessLevel')}
                />
              </Grid.Col>
            </Grid>

            <TextInput
              label="Document Title"
              placeholder="Enter document title"
              required
              {...editForm.getInputProps('title')}
            />

            <Textarea
              label="Description"
              placeholder="Enter document description"
              minRows={2}
              {...editForm.getInputProps('description')}
            />

            <DatePickerInput
              label="Expiration Date"
              placeholder="Select expiration date (optional)"
              minDate={new Date()}
              {...editForm.getInputProps('expirationDate')}
              leftSection={<IconCalendar size="1rem" />}
            />

            <Group justify="flex-end" mt="xl">
              <Button
                variant="outline"
                onClick={handleEditModalClose}
              >
                Cancel
              </Button>
              <Button
                type="submit"
                leftSection={<IconDeviceFloppy size="1rem" />}
              >
                Save Changes
              </Button>
            </Group>
          </Stack>
        </form>
      </Modal>
    </>
  );
}

'use client';

import React, { useState } from 'react';
import {
  Modal,
  Stack,
  Group,
  Text,
  TextInput,
  Textarea,
  Select,
  Button,
  Paper,
  Title,
  Badge,
  ActionIcon,
  Grid,
  Alert,
  Divider,
  Timeline,
  Card,
  Tabs,
  Switch,
  NumberInput,
  TagsInput
} from '@mantine/core';
import { useForm } from '@mantine/form';
import { DatePickerInput } from '@mantine/dates';
import {
  IconMedicalCross,
  IconAlertCircle,
  IconPill,
  IconHeartbeat,
  IconUser,
  IconStethoscope,
  IconCalendar,
  IconPlus,
  IconEdit,
  IconTrash,
  IconCheck,
  IconX,
  IconNotes,
  IconVaccine
} from '@tabler/icons-react';
import { notifications } from '@mantine/notifications';
import { MedicalHistory } from '../../types/patient';
import { formatDate } from '../../lib/utils';

interface MedicalHistoryManagerProps {
  opened: boolean;
  onClose: () => void;
  patientId: string;
  patientName: string;
  medicalHistory: MedicalHistory[];
  onSave: (history: Partial<MedicalHistory>) => Promise<void>;
  onUpdate: (id: string, history: Partial<MedicalHistory>) => Promise<void>;
  onDelete: (id: string) => Promise<void>;
}

interface HistoryFormData {
  historyType: 'medical' | 'surgical' | 'family' | 'social' | 'allergy' | 'medication' | 'immunization';
  title: string;
  description: string;
  date?: Date;
  severity?: 'mild' | 'moderate' | 'severe';
  outcome?: string;
  complications?: string;
  treatmentReceived?: string;
  doctorName?: string;
  hospitalName?: string;
  notes?: string;
  isActive: boolean;
}

export default function MedicalHistoryManager({
  opened,
  onClose,
  patientId,
  patientName,
  medicalHistory,
  onSave,
  onUpdate,
  onDelete
}: MedicalHistoryManagerProps) {
  const [activeTab, setActiveTab] = useState('all');
  const [formOpened, setFormOpened] = useState(false);
  const [editingHistory, setEditingHistory] = useState<MedicalHistory | null>(null);
  const [loading, setLoading] = useState(false);

  const form = useForm<HistoryFormData>({
    initialValues: {
      historyType: 'medical',
      title: '',
      description: '',
      date: undefined,
      severity: undefined,
      outcome: '',
      complications: '',
      treatmentReceived: '',
      doctorName: '',
      hospitalName: '',
      notes: '',
      isActive: true
    },
    validate: {
      title: (value) => (value.trim().length < 2 ? 'Title must be at least 2 characters' : null),
      description: (value) => (value.trim().length < 5 ? 'Description must be at least 5 characters' : null)
    }
  });

  const handleSubmit = async (values: HistoryFormData) => {
    try {
      setLoading(true);
      
      if (editingHistory) {
        await onUpdate(editingHistory.id, values);
        notifications.show({
          title: 'History Updated',
          message: 'Medical history entry has been updated successfully.',
          color: 'green'
        });
      } else {
        await onSave(values);
        notifications.show({
          title: 'History Added',
          message: 'New medical history entry has been added successfully.',
          color: 'green'
        });
      }
      
      handleFormClose();
    } catch (error) {
      notifications.show({
        title: 'Error',
        message: `Failed to ${editingHistory ? 'update' : 'save'} medical history. Please try again.`,
        color: 'red'
      });
    } finally {
      setLoading(false);
    }
  };

  const handleEdit = (history: MedicalHistory) => {
    setEditingHistory(history);
    form.setValues({
      historyType: history.historyType,
      title: history.title,
      description: history.description,
      date: history.date ? new Date(history.date) : undefined,
      severity: history.severity,
      outcome: history.outcome || '',
      complications: history.complications || '',
      treatmentReceived: history.treatmentReceived || '',
      doctorName: history.doctorName || '',
      hospitalName: history.hospitalName || '',
      notes: history.notes || '',
      isActive: history.isActive
    });
    setFormOpened(true);
  };

  const handleDelete = async (id: string) => {
    try {
      await onDelete(id);
      notifications.show({
        title: 'History Deleted',
        message: 'Medical history entry has been deleted successfully.',
        color: 'green'
      });
    } catch (error) {
      notifications.show({
        title: 'Error',
        message: 'Failed to delete medical history. Please try again.',
        color: 'red'
      });
    }
  };

  const handleFormClose = () => {
    form.reset();
    setEditingHistory(null);
    setFormOpened(false);
  };

  const getHistoryIcon = (type: string) => {
    switch (type) {
      case 'medical': return <IconMedicalCross size="1rem" />;
      case 'surgical': return <IconStethoscope size="1rem" />;
      case 'family': return <IconStethoscope size="1rem" />;
      case 'social': return <IconUser size="1rem" />;
      case 'allergy': return <IconAlertCircle size="1rem" />;
      case 'medication': return <IconPill size="1rem" />;
      case 'immunization': return <IconVaccine size="1rem" />;
      default: return <IconHeartbeat size="1rem" />;
    }
  };

  const getHistoryTypeColor = (type: string) => {
    switch (type) {
      case 'medical': return 'blue';
      case 'surgical': return 'orange';
      case 'family': return 'purple';
      case 'social': return 'teal';
      case 'allergy': return 'red';
      case 'medication': return 'green';
      case 'immunization': return 'cyan';
      default: return 'gray';
    }
  };

  const getSeverityColor = (severity?: string) => {
    switch (severity) {
      case 'severe': return 'red';
      case 'moderate': return 'orange';
      case 'mild': return 'yellow';
      default: return 'gray';
    }
  };

  const filterHistoryByType = (type: string) => {
    if (type === 'all') return medicalHistory;
    return medicalHistory.filter(history => history.historyType === type);
  };

  const historyTabs = [
    { value: 'all', label: 'All History', count: medicalHistory.length },
    { value: 'medical', label: 'Medical', count: medicalHistory.filter(h => h.historyType === 'medical').length },
    { value: 'surgical', label: 'Surgical', count: medicalHistory.filter(h => h.historyType === 'surgical').length },
    { value: 'allergy', label: 'Allergies', count: medicalHistory.filter(h => h.historyType === 'allergy').length },
    { value: 'family', label: 'Family', count: medicalHistory.filter(h => h.historyType === 'family').length },
    { value: 'medication', label: 'Medications', count: medicalHistory.filter(h => h.historyType === 'medication').length }
  ];

  const HistoryList = ({ histories }: { histories: MedicalHistory[] }) => (
    <>
      {histories.length === 0 ? (
        <Paper p="xl" withBorder style={{ textAlign: 'center' }}>
          <IconMedicalCross size="3rem" color="var(--mantine-color-gray-5)" />
          <Text mt="md" c="dimmed">No medical history found for this category</Text>
        </Paper>
      ) : (
        <Stack gap="md">
          {histories.map((history) => (
            <Paper key={history.id} p="md" withBorder>
              <Group justify="space-between" align="flex-start" mb="sm">
                <Group>
                  <Badge
                    color={getHistoryTypeColor(history.historyType)}
                    variant="light"
                    leftSection={getHistoryIcon(history.historyType)}
                  >
                    {history.historyType}
                  </Badge>
                  <Text fw={500}>{history.title}</Text>
                </Group>
                
                <Group gap="xs">
                  {history.severity && (
                    <Badge
                      color={getSeverityColor(history.severity)}
                      variant="light"
                      size="sm"
                    >
                      {history.severity}
                    </Badge>
                  )}
                  <ActionIcon
                    variant="subtle"
                    size="sm"
                    onClick={() => handleEdit(history)}
                  >
                    <IconEdit size="0.8rem" />
                  </ActionIcon>
                  <ActionIcon
                    variant="subtle"
                    size="sm"
                    color="red"
                    onClick={() => handleDelete(history.id)}
                  >
                    <IconTrash size="0.8rem" />
                  </ActionIcon>
                </Group>
              </Group>

              <Text size="sm" mb="sm">{history.description}</Text>

              <Grid>
                {history.date && (
                  <Grid.Col span={6}>
                    <Text size="xs" c="dimmed">
                      <IconCalendar size="0.8rem" style={{ marginRight: 4 }} />
                      {formatDate(history.date)}
                    </Text>
                  </Grid.Col>
                )}
                
                {history.doctorName && (
                  <Grid.Col span={6}>
                    <Text size="xs" c="dimmed">
                      Doctor: {history.doctorName}
                    </Text>
                  </Grid.Col>
                )}
                
                {history.hospitalName && (
                  <Grid.Col span={6}>
                    <Text size="xs" c="dimmed">
                      Hospital: {history.hospitalName}
                    </Text>
                  </Grid.Col>
                )}
                
                {history.outcome && (
                  <Grid.Col span={6}>
                    <Text size="xs" c="dimmed">
                      Outcome: {history.outcome}
                    </Text>
                  </Grid.Col>
                )}
              </Grid>

              {history.treatmentReceived && (
                <div>
                  <Text size="xs" fw={500} mt="sm" mb="xs">Treatment Received:</Text>
                  <Text size="xs" c="dimmed">{history.treatmentReceived}</Text>
                </div>
              )}

              {history.complications && (
                <Alert color="orange" mt="sm" p="xs">
                  <Text size="xs" fw={500}>Complications:</Text>
                  <Text size="xs">{history.complications}</Text>
                </Alert>
              )}

              {history.notes && (
                <div>
                  <Text size="xs" fw={500} mt="sm" mb="xs">Notes:</Text>
                  <Text size="xs" c="dimmed">{history.notes}</Text>
                </div>
              )}

              {!history.isActive && (
                <Badge color="gray" size="sm" mt="sm">Inactive</Badge>
              )}
            </Paper>
          ))}
        </Stack>
      )}
    </>
  );

  return (
    <>
      <Modal
        opened={opened}
        onClose={onClose}
        title={
          <Group>
            <IconMedicalCross size="1.2rem" />
            <div>
              <Text fw={600}>Medical History</Text>
              <Text size="sm" c="dimmed">{patientName}</Text>
            </div>
          </Group>
        }
        size="xl"
      >
        <Stack gap="lg">
          <Group justify="space-between">
            <Text size="sm" c="dimmed">
              Total entries: {medicalHistory.length}
            </Text>
            <Button
              leftSection={<IconPlus size="1rem" />}
              onClick={() => setFormOpened(true)}
            >
              Add History
            </Button>
          </Group>

          <Tabs value={activeTab} onChange={(value) => setActiveTab(value || 'all')}>
            <Tabs.List>
              {historyTabs.map((tab) => (
                <Tabs.Tab key={tab.value} value={tab.value}>
                  {tab.label} ({tab.count})
                </Tabs.Tab>
              ))}
            </Tabs.List>

            {historyTabs.map((tab) => (
              <Tabs.Panel key={tab.value} value={tab.value} pt="md">
                <HistoryList histories={filterHistoryByType(tab.value)} />
              </Tabs.Panel>
            ))}
          </Tabs>
        </Stack>
      </Modal>

      {/* Add/Edit History Form */}
      <Modal
        opened={formOpened}
        onClose={handleFormClose}
        title={
          <Group>
            <IconMedicalCross size="1.2rem" />
            <Text fw={600}>{editingHistory ? 'Edit' : 'Add'} Medical History</Text>
          </Group>
        }
        size="lg"
      >
        <form onSubmit={form.onSubmit(handleSubmit)}>
          <Stack gap="md">
            <Grid>
              <Grid.Col span={{ base: 12, md: 6 }}>
                <Select
                  label="History Type"
                  placeholder="Select history type"
                  required
                  data={[
                    { value: 'medical', label: 'Medical Condition' },
                    { value: 'surgical', label: 'Surgical History' },
                    { value: 'family', label: 'Family History' },
                    { value: 'social', label: 'Social History' },
                    { value: 'allergy', label: 'Allergy' },
                    { value: 'medication', label: 'Medication History' },
                    { value: 'immunization', label: 'Immunization' }
                  ]}
                  {...form.getInputProps('historyType')}
                />
              </Grid.Col>
              
              <Grid.Col span={{ base: 12, md: 6 }}>
                <DatePickerInput
                  label="Date"
                  placeholder="Select date"
                  maxDate={new Date()}
                  {...form.getInputProps('date')}
                  leftSection={<IconCalendar size="1rem" />}
                />
              </Grid.Col>
            </Grid>

            <TextInput
              label="Title"
              placeholder="Enter condition/procedure title"
              required
              {...form.getInputProps('title')}
            />

            <Textarea
              label="Description"
              placeholder="Enter detailed description"
              required
              minRows={3}
              {...form.getInputProps('description')}
            />

            <Grid>
              <Grid.Col span={{ base: 12, md: 6 }}>
                <Select
                  label="Severity"
                  placeholder="Select severity level"
                  data={[
                    { value: 'mild', label: 'Mild' },
                    { value: 'moderate', label: 'Moderate' },
                    { value: 'severe', label: 'Severe' }
                  ]}
                  {...form.getInputProps('severity')}
                  clearable
                />
              </Grid.Col>
              
              <Grid.Col span={{ base: 12, md: 6 }}>
                <TextInput
                  label="Outcome"
                  placeholder="Enter outcome/result"
                  {...form.getInputProps('outcome')}
                />
              </Grid.Col>
            </Grid>

            <Textarea
              label="Treatment Received"
              placeholder="Describe treatment received"
              minRows={2}
              {...form.getInputProps('treatmentReceived')}
            />

            <Textarea
              label="Complications"
              placeholder="Any complications experienced"
              minRows={2}
              {...form.getInputProps('complications')}
            />

            <Grid>
              <Grid.Col span={{ base: 12, md: 6 }}>
                <TextInput
                  label="Doctor Name"
                  placeholder="Enter treating doctor's name"
                  {...form.getInputProps('doctorName')}
                />
              </Grid.Col>
              
              <Grid.Col span={{ base: 12, md: 6 }}>
                <TextInput
                  label="Hospital/Clinic Name"
                  placeholder="Enter hospital/clinic name"
                  {...form.getInputProps('hospitalName')}
                />
              </Grid.Col>
            </Grid>

            <Textarea
              label="Additional Notes"
              placeholder="Any additional notes"
              minRows={2}
              {...form.getInputProps('notes')}
            />

            <Switch
              label="Active"
              description="Is this medical history item currently active/relevant?"
              {...form.getInputProps('isActive', { type: 'checkbox' })}
            />

            <Group justify="flex-end" mt="xl">
              <Button
                variant="outline"
                onClick={handleFormClose}
              >
                Cancel
              </Button>
              <Button
                type="submit"
                loading={loading}
                leftSection={<IconCheck size="1rem" />}
              >
                {editingHistory ? 'Update' : 'Save'} History
              </Button>
            </Group>
          </Stack>
        </form>
      </Modal>
    </>
  );
}

'use client';

import React, { useState, useMemo } from 'react';
import {
  Paper,
  Title,
  Text,
  Grid,
  Card,
  Group,
  Badge,
  Progress,
  SimpleGrid,
  Stack,
  Select,
  SegmentedControl,
  RingProgress,
  ThemeIcon,
  Timeline,
  Table,
  Alert,
  Tabs,
  ActionIcon,
  Menu,
  Button,
  Center,
  Divider,
  Modal
} from '@mantine/core';
import {
  IconTrendingUp,
  IconTrendingDown,
  IconUsers,
  IconUserPlus,
  IconHeart,
  IconCalendar,
  IconChartBar,
  IconChartPie,
  IconRefresh,
  IconDownload,
  IconPrinter,
  IconShare,
  IconFilter,
  IconEye,
  IconAlertCircle,
  IconMedicalCross,
  IconShield,
  IconStethoscope,
  IconActivity,
  IconClock
} from '@tabler/icons-react';
import { PatientStats, Patient } from '../../types/patient';
import { BloodGroup } from '../../types/common';
import { formatDate } from '../../lib/utils';

interface PatientAnalyticsProps {
  opened: boolean;
  onClose: () => void;
  patients: Patient[];
  stats: PatientStats | null;
}

interface ChartTimeframe {
  value: string;
  label: string;
}

const timeframes: ChartTimeframe[] = [
  { value: '7d', label: 'Last 7 Days' },
  { value: '30d', label: 'Last 30 Days' },
  { value: '90d', label: 'Last 3 Months' },
  { value: '1y', label: 'Last Year' }
];

export default function PatientAnalytics({ opened, onClose, patients, stats }: PatientAnalyticsProps) {
  const [selectedTimeframe, setSelectedTimeframe] = useState('30d');
  const [activeTab, setActiveTab] = useState('overview');

  // Calculate derived statistics (must be before conditional return to follow Rules of Hooks)
  const derivedStats = useMemo(() => {
    const totalPatients = patients.length;
    const today = new Date();
    const lastMonth = new Date(today.getFullYear(), today.getMonth() - 1, today.getDate());
    const lastYear = new Date(today.getFullYear() - 1, today.getMonth(), today.getDate());

    const newPatientsThisMonth = patients.filter(p => 
      new Date(p.registrationDate) >= lastMonth
    ).length;

    const newPatientsLastMonth = patients.filter(p => {
      const regDate = new Date(p.registrationDate);
      const twoMonthsAgo = new Date(today.getFullYear(), today.getMonth() - 2, today.getDate());
      return regDate >= twoMonthsAgo && regDate < lastMonth;
    }).length;

    const growthRate = newPatientsLastMonth > 0 
      ? ((newPatientsThisMonth - newPatientsLastMonth) / newPatientsLastMonth) * 100
      : 0;

    // Age group distribution
    const ageGroups = patients.reduce((acc, patient) => {
      if (patient.age < 18) acc.pediatric++;
      else if (patient.age < 65) acc.adult++;
      else acc.senior++;
      return acc;
    }, { pediatric: 0, adult: 0, senior: 0 });

    // Most common conditions
    const conditions = patients
      .flatMap(p => p.chronicDiseases)
      .reduce((acc, condition) => {
        acc[condition] = (acc[condition] || 0) + 1;
        return acc;
      }, {} as Record<string, number>);

    const topConditions = Object.entries(conditions)
      .sort(([,a], [,b]) => b - a)
      .slice(0, 5)
      .map(([condition, count]) => ({
        condition,
        count,
        percentage: (count / totalPatients) * 100
      }));

    return {
      growthRate,
      ageGroups,
      topConditions,
      totalPatients,
      newPatientsThisMonth
    };
  }, [patients]);

  // Handle null stats (after all hooks to follow Rules of Hooks)
  if (!stats) {
    return (
      <Modal
        opened={opened}
        onClose={onClose}
        size="xl"
        title="Patient Analytics"
        padding="lg"
      >
        <Center p="xl">
          <Stack align="center" gap="md">
            <IconAlertCircle size="3rem" color="gray" />
            <Text size="lg" c="dimmed">No patient statistics available</Text>
            <Text size="sm" c="dimmed">Patient data is still loading or unavailable</Text>
          </Stack>
        </Center>
      </Modal>
    );
  }

  // Stat Card Component
  const StatCard = ({ 
    title, 
    value, 
    subtitle, 
    icon, 
    color, 
    trend, 
    trendValue 
  }: {
    title: string;
    value: string | number;
    subtitle?: string;
    icon: React.ReactNode;
    color: string;
    trend?: 'up' | 'down' | 'neutral';
    trendValue?: string;
  }) => (
    <Card withBorder p="lg" radius="md">
      <Group justify="space-between" align="flex-start">
        <div>
          <Text size="xs" c="dimmed" tt="uppercase" fw={700}>
            {title}
          </Text>
          <Text size="xl" fw={700} mt="xs">
            {value}
          </Text>
          {subtitle && (
            <Text size="sm" c="dimmed" mt="xs">
              {subtitle}
            </Text>
          )}
          {trend && trendValue && (
            <Group gap="xs" mt="xs">
              <ThemeIcon
                size="xs"
                variant="light"
                color={trend === 'up' ? 'green' : trend === 'down' ? 'red' : 'gray'}
              >
                {trend === 'up' ? (
                  <IconTrendingUp size="0.7rem" />
                ) : trend === 'down' ? (
                  <IconTrendingDown size="0.7rem" />
                ) : (
                  <IconActivity size="0.7rem" />
                )}
              </ThemeIcon>
              <Text
                size="xs"
                c={trend === 'up' ? 'green' : trend === 'down' ? 'red' : 'dimmed'}
                fw={500}
              >
                {trendValue}
              </Text>
            </Group>
          )}
        </div>
        <ThemeIcon size="xl" variant="light" color={color}>
          {icon}
        </ThemeIcon>
      </Group>
    </Card>
  );

  // Overview Tab
  const OverviewTab = () => (
    <Stack gap="lg">
      {/* Key Metrics */}
      <SimpleGrid cols={{ base: 1, sm: 2, lg: 4 }}>
        <StatCard
          title="Total Patients"
          value={derivedStats.totalPatients.toLocaleString()}
          subtitle="All registered patients"
          icon={<IconUsers size="1.5rem" />}
          color="blue"
          trend="up"
          trendValue={`+${derivedStats.newPatientsThisMonth} this month`}
        />
        <StatCard
          title="New This Month"
          value={derivedStats.newPatientsThisMonth}
          subtitle="New registrations"
          icon={<IconUserPlus size="1.5rem" />}
          color="green"
          trend={derivedStats.growthRate >= 0 ? 'up' : 'down'}
          trendValue={`${derivedStats.growthRate >= 0 ? '+' : ''}${derivedStats.growthRate.toFixed(1)}%`}
        />
        <StatCard
          title="Active Patients"
          value={stats.activePatients.toLocaleString()}
          subtitle="Currently under care"
          icon={<IconHeart size="1.5rem" />}
          color="red"
        />
        <StatCard
          title="Average Age"
          value={`${stats.averageAge} years`}
          subtitle="Patient demographics"
          icon={<IconCalendar size="1.5rem" />}
          color="purple"
        />
      </SimpleGrid>

      {/* Gender and Age Distribution */}
      <Grid>
        <Grid.Col span={{ base: 12, md: 6 }}>
          <Paper p="md" withBorder>
            <Title order={4} mb="md">Gender Distribution</Title>
            <Stack gap="md">
              <div>
                <Group justify="space-between" mb="xs">
                  <Text size="sm">Male</Text>
                  <Text size="sm" fw={500}>
                    {stats.genderDistribution.male} ({((stats.genderDistribution.male / derivedStats.totalPatients) * 100).toFixed(1)}%)
                  </Text>
                </Group>
                <Progress 
                  value={(stats.genderDistribution.male / derivedStats.totalPatients) * 100} 
                  color="blue" 
                  size="lg" 
                />
              </div>
              
              <div>
                <Group justify="space-between" mb="xs">
                  <Text size="sm">Female</Text>
                  <Text size="sm" fw={500}>
                    {stats.genderDistribution.female} ({((stats.genderDistribution.female / derivedStats.totalPatients) * 100).toFixed(1)}%)
                  </Text>
                </Group>
                <Progress 
                  value={(stats.genderDistribution.female / derivedStats.totalPatients) * 100} 
                  color="pink" 
                  size="lg" 
                />
              </div>
              
              {stats.genderDistribution.other > 0 && (
                <div>
                  <Group justify="space-between" mb="xs">
                    <Text size="sm">Other</Text>
                    <Text size="sm" fw={500}>
                      {stats.genderDistribution.other} ({((stats.genderDistribution.other / derivedStats.totalPatients) * 100).toFixed(1)}%)
                    </Text>
                  </Group>
                  <Progress 
                    value={(stats.genderDistribution.other / derivedStats.totalPatients) * 100} 
                    color="gray" 
                    size="lg" 
                  />
                </div>
              )}
            </Stack>
          </Paper>
        </Grid.Col>

        <Grid.Col span={{ base: 12, md: 6 }}>
          <Paper p="md" withBorder>
            <Title order={4} mb="md">Age Groups</Title>
            <Group justify="center" mb="md">
              <RingProgress
                size={200}
                thickness={20}
                sections={[
                  { value: (derivedStats.ageGroups.pediatric / derivedStats.totalPatients) * 100, color: 'cyan', tooltip: 'Pediatric (0-17)' },
                  { value: (derivedStats.ageGroups.adult / derivedStats.totalPatients) * 100, color: 'blue', tooltip: 'Adult (18-64)' },
                  { value: (derivedStats.ageGroups.senior / derivedStats.totalPatients) * 100, color: 'orange', tooltip: 'Senior (65+)' }
                ]}
                label={
                  <Center>
                    <div>
                      <Text size="xl" fw={700} ta="center">
                        {derivedStats.totalPatients}
                      </Text>
                      <Text size="sm" c="dimmed" ta="center">
                        Total Patients
                      </Text>
                    </div>
                  </Center>
                }
              />
            </Group>
            <Stack gap="xs">
              <Group justify="space-between">
                <Group gap="xs">
                  <div style={{ width: 12, height: 12, backgroundColor: 'var(--mantine-color-cyan-6)', borderRadius: 2 }} />
                  <Text size="sm">Pediatric (0-17)</Text>
                </Group>
                <Text size="sm" fw={500}>{derivedStats.ageGroups.pediatric}</Text>
              </Group>
              <Group justify="space-between">
                <Group gap="xs">
                  <div style={{ width: 12, height: 12, backgroundColor: 'var(--mantine-color-blue-6)', borderRadius: 2 }} />
                  <Text size="sm">Adult (18-64)</Text>
                </Group>
                <Text size="sm" fw={500}>{derivedStats.ageGroups.adult}</Text>
              </Group>
              <Group justify="space-between">
                <Group gap="xs">
                  <div style={{ width: 12, height: 12, backgroundColor: 'var(--mantine-color-orange-6)', borderRadius: 2 }} />
                  <Text size="sm">Senior (65+)</Text>
                </Group>
                <Text size="sm" fw={500}>{derivedStats.ageGroups.senior}</Text>
              </Group>
            </Stack>
          </Paper>
        </Grid.Col>
      </Grid>

      {/* Visit Trends */}
      <Paper p="md" withBorder>
        <Group justify="space-between" mb="md">
          <Title order={4}>Visit Trends</Title>
          <Select
            data={timeframes}
            value={selectedTimeframe}
            onChange={(value) => setSelectedTimeframe(value || '30d')}
            size="sm"
            w={150}
          />
        </Group>
        <Stack gap="sm">
          {stats.visitTrends.map((trend, index) => (
            <Group key={index} justify="space-between">
              <Text size="sm">{formatDate(new Date(trend.date))}</Text>
              <Group gap="sm" align="center">
                <Progress 
                  value={(trend.count / 200) * 100} 
                  size="sm" 
                  w={100}
                  color="blue"
                />
                <Text size="sm" fw={500} w={40} ta="right">{trend.count}</Text>
              </Group>
            </Group>
          ))}
        </Stack>
      </Paper>
    </Stack>
  );

  // Medical Analytics Tab
  const MedicalTab = () => (
    <Stack gap="lg">
      {/* Blood Group Distribution */}
      <Paper p="md" withBorder>
        <Title order={4} mb="md">Blood Group Distribution</Title>
        <Grid>
          {Object.entries(stats.bloodGroupDistribution).map(([bloodGroup, count]) => (
            <Grid.Col key={bloodGroup} span={{ base: 6, md: 3 }}>
              <Card withBorder p="md" ta="center">
                <Text size="xl" fw={700} c="red">{bloodGroup}</Text>
                <Text size="sm" c="dimmed" mt="xs">{count} patients</Text>
                <Text size="xs" c="dimmed">
                  {((count / derivedStats.totalPatients) * 100).toFixed(1)}%
                </Text>
              </Card>
            </Grid.Col>
          ))}
        </Grid>
      </Paper>

      {/* Top Medical Conditions */}
      {derivedStats.topConditions.length > 0 && (
        <Paper p="md" withBorder>
          <Title order={4} mb="md">Most Common Chronic Conditions</Title>
          <Stack gap="md">
            {derivedStats.topConditions.map((condition, index) => (
              <Group key={condition.condition} justify="space-between" align="center">
                <Group>
                  <Badge size="sm" variant="light" color="orange">
                    #{index + 1}
                  </Badge>
                  <Text size="sm" fw={500}>{condition.condition}</Text>
                </Group>
                <Group gap="sm" align="center">
                  <Progress 
                    value={condition.percentage} 
                    size="sm" 
                    w={100}
                    color="orange"
                  />
                  <Text size="sm" fw={500} w={60} ta="right">
                    {condition.count} ({condition.percentage.toFixed(1)}%)
                  </Text>
                </Group>
              </Group>
            ))}
          </Stack>
        </Paper>
      )}

      {/* Medical Alerts Summary */}
      <Grid>
        <Grid.Col span={{ base: 12, md: 6 }}>
          <Paper p="md" withBorder>
            <Group mb="md">
              <IconAlertCircle size="1.2rem" color="red" />
              <Title order={4}>Allergy Alert Summary</Title>
            </Group>
            <Stack gap="xs">
              <Group justify="space-between">
                <Text size="sm">Patients with Known Allergies</Text>
                <Badge color="red" variant="light">
                  {patients.filter(p => p.allergies.length > 0).length}
                </Badge>
              </Group>
              <Group justify="space-between">
                <Text size="sm">No Known Allergies</Text>
                <Badge color="green" variant="light">
                  {patients.filter(p => p.allergies.length === 0).length}
                </Badge>
              </Group>
              <Progress 
                value={(patients.filter(p => p.allergies.length > 0).length / derivedStats.totalPatients) * 100}
                color="red"
                size="sm"
                mt="sm"
              />
            </Stack>
          </Paper>
        </Grid.Col>

        <Grid.Col span={{ base: 12, md: 6 }}>
          <Paper p="md" withBorder>
            <Group mb="md">
              <IconMedicalCross size="1.2rem" color="orange" />
              <Title order={4}>Chronic Disease Summary</Title>
            </Group>
            <Stack gap="xs">
              <Group justify="space-between">
                <Text size="sm">Patients with Chronic Conditions</Text>
                <Badge color="orange" variant="light">
                  {patients.filter(p => p.chronicDiseases.length > 0).length}
                </Badge>
              </Group>
              <Group justify="space-between">
                <Text size="sm">No Chronic Conditions</Text>
                <Badge color="green" variant="light">
                  {patients.filter(p => p.chronicDiseases.length === 0).length}
                </Badge>
              </Group>
              <Progress 
                value={(patients.filter(p => p.chronicDiseases.length > 0).length / derivedStats.totalPatients) * 100}
                color="orange"
                size="sm"
                mt="sm"
              />
            </Stack>
          </Paper>
        </Grid.Col>
      </Grid>
    </Stack>
  );

  // Insurance Analytics Tab
  const InsuranceTab = () => (
    <Stack gap="lg">
      {/* Insurance Coverage Overview */}
      <Paper p="md" withBorder>
        <Title order={4} mb="md">Insurance Coverage Overview</Title>
        <Group justify="center" mb="md">
          <RingProgress
            size={200}
            thickness={20}
            sections={[
              { 
                value: (stats.insuranceDistribution.insured / derivedStats.totalPatients) * 100, 
                color: 'green', 
                tooltip: 'Insured Patients' 
              },
              { 
                value: (stats.insuranceDistribution.uninsured / derivedStats.totalPatients) * 100, 
                color: 'red', 
                tooltip: 'Uninsured Patients' 
              }
            ]}
            label={
              <Center>
                <div>
                  <Text size="xl" fw={700} ta="center" c="green">
                    {((stats.insuranceDistribution.insured / derivedStats.totalPatients) * 100).toFixed(1)}%
                  </Text>
                  <Text size="sm" c="dimmed" ta="center">
                    Insured
                  </Text>
                </div>
              </Center>
            }
          />
        </Group>
        
        <Grid>
          <Grid.Col span={6}>
            <Group justify="space-between">
              <Group gap="xs">
                <ThemeIcon size="sm" color="green" variant="light">
                  <IconShield size="0.8rem" />
                </ThemeIcon>
                <Text size="sm">Insured</Text>
              </Group>
              <Text size="sm" fw={500}>{stats.insuranceDistribution.insured}</Text>
            </Group>
          </Grid.Col>
          <Grid.Col span={6}>
            <Group justify="space-between">
              <Group gap="xs">
                <ThemeIcon size="sm" color="red" variant="light">
                  <IconAlertCircle size="0.8rem" />
                </ThemeIcon>
                <Text size="sm">Uninsured</Text>
              </Group>
              <Text size="sm" fw={500}>{stats.insuranceDistribution.uninsured}</Text>
            </Group>
          </Grid.Col>
        </Grid>
      </Paper>

      {/* Insurance Type Breakdown */}
      <Paper p="md" withBorder>
        <Title order={4} mb="md">Insurance Type Distribution</Title>
        <Alert icon={<IconShield size="1rem" />} color="blue" mb="md">
          <Stack gap="xs">
            <Group justify="space-between">
              <Text size="sm">Government Insurance</Text>
              <Text size="sm" fw={500}>45% (1,281 patients)</Text>
            </Group>
            <Group justify="space-between">
              <Text size="sm">Private Insurance</Text>
              <Text size="sm" fw={500}>35% (996 patients)</Text>
            </Group>
            <Group justify="space-between">
              <Text size="sm">Corporate Insurance</Text>
              <Text size="sm" fw={500}>20% (569 patients)</Text>
            </Group>
          </Stack>
        </Alert>
      </Paper>

      {/* Payment Analysis */}
      <Paper p="md" withBorder>
        <Title order={4} mb="md">Payment Method Analysis</Title>
        <SimpleGrid cols={{ base: 1, md: 3 }}>
          <Card withBorder p="md" ta="center">
            <ThemeIcon size="xl" variant="light" color="green" mx="auto" mb="sm">
              <IconShield size="1.5rem" />
            </ThemeIcon>
            <Text size="xl" fw={700} c="green">75%</Text>
            <Text size="sm" c="dimmed">Insurance Claims</Text>
          </Card>
          
          <Card withBorder p="md" ta="center">
            <ThemeIcon size="xl" variant="light" color="blue" mx="auto" mb="sm">
              <IconClock size="1.5rem" />
            </ThemeIcon>
            <Text size="xl" fw={700} c="blue">20%</Text>
            <Text size="sm" c="dimmed">Self Pay</Text>
          </Card>
          
          <Card withBorder p="md" ta="center">
            <ThemeIcon size="xl" variant="light" color="orange" mx="auto" mb="sm">
              <IconAlertCircle size="1.5rem" />
            </ThemeIcon>
            <Text size="xl" fw={700} c="orange">5%</Text>
            <Text size="sm" c="dimmed">Payment Plans</Text>
          </Card>
        </SimpleGrid>
      </Paper>
    </Stack>
  );

  return (
    <Modal
      opened={opened}
      onClose={onClose}
      size="xl"
      title={
        <div>
          <Title order={2}>Patient Analytics</Title>
          <Text c="dimmed" size="sm">Comprehensive insights and statistics about patient data</Text>
        </div>
      }
      padding="lg"
    >
      <Group justify="space-between" align="flex-start" mb="md">
        <Group>
          <ActionIcon variant="light" size="lg">
            <IconRefresh size="1.2rem" />
          </ActionIcon>
          <Menu shadow="md" width={200}>
            <Menu.Target>
              <Button variant="outline" leftSection={<IconDownload size="1rem" />}>
                Export
              </Button>
            </Menu.Target>
            <Menu.Dropdown>
              <Menu.Item leftSection={<IconDownload size="0.9rem" />}>
                Export as PDF
              </Menu.Item>
              <Menu.Item leftSection={<IconDownload size="0.9rem" />}>
                Export as Excel
              </Menu.Item>
              <Menu.Item leftSection={<IconPrinter size="0.9rem" />}>
                Print Report
              </Menu.Item>
              <Menu.Item leftSection={<IconShare size="0.9rem" />}>
                Share Report
              </Menu.Item>
            </Menu.Dropdown>
          </Menu>
        </Group>
      </Group>

      <Tabs value={activeTab} onChange={(value) => setActiveTab(value || 'overview')}>
        <Tabs.List>
            <Tabs.Tab value="overview" leftSection={<IconChartBar size="0.8rem" />}>
              Overview
            </Tabs.Tab>
            <Tabs.Tab value="medical" leftSection={<IconStethoscope size="0.8rem" />}>
              Medical Analytics
            </Tabs.Tab>
            <Tabs.Tab value="insurance" leftSection={<IconShield size="0.8rem" />}>
              Insurance Analytics
            </Tabs.Tab>
          </Tabs.List>

          <Tabs.Panel value="overview" pt="md">
            <OverviewTab />
          </Tabs.Panel>

          <Tabs.Panel value="medical" pt="md">
            <MedicalTab />
          </Tabs.Panel>

          <Tabs.Panel value="insurance" pt="md">
            <InsuranceTab />
          </Tabs.Panel>
        </Tabs>
    </Modal>
  );
}

'use client';

import React, { useState } from 'react';
import {
  Modal,
  Stack,
  Group,
  Text,
  Select,
  Button,
  Paper,
  Title,
  Grid,
  Checkbox,
  Alert,
  Tabs,
  Progress,
  Badge,
  Card,
  Divider,
  MultiSelect,
  DateInput,
  Switch,
  TextInput,
  Textarea,
  ActionIcon,
  Menu,
  List,
  Timeline,
  ThemeIcon
} from '@mantine/core';
import { useForm } from '@mantine/form';
import { DatePickerInput } from '@mantine/dates';
import { notifications } from '@mantine/notifications';
import {
  IconDownload,
  IconFileExport,
  // IconFilePdf, // not available
  IconFileSpreadsheet,
  IconPrinter,
  IconMail,
  IconShare,
  IconCalendar,
  IconFilter,
  IconSettings,
  IconCheck,
  IconClock,
  IconFileText,
  IconChartBar,
  IconUser,
  IconStethoscope,
  IconShield,
  IconHistory,
  IconAlertCircle,
  IconReportAnalytics
} from '@tabler/icons-react';
import { PatientExportOptions, PatientReport, PatientSearchCriteria } from '../../types/patient';

interface PatientExportReportProps {
  opened: boolean;
  onClose: () => void;
  onExport: (options: PatientExportOptions) => Promise<void>;
  onGenerateReport: (reportType: string, patientIds?: string[]) => Promise<PatientReport>;
  patientCount: number;
  selectedPatientIds?: string[];
}

const exportFormats = [
  { value: 'csv', label: 'CSV File', icon: <IconFileText size="1rem" /> },
  { value: 'excel', label: 'Excel Spreadsheet', icon: <IconFileSpreadsheet size="1rem" /> },
  { value: 'pdf', label: 'PDF Document', icon: <IconFileText size="1rem" /> }
];

const availableFields = [
  { group: 'Basic Information', fields: [
    { value: 'patientId', label: 'Patient ID' },
    { value: 'firstName', label: 'First Name' },
    { value: 'lastName', label: 'Last Name' },
    { value: 'age', label: 'Age' },
    { value: 'gender', label: 'Gender' },
    { value: 'dateOfBirth', label: 'Date of Birth' },
    { value: 'bloodGroup', label: 'Blood Group' },
    { value: 'maritalStatus', label: 'Marital Status' }
  ]},
  { group: 'Contact Information', fields: [
    { value: 'phone', label: 'Phone Number' },
    { value: 'email', label: 'Email Address' },
    { value: 'address', label: 'Address' },
    { value: 'emergencyContact', label: 'Emergency Contact' }
  ]},
  { group: 'Medical Information', fields: [
    { value: 'allergies', label: 'Allergies' },
    { value: 'chronicDiseases', label: 'Chronic Diseases' },
    { value: 'currentMedications', label: 'Current Medications' },
    { value: 'lastVisitDate', label: 'Last Visit Date' },
    { value: 'totalVisits', label: 'Total Visits' }
  ]},
  { group: 'Insurance Information', fields: [
    { value: 'insuranceProvider', label: 'Insurance Provider' },
    { value: 'policyNumber', label: 'Policy Number' },
    { value: 'coverageAmount', label: 'Coverage Amount' },
    { value: 'insuranceType', label: 'Insurance Type' }
  ]},
  { group: 'System Information', fields: [
    { value: 'registrationDate', label: 'Registration Date' },
    { value: 'status', label: 'Status' },
    { value: 'createdBy', label: 'Created By' },
    { value: 'updatedAt', label: 'Last Updated' }
  ]}
];

const reportTypes = [
  {
    type: 'demographics',
    title: 'Demographics Report',
    description: 'Statistical breakdown of patient demographics including age groups, gender distribution, and geographic data',
    icon: <IconUser size="1.2rem" />,
    color: 'blue'
  },
  {
    type: 'visit_summary',
    title: 'Visit Summary Report',
    description: 'Comprehensive analysis of patient visits, appointment patterns, and healthcare utilization',
    icon: <IconStethoscope size="1.2rem" />,
    color: 'green'
  },
  {
    type: 'medical_summary',
    title: 'Medical Summary Report',
    description: 'Overview of medical conditions, treatments, medications, and health outcomes',
    icon: <IconHistory size="1.2rem" />,
    color: 'orange'
  },
  {
    type: 'insurance_summary',
    title: 'Insurance Summary Report',
    description: 'Analysis of insurance coverage, billing patterns, and payment trends',
    icon: <IconShield size="1.2rem" />,
    color: 'purple'
  }
];

export default function PatientExportReport({
  opened,
  onClose,
  onExport,
  onGenerateReport,
  patientCount,
  selectedPatientIds = []
}: PatientExportReportProps) {
  const [activeTab, setActiveTab] = useState('export');
  const [loading, setLoading] = useState(false);
  const [exportProgress, setExportProgress] = useState(0);
  const [generatingReport, setGeneratingReport] = useState<string | null>(null);

  const exportForm = useForm<PatientExportOptions>({
    initialValues: {
      format: 'csv',
      includeFields: ['patientId', 'firstName', 'lastName', 'phone', 'email', 'registrationDate'],
      includeVisitHistory: false,
      includeMedicalHistory: false,
      includeDocuments: false,
      dateRange: undefined
    },
    validate: {
      format: (value) => (!value ? 'Export format is required' : null),
      includeFields: (value) => (!value.length ? 'At least one field must be selected' : null)
    }
  });

  const handleExport = async (values: PatientExportOptions) => {
    try {
      setLoading(true);
      setExportProgress(0);

      // Simulate progress updates
      const progressInterval = setInterval(() => {
        setExportProgress(prev => {
          if (prev >= 90) {
            clearInterval(progressInterval);
            return prev;
          }
          return prev + 10;
        });
      }, 200);

      await onExport(values);
      
      clearInterval(progressInterval);
      setExportProgress(100);

      notifications.show({
        title: 'Export Successful',
        message: `Patient data has been exported to ${values.format.toUpperCase()} format successfully.`,
        color: 'green',
        icon: <IconCheck size="1rem" />
      });

      setTimeout(() => {
        setExportProgress(0);
        onClose();
      }, 1000);

    } catch (error) {
      notifications.show({
        title: 'Export Failed',
        message: 'Failed to export patient data. Please try again.',
        color: 'red',
        icon: <IconAlertCircle size="1rem" />
      });
    } finally {
      setLoading(false);
    }
  };

  const handleGenerateReport = async (reportType: string) => {
    try {
      setGeneratingReport(reportType);
      const report = await onGenerateReport(reportType, selectedPatientIds);
      
      notifications.show({
        title: 'Report Generated',
        message: `${reportTypes.find(r => r.type === reportType)?.title} has been generated successfully.`,
        color: 'green',
        icon: <IconCheck size="1rem" />
      });

      // In a real application, this would typically open or download the report
      console.log('Generated report:', report);

    } catch (error) {
      notifications.show({
        title: 'Report Generation Failed',
        message: 'Failed to generate report. Please try again.',
        color: 'red',
        icon: <IconAlertCircle size="1rem" />
      });
    } finally {
      setGeneratingReport(null);
    }
  };

  const selectAllFields = (groupFields: string[]) => {
    const currentFields = exportForm.values.includeFields;
    const allGroupSelected = groupFields.every(field => currentFields.includes(field));
    
    if (allGroupSelected) {
      // Deselect all from this group
      exportForm.setFieldValue('includeFields', currentFields.filter(field => !groupFields.includes(field)));
    } else {
      // Select all from this group
      const newFields = [...new Set([...currentFields, ...groupFields])];
      exportForm.setFieldValue('includeFields', newFields);
    }
  };

  const getSelectedFieldsCount = () => exportForm.values.includeFields.length;

  // Export Tab
  const ExportTab = () => (
    <form onSubmit={exportForm.onSubmit(handleExport)}>
      <Stack gap="lg">
        {/* Export Summary */}
        <Alert icon={<IconFileExport size="1rem" />} color="blue">
          <Group justify="space-between">
            <Text size="sm">
              Ready to export {selectedPatientIds.length > 0 ? selectedPatientIds.length : patientCount} patient records
            </Text>
            <Badge variant="light">
              {getSelectedFieldsCount()} fields selected
            </Badge>
          </Group>
        </Alert>

        {/* Format Selection */}
        <Paper p="md" withBorder>
          <Title order={4} mb="md">Export Format</Title>
          <Grid>
            {exportFormats.map((format) => (
              <Grid.Col key={format.value} span={{ base: 12, md: 4 }}>
                <Card
                  withBorder
                  p="md"
                  style={{
                    cursor: 'pointer',
                    backgroundColor: exportForm.values.format === format.value ? 'var(--mantine-color-blue-0)' : undefined,
                    borderColor: exportForm.values.format === format.value ? 'var(--mantine-color-blue-5)' : undefined
                  }}
                  onClick={() => exportForm.setFieldValue('format', format.value as any)}
                >
                  <Group>
                    <ThemeIcon
                      variant={exportForm.values.format === format.value ? 'filled' : 'light'}
                      color="blue"
                    >
                      {format.icon}
                    </ThemeIcon>
                    <div>
                      <Text fw={500} size="sm">{format.label}</Text>
                    </div>
                  </Group>
                </Card>
              </Grid.Col>
            ))}
          </Grid>
        </Paper>

        {/* Field Selection */}
        <Paper p="md" withBorder>
          <Group justify="space-between" mb="md">
            <Title order={4}>Select Fields to Export</Title>
            <Text size="sm" c="dimmed">
              {getSelectedFieldsCount()} of {availableFields.reduce((sum, group) => sum + group.fields.length, 0)} fields selected
            </Text>
          </Group>
          
          <Stack gap="md">
            {availableFields.map((group) => {
              const groupFields = group.fields.map(f => f.value);
              const allSelected = groupFields.every(field => exportForm.values.includeFields.includes(field));
              const someSelected = groupFields.some(field => exportForm.values.includeFields.includes(field));
              
              return (
                <div key={group.group}>
                  <Group justify="space-between" mb="sm">
                    <Text fw={500} size="sm">{group.group}</Text>
                    <Button
                      variant="subtle"
                      size="xs"
                      onClick={() => selectAllFields(groupFields)}
                    >
                      {allSelected ? 'Deselect All' : 'Select All'}
                    </Button>
                  </Group>
                  <Grid>
                    {group.fields.map((field) => (
                      <Grid.Col key={field.value} span={{ base: 12, md: 6 }}>
                        <Checkbox
                          label={field.label}
                          checked={exportForm.values.includeFields.includes(field.value)}
                          onChange={(event) => {
                            const currentFields = exportForm.values.includeFields;
                            if (event.currentTarget.checked) {
                              exportForm.setFieldValue('includeFields', [...currentFields, field.value]);
                            } else {
                              exportForm.setFieldValue('includeFields', currentFields.filter(f => f !== field.value));
                            }
                          }}
                        />
                      </Grid.Col>
                    ))}
                  </Grid>
                  {group !== availableFields[availableFields.length - 1] && <Divider mt="md" />}
                </div>
              );
            })}
          </Stack>
        </Paper>

        {/* Additional Options */}
        <Paper p="md" withBorder>
          <Title order={4} mb="md">Additional Data</Title>
          <Stack gap="sm">
            <Checkbox
              label="Include Visit History"
              description="Export patient visit records and medical encounters"
              {...exportForm.getInputProps('includeVisitHistory', { type: 'checkbox' })}
            />
            <Checkbox
              label="Include Medical History"
              description="Export medical history, allergies, and chronic conditions"
              {...exportForm.getInputProps('includeMedicalHistory', { type: 'checkbox' })}
            />
            <Checkbox
              label="Include Documents"
              description="Export patient document references and metadata"
              {...exportForm.getInputProps('includeDocuments', { type: 'checkbox' })}
            />
          </Stack>
        </Paper>

        {/* Date Range Filter */}
        <Paper p="md" withBorder>
          <Title order={4} mb="md">Date Range Filter (Optional)</Title>
          <Grid>
            <Grid.Col span={{ base: 12, md: 6 }}>
              <DatePickerInput
                label="Start Date"
                placeholder="Select start date"
                value={exportForm.values.dateRange?.startDate}
                onChange={(date) => exportForm.setFieldValue('dateRange.startDate', date)}
                leftSection={<IconCalendar size="1rem" />}
              />
            </Grid.Col>
            <Grid.Col span={{ base: 12, md: 6 }}>
              <DatePickerInput
                label="End Date"
                placeholder="Select end date"
                value={exportForm.values.dateRange?.endDate}
                onChange={(date) => exportForm.setFieldValue('dateRange.endDate', date)}
                leftSection={<IconCalendar size="1rem" />}
              />
            </Grid.Col>
          </Grid>
        </Paper>

        {/* Export Progress */}
        {loading && (
          <Paper p="md" withBorder>
            <Group justify="space-between" mb="sm">
              <Text fw={500}>Exporting Data...</Text>
              <Text size="sm" c="dimmed">{exportProgress}%</Text>
            </Group>
            <Progress value={exportProgress} animated />
          </Paper>
        )}

        {/* Action Buttons */}
        <Group justify="flex-end" mt="xl">
          <Button variant="outline" onClick={onClose} disabled={loading}>
            Cancel
          </Button>
          <Button
            type="submit"
            loading={loading}
            leftSection={<IconDownload size="1rem" />}
          >
            Export Data
          </Button>
        </Group>
      </Stack>
    </form>
  );

  // Reports Tab
  const ReportsTab = () => (
    <Stack gap="lg">
      <Alert icon={<IconReportAnalytics size="1rem" />} color="blue">
        Generate comprehensive reports with insights and analytics about your patient data.
      </Alert>

      <Grid>
        {reportTypes.map((report) => (
          <Grid.Col key={report.type} span={{ base: 12, md: 6 }}>
            <Card withBorder p="lg" h="100%">
              <Group align="flex-start" mb="md">
                <ThemeIcon size="xl" variant="light" color={report.color}>
                  {report.icon}
                </ThemeIcon>
                <div style={{ flex: 1 }}>
                  <Text fw={600} mb="xs">{report.title}</Text>
                  <Text size="sm" c="dimmed">{report.description}</Text>
                </div>
              </Group>
              
              <Group justify="space-between" mt="auto">
                <Badge variant="light" color={report.color}>
                  {selectedPatientIds.length > 0 ? selectedPatientIds.length : patientCount} patients
                </Badge>
                <Button
                  size="sm"
                  variant="light"
                  color={report.color}
                  loading={generatingReport === report.type}
                  onClick={() => handleGenerateReport(report.type)}
                  leftSection={<IconDownload size="0.8rem" />}
                >
                  Generate
                </Button>
              </Group>
            </Card>
          </Grid.Col>
        ))}
      </Grid>

      {/* Report History */}
      <Paper p="md" withBorder>
        <Title order={4} mb="md">Recent Reports</Title>
        <Timeline active={1} bulletSize={24} lineWidth={2}>
          <Timeline.Item bullet={<IconFileText size="0.8rem" />} title="Demographics Report">
            <Text c="dimmed" size="sm">Generated on March 15, 2024</Text>
            <Text size="sm" mt={4}>Complete demographic analysis with 2,847 patients</Text>
            <Group mt="xs">
              <Button variant="subtle" size="xs" leftSection={<IconDownload size="0.7rem" />}>
                Download
              </Button>
              <Button variant="subtle" size="xs" leftSection={<IconShare size="0.7rem" />}>
                Share
              </Button>
            </Group>
          </Timeline.Item>
          
          <Timeline.Item bullet={<IconFileSpreadsheet size="0.8rem" />} title="Visit Summary Report">
            <Text c="dimmed" size="sm">Generated on March 10, 2024</Text>
            <Text size="sm" mt={4}>Monthly visit analysis and appointment trends</Text>
            <Group mt="xs">
              <Button variant="subtle" size="xs" leftSection={<IconDownload size="0.7rem" />}>
                Download
              </Button>
              <Button variant="subtle" size="xs" leftSection={<IconShare size="0.7rem" />}>
                Share
              </Button>
            </Group>
          </Timeline.Item>
          
          <Timeline.Item bullet={<IconChartBar size="0.8rem" />} title="Insurance Analysis">
            <Text c="dimmed" size="sm">Generated on March 5, 2024</Text>
            <Text size="sm" mt={4}>Insurance coverage and billing analysis</Text>
            <Group mt="xs">
              <Button variant="subtle" size="xs" leftSection={<IconDownload size="0.7rem" />}>
                Download
              </Button>
              <Button variant="subtle" size="xs" leftSection={<IconShare size="0.7rem" />}>
                Share
              </Button>
            </Group>
          </Timeline.Item>
        </Timeline>
      </Paper>
    </Stack>
  );

  return (
    <Modal
      opened={opened}
      onClose={onClose}
      title={
        <Group>
          <IconFileExport size="1.2rem" />
          <Text fw={600}>Export & Reports</Text>
        </Group>
      }
      size="xl"
    >
      <Tabs value={activeTab} onChange={(value) => setActiveTab(value || 'export')}>
        <Tabs.List>
          <Tabs.Tab value="export" leftSection={<IconDownload size="0.8rem" />}>
            Export Data
          </Tabs.Tab>
          <Tabs.Tab value="reports" leftSection={<IconReportAnalytics size="0.8rem" />}>
            Generate Reports
          </Tabs.Tab>
        </Tabs.List>

        <Tabs.Panel value="export" pt="md">
          <ExportTab />
        </Tabs.Panel>

        <Tabs.Panel value="reports" pt="md">
          <ReportsTab />
        </Tabs.Panel>
      </Tabs>
    </Modal>
  );
}

'use client';

import React, { useState, useEffect } from 'react';
import {
  Modal,
  Stack,
  Group,
  Text,
  TextInput,
  Select,
  Textarea,
  Button,
  Grid,
  Paper,
  Title,
  Divider,
  Alert,
  Tabs,
  FileInput,
  Checkbox,
  NumberInput,
  Badge,
  ActionIcon,
  Tooltip,
  Switch,
  TagsInput,
  Stepper,
  LoadingOverlay,
  Card
} from '@mantine/core';
import { useForm } from '@mantine/form';
import { useDisclosure } from '@mantine/hooks';
import { DatePickerInput } from '@mantine/dates';
import {
  IconUsers,
  IconCheck,
  IconX,
  IconAlertCircle,
  IconPhone,
  IconMail,
  IconMapPin,
  IconShieldX,
  IconFileText,
  IconPlus,
  IconTrash,
  IconCloudUpload,
  IconEye,
  IconCalendar,
  IconUpload
} from '@tabler/icons-react';
import { notifications } from '@mantine/notifications';
import { 
  CreatePatientDto, 
  UpdatePatientDto, 
  Patient, 
  InsuranceInfo 
} from '../../types/patient';
import { 
  Gender, 
  BloodGroup, 
  MaritalStatus, 
  ContactInfo, 
  Address 
} from '../../types/common';
import { formatPhoneNumber, isValidEmail, isValidPhoneNumber } from '../../lib/utils';

interface PatientFormProps {
  opened: boolean;
  onClose: () => void;
  patient?: Patient | null;
  onSubmit: (data: CreatePatientDto | UpdatePatientDto) => Promise<void>;
  loading?: boolean;
}

interface FormStep {
  label: string;
  description: string;
}

const steps: FormStep[] = [
  { label: 'Basic Info', description: 'Personal information' },
  { label: 'Contact', description: 'Contact details' },
  { label: 'Medical', description: 'Medical history' },
  { label: 'Insurance', description: 'Insurance information' },
  { label: 'Documents', description: 'Upload documents' },
  { label: 'Review & Submit', description: 'Review and submit' }
];

function PatientForm({ opened, onClose, patient, onSubmit, loading = false }: PatientFormProps) {
  const [activeStep, setActiveStep] = useState(0);
  const [uploading, setUploading] = useState(false);
  const [uploadedFiles, setUploadedFiles] = useState<File[]>([]);
  const [formLoading, { open: startLoading, close: stopLoading }] = useDisclosure(false);

  // Form initialization
  const form = useForm<CreatePatientDto>({
    initialValues: {
      firstName: '',
      lastName: '',
      middleName: '',
      dateOfBirth: new Date(),
      gender: Gender.MALE,
      bloodGroup: undefined,
      maritalStatus: undefined,
      contactInfo: {
        phone: '',
        email: '',
        alternatePhone: '',
        emergencyContact: {
          name: '',
          phone: '',
          relationship: ''
        }
      },
      address: {
        street: '',
        city: '',
        state: '',
        country: 'India',
        postalCode: '',
        landmark: ''
      },
      insuranceInfo: {
        insuranceType: undefined,
        insuranceProvider: '',
        policyNumber: '',
        policyHolderName: '',
        relationshipToPatient: '',
        coverageAmount: undefined,
        isActive: false
      },
      aadhaarNumber: '',
      otherIdNumber: '',
      otherIdType: undefined,
      allergies: [],
      chronicDiseases: [],
      currentMedications: [],
      occupation: '',
      religion: '',
      language: '',
      notes: ''
    },
    validate: {
      firstName: (value) => (value.trim().length < 2 ? 'First name must be at least 2 characters' : null),
      lastName: (value) => (value.trim().length < 2 ? 'Last name must be at least 2 characters' : null),
      middleName: (value) => {
        if (value && value.trim().length > 0 && value.trim().length < 2) {
          return 'Middle name must be at least 2 characters if provided';
        }
        return null;
      }
    }
  });

  // Load patient data if editing
  useEffect(() => {
    if (patient && opened) {
      // Reset to first step when editing
      setActiveStep(0);
      form.setValues({
        firstName: patient.firstName,
        lastName: patient.lastName,
        middleName: '', // Add middleName when loading patient data
        dateOfBirth: patient.dateOfBirth,
        gender: patient.gender,
        bloodGroup: patient.bloodGroup,
        maritalStatus: patient.maritalStatus,
        contactInfo: patient.contactInfo,
        address: patient.address,
        insuranceInfo: patient.insuranceInfo || {
          insuranceType: undefined,
          insuranceProvider: '',
          policyNumber: '',
          policyHolderName: '',
          relationshipToPatient: '',
          coverageAmount: undefined,
          isActive: false
        },
        aadhaarNumber: patient.aadhaarNumber || '',
        otherIdNumber: patient.otherIdNumber || '',
        otherIdType: patient.otherIdType,
        allergies: patient.allergies || [],
        chronicDiseases: patient.chronicDiseases || [],
        currentMedications: patient.currentMedications || [],
        occupation: patient.occupation || '',
        religion: patient.religion || '',
        language: patient.language || '',
        notes: patient.notes || ''
      });
    } else if (!patient && opened) {
      // Reset to first step for new patient
      setActiveStep(0);
      form.reset();
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [patient, opened]);

  const handleSubmit = async (values: CreatePatientDto) => {
    try {
      startLoading();
      const submitData = patient 
        ? { ...values, id: patient.id } as UpdatePatientDto
        : values;
      await onSubmit(submitData);
      
      notifications.show({
        title: patient ? 'Patient Updated' : 'Patient Created',
        message: `Patient ${values.firstName} ${values.lastName} has been ${patient ? 'updated' : 'created'} successfully.`,
        color: 'green'
      });
      
      handleClose();
    } catch (error) {
      notifications.show({
        title: 'Error',
        message: `Failed to ${patient ? 'update' : 'create'} patient. Please try again.`,
        color: 'red'
      });
    } finally {
      stopLoading();
    }
  };

  const handleClose = () => {
    form.reset();
    setActiveStep(0);
    setUploadedFiles([]);
    onClose();
  };

  const nextStep = () => {
    const currentStepValid = validateCurrentStep();
    if (currentStepValid) {
      setActiveStep((current) => (current < steps.length - 1 ? current + 1 : current));
    }
  };

  const prevStep = () => {
    setActiveStep((current) => (current > 0 ? current - 1 : current));
  };

  const validateCurrentStep = (): boolean => {
    switch (activeStep) {
      case 0: // Basic Info
        if (!form.values.firstName || form.values.firstName.trim().length < 2) {
          notifications.show({
            title: 'Validation Error',
            message: 'First name must be at least 2 characters',
            color: 'red',
            icon: <IconAlertCircle />
          });
          return false;
        }
        if (!form.values.lastName || form.values.lastName.trim().length < 2) {
          notifications.show({
            title: 'Validation Error',
            message: 'Last name must be at least 2 characters',
            color: 'red',
            icon: <IconAlertCircle />
          });
          return false;
        }
        if (!form.values.dateOfBirth) {
          notifications.show({
            title: 'Validation Error',
            message: 'Date of birth is required',
            color: 'red',
            icon: <IconAlertCircle />
          });
          return false;
        }
        return true;
      case 1: // Contact
        // More lenient phone validation - just check if it has enough digits
        const phoneDigits = form.values.contactInfo.phone.replace(/\D/g, '');
        if (!form.values.contactInfo.phone || phoneDigits.length < 10) {
          notifications.show({
            title: 'Validation Error',
            message: 'Please enter a valid 10-digit phone number',
            color: 'red',
            icon: <IconAlertCircle />
          });
          return false;
        }
        if (form.values.contactInfo.email && !isValidEmail(form.values.contactInfo.email)) {
          notifications.show({
            title: 'Validation Error',
            message: 'Please enter a valid email address',
            color: 'red',
            icon: <IconAlertCircle />
          });
          return false;
        }
        if (!form.values.address.street || form.values.address.street.trim().length < 3) {
          notifications.show({
            title: 'Validation Error',
            message: 'Street address is required (minimum 3 characters)',
            color: 'red',
            icon: <IconAlertCircle />
          });
          return false;
        }
        if (!form.values.address.city || form.values.address.city.trim().length < 2) {
          notifications.show({
            title: 'Validation Error',
            message: 'City is required',
            color: 'red',
            icon: <IconAlertCircle />
          });
          return false;
        }
        if (!form.values.address.state || form.values.address.state.trim().length < 2) {
          notifications.show({
            title: 'Validation Error',
            message: 'State is required',
            color: 'red',
            icon: <IconAlertCircle />
          });
          return false;
        }
        if (!form.values.address.postalCode || form.values.address.postalCode.trim().length < 4) {
          notifications.show({
            title: 'Validation Error',
            message: 'Postal code is required (minimum 4 characters)',
            color: 'red',
            icon: <IconAlertCircle />
          });
          return false;
        }
        return true;
      case 2: // Medical
        return true; // Medical info is optional
      case 3: // Insurance
        return true; // Insurance is optional
      case 4: // Documents
        return true; // Documents are optional
      case 5: // Review & Submit
        return true; // Review & Submit is optional
      default:
        return true;
    }
  };

  const handleFileUpload = async (files: File | File[] | null) => {
    if (!files) return;
    const fileArray = Array.isArray(files) ? files : [files];
    if (!fileArray.length) return;
    
    setUploading(true);
    try {
      // Simulate file upload
      await new Promise(resolve => setTimeout(resolve, 1000));
      setUploadedFiles(prev => [...prev, ...fileArray]);
      notifications.show({
        title: 'Files Uploaded',
        message: `${fileArray.length} file(s) uploaded successfully.`,
        color: 'green'
      });
    } catch (error) {
      notifications.show({
        title: 'Upload Failed',
        message: 'Failed to upload files. Please try again.',
        color: 'red'
      });
    } finally {
      setUploading(false);
    }
  };

  const removeUploadedFile = (index: number) => {
    setUploadedFiles(prev => prev.filter((_, i) => i !== index));
  };

  // Step 1: Basic Information
  const BasicInfoStep = React.useMemo(() => (
    <Grid>
      <Grid.Col span={{ base: 12, md: 6 }}>
        <TextInput
          label="First Name"
          placeholder="Enter first name"
          required
          {...form.getInputProps('firstName')}
        />
      </Grid.Col>
      <Grid.Col span={{ base: 12, md: 6 }}>
        <TextInput
          label="Last Name"
          placeholder="Enter last name"
          required
          {...form.getInputProps('lastName')}
        />
      </Grid.Col>
      
      <Grid.Col span={{ base: 12, md: 6 }}>
        <DatePickerInput
          label="Date of Birth"
          placeholder="Select date of birth"
          required
          maxDate={new Date()}
          {...form.getInputProps('dateOfBirth')}
          leftSection={<IconCalendar size="1rem" />}
        />
      </Grid.Col>
      <Grid.Col span={{ base: 12, md: 6 }}>
        <Select
          label="Gender"
          placeholder="Select gender"
          required
          data={[
            { value: Gender.MALE, label: 'Male' },
            { value: Gender.FEMALE, label: 'Female' },
            { value: Gender.OTHER, label: 'Other' }
          ]}
          {...form.getInputProps('gender')}
        />
      </Grid.Col>

      <Grid.Col span={{ base: 12, md: 6 }}>
        <Select
          label="Blood Group"
          placeholder="Select blood group"
          data={Object.values(BloodGroup).map(bg => ({ value: bg, label: bg }))}
          {...form.getInputProps('bloodGroup')}
          clearable
        />
      </Grid.Col>
      <Grid.Col span={{ base: 12, md: 6 }}>
        <Select
          label="Marital Status"
          placeholder="Select marital status"
          data={Object.values(MaritalStatus).map(ms => ({ value: ms, label: ms.replace('_', ' ') }))}
          {...form.getInputProps('maritalStatus')}
          clearable
        />
      </Grid.Col>

      <Grid.Col span={{ base: 12, md: 6 }}>
        <TextInput
          label="Occupation"
          placeholder="Enter occupation"
          {...form.getInputProps('occupation')}
        />
      </Grid.Col>
      <Grid.Col span={{ base: 12, md: 6 }}>
        <TextInput
          label="Religion"
          placeholder="Enter religion"
          {...form.getInputProps('religion')}
        />
      </Grid.Col>

      <Grid.Col span={12}>
        <TextInput
          label="Languages Known"
          placeholder="Enter languages (comma separated)"
          {...form.getInputProps('language')}
        />
      </Grid.Col>
    </Grid>
  ), [form]);

  // Step 2: Contact Information
  const ContactInfoStep = React.useMemo(() => (
    <Stack gap="lg">
      <Paper p="md" withBorder>
        <Group mb="md">
          <IconPhone size="1.2rem" />
          <Title order={4}>Primary Contact</Title>
        </Group>
        <Grid>
          <Grid.Col span={{ base: 12, md: 6 }}>
            <TextInput
              label="Phone Number"
              placeholder="+91 XXXXX XXXXX"
              required
              {...form.getInputProps('contactInfo.phone')}
              leftSection={<IconPhone size="1rem" />}
            />
          </Grid.Col>
          <Grid.Col span={{ base: 12, md: 6 }}>
            <TextInput
              label="Email Address"
              placeholder="email@example.com"
              {...form.getInputProps('contactInfo.email')}
              leftSection={<IconMail size="1rem" />}
            />
          </Grid.Col>
          <Grid.Col span={12}>
            <TextInput
              label="Alternate Phone"
              placeholder="+91 XXXXX XXXXX"
              {...form.getInputProps('contactInfo.alternatePhone')}
              leftSection={<IconPhone size="1rem" />}
            />
          </Grid.Col>
        </Grid>
      </Paper>

      <Paper p="md" withBorder>
        <Group mb="md">
          <IconMapPin size="1.2rem" />
          <Title order={4}>Address Information</Title>
        </Group>
        <Grid>
          <Grid.Col span={12}>
            <Textarea
              label="Street Address"
              placeholder="Enter complete street address"
              required
              {...form.getInputProps('address.street')}
              minRows={2}
            />
          </Grid.Col>
          <Grid.Col span={{ base: 12, md: 6 }}>
            <TextInput
              label="City"
              placeholder="Enter city"
              required
              {...form.getInputProps('address.city')}
            />
          </Grid.Col>
          <Grid.Col span={{ base: 12, md: 6 }}>
            <TextInput
              label="State"
              placeholder="Enter state"
              required
              {...form.getInputProps('address.state')}
            />
          </Grid.Col>
          <Grid.Col span={{ base: 12, md: 6 }}>
            <TextInput
              label="Postal Code"
              placeholder="Enter postal code"
              required
              {...form.getInputProps('address.postalCode')}
            />
          </Grid.Col>
          <Grid.Col span={{ base: 12, md: 6 }}>
            <TextInput
              label="Country"
              placeholder="Enter country"
              {...form.getInputProps('address.country')}
            />
          </Grid.Col>
          <Grid.Col span={12}>
            <TextInput
              label="Landmark"
              placeholder="Enter nearby landmark"
              {...form.getInputProps('address.landmark')}
            />
          </Grid.Col>
        </Grid>
      </Paper>

      <Paper p="md" withBorder>
        <Group mb="md">
          <IconAlertCircle size="1.2rem" />
          <Title order={4}>Emergency Contact</Title>
        </Group>
        <Grid>
          <Grid.Col span={{ base: 12, md: 6 }}>
            <TextInput
              label="Contact Name"
              placeholder="Enter emergency contact name"
              {...form.getInputProps('contactInfo.emergencyContact.name')}
            />
          </Grid.Col>
          <Grid.Col span={{ base: 12, md: 6 }}>
            <TextInput
              label="Contact Phone"
              placeholder="+91 XXXXX XXXXX"
              {...form.getInputProps('contactInfo.emergencyContact.phone')}
              leftSection={<IconPhone size="1rem" />}
            />
          </Grid.Col>
          <Grid.Col span={{ base: 12, md: 6 }}>
            <TextInput
              label="Relationship"
              placeholder="Enter relationship"
              {...form.getInputProps('contactInfo.emergencyContact.relationship')}
            />
          </Grid.Col>
        </Grid>
      </Paper>
    </Stack>
  ), [form]);

  // Step 3: Medical Information
  const MedicalInfoStep = React.useMemo(() => (
    <Stack gap="lg">
      <Paper p="md" withBorder>
        <Group mb="md">
          <IconAlertCircle size="1.2rem" color="red" />
          <Title order={4}>Allergies</Title>
        </Group>
        <TagsInput
          label="Known Allergies"
          description="Add allergies one by one"
          placeholder="Type allergy and press Enter"
          {...form.getInputProps('allergies')}
        />
      </Paper>

      <Paper p="md" withBorder>
        <Group mb="md">
          <IconFileText size="1.2rem" color="orange" />
          <Title order={4}>Chronic Diseases</Title>
        </Group>
        <TagsInput
          label="Chronic Diseases"
          description="Add chronic conditions one by one"
          placeholder="Type condition and press Enter"
          {...form.getInputProps('chronicDiseases')}
        />
      </Paper>

      <Paper p="md" withBorder>
        <Group mb="md">
          <IconFileText size="1.2rem" color="blue" />
          <Title order={4}>Current Medications</Title>
        </Group>
        <TagsInput
          label="Current Medications"
          description="Add current medications one by one"
          placeholder="Type medication and press Enter"
          {...form.getInputProps('currentMedications')}
        />
      </Paper>

      <Paper p="md" withBorder>
        <Group mb="md">
          <IconFileText size="1.2rem" />
          <Title order={4}>Identity Documents</Title>
        </Group>
        <Grid>
          <Grid.Col span={{ base: 12, md: 8 }}>
            <TextInput
              label="Aadhaar Number"
              placeholder="XXXX XXXX XXXX"
              {...form.getInputProps('aadhaarNumber')}
            />
          </Grid.Col>
          <Grid.Col span={{ base: 12, md: 6 }}>
            <Select
              label="Other ID Type"
              placeholder="Select ID type"
              data={[
                { value: 'pan', label: 'PAN Card' },
                { value: 'passport', label: 'Passport' },
                { value: 'driving_license', label: 'Driving License' },
                { value: 'voter_id', label: 'Voter ID' }
              ]}
              {...form.getInputProps('otherIdType')}
              clearable
            />
          </Grid.Col>
          <Grid.Col span={{ base: 12, md: 6 }}>
            <TextInput
              label="Other ID Number"
              placeholder="Enter ID number"
              {...form.getInputProps('otherIdNumber')}
            />
          </Grid.Col>
        </Grid>
      </Paper>

      <Paper p="md" withBorder>
        <Title order={4} mb="md">Additional Notes</Title>
        <Textarea
          label="Medical Notes"
          description="Any additional medical information"
          placeholder="Enter any additional notes"
          minRows={3}
          {...form.getInputProps('notes')}
        />
      </Paper>
    </Stack>
  ), [form]);

  // Step 4: Insurance Information
  const InsuranceStep = React.useMemo(() => (
    <Paper p="md" withBorder>
      <Group mb="md">
        <IconShieldX size="1.2rem" color="green" />
        <Title order={4}>Insurance Information</Title>
      </Group>
      <Alert color="blue" mb="md">
        Insurance information is optional but recommended for billing purposes.
      </Alert>
      
      <Grid>
        <Grid.Col span={{ base: 12, md: 6 }}>
          <Select
            label="Insurance Type"
            placeholder="Select insurance type"
            data={[
              { value: 'government', label: 'Government' },
              { value: 'private', label: 'Private' },
              { value: 'corporate', label: 'Corporate' },
              { value: 'self_pay', label: 'Self Pay' }
            ]}
            {...form.getInputProps('insuranceInfo.insuranceType')}
          />
        </Grid.Col>
        <Grid.Col span={{ base: 12, md: 6 }}>
          <TextInput
            label="Insurance Provider"
            placeholder="Enter insurance company name"
            {...form.getInputProps('insuranceInfo.insuranceProvider')}
          />
        </Grid.Col>
        <Grid.Col span={{ base: 12, md: 6 }}>
          <TextInput
            label="Policy Number"
            placeholder="Enter policy number"
            {...form.getInputProps('insuranceInfo.policyNumber')}
          />
        </Grid.Col>
        <Grid.Col span={{ base: 12, md: 6 }}>
          <TextInput
            label="Policy Holder Name"
            placeholder="Enter policy holder name"
            {...form.getInputProps('insuranceInfo.policyHolderName')}
          />
        </Grid.Col>
        <Grid.Col span={{ base: 12, md: 6 }}>
          <TextInput
            label="Relationship to Patient"
            placeholder="Self, Spouse, Parent, etc."
            {...form.getInputProps('insuranceInfo.relationshipToPatient')}
          />
        </Grid.Col>
        <Grid.Col span={{ base: 12, md: 6 }}>
          <NumberInput
            label="Coverage Amount"
            placeholder="Enter coverage amount"
            min={0}
            {...form.getInputProps('insuranceInfo.coverageAmount')}
            leftSection="√¢‚Äö¬π"
          />
        </Grid.Col>
      </Grid>
    </Paper>
  ), [form]);

  // Step 5: Document Upload
  const DocumentsStep = React.useMemo(() => (
    <Stack gap="lg">
      <Paper p="md" withBorder>
        <Group mb="md">
          <IconUpload size="1.2rem" />
          <Title order={4}>Upload Documents</Title>
        </Group>
        
        <FileInput
          label="Upload Patient Documents"
          description="Upload ID proofs, medical reports, insurance documents, etc."
          placeholder="Click to upload or drag files here"
          multiple
          accept="image/*,application/pdf,.doc,.docx"
          onChange={handleFileUpload}
          leftSection={<IconCloudUpload size="1rem" />}
        />

        {uploadedFiles.length > 0 && (
          <Stack mt="md">
            <Text fw={500}>Uploaded Files:</Text>
            {uploadedFiles.map((file, index) => (
              <Group key={index} justify="space-between" p="xs" bg="gray.0" style={{ borderRadius: 4 }}>
                <Group>
                  <IconFileText size="1rem" />
                  <div>
                    <Text size="sm" fw={500}>{file.name}</Text>
                    <Text size="xs" c="dimmed">{(file.size / 1024).toFixed(1)} KB</Text>
                  </div>
                </Group>
                <Group>
                  <ActionIcon variant="subtle" size="sm" onClick={() => removeUploadedFile(index)}>
                    <IconTrash size="0.8rem" />
                  </ActionIcon>
                </Group>
              </Group>
            ))}
          </Stack>
        )}
      </Paper>

      <Alert color="blue">
        <Group>
          <IconAlertCircle size="1rem" />
          <Text size="sm">
            Documents can also be uploaded after patient registration from the patient details page.
          </Text>
        </Group>
      </Alert>
    </Stack>
  ), [uploadedFiles, uploading]);
  // Step 5: Review & Submit
  const ReviewSubmitStep = React.useMemo(() => (
    <Stack gap="lg">
      <Paper p="md" withBorder>
        <Group mb="md">
          <IconCheck size="1.2rem" color="green" />
          <Title order={4}>Review & Submit</Title>
        </Group>

        <Alert color="blue" mb="md">
          Please review the information below before submitting. You can go back to make changes if needed.
        </Alert>

        <Grid>
          <Grid.Col span={12}>
            <Card withBorder>
              <Title order={5} mb="sm">Patient Information Summary</Title>
              <Stack gap="xs">
                <Group>
                  <Text fw={500}>Name:</Text>
                  <Text>{form.values.firstName} {form.values.lastName}</Text>
                </Group>
                <Group>
                  <Text fw={500}>Phone:</Text>
                  <Text>{form.values.contactInfo.phone}</Text>
                </Group>
                <Group>
                  <Text fw={500}>Email:</Text>
                  <Text>{form.values.contactInfo.email || 'Not provided'}</Text>
                </Group>
                <Group>
                  <Text fw={500}>Address:</Text>
                  <Text>{form.values.address.street}, {form.values.address.city}</Text>
                </Group>
              </Stack>
            </Card>
          </Grid.Col>

          <Grid.Col span={12}>
            <Card withBorder>
              <Title order={5} mb="sm">Documents</Title>
              {uploadedFiles.length > 0 ? (
                <Text c="green">‚úì {uploadedFiles.length} file(s) uploaded</Text>
              ) : (
                <Text c="dimmed">No documents uploaded (optional)</Text>
              )}
            </Card>
          </Grid.Col>
        </Grid>
      </Paper>

      <Alert color="green">
        <Group>
          <IconCheck size="1rem" />
          <Text size="sm">
            Ready to submit! Click &quot;Create Patient&quot; to complete the registration process.
          </Text>
        </Group>
      </Alert>
    </Stack>
  ), [form, uploadedFiles]);

  const getCurrentStepContent = () => {
    switch (activeStep) {
      case 0: return BasicInfoStep;
      case 1: return ContactInfoStep;
      case 2: return MedicalInfoStep;
      case 3: return InsuranceStep;
      case 4: return DocumentsStep;
      case 5: return ReviewSubmitStep;
      default: return null;
    }
  };

  return (
    <Modal
      key={patient?.id || 'new'}
      opened={opened}
      onClose={handleClose}
      title={
        <Group>
          <IconUsers size="1.2rem" />
          <Text fw={600}>{patient ? 'Edit Patient' : 'New Patient Registration'}</Text>
        </Group>
      }
      size="xl"
      closeOnClickOutside={false}
      closeOnEscape={!formLoading}
    >
      <form onSubmit={form.onSubmit((values: any) => handleSubmit(values as CreatePatientDto))}>
        <LoadingOverlay visible={formLoading} />
        
        <Stack gap="lg">
          <Stepper active={activeStep}>
            {steps.map((step, index) => (
              <Stepper.Step
                key={index}
                label={step.label}
                description={step.description}
              />
            ))}
          </Stepper>

          <Divider />

          {getCurrentStepContent()}

          <Group justify="space-between" mt="xl">
            <Button
              variant="outline"
              onClick={prevStep}
              disabled={activeStep === 0 || formLoading}
            >
              Previous
            </Button>

            <Group>
              {activeStep < steps.length - 1 ? (
                <Button onClick={nextStep} disabled={formLoading}>
                  Next
                </Button>
              ) : (
                <Button
                  type="submit"
                  loading={formLoading}
                  leftSection={<IconCheck size="1rem" />}
                  color="green"
                >
                  {patient ? 'Update Patient' : 'Create Patient'}
                </Button>
              )}
            </Group>
          </Group>
        </Stack>
      </form>
    </Modal>
  );
}

export default PatientForm;

'use client';

import React, { useState, useEffect } from 'react';
import {
  Modal,
  Stack,
  Group,
  Text,
  Button,
  Paper,
  Title,
  Grid,
  Card,
  Badge,
  Switch,
  TextInput,
  Alert,
  Tabs,
  Timeline,
  Table,
  ActionIcon,
  Menu,
  Progress,
  ThemeIcon,
  Avatar,
  Divider,
  Select,
  PasswordInput,
  Checkbox,
  NumberInput
} from '@mantine/core';
import { useForm } from '@mantine/form';
import { notifications } from '@mantine/notifications';
import {
  IconUserCheck,
  IconUserX,
  IconKey,
  IconMail,
  IconPhone,
  IconCalendar,
  IconLogin,
  IconLogout,
  IconSettings,
  IconShield,
  IconAlertCircle,
  IconCheck,
  IconX,
  IconEye,
  IconEdit,
  IconTrash,
  IconSend,
  IconLock,
  IconUnlock,
  IconRefresh,
  IconClock,
  IconBell,
  IconDeviceMobile,
  IconWorld,
  IconActivity,
  IconHistory
} from '@tabler/icons-react';
import { PatientPortalAccess, PatientPortalPreferences, Patient } from '../../types/patient';
import { formatDate } from '../../lib/utils';

interface PatientPortalAccessProps {
  opened: boolean;
  onClose: () => void;
  patient: Patient | null;
  portalAccess?: PatientPortalAccess;
  onEnableAccess: (patientId: string, preferences: PatientPortalPreferences) => Promise<void>;
  onDisableAccess: (patientId: string) => Promise<void>;
  onUpdatePreferences: (patientId: string, preferences: PatientPortalPreferences) => Promise<void>;
  onResetPassword: (patientId: string) => Promise<void>;
  onSendCredentials: (patientId: string, method: 'email' | 'sms') => Promise<void>;
}

const defaultPreferences: PatientPortalPreferences = {
  receiveEmailNotifications: true,
  receiveSmsNotifications: false,
  appointmentReminders: true,
  labResultNotifications: true,
  prescriptionRefillReminders: true,
  languagePreference: 'English',
  timeZone: 'Asia/Kolkata'
};

export default function PatientPortalAccess({
  opened,
  onClose,
  patient,
  portalAccess,
  onEnableAccess,
  onDisableAccess,
  onUpdatePreferences,
  onResetPassword,
  onSendCredentials
}: PatientPortalAccessProps) {
  const [activeTab, setActiveTab] = useState('overview');
  const [loading, setLoading] = useState(false);
  const [actionLoading, setActionLoading] = useState<string | null>(null);

  const preferencesForm = useForm<PatientPortalPreferences>({
    initialValues: portalAccess?.preferences || defaultPreferences
  });

  useEffect(() => {
    if (portalAccess?.preferences) {
      preferencesForm.setValues(portalAccess.preferences);
    }
  }, [portalAccess]);

  const handleEnableAccess = async () => {
    if (!patient) return;
    
    try {
      setActionLoading('enable');
      await onEnableAccess(patient.id, preferencesForm.values);
      
      notifications.show({
        title: 'Portal Access Enabled',
        message: 'Patient portal access has been enabled successfully.',
        color: 'green',
        icon: <IconCheck size="1rem" />
      });
    } catch (error) {
      notifications.show({
        title: 'Error',
        message: 'Failed to enable portal access. Please try again.',
        color: 'red',
        icon: <IconAlertCircle size="1rem" />
      });
    } finally {
      setActionLoading(null);
    }
  };

  const handleDisableAccess = async () => {
    if (!patient) return;
    
    try {
      setActionLoading('disable');
      await onDisableAccess(patient.id);
      
      notifications.show({
        title: 'Portal Access Disabled',
        message: 'Patient portal access has been disabled.',
        color: 'orange',
        icon: <IconCheck size="1rem" />
      });
    } catch (error) {
      notifications.show({
        title: 'Error',
        message: 'Failed to disable portal access. Please try again.',
        color: 'red',
        icon: <IconAlertCircle size="1rem" />
      });
    } finally {
      setActionLoading(null);
    }
  };

  const handleUpdatePreferences = async (values: PatientPortalPreferences) => {
    if (!patient) return;
    
    try {
      setLoading(true);
      await onUpdatePreferences(patient.id, values);
      
      notifications.show({
        title: 'Preferences Updated',
        message: 'Portal preferences have been updated successfully.',
        color: 'green',
        icon: <IconCheck size="1rem" />
      });
    } catch (error) {
      notifications.show({
        title: 'Error',
        message: 'Failed to update preferences. Please try again.',
        color: 'red',
        icon: <IconAlertCircle size="1rem" />
      });
    } finally {
      setLoading(false);
    }
  };

  const handleResetPassword = async () => {
    if (!patient) return;
    
    try {
      setActionLoading('reset');
      await onResetPassword(patient.id);
      
      notifications.show({
        title: 'Password Reset',
        message: 'Password has been reset and new credentials sent to the patient.',
        color: 'green',
        icon: <IconCheck size="1rem" />
      });
    } catch (error) {
      notifications.show({
        title: 'Error',
        message: 'Failed to reset password. Please try again.',
        color: 'red',
        icon: <IconAlertCircle size="1rem" />
      });
    } finally {
      setActionLoading(null);
    }
  };

  const handleSendCredentials = async (method: 'email' | 'sms') => {
    if (!patient) return;
    
    try {
      setActionLoading(`send-${method}`);
      await onSendCredentials(patient.id, method);
      
      notifications.show({
        title: 'Credentials Sent',
        message: `Login credentials have been sent via ${method.toUpperCase()}.`,
        color: 'green',
        icon: <IconCheck size="1rem" />
      });
    } catch (error) {
      notifications.show({
        title: 'Error',
        message: `Failed to send credentials via ${method.toUpperCase()}. Please try again.`,
        color: 'red',
        icon: <IconAlertCircle size="1rem" />
      });
    } finally {
      setActionLoading(null);
    }
  };

  if (!patient) return null;

  // Overview Tab
  const OverviewTab = () => (
    <Stack gap="lg">
      {/* Portal Status */}
      <Paper p="lg" withBorder>
        <Grid>
          <Grid.Col span={{ base: 12, md: 8 }}>
            <Group mb="md">
              <Avatar size="lg" name={`${patient.firstName} ${patient.lastName}`} color="blue" />
              <div>
                <Title order={3}>{patient.firstName} {patient.lastName}</Title>
                <Text c="dimmed">{patient.patientId}</Text>
              </div>
            </Group>
            
            <Group>
              <Badge
                size="lg"
                color={portalAccess?.isEnabled ? 'green' : 'red'}
                variant="light"
                leftSection={portalAccess?.isEnabled ? <IconUserCheck size="0.8rem" /> : <IconUserX size="0.8rem" />}
              >
                {portalAccess?.isEnabled ? 'Portal Access Enabled' : 'Portal Access Disabled'}
              </Badge>
              {portalAccess?.accountLocked && (
                <Badge color="red" variant="filled">
                  Account Locked
                </Badge>
              )}
            </Group>
          </Grid.Col>
          
          <Grid.Col span={{ base: 12, md: 4 }}>
            <Stack>
              {!portalAccess?.isEnabled ? (
                <Button
                  fullWidth
                  color="green"
                  onClick={handleEnableAccess}
                  loading={actionLoading === 'enable'}
                  leftSection={<IconUserCheck size="1rem" />}
                >
                  Enable Portal Access
                </Button>
              ) : (
                <Group grow>
                  <Button
                    color="red"
                    variant="outline"
                    onClick={handleDisableAccess}
                    loading={actionLoading === 'disable'}
                    leftSection={<IconUserX size="1rem" />}
                  >
                    Disable Access
                  </Button>
                </Group>
              )}
              
              {portalAccess?.isEnabled && (
                <Group grow>
                  <Button
                    variant="outline"
                    onClick={handleResetPassword}
                    loading={actionLoading === 'reset'}
                    leftSection={<IconKey size="1rem" />}
                    size="sm"
                  >
                    Reset Password
                  </Button>
                </Group>
              )}
            </Stack>
          </Grid.Col>
        </Grid>
      </Paper>

      {portalAccess?.isEnabled && (
        <>
          {/* Account Information */}
          <Grid>
            <Grid.Col span={{ base: 12, md: 6 }}>
              <Paper p="md" withBorder>
                <Group mb="md">
                  <IconActivity size="1.2rem" />
                  <Title order={4}>Account Activity</Title>
                </Group>
                <Stack gap="sm">
                  <Group justify="space-between">
                    <Text size="sm">Last Login</Text>
                    <Text size="sm" c="dimmed">
                      {portalAccess.lastLogin ? formatDate(portalAccess.lastLogin) : 'Never'}
                    </Text>
                  </Group>
                  <Group justify="space-between">
                    <Text size="sm">Login Attempts</Text>
                    <Text size="sm" c="dimmed">{portalAccess.loginAttempts}</Text>
                  </Group>
                  <Group justify="space-between">
                    <Text size="sm">Two Factor Auth</Text>
                    <Badge color={portalAccess.twoFactorEnabled ? 'green' : 'gray'} size="sm">
                      {portalAccess.twoFactorEnabled ? 'Enabled' : 'Disabled'}
                    </Badge>
                  </Group>
                </Stack>
              </Paper>
            </Grid.Col>

            <Grid.Col span={{ base: 12, md: 6 }}>
              <Paper p="md" withBorder>
                <Group mb="md">
                  <IconBell size="1.2rem" />
                  <Title order={4}>Notification Preferences</Title>
                </Group>
                <Stack gap="sm">
                  <Group justify="space-between">
                    <Text size="sm">Email Notifications</Text>
                    <Badge color={portalAccess.preferences.receiveEmailNotifications ? 'green' : 'gray'} size="sm">
                      {portalAccess.preferences.receiveEmailNotifications ? 'Enabled' : 'Disabled'}
                    </Badge>
                  </Group>
                  <Group justify="space-between">
                    <Text size="sm">SMS Notifications</Text>
                    <Badge color={portalAccess.preferences.receiveSmsNotifications ? 'green' : 'gray'} size="sm">
                      {portalAccess.preferences.receiveSmsNotifications ? 'Enabled' : 'Disabled'}
                    </Badge>
                  </Group>
                  <Group justify="space-between">
                    <Text size="sm">Appointment Reminders</Text>
                    <Badge color={portalAccess.preferences.appointmentReminders ? 'green' : 'gray'} size="sm">
                      {portalAccess.preferences.appointmentReminders ? 'Enabled' : 'Disabled'}
                    </Badge>
                  </Group>
                </Stack>
              </Paper>
            </Grid.Col>
          </Grid>

          {/* Send Credentials */}
          <Paper p="md" withBorder>
            <Group justify="space-between" mb="md">
              <div>
                <Title order={4}>Send Login Credentials</Title>
                <Text size="sm" c="dimmed">
                  Send new login credentials to the patient via email or SMS
                </Text>
              </div>
              <Group>
                <Button
                  variant="outline"
                  onClick={() => handleSendCredentials('email')}
                  loading={actionLoading === 'send-email'}
                  leftSection={<IconMail size="1rem" />}
                  disabled={!patient.contactInfo.email}
                >
                  Send via Email
                </Button>
                <Button
                  variant="outline"
                  onClick={() => handleSendCredentials('sms')}
                  loading={actionLoading === 'send-sms'}
                  leftSection={<IconPhone size="1rem" />}
                >
                  Send via SMS
                </Button>
              </Group>
            </Group>
            <Alert color="blue" icon={<IconAlertCircle size="1rem" />}>
              New credentials will invalidate any existing login sessions for this patient.
            </Alert>
          </Paper>
        </>
      )}
    </Stack>
  );

  // Preferences Tab
  const PreferencesTab = () => (
    <form onSubmit={preferencesForm.onSubmit(handleUpdatePreferences)}>
      <Stack gap="lg">
        {!portalAccess?.isEnabled && (
          <Alert color="orange" icon={<IconAlertCircle size="1rem" />}>
            Portal access must be enabled before preferences can be configured.
          </Alert>
        )}

        {/* Notification Preferences */}
        <Paper p="md" withBorder>
          <Title order={4} mb="md">Notification Preferences</Title>
          <Stack gap="md">
            <Switch
              label="Email Notifications"
              description="Receive notifications via email"
              {...preferencesForm.getInputProps('receiveEmailNotifications', { type: 'checkbox' })}
              disabled={!portalAccess?.isEnabled || !patient.contactInfo.email}
            />
            <Switch
              label="SMS Notifications"
              description="Receive notifications via SMS"
              {...preferencesForm.getInputProps('receiveSmsNotifications', { type: 'checkbox' })}
              disabled={!portalAccess?.isEnabled}
            />
            <Switch
              label="Appointment Reminders"
              description="Receive reminders for upcoming appointments"
              {...preferencesForm.getInputProps('appointmentReminders', { type: 'checkbox' })}
              disabled={!portalAccess?.isEnabled}
            />
            <Switch
              label="Lab Result Notifications"
              description="Get notified when lab results are available"
              {...preferencesForm.getInputProps('labResultNotifications', { type: 'checkbox' })}
              disabled={!portalAccess?.isEnabled}
            />
            <Switch
              label="Prescription Refill Reminders"
              description="Receive reminders for prescription refills"
              {...preferencesForm.getInputProps('prescriptionRefillReminders', { type: 'checkbox' })}
              disabled={!portalAccess?.isEnabled}
            />
          </Stack>
        </Paper>

        {/* Language and Regional Settings */}
        <Paper p="md" withBorder>
          <Title order={4} mb="md">Language & Regional Settings</Title>
          <Grid>
            <Grid.Col span={{ base: 12, md: 6 }}>
              <Select
                label="Language Preference"
                data={[
                  { value: 'English', label: 'English' },
                  { value: 'Hindi', label: '‡§π‡§ø‡§Ç‡§¶‡•Ä (Hindi)' },
                  { value: 'Spanish', label: 'Espa√±ol (Spanish)' },
                  { value: 'French', label: 'Fran√ßais (French)' }
                ]}
                {...preferencesForm.getInputProps('languagePreference')}
                disabled={!portalAccess?.isEnabled}
              />
            </Grid.Col>
            <Grid.Col span={{ base: 12, md: 6 }}>
              <Select
                label="Time Zone"
                data={[
                  { value: 'Asia/Kolkata', label: 'India Standard Time (IST)' },
                  { value: 'America/New_York', label: 'Eastern Time (EST)' },
                  { value: 'America/Los_Angeles', label: 'Pacific Time (PST)' },
                  { value: 'Europe/London', label: 'Greenwich Mean Time (GMT)' }
                ]}
                {...preferencesForm.getInputProps('timeZone')}
                disabled={!portalAccess?.isEnabled}
              />
            </Grid.Col>
          </Grid>
        </Paper>

        {/* Action Buttons */}
        <Group justify="flex-end">
          <Button variant="outline" onClick={onClose}>
            Cancel
          </Button>
          <Button
            type="submit"
            loading={loading}
            disabled={!portalAccess?.isEnabled}
            leftSection={<IconSettings size="1rem" />}
          >
            Update Preferences
          </Button>
        </Group>
      </Stack>
    </form>
  );

  // Access History Tab
  const AccessHistoryTab = () => (
    <Stack gap="lg">
      {!portalAccess?.isEnabled ? (
        <Alert color="gray" icon={<IconHistory size="1rem" />}>
          Portal access history will be available once the patient portal is enabled.
        </Alert>
      ) : (
        <>
          <Paper p="md" withBorder>
            <Title order={4} mb="md">Recent Access Activity</Title>
            <Timeline active={1} bulletSize={24} lineWidth={2}>
              <Timeline.Item bullet={<IconLogin size="0.8rem" />} title="Successful Login">
                <Text c="dimmed" size="sm">March 15, 2024 at 10:30 AM</Text>
                <Text size="sm" mt={4}>Logged in from Chrome browser (IP: 192.168.1.100)</Text>
              </Timeline.Item>
              
              <Timeline.Item bullet={<IconEye size="0.8rem" />} title="Viewed Lab Results">
                <Text c="dimmed" size="sm">March 15, 2024 at 10:32 AM</Text>
                <Text size="sm" mt={4}>Accessed recent blood test results</Text>
              </Timeline.Item>
              
              <Timeline.Item bullet={<IconCalendar size="0.8rem" />} title="Appointment Scheduled">
                <Text c="dimmed" size="sm">March 15, 2024 at 10:35 AM</Text>
                <Text size="sm" mt={4}>Scheduled follow-up appointment for March 25th</Text>
              </Timeline.Item>
              
              <Timeline.Item bullet={<IconLogout size="0.8rem" />} title="Logged Out">
                <Text c="dimmed" size="sm">March 15, 2024 at 10:45 AM</Text>
                <Text size="sm" mt={4}>Session ended normally</Text>
              </Timeline.Item>
            </Timeline>
          </Paper>

          <Paper p="md" withBorder>
            <Title order={4} mb="md">Security Events</Title>
            <Table>
              <Table.Thead>
                <Table.Tr>
                  <Table.Th>Date</Table.Th>
                  <Table.Th>Event</Table.Th>
                  <Table.Th>Status</Table.Th>
                  <Table.Th>Details</Table.Th>
                </Table.Tr>
              </Table.Thead>
              <Table.Tbody>
                <Table.Tr>
                  <Table.Td>Mar 15, 2024</Table.Td>
                  <Table.Td>Password Reset</Table.Td>
                  <Table.Td>
                    <Badge color="green" size="sm">Success</Badge>
                  </Table.Td>
                  <Table.Td>Admin initiated password reset</Table.Td>
                </Table.Tr>
                <Table.Tr>
                  <Table.Td>Mar 10, 2024</Table.Td>
                  <Table.Td>Failed Login</Table.Td>
                  <Table.Td>
                    <Badge color="red" size="sm">Failed</Badge>
                  </Table.Td>
                  <Table.Td>Incorrect password (3 attempts)</Table.Td>
                </Table.Tr>
                <Table.Tr>
                  <Table.Td>Mar 5, 2024</Table.Td>
                  <Table.Td>Account Created</Table.Td>
                  <Table.Td>
                    <Badge color="blue" size="sm">Info</Badge>
                  </Table.Td>
                  <Table.Td>Portal access enabled by admin</Table.Td>
                </Table.Tr>
              </Table.Tbody>
            </Table>
          </Paper>
        </>
      )}
    </Stack>
  );

  return (
    <Modal
      opened={opened}
      onClose={onClose}
      title={
        <Group>
          <IconUserCheck size="1.2rem" />
          <Text fw={600}>Patient Portal Access</Text>
        </Group>
      }
      size="xl"
    >
      <Tabs value={activeTab} onChange={(value) => setActiveTab(value || 'overview')}>
        <Tabs.List>
          <Tabs.Tab value="overview" leftSection={<IconShield size="0.8rem" />}>
            Overview
          </Tabs.Tab>
          <Tabs.Tab value="preferences" leftSection={<IconSettings size="0.8rem" />}>
            Preferences
          </Tabs.Tab>
          <Tabs.Tab value="history" leftSection={<IconHistory size="0.8rem" />}>
            Access History
          </Tabs.Tab>
        </Tabs.List>

        <Tabs.Panel value="overview" pt="md">
          <OverviewTab />
        </Tabs.Panel>

        <Tabs.Panel value="preferences" pt="md">
          <PreferencesTab />
        </Tabs.Panel>

        <Tabs.Panel value="history" pt="md">
          <AccessHistoryTab />
        </Tabs.Panel>
      </Tabs>
    </Modal>
  );
}
